            if not tree:
                return None
            nearest_node = nearest(tree, node.position)
            last_node = nearest_node

            while True:
                new_pos = steer(last_node.position, node.position)
                if not can_add_node(new_pos):
                    return None
                if not can_connect(last_node.position, new_pos):
                    return None

                new_cost = last_node.cost + dist(last_node.position, new_pos)
                new_node = Node(new_pos, parent=last_node, cost=new_cost)
                last_node.children.append(new_node)
                tree.append(new_node)
