class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.1, radius_constant: float = 30.0, radius_min: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.radius_constant = radius_constant
        self.radius_min = radius_min

    def plan(self, map) -> 'PlannerResult':
        import random
        import math

        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        def sample_free():
            if random.random() < self.goal_sample_rate:
                return goal_position if random.random() < 0.7 else start_position
            else:
                while True:
                    if is_3d:
                        p = tuple(random.uniform(0, bounds[d]) for d in range(3))
                    else:
                        p = (random.uniform(0, bounds[0]), random.uniform(0, bounds[1]))
                    if not self._is_in_obstacle(p, obstacles, is_3d):
                        return p

        def steer(from_pos, to_pos):
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(len(from_pos)))

        def distance(a, b):
            return math.dist(a, b)

        def nearest(tree, point):
            return min(tree, key=lambda node: distance(node.position, point))

        def near_nodes(tree, pos, radius):
            return [node for node in tree if distance(node.position, pos) <= radius]

        success_state = False
        extracted_path = []
        nodes = []
        edges = []

        start_root = Node(start_position, cost=0.0)
        goal_root = Node(goal_position, cost=0.0)
        tree_start = [start_root]
        tree_goal = [goal_root]
        nodes.extend([start_root, goal_root])

        def radius_by_nodes(n_nodes):
            # Radius shrinks as more nodes are added (typical for RRT*), with a minimum radius
            return max(self.radius_min, self.radius_constant * math.sqrt((math.log(n_nodes + 1) / (n_nodes + 1))))

        def try_extend(tree, point):
            if not tree:
                return None
            nearest_node = nearest(tree, point)
            new_pos = steer(nearest_node.position, point)

            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(nearest_node.position, new_pos, obstacles, is_3d):
                return None

            radius = radius_by_nodes(len(tree))
            near = near_nodes(tree, new_pos, radius)

            min_cost = nearest_node.cost + distance(nearest_node.position, new_pos)
            best_parent = nearest_node
            for node in near:
                temp_cost = node.cost + distance(node.position, new_pos)
                if temp_cost < min_cost:
                    if not self._is_edge_in_obstacle(node.position, new_pos, obstacles, is_3d):
                        min_cost = temp_cost
                        best_parent = node

            new_node = Node(new_pos)
            new_node.cost = min_cost
            new_node.parent = best_parent
            best_parent.children.append(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire around new_node to improve paths locally
            for neighbor in near:
                if neighbor == best_parent:
                    continue
                cost_via_new = new_node.cost + distance(new_node.position, neighbor.position)
                if cost_via_new + 1e-6 < neighbor.cost:  # Strict improvement with epsilon
                    if not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                        try:
                            edges.remove((neighbor.parent, neighbor))
                        except ValueError:
                            pass
                        neighbor.update_parent(new_node, cost_via_new)
                        edges.append((new_node, neighbor))

            return new_node

        def try_connect(tree, node):
            if not tree:
                return None
            nearest_node = nearest(tree, node.position)
            last_node = nearest_node
            while True:
                new_pos = steer(last_node.position, node.position)
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    return None
                if self._is_edge_in_obstacle(last_node.position, new_pos, obstacles, is_3d):
                    return None

                new_cost = last_node.cost + distance(last_node.position, new_pos)
                new_node = Node(new_pos)
                new_node.cost = new_cost
                new_node.parent = last_node
                last_node.children.append(new_node)
                tree.append(new_node)
                nodes.append(new_node)
                edges.append((last_node, new_node))

                if distance(new_pos, node.position) <= self.step_size:
                    # Check final edge feasibility before return
                    if not self._is_edge_in_obstacle(new_pos, node.position, obstacles, is_3d):
                        final_node = Node(node.position)
                        final_node.cost = new_node.cost + distance(new_pos, node.position)
                        final_node.parent = new_node
                        new_node.children.append(final_node)
                        tree.append(final_node)
                        nodes.append(final_node)
                        edges.append((new_node, final_node))
                        return final_node
                    else:
                        return None
                last_node = new_node

        # Alternate growth direction, keep track of trees accordingly
        for itr in range(self.max_iter):
            rand_point = sample_free()
            # Always extend the smaller tree first to balance growths
            if len(tree_start) > len(tree_goal):
                tree_start, tree_goal = tree_goal, tree_start

            new_node_start = try_extend(tree_start, rand_point)
            if new_node_start is None:
                continue

            new_node_goal = try_connect(tree_goal, new_node_start)
            if new_node_goal is not None:
                success_state = True

                path_from_start = new_node_start.path_from_root()
                path_from_goal = new_node_goal.path_from_root()

                # Merge and avoid duplicate connection node
                extracted_path = path_from_start + path_from_goal[::-1][1:]
                break

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges,
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * i / steps for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=6.0,
                 goal_sample_rate: float=0.12, radius_constant: float=25.0,
                 radius_min: float=6.0, prune_interval: int=150,
                 prune_threshold: int=150, smoothing_iterations: int=50):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.radius_constant = radius_constant
        self.radius_min = radius_min
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size                  # Tuple[int, ...]: (W,H) or (W,H,D)
        start_position = map.start         # Tuple[float, ...]
        goal_position = map.goal           # Tuple[float, ...]
        obstacles = map.obstacles          # List of obstacle rects/cuboids

        import random
        import math

        is_3d = (len(bounds) == 3)

        success_state = False
        extracted_path = []
        nodes = []
        edges = []

        start_root = Node(start_position, cost=0.0)
        goal_root = Node(goal_position, cost=0.0)
        tree_start = [start_root]
        tree_goal = [goal_root]
        nodes.extend([start_root, goal_root])

        def dist(a, b):
            return math.dist(a, b)

        def sample_free():
            p_bias = 0.7
            if random.random() < self.goal_sample_rate:
                # Bias sampling: 75% goal, 25% start
                return goal_position if random.random() < p_bias else start_position
            else:
                while True:
                    sample = tuple(random.uniform(0, bounds[d]) for d in range(len(bounds)))
                    if not self._is_in_obstacle(sample, obstacles, is_3d):
                        return sample

        def steer(from_pos, to_pos):
            distance_between = dist(from_pos, to_pos)
            if distance_between <= self.step_size:
                return to_pos
            ratio = self.step_size / distance_between
            return tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(len(from_pos)))

        def nearest(tree, point):
            return min(tree, key=lambda node: dist(node.position, point))

        def near_nodes(tree, pos, radius):
            return [node for node in tree if dist(node.position, pos) <= radius]

        def radius_dynamic(n):
            if n <= 1:
                return self.radius_constant
            rad = self.radius_constant * math.sqrt(math.log(n + 1) / (n + 1))
            return max(rad, self.radius_min)

        def can_add_node(pos):
            if self._is_in_obstacle(pos, obstacles, is_3d):
                return False
            for d in range(len(pos)):
                if not (0 <= pos[d] <= bounds[d]):
                    return False
            return True

        def can_connect(p1, p2):
            if self._is_edge_in_obstacle(p1, p2, obstacles, is_3d):
                return False
            return True

        def try_extend(tree, sample_pt):
            if not tree:
                return None
            nearest_node = nearest(tree, sample_pt)
            new_pos = steer(nearest_node.position, sample_pt)

            if not can_add_node(new_pos):
                return None
            if not can_connect(nearest_node.position, new_pos):
                return None

            radius = radius_dynamic(len(tree))
            near = near_nodes(tree, new_pos, radius)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for node in near:
                tentative_cost = node.cost + dist(node.position, new_pos)
                if tentative_cost + 1e-12 < min_cost:
                    if can_connect(node.position, new_pos):
                        min_cost = tentative_cost
                        best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors for improved cost
            for neighbor in near:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost:
                    if can_connect(new_node.position, neighbor.position):
                        try:
                            edges.remove((neighbor.parent, neighbor))
                        except ValueError:
                            pass
                        neighbor.update_parent(new_node, cost_via_new)
                        edges.append((new_node, neighbor))

            return new_node

        def try_connect(tree, node):
            if not tree:
                return None
            nearest_node = nearest(tree, node.position)
            last_node = nearest_node

            while True:
                new_pos = steer(last_node.position, node.position)
                if not can_add_node(new_pos):
                    return None
                if not can_connect(last_node.position, new_pos):
                    return None

                new_cost = last_node.cost + dist(last_node.position, new_pos)
                new_node = Node(new_pos, parent=last_node, cost=new_cost)
                last_node.children.append(new_node)
                tree.append(new_node)
                nodes.append(new_node)
                edges.append((last_node, new_node))

                if dist(new_pos, node.position) <= self.step_size:
                    if can_connect(new_pos, node.position):
                        final_cost = new_node.cost + dist(new_pos, node.position)
                        final_node = Node(node.position, parent=new_node, cost=final_cost)
                        new_node.children.append(final_node)
                        tree.append(final_node)
                        nodes.append(final_node)
                        edges.append((new_node, final_node))
                        return final_node
                    else:
                        return None

                last_node = new_node

        def prune_dead_nodes():
            removed = []
            for node in nodes[:]:
                if node is start_root or node is goal_root:
                    continue
                if not node.children:
                    try:
                        if node.parent:
                            node.parent.children.remove(node)
                        nodes.remove(node)
                        edges[:] = [(p, c) for (p, c) in edges if c != node and p != node]
                        removed.append(node)
                    except Exception:
                        continue
            return removed

        def path_length(path):
            length = 0.0
            for i in range(len(path) - 1):
                length += dist(path[i], path[i + 1])
            return length

        def shortcut_smoothing(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if not can_connect(p1, p2):
                    continue
                path = path[:i+1] + path[j:]
            return path

        for itr in range(self.max_iter):
            sample_pt = sample_free()

            # Always extend the smaller tree first to balance search
            if len(tree_start) > len(tree_goal):
                tree_start, tree_goal = tree_goal, tree_start

            new_node_start = try_extend(tree_start, sample_pt)
            if new_node_start is None:
                continue

            new_node_goal = try_connect(tree_goal, new_node_start)
            if new_node_goal is not None:
                success_state = True
                path_start = new_node_start.path_from_root()
                path_goal = new_node_goal.path_from_root()
                raw_path = path_start + path_goal[::-1][1:]

                smoothed = shortcut_smoothing(raw_path)
                if path_length(smoothed) + 1e-12 < path_length(raw_path):
                    extracted_path = smoothed
                else:
                    extracted_path = raw_path
                break

            if itr % self.prune_interval == 0 and len(nodes) > self.prune_threshold:
                prune_dead_nodes()

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0, max_neighbor_radius: float = 30.0,
                 prune_interval: int = 200, prune_threshold: int = 300,
                 smoothing_iterations: int = 150, collision_check_resolution: float = 1.0,
                 goal_sample_rate: float = 0.15):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = dim == 3

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for dd in range(self.dim):
                        delta = p[dd] - pt[dd]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))
            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            u = a1_np - np.array([1 if d == 0 else 0 for d in range(dim)])
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            val = max(r1 * r1 - (c_min / 2) * (c_min / 2), 0)
            r_others = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x <= 1 and norm_x > 1e-15:
                    radius_factor = np.random.uniform(0, 1) ** (1/dim)
                    x_scaled = (x / norm_x) * radius_factor
                    x_scaled = x_scaled * radii
                    p = rot @ x_scaled + np.array(center)
                    p_tuple = tuple(float(coord) for coord in p)
                    if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                        return p_tuple

            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p  # fallback

        edge_collision_cache = {}

        def can_connect(p1, p2):
            if (p1, p2) in edge_collision_cache:
                return not edge_collision_cache[(p1, p2)]
            if (p2, p1) in edge_collision_cache:
                return not edge_collision_cache[(p2, p1)]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[(p1, p2)] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[(p1, p2)] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            removed = 0
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Balance tree extension: smaller tree first
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes:
                candidate_cost = node.cost + dist(node.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors around new node
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            # Incremental connect opposite tree towards new_node
            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Periodic pruning
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=5.0,
                 max_neighbor_radius: float=30.0,
                 prune_interval: int=150,
                 prune_threshold: int=250,
                 smoothing_iterations: int=100,
                 collision_check_resolution: float=1.0,
                 goal_sample_rate: float=0.15):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> "PlannerResult":
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio*(to_pos[d]-from_pos[d]) for d in range(dim))
            # Clamp to bounds to avoid out-of-map issues
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / max(1, self.max_iter)) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def informed_sample(c_best, c_min, start, goal):
            # Sampling inside the ellipsoidal informed set, or uniform if no current solution
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(10):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not self._is_in_obstacle(p, obstacles, is_3d):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r_others = math.sqrt(max(r1**2 - (c_min / 2)**2, 0))
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(100):
                x = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x) <= 1:
                    sample = rot @ (x * radii) + center
                    sample_t = tuple(float(coord) for coord in sample)
                    if in_bounds(sample_t) and not self._is_in_obstacle(sample_t, obstacles, is_3d):
                        return sample_t

            # Fallback uniform random sampling if informed fails
            for _ in range(10):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p
            return p

        # Cache collision for edges to avoid redundant expensive checks
        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            reverse_key = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            elif reverse_key in edge_collision_cache:
                return not edge_collision_cache[reverse_key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if self._is_in_obstacle(pos, obstacles, is_3d):
                return False
            return True

        class KDTree:
            def __init__(self, pts_nodes):
                self.points = [pn[0] for pn in pts_nodes]
                self.nodes = [pn[1] for pn in pts_nodes]

            def nearest(self, pt):
                best_node = None
                best_dist = float('inf')
                for p, n in zip(self.points, self.nodes):
                    d = dist(p, pt)
                    if d < best_dist:
                        best_dist = d
                        best_node = n
                return best_node

            def radius_search(self, pt, radius):
                r2 = radius*radius
                res = []
                for p, n in zip(self.points, self.nodes):
                    sqd = 0.0
                    for d_ in range(dim):
                        diff = p[d_] - pt[d_]
                        sqd += diff*diff
                        if sqd > r2:
                            break
                    else:
                        res.append(n)
                return res

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i+1] + path[j:]
            return path

        def prune_nonpromising_nodes(nodes_list, tree_s, tree_g, best_cost):
            to_remove = []
            dist_to_goal = lambda node: dist(node.position, goal_pos)
            for node in nodes_list:
                if node in (root_start, root_goal):
                    continue
                # prune dead-end nodes
                if not node.children:
                    to_remove.append(node)
                    continue
                # prune nodes with cost + heuristic >= best_cost to focus search
                if node.cost + dist_to_goal(node) >= best_cost:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except ValueError:
                        pass
                try:
                    nodes_list.remove(node)
                except ValueError:
                    pass
                if node in tree_s:
                    try:
                        tree_s.remove(node)
                    except ValueError:
                        pass
                if node in tree_g:
                    try:
                        tree_g.remove(node)
                    except ValueError:
                        pass
            return len(to_remove)

        # Initialization
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for itr in range(self.max_iter):
            sample_point = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate growth of smaller tree for balance
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(sample_point)
            new_pos = steer(nearest_a.position, sample_point)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius = neighbor_radius(itr, len(nodes))
            near_a = kdtree_a.radius_search(new_pos, radius)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_a:
                tentative_cost = node.cost + dist(node.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewiring neighbors in tree_a
            for neighbor in near_a:
                if neighbor is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, neighbor.position)
                if new_cost + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

            # Incrementally attempt connection from tree_b to new_node
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_b = nearest_b
            connected = False

            max_connect_steps = int(dist(last_b.position, new_node.position) / self.step_size) + 1
            for _ in range(max_connect_steps):
                next_pos_b = steer(last_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_b.position, next_pos_b):
                    break

                cost_b = last_b.cost + dist(last_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_b, cost=cost_b)
                last_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, radius)
                for neighbor_b in near_b:
                    if neighbor_b is last_b:
                        continue
                    cost_via_new = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new)
                        edges.append((new_node_b, neighbor_b))

                dist_to_target = dist(new_node_b.position, new_node.position)
                if dist_to_target <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_target
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    # Recover and merge paths according to expansion order
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    # Update best path if improvement found
                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Periodic pruning to keep runtime manageable & encourage convergence
            if itr > 0 and itr % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_nonpromising_nodes(nodes, tree_start, tree_goal, c_best)
                edge_collision_cache.clear()

        if success_state and extracted_path:
            # Final iterative smoothing for improved path quality
            extracted_path = shortcut_path(extracted_path)

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D or 3D
        self.parent = parent            # Node or None
        self.cost = cost                # Cost from root
        self.children = []
        self.valid = True               # For collision or pruning

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_radius = 5.0
        self.max_radius = 30.0
        self.prune_interval = 200
        self.prune_threshold = 300
        self.smoothing_iterations = 100
        self.collision_check_resolution = 1.0

    def plan(self, map) -> 'PlannerResult':
        import random
        import math
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp inside bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def radius_by_iter_nodes(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_radius + (self.max_radius - self.min_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_radius, min(r_iter, r_nodes))

        # Collision checking caching for edges
        edge_collision_cache = {}

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w, h = obs
                    px, py = pos
                    if x <= px <= x + w and y <= py <= y + h:
                        return True
            return False

        def is_edge_in_obstacle(from_pos, to_pos):
            key = (from_pos, to_pos)
            rev_key = (to_pos, from_pos)
            if key in edge_collision_cache:
                return edge_collision_cache[key]
            if rev_key in edge_collision_cache:
                return edge_collision_cache[rev_key]

            distance_ = dist(from_pos, to_pos)
            steps = max(1, int(distance_ / self.collision_check_resolution))
            for i in range(steps + 1):
                ip = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * i / steps for d in range(dim))
                if is_in_obstacle(ip):
                    edge_collision_cache[key] = True
                    return True
            edge_collision_cache[key] = False
            return False

        # KDTree simplified but efficient for neighbor search
        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [pn[0] for pn in points_nodes]
                self.nodes = [pn[1] for pn in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                results = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    else:
                        results.append(self.nodes[i])
                return results

        def can_connect(p1, p2):
            if not in_bounds(p1) or not in_bounds(p2):
                return False
            if is_in_obstacle(p1) or is_in_obstacle(p2):
                return False
            if is_edge_in_obstacle(p1, p2):
                return False
            return True

        def can_add_node(pos):
            return in_bounds(pos) and not is_in_obstacle(pos)

        import numpy as np

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001:
                # Uniform random sampling until free space found
                for _ in range(10):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                # fallback random sampling (may be in obstacle)
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            id_mat = np.eye(dim)
            v = a1 - np.array([1] + [0]*(dim-1))
            norm_v = np.linalg.norm(v)
            if norm_v > 1e-10:
                v = v / norm_v
                rot = id_mat - 2 * np.outer(v, v)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            term = r1 * r1 - (c_min / 2.0)**2
            r_others = math.sqrt(term) if term > 0 else 0.0
            radii = np.array([r1] + [r_others]*(dim-1))

            for _ in range(100):
                x_ball = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x_ball) <= 1.0:
                    sample = rot @ (x_ball * radii) + center
                    p = tuple(float(c) for c in sample)
                    if in_bounds(p) and not is_in_obstacle(p):
                        return p

            # fallback uniform sampling
            for _ in range(10):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p  # fallback last sample

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i+1] + path[j:]
            return path

        def prune_nodes(best_cost):
            removed = 0
            to_remove = []
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                heuristic = dist(node.position, goal_pos)
                if node.cost + heuristic >= best_cost:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for iter_ in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate growth: extend the smaller tree for balance
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])

            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = radius_by_iter_nodes(iter_, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes:
                candidate_cost = node.cost + dist(node.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors around new_node for path cost improvement
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                c_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if c_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, c_via_new)
                    edges.append((new_node, neighbor))

            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            connected = False
            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                # Rewire neighbors in tree_b around new_node_b
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    c_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if c_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, c_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        # Shortcut smoothing to improve path quality
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Periodic pruning of nodes that cannot improve solution
            if iter_ > 0 and iter_ % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D or 3D
        self.parent = parent            # Node or None
        self.cost = cost                # Cost from root
        self.children = []
        self.valid = True               # For collision status

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        node, path = self, []
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0,
                 max_neighbor_radius: float = 30.0,
                 prune_interval: int = 200,
                 prune_threshold: int = 300,
                 smoothing_iterations: int = 100,
                 collision_check_resolution: float = 1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        import random
        import math
        import numpy as np

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001:
                # Uninformed sampling fallback
                for _ in range(10):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            id_mat = np.eye(dim)
            v = a1 - id_mat[0]
            norm_v = np.linalg.norm(v)
            if norm_v > 1e-10:
                v /= norm_v
                rot = id_mat - 2 * np.outer(v, v)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r_others = math.sqrt(max(r1 ** 2 - (c_min / 2) ** 2, 0))
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(100):
                x_ball = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x_ball) <= 1.0:
                    sample = rot @ (x_ball * radii) + center
                    p = tuple(float(coord) for coord in sample)
                    if in_bounds(p) and not is_in_obstacle(p):
                        return p

            for _ in range(10):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p  # fallback

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            key_rev = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if key_rev in edge_collision_cache:
                return not edge_collision_cache[key_rev]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            removed = 0
            to_remove = []
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                heuristic = dist(node.position, goal_pos)
                if node.cost + heuristic >= best_cost:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Always extend smaller tree first for balanced growth
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos):
                continue
            if not can_connect(nearest_a.position, new_pos):
                continue

            radius = neighbor_radius(it, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, radius)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node_near in near_nodes:
                candidate_cost = node_near.cost + dist(node_near.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node_near.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node_near

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewiring neighbors
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                c_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if c_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, c_via_new)
                    edges.append((new_node, neighbor))

            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False

            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1
            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b):
                    break
                if not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, radius)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    conn_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(conn_node)
                    tree_b.append(conn_node)
                    nodes.append(conn_node)
                    edges.append((new_node_b, conn_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = conn_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = conn_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]
                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True  # For collision or validity checks

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self,
                 max_iter: int=5000,
                 step_size: float=5.0,
                 min_neighbor_radius: float=5.0,
                 max_neighbor_radius: float=30.0,
                 prune_interval: int=200,
                 prune_threshold: int=300,
                 smoothing_iterations: int=150,
                 collision_check_resolution: float=1.0,
                 goal_sample_rate: float=0.15):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> PlannerResult:
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p: Tuple[float, ...]) -> bool:
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a: Tuple[float, ...], b: Tuple[float, ...]) -> float:
            return math.dist(a, b)

        def steer(from_pos: Tuple[float, ...], to_pos: Tuple[float, ...]) -> Tuple[float, ...]:
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration: int, n_nodes: int) -> float:
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos: Tuple[float, ...]) -> bool:
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1: Tuple[float, ...], p2: Tuple[float, ...]) -> bool:
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes: List[Tuple[Tuple[float, ...], Node]]):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt: Tuple[float, ...]) -> Node:
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt: Tuple[float, ...], radius: float) -> List[Node]:
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for dd in range(self.dim):
                        delta = p[dd] - pt[dd]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best: float, c_min: float, start: Tuple[float, ...], goal: Tuple[float, ...]) -> Tuple[float, ...]:
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))
            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            u = a1_np - np.array([1 if d == 0 else 0 for d in range(dim)])
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            val = max(r1 * r1 - (c_min / 2) * (c_min / 2), 0)
            r_others = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x <= 1 and norm_x > 1e-15:
                    radius_factor = np.random.uniform(0,1) ** (1/dim)
                    x_scaled = (x / norm_x) * radius_factor
                    x_scaled = x_scaled * radii
                    p = rot @ x_scaled + np.array(center)
                    p_tuple = tuple(float(coord) for coord in p)
                    if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                        return p_tuple

            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1: Tuple[float, ...], p2: Tuple[float, ...]) -> bool:
            if (p1, p2) in edge_collision_cache:
                return not edge_collision_cache[(p1, p2)]
            if (p2, p1) in edge_collision_cache:
                return not edge_collision_cache[(p2, p1)]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[(p1, p2)] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[(p1, p2)] = collision
            return not collision

        def can_add_node(pos: Tuple[float, ...]) -> bool:
            if not in_bounds(pos): return False
            if is_in_obstacle(pos): return False
            return True

        def shortcut_path(path: List[Tuple[float, ...]]) -> List[Tuple[float, ...]]:
            if len(path) < 3:
                return path
            iteration = 0
            while iteration < self.smoothing_iterations:
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
                    iteration = 0  # Restart smoothing after successful shortcut
                else:
                    iteration += 1
            return path

        def prune_useless_nodes(best_cost: float) -> int:
            removed = 0
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                # If estimated total cost worse than current best, prune
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Extend smaller tree first
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes:
                candidate_cost = node.cost + dist(node.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors around new node (incremental rewiring)
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            # Incrementally connect opposite tree towards new_node
            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Periodic pruning to remove non-promising nodes and clear cache
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D or 3D
        self.parent = parent            # Parent node
        self.cost = cost                # Cost from root/start to this node
        self.children = []             # List of children
        self.valid = True              # Validity flag for pruning or collision

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)
        # Recursively update costs of descendants
        def update_subtree_costs(node: 'Node'):
            for c in node.children:
                c.cost = node.cost + self._dist(node.position, c.position)
                update_subtree_costs(c)
        update_subtree_costs(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]

    @staticmethod
    def _dist(a, b):
        import math
        return math.dist(a, b)
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=5.0,
                 max_neighbor_radius: float=30.0,
                 prune_interval: int=200,
                 prune_threshold: int=300,
                 smoothing_iterations: int=100,
                 collision_check_resolution: float=1.0,
                 goal_sample_rate: float=0.15):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> 'PlannerResult':
        import math
        import random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio*(to_pos[d]-from_pos[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def informed_sample(c_best, c_min, start, goal):
            import numpy as np
            # Goal bias
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(10):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not self._is_in_obstacle(p, obstacles, is_3d):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r_others = math.sqrt(max(r1**2 - (c_min / 2)**2, 0))
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(100):
                x = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x) <= 1:
                    sample = rot @ (x * radii) + center
                    sample_t = tuple(float(coord) for coord in sample)
                    if in_bounds(sample_t) and not self._is_in_obstacle(sample_t, obstacles, is_3d):
                        return sample_t

            for _ in range(10):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            reverse_key = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if reverse_key in edge_collision_cache:
                return not edge_collision_cache[reverse_key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            return in_bounds(pos) and not self._is_in_obstacle(pos, obstacles, is_3d)

        # KD-tree substitute
        class KDTree:
            def __init__(self, pts_nodes):
                self.points = [pn[0] for pn in pts_nodes]
                self.nodes = [pn[1] for pn in pts_nodes]

            def nearest(self, pt):
                best_node = None
                best_dist = float('inf')
                for p, n in zip(self.points, self.nodes):
                    d = dist(p, pt)
                    if d < best_dist:
                        best_dist = d
                        best_node = n
                return best_node

            def radius_search(self, pt, radius):
                r2 = radius * radius
                res = []
                for p, n in zip(self.points, self.nodes):
                    sqd = 0.0
                    for d_ in range(dim):
                        diff = p[d_] - pt[d_]
                        sqd += diff*diff
                        if sqd > r2:
                            break
                    else:
                        res.append(n)
                return res

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i+1] + path[j:]
            return path

        def prune_nonpromising_nodes(nodes_list, tree_s, tree_g, best_cost):
            to_remove = []
            for node in nodes_list:
                if node in (root_start, root_goal):
                    continue
                if not node.children:
                    to_remove.append(node)
                    continue
                h_cost = dist(node.position, goal_pos)
                if node.cost + h_cost >= best_cost:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except ValueError:
                        pass
                try:
                    nodes_list.remove(node)
                except ValueError:
                    pass
                if node in tree_s:
                    try:
                        tree_s.remove(node)
                    except ValueError:
                        pass
                if node in tree_g:
                    try:
                        tree_g.remove(node)
                    except ValueError:
                        pass
            return len(to_remove)

        # Initialize roots
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for itr in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Balanced tree expansion
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(itr, len(nodes))
            near_a = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_a:
                cost_cand = node.cost + dist(node.position, new_pos)
                if cost_cand + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_cand
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors in tree_a
            for neighbor in near_a:
                if neighbor is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, neighbor.position)
                if new_cost + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

            # Incrementally connect tree_b towards new_node
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_b = nearest_b
            connected = False

            max_steps = int(dist(last_b.position, new_node.position) / self.step_size) + 1
            for _ in range(max_steps):
                next_pos_b = steer(last_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_b.position, next_pos_b):
                    break

                cost_b = last_b.cost + dist(last_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_b, cost=cost_b)
                last_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_b:
                        continue
                    cost_via_new = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    # Extract and merge paths according to tree expanded first
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged)

                    connected = True
                    break

                last_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Periodic pruning to improve efficiency
            if itr > 0 and itr % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                removed = prune_nonpromising_nodes(nodes, tree_start, tree_goal, c_best)
                edge_collision_cache.clear()

        if success_state and extracted_path:
            # Final smoothing of extracted path
            extracted_path = shortcut_path(extracted_path)

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * i / steps for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child):
        self.children.append(child)

    def distance(self, other):
        return math.dist(self.position, other.position)

    def path(self):
        node, p = self, []
        while node:
            p.append(node.position)
            node = node.parent
        return p[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0, goal_sample_rate: float=0.1, rewire_radius: float=15.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.rewire_radius = rewire_radius

    def plan(self, map) -> PlannerResult:
        bounds = map.size                  # Tuple[int, ...]: (W,H) or (W,H,D)
        start_position = map.start         # Tuple[float, ...] (W,H) or (W,H,D)
        goal_position = map.goal           # Tuple[float, ...] (W,H) or (W,H,D)
        obstacles = map.obstacles          # Rectangular blocks: 2D=(x,y,w,h), 3D=(x,y,z,w,h,d)
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        start_tree: List[Node] = [Node(start_position)]
        goal_tree: List[Node] = [Node(goal_position)]
        nodes: List[Node] = start_tree + goal_tree
        edges: List[Tuple[Node, Node]] = []

        # Track best solution and goal node reached from start tree
        best_cost = math.inf
        connection_node_start = None
        connection_node_goal = None

        def steer(from_pos, to_pos, max_dist):
            dist = math.dist(from_pos, to_pos)
            if dist <= max_dist:
                return to_pos
            ratio = max_dist / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dim))

        def sample_in_ellipsoid():
            # Informed sampling within ellipsoid when a solution found, else uniform random
            if best_cost == math.inf:
                # No solution yet: uniform random in bounds
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))
            else:
                c_min = math.dist(start_position, goal_position)
                if best_cost < c_min:
                    # Numerical stability guard
                    c_max = c_min + 1e-6
                else:
                    c_max = best_cost

                # Ellipsoid parameters
                center = tuple((start_position[d] + goal_position[d]) / 2 for d in range(dim))

                # Unit vector from start to goal
                line_vec = tuple(goal_position[d] - start_position[d] for d in range(dim))
                length = math.dist(start_position, goal_position)
                if length == 0:
                    return tuple(random.uniform(0, bounds[d]) for d in range(dim))
                e_axis = tuple(v / length for v in line_vec)

                # Radii of ellipsoid: major axis = best cost / 2, minor axes = sqrt(best_cost^2 - c_min^2)/2
                r1 = c_max / 2
                r2 = math.sqrt(abs(c_max**2 - c_min**2)) / 2 if c_max > c_min else 0.0

                # Sample random point in unit n-ball
                while True:
                    sample = [random.uniform(-1,1) for _ in range(dim)]
                    norm = math.sqrt(sum(x**2 for x in sample))
                    if norm <= 1 and norm > 0:
                        unit_ball = [x / norm for x in sample]
                        break

                # Scale by ellipsoid radii
                scaled = [0.0]*dim
                scaled[0] = unit_ball[0] * r1
                for i in range(1, dim):
                    scaled[i] = unit_ball[i] * r2

                # Rotating scaled point to align with major axis e_axis
                # Create rotation matrix via Gram-Schmidt for 2D and 3D
                # For 2D: just rotate
                if dim == 2:
                    # Rotation matrix: first col = e_axis, second perpendicular
                    ex, ey = e_axis
                    perp = (-ey, ex)
                    point = tuple(center[d] + scaled[0]*ex + scaled[1]*perp[d] for d in range(dim))
                elif dim == 3:
                    # Build orthonormal basis with e_axis as x-axis
                    ex = list(e_axis)
                    # Find a vector not parallel to ex for y-axis
                    if abs(ex[0]) < 0.9:
                        temp = [1,0,0]
                    else:
                        temp = [0,1,0]
                    # y-axis
                    ey = [temp[i] - sum(temp[j]*ex[j] for j in range(dim))*ex[i] for i in range(dim)]
                    ey_norm = math.sqrt(sum(x**2 for x in ey))
                    if ey_norm == 0:
                        # Degenerate case: fallback uniform sample
                        return tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    ey = [x/ey_norm for x in ey]
                    # z-axis = cross product
                    ez = [ex[1]*ey[2] - ex[2]*ey[1],
                          ex[2]*ey[0] - ex[0]*ey[2],
                          ex[0]*ey[1] - ex[1]*ey[0]]
                    point = [center[d] + scaled[0]*ex[d] + scaled[1]*ey[d] + scaled[2]*ez[d] for d in range(dim)]
                    point = tuple(point)
                else:
                    # Higher dims fallback uniform
                    point = tuple(center[d] + scaled[d] for d in range(dim))

                # Clamp inside bounds to avoid overshoot
                clamped = tuple(min(max(point[d],0), bounds[d]) for d in range(dim))
                return clamped

        def get_nearby_nodes(tree, new_node, radius):
            return [node for node in tree if node.position and node.distance(new_node) <= radius]

        def can_connect(pos_a, pos_b):
            if not in_bounds(pos_b):
                return False
            if self._is_in_obstacle(pos_b, obstacles, is_3d):
                return False
            if self._is_edge_in_obstacle(pos_a, pos_b, obstacles, is_3d):
                return False
            return True

        # Core planning loop
        for itr in range(self.max_iter):
            for tree_a, tree_b in [(start_tree, goal_tree), (goal_tree, start_tree)]:
                # Sample selection: goal biasing and informed sampling
                if random.random() < self.goal_sample_rate:
                    sample = goal_position if tree_a is start_tree else start_position
                else:
                    sample = sample_in_ellipsoid()

                if not in_bounds(sample):
                    continue
                if self._is_in_obstacle(sample, obstacles, is_3d):
                    continue

                # Find nearest node in tree_a
                nearest = min(tree_a, key=lambda n: math.dist(n.position, sample))
                new_pos = steer(nearest.position, sample, self.step_size)

                if not can_connect(nearest.position, new_pos):
                    continue

                new_cost = nearest.cost + math.dist(nearest.position, new_pos)
                new_node = Node(new_pos, parent=nearest, cost=new_cost)
                nearest.add_child(new_node)
                tree_a.append(new_node)
                nodes.append(new_node)
                edges.append((nearest, new_node))

                # Rewiring step for path quality improvement
                neighbors = get_nearby_nodes(tree_a, new_node, self.rewire_radius)
                for neighbor in neighbors:
                    if neighbor is new_node.parent:
                        continue
                    potential_cost = new_node.cost + new_node.distance(neighbor)
                    if potential_cost < neighbor.cost:
                        # Check edge collision before rewiring
                        if can_connect(new_node.position, neighbor.position):
                            # Rewire neighbor
                            if neighbor.parent:
                                try:
                                    neighbor.parent.children.remove(neighbor)
                                except ValueError:
                                    pass
                            neighbor.parent = new_node
                            neighbor.cost = potential_cost
                            new_node.add_child(neighbor)

                # Attempt connecting tree_a's new_node and tree_b
                nearest_b = min(tree_b, key=lambda n: math.dist(n.position, new_pos))
                dist_to_b = math.dist(new_pos, nearest_b.position)
                if dist_to_b <= self.step_size:
                    if can_connect(new_pos, nearest_b.position):
                        # Create bridging node from new_node to nearest_b.position in tree_a
                        conn_node = Node(nearest_b.position, parent=new_node, cost=new_node.cost + dist_to_b)
                        new_node.add_child(conn_node)
                        tree_a.append(conn_node)
                        nodes.append(conn_node)
                        edges.append((new_node, conn_node))

                        # Path cost from start to goal candidate
                        start_root_node = conn_node if tree_a is start_tree else nearest_b
                        goal_root_node = nearest_b if tree_b is goal_tree else conn_node

                        total_cost = start_root_node.cost + goal_root_node.cost

                        if total_cost < best_cost:
                            best_cost = total_cost
                            connection_node_start = conn_node if tree_a is start_tree else nearest_b
                            connection_node_goal = nearest_b if tree_b is goal_tree else conn_node

            if best_cost < math.inf:
                # Early termination if desired: can comment out to run full iterations
                break

        success_state = best_cost < math.inf and connection_node_start is not None and connection_node_goal is not None
        extracted_path: List[Tuple[float, ...]] = []

        if success_state:
            # Extract and merge paths from the connection nodes
            path_start = []
            node = connection_node_start
            while node:
                path_start.append(node.position)
                node = node.parent
            path_start.reverse()

            path_goal = []
            node = connection_node_goal
            while node:
                path_goal.append(node.position)
                node = node.parent

            extracted_path = path_start + path_goal[1:]  # Avoid duplicate connection node position

            # Optional smoothing pass: shortcutting using collision checks to reduce path points
            extracted_path = self._shortcut_path(extracted_path, obstacles, is_3d, in_bounds)

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _shortcut_path(self, path, obstacles, is_3d, in_bounds_func):
        # Iteratively try to shortcut path by bypassing intermediate points
        if len(path) < 3:
            return path
        new_path = [path[0]]
        idx = 0
        while idx < len(path) - 1:
            next_idx = len(path) - 1
            while next_idx > idx + 1:
                if in_bounds_func(path[next_idx]) and not self._is_edge_in_obstacle(path[idx], path[next_idx], obstacles, is_3d):
                    break
                next_idx -= 1
            new_path.append(path[next_idx])
            idx = next_idx
        return new_path

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0,
                 max_neighbor_radius: float = 30.0,
                 prune_interval: int = 200,
                 prune_threshold: int = 300,
                 smoothing_iterations: int = 100,
                 collision_check_resolution: float = 1.0,
                 goal_sample_rate: float = 0.05):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> 'PlannerResult':
        import random
        import math
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # clamp inside bounds
            clamped = tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))
            return clamped

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_i in range(self.dim):
                        delta = p[d_i] - pt[d_i]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            import numpy as np

            # With chance, sample goal directly for bias
            if random.random() < self.goal_sample_rate:
                if not is_in_obstacle(goal):
                    return goal

            if c_best == float('inf') or c_best < c_min * 1.00001:
                for _ in range(10):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            c1 = np.array(goal) - np.array(start)
            a1 = c1 / c_min if c_min > 0 else np.eye(dim)[0]

            id_mat = np.eye(dim)
            v = a1 - id_mat[0]
            norm_v = np.linalg.norm(v)
            if norm_v > 1e-10:
                v /= norm_v
                rot = id_mat - 2 * np.outer(v, v)  # Householder reflection
            else:
                rot = id_mat

            r1 = c_best / 2.0
            term = r1 * r1 - (c_min / 2.0) ** 2
            r_others = math.sqrt(term) if term > 0 else 0.0
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(100):
                x_ball = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x_ball) <= 1.0:
                    sample = rot @ (x_ball * radii) + center
                    p = tuple(float(coord) for coord in sample)
                    if in_bounds(p) and not is_in_obstacle(p):
                        return p

            # fallback random sampling
            for _ in range(10):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            key_rev = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if key_rev in edge_collision_cache:
                return not edge_collision_cache[key_rev]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)
            edge_collision_cache[key] = collision
            return not collision

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i+1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            removed = 0
            to_remove = []
            for node in nodes:
                if node is root_start or node is root_goal:
                    continue
                heuristic = dist(node.position, goal_pos)
                if node.cost + heuristic >= best_cost:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate tree to extend
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near:
                cost_candidate = node.cost + dist(node.position, new_pos)
                if cost_candidate + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_candidate
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewiring neighbors for cost improvement
            for neighbor in near:
                if neighbor is best_parent:
                    continue
                c_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if c_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, c_via_new)
                    edges.append((new_node, neighbor))

            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    c_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if c_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, c_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        if is_3d:
            pz = pos[2]
            for obs in obstacles:
                x, y, z, w, h, d = obs
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
        else:
            for obs in obstacles:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
