class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0, goal_sample_rate: float = 0.15, rewire_radius: float = 10.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.rewire_radius = rewire_radius

    def plan(self, map) -> "PlannerResult":
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        is_3d = len(bounds) == 3

        start_tree = [Node(start_pos)]
        goal_tree = [Node(goal_pos)]
        nodes = start_tree + goal_tree
        edges = []

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(len(bounds)))

        def steer(from_pos, to_pos, step_size):
            dist = math.dist(from_pos, to_pos)
            if dist <= step_size:
                return to_pos
            ratio = step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(len(from_pos)))

        def near_nodes(tree, position, radius):
            return [n for n in tree if math.dist(n.position, position) <= radius]

        def rewire(tree, new_node, obstacles, is_3d):
            neighbors = near_nodes(tree, new_node.position, self.rewire_radius)
            for neighbor in neighbors:
                if neighbor is new_node.parent:
                    continue
                # Attempt rewire: check if new_node->neighbor path is better and collision free
                dist_new_to_neighbor = math.dist(new_node.position, neighbor.position)
                proposed_cost = new_node.cost + dist_new_to_neighbor
                if proposed_cost < neighbor.cost:
                    if (not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d)):
                        # Rewire neighbor
                        if neighbor.parent:
                            neighbor.parent.remove_child(neighbor)
                        neighbor.parent = new_node
                        neighbor.cost = proposed_cost
                        new_node.add_child(neighbor)
                        # Update costs recursively downstream
                        self._update_costs(neighbor)

        start_grows = True # flag to alternate expansion

        for itr in range(self.max_iter):
            for tree_a, tree_b, tree_a_start_flag in [(start_tree, goal_tree, start_grows), (goal_tree, start_tree, not start_grows)]:
                # Sample with goal biasing: towards goal if extending start_tree, else towards start
                if (random.random() < self.goal_sample_rate):
                    sample = goal_pos if tree_a_start_flag else start_pos
                else:
                    sample = tuple(random.uniform(0, bounds[d]) for d in range(len(bounds)))

                if not in_bounds(sample) or self._is_in_obstacle(sample, obstacles, is_3d):
                    continue

                nearest = min(tree_a, key=lambda n: math.dist(n.position, sample))
                new_pos = steer(nearest.position, sample, self.step_size)

                if not in_bounds(new_pos):
                    continue
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    continue
                if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
                    continue

                new_node = Node(new_pos, parent=nearest, cost=nearest.cost + math.dist(nearest.position, new_pos))
                nearest.add_child(new_node)
                tree_a.append(new_node)
                nodes.append(new_node)
                edges.append((nearest, new_node))

                # Rewire nearby nodes to improve path costs in this tree
                rewire(tree_a, new_node, obstacles, is_3d)

                # Incremental attempts to connect tree_b toward new_node
                # Grow tree_b step-wise from its nearest node toward new_node
                connect_node_b = min(tree_b, key=lambda n: math.dist(n.position, new_pos))
                current_b_node = connect_node_b
                reached_connection = False

                while True:
                    next_pos = steer(current_b_node.position, new_pos, self.step_size)
                    if not in_bounds(next_pos):
                        break
                    if self._is_in_obstacle(next_pos, obstacles, is_3d):
                        break
                    if self._is_edge_in_obstacle(current_b_node.position, next_pos, obstacles, is_3d):
                        break

                    temp_node = Node(next_pos, parent=current_b_node, cost=current_b_node.cost + math.dist(current_b_node.position, next_pos))
                    current_b_node.add_child(temp_node)
                    tree_b.append(temp_node)
                    nodes.append(temp_node)
                    edges.append((current_b_node, temp_node))
                    current_b_node = temp_node

                    # Rewire nearby nodes in tree_b
                    rewire(tree_b, current_b_node, obstacles, is_3d)

                    dist_to_new_node = math.dist(current_b_node.position, new_pos)
                    if dist_to_new_node <= self.step_size:
                        # Final connection step - connect current_b_node to new_node
                        if not self._is_edge_in_obstacle(current_b_node.position, new_pos, obstacles, is_3d):
                            final_node = Node(new_pos, parent=current_b_node, cost=current_b_node.cost + dist_to_new_node)
                            current_b_node.add_child(final_node)
                            tree_b.append(final_node)
                            nodes.append(final_node)
                            edges.append((current_b_node, final_node))
                            # Merge trees at new_node and final_node
                            # Extract path: from start root (depending on which tree new_node is) and from goal root
                            if tree_a is start_tree:
                                path = self._extract_path(new_node, final_node)
                            else:
                                path = self._extract_path(final_node, new_node)

                            # Smooth path
                            path = self._smooth_path(path, obstacles, is_3d)

                            return PlannerResult(True, path, nodes, edges)
                        break  # cannot connect edge

            start_grows = not start_grows

        return PlannerResult(False, [], nodes, edges)

    def _update_costs(self, node):
        # Recursively update cost of children based on parent's cost
        for child in node.children:
            dist = math.dist(node.position, child.position)
            new_cost = node.cost + dist
            if new_cost < child.cost:
                child.cost = new_cost
                self._update_costs(child)

    def _extract_path(self, node_a: Node, node_b: Node):
        path_a = []
        cur = node_a
        while cur:
            path_a.append(cur.position)
            cur = cur.parent
        path_a.reverse()

        path_b = []
        cur = node_b
        while cur:
            path_b.append(cur.position)
            cur = cur.parent

        return path_a + path_b[1:]  # avoid duplication

    def _smooth_path(self, path: "List[Tuple[float, ...]]", obstacles, is_3d):
        if len(path) < 3:
            return path
        smoothed = [path[0]]
        i = 0
        while i < len(path)-1:
            j = len(path) - 1
            while j > i +1:
                if not self._is_edge_in_obstacle(path[i], path[j], obstacles, is_3d):
                    break
                j -= 1
            smoothed.append(path[j])
            i = j
        return smoothed

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0,
                 max_neighbor_radius: float = 30.0,
                 prune_interval: int = 200,
                 prune_threshold: int = 300,
                 smoothing_iterations: int = 150,
                 collision_check_resolution: float = 1.0,
                 goal_sample_rate: float = 0.15):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp to bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + \
                      (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        # KD-Tree implementation optimized for nearest and radius queries
        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            # Samples inside ellipsoidal informed set or uniform if no solution found
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))
            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            u = a1_np - np.array([1 if d == 0 else 0 for d in range(dim)])
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            val = max(r1 * r1 - (c_min / 2) * (c_min / 2), 0)
            r_others = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x <= 1 and norm_x > 1e-15:
                    radius_factor = np.random.uniform(0, 1) ** (1 / dim)
                    x_scaled = (x / norm_x) * radius_factor
                    x_scaled = x_scaled * radii
                    p = rot @ x_scaled + np.array(center)
                    p_tuple = tuple(float(coord) for coord in p)
                    if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                        return p_tuple

            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            if (p1, p2) in edge_collision_cache:
                return not edge_collision_cache[(p1, p2)]
            if (p2, p1) in edge_collision_cache:
                return not edge_collision_cache[(p2, p1)]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[(p1, p2)] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[(p1, p2)] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            heuristic = lambda pos: dist(pos, goal_pos)
            to_remove = []
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(node)
                except ValueError:
                    pass
                if node in tree_start:
                    tree_start.remove(node)
                if node in tree_goal:
                    tree_goal.remove(node)
            return len(to_remove)

        # Initialization
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Balance tree extension: smaller tree first
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near_nodes_a = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes_a:
                candidate_cost = node.cost + dist(node.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors around new node in tree_a
            for neighbor in near_nodes_a:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            # Incrementally connect the other tree towards new_node
            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except (ValueError, AttributeError):
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float = 5.0, max_neighbor_radius: float = 30.0,
                 prune_interval: int = 150, prune_threshold: int = 250,
                 smoothing_trials: int = 100, collision_check_resolution: float = 1.0,
                 goal_sample_rate: float = 0.05):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_trials = smoothing_trials
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate  # added low probability goal bias in sampling

    def plan(self, map) -> PlannerResult:
        import math
        import random
        import numpy as np

        bounds = map.size                  # Tuple[int, ...]: (W,H) or (W,H,D)
        start_position = map.start         # Tuple[float, ...] (W,H) or (W,H,D)
        goal_position = map.goal           # Tuple[float, ...] (W,H) or (W,H,D)
        obstacles = map.obstacles          # Rectangular blocks: 2D=(x,y,w,h), 3D=(x,y,z,w,h,d)

        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d_] + ratio * (to_p[d_] - from_p[d_]) for d_ in range(dim))
            # Clamp to bounds
            return tuple(max(0, min(new_p[d_], bounds[d_])) for d_ in range(dim))

        # KDTree Helper (brute force)
        class KDTree:
            def __init__(self, points_nodes):
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                res = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        res.append(self.nodes[i])
                return res

        edge_collision_cache = {}

        def can_connect(p1, p2) -> bool:
            key = (p1, p2) if p1 < p2 else (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos) -> bool:
            return in_bounds(pos) and not self._is_in_obstacle(pos, obstacles, is_3d)

        # Adaptive informed ellipsoidal sampling around best path and goal bias
        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < 0.1:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not self._is_in_obstacle(p, obstacles, is_3d):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])
            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)  # Householder reflection
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r2 = math.sqrt(max(r1*r1 - (c_min/2)**2, 0))
            radii = np.array([r1] + [r2]*(dim-1))

            for _ in range(150):
                x = np.random.uniform(-1,1,dim)
                norm_x = np.linalg.norm(x)
                if norm_x < 1e-10 or norm_x > 1:
                    continue
                x = x / norm_x
                x = x * np.power(np.random.uniform(0,1), 1/dim)  # Uniform in unit n-ball
                p = rot @ (x * radii) + center
                p_tuple = tuple(float(coord) for coord in p)
                if in_bounds(p_tuple) and not self._is_in_obstacle(p_tuple, obstacles, is_3d):
                    # 5% goal bias inside ellipsoid to enhance direct paths
                    if random.random() < self.goal_sample_rate:
                        return goal_position
                    return p_tuple

            # Fallback uniform sampling
            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p
            return p

        # Sample near best known path with Gaussian noise fallback to informed_sample
        def adaptive_sample(best_path, c_best, c_min):
            if best_path and random.random() < 0.75:
                for _ in range(50):
                    base_point = best_path[random.randint(0, len(best_path)-1)]
                    noise = np.random.normal(0, self.step_size*0.4, dim)
                    sample_point = tuple(base_point[d] + noise[d] for d in range(dim))
                    if in_bounds(sample_point) and not self._is_in_obstacle(sample_point, obstacles, is_3d):
                        return sample_point
            # Small goal sample bias outside best_path sampling
            if random.random() < self.goal_sample_rate:
                return goal_position
            return informed_sample(c_best, c_min, start_position, goal_position)

        def neighbor_radius(n_nodes, iteration):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            const_factor = max(n_nodes,1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration/self.max_iter)**0.85)
            r_nodes = self.min_neighbor_radius + \
                      (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(const_factor+1)/const_factor)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        # Shortcut smoothing on final path by attempting to replace path segments with direct edges
        def shortcut_path(path):
            if len(path) < 3:
                return path[:]
            smooth = path[:]
            for _ in range(self.smoothing_trials):
                if len(smooth) < 3:
                    break
                i = random.randint(0, len(smooth)-3)
                j = random.randint(i+2, len(smooth)-1)
                p1, p2 = smooth[i], smooth[j]
                if can_connect(p1, p2):
                    smooth = smooth[:i+1] + smooth[j:]
            return smooth

        def prune_nodes(tree_list, best_cost, root_s, root_g):
            removed = 0
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_position)
            for node in tree_list:
                if node in (root_s, root_g):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except (ValueError, AttributeError):
                        pass
                try:
                    tree_list.remove(node)
                except ValueError:
                    pass
                removed += 1
            return removed

        root_start = Node(start_position, cost=0.0)
        root_goal = Node(goal_position, cost=0.0)
        tree_start = [root_start]
        tree_goal = [root_goal]
        nodes = [root_start, root_goal]
        edges = []

        c_min = dist(start_position, goal_position)
        c_best = float('inf')
        best_path: List[Tuple[float, ...]] = []
        success_state = False

        for it in range(self.max_iter):
            q_rand = adaptive_sample(best_path, c_best, c_min)

            # Alternate growth: extend smaller tree first
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius_a = neighbor_radius(len(nodes), it)
            near_nodes_a = kdtree_a.radius_search(new_pos, radius_a)

            # Parent selection minimizing cost
            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes_a:
                cost_cand = node.cost + dist(node.position, new_pos)
                if cost_cand + 1e-14 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_cand
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors if better cost found
            for node in near_nodes_a:
                if node is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, node.position)
                if new_cost + 1e-14 < node.cost and can_connect(new_node.position, node.position):
                    try:
                        edges.remove((node.parent, node))
                    except ValueError:
                        pass
                    node.update_parent(new_node, new_cost)
                    edges.append((new_node, node))

            # Incrementally connect tree_b toward new_node
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            max_steps = int(dist(nearest_b.position, new_node.position) / self.step_size) + 2
            last_node_b = nearest_b
            connected = False

            for _step in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break
                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                radius_b = neighbor_radius(len(nodes), it)
                near_nodes_b = kdtree_b.radius_search(next_pos_b, radius_b)
                for nbr_b in near_nodes_b:
                    if nbr_b is last_node_b:
                        continue
                    new_cost_b = new_node_b.cost + dist(new_node_b.position, nbr_b.position)
                    if new_cost_b + 1e-14 < nbr_b.cost and can_connect(new_node_b.position, nbr_b.position):
                        try:
                            edges.remove((nbr_b.parent, nbr_b))
                        except ValueError:
                            pass
                        nbr_b.update_parent(new_node_b, new_cost_b)
                        edges.append((new_node_b, nbr_b))

                # Check if connection possible within step size
                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    cost_connect = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connect_node = Node(new_node.position, parent=new_node_b, cost=cost_connect)
                    new_node_b.children.append(connect_node)
                    tree_b.append(connect_node)
                    nodes.append(connect_node)
                    edges.append((new_node_b, connect_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connect_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connect_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]
                    if cost_connect + 1e-14 < c_best:
                        c_best = cost_connect
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                break

            # Periodic pruning to remove nodes unlikely to improve solution
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_nodes(tree_start, c_best, root_start, root_goal)
                prune_nodes(tree_goal, c_best, root_start, root_goal)
                edge_collision_cache.clear()

        extracted_path = best_path if success_state else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        if is_3d:
            pz = pos[2]
            for obs in obstacles:
                x, y, z, w, h, d = obs
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
        else:
            for obs in obstacles:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D or 3D
        self.parent = parent            # Node or None
        self.cost = cost                # Cost from root
        self.children = []
        self.valid = True               # For collision status

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        node, path = self, []
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = 5.0
        self.max_neighbor_radius = 30.0
        self.prune_interval = 200
        self.prune_threshold = 300
        self.smoothing_iterations = 100
        self.collision_check_resolution = 1.0

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes,1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        import numpy as np
        import random

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []
        success_state = False
        extracted_path = []

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            key_rev = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if key_rev in edge_collision_cache:
                return not edge_collision_cache[key_rev]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
