            return True

        def informed_sample(c_best_sample, c_min_sample, start, goal):
            if c_best_sample == float('inf') or c_best_sample < c_min_sample * 1.00001:
                for _ in range(10):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
