            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min_sample for d in range(dim))

            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            v = a1_np - np.array([1] + [0]*(dim - 1))
            norm_v = np.linalg.norm(v)
            if norm_v > 1e-10:
                v /= norm_v
                rot = id_mat - 2 * np.outer(v, v)
            else:
                rot = id_mat

            r1 = c_best_sample / 2.0
            r_others = math.sqrt(max(r1 * r1 - (c_min_sample / 2) ** 2, 0))
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(100):
                x_ball = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x_ball) <= 1.0:
                    sample = rot @ (x_ball * radii) + np.array(center)
                    p = tuple(float(coord) for coord in sample)
                    if in_bounds(p) and not is_in_obstacle(p):
