class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter=5000, step_size=5.0, goal_sample_rate=0.1):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map):
        import time
        import math
        import random

        start_time = time.monotonic()

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            # Euclidean distance
            return math.dist(a, b)

        def sample():
            # Goal biased sampling: with some probability sample the goal
            if random.random() < self.goal_sample_rate:
                return goal_pos
            # Otherwise sample uniformly inside bounds but avoid obstacles via rejection
            for _ in range(100):
                if is_3d:
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                else:
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p
            # Fallback if no free sample found in 100 tries (unlikely)
            return goal_pos

        def steer(from_pos, to_pos):
            vec = tuple(to_pos[d] - from_pos[d] for d in range(dim))
            norm = dist(from_pos, to_pos)
            if norm <= self.step_size:
                new_pos = to_pos
            else:
                scale = self.step_size / norm
                new_pos = tuple(from_pos[d] + vec[d] * scale for d in range(dim))
            if in_bounds(new_pos):
                return new_pos
            # Clip to bounds if numerics cause issues
            clipped = tuple(min(max(new_pos[d], 0), bounds[d]) for d in range(dim))
            return clipped

        def line_cost(a, b):
            return dist(a, b)

        def adaptive_radius(n):
            # Adaptive rewiring radius: γ*(log(n)/n)^(1/d); γ chosen heuristically
            gamma = 50.0  # Tuned constant, can be adjusted
            if n < 2:
                return self.step_size * 5.0
            return min(gamma * (math.log(n)/n) ** (1/dim), self.step_size * 10)

        start_root = Node(start_pos, parent=None, cost=0.0)
        goal_root = Node(goal_pos, parent=None, cost=0.0)

        tree_start = [start_root]
        tree_goal = [goal_root]

        nodes = [start_root, goal_root]
        edges = []

        best_cost = float('inf')
        best_start_node = None
        best_goal_node = None
        success = False

        for i in range(self.max_iter):
            if time.monotonic() - start_time > 30:
                break

            # Alternate between trees: grow_start -> grow_goal -> grow_start etc
            if i % 2 == 0:
                tree_from, tree_to = tree_start, tree_goal
            else:
                tree_from, tree_to = tree_goal, tree_start

            sample_point = sample()
            nearest = min(tree_from, key=lambda n: dist(n.position, sample_point))
            new_pos = steer(nearest.position, sample_point)

            # Validate new_pos node collision and edge collision
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                continue
            if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
                continue

            # Adaptive rewiring radius
            r = adaptive_radius(len(nodes))

            # Find neighbors in tree_from within radius r
            neighbors = [n for n in tree_from if dist(n.position, new_pos) <= r]

            # Choose best parent minimizing cost + distance via collision-free edge
            min_cost = nearest.cost + line_cost(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                cost_through_nbr = nbr.cost + line_cost(nbr.position, new_pos)
                if cost_through_nbr < min_cost:
                    if not self._is_edge_in_obstacle(nbr.position, new_pos, obstacles, is_3d):
                        min_cost = cost_through_nbr
                        best_parent = nbr

            new_node = Node(new_pos)
            new_node.update_parent(best_parent, min_cost)

            tree_from.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors if going through new_node improves cost
            for nbr in neighbors:
                if nbr is best_parent:
                    continue
                cost_through_new = new_node.cost + line_cost(new_node.position, nbr.position)
                if cost_through_new < nbr.cost:
                    if not self._is_edge_in_obstacle(new_node.position, nbr.position, obstacles, is_3d):
                        try:
                            edges.remove((nbr.parent, nbr))
                        except Exception:
                            pass
                        nbr.update_parent(new_node, cost_through_new)
                        edges.append((new_node, nbr))

            # Try connecting the other tree to new_node to check if paths can be connected
            nearest_to = min(tree_to, key=lambda n: dist(n.position, new_node.position))
            while True:
                connect_pos = steer(nearest_to.position, new_node.position)
                # Check collision for edge
                if self._is_in_obstacle(connect_pos, obstacles, is_3d):
                    break
                if self._is_edge_in_obstacle(nearest_to.position, connect_pos, obstacles, is_3d):
                    break
                # Add node to tree_to
                new_to_node = Node(connect_pos)
                new_to_node.update_parent(nearest_to, nearest_to.cost + line_cost(nearest_to.position, connect_pos))
                tree_to.append(new_to_node)
                nodes.append(new_to_node)
                edges.append((nearest_to, new_to_node))

                if dist(connect_pos, new_node.position) <= self.step_size:
                    # Check final edge to new_node
                    if not self._is_edge_in_obstacle(connect_pos, new_node.position, obstacles, is_3d):
                        final_connect = Node(new_node.position)
                        final_connect.update_parent(new_to_node, new_to_node.cost + dist(new_node.position, connect_pos))
                        tree_to.append(final_connect)
                        nodes.append(final_connect)
                        edges.append((new_to_node, final_connect))

                        # Found a path connecting both trees
                        total_cost = final_connect.cost + new_node.cost
                        if total_cost < best_cost:
                            best_cost = total_cost
                            success = True
                            # We return combined path by remembering nodes connecting trees
                            best_start_node = new_node if tree_from is tree_start else final_connect
                            best_goal_node = final_connect if tree_to is tree_goal else new_node
                        break
                    else:
                        break
                nearest_to = new_to_node

            if success:
                break

        extracted_path = []
        if success and best_start_node and best_goal_node:
            path_start = best_start_node.path_from_root()
            path_goal = best_goal_node.path_from_root()
            # path_goal is from goal_root to connection, reverse except overlap connection node
            extracted_path = path_start + path_goal[-2::-1]

            # Perform path shortcutting smoothing
            extracted_path = self._shortcut_path(extracted_path, obstacles, is_3d, bounds)

        return PlannerResult(
            success=success,
            path=extracted_path,
            nodes=nodes,
            edges=edges,
        )

    def _shortcut_path(self, path, obstacles, is_3d, bounds):
        import math
        def can_connect(a, b):
            if a == b:
                return True
            if self._is_edge_in_obstacle(a, b, obstacles, is_3d):
                return False
            return True

        if len(path) < 3:
            return path

        # Iteratively try to shortcut path by connecting farther points directly
        shortened = [path[0]]
        i = 0
        while i < len(path) - 1:
            j = len(path) - 1
            while j > i + 1:
                if can_connect(path[i], path[j]):
                    break
                j -= 1
            shortened.append(path[j])
            i = j
        return shortened

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent: Optional["Node"]=None, cost: float=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children: List["Node"] = []
        self.valid = True

    def add_child(self, child: "Node"):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child: "Node"):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: Optional["Node"], new_cost: float):
        if self.parent is not new_parent:
            if self.parent is not None:
                self.parent.remove_child(self)
            self.parent = new_parent
            self.cost = new_cost
            if new_parent is not None:
                new_parent.children.append(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int=6000, step_size: float=6.0,
                 goal_sample_rate: float=0.15, gamma_rrt_star: float=50.0,
                 max_neighbor_radius: float=40.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.gamma_rrt_star = gamma_rrt_star
        self.max_neighbor_radius = max_neighbor_radius

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        start_time = time.monotonic()
        time_limit = 30.0

        nodes: List[Node] = []
        edges: List[Tuple[Node, Node]] = []

        def in_bounds(p):
            for i in range(dim):
                if p[i] < 0 or p[i] > bounds[i]:
                    return False
            return True

        def is_free_node(p):
            if not in_bounds(p):
                return False
            return not self._is_in_obstacle(p, obstacles, is_3d)

        def dist(p1, p2):
            return math.dist(p1, p2)

        def steer(from_pos, to_pos, max_dist):
            d = dist(from_pos, to_pos)
            if d <= max_dist:
                return to_pos
            ratio = max_dist / d
            return tuple(from_pos[i] + ratio * (to_pos[i] - from_pos[i]) for i in range(dim))

        def sample_free(best_cost: float):
            # Goal biased informed sampling
            if best_cost < float('inf'):
                c_best = best_cost
                c_min = dist(start_pos, goal_pos)
                if c_best < c_min:
                    c_best = c_min + 1e-3
                # Sample inside an ellipsoid between start and goal
                center = tuple((s + g) / 2.0 for s, g in zip(start_pos, goal_pos))
                diff = tuple(g - s for s, g in zip(start_pos, goal_pos))
                unit_vec = tuple(d / c_min for d in diff)
                while True:
                    if random.random() < self.goal_sample_rate:
                        p = goal_pos
                    else:
                        # Sample inside unit n-ball scaled to current cost radius
                        while True:
                            x = [random.uniform(-1,1) for _ in range(dim)]
                            norm_x = math.sqrt(sum(v*v for v in x))
                            if norm_x <= 1:
                                break
                        scale = c_best / 2.0
                        sample = tuple(center[i] + scale * x[i] for i in range(dim))
                        p = sample
                    if in_bounds(p) and is_free_node(p):
                        return p
            else:
                # Uniform random sampling
                while True:
                    p = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                    if is_free_node(p):
                        return p

        def nearest(tree: List[Node], point: Tuple[float, ...]) -> Node:
            best_node = tree[0]
            best_dist = dist(best_node.position, point)
            for node in tree[1:]:
                d = dist(node.position, point)
                if d < best_dist:
                    best_dist = d
                    best_node = node
            return best_node

        def near_nodes(tree: List[Node], point: Tuple[float, ...], n_nodes: int) -> List[Node]:
            if n_nodes < 2:
                return []
            r = self.gamma_rrt_star * (math.log(n_nodes) / n_nodes) ** (1/dim)
            radius = min(self.max_neighbor_radius, max(self.step_size * 2.0, r))
            return [node for node in tree if dist(node.position, point) <= radius]

        def lazy_collision_check_edge(parent_pos, new_pos):
            # Lazy collision: Check only after rewiring to reduce calls
            # Here we'll do a coarse resolution check
            length = dist(parent_pos, new_pos)
            resolution = min(1.0, self.step_size * 0.5)
            steps = max(1, int(length / resolution))
            for i in range(steps + 1):
                interp = tuple(parent_pos[d] + (new_pos[d] - parent_pos[d]) * i / steps for d in range(dim))
                if self._is_in_obstacle(interp, obstacles, is_3d):
                    return False
            return True

        def choose_parent_and_add(tree: List[Node], new_pos: Tuple[float, ...], nodes_ref: List[Node], edges_ref: List[Tuple[Node,Node]]) -> Optional[Tuple[Node,List[Node]]]:
            nearest_node_ = nearest(tree, new_pos)
            neighbors = near_nodes(tree, new_pos, len(tree))
            min_cost = nearest_node_.cost + dist(nearest_node_.position, new_pos)
            best_parent = nearest_node_
            for nb in neighbors:
                if nb is nearest_node_:
                    continue
                new_cost = nb.cost + dist(nb.position, new_pos)
                if new_cost < min_cost and lazy_collision_check_edge(nb.position, new_pos):
                    min_cost = new_cost
                    best_parent = nb
            if not lazy_collision_check_edge(best_parent.position, new_pos):
                return None
            new_node = Node(new_pos, None, min_cost)
            best_parent.children.append(new_node)
            new_node.parent = best_parent
            nodes_ref.append(new_node)
            tree.append(new_node)
            edges_ref.append((best_parent, new_node))
            return (new_node, neighbors)

        def rewire(node: Node, neighbors: List[Node], edges_ref: List[Tuple[Node, Node]]):
            n_updated = []
            for nb in neighbors:
                if nb is node:
                    continue
                potential_cost = node.cost + dist(node.position, nb.position)
                if potential_cost + 1e-15 < nb.cost:
                    if lazy_collision_check_edge(node.position, nb.position):
                        # Remove old parent edge
                        if nb.parent is not None:
                            try:
                                edges_ref.remove((nb.parent, nb))
                            except ValueError:
                                pass
                            nb.parent.children.remove(nb)
                        nb.parent = node
                        nb.cost = potential_cost
                        node.children.append(nb)
                        edges_ref.append((node, nb))
                        n_updated.append(nb)
            return n_updated

        def extract_path(node_start: Node, node_goal: Node) -> List[Tuple[float,...]]:
            path_start = node_start.path_from_root()
            path_goal = node_goal.path_from_root()
            if path_start[-1] == path_goal[-1]:
                path_goal = path_goal[:-1]
            return path_start + path_goal[::-1]

        def shortcut_path(path_points: List[Tuple[float,...]]) -> List[Tuple[float,...]]:
            # Iterative shortcutting, max 20 iterations
            if len(path_points) < 3:
                return path_points
            for _ in range(20):
                changed = False
                i = 0
                while i < len(path_points) - 2:
                    j = len(path_points) -1
                    while j > i+1:
                        if self._is_edge_in_obstacle(path_points[i], path_points[j], obstacles, is_3d) == False:
                            # Remove intermediate points between i and j
                            del path_points[i+1:j]
                            changed = True
                            break
                        j -= 1
                    i += 1
                if not changed:
                    break
            return path_points

        # Initialize trees
        start_root = Node(start_pos, None, 0.0)
        goal_root = Node(goal_pos, None, 0.0)
        nodes.extend([start_root, goal_root])
        tree_start = [start_root]
        tree_goal = [goal_root]

        best_cost = float('inf')
        best_path_nodes: List[Tuple[float,...]] = []
        success = False

        for iteration in range(self.max_iter):
            now = time.monotonic()
            if now - start_time > time_limit:
                break

            # Goal biased informed sampling
            sample = sample_free(best_cost)

            # Alternate tree expansion (start and goal)
            if iteration % 2 == 0:
                tree_a, tree_b = tree_start, tree_goal
            else:
                tree_a, tree_b = tree_goal, tree_start

            nearest_node_a = nearest(tree_a, sample)
            new_pos = steer(nearest_node_a.position, sample, self.step_size)
            if not is_free_node(new_pos):
                continue
            result = choose_parent_and_add(tree_a, new_pos, nodes, edges)
            if result is None:
                continue
            new_node, neighbors = result

            # Rewire neighbors for better cost and smoothness
            rewired_list = rewire(new_node, neighbors, edges)
            # Rewire descendants of rewired nodes to propagate cost changes
            stack = rewired_list[:]
            while stack:
                current = stack.pop()
                nbs = near_nodes(tree_a, current.position, len(tree_a))
                updated = rewire(current, nbs, edges)
                stack.extend(updated)

            # Attempt direct connection between the trees
            nearest_in_b = nearest(tree_b, new_node.position)
            d_connect = dist(new_node.position, nearest_in_b.position)
            if d_connect <= self.step_size:
                if not self._is_edge_in_obstacle(new_node.position, nearest_in_b.position, obstacles, is_3d) and \
                   not self._is_in_obstacle(nearest_in_b.position, obstacles, is_3d):
                    # Connect nodes
                    connection_cost = new_node.cost + d_connect + nearest_in_b.cost
                    if connection_cost < best_cost:
                        best_cost = connection_cost
                        success = True
                        best_path_nodes = extract_path(new_node, nearest_in_b)
                        break

        if not success:
            # No full path found: fallback to closest node to goal in start tree
            nearest_to_goal = min(tree_start, key=lambda n: dist(n.position, goal_pos))
            best_path_nodes = nearest_to_goal.path_from_root()

        # Post processing shortcutting for smoothness and path length reduction
        best_path_nodes = shortcut_path(best_path_nodes)

        return PlannerResult(success=success, path=best_path_nodes, nodes=nodes, edges=edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * i / steps for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

