class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = 0.05  # 5% samples biased to goal
        self.rewire_radius = 15.0     # fixed radius for rewiring neighbors

    def plan(self, map) -> PlannerResult:
        start_time = time.monotonic()
        time_limit = 30.0  # 30 s wall-clock hard limit

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def dist(a,b): return math.dist(a,b)

        def steer(from_pos, to_pos, max_dist=None):
            d = dist(from_pos, to_pos)
            step = max_dist if max_dist is not None else self.step_size
            if d <= step:
                return to_pos
            ratio = step / d
            return tuple(from_pos[i] + (to_pos[i]-from_pos[i])*ratio for i in range(dim))

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x,y,z,w,h,d = obs
                    px,py,pz = pos
                    if x <= px <= x+w and y <= py <= y+h and z <= pz <= z+d:
                        return True
                else:
                    x,y,w,h = obs
                    px,py = pos
                    if x <= px <= x+w and y <= py <= y+h:
                        return True
            return False

        def is_edge_in_obstacle(a,b, resolution=1.0):
            distance = dist(a,b)
            steps = max(1,int(distance / resolution))
            for i in range(steps + 1):
                interp = tuple(a[d] + (b[d]-a[d])*(i/steps) for d in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def nearest(tree, p):
            return min(tree, key = lambda n: dist(n.position, p))

        def near_nodes(tree, p, radius):
            return [n for n in tree if dist(n.position, p) <= radius]

        # Goal bias sampling: occasionally return goal directly, else uniform random in bounds free space
        def sample_free():
            if random.random() < self.goal_sample_rate:
                if not is_in_obstacle(goal_pos):
                    return goal_pos
            for _ in range(100):
                p = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not is_in_obstacle(p):
                    return p
            # fallback if unlucky
            return tuple(random.uniform(0,bounds[i]) for i in range(dim))

        # Initialize trees and supporting structures
        start_node = Node(start_pos)
        goal_node = Node(goal_pos)
        tree_start = [start_node]
        tree_goal = [goal_node]
        nodes = [start_node, goal_node]
        edges = []

        success = False
        path = []
        best_cost = float('inf')
        best_start_node = None
        best_goal_node = None

        # Alternate tree growth
        expand_start_tree = True

        for iter_count in range(self.max_iter):
            current_time = time.monotonic()
            if current_time - start_time > time_limit:
                break

            tree_a = tree_start if expand_start_tree else tree_goal
            tree_b = tree_goal if expand_start_tree else tree_start

            x_rand = sample_free()

            nearest_node = nearest(tree_a, x_rand)
            new_pos = steer(nearest_node.position, x_rand, self.step_size)

            if is_in_obstacle(new_pos) or is_edge_in_obstacle(nearest_node.position, new_pos):
                expand_start_tree = not expand_start_tree
                continue

            new_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            new_node = Node(new_pos)
            new_node.cost = new_cost

            # Choose best parent among neighbors
            radius = self.rewire_radius
            neighbors = near_nodes(tree_a, new_pos, radius)
            best_parent = nearest_node
            min_cost = new_cost
            for nb in neighbors:
                if nb == nearest_node:
                    continue
                tentative_cost = nb.cost + dist(nb.position, new_pos)
                if tentative_cost < min_cost:
                    if not is_edge_in_obstacle(nb.position, new_pos):
                        best_parent = nb
                        min_cost = tentative_cost
            new_node.parent = best_parent
            new_node.cost = min_cost
            best_parent.add_child(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire other neighbors if better connection found
            for nb in neighbors:
                if nb == best_parent or nb == new_node:
                    continue
                alt_cost = new_node.cost + dist(new_node.position, nb.position)
                if alt_cost < nb.cost:
                    if not is_edge_in_obstacle(new_node.position, nb.position):
                        # Remove old edge if exists
                        if nb.parent:
                            try:
                                edges.remove((nb.parent, nb))
                            except ValueError:
                                pass
                            if nb in nb.parent.children:
                                nb.parent.children.remove(nb)
                        nb.parent = new_node
                        nb.cost = alt_cost
                        new_node.add_child(nb)
                        edges.append((new_node, nb))

            # Try connecting new_node to tree_b nodes in radius
            near_other = near_nodes(tree_b, new_node.position, radius)
            for other_node in near_other:
                if is_edge_in_obstacle(new_node.position, other_node.position):
                    continue
                # Calculate total cost of connection
                total_cost = new_node.cost + dist(new_node.position, other_node.position) + other_node.cost
                if total_cost < best_cost:
                    best_cost = total_cost
                    success = True
                    best_start_node = new_node if expand_start_tree else other_node
                    best_goal_node = other_node if expand_start_tree else new_node

            if success:
                # Extract combined path with duplicate connection point removed
                path_start = best_start_node.path_from_root()
                path_goal = best_goal_node.path_from_root()
                if path_start[-1] == path_goal[0]:
                    path_goal = path_goal[1:]
                path = path_start + path_goal[::-1]
                break

            expand_start_tree = not expand_start_tree

        # If success, return full path
        if success:
            return PlannerResult(
                success=True,
                path=path,
                nodes=nodes,
                edges=edges
            )

        # Otherwise return best partial path from start tree nearest to goal
        best_partial_node = None
        best_dist = float('inf')
        for node in tree_start:
            d = dist(node.position, goal_pos)
            if d < best_dist and node.valid:
                best_dist = d
                best_partial_node = node
        if best_partial_node:
            partial_path = best_partial_node.path_from_root()
            return PlannerResult(
                success=False,
                path=partial_path,
                nodes=nodes,
                edges=edges
            )

        # No progress case
        return PlannerResult(
            success=False,
            path=[],
            nodes=nodes,
            edges=edges
        )

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size

    def plan(self, map) -> 'PlannerResult':
        import math, random, time

        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        start_tree = [Node(start_position, cost=0.0)]
        goal_tree = [Node(goal_position, cost=0.0)]
        nodes = start_tree + goal_tree
        edges = []
        success_state = False
        extracted_path = []

        time_start = time.monotonic()
        time_limit = 30.0

        def in_time():
            return time.monotonic() - time_start < time_limit

        def sample_free():
            p_goal_bias = 0.05
            if random.random() < p_goal_bias:
                return goal_position
            while True:
                point = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not self._is_in_obstacle(point, obstacles, is_3d):
                    return point

        def nearest(tree, point):
            return min(tree, key=lambda n: math.dist(n.position, point))

        def near(tree, point, radius):
            r2 = radius * radius
            return [n for n in tree if self._sqdist(n.position, point) <= r2]

        def steer(from_pos, to_pos):
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[i] + (to_pos[i] - from_pos[i]) * ratio for i in range(dim))

        def rewire(tree, new_node, radius):
            near_nodes = near(tree, new_node.position, radius)
            # Choose minimum cost parent
            min_cost = new_node.cost
            min_parent = new_node.parent
            for near_node in near_nodes:
                if near_node == new_node.parent:
                    continue
                edge_clear = not self._is_edge_in_obstacle(near_node.position, new_node.position, obstacles, is_3d)
                cost_through = near_node.cost + math.dist(near_node.position, new_node.position)
                if edge_clear and cost_through < min_cost:
                    min_cost = cost_through
                    min_parent = near_node
            if min_parent != new_node.parent:
                if new_node.parent:
                    try:
                        new_node.parent.children.remove(new_node)
                        edges.remove((new_node.parent, new_node))
                    except Exception:
                        pass
                new_node.parent = min_parent
                new_node.cost = min_cost
                min_parent.children.append(new_node)
                edges.append((min_parent, new_node))
            # Now rewire near nodes to new_node if beneficial
            for near_node in near_nodes:
                if near_node == new_node or near_node == min_parent:
                    continue
                edge_clear = not self._is_edge_in_obstacle(new_node.position, near_node.position, obstacles, is_3d)
                cost_through = new_node.cost + math.dist(new_node.position, near_node.position)
                if edge_clear and cost_through < near_node.cost:
                    if near_node.parent:
                        try:
                            near_node.parent.children.remove(near_node)
                            edges.remove((near_node.parent, near_node))
                        except Exception:
                            pass
                    near_node.parent = new_node
                    near_node.cost = cost_through
                    new_node.children.append(near_node)
                    edges.append((new_node, near_node))

        def try_connect(tree_a, tree_b):
            # Attempt to connect a new node in tree_a to nearest in tree_b within step_size, extend incrementally
            last_node = None
            for node_a in tree_a[-1:]:  # only last added node
                nearest_b = nearest(tree_b, node_a.position)
                dist = math.dist(node_a.position, nearest_b.position)
                if dist <= self.step_size and not self._is_edge_in_obstacle(node_a.position, nearest_b.position, obstacles, is_3d):
                    return node_a, nearest_b
                # Try to incrementally extend tree_b toward node_a
                from_pos = nearest_b.position
                to_pos = node_a.position
                direction_dist = dist
                steps = int(direction_dist / self.step_size)
                current_node = nearest_b
                for step_i in range(steps):
                    next_pos = steer(from_pos, to_pos)
                    if self._is_in_obstacle(next_pos, obstacles, is_3d) or self._is_edge_in_obstacle(from_pos, next_pos, obstacles, is_3d):
                        break
                    new_node = Node(next_pos, parent=current_node,
                                    cost=current_node.cost + math.dist(current_node.position, next_pos))
                    current_node.add_child(new_node)
                    tree_b.append(new_node)
                    nodes.append(new_node)
                    edges.append((current_node, new_node))
                    from_pos = next_pos
                    current_node = new_node
                    if math.dist(next_pos, node_a.position) <= self.step_size:
                        if not self._is_edge_in_obstacle(next_pos, node_a.position, obstacles, is_3d):
                            return node_a, current_node
                # else no connection
            return None

        def extract_path(node_start, node_goal):
            path_start = node_start.path_from_root()
            path_goal = node_goal.path_from_root()
            # Remove duplicate connecting node
            if path_start[-1] == path_goal[-1]:
                path_goal = path_goal[:-1]
            return path_start + path_goal[::-1]

        def shortcut_path(path):
            if len(path) <= 2:
                return path
            shortened = [path[0]]
            i = 0
            while i < len(path) - 1:
                j = len(path) - 1
                while j > i + 1:
                    if not self._is_edge_in_obstacle(path[i], path[j], obstacles, is_3d):
                        break
                    j -= 1
                shortened.append(path[j])
                i = j
            return shortened

        # Adaptive rewiring radius based on problem dimension and number of nodes
        def adaptive_radius(n):
            if n <= 1:
                return self.step_size * 2.0
            from math import log, pi, pow
            gamma_rrt_star = 2 * (1 + 1/dim)**(1/dim) * pow((pi / 2), 1/dim)
            r = min(self.step_size * 15.0, gamma_rrt_star * pow(log(n)/n, 1/dim))
            return max(r, self.step_size)

        for iteration in range(self.max_iter):
            if not in_time():
                break
            sample = sample_free()

            for tree_a, tree_b in [(start_tree, goal_tree), (goal_tree, start_tree)]:
                nearest_node = nearest(tree_a, sample)
                new_pos = steer(nearest_node.position, sample)
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    continue
                if self._is_edge_in_obstacle(nearest_node.position, new_pos, obstacles, is_3d):
                    continue
                new_node = Node(new_pos)
                dist_to_parent = math.dist(nearest_node.position, new_pos)
                new_node.parent = nearest_node
                new_node.cost = nearest_node.cost + dist_to_parent
                nearest_node.children.append(new_node)
                tree_a.append(new_node)
                nodes.append(new_node)
                edges.append((nearest_node, new_node))

                radius = adaptive_radius(len(tree_a))
                rewire(tree_a, new_node, radius)

                connected = try_connect(tree_a, tree_b)
                if connected:
                    node_a, node_b = connected
                    success_state = True
                    extracted_path = extract_path(node_a, node_b)
                    extracted_path = shortcut_path(extracted_path)
                    return PlannerResult(
                        success=success_state,
                        path=extracted_path,
                        nodes=nodes,
                        edges=edges
                    )

        # If timeout or max iterations reached return best path if any
        if success_state:
            extracted_path = shortcut_path(extracted_path)
        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

    def _sqdist(self, a, b):
        return sum((a[i] - b[i])**2 for i in range(len(a)))

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not new_parent:
            if self.parent is not None:
                self.parent.remove_child(self)
            self.parent = new_parent
            self.cost = new_cost
            if new_parent is not None:
                new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter=5000, step_size=5.0, goal_sample_rate=0.1):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.time_limit = 30.0
        self.gamma_rrt_star = 35.0
        self.max_neighbor_radius = 30.0
        self.improve_tol = 1e-10

    def plan(self, map):
        import time
        import math
        import random

        start_time = time.monotonic()
        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        def dist(a, b):
            return math.dist(a, b)

        def in_bounds(p):
            return all(0.0 <= p[i] <= bounds[i] for i in range(dim))

        def is_in_obstacle(p):
            for obs in obstacles:
                if is_3d:
                    x,y,z,w,h,d = obs
                    px,py,pz = p
                    if x <= px <= x+w and y <= py <= y+h and z <= pz <= z+d:
                        return True
                else:
                    x,y,w,h = obs
                    px,py = p
                    if x <= px <= x+w and y <= py <= y+h:
                        return True
            return False

        def is_edge_in_obstacle(a,b, resolution=None):
            dist_ab = dist(a,b)
            res = resolution
            if res is None:
                res = min(1.0, self.step_size * 0.5)
            steps = max(1,int(dist_ab/res))
            for i in range(steps+1):
                interp = tuple(a[j] + (b[j]-a[j])*(i/steps) for j in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def steer(from_p, to_p, max_step):
            d = dist(from_p, to_p)
            if d <= max_step:
                return to_p
            ratio = max_step / d
            return tuple(from_p[i] + ratio * (to_p[i] - from_p[i]) for i in range(dim))

        def uniform_sample():
            while True:
                sample = tuple(random.uniform(0,bounds[i]) for i in range(dim))
                if not is_in_obstacle(sample):
                    return sample

        # Ellipsoidal informed sampling around foci start and goal with cost c_best
        def informed_sample(c_best):
            c_min = dist(start_pos, goal_pos)
            if c_best == float('inf') or c_best < c_min + 1e-12:
                return uniform_sample()

            center = tuple((start_pos[i]+goal_pos[i])*0.5 for i in range(dim))
            a1 = tuple(goal_pos[i]-start_pos[i] for i in range(dim))
            length_a1 = math.sqrt(sum(x*x for x in a1))
            if length_a1 < 1e-12:
                return uniform_sample()
            e1 = tuple(x/length_a1 for x in a1)
            r1 = c_best/2.0
            r2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0.0))/2.0

            attempts = 0
            while attempts < 100:
                # Sample unit ball
                x_raw = [random.gauss(0,1) for _ in range(dim)]
                norm_x = math.sqrt(sum(x*x for x in x_raw))
                if norm_x < 1e-12:
                    attempts += 1
                    continue
                x_unit = [xi / norm_x for xi in x_raw]
                radius = random.random() ** (1.0 / dim)

                if dim == 2:
                    coords = [radius*r1*x_unit[0], radius*r2*x_unit[1]]
                    # rotate coords to align with e1
                    cos_t, sin_t = e1[0], e1[1]
                    rot_x = cos_t*coords[0] - sin_t*coords[1]
                    rot_y = sin_t*coords[0] + cos_t*coords[1]
                    sample = (center[0] + rot_x, center[1] + rot_y)
                elif dim == 3:
                    coords = [radius*r1*x_unit[0], radius*r2*x_unit[1], radius*r2*x_unit[2]]
                    # Rodrigues rotation from x-axis to e1
                    v = (1.0, 0.0, 0.0)
                    dot = sum(v[i]*e1[i] for i in range(3))
                    if abs(dot-1.0) < 1e-9:
                        rot = coords
                    elif abs(dot+1.0) < 1e-9:
                        rot = (-coords[0], coords[1], coords[2])
                    else:
                        k = (v[1]*e1[2]-v[2]*e1[1],
                             v[2]*e1[0]-v[0]*e1[2],
                             v[0]*e1[1]-v[1]*e1[0])
                        k_norm = math.sqrt(sum(x*x for x in k))
                        k = tuple(x/k_norm for x in k)
                        angle = math.acos(dot)
                        cos_a = math.cos(angle)
                        sin_a = math.sin(angle)
                        v_vec = coords
                        k_dot_v = sum(k[i]*v_vec[i] for i in range(3))
                        cross = (k[1]*v_vec[2]-k[2]*v_vec[1],
                                 k[2]*v_vec[0]-k[0]*v_vec[2],
                                 k[0]*v_vec[1]-k[1]*v_vec[0])
                        rot = tuple(v_vec[i]*cos_a + cross[i]*sin_a + k[i]*k_dot_v*(1-cos_a) for i in range(3))
                    sample = tuple(center[i]+rot[i] for i in range(3))
                else:
                    sample = uniform_sample()

                if in_bounds(sample) and not is_in_obstacle(sample):
                    return sample
                attempts += 1
            return uniform_sample()

        def nearest_node(tree_nodes, pt):
            best = tree_nodes[0]
            best_dist = dist(best.position, pt)
            for node in tree_nodes[1:]:
                d = dist(node.position, pt)
                if d < best_dist:
                    best = node
                    best_dist = d
            return best

        def near_nodes(tree_nodes, pt):
            n = len(tree_nodes)
            if n < 2:
                return []
            rad = self.gamma_rrt_star * ((math.log(n)/n) ** (1.0/dim))
            radius = min(self.max_neighbor_radius, max(self.step_size*2.0, rad))
            return [node for node in tree_nodes if dist(node.position, pt) <= radius]

        def choose_parent_and_add(tree_nodes, new_pos, nodes_list, edges_list):
            near_n = near_nodes(tree_nodes, new_pos)
            nearest_n = nearest_node(tree_nodes, new_pos)
            min_cost = nearest_n.cost + dist(nearest_n.position, new_pos)
            best_parent = nearest_n
            # Check neighbors for better parent
            for nbr in near_n:
                if nbr == nearest_n:
                    continue
                if nbr.cost + dist(nbr.position, new_pos) + self.improve_tol < min_cost:
                    if not is_edge_in_obstacle(nbr.position, new_pos):
                        min_cost = nbr.cost + dist(nbr.position, new_pos)
                        best_parent = nbr
            if not is_edge_in_obstacle(best_parent.position, new_pos):
                new_node = Node(new_pos, best_parent, min_cost)
                best_parent.add_child(new_node)
                nodes_list.append(new_node)
                tree_nodes.append(new_node)
                edges_list.append((best_parent, new_node))
                rewire(new_node, near_n, nodes_list, edges_list)
                return new_node
            return None

        def rewire(pivot, neighbors, nodes_list, edges_list):
            for nbr in neighbors:
                if nbr is pivot:
                    continue
                candidate_cost = pivot.cost + dist(pivot.position, nbr.position)
                if candidate_cost + self.improve_tol < nbr.cost:
                    if not is_edge_in_obstacle(pivot.position, nbr.position):
                        if nbr.parent is not None:
                            try:
                                edges_list.remove((nbr.parent, nbr))
                            except ValueError:
                                pass
                            nbr.parent.remove_child(nbr)
                        nbr.update_parent(pivot, candidate_cost)
                        edges_list.append((pivot, nbr))

        def merge_trees(node_in_a, tree_b):
            nearest_b = nearest_node(tree_b, node_in_a.position)
            d = dist(node_in_a.position, nearest_b.position)
            if d > self.step_size:
                return None
            if is_edge_in_obstacle(node_in_a.position, nearest_b.position):
                return None
            total_cost = node_in_a.cost + d + nearest_b.cost
            path_a = node_in_a.path_from_root()
            path_b = nearest_b.path_from_root()
            if path_a[-1] == path_b[-1]:
                path_b = path_b[:-1]
            return path_a + path_b[::-1], total_cost

        def shortcut_path(path):
            if len(path) < 3:
                return path
            i = 0
            while i < len(path) - 2:
                j = len(path) - 1
                while j > i + 1:
                    if not is_edge_in_obstacle(path[i], path[j]):
                        path = path[:i+1] + path[j:]
                        j = len(path) - 1
                    else:
                        j -= 1
                i += 1
            return path

        # Initialization
        nodes = []
        edges = []
        tree_start = []
        tree_goal = []

        root_start = Node(start_pos, None, 0.0)
        root_goal = Node(goal_pos, None, 0.0)
        nodes.extend([root_start, root_goal])
        tree_start.append(root_start)
        tree_goal.append(root_goal)

        best_path = []
        best_cost = float('inf')
        found_solution = False

        for it in range(self.max_iter):
            if time.monotonic() - start_time > self.time_limit:
                break

            # Sampling: goal bias and informed sampling after first solution
            if found_solution:
                sample = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos
                else:
                    sample = uniform_sample()
            if is_in_obstacle(sample):
                continue

            # Alternate tree expansion
            if it % 2 == 0:
                tree_a = tree_start
                tree_b = tree_goal
            else:
                tree_a = tree_goal
                tree_b = tree_start

            nearest_n = nearest_node(tree_a, sample)
            new_pos = steer(nearest_n.position, sample, self.step_size)
            if not in_bounds(new_pos) or is_in_obstacle(new_pos) or is_edge_in_obstacle(nearest_n.position, new_pos):
                continue

            new_node = choose_parent_and_add(tree_a, new_pos, nodes, edges)
            if new_node is None:
                continue

            # Attempt merging trees
            merge_res = merge_trees(new_node, tree_b)
            if merge_res is not None:
                path_candidate, cost_candidate = merge_res
                if cost_candidate + self.improve_tol < best_cost:
                    best_cost = cost_candidate
                    best_path = path_candidate
                    found_solution = True
                    # Early stopping immediately on connection
                    break

        # If no solution, try heuristic fallback: connect closest start tree node to goal if possible
        if not found_solution:
            nearest_goal_node = nearest_node(tree_start, goal_pos)
            if nearest_goal_node and not is_in_obstacle(goal_pos) and not is_edge_in_obstacle(nearest_goal_node.position, goal_pos):
                best_path = nearest_goal_node.path_from_root() + [goal_pos]

        if best_path:
            best_path = shortcut_path(best_path)
            success = found_solution
        else:
            success = False

        return PlannerResult(
            success=success,
            path=best_path,
            nodes=nodes,
            edges=edges
        )

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)

    def update_parent(self, new_parent, new_cost):
        if self.parent is not new_parent:
            if self.parent is not None:
                self.parent.remove_child(self)
            self.parent = new_parent
            self.cost = new_cost
            if new_parent is not None:
                new_parent.add_child(self)
class Planner:
    def __init__(self, max_iter: int=6000, step_size: float=5.0,
                 goal_sample_rate: float=0.2,
                 max_neighbor_radius: float=20.0, min_neighbor_radius: float=2.0,
                 shortcut_iters: int=30):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.max_neighbor_radius = max_neighbor_radius
        self.min_neighbor_radius = min_neighbor_radius
        self.shortcut_iters = shortcut_iters

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        start_time = time.monotonic()
        TIME_LIMIT = 30.0

        nodes: List[Node] = []
        edges: List[Tuple[Node, Node]] = []

        def dist(a, b):
            # Euclidean distance
            return math.dist(a, b)

        def in_bounds(p):
            for i in range(dim):
                if p[i] < 0 or p[i] > bounds[i]:
                    return False
            return True

        def is_free_node(p):
            # Node collision & boundary check
            return in_bounds(p) and not self._is_in_obstacle(p, obstacles, is_3d)

        def is_free_edge(p1, p2):
            # Collision check with adaptive resolution (0.5 * step_size)
            d = dist(p1, p2)
            resolution = min(1.0, self.step_size * 0.5)
            steps = max(1, int(d / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if self._is_in_obstacle(interp, obstacles, is_3d):
                    return False
            return True

        def steer(from_pos, to_pos, max_dist):
            d = dist(from_pos, to_pos)
            if d <= max_dist:
                return to_pos
            ratio = max_dist / d
            return tuple(from_pos[i] + ratio * (to_pos[i] - from_pos[i]) for i in range(dim))

        def heuristic_cost(a, b):
            return dist(a, b)

        # Ellipsoidal informed sampling function
        def sample_informed(c_best, x_start, x_goal):
            if math.isinf(c_best):
                # No current solution: sample uniform random in bounds
                return tuple(random.uniform(0, bounds[i]) for i in range(dim))
            c_min = heuristic_cost(x_start, x_goal)
            if c_min < 1e-12:
                return x_start
            center = tuple((x_start[i] + x_goal[i]) * 0.5 for i in range(dim))
            a1 = tuple((x_goal[i] - x_start[i]) / c_min for i in range(dim))
            # Build rotation matrix aligning x-axis to a1
            def build_rotation_matrix(a):
                if dim == 2:
                    cos_theta = a[0]
                    sin_theta = a[1]
                    return [[cos_theta, -sin_theta],
                            [sin_theta, cos_theta]]
                elif dim ==3:
                    x_axis = a
                    # Orthonormal basis
                    if abs(x_axis[0]) < 0.9:
                        temp = (1,0,0)
                    else:
                        temp = (0,1,0)
                    v = tuple(temp[i] - x_axis[i] * sum(x_axis[j]*temp[j] for j in range(3)) for i in range(3))
                    norm_v = math.sqrt(sum(v[i]*v[i] for i in range(3)))
                    if norm_v < 1e-12:
                        return [[1,0,0],[0,1,0],[0,0,1]]
                    v_unit = tuple(v[i]/norm_v for i in range(3))
                    w = (x_axis[1]*v_unit[2] - x_axis[2]*v_unit[1],
                         x_axis[2]*v_unit[0] - x_axis[0]*v_unit[2],
                         x_axis[0]*v_unit[1] - x_axis[1]*v_unit[0])
                    R = [list(x_axis), list(v_unit), list(w)]
                    return [[R[0][0], R[1][0], R[2][0]],
                            [R[0][1], R[1][1], R[2][1]],
                            [R[0][2], R[1][2], R[2][2]]]
                else:
                    return None
            R = build_rotation_matrix(a1)
            if R is None:
                return tuple(random.uniform(0, bounds[i]) for i in range(dim))
            r1 = c_best * 0.5
            val = abs(c_best * c_best - c_min * c_min)
            r2 = math.sqrt(val)*0.5 if val > 0 else 0.0001
            r2 = max(r2, 0.001)
            radii = [r1] + [r2]*(dim -1)

            while True:
                # Sample unit ball point
                rnd = [random.gauss(0,1) for _ in range(dim)]
                norm_rnd = math.sqrt(sum(x*x for x in rnd))
                if norm_rnd > 1e-12:
                    break
            unit_ball = [x / norm_rnd for x in rnd]
            u = random.random()
            scale = u ** (1.0/dim)
            sample_ball = [scale * unit_ball[i] * radii[i] for i in range(dim)]
            sample = [0.0]*dim
            for i in range(dim):
                s = 0.0
                for j in range(dim):
                    s += R[i][j]*sample_ball[j]
                sample[i] = s + center[i]
            clipped = tuple(max(0.0, min(sample[i], bounds[i])) for i in range(dim))
            return clipped

        # Spatial grid for neighbor search acceleration
        class SpatialGrid:
            def __init__(self, bounds, cell_size):
                self.bounds = bounds
                self.cell_size = cell_size
                self.dim = len(bounds)
                self.grid = dict()  # key: cell tuple to list of nodes

            def _cell_coords(self, pos):
                return tuple(int(pos[i] // self.cell_size) for i in range(self.dim))

            def insert(self, node):
                c = self._cell_coords(node.position)
                if c not in self.grid:
                    self.grid[c] = []
                self.grid[c].append(node)

            def remove(self, node):
                c = self._cell_coords(node.position)
                if c in self.grid and node in self.grid[c]:
                    self.grid[c].remove(node)
                    if not self.grid[c]:
                        del self.grid[c]

            def nearby_nodes(self, pos, radius):
                cell_r = int(math.ceil(radius / self.cell_size))
                center_cell = self._cell_coords(pos)
                candidates = []
                def rec(dim_idx, prefix):
                    if dim_idx == self.dim:
                        c = tuple(prefix)
                        if c in self.grid:
                            candidates.extend(self.grid[c])
                        return
                    for offset in range(-cell_r, cell_r+1):
                        recursive_prefix = prefix + [center_cell[dim_idx]+offset]
                        rec(dim_idx+1, recursive_prefix)
                rec(0, [])
                return candidates

        def nearest_node(p):
            # Use spatial grid for candidates
            candidate_radius = search_radius * 1.2
            candidates = grid.nearby_nodes(p, candidate_radius)
            best = None
            best_dist = float("inf")
            for node in candidates:
                dcur = dist(node.position, p)
                if dcur < best_dist:
                    best_dist = dcur
                    best = node
            if best is None and nodes:
                # fallback linear search
                best = nodes[0]
                best_dist = dist(best.position, p)
                for n in nodes[1:]:
                    dcur = dist(n.position, p)
                    if dcur < best_dist:
                        best_dist = dcur
                        best = n
            return best, best_dist

        def neighbor_radius(n_count):
            if n_count <= 1:
                return self.max_neighbor_radius
            g_rrt_star = self.max_neighbor_radius
            r = g_rrt_star * ((math.log(n_count)/n_count) ** (1.0/dim))
            return max(self.min_neighbor_radius, min(r, self.max_neighbor_radius))

        # Initialize tree
        root = Node(start_pos)
        nodes.append(root)
        grid_cell = max(self.step_size * 1.5, 1.0)
        grid = SpatialGrid(bounds, cell_size=grid_cell)
        grid.insert(root)
        best_goal_node = None
        best_goal_cost = float("inf")
        success = False
        search_radius = self.max_neighbor_radius

        for iter_idx in range(self.max_iter):
            if time.monotonic() - start_time > TIME_LIMIT:
                break

            if success:
                sample = sample_informed(best_goal_cost, start_pos, goal_pos)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos
                else:
                    sample = tuple(random.uniform(0, bounds[i]) for i in range(dim))

            nearest, _ = nearest_node(sample)
            new_pos = steer(nearest.position, sample, self.step_size)

            if not is_free_node(new_pos):
                continue
            if not is_free_edge(nearest.position, new_pos):
                continue

            n_nodes = len(nodes)
            search_radius = neighbor_radius(n_nodes)
            nbr_candidates = grid.nearby_nodes(new_pos, search_radius)
            neighbors = [node for node in nbr_candidates if dist(node.position, new_pos) <= search_radius]

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest
            for nb in neighbors:
                tentative_cost = nb.cost + dist(nb.position, new_pos)
                if tentative_cost < min_cost and is_free_edge(nb.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nb

            new_node = Node(new_pos)
            new_node.update_parent(best_parent, min_cost)
            nodes.append(new_node)
            edges.append((best_parent, new_node))
            grid.insert(new_node)

            # Rewire neighbors via new node if cheaper and feasible
            for nb in neighbors:
                if nb is best_parent:
                    continue
                alt_cost = new_node.cost + dist(new_node.position, nb.position)
                if alt_cost < nb.cost and is_free_edge(new_node.position, nb.position):
                    if nb.parent is not None:
                        try:
                            edges.remove((nb.parent, nb))
                        except ValueError:
                            pass
                        nb.update_parent(new_node, alt_cost)
                        edges.append((new_node, nb))

            dist_to_goal = dist(new_pos, goal_pos)
            if dist_to_goal <= self.step_size:
                if is_free_node(goal_pos) and is_free_edge(new_pos, goal_pos):
                    goal_node = Node(goal_pos)
                    goal_cost = new_node.cost + dist_to_goal
                    goal_node.update_parent(new_node, goal_cost)
                    nodes.append(goal_node)
                    edges.append((new_node, goal_node))
                    grid.insert(goal_node)
                    if goal_cost < best_goal_cost:
                        best_goal_cost = goal_cost
                        best_goal_node = goal_node
                        success = True
                    break  # Early termination on goal reached

        # Extract path from best goal node if any
        path: List[Tuple[float, ...]] = []
        if success and best_goal_node is not None:
            current = best_goal_node
            while current is not None:
                path.append(current.position)
                current = current.parent
            path.reverse()
        else:
            # No goal reached: choose closest node to goal
            min_dist = float("inf")
            best_node = None
            for n in nodes:
                dcur = dist(n.position, goal_pos)
                if dcur < min_dist:
                    min_dist = dcur
                    best_node = n
            if best_node:
                current = best_node
                while current is not None:
                    path.append(current.position)
                    current = current.parent
                path.reverse()

        # Multi-pass iterative shortcutting for path smoothing
        def shortcut_path(path_points):
            if len(path_points) < 3:
                return path_points[:]
            shortened = path_points[:]
            for _ in range(self.shortcut_iters):
                if len(shortened) < 3:
                    break
                made_change = False
                i = 0
                while i < len(shortened) - 2:
                    j = len(shortened) -1
                    shortcut_done = False
                    while j > i + 1:
                        if is_free_edge(shortened[i], shortened[j]):
                            # remove intermediate points between i and j
                            shortened = shortened[:i+1] + shortened[j:]
                            made_change = True
                            shortcut_done = True
                            break
                        j -= 1
                    if not shortcut_done:
                        i += 1
                    # else do not increment i to check again after change
                if not made_change:
                    break
            return shortened

        path = shortcut_path(path)

        return PlannerResult(success=success, path=path, nodes=nodes, edges=edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = parent            # Node or None
        self.cost = cost                # Cost from start in start tree, from goal in goal tree
        self.children = []              # List[Node]
        self.valid = True               # Mark if still valid (not pruned)

    def add_child(self, child):
        self.children.append(child)

    def remove_child(self, child):
        try:
            self.children.remove(child)
        except ValueError:
            pass

    def update_parent(self, new_parent, new_cost):
        if self.parent is not new_parent:
            if self.parent is not None:
                self.parent.remove_child(self)
            self.parent = new_parent
            self.cost = new_cost
            if new_parent is not None:
                new_parent.add_child(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter=15000, step_size=5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = 0.10    # Probability of sampling goal
        self.max_neighbor_radius = 20.0
        self.min_neighbor_radius = 4.0

    def plan(self, map):
        import time
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        start_time = time.monotonic()
        time_limit = 30.0

        # Distance function
        def dist(a, b):
            return math.dist(a, b)

        # Check if position is inside map bounds
        def in_bounds(pos):
            for i in range(dim):
                if pos[i] < 0 or pos[i] > bounds[i]:
                    return False
            return True

        # Check node collision
        def is_free_node(pos):
            if not in_bounds(pos):
                return False
            return not self._is_in_obstacle(pos, obstacles, is_3d)

        # Check edge collision (line segment)
        def is_free_edge(p1, p2):
            d = dist(p1, p2)
            resolution = min(1.0, self.step_size * 0.4)
            steps = max(1, int(d / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if self._is_in_obstacle(interp, obstacles, is_3d):
                    return False
            return True

        # Steer from 'from_pos' toward 'to_pos' by max length step_size
        def steer(from_pos, to_pos, max_len):
            d = dist(from_pos, to_pos)
            if d <= max_len:
                return to_pos
            ratio = max_len / d
            return tuple(from_pos[i] + ratio * (to_pos[i] - from_pos[i]) for i in range(dim))

        # Heuristic cost (Euclidean)
        def heuristic_cost(a, b):
            return dist(a, b)

        # Informed sampling inside ellipsoidal region
        def informed_sample(c_best, x_start, x_goal):
            if math.isinf(c_best):
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))
            c_min = heuristic_cost(x_start, x_goal)
            if c_min == 0.0:
                return x_start
            center = tuple((x_start[i] + x_goal[i]) * 0.5 for i in range(dim))
            a1 = tuple((x_goal[i] - x_start[i]) / c_min for i in range(dim))

            def build_rotation_matrix(a):
                if dim == 2:
                    cos_theta, sin_theta = a[0], a[1]
                    return [[cos_theta, -sin_theta],
                            [sin_theta,  cos_theta]]
                elif dim == 3:
                    x_axis = a
                    if abs(x_axis[0]) < 0.9:
                        temp = (1, 0, 0)
                    else:
                        temp = (0, 1, 0)
                    v = [temp[i] - sum(x_axis[j] * temp[j] for j in range(3)) * x_axis[i]
                         for i in range(3)]
                    norm_v = math.sqrt(sum(vi * vi for vi in v))
                    if norm_v < 1e-10:
                        return [[1, 0, 0], [0, 1, 0], [0, 0, 1]]
                    v_unit = [vi / norm_v for vi in v]
                    w = [x_axis[1] * v_unit[2] - x_axis[2] * v_unit[1],
                         x_axis[2] * v_unit[0] - x_axis[0] * v_unit[2],
                         x_axis[0] * v_unit[1] - x_axis[1] * v_unit[0]]
                    R = [list(x_axis), v_unit, w]
                    # transpose
                    return [[R[0][0], R[1][0], R[2][0]],
                            [R[0][1], R[1][1], R[2][1]],
                            [R[0][2], R[1][2], R[2][2]]]
                else:
                    # In higher dimension fallback to uniform sample
                    return None

            R = build_rotation_matrix(a1)
            if R is None:
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            r1 = c_best / 2.0
            val = c_best * c_best - c_min * c_min
            r_rest = math.sqrt(val) / 2.0 if val > 0 else 0.001
            radii = [r1] + [r_rest] * (dim - 1)

            while True:
                rnd = [random.gauss(0, 1) for _ in range(dim)]
                norm_rnd = math.sqrt(sum(x * x for x in rnd))
                if norm_rnd > 1e-10:
                    break
            unit_ball = [x / norm_rnd for x in rnd]
            u = random.random()
            scale = u ** (1 / dim)
            sample_ball = [scale * unit_ball[i] * radii[i] for i in range(dim)]

            sample = [0.0] * dim
            for i in range(dim):
                for j in range(dim):
                    sample[i] += R[i][j] * sample_ball[j]
                sample[i] += center[i]

            clipped = tuple(min(max(0.0, sample[i]), bounds[i]) for i in range(dim))
            return clipped

        # Adaptive neighbor radius function based on node count
        def radius_adaptive(n_nodes):
            if n_nodes < 2:
                return self.max_neighbor_radius
            val = (math.log(n_nodes) / n_nodes) ** (1 / dim)
            r = self.max_neighbor_radius * val
            return max(min(r, self.max_neighbor_radius), self.min_neighbor_radius)

        # Spatial hash grid for efficient neighborhood queries
        class SpatialGrid:
            def __init__(self, domain, cell_size):
                self.domain = domain
                self.cell_size = cell_size
                self.dim = len(domain)
                self.cells = {}

            def _cell_coords(self, p):
                return tuple(int(p[d] // self.cell_size) for d in range(self.dim))

            def add_node(self, node):
                cc = self._cell_coords(node.position)
                if cc not in self.cells:
                    self.cells[cc] = []
                self.cells[cc].append(node)

            def remove_node(self, node):
                cc = self._cell_coords(node.position)
                if cc in self.cells:
                    try:
                        self.cells[cc].remove(node)
                        if not self.cells[cc]:
                            del self.cells[cc]
                    except ValueError:
                        pass

            def neighbor_candidates(self, p, radius):
                ci = self._cell_coords(p)
                r_cells = int(math.ceil(radius / self.cell_size))
                neighbors = []
                if self.dim == 2:
                    for dx in range(-r_cells, r_cells + 1):
                        for dy in range(-r_cells, r_cells + 1):
                            c = (ci[0] + dx, ci[1] + dy)
                            if c in self.cells:
                                neighbors.extend(self.cells[c])
                elif self.dim == 3:
                    for dx in range(-r_cells, r_cells + 1):
                        for dy in range(-r_cells, r_cells + 1):
                            for dz in range(-r_cells, r_cells + 1):
                                c = (ci[0] + dx, ci[1] + dy, ci[2] + dz)
                                if c in self.cells:
                                    neighbors.extend(self.cells[c])
                return neighbors

        # Initialize trees: start_tree grows from start, goal_tree from goal
        start_root = Node(start_pos)
        goal_root = Node(goal_pos)

        start_tree = [start_root]
        goal_tree = [goal_root]

        nodes = [start_root, goal_root]
        edges = []

        # Setup spatial grids for start and goal trees
        grid_cell_size = self.max_neighbor_radius
        start_grid = SpatialGrid(bounds, grid_cell_size)
        goal_grid = SpatialGrid(bounds, grid_cell_size)
        start_grid.add_node(start_root)
        goal_grid.add_node(goal_root)

        best_cost = float('inf')
        best_start_node = None
        best_goal_node = None
        success = False

        # Alternate growth
        expand_start_tree = True

        # Track current best cost for informed sampling
        c_best = float('inf')

        # Early stopping counters
        min_improve = 1e-4
        no_improve_count = 0
        max_no_improve = 2000

        for it in range(self.max_iter):
            current_time = time.monotonic()
            if current_time - start_time > time_limit:
                break

            # Select which tree to expand and corresponding grid
            tree_a = start_tree if expand_start_tree else goal_tree
            tree_b = goal_tree if expand_start_tree else start_tree
            grid_a = start_grid if expand_start_tree else goal_grid

            # Sample point biased to goal and informed sampling after solution found
            if not math.isinf(c_best):
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if expand_start_tree else start_pos
                else:
                    # Informed sampling from start to goal for start tree, reverse for goal tree
                    sample = informed_sample(c_best, start_pos if expand_start_tree else goal_pos,
                                             goal_pos if expand_start_tree else start_pos)
            else:
                # No solution found yet: goal bias then uniform sampling in bounds
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if expand_start_tree else start_pos
                else:
                    for _ in range(100):
                        p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                        if is_free_node(p):
                            sample = p
                            break
                    else:
                        sample = tuple(random.uniform(0, bounds[d]) for d in range(dim))

            # Get nearest node to sample in tree A (linear search)
            nearest = tree_a[0]
            min_dist = dist(nearest.position, sample)
            for node in tree_a[1:]:
                d_ = dist(node.position, sample)
                if d_ < min_dist:
                    nearest = node
                    min_dist = d_

            new_pos = steer(nearest.position, sample, self.step_size)
            if not is_free_node(new_pos) or not is_free_edge(nearest.position, new_pos):
                expand_start_tree = not expand_start_tree
                continue

            # Determine adaptive radius for neighbor search
            radius = radius_adaptive(len(tree_a))
            candidates = grid_a.neighbor_candidates(new_pos, radius)
            radius_sq = radius * radius
            neighbors = []
            for c in candidates:
                sum_sq = 0.0
                p_c = c.position
                for i_d in range(dim):
                    diff = p_c[i_d] - new_pos[i_d]
                    sum_sq += diff * diff
                    if sum_sq > radius_sq:
                        break
                else:
                    neighbors.append(c)

            # Find best parent for new node among neighbors to minimize cost
            best_parent = nearest
            min_cost = nearest.cost + dist(nearest.position, new_pos)
            for nb in neighbors:
                tentative = nb.cost + dist(nb.position, new_pos)
                if tentative < min_cost and is_free_edge(nb.position, new_pos):
                    best_parent = nb
                    min_cost = tentative

            new_node = Node(new_pos)
            new_node.update_parent(best_parent, min_cost)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))
            grid_a.add_node(new_node)

            # Rewire neighbors to potentially improve their cost and parents
            for nb in neighbors:
                if nb is best_parent:
                    continue
                alt_cost = new_node.cost + dist(new_node.position, nb.position)
                if alt_cost + 1e-10 < nb.cost and is_free_edge(new_node.position, nb.position):
                    if nb.parent is not None:
                        try:
                            edges.remove((nb.parent, nb))
                        except ValueError:
                            pass
                        if nb in nb.parent.children:
                            nb.parent.children.remove(nb)
                    nb.update_parent(new_node, alt_cost)
                    edges.append((new_node, nb))

            # Attempt to connect new node to closest nodes in opposite tree within same radius
            candidates_b = (goal_grid if expand_start_tree else start_grid).neighbor_candidates(new_pos, radius)
            neighbors_b = []
            for c in candidates_b:
                sum_sq = 0.0
                p_c = c.position
                for i_d in range(dim):
                    diff = p_c[i_d] - new_pos[i_d]
                    sum_sq += diff * diff
                    if sum_sq > radius_sq:
                        break
                else:
                    neighbors_b.append(c)

            # Try connections to opposite tree nodes
            improved = False
            for other_node in neighbors_b:
                if is_free_edge(new_node.position, other_node.position):
                    total_cost = new_node.cost + dist(new_node.position, other_node.position) + other_node.cost
                    if total_cost + 1e-10 < best_cost:
                        best_cost = total_cost
                        best_start_node = new_node if expand_start_tree else other_node
                        best_goal_node = other_node if expand_start_tree else new_node
                        success = True
                        improved = True

            # If success, we can stop early
            if success:
                break

            # Update no improvement count for early stopping if desired
            if improved:
                no_improve_count = 0
                c_best = best_cost
            else:
                no_improve_count += 1
                if no_improve_count > max_no_improve:
                    break

            expand_start_tree = not expand_start_tree

        # Extract the best path by concatenating paths from start and goal trees
        def extract_full_path(s_node, g_node):
            path_start = s_node.path_from_root()
            path_goal = g_node.path_from_root()
            # Avoid duplicate node at connection
            if path_start[-1] == path_goal[-1]:
                path_goal.pop()
            full_path = path_start + path_goal[::-1]
            return full_path

        if success and best_start_node is not None and best_goal_node is not None:
            extracted_path = extract_full_path(best_start_node, best_goal_node)
            success_state = True
        else:
            # Fallback: choose closest node in start tree to goal and extract path
            min_d = float('inf')
            closest_node = None
            for n in start_tree:
                d_ = dist(n.position, goal_pos)
                if d_ < min_d and n.valid:
                    min_d = d_
                    closest_node = n
            if closest_node is not None:
                extracted_path = closest_node.path_from_root()
            else:
                extracted_path = [start_pos]
            success_state = False

        # Multi-pass shortcutting smoothing for final path
        def shortcut_path(path):
            if len(path) <= 2:
                return path[:]
            new_path = path[:]
            max_passes = 4
            max_iterations = 250
            for _ in range(max_passes):
                improved = False
                iteration = 0
                while iteration < max_iterations and len(new_path) > 2:
                    iteration += 1
                    i = random.randint(0, len(new_path) - 3)
                    j = random.randint(i + 2, len(new_path) - 1)
                    p1 = new_path[i]
                    p2 = new_path[j]
                    if is_free_edge(p1, p2):
                        del new_path[i + 1:j]
                        improved = True
                if not improved:
                    break
            return new_path

        extracted_path = shortcut_path(extracted_path)

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)


class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0, radius: float = 10.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.radius = radius

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        start_tree = [Node(start_position)]
        goal_tree = [Node(goal_position)]
        nodes = []
        edges = []
        success_state = False
        extracted_path = []

        for iter in range(self.max_iter):
            rand = self._sample_free(bounds, obstacles, is_3d)
            for tree_a, tree_b in [(start_tree, goal_tree), (goal_tree, start_tree)]:
                nearest = self._nearest(tree_a, rand)
                new_pos = self._steer(nearest.position, rand, self.step_size)
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    continue
                if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
                    continue

                new_node = Node(new_pos)
                near_nodes = self._near(tree_a, new_pos, self.radius)
                min_cost = nearest.cost + self._distance(nearest.position, new_pos)
                min_parent = nearest

                for near in near_nodes:
                    cost = near.cost + self._distance(near.position, new_pos)
                    if cost < min_cost and not self._is_edge_in_obstacle(near.position, new_pos, obstacles, is_3d):
                        min_cost = cost
                        min_parent = near

                new_node.parent = min_parent
                new_node.cost = min_cost
                min_parent.add_child(new_node)
                tree_a.append(new_node)
                nodes.append(new_node)
                edges.append((min_parent, new_node))

                for near in near_nodes:
                    if near == min_parent:
                        continue
                    new_cost = new_node.cost + self._distance(new_node.position, near.position)
                    if new_cost < near.cost and not self._is_edge_in_obstacle(new_node.position, near.position, obstacles, is_3d):
                        if near.parent:
                            edges.remove((near.parent, near))
                        near.parent = new_node
                        near.cost = new_cost
                        new_node.add_child(near)
                        edges.append((new_node, near))

                connect_node = self._nearest(tree_b, new_node.position)
                if self._distance(new_node.position, connect_node.position) < self.step_size and not self._is_edge_in_obstacle(new_node.position, connect_node.position, obstacles, is_3d):
                    success_state = True
                    extracted_path = self._extract_path(new_node, connect_node)
                    return PlannerResult(True, extracted_path, start_tree + goal_tree, edges)

        return PlannerResult(False, [], start_tree + goal_tree, edges)

    def _sample_free(self, bounds, obstacles, is_3d):
        while True:
            point = tuple(random.uniform(0, bounds[i]) for i in range(3 if is_3d else 2))
            if not self._is_in_obstacle(point, obstacles, is_3d):
                return point

    def _nearest(self, tree, point):
        return min(tree, key=lambda node: self._distance(node.position, point))

    def _near(self, tree, point, radius):
        return [node for node in tree if self._distance(node.position, point) <= radius]

    def _steer(self, from_pos, to_pos, step_size):
        dist = self._distance(from_pos, to_pos)
        if dist <= step_size:
            return to_pos
        ratio = step_size / dist
        return tuple(from_pos[i] + (to_pos[i] - from_pos[i]) * ratio for i in range(len(from_pos)))

    def _extract_path(self, node_a, node_b):
        path_a = []
        while node_a:
            path_a.append(node_a.position)
            node_a = node_a.parent
        path_b = []
        while node_b:
            path_b.append(node_b.position)
            node_b = node_b.parent
        return list(reversed(path_a)) + path_b

    def _distance(self, a, b):
        return math.dist(a, b)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = self._distance(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self,
                 max_iter: int = 5000,
                 step_size: float = 5.0,
                 goal_sample_rate: float = 0.05,
                 max_no_improve: int = 80,
                 improve_tol: float = 1e-6,
                 time_limit_sec: float = 30.0,
                 gamma_rrt_star: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.max_no_improve = max_no_improve
        self.improve_tol = improve_tol
        self.time_limit_sec = time_limit_sec
        self.gamma_rrt_star = gamma_rrt_star

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = dim == 3

        def dist(a, b):
            return math.dist(a, b)

        def steer(fr, to):
            d = dist(fr, to)
            if d <= self.step_size:
                return to
            r = self.step_size / d
            return tuple(fr[i] + (to[i] - fr[i]) * r for i in range(dim))

        def within_bounds(p):
            return all(0.0 <= p[i] <= bounds[i] for i in range(dim))

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w_, h_ = obs
                    px, py = pos
                    if x <= px <= x + w_ and y <= py <= y + h_:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2, resolution=1.0):
            length = dist(p1, p2)
            steps = max(1, int(length / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def uniform_sample():
            trials = 0
            while True:
                s = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not is_in_obstacle(s):
                    return s
                trials += 1
                if trials > 1000:
                    return start_pos

        def informed_sample(start, goal, c_best, c_min):
            if c_best == float('inf') or c_min <= 1e-12:
                return uniform_sample()
            center = tuple((s + g) * 0.5 for s, g in zip(start, goal))
            a1 = tuple((g - s) / c_min for s, g in zip(start, goal))
            if dim == 2:
                a2 = (-a1[1], a1[0])
                basis = [a1, a2]
                r1 = c_best / 2.0
                r2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) / 2.0
                radii = [r1, r2]
            else:
                ref = (1.0, 0.0, 0.0) if abs(a1[0]) < 0.9 else (0.0, 1.0, 0.0)
                b2 = self._normalize(self._cross(a1, ref))
                if self._norm(b2) < 1e-12:
                    ref = (0.0, 0.0, 1.0)
                    b2 = self._normalize(self._cross(a1, ref))
                b3 = self._cross(a1, b2)
                basis = [a1, b2, b3]
                r1 = c_best / 2.0
                r_other = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) / 2.0
                radii = [r1, r_other, r_other]

            def sample_unit_ball(dim_):
                while True:
                    v = [random.gauss(0, 1) for _ in range(dim_)]
                    norm_ = math.sqrt(sum(x * x for x in v))
                    if norm_ > 1e-12:
                        v = [x / norm_ for x in v]
                        r = random.random() ** (1.0 / dim_)
                        return [x * r for x in v]

            u = sample_unit_ball(dim)
            mapped = [center[i] for i in range(dim)]
            for i in range(dim):
                for j in range(dim):
                    mapped[i] += basis[j][i] * radii[j] * u[j]
            mapped = tuple(max(0.0, min(mapped[i], bounds[i])) for i in range(dim))
            if is_in_obstacle(mapped):
                return uniform_sample()
            return mapped

        def neighbor_radius(n_nodes):
            if n_nodes < 2:
                return max(2.0, self.step_size * 2.0)
            val = self.gamma_rrt_star * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            max_r = max(bounds) * 0.25
            min_r = max(2.0, self.step_size)
            r = max(min_r, min(max_r, val))
            return r

        def near_nodes(nodes_list, p, radius):
            result = []
            radius_sq = radius * radius
            for n in nodes_list:
                d_sq = sum((n.position[i] - p[i])**2 for i in range(dim))
                if d_sq <= radius_sq:
                    result.append(n)
            return result

        def add_node_rrt_star(tree, pos, nodes_all, edges_list):
            r = neighbor_radius(len(tree))
            neighbors = near_nodes(tree, pos, r)
            parents = [n for n in neighbors if not is_edge_in_obstacle(n.position, pos)]
            if not parents:
                p = None
                min_d = float('inf')
                for n_ in tree:
                    d_ = dist(n_.position, pos)
                    if d_ < min_d and not is_edge_in_obstacle(n_.position, pos):
                        min_d = d_
                        p = n_
                if p is None:
                    return None, []
                parents = [p]
            best_parent = min(parents, key=lambda n: n.cost + dist(n.position, pos))
            new_cost = best_parent.cost + dist(best_parent.position, pos)
            new_node = Node(pos, parent=None, cost=new_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            nodes_all.append(new_node)
            edges_list.append((best_parent, new_node))
            return new_node, neighbors

        def propagate_cost_to_children(node):
            for child in node.children:
                old_cost = child.cost
                new_cost = node.cost + dist(node.position, child.position)
                if new_cost + 1e-12 < old_cost:
                    child.cost = new_cost
                    propagate_cost_to_children(child)

        def rewire(tree, new_node, neighbors, edges_list):
            for nb in neighbors:
                if nb is new_node:
                    continue
                if is_edge_in_obstacle(new_node.position, nb.position):
                    continue
                new_cost = new_node.cost + dist(new_node.position, nb.position)
                if new_cost + 1e-12 < nb.cost:
                    if nb.parent:
                        nb.parent.remove_child(nb)
                        try:
                            edges_list.remove((nb.parent, nb))
                        except ValueError:
                            pass
                    new_node.add_child(nb)
                    nb.cost = new_cost
                    edges_list.append((new_node, nb))
                    propagate_cost_to_children(nb)

        def can_connect(n1, n2):
            return n1.valid and n2.valid and not is_edge_in_obstacle(n1.position, n2.position)

        def stitch_path(n_start, n_goal, a_is_start):
            path_a = n_start.path_from_root()
            path_b = n_goal.path_from_root()
            # Remove duplicated connection point if any
            if path_a and path_b and path_a[-1] == path_b[-1]:
                merged = path_a + path_b[-2::-1]
            else:
                merged = path_a + path_b[::-1]
            return merged if a_is_start else merged[::-1]

        def shortcut_path(path):
            if len(path) < 3:
                return path
            smoothed = [path[0]]
            i = 0
            while i < len(path) - 1:
                # Attempt to jump as far as possible with collision-free segment
                j = len(path) - 1
                while j > i + 1:
                    if not is_edge_in_obstacle(path[i], path[j]):
                        break
                    j -= 1
                smoothed.append(path[j])
                i = j
            # Optionally do a second pass for minor local smoothing
            return smoothed

        start_time = time.monotonic()
        tree_start = [Node(start_pos, cost=0.0)]
        tree_goal = [Node(goal_pos, cost=0.0)]
        nodes = [tree_start[0], tree_goal[0]]
        edges = []

        best_path = []
        best_cost = float('inf')
        found_first = False
        no_improve_count = 0

        c_min = dist(start_pos, goal_pos)

        for it in range(self.max_iter):
            elapsed = time.monotonic() - start_time
            if elapsed > self.time_limit_sec:
                break

            a_side = (it % 2 == 0)
            curr_tree = tree_start if a_side else tree_goal
            other_tree = tree_goal if a_side else tree_start

            # Sample with goal bias and informed sampling after first solution
            if found_first:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if a_side else start_pos
                else:
                    sample = informed_sample(start_pos, goal_pos, best_cost, c_min)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if a_side else start_pos
                else:
                    sample = uniform_sample()

            nearest_node = None
            min_dist = float('inf')
            for node in curr_tree:
                d_ = dist(node.position, sample)
                if d_ < min_dist:
                    min_dist = d_
                    nearest_node = node

            new_pos = steer(nearest_node.position, sample)
            if not within_bounds(new_pos) or is_in_obstacle(new_pos) or is_edge_in_obstacle(nearest_node.position, new_pos):
                no_improve_count = found_first and no_improve_count + 1 or 0
                if found_first and no_improve_count >= self.max_no_improve:
                    break
                continue

            new_node, neighbors = add_node_rrt_star(curr_tree, new_pos, nodes, edges)
            if new_node is None:
                no_improve_count = found_first and no_improve_count + 1 or 0
                if found_first and no_improve_count >= self.max_no_improve:
                    break
                continue

            rewire(curr_tree, new_node, neighbors, edges)

            # Attempt early direct connection between trees
            nearest_other = None
            min_other_dist = float('inf')
            for n in other_tree:
                d_ = dist(n.position, new_node.position)
                if d_ < min_other_dist:
                    min_other_dist = d_
                    nearest_other = n

            connected_other = None
            if min_other_dist <= self.step_size and nearest_other and can_connect(nearest_other, new_node):
                final_node = Node(new_node.position, parent=None,
                                  cost=nearest_other.cost + dist(nearest_other.position, new_node.position))
                nearest_other.add_child(final_node)
                other_tree.append(final_node)
                nodes.append(final_node)
                edges.append((nearest_other, final_node))
                connected_other = final_node
            else:
                # Incremental connection attempts with limited steps
                if nearest_other is not None:
                    current = nearest_other
                    tries = 0
                    max_connect_attempts = 15
                    while tries < max_connect_attempts:
                        tries += 1
                        step_pos = steer(current.position, new_node.position)
                        if (not within_bounds(step_pos) or is_in_obstacle(step_pos) or
                                is_edge_in_obstacle(current.position, step_pos)):
                            break
                        other_new, other_neighbors = add_node_rrt_star(other_tree, step_pos, nodes, edges)
                        if other_new is None:
                            break
                        rewire(other_tree, other_new, other_neighbors, edges)
                        current = other_new
                        if dist(current.position, new_node.position) <= self.step_size and can_connect(current, new_node):
                            final_node = Node(new_node.position, parent=None,
                                              cost=current.cost + dist(current.position, new_node.position))
                            current.add_child(final_node)
                            other_tree.append(final_node)
                            nodes.append(final_node)
                            edges.append((current, final_node))
                            connected_other = final_node
                            break

            if connected_other is not None:
                candidate_path = stitch_path(new_node, connected_other, a_side)
                cost_candidate = 0.0
                for i in range(len(candidate_path) - 1):
                    cost_candidate += dist(candidate_path[i], candidate_path[i + 1])

                if cost_candidate + self.improve_tol < best_cost:
                    best_cost = cost_candidate
                    best_path = candidate_path
                    found_first = True
                    no_improve_count = 0
                else:
                    no_improve_count += 1
                if found_first and no_improve_count >= self.max_no_improve:
                    break
            else:
                if found_first:
                    no_improve_count += 1
                    if no_improve_count >= self.max_no_improve:
                        break

        if best_path:
            best_path = shortcut_path(best_path)
            return PlannerResult(
                success=True,
                path=best_path,
                nodes=nodes,
                edges=edges
            )
        else:
            return PlannerResult(
                success=False,
                path=[],
                nodes=nodes,
                edges=edges
            )

    def _cross(self, a, b):
        ax, ay, az = a
        bx, by, bz = b
        return (ay * bz - az * by, az * bx - ax * bz, ax * by - ay * bx)

    def _norm(self, v):
        return math.sqrt(sum(x * x for x in v))

    def _normalize(self, v):
        n = self._norm(v)
        if n < 1e-12:
            return v
        return tuple(x / n for x in v)

class Node:
    def __init__(self, position, parent=None, cost: float = 0.0):
        self.position = position              # Tuple[float, ...]
        self.parent: Optional["Node"] = parent
        self.cost: float = cost
        self.children: List["Node"] = []
        self.valid = True

    def add_child(self, child_node: "Node"):
        self.children.append(child_node)
        child_node.parent = self

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]


class Planner:
    def __init__(
        self,
        max_iter: int = 5000,
        step_size: float = 5.0,
        gamma_rrt_star: float = 50.0,
        max_radius: float = 60.0,
    ):
        self.max_iter = max_iter
        self.step_size = step_size
        self.gamma_rrt_star = gamma_rrt_star
        self.max_radius = max_radius

    # ======================= Public API =======================

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        success_state = False
        extracted_path: List[Tuple[float, ...]] = []
        nodes: List[Node] = []
        edges: List[Tuple[Node, Node]] = []

        start_root = Node(start_position, parent=None, cost=0.0)
        goal_root  = Node(goal_position,  parent=None, cost=0.0)
        nodes.extend([start_root, goal_root])

        tree_a: List[Node] = [start_root]
        tree_b: List[Node] = [goal_root]

        for _ in range(self.max_iter):
            rand_point = self._sample_free(bounds, obstacles, is_3d)

            new_a = self._extend_and_rewire(tree_a, rand_point, obstacles, is_3d, nodes, edges, bounds)
            if new_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_b = self._connect_and_rewire(tree_b, new_a, obstacles, is_3d, nodes, edges, bounds)
            if new_b:
                success_state = True
                path_a = new_a.path_from_root()
                path_b = new_b.path_from_root()
                if path_a and path_b and path_a[-1] == path_b[-1]:
                    path_b = path_b[:-1]
                extracted_path = path_a + path_b[::-1]
                break

            tree_a, tree_b = tree_b, tree_a

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _sample_free(self, bounds, obstacles, is_3d):
        while True:
            p = tuple(random.uniform(0, bounds[d]) for d in range(len(bounds)))
            if not self._is_in_obstacle(p, obstacles, is_3d):
                return p

    def _nearest(self, tree: List[Node], point):
        return min(tree, key=lambda node: math.dist(node.position, point))

    def _steer(self, from_pos, to_pos):
        dist = math.dist(from_pos, to_pos)
        if dist <= self.step_size:
            return to_pos
        ratio = self.step_size / dist
        return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(len(from_pos)))

    def _near_nodes(self, tree: List[Node], position, bounds) -> List[Node]:
        n = max(1, len(tree))
        d = len(position)
        # 공간 스케일을 간단히 bounds로 추정 (정밀 X, 실용적)
        scale = max(1.0, sum(bounds) / d)
        r_theory = self.gamma_rrt_star * ((math.log(n + 1) / (n + 1)) ** (1.0 / d))
        radius = max(self.step_size * 2.0, min(self.max_radius, r_theory * 0.5 + scale * 0.02))
        return [node for node in tree if math.dist(node.position, position) <= radius]

    def _choose_parent_and_add(self, tree, new_pos, obstacles, is_3d, nodes, edges, bounds):
        nearest = self._nearest(tree, new_pos)
        neighbors = self._near_nodes(tree, new_pos, bounds)

        min_cost = nearest.cost + math.dist(nearest.position, new_pos)
        best_parent = nearest

        for nb in neighbors:
            if not self._is_edge_in_obstacle(nb.position, new_pos, obstacles, is_3d):
                cand = nb.cost + math.dist(nb.position, new_pos)
                if cand < min_cost:
                    min_cost = cand
                    best_parent = nb

        new_node = Node(new_pos, parent=None, cost=min_cost)
        best_parent.add_child(new_node)
        tree.append(new_node)
        nodes.append(new_node)
        edges.append((best_parent, new_node))
        return new_node, neighbors

    def _rewire_from(self, pivot: Node, neighbors: List[Node], obstacles, is_3d, edges):
        for nb in neighbors:
            if not self._is_edge_in_obstacle(pivot.position, nb.position, obstacles, is_3d):
                new_cost = pivot.cost + math.dist(pivot.position, nb.position)
                if new_cost + 1e-12 < nb.cost:  # 작은 수치 버퍼
                    if nb.parent is not None:
                        try:
                            edges.remove((nb.parent, nb))
                        except ValueError:
                            pass
                        if nb in nb.parent.children:
                            nb.parent.children.remove(nb)
                    pivot.add_child(nb)
                    nb.cost = new_cost
                    edges.append((pivot, nb))

    def _extend_and_rewire(self, tree, sample_point, obstacles, is_3d, nodes, edges, bounds):
        nearest = self._nearest(tree, sample_point)
        new_pos = self._steer(nearest.position, sample_point)

        if self._is_in_obstacle(new_pos, obstacles, is_3d):
            return None
        if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
            return None

        new_node, neighbors = self._choose_parent_and_add(tree, new_pos, obstacles, is_3d, nodes, edges, bounds)
        self._rewire_from(new_node, neighbors, obstacles, is_3d, edges)
        return new_node

    def _connect_and_rewire(self, tree, target_node: Node, obstacles, is_3d, nodes, edges, bounds):
        nearest = self._nearest(tree, target_node.position)

        while True:
            new_pos = self._steer(nearest.position, target_node.position)

            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
                return None

            connect_node, neighbors = self._choose_parent_and_add(tree, new_pos, obstacles, is_3d, nodes, edges, bounds)
            self._rewire_from(connect_node, neighbors, obstacles, is_3d, edges)

            if math.dist(connect_node.position, target_node.position) <= self.step_size:
                if (not self._is_edge_in_obstacle(connect_node.position, target_node.position, obstacles, is_3d) and
                    not self._is_in_obstacle(target_node.position, obstacles, is_3d)):
                    final_node = Node(target_node.position, parent=None, cost=connect_node.cost + math.dist(connect_node.position, target_node.position))
                    connect_node.add_child(final_node)
                    tree.append(final_node)
                    nodes.append(final_node)
                    edges.append((connect_node, final_node))
                    return final_node
                else:
                    return None

            nearest = connect_node


    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False


    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

