                radii = [r1, r2]
            else:
                ref = (1.0, 0.0, 0.0) if abs(a1[0]) < 0.9 else (0.0, 1.0, 0.0)
                b2 = self._normalize(self._cross(a1, ref))
                if self._norm(b2) < 1e-12:
                    ref = (0.0, 0.0, 1.0)
                    b2 = self._normalize(self._cross(a1, ref))
                b3 = self._cross(a1, b2)
                basis = [a1, b2, b3]
                r1 = c_best / 2.0
                r_other = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) / 2.0
                radii = [r1, r_other, r_other]

            def sample_unit_ball(d):
                while True:
                    v = [random.gauss(0, 1) for _ in range(d)]
                    norm_ = math.sqrt(sum(x * x for x in v))
                    if norm_ > 1e-12:
                        v = [x / norm_ for x in v]
                        r = random.random() ** (1.0 / d)
                        return [x * r for x in v]

            u = sample_unit_ball(dim)
            mapped = [center[i] for i in range(dim)]
            for i in range(dim):
                for j in range(dim):
                    mapped[i] += basis[j][i] * radii[j] * u[j]
            mapped = tuple(min(max(mapped[i], 0.0), bounds[i]) for i in range(dim))
            if is_in_obstacle(mapped):
                return uniform_sample()
            return mapped

        def neighbor_radius(n_nodes):
            if n_nodes < 2:
                return max(2.0, self.step_size * 2.0)
            val = self.gamma_rrt_star * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            max_r = max(bounds) * 0.25
            min_r = max(2.0, self.step_size)
            return max(min_r, min(max_r, val))

        def nearest(nodes_list, p):
            min_node = None
            min_d = float('inf')
            for n in nodes_list:
                d_ = dist(n.position, p)
                if d_ < min_d:
                    min_d = d_
                    min_node = n
            return min_node

        def near_nodes(nodes_list, p, radius):
            return [n for n in nodes_list if dist(n.position, p) <= radius]

        def add_node_rrt_star(tree, pos, nodes_all, edges_list):
            r = neighbor_radius(len(tree))
            neighbors = near_nodes(tree, pos, r)
            possible_parents = [n for n in neighbors if not is_edge_in_obstacle(n.position, pos)]
            if not possible_parents:
                p = nearest(tree, pos)
                if not p or is_edge_in_obstacle(p.position, pos):
                    return None, []
                possible_parents = [p]
            best_parent = min(possible_parents, key=lambda n: n.cost + dist(n.position, pos))
            new_cost = best_parent.cost + dist(best_parent.position, pos)
            new_node = Node(pos, parent=None, cost=new_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            nodes_all.append(new_node)
            edges_list.append((best_parent, new_node))
            return new_node, neighbors

        def propagate_cost_to_children(node):
            for child in node.children:
                old_cost = child.cost
                child.cost = node.cost + dist(node.position, child.position)
                if child.cost + 1e-12 < old_cost:
                    propagate_cost_to_children(child)

        def rewire(tree, new_node, neighbors, edges_list):
            for nb in neighbors:
                if nb is new_node:
                    continue
                if is_edge_in_obstacle(new_node.position, nb.position):
                    continue
                new_cost = new_node.cost + dist(new_node.position, nb.position)
                if new_cost + 1e-12 < nb.cost:
                    if nb.parent:
                        nb.parent.remove_child(nb)
                        try:
                            edges_list.remove((nb.parent, nb))
                        except ValueError:
                            pass
                    new_node.add_child(nb)
                    nb.cost = new_cost
                    edges_list.append((new_node, nb))
                    propagate_cost_to_children(nb)

        def can_connect(n1, n2):
            return not is_edge_in_obstacle(n1.position, n2.position) and n1.valid and n2.valid

        def stitch_path(n1, n2, n1_is_start):
            path1 = n1.path_from_root()
            path2 = n2.path_from_root()
            if path1 and path2 and path1[-1] == path2[-1]:
                combined = path1 + path2[-2::-1]
            else:
                combined = path1 + path2[::-1]
            return combined if n1_is_start else combined[::-1]

        def shortcut_path(path):
            if len(path) < 3:
                return path
            smoothed = [path[0]]
            i = 0
            while i < len(path) - 1:
                j = len(path) - 1
                while j > i + 1:
                    if not is_edge_in_obstacle(path[i], path[j]):
                        break
                    j -= 1
                smoothed.append(path[j])
                i = j
            return smoothed

        start_time = time.monotonic()
        tree_start = [Node(start_pos, cost=0.0)]
        tree_goal = [Node(goal_pos, cost=0.0)]
        nodes = tree_start + tree_goal[:]
        edges = []

        best_path = []
        best_cost = float('inf')
        found_first = False
        post_no_improve = 0
        post_iter = 0
        c_min = dist(start_pos, goal_pos)

        for it in range(self.max_iter):
            if time.monotonic() - start_time > self.time_limit_sec:
                break

            if it % 2 == 0:
                curr_tree = tree_start
                other_tree = tree_goal
                curr_is_start = True
            else:
                curr_tree = tree_goal
                other_tree = tree_start
                curr_is_start = False

            # Sampling
            if found_first:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if curr_is_start else start_pos
                else:
                    sample = informed_sample(start_pos, goal_pos, best_cost, c_min)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if curr_is_start else start_pos
                else:
                    sample = uniform_sample()

            nearest_node = nearest(curr_tree, sample)
            new_pos = steer(nearest_node.position, sample)

            if (not within_bounds(new_pos)) or is_in_obstacle(new_pos) or is_edge_in_obstacle(nearest_node.position, new_pos):
                if found_first:
                    post_no_improve += 1
                    post_iter += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break
                continue

            new_node, neighbor_nodes = add_node_rrt_star(curr_tree, new_pos, nodes, edges)
            if new_node is None:
                if found_first:
                    post_no_improve += 1
                    post_iter += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break
                continue

            rewire(curr_tree, new_node, neighbor_nodes, edges)

            # Try fast direct connection
            nearest_other = nearest(other_tree, new_node.position)
            connected_other = None
            if dist(nearest_other.position, new_node.position) <= self.step_size and can_connect(nearest_other, new_node):
                final_node = Node(new_node.position, cost=nearest_other.cost + dist(nearest_other.position, new_node.position))
                nearest_other.add_child(final_node)
                other_tree.append(final_node)
                nodes.append(final_node)
                edges.append((nearest_other, final_node))
                connected_other = final_node
            else:
                current = nearest_other
                tries = 0
                max_steps = 20
                while tries < max_steps:
                    tries += 1
                    step_pos = steer(current.position, new_node.position)
                    if (not within_bounds(step_pos)) or is_in_obstacle(step_pos) or is_edge_in_obstacle(current.position, step_pos):
                        break
                    new_other_node, neighbors_other = add_node_rrt_star(other_tree, step_pos, nodes, edges)
                    if new_other_node is None:
                        break
                    rewire(other_tree, new_other_node, neighbors_other, edges)
                    current = new_other_node
                    if dist(current.position, new_node.position) <= self.step_size:
                        if can_connect(current, new_node):
                            final_node = Node(new_node.position, cost=current.cost + dist(current.position, new_node.position))
                            current.add_child(final_node)
                            other_tree.append(final_node)
                            nodes.append(final_node)
                            edges.append((current, final_node))
                            connected_other = final_node
                        break

            if connected_other is not None:
                candidate_path = stitch_path(new_node, connected_other, curr_is_start)
                candidate_cost = 0.0
                for i in range(len(candidate_path) - 1):
                    candidate_cost += dist(candidate_path[i], candidate_path[i + 1])
                if candidate_cost + self.improve_tol < best_cost:
                    best_cost = candidate_cost
                    best_path = candidate_path
                    found_first = True
                    post_no_improve = 0
                    post_iter = 0
                else:
                    if found_first:
                        post_no_improve += 1
                    post_iter += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break
            else:
                if found_first:
                    post_no_improve += 1
                    post_iter += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break

        if best_path:
            best_path = shortcut_path(best_path)
            return PlannerResult(success=True, path=best_path, nodes=nodes, edges=edges)
        else:
            # Return best approximate path
            if nodes:
                # fallback to nearest node to goal from the tree with minimal cost + distance to goal
                end_node = min(nodes, key=lambda n: n.cost + dist(n.position, goal_pos))
                path = end_node.path_from_root()
                return PlannerResult(success=False, path=path, nodes=nodes, edges=edges)
            else:
                return PlannerResult(success=False, path=[], nodes=nodes, edges=edges)

    def _cross(self, a, b):
        ax, ay, az = a
        bx, by, bz = b
        return (ay * bz - az * by, az * bx - ax * bz, ax * by - ay * bx)

    def _norm(self, v):
        return sum(x * x for x in v) ** 0.5

    def _normalize(self, v):
        n = self._norm(v)
        if n < 1e-12:
            return v
        return tuple(x / n for x in v)

class Node:
    def __init__(self, position: Tuple[float, ...], parent: Optional["Node"] = None, cost: float = 0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children: List["Node"] = []
        self.valid = True

    def add_child(self, child: "Node"):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child: "Node"):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: Optional["Node"], new_cost: float):
        if self.parent is not new_parent:
            if self.parent is not None:
                self.parent.remove_child(self)
            self.parent = new_parent
            self.cost = new_cost
            if new_parent is not None:
                new_parent.children.append(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.05, gamma_rrt_star: float = 25.0, max_neighbor_radius: float = 20.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.gamma_rrt_star = gamma_rrt_star
        self.max_neighbor_radius = max_neighbor_radius

    def plan(self, map) -> PlannerResult:
        bounds = map.size                  # Tuple[int, ...]: (W,H) or (W,H,D)
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        TIME_LIMIT = 30.0
        t_start = time.monotonic()

        nodes_all: List[Node] = []
        edges_all: List[Tuple[Node, Node]] = []

        def in_bounds(p):
            for i in range(dim):
                if p[i] < 0 or p[i] > bounds[i]:
                    return False
            return True

        def is_free_node(p):
            if not in_bounds(p):
                return False
            return not self._is_in_obstacle(p, obstacles, is_3d)

        def is_free_edge(p1, p2):
            dist = math.dist(p1, p2)
            resolution = min(1.0, self.step_size*0.5)
            steps = max(1, int(dist / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * i / steps for j in range(dim))
                if self._is_in_obstacle(interp, obstacles, is_3d):
                    return False
            return True

        def steer(from_pos, to_pos, max_dist):
            dist = math.dist(from_pos, to_pos)
            if dist <= max_dist:
                return to_pos
            ratio = max_dist / dist
            return tuple(from_pos[i] + ratio * (to_pos[i] - from_pos[i]) for i in range(dim))

        def sample_uniform():
            while True:
                p = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if is_free_node(p):
                    return p

        def sample_goal():
            if is_free_node(goal_pos):
                return goal_pos
            else:
                return sample_uniform()

        def sample_ellipsoid(c_best, c_min, x_center, C):
            # Sample uniformly inside unit ball and transform via C*ball + x_center
            # Returns point in informed subset ellipsoid focusing near optimal path.
            while True:
                ball = self._sample_unit_ball(dim)
                if ball is None:
                    return sample_uniform()  # fallback
                point_local = tuple(ball[i] * c_best / 2 if i == 0 else ball[i] * math.sqrt(c_best**2 - c_min**2) / 2 for i in range(dim))
                p = tuple(sum(C[i][j] * point_local[j] for j in range(dim)) + x_center[i] for i in range(dim))
                if in_bounds(p) and is_free_node(p):
                    return p

        def nearest(tree_nodes: List[Node], point: Tuple[float, ...]) -> Optional[Node]:
            if not tree_nodes:
                return None
            best_node = tree_nodes[0]
            best_dist = math.dist(best_node.position, point)
            for n in tree_nodes[1:]:
                d = math.dist(n.position, point)
                if d < best_dist:
                    best_dist = d
                    best_node = n
            return best_node

        def near_nodes(tree_nodes: List[Node], point: Tuple[float, ...]) -> List[Node]:
            n = len(tree_nodes)
            if n < 2:
                return []
            rad = self.gamma_rrt_star * (math.log(n) / n) ** (1 / dim)
            radius = min(self.max_neighbor_radius, max(self.step_size * 2, rad))
            return [node for node in tree_nodes if math.dist(node.position, point) <= radius]

        def rewire(new_node: Node, neighbors: List[Node], edges: List[Tuple[Node, Node]]):
            for nb in neighbors:
                if nb is new_node:
                    continue
                if not self._is_edge_in_obstacle(new_node.position, nb.position, obstacles, is_3d):
                    new_cost = new_node.cost + math.dist(new_node.position, nb.position)
                    if new_cost + 1e-12 < nb.cost:
                        # Update parent and edges
                        if nb.parent is not None:
                            try:
                                edges.remove((nb.parent, nb))
                            except ValueError:
                                pass
                            nb.parent.remove_child(nb)
                        nb.update_parent(new_node, new_cost)
                        edges.append((new_node, nb))

        def choose_parent_and_add(tree_nodes: List[Node], new_pos: Tuple[float, ...]) -> Optional[Node]:
            if not tree_nodes:
                return None
            nearest_node = nearest(tree_nodes, new_pos)
            if nearest_node is None:
                return None
            neighbors = near_nodes(tree_nodes, new_pos)
            # Candidate parent nodes include nearest
            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for nb in neighbors:
                if nb == nearest_node:
                    continue
                if self._is_edge_in_obstacle(nb.position, new_pos, obstacles, is_3d):
                    continue
                cand_cost = nb.cost + math.dist(nb.position, new_pos)
                if cand_cost < min_cost:
                    min_cost = cand_cost
                    best_parent = nb
            if not is_free_edge(best_parent.position, new_pos):
                return None
            new_node = Node(new_pos, None, min_cost)
            best_parent.add_child(new_node)
            nodes_all.append(new_node)
            tree_nodes.append(new_node)
            edges_all.append((best_parent, new_node))
            rewire(new_node, neighbors, edges_all)
            return new_node

        def try_connect(new_node: Node, other_tree: List[Node]) -> Optional[Tuple[List[Tuple[float, ...]], float]]:
            if not other_tree:
                return None
            nearest_other = nearest(other_tree, new_node.position)
            if nearest_other is None:
                return None
            dist_between = math.dist(new_node.position, nearest_other.position)
            if dist_between > self.step_size:
                return None
            if not is_free_edge(new_node.position, nearest_other.position):
                return None
            path1 = new_node.path_from_root()
            path2 = nearest_other.path_from_root()
            # Avoid duplication of meeting node
            if path1[-1] == path2[-1]:
                path2 = path2[:-1]
            full_path = path1 + path2[::-1]
            total_cost = new_node.cost + nearest_other.cost + dist_between
            return full_path, total_cost

        def shortcut_path(path: List[Tuple[float, ...]], timeout_sec=5.0) -> List[Tuple[float, ...]]:
            n = len(path)
            if n < 3:
                return path
            t0 = time.monotonic()
            shortened = [path[0]]
            i = 0
            while i < n - 1:
                end = n - 1
                while end > i + 1:
                    if time.monotonic() - t0 > timeout_sec:
                        break
                    if is_free_edge(path[i], path[end]):
                        shortened.append(path[end])
                        i = end
                        break
                    end -= 1
                else:
                    i += 1
                    if i < n:
                        shortened.append(path[i])
                    continue
            return shortened

        def compute_rotation_matrix(unit_x: Tuple[float, ...]) -> List[List[float]]:
            # Returns rotation matrix C such that C * (c_min/2,0,..0) aligns with unit_x
            if dim == 2:
                x, y = unit_x
                norm = math.hypot(x, y)
                if norm < 1e-14:
                    return [[1, 0], [0, 1]]
                x /= norm
                y /= norm
                return [[x, -y],[y, x]]
            elif dim == 3:
                # Use SVD on unit_x to generate rotation matrix
                import math
                ux, uy, uz = unit_x
                norm = math.sqrt(ux*ux+uy*uy+uz*uz)
                if norm < 1e-14:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                ux /= norm
                uy /= norm
                uz /= norm
                # Pick arbitrary vector not colinear to unit_x
                v = (0,0,1) if abs(uz) < 0.9 else (0,1,0)
                # Compute axis = unit_x cross v
                ax = uy*v[2] - uz*v[1]
                ay = uz*v[0] - ux*v[2]
                az = ux*v[1] - uy*v[0]
                axis_length = math.sqrt(ax*ax + ay*ay + az*az)
                if axis_length < 1e-14:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                ax /= axis_length
                ay /= axis_length
                az /= axis_length
                angle = math.acos(ux*0 + uy*0 + uz*1)
                c = math.cos(angle)
                s = math.sin(angle)
                t = 1 - c
                C = [[0.0]*3 for _ in range(3)]
                C[0][0] = t*ax*ax + c
                C[0][1] = t*ax*ay - s*az
                C[0][2] = t*ax*az + s*ay
                C[1][0] = t*ax*ay + s*az
                C[1][1] = t*ay*ay + c
                C[1][2] = t*ay*az - s*ax
                C[2][0] = t*ax*az - s*ay
                C[2][1] = t*ay*az + s*ax
                C[2][2] = t*az*az + c
                return C
            else:
                # For higher dims, just identity
                return [[1 if i == j else 0 for j in range(dim)] for i in range(dim)]

        def informed_sample(c_best: float, c_min: float, start: Tuple[float, ...], goal: Tuple[float, ...]) -> Tuple[float, ...]:
            if c_best == float('inf'):
                return sample_uniform()
            # Define ellipse parameters
            midpoint = tuple((start[i] + goal[i]) / 2 for i in range(dim))
            diff = tuple(goal[i] - start[i] for i in range(dim))
            norm_diff = math.dist(start, goal)
            if norm_diff < 1e-14:
                return sample_uniform()
            unit_x = tuple(d / norm_diff for d in diff)
            C = compute_rotation_matrix(unit_x)
            # Ellipsoid radii
            r1 = c_best / 2.0
            r2 = math.sqrt(c_best ** 2 - c_min ** 2) / 2.0
            # Create diagonal scaling matrix but use in sample_ellipsoid logic
            while True:
                # Sample in unit n-ball
                u = self._sample_unit_ball(dim)
                if u is None:
                    return sample_uniform()
                # Scale sample: first dimension scaled by r1, others by r2
                local = [0.0]*dim
                local[0] = u[0]*r1
                for i in range(1, dim):
                    local[i] = u[i]*r2
                # Rotate and translate
                point = []
                for i in range(dim):
                    s = 0.0
                    for j in range(dim):
                        s += C[i][j]*local[j]
                    point.append(s + midpoint[i])
                if in_bounds(point) and is_free_node(tuple(point)):
                    return tuple(point)

        def _sample_unit_ball(d: int) -> Optional[Tuple[float, ...]]:
            # Rejection sample from unit ball
            for _ in range(100):
                coord = tuple(random.uniform(-1,1) for _ in range(d))
                norm = math.sqrt(sum(x*x for x in coord))
                if norm <= 1 and norm > 1e-14:
                    return tuple(x / norm for x in coord)
            return None

        start_root = Node(start_pos, None, 0.0)
        goal_root = Node(goal_pos, None, 0.0)
        nodes_all.extend([start_root, goal_root])
        tree_start = [start_root]
        tree_goal = [goal_root]

        best_path: List[Tuple[float, ...]] = []
        best_cost = float('inf')
        found_solution = False

        for iter_count in range(self.max_iter):
            if time.monotonic() - t_start > TIME_LIMIT:
                break

            if found_solution:
                sample = informed_sample(best_cost, math.dist(start_pos, goal_pos), start_pos, goal_pos)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = sample_goal()
                else:
                    sample = sample_uniform()

            # Alternate tree expansion
            if iter_count % 2 == 0:
                tree_a, tree_b = tree_start, tree_goal
            else:
                tree_a, tree_b = tree_goal, tree_start

            nearest_node = nearest(tree_a, sample)
            if nearest_node is None:
                continue
            new_pos = steer(nearest_node.position, sample, self.step_size)
            if not is_free_node(new_pos):
                continue
            new_node = choose_parent_and_add(tree_a, new_pos)
            if new_node is None:
                continue

            connect_result = try_connect(new_node, tree_b)
            if connect_result is not None:
                path_candidate, cost_candidate = connect_result
                if cost_candidate < best_cost:
                    best_cost = cost_candidate
                    best_path = path_candidate
                    found_solution = True
                    # Early stop on good solution or keep searching until time runs out
                    # Here we break immediately to reduce planning time
                    break

        # If no path found, try direct connection if feasible
        if not found_solution:
            nearest_goal = nearest(tree_start, goal_pos)
            if nearest_goal and is_free_edge(nearest_goal.position, goal_pos):
                p = nearest_goal.path_from_root() + [goal_pos]
                best_path = p
                found_solution = False

        if best_path:
            # Iterative shortcutting to further improve smoothness & length within small time to not exceed time limit
            t_sc_start = time.monotonic()
            time_for_shortcut = max(1.0, TIME_LIMIT - (time.monotonic() - t_start) - 0.5)
            # Multiple iterations of shortcutting limited by remaining time
            for _ in range(8):
                best_path = shortcut_path(best_path, timeout_sec=time_for_shortcut)
                if time.monotonic() - t_sc_start > time_for_shortcut:
                    break

        return PlannerResult(
            success=found_solution,
            path=best_path,
            nodes=nodes_all,
            edges=edges_all
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=0.5):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter=5000, step_size=5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.time_limit = 30.0
        self.improve_tol = 1e-5
        self.max_no_improve = 150
        self.prune_every = 300
        self.goal_sample_rate_start = 0.05
        self.goal_sample_rate_max = 0.25
        self.goal_sample_rate_inc = 1e-4

    def plan(self, map) -> 'PlannerResult':
        import time
        import math
        import random

        start_time = time.monotonic()

        bounds = map.size
        dim = len(bounds)
        is_3d = dim == 3
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        # Trees
        tree_start = []
        tree_goal = []

        nodes = []
        edges = []

        start_node = Node(start_pos, cost=0.0)
        goal_node = Node(goal_pos, cost=0.0)
        tree_start.append(start_node)
        tree_goal.append(goal_node)
        nodes.append(start_node)
        nodes.append(goal_node)

        best_cost = float('inf')
        best_start_node = None
        best_goal_node = None
        found_solution = False
        no_improve_count = 0
        goal_sample_rate = self.goal_sample_rate_start

        c_min = math.dist(start_pos, goal_pos)

        def within_bounds(p):
            return all(0.0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            step = self.step_size
            if d <= step:
                return to_p
            ratio = step / d
            return tuple(from_p[i] + (to_p[i] - from_p[i]) * ratio for i in range(dim))

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d_ = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d_:
                        return True
                else:
                    x, y, w_, h_ = obs
                    px, py = pos
                    if x <= px <= x + w_ and y <= py <= y + h_:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2, resolution=1.0):
            distance = dist(p1, p2)
            steps = max(1, int(distance / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[d] + (p2[d] - p1[d]) * (i / steps) for d in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def uniform_sample():
            for _ in range(100):
                sample = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(sample):
                    return sample
            return start_pos

        def informed_sample(c_best, c_min):
            if not found_solution or c_best == float('inf'):
                return uniform_sample()
            center = tuple((start_pos[i] + goal_pos[i]) * 0.5 for i in range(dim))
            a1 = tuple(goal_pos[i] - start_pos[i] for i in range(dim))
            len_a1 = math.sqrt(sum(x * x for x in a1))
            if len_a1 < 1e-10:
                return uniform_sample()
            e1 = tuple(x / len_a1 for x in a1)

            if dim == 2:
                r1 = c_best * 0.5
                r2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) * 0.5
                axes = (r1, r2)
            elif dim == 3:
                r1 = c_best * 0.5
                r_side = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) * 0.5
                axes = (r1, r_side, r_side)
            else:
                return uniform_sample()

            attempts = 0
            while attempts < 200:
                raw = [random.gauss(0, 1) for _ in range(dim)]
                norm = math.sqrt(sum(x * x for x in raw))
                if norm < 1e-10:
                    attempts += 1
                    continue
                unit = [x / norm for x in raw]
                radius = random.random() ** (1.0 / dim)
                scaled = [radius * axes[i] * unit[i] for i in range(dim)]

                # Rotate to align first axis e1 (only 2D/3D cases)
                v = [1.0] + [0.0] * (dim - 1)
                dot = sum(v[i] * e1[i] for i in range(dim))
                if abs(dot - 1.0) < 1e-10:
                    rotated = scaled
                elif abs(dot + 1.0) < 1e-10:
                    rotated = [-scaled[0]] + scaled[1:]
                else:
                    if dim == 2:
                        angle = math.acos(dot)
                        cos_a = math.cos(angle)
                        sin_a = math.sin(angle)
                        x0, x1 = scaled[0], scaled[1]
                        rotated = (x0 * cos_a - x1 * sin_a, x0 * sin_a + x1 * cos_a)
                    elif dim == 3:
                        k = [v[1] * e1[2] - v[2] * e1[1],
                             v[2] * e1[0] - v[0] * e1[2],
                             v[0] * e1[1] - v[1] * e1[0]]
                        k_norm = math.sqrt(sum(kk * kk for kk in k))
                        if k_norm < 1e-10:
                            rotated = scaled
                        else:
                            k = [kk / k_norm for kk in k]
                            angle = math.acos(dot)
                            cos_a = math.cos(angle)
                            sin_a = math.sin(angle)
                            v_vec = scaled
                            k_dot_v = sum(k[i] * v_vec[i] for i in range(dim))
                            cross = [k[1] * v_vec[2] - k[2] * v_vec[1],
                                     k[2] * v_vec[0] - k[0] * v_vec[2],
                                     k[0] * v_vec[1] - k[1] * v_vec[0]]
                            rotated = [0.0] * 3
                            for i in range(3):
                                rotated[i] = v_vec[i] * cos_a + cross[i] * sin_a + k[i] * k_dot_v * (1 - cos_a)
                    else:
                        rotated = scaled
                sample = tuple(center[i] + rotated[i] for i in range(dim))
                if all(0.0 <= sample[i] <= bounds[i] for i in range(dim)):
                    if not is_in_obstacle(sample):
                        return sample
                attempts += 1
            return uniform_sample()

        def nearest_node(tree, point):
            return min(tree, key=lambda n: dist(n.position, point))

        def near_nodes(tree, point, radius):
            return [n for n in tree if dist(n.position, point) <= radius]

        def prune_disconnected_nodes():
            to_remove = []
            for node in nodes:
                if not node.valid:
                    to_remove.append(node)
                    continue
                if node.parent is None and node not in (start_node, goal_node):
                    to_remove.append(node)
            for n in to_remove:
                if n.parent is not None:
                    n.parent.remove_child(n)
                for c in list(n.children):
                    c.parent = None
                if n in nodes:
                    nodes.remove(n)
                # remove edges involving n if present
                for e in list(edges):
                    if e[0] == n or e[1] == n:
                        edges.remove(e)
                if n in tree_start:
                    tree_start.remove(n)
                if n in tree_goal:
                    tree_goal.remove(n)

        def shortcut_path(path):
            if len(path) < 3:
                return path
            shortened = [path[0]]
            i = 0
            while i < len(path) - 1:
                j = len(path) - 1
                while j > i + 1:
                    if not is_edge_in_obstacle(path[i], path[j]):
                        break
                    j -= 1
                shortened.append(path[j])
                i = j
            return shortened

        # Alternate tree expansion flag (True = start tree, False = goal tree)
        expand_start_tree = True

        for iteration in range(1, self.max_iter + 1):
            current_time = time.monotonic()
            if current_time - start_time > self.time_limit:
                break

            # Increase goal bias progressively capped by max rate
            if goal_sample_rate < self.goal_sample_rate_max:
                goal_sample_rate = min(self.goal_sample_rate_max, goal_sample_rate + self.goal_sample_rate_inc)

            tree_a = tree_start if expand_start_tree else tree_goal
            tree_b = tree_goal if expand_start_tree else tree_start

            # Sample with goal bias and informed sampling if solution found
            p_rand = random.random()
            if p_rand < goal_sample_rate and not is_in_obstacle(goal_pos):
                sample = goal_pos
            else:
                c_best = best_cost if found_solution else float('inf')
                sample = informed_sample(c_best, c_min)

            if not within_bounds(sample):
                expand_start_tree = not expand_start_tree
                continue

            nearest_n = nearest_node(tree_a, sample)
            new_pos = steer(nearest_n.position, sample)
            if not within_bounds(new_pos) or is_in_obstacle(new_pos) or is_edge_in_obstacle(nearest_n.position, new_pos):
                expand_start_tree = not expand_start_tree
                continue

            # Adaptive radius, scaled with tree size
            n_nodes = len(tree_a) + 1
            gamma = 30.0  # Practical tuning constant
            radius = min(gamma * (math.log(n_nodes) / n_nodes) ** (1.0 / dim), self.step_size * 20.0)
            radius = max(radius, self.step_size)

            neighbors = near_nodes(tree_a, new_pos, radius)

            # Choose best parent minimizing cost + dist
            best_parent = nearest_n
            best_cost_through = nearest_n.cost + dist(nearest_n.position, new_pos)
            for nb in neighbors:
                if nb == nearest_n:
                    continue
                cost_candidate = nb.cost + dist(nb.position, new_pos)
                if cost_candidate + self.improve_tol < best_cost_through:
                    if not is_edge_in_obstacle(nb.position, new_pos):
                        best_parent = nb
                        best_cost_through = cost_candidate

            new_node = Node(new_pos, parent=best_parent, cost=best_cost_through)
            best_parent.add_child(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors via new_node if improves cost
            for nb in neighbors:
                if nb == new_node or nb == best_parent:
                    continue
                alt_cost = new_node.cost + dist(new_node.position, nb.position)
                if alt_cost + self.improve_tol < nb.cost and not is_edge_in_obstacle(new_node.position, nb.position):
                    if nb.parent is not None:
                        try:
                            edges.remove((nb.parent, nb))
                        except ValueError:
                            pass
                        nb.parent.remove_child(nb)
                    new_node.add_child(nb)
                    nb.cost = alt_cost
                    edges.append((new_node, nb))

            # Try connecting new_node to tree_b nodes (within radius) for path connection
            near_other = near_nodes(tree_b, new_pos, radius)
            for other_node in near_other:
                if is_edge_in_obstacle(new_pos, other_node.position):
                    continue
                total_cost = new_node.cost + dist(new_pos, other_node.position) + other_node.cost
                if total_cost + self.improve_tol < best_cost:
                    best_cost = total_cost
                    best_start_node = new_node if expand_start_tree else other_node
                    best_goal_node = other_node if expand_start_tree else new_node
                    found_solution = True
                    no_improve_count = 0

            if found_solution:
                no_improve_count += 1
                if no_improve_count >= self.max_no_improve:
                    break

            # Periodic pruning of invalid/disconnected nodes
            if iteration % self.prune_every == 0 and found_solution:
                prune_disconnected_nodes()

            expand_start_tree = not expand_start_tree

        success_state = found_solution and best_start_node is not None and best_goal_node is not None
        if success_state:
            path_start = best_start_node.path_from_root()
            path_goal = best_goal_node.path_from_root()
            if path_start[-1] == path_goal[0]:
                path_goal = path_goal[1:]
            raw_path = path_start + path_goal[::-1]
            extracted_path = shortcut_path(raw_path)
        else:
            # Fallback: closest node in start tree to goal
            closest = None
            min_d = float('inf')
            for node in tree_start:
                d = dist(node.position, goal_pos)
                if d < min_d and node.valid:
                    min_d = d
                    closest = node
            if closest is not None:
                extracted_path = closest.path_from_root()
            else:
                extracted_path = []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self,
                 max_iter: int = 12000,
                 step_size: float = 4.0,
                 goal_sample_rate: float = 0.10,
                 post_opt_iters: int = 250,
                 max_no_improve: int = 60,
                 improve_tol: float = 1e-6,
                 time_limit_sec: float = 30.0,
                 gamma_rrt_star: float = 35.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.post_opt_iters = post_opt_iters
        self.max_no_improve = max_no_improve
        self.improve_tol = improve_tol
        self.time_limit_sec = time_limit_sec
        self.gamma_rrt_star = gamma_rrt_star

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = dim == 3

        def dist(a, b):
            return math.dist(a, b)

        def steer(fr, to):
            d = dist(fr, to)
            if d <= self.step_size:
                return to
            r = self.step_size / d
            return tuple(fr[i] + (to[i] - fr[i]) * r for i in range(dim))

        def within_bounds(p):
            return all(0.0 <= p[i] <= bounds[i] for i in range(dim))

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w_, h_ = obs
                    px, py = pos
                    if x <= px <= x + w_ and y <= py <= y + h_:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2, resolution=1.0):
            length = dist(p1, p2)
            steps = max(2, int(length / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def uniform_sample():
            trials = 0
            while True:
                s = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not is_in_obstacle(s):
                    return s
                trials += 1
                if trials > 1000:
                    return start_pos

        def informed_sample(start, goal, c_best, c_min):
            if c_best == float('inf') or c_min <= 1e-12:
                return uniform_sample()
            center = tuple((s + g) * 0.5 for s, g in zip(start, goal))
            a1 = tuple((g - s) / c_min for s, g in zip(start, goal))
            if dim == 2:
                a2 = (-a1[1], a1[0])
                basis = [a1, a2]
                r1 = c_best / 2.0
                r2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0.0)) / 2.0
                radii = [r1, r2]
            else:
                ref = (1., 0., 0.) if abs(a1[0]) < 0.9 else (0.,1.,0.)
                b2 = self._normalize(self._cross(a1, ref))
                if self._norm(b2) < 1e-12:
                    ref = (0., 0., 1.)
                    b2 = self._normalize(self._cross(a1, ref))
                b3 = self._cross(a1, b2)
                basis = [a1, b2, b3]
                r1 = c_best / 2.0
                r_other = math.sqrt(max(c_best*c_best - c_min*c_min, 0.0)) / 2.0
                radii = [r1, r_other, r_other]

            def sample_unit_ball(dim_):
                while True:
                    v = [random.gauss(0,1) for _ in range(dim_)]
                    norm_ = math.sqrt(sum(x*x for x in v))
                    if norm_ > 1e-12:
                        v = [x/norm_ for x in v]
                        r = random.random()**(1.0/dim_)
                        return [x*r for x in v]

            u = sample_unit_ball(dim)
            mapped = list(center)
            for i in range(dim):
                for j in range(dim):
                    mapped[i] += basis[j][i] * radii[j] * u[j]
            mapped = tuple(min(max(mapped[i],0.0), bounds[i]) for i in range(dim))
            if is_in_obstacle(mapped):
                return uniform_sample()
            return mapped

        def nearest(nodes_list, p):
            min_node = None
            min_d = float('inf')
            for n in nodes_list:
                d_ = dist(n.position, p)
                if d_ < min_d:
                    min_d = d_
                    min_node = n
            return min_node

        def neighbor_radius(n_nodes):
            if n_nodes < 2:
                return max(2.5, self.step_size * 1.5)
            val = self.gamma_rrt_star * ((math.log(n_nodes) / n_nodes) ** (1.0/dim))
            max_r = max(bounds) * 0.22
            min_r = max(2.5, self.step_size * 1.2)
            return max(min_r, min(max_r, val))

        def near_nodes(nodes_list, p, radius):
            result = []
            r_sq = radius*radius
            for n in nodes_list:
                d_ = dist(n.position, p)
                if d_ <= radius:
                    result.append(n)
            return result

        def add_node_rrt_star(tree, pos, nodes_all, edges_list):
            r = neighbor_radius(len(tree))
            neighbors = near_nodes(tree, pos, r)
            parents = [n for n in neighbors if not is_edge_in_obstacle(n.position, pos)]
            if not parents:
                p = nearest(tree, pos)
                if is_edge_in_obstacle(p.position, pos):
                    return None, []
                parents = [p]
            best_parent = min(parents, key=lambda n: n.cost + dist(n.position, pos))
            new_cost = best_parent.cost + dist(best_parent.position, pos)
            new_node = Node(pos, parent=None, cost=new_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            nodes_all.append(new_node)
            edges_list.append((best_parent, new_node))
            return new_node, neighbors

        def propagate_cost_to_children(node):
            for child in node.children:
                old_cost = child.cost
                child.cost = node.cost + dist(node.position, child.position)
                if child.cost + 1e-12 < old_cost:
                    propagate_cost_to_children(child)

        def rewire(tree, new_node, neighbors, edges_list):
            for nb in neighbors:
                if nb is new_node:
                    continue
                if is_edge_in_obstacle(new_node.position, nb.position):
                    continue
                new_cost = new_node.cost + dist(new_node.position, nb.position)
                if new_cost + 1e-12 < nb.cost:
                    if nb.parent:
                        nb.parent.remove_child(nb)
                        try:
                            edges_list.remove((nb.parent, nb))
                        except ValueError:
                            pass
                    new_node.add_child(nb)
                    nb.cost = new_cost
                    edges_list.append((new_node, nb))
                    propagate_cost_to_children(nb)

        def can_connect(n1, n2):
            return not is_edge_in_obstacle(n1.position, n2.position) and n1.valid and n2.valid

        def stitch_path(n_start, n_goal, a_is_start):
            path_a = n_start.path_from_root()
            path_b = n_goal.path_from_root()
            if path_a and path_b and path_a[-1] == path_b[-1]:
                merged = path_a + path_b[-2::-1]
            else:
                merged = path_a + path_b[::-1]
            if a_is_start:
                return merged
            else:
                return merged[::-1]

        def shortcut_path(path):
            if len(path) < 3:
                return path
            smoothed = [path[0]]
            i = 0
            while i < len(path) - 1:
                j = len(path) - 1
                while j > i + 1:
                    if not is_edge_in_obstacle(path[i], path[j]):
                        break
                    j -= 1
                smoothed.append(path[j])
                i = j
            return smoothed

        start_time = time.monotonic()
        tree_start = [Node(start_pos, cost=0.0)]
        tree_goal = [Node(goal_pos, cost=0.0)]
        nodes = [tree_start[0], tree_goal[0]]
        edges = []

        best_path = []
        best_cost = float('inf')
        found_first = False
        post_no_improve = 0
        post_iter = 0
        c_min = dist(start_pos, goal_pos)

        for it in range(self.max_iter):
            elapsed = time.monotonic() - start_time
            if elapsed > self.time_limit_sec:
                break

            curr_tree = tree_start if it % 2 == 0 else tree_goal
            other_tree = tree_goal if it % 2 == 0 else tree_start
            a_is_start_side = (it % 2 == 0)

            if found_first:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if a_is_start_side else start_pos
                else:
                    sample = informed_sample(start_pos, goal_pos, best_cost, c_min)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if a_is_start_side else start_pos
                else:
                    sample = uniform_sample()

            nearest_node_curr = nearest(curr_tree, sample)
            new_pos = steer(nearest_node_curr.position, sample)

            if not within_bounds(new_pos) or is_in_obstacle(new_pos) or is_edge_in_obstacle(nearest_node_curr.position, new_pos):
                if found_first:
                    post_no_improve += 1
                    post_iter += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break
                continue

            new_node, neighbors = add_node_rrt_star(curr_tree, new_pos, nodes, edges)
            if not new_node:
                if found_first:
                    post_no_improve += 1
                    post_iter += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break
                continue

            rewire(curr_tree, new_node, neighbors, edges)

            nearest_other = nearest(other_tree, new_node.position)
            connected_other = None

            # Try a direct connection if within step_size and collision free
            if dist(nearest_other.position, new_node.position) <= self.step_size and can_connect(nearest_other, new_node):
                final_node = Node(new_node.position, parent=None,
                                  cost=nearest_other.cost + dist(nearest_other.position, new_node.position))
                nearest_other.add_child(final_node)
                other_tree.append(final_node)
                nodes.append(final_node)
                edges.append((nearest_other, final_node))
                connected_other = final_node
            else:
                # Incremental greedy connection attempts (limit reduced to improve speed)
                current = nearest_other
                max_greedy_steps = 15
                tries = 0
                while tries < max_greedy_steps:
                    tries += 1
                    step_pos = steer(current.position, new_node.position)
                    if not within_bounds(step_pos) or is_in_obstacle(step_pos) or is_edge_in_obstacle(current.position, step_pos):
                        break
                    other_new, other_neighbors = add_node_rrt_star(other_tree, step_pos, nodes, edges)
                    if not other_new:
                        break
                    rewire(other_tree, other_new, other_neighbors, edges)
                    current = other_new
                    if dist(current.position, new_node.position) <= self.step_size and can_connect(current, new_node):
                        final_node = Node(new_node.position, parent=None,
                                          cost=current.cost + dist(current.position, new_node.position))
                        current.add_child(final_node)
                        other_tree.append(final_node)
                        nodes.append(final_node)
                        edges.append((current, final_node))
                        connected_other = final_node
                        break

            if connected_other is not None:
                candidate_path = stitch_path(new_node, connected_other, a_is_start_side)
                cost_candidate = 0.0
                for i in range(len(candidate_path) - 1):
                    cost_candidate += dist(candidate_path[i], candidate_path[i+1])

                improved = False
                if cost_candidate + self.improve_tol < best_cost:
                    best_cost = cost_candidate
                    best_path = candidate_path
                    found_first = True
                    post_iter = 0
                    post_no_improve = 0
                    improved = True
                if not improved:
                    if found_first:
                        post_no_improve += 1
                if found_first:
                    post_iter += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break
            else:
                if found_first:
                    post_iter += 1
                    post_no_improve += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break

        if best_path:
            best_path = shortcut_path(best_path)
            return PlannerResult(
                success=True,
                path=best_path,
                nodes=nodes,
                edges=edges
            )
        else:
            return PlannerResult(
                success=False,
                path=[],
                nodes=nodes,
                edges=edges
            )

    def _cross(self, a, b):
        ax, ay, az = a
        bx, by, bz = b
        return (ay * bz - az * by, az * bx - ax * bz, ax * by - ay * bx)

    def _norm(self, v):
        return math.sqrt(sum(x*x for x in v))

    def _normalize(self, v):
        n = self._norm(v)
        if n < 1e-12:
            return v
        return tuple(x / n for x in v)

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position       # Tuple of coordinates
        self.parent = parent           # Parent Node or None
        self.cost = cost               # Cost from start to this node
        self.children = []             # List of child nodes
        self.valid = True              # Mark node as valid or pruned

    def add_child(self, child):
        self.children.append(child)

    def remove_child(self, child):
        try:
            self.children.remove(child)
        except ValueError:
            pass

    def update_parent(self, new_parent, new_cost):
        if self.parent is not new_parent:
            if self.parent is not None:
                self.parent.remove_child(self)
            self.parent = new_parent
            self.cost = new_cost
            if new_parent is not None:
                new_parent.add_child(self)
class Planner:
    def __init__(self, max_iter: int=15000, step_size: float=5.0,
                 goal_sample_rate: float=0.15, max_neighbor_radius: float=20.0,
                 min_neighbor_radius: float=4.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.max_neighbor_radius = max_neighbor_radius
        self.min_neighbor_radius = min_neighbor_radius

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        start_time = time.monotonic()
        time_limit = 30.0

        nodes: List[Node] = []
        edges: List[Tuple[Node, Node]] = []

        def dist(a: Tuple[float, ...], b: Tuple[float, ...]) -> float:
            return math.dist(a,b)

        def in_bounds(pos):
            for i in range(dim):
                if pos[i] < 0 or pos[i] > bounds[i]:
                    return False
            return True

        def is_free_node(pos):
            if not in_bounds(pos):
                return False
            return not self._is_in_obstacle(pos, obstacles, is_3d)

        def is_free_edge(p1, p2):
            d = dist(p1, p2)
            resolution = min(1.0, self.step_size * 0.4)
            steps = max(1, int(d / resolution))
            for i in range(steps+1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i/steps) for j in range(dim))
                if self._is_in_obstacle(interp, obstacles, is_3d):
                    return False
            return True

        def steer(from_pos, to_pos, max_len):
            d = dist(from_pos, to_pos)
            if d <= max_len:
                return to_pos
            ratio = max_len / d
            return tuple(from_pos[i] + ratio * (to_pos[i] - from_pos[i]) for i in range(dim))

        def heuristic_cost(a, b):
            return dist(a, b)

        # Informed sampling inside ellipsoid after first solution
        def sample_informed(c_best, x_start, x_goal):
            if math.isinf(c_best):
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))
            c_min = heuristic_cost(x_start, x_goal)
            if c_min == 0.0:
                return x_start

            center = tuple((x_start[i] + x_goal[i]) * 0.5 for i in range(dim))
            a1 = tuple((x_goal[i] - x_start[i]) / c_min for i in range(dim))

            def build_rotation_matrix(a):
                if dim == 2:
                    cos_theta, sin_theta = a[0], a[1]
                    return [[cos_theta, -sin_theta],
                            [sin_theta,  cos_theta]]
                elif dim == 3:
                    x_axis = a
                    # Arbitrary vector not colinear with x_axis
                    if abs(x_axis[0]) < 0.9:
                        temp = (1,0,0)
                    else:
                        temp = (0,1,0)
                    v = [temp[i] - sum(x_axis[j]*temp[j] for j in range(3))*x_axis[i] for i in range(3)]
                    norm_v = math.sqrt(sum(vi*vi for vi in v))
                    if norm_v < 1e-10:
                        return [[1,0,0],[0,1,0],[0,0,1]]
                    v_unit = [vi/norm_v for vi in v]
                    w = [x_axis[1]*v_unit[2]-x_axis[2]*v_unit[1],
                         x_axis[2]*v_unit[0]-x_axis[0]*v_unit[2],
                         x_axis[0]*v_unit[1]-x_axis[1]*v_unit[0]]
                    R = [list(x_axis), v_unit, w]
                    # transpose
                    return [[R[0][0], R[1][0], R[2][0]],
                            [R[0][1], R[1][1], R[2][1]],
                            [R[0][2], R[1][2], R[2][2]]]
                else:
                    return None

            R = build_rotation_matrix(a1)
            if R is None:
                return tuple(random.uniform(0,bounds[d]) for d in range(dim))

            r1 = c_best / 2.0
            val = c_best*c_best - c_min*c_min
            r_rest = math.sqrt(val)/2.0 if val > 0 else 0.001
            radii = [r1] + [r_rest]*(dim-1)

            while True:
                rnd = [random.gauss(0,1) for _ in range(dim)]
                norm_rnd = math.sqrt(sum(x*x for x in rnd))
                if norm_rnd > 1e-10:
                    break
            unit_ball = [x / norm_rnd for x in rnd]
            u = random.random()
            scale = u**(1/dim)
            sample_ball = [scale * unit_ball[i] * radii[i] for i in range(dim)]

            sample = [0.0]*dim
            for i in range(dim):
                for j in range(dim):
                    sample[i] += R[i][j] * sample_ball[j]
                sample[i] += center[i]

            clipped = tuple(min(max(0.0, sample[i]), bounds[i]) for i in range(dim))
            return clipped

        # Adaptive neighbor radius function
        def radius_adaptive(n_nodes):
            if n_nodes < 2:
                return self.max_neighbor_radius
            val = (math.log(n_nodes) / n_nodes)**(1/dim)
            r = self.max_neighbor_radius * val
            return max(min(r, self.max_neighbor_radius), self.min_neighbor_radius)

        # Spatial grid for neighbor lookup
        class SpatialGrid:
            def __init__(self, domain, cell_size):
                self.domain = domain
                self.cell_size = cell_size
                self.dim = len(domain)
                self.cells = {}

            def _cell_coords(self, p):
                return tuple(int(p[d] // self.cell_size) for d in range(self.dim))

            def add_node(self, node):
                cc = self._cell_coords(node.position)
                if cc not in self.cells:
                    self.cells[cc] = []
                self.cells[cc].append(node)

            def neighbor_candidates(self, p, radius):
                ci = self._cell_coords(p)
                r_cells = int(math.ceil(radius / self.cell_size))
                neighbors = []
                def expand_coords(ci, r_cells):
                    if self.dim == 2:
                        for dx in range(-r_cells, r_cells+1):
                            for dy in range(-r_cells, r_cells+1):
                                yield (ci[0]+dx, ci[1]+dy)
                    elif self.dim == 3:
                        for dx in range(-r_cells, r_cells+1):
                            for dy in range(-r_cells, r_cells+1):
                                for dz in range(-r_cells, r_cells+1):
                                    yield (ci[0]+dx, ci[1]+dy, ci[2]+dz)
                    else:
                        return []
                for c in expand_coords(ci, r_cells):
                    if c in self.cells:
                        neighbors.extend(self.cells[c])
                return neighbors

        # Initialize start node and spatial grid
        root = Node(start_pos)
        nodes.append(root)
        edges = []
        grid_cell_size = self.max_neighbor_radius
        spatial_grid = SpatialGrid(bounds, grid_cell_size)
        spatial_grid.add_node(root)

        best_goal_cost = float('inf')
        best_goal_node = None

        # Early stopping criteria
        min_improve = 1e-4
        no_improve_count = 0
        max_no_improve = 1500

        for it in range(self.max_iter):
            if time.monotonic() - start_time > time_limit:
                break

            # Sample point: goal bias + informed sampling after first success
            if best_goal_node is not None:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos
                else:
                    sample = sample_informed(best_goal_cost, start_pos, goal_pos)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos
                else:
                    sample = tuple(random.uniform(0, bounds[d]) for d in range(dim))

            # Nearest node linear search
            nearest = nodes[0]
            min_dist = dist(nearest.position, sample)
            for n in nodes[1:]:
                d_ = dist(n.position, sample)
                if d_ < min_dist:
                    nearest = n
                    min_dist = d_

            new_pos = steer(nearest.position, sample, self.step_size)
            if not is_free_node(new_pos):
                continue
            if not is_free_edge(nearest.position, new_pos):
                continue

            radius = radius_adaptive(len(nodes))

            # Candidate neighbors from spatial grid
            candidates = spatial_grid.neighbor_candidates(new_pos, radius)
            radius_sq = radius*radius
            neighbors = []
            for c in candidates:
                sum_sq = 0.0
                p_c = c.position
                for i_d in range(dim):
                    diff = p_c[i_d] - new_pos[i_d]
                    sum_sq += diff*diff
                    if sum_sq > radius_sq:
                        break
                if sum_sq <= radius_sq:
                    neighbors.append(c)

            # Choose best parent among neighbors
            best_parent = nearest
            min_cost = nearest.cost + dist(nearest.position, new_pos)
            for nb in neighbors:
                tentative = nb.cost + dist(nb.position, new_pos)
                if tentative < min_cost and is_free_edge(nb.position, new_pos):
                    best_parent = nb
                    min_cost = tentative

            new_node = Node(new_pos)
            new_node.update_parent(best_parent, min_cost)
            nodes.append(new_node)
            edges.append((best_parent, new_node))
            spatial_grid.add_node(new_node)

            # Rewire neighbors
            for nb in neighbors:
                if nb is best_parent:
                    continue
                alt_cost = new_node.cost + dist(new_node.position, nb.position)
                if alt_cost + 1e-8 < nb.cost and is_free_edge(new_node.position, nb.position):
                    if nb.parent is not None:
                        try:
                            edges.remove((nb.parent, nb))
                        except ValueError:
                            pass
                    nb.update_parent(new_node, alt_cost)
                    edges.append((new_node, nb))

            # Check connection to goal
            dist_goal = dist(new_pos, goal_pos)
            if dist_goal <= self.step_size and is_free_node(goal_pos) and is_free_edge(new_pos, goal_pos):
                goal_node = Node(goal_pos)
                goal_cost = new_node.cost + dist_goal
                goal_node.update_parent(new_node, goal_cost)
                nodes.append(goal_node)
                edges.append((new_node, goal_node))

                if goal_cost + 1e-8 < best_goal_cost:
                    improvement = best_goal_cost - goal_cost
                    best_goal_cost = goal_cost
                    best_goal_node = goal_node
                    no_improve_count = 0
                else:
                    improvement = 0.0

                if improvement < min_improve:
                    break

            if best_goal_node is not None:
                no_improve_count += 1
                if no_improve_count > max_no_improve:
                    break

        # Extract path from best goal node or closest node if none
        def extract_path(end_node):
            path = []
            curr = end_node
            while curr is not None:
                path.append(curr.position)
                curr = curr.parent
            path.reverse()
            return path

        success_state = False
        if best_goal_node is not None:
            success_state = True
            extracted_path = extract_path(best_goal_node)
        else:
            # Closest node to goal fallback
            min_d_goal = float('inf')
            closest = None
            for n in nodes:
                d_ = dist(n.position, goal_pos)
                if d_ < min_d_goal:
                    min_d_goal = d_
                    closest = n
            if closest is not None:
                extracted_path = extract_path(closest)
            else:
                extracted_path = [start_pos]

        # Multi-pass shortcutting procedure to improve smoothness and length
        def shortcut_path(path: List[Tuple[float, ...]]) -> List[Tuple[float, ...]]:
            if len(path) <= 2:
                return path[:]
            new_path = path[:]
            max_passes = 4
            max_iter = 250
            for _ in range(max_passes):
                improved = False
                iteration = 0
                while iteration < max_iter and len(new_path) > 2:
                    iteration += 1
                    # Attempt random shortcuts
                    i = random.randint(0, len(new_path) - 3)
                    j = random.randint(i+2, len(new_path) -1)
                    p1 = new_path[i]
                    p2 = new_path[j]
                    if is_free_edge(p1, p2):
                        # Remove intermediate nodes i+1,...,j-1
                        del new_path[i+1:j]
                        improved = True
                if not improved:
                    break
            return new_path

        extracted_path = shortcut_path(extracted_path)

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges,
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []

    def add_child(self, child):
        self.children.append(child)

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
class Planner:
    def __init__(self, max_iter=5000, step_size=5.0, goal_sample_rate=0.05, rewiring_radius=10.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.rewiring_radius = rewiring_radius

    def plan(self, map):
        import time
        import random
        import math

        start_time = time.monotonic()
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        nodes = []
        edges = []

        root = Node(start_pos, None, 0.0)
        nodes.append(root)

        success = False
        goal_node = None

        for _ in range(self.max_iter):
            if time.monotonic() - start_time > 30:
                break

            # Goal biased sampling
            if random.random() < self.goal_sample_rate:
                sample = goal_pos
            else:
                sample = tuple(random.uniform(0, bounds[d]) for d in range(dim))

            # Find nearest node
            nearest = min(nodes, key=lambda n: math.dist(n.position, sample))
            direction = tuple(sample[d] - nearest.position[d] for d in range(dim))
            dist_to_sample = math.dist(nearest.position, sample)
            if dist_to_sample == 0:
                continue
            scale = min(self.step_size, dist_to_sample) / dist_to_sample
            new_pos = tuple(nearest.position[d] + direction[d]*scale for d in range(dim))

            if not in_bounds(new_pos):
                continue
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                continue
            if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
                continue

            # Find neighbors to consider for rewiring within fixed radius
            neighbors = [n for n in nodes if math.dist(n.position, new_pos) <= self.rewiring_radius]

            # Select parent minimizing cost + dist with collision-free edge
            min_cost = nearest.cost + math.dist(nearest.position, new_pos)
            best_parent = nearest
            for nb in neighbors:
                cost_through_nb = nb.cost + math.dist(nb.position, new_pos)
                if cost_through_nb < min_cost:
                    if not self._is_edge_in_obstacle(nb.position, new_pos, obstacles, is_3d):
                        min_cost = cost_through_nb
                        best_parent = nb

            new_node = Node(new_pos)
            new_node.update_parent(best_parent, min_cost)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors if passing through new_node improves cost collision-free
            for nb in neighbors:
                if nb is best_parent:
                    continue
                cost_through_new = new_node.cost + math.dist(new_node.position, nb.position)
                if cost_through_new < nb.cost:
                    if not self._is_edge_in_obstacle(new_node.position, nb.position, obstacles, is_3d):
                        try:
                            edges.remove((nb.parent, nb))
                        except ValueError:
                            pass
                        nb.update_parent(new_node, cost_through_new)
                        edges.append((new_node, nb))

            # If new node is close enough to goal and path is collision-free, finish early
            dist_to_goal = math.dist(new_node.position, goal_pos)
            if dist_to_goal <= self.step_size:
                if not self._is_edge_in_obstacle(new_node.position, goal_pos, obstacles, is_3d):
                    if not self._is_in_obstacle(goal_pos, obstacles, is_3d):
                        goal_node = Node(goal_pos)
                        goal_node.update_parent(new_node, new_node.cost + dist_to_goal)
                        nodes.append(goal_node)
                        edges.append((new_node, goal_node))
                        success = True
                        break

        path = []
        if success and goal_node:
            node = goal_node
            while node is not None:
                path.append(node.position)
                node = node.parent
            path.reverse()

        return PlannerResult(
            success=success,
            path=path,
            nodes=nodes,
            edges=edges,
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True  # For future collision state if needed

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not new_parent:
            if self.parent is not None:
                self.parent.remove_child(self)
            self.parent = new_parent
            self.cost = new_cost
            if new_parent is not None and self not in new_parent.children:
                new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 6.0, goal_sample_rate: float = 0.1, rewire_batch_size: int = 50):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.rewire_batch_size = rewire_batch_size
        self.time_limit = 30.0
        self.improve_tol = 1e-10
        self.max_neighbor_radius = 25.0  # Max cap on neighbor radius

    def plan(self, map) -> PlannerResult:
        start_time = time.monotonic()
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)
        nodes: List[Node] = []
        edges: List[Tuple[Node, Node]] = []

        def in_bounds(p):
            return all(0.0 <= p[i] <= bounds[i] for i in range(dim))

        def is_in_obstacle(p):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = p
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w, h = obs
                    px, py = p
                    if x <= px <= x + w and y <= py <= y + h:
                        return True
            return False

        def is_edge_in_obstacle(a, b, resolution=None):
            dist_ab = math.dist(a, b)
            res = resolution
            if res is None:
                res = min(1.0, self.step_size * 0.4)
            steps = max(1, int(dist_ab / res))
            for i in range(steps + 1):
                interp = tuple(a[j] + (b[j] - a[j]) * (i / steps) for j in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def steer(from_p, to_p, max_step):
            d = math.dist(from_p, to_p)
            if d <= max_step:
                return to_p
            ratio = max_step / d
            return tuple(from_p[i] + ratio * (to_p[i] - from_p[i]) for i in range(dim))

        def sample_point():
            if random.random() < self.goal_sample_rate:
                return goal_pos
            while True:
                sample = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not is_in_obstacle(sample):
                    return sample

        def nearest_node(pt):
            best = nodes[0]
            best_dist = math.dist(best.position, pt)
            for node in nodes[1:]:
                d = math.dist(node.position, pt)
                if d < best_dist:
                    best = node
                    best_dist = d
            return best

        def compute_neighbor_radius(num_nodes):
            # Adaptive radius shrinking as n grows: r = min(maxr, gamma*(log(n)/n)^(1/dim))
            if num_nodes < 2:
                return self.max_neighbor_radius
            gamma = 50.0  # Tunable constant
            r = gamma * ((math.log(num_nodes) / num_nodes) ** (1.0 / dim))
            r = min(r, self.max_neighbor_radius)
            r = max(r, self.step_size * 1.5)  # Ensure not too small
            return r

        def near_nodes(pt, radius):
            return [node for node in nodes if math.dist(node.position, pt) <= radius]

        def choose_parent(new_pos, near_n):
            min_cost = float('inf')
            best_parent = None
            for nbr in near_n:
                cost_cand = nbr.cost + math.dist(nbr.position, new_pos)
                if cost_cand < min_cost and not is_edge_in_obstacle(nbr.position, new_pos):
                    min_cost = cost_cand
                    best_parent = nbr
            return best_parent, min_cost if best_parent is not None else (None, None)

        def add_node(new_pos):
            radius = compute_neighbor_radius(len(nodes) + 1)
            near_n = near_nodes(new_pos, radius)
            parent, cost = choose_parent(new_pos, near_n)
            if parent is None:
                # fallback to nearest if no collision free parents in radius
                nearest = nearest_node(new_pos)
                if not is_edge_in_obstacle(nearest.position, new_pos):
                    parent = nearest
                    cost = nearest.cost + math.dist(nearest.position, new_pos)
                else:
                    return None
            new_node = Node(new_pos, parent, cost)
            parent.add_child(new_node)
            nodes.append(new_node)
            edges.append((parent, new_node))
            return new_node, near_n

        def batch_rewire(new_node, near_n):
            for nbr in near_n:
                if nbr is new_node:
                    continue
                cost_through_new = new_node.cost + math.dist(new_node.position, nbr.position)
                if cost_through_new + self.improve_tol < nbr.cost:
                    if not is_edge_in_obstacle(new_node.position, nbr.position):
                        # Rewire nbr to new_node
                        if nbr.parent is not None:
                            try:
                                edges.remove((nbr.parent, nbr))
                            except ValueError:
                                pass
                            nbr.parent.remove_child(nbr)
                        nbr.update_parent(new_node, cost_through_new)
                        edges.append((new_node, nbr))

        def shortcut_path(path):
            if len(path) < 3:
                return path
            i = 0
            changed = False
            while i < len(path) - 2:
                j = len(path) - 1
                shortcut_found = False
                while j > i + 1:
                    if not is_edge_in_obstacle(path[i], path[j]):
                        path = path[:i+1] + path[j:]
                        changed = True
                        shortcut_found = True
                        j = len(path) - 1
                    else:
                        j -= 1
                i += 1 if not shortcut_found else 0
            return path, changed

        # Initialize with start node
        root = Node(start_pos, None, 0.0)
        nodes.append(root)
        edges = []
        best_goal_node = None
        best_cost = float('inf')

        # Minimum step size and max step size for dynamic adjustment
        min_step = self.step_size * 0.5
        max_step = self.step_size * 1.2

        # Helper to estimate local node density for adaptive step sizing
        def local_density(pos):
            radius = compute_neighbor_radius(len(nodes))
            neighbors = near_nodes(pos, radius)
            return len(neighbors) / (radius ** dim)

        def dynamic_step(nearest):
            # Smaller step in dense areas to enable finer expansions, larger step in sparse area
            density = local_density(nearest.position)
            # Inverse relation: higher density smaller steps but clipped
            step = max(min_step, min(max_step, max_step / (1 + density)))
            return step

        iteration_since_last_rewire = 0

        for i in range(self.max_iter):
            if time.monotonic() - start_time > self.time_limit:
                break

            sample = sample_point()
            nearest = nearest_node(sample)
            step = dynamic_step(nearest)
            new_pos = steer(nearest.position, sample, step)
            if not in_bounds(new_pos):
                continue
            if is_in_obstacle(new_pos):
                continue
            if is_edge_in_obstacle(nearest.position, new_pos):
                continue

            result = add_node(new_pos)
            if result is None:
                continue
            new_node, near_n = result

            if math.dist(new_node.position, goal_pos) <= step and not is_edge_in_obstacle(new_node.position, goal_pos):
                if not is_in_obstacle(goal_pos):
                    goal_node = Node(goal_pos, new_node, new_node.cost + math.dist(new_node.position, goal_pos))
                    new_node.add_child(goal_node)
                    nodes.append(goal_node)
                    edges.append((new_node, goal_node))
                    if goal_node.cost < best_cost:
                        best_goal_node = goal_node
                        best_cost = goal_node.cost

            iteration_since_last_rewire += 1
            if iteration_since_last_rewire >= self.rewire_batch_size:
                batch_rewire(new_node, near_n)
                iteration_since_last_rewire = 0

            # Early stop if goal reached
            if best_goal_node is not None:
                # Optional: break here for early solution; or continue to improve until time runs out
                break

        success = best_goal_node is not None
        extracted_path = []
        if success:
            # Extract path
            node = best_goal_node
            while node is not None:
                extracted_path.append(node.position)
                node = node.parent
            extracted_path.reverse()

            # Incremental shortcutting smoothing: adapt attempts and early terminate if no improvement or time near
            shortcut_iterations = 0
            max_shortcut_iterations = 30
            last_changed = True
            while shortcut_iterations < max_shortcut_iterations and last_changed and (time.monotonic() - start_time) < self.time_limit - 0.1:
                extracted_path, last_changed = shortcut_path(extracted_path)
                shortcut_iterations += 1

        return PlannerResult(
            success=success,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child_node):
        self.children.append(child_node)

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        if new_parent is not None:
            new_parent.add_child(self)
class Planner:
    def __init__(self, max_iter=5000, step_size=6.0, goal_sample_rate=0.1, min_radius=8.0, max_radius=35.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate  # Probability to sample goal
        self.min_radius = min_radius   # Minimum rewiring radius
        self.max_radius = max_radius   # Maximum rewiring radius

    def plan(self, map):
        import time
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        time_limit = 30.0
        start_time = time.monotonic()

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos, max_dist):
            d = dist(from_pos, to_pos)
            if d <= max_dist:
                return to_pos
            ratio = max_dist / d
            return tuple(from_pos[i] + (to_pos[i] - from_pos[i]) * ratio for i in range(dim))

        def is_point_in_ellipse(p, f1, f2, max_dist):
            # Check if p is inside ellipse defined by foci f1, f2 and major axis max_dist
            # sum of distances from p to foci <= max_dist
            return dist(p, f1) + dist(p, f2) <= max_dist + 1e-8

        def heuristic_radius(n_nodes):
            # Adaptive rewiring radius based on number of nodes and dimension,
            # from RRT* theory: r = min(max_radius, gamma * (log(n)/n)^(1/d))
            # gamma chosen to ensure connectivity, here gamma tuned empirically
            if n_nodes <= 1:
                return self.max_radius
            gamma_rrt = 35.0
            radius = gamma_rrt * (math.log(n_nodes) / n_nodes) ** (1.0 / dim)
            radius = max(self.min_radius, min(radius, self.max_radius))
            return radius

        def extract_path(end_node):
            path = []
            current = end_node
            while current is not None:
                path.append(current.position)
                current = current.parent
            return path[::-1]

        nodes = []
        edges = []
        root = Node(start_pos)
        nodes.append(root)

        best_goal_node = None
        best_goal_cost = float('inf')
        success = False

        max_dist_start_goal = dist(start_pos, goal_pos)
        # initialize informed sampling ellipse parameters:
        c_min = max_dist_start_goal
        # heuristic cost to beat is infinite initially; update upon feasible path

        def sample_informed():
            # If no solution found, sample uniformly in map
            if best_goal_node is None:
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))
            else:
                # Sample uniformly in an ellipsoid (informed sampling)
                # Ellipse defined with foci = start and goal, and major axis = cost of best solution
                c_best = best_goal_cost
                if c_best < float('inf'):
                    # Generate random point in unit ball
                    while True:
                        sample_ball = [random.gauss(0, 1) for _ in range(dim)]
                        norm = math.sqrt(sum(x * x for x in sample_ball))
                        if norm > 1e-8:
                            sample_ball = [x / norm for x in sample_ball]
                            break
                    radius = random.uniform(0, 1) ** (1.0 / dim)
                    sample_ball = [x * radius for x in sample_ball]

                    # Compute ellipse transform parameters
                    # Rotation or aligning ellipse along start-goal vector
                    # Construct transform matrix that scales unit ball to ellipse
                    # Length of major axis a = c_best / 2
                    # Minor axes radii b = sqrt(c_best^2 - c_min^2) / 2

                    a = c_best / 2.0
                    if c_best**2 - c_min**2 > 0:
                        b = math.sqrt(c_best**2 - c_min**2) / 2.0
                    else:
                        b = 0.0  # Degenerate ellipse: almost line

                    # Rotate sample_ball aligned with start->goal vector
                    # Create unit vector for start->goal
                    unit_vec = [(goal_pos[i] - start_pos[i]) / c_min for i in range(dim)]
                    # Orthogonal basis: for dim>1, just 1D alignment, rest arbitrary axes (identity)
                    # For simplicity, align first dimension to start->goal, scale others by b/a

                    if dim == 1:
                        # 1D case, scale by a along line (unit_vec=1 or -1)
                        new_sample = [start_pos[0] + sample_ball[0] * a * unit_vec[0]]
                    else:
                        # Build sample point in aligned coords
                        new_sample = [0.0] * dim
                        new_sample[0] = sample_ball[0] * a
                        for i in range(1, dim):
                            new_sample[i] = sample_ball[i] * b
                        # Rotate back to world frame: since only aligned with unit_vec on axis0,
                        # approximate by placing first dim component along unit_vec, others arbitrary orthogonal
                        # Find perpendicular basis by Gram-Schmidt (approx)
                        # We only do a simple linear transform ignoring rotation for complexity,
                        # Instead, we map sample: sample_world = start + unit_vec * new_sample[0] + perpendicular components
                        base_point = list(start_pos)
                        # Decompose new_sample into unit_vec axis and orthogonal components
                        # For simplicity, distribute orthogonal components equally among remaining dims

                        sample_world = [base_point[i] + unit_vec[i] * new_sample[0] for i in range(dim)]
                        # Orthogonal part: we add equally distributed parts scaled by new_sample[i]
                        # Construct orthogonal vectors to unit_vec by simple Gram-Schmidt approach
                        # For dims>1, find orthogonal vectors:
                        ortho_vectors = []
                        def normalize(vec):
                            nrm = math.sqrt(sum(x*x for x in vec))
                            if nrm < 1e-10:
                                return [0.0]*len(vec)
                            return [x/nrm for x in vec]
                        # Start with identity basis
                        identity = [[0]*dim for _ in range(dim)]
                        for i in range(dim):
                            identity[i][i] = 1
                        for i in range(dim):
                            proj = sum(identity[i][j]*unit_vec[j] for j in range(dim))
                            ortho = [identity[i][j]-proj*unit_vec[j] for j in range(dim)]
                            ortho = normalize(ortho)
                            ortho_vectors.append(ortho)
                        # Add scaled orthogonal components
                        for i in range(1, dim):
                            for j in range(dim):
                                sample_world[j] += ortho_vectors[i][j]*new_sample[i]
                        return tuple(max(0.0, min(sample_world[i], bounds[i])) for i in range(dim))
                    return tuple(new_sample)
                else:
                    # fallback: uniform sampling
                    return tuple(random.uniform(0, bounds[d]) for d in range(dim))

        def node_density():
            # Approximate node density as number of nodes / total volume
            # Volume = product of bounds dimensions
            volume = 1.0
            for v in bounds:
                volume *= v
            return len(nodes) / max(1e-10, volume)

        def prune_nodes():
            # Periodically prune nodes that have cost worse than best_goal_cost + margin or disconnected
            # Also prune isolated nodes without children nor parent (except root)
            margin = 15.0
            to_remove = []
            for node in nodes:
                if node is root:
                    continue
                if best_goal_cost < float('inf'):
                    if node.cost > best_goal_cost + margin:
                        to_remove.append(node)
                        continue
                if (node.parent is None) and (len(node.children) == 0):
                    # isolated
                    to_remove.append(node)
            for node in to_remove:
                # remove from nodes list and edges
                nodes.remove(node)
                if node.parent is not None:
                    try:
                        node.parent.children.remove(node)
                    except Exception:
                        pass
                # Remove edges referencing this node
                edges[:] = [(p, c) for p, c in edges if p != node and c != node]

        iter_chunk = 100  # prune and update radius every chunk

        for iter_idx in range(self.max_iter):
            if time.monotonic() - start_time > time_limit:
                break

            # Sample point with goal bias and informed sampling
            if random.random() < self.goal_sample_rate:
                sample = goal_pos
            else:
                sample = sample_informed()

            # Nearest node to sample
            if not nodes:
                break
            nearest_node = min(nodes, key=lambda n: dist(n.position, sample))
            new_pos = steer(nearest_node.position, sample, self.step_size)

            if any(new_pos[i] < 0.0 or new_pos[i] > bounds[i] for i in range(dim)):
                continue

            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                continue
            if self._is_edge_in_obstacle(nearest_node.position, new_pos, obstacles, is_3d):
                continue

            # Adaptive neighbor radius for rewiring
            r = heuristic_radius(len(nodes))

            # Find neighbors within radius r
            near_nodes = [n for n in nodes if dist(n.position, new_pos) <= r]

            # Choose best parent minimizing cost + dist and collision free
            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in near_nodes:
                tentative_cost = neighbor.cost + dist(neighbor.position, new_pos)
                if tentative_cost < min_cost:
                    if not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                        min_cost = tentative_cost
                        best_parent = neighbor

            new_node = Node(new_pos)
            new_node.update_parent(best_parent, min_cost)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors through new node if improves cost and collision free
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                alt_cost = new_node.cost + dist(new_node.position, neighbor.position)
                if alt_cost + 1e-7 < neighbor.cost:
                    if not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                        try:
                            edges.remove((neighbor.parent, neighbor))
                        except Exception:
                            pass
                        neighbor.update_parent(new_node, alt_cost)
                        edges.append((new_node, neighbor))

            # Check if new node can connect to goal directly
            dist_to_goal = dist(new_node.position, goal_pos)
            if dist_to_goal <= self.step_size:
                if (not self._is_edge_in_obstacle(new_node.position, goal_pos, obstacles, is_3d) and
                    not self._is_in_obstacle(goal_pos, obstacles, is_3d)):
                    goal_node = Node(goal_pos)
                    goal_cost = new_node.cost + dist_to_goal
                    if goal_cost < best_goal_cost:
                        goal_node.update_parent(new_node, goal_cost)
                        nodes.append(goal_node)
                        edges.append((new_node, goal_node))
                        best_goal_node = goal_node
                        best_goal_cost = goal_cost
                        success = True

                        # Early break on finding better path
                        break

            # Periodic pruning and radius update
            if (iter_idx > 0) and (iter_idx % iter_chunk == 0) and (success):
                prune_nodes()

        # Extract path if success
        if best_goal_node is not None and success:
            extracted_path = extract_path(best_goal_node)
        else:
            extracted_path = []

        return PlannerResult(
            success=success,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float = 0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children: List["Node"] = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)

    def remove_child(self, child):
        try:
            self.children.remove(child)
        except ValueError:
            pass

    def update_parent(self, new_parent, new_cost):
        if self.parent is not new_parent:
            if self.parent is not None:
                self.parent.remove_child(self)
            self.parent = new_parent
            self.cost = new_cost
            if new_parent is not None:
                new_parent.add_child(self)
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0, goal_sample_rate: float = 0.1, time_limit: float = 30.0,
                 max_neighbor_radius: float = 25.0, min_neighbor_radius: float = 2.0, shortcut_attempts: int = 80):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.time_limit = time_limit
        self.max_neighbor_radius = max_neighbor_radius
        self.min_neighbor_radius = min_neighbor_radius
        self.shortcut_attempts = shortcut_attempts

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        start_time = time.monotonic()

        # Two trees: forward from start, backward from goal
        tree_start: List[Node] = [Node(start_pos)]
        tree_goal: List[Node] = [Node(goal_pos)]
        all_nodes: List[Node] = tree_start + tree_goal
        edges: List[Tuple[Node, Node]] = []

        best_path: List[Tuple[float, ...]] = []
        best_cost = float('inf')
        success = False
        best_goal_node_start = None  # node in start tree connected to goal tree

        def dist(a, b):
            return math.dist(a, b)

        def in_bounds(p):
            for i in range(dim):
                if p[i] < 0 or p[i] > bounds[i]:
                    return False
            return True

        def is_in_obstacle(p):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = p
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w, h = obs
                    px, py = p
                    if x <= px <= x + w and y <= py <= y + h:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2, resolution=0.5):
            distance = dist(p1, p2)
            steps = max(1, int(distance / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[d] + (p2[d] - p1[d]) * (i / steps) for d in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def steer(from_p, to_p, max_step):
            d = dist(from_p, to_p)
            if d <= max_step:
                return to_p
            ratio = max_step / d
            return tuple(from_p[i] + ratio * (to_p[i] - from_p[i]) for i in range(dim))

        def build_rotation_matrix_2d(a):
            # a is unit vector in 2D
            cos_theta = a[0]
            sin_theta = a[1]
            return [[cos_theta, -sin_theta],
                    [sin_theta,  cos_theta]]

        def build_rotation_matrix_3d(a):
            x_axis = a
            # choose arbitrary vector not colinear
            if abs(x_axis[0]) < 0.9:
                temp = (1, 0, 0)
            else:
                temp = (0, 1, 0)
            # v = temp - proj(temp on x_axis)
            dot = sum(x_axis[i] * temp[i] for i in range(3))
            v = tuple(temp[i] - dot * x_axis[i] for i in range(3))
            norm_v = math.sqrt(sum(v[i] * v[i] for i in range(3)))
            if norm_v < 1e-10:
                # x_axis aligned, return identity
                return [[1, 0, 0], [0, 1, 0], [0, 0, 1]]
            v_unit = tuple(v[i] / norm_v for i in range(3))
            w = (x_axis[1] * v_unit[2] - x_axis[2] * v_unit[1],
                 x_axis[2] * v_unit[0] - x_axis[0] * v_unit[2],
                 x_axis[0] * v_unit[1] - x_axis[1] * v_unit[0])
            # Rotation matrix columns: x_axis, v_unit, w
            return [[x_axis[0], v_unit[0], w[0]],
                    [x_axis[1], v_unit[1], w[1]],
                    [x_axis[2], v_unit[2], w[2]]]

        def sample_informed(c_best, x_start, x_goal):
            if math.isinf(c_best):
                # no solution yet, uniform sample in bounds
                return tuple(random.uniform(0, bounds[i]) for i in range(dim))

            c_min = dist(x_start, x_goal)
            if c_min == 0:
                return x_start

            center = tuple((x_start[i] + x_goal[i]) / 2.0 for i in range(dim))
            a1 = tuple((x_goal[i] - x_start[i]) / c_min for i in range(dim))

            if dim == 2:
                R = build_rotation_matrix_2d(a1)
            elif dim == 3:
                R = build_rotation_matrix_3d(a1)
            else:
                # fallback to uniform
                return tuple(random.uniform(0, bounds[i]) for i in range(dim))

            r1 = c_best / 2.0
            diff = c_best * c_best - c_min * c_min
            r2 = math.sqrt(abs(diff)) / 2.0 if diff > 1e-6 else max(self.min_neighbor_radius * 0.1, 0.001)
            radii = [r1] + [r2] * (dim - 1)

            while True:
                # sample inside unit ball
                rnd = [random.gauss(0, 1) for _ in range(dim)]
                norm_rnd = math.sqrt(sum(x * x for x in rnd))
                if norm_rnd > 1e-10:
                    break
            unit_ball = [x / norm_rnd for x in rnd]
            u = random.random()
            scale = u ** (1.0 / dim)
            sample_ball = [scale * unit_ball[i] * radii[i] for i in range(dim)]

            sample = [0.0] * dim
            for i in range(dim):
                s = 0.0
                for j in range(dim):
                    s += R[i][j] * sample_ball[j]
                sample[i] = s + center[i]

            clipped = tuple(max(0.0, min(sample[i], bounds[i])) for i in range(dim))
            return clipped

        def nearest_node(tree, point):
            best_node = tree[0]
            best_dist = dist(best_node.position, point)
            for node in tree[1:]:
                dcur = dist(node.position, point)
                if dcur < best_dist:
                    best_dist = dcur
                    best_node = node
            return best_node

        def nearby_nodes(tree, point, radius):
            r2 = radius * radius
            result = []
            for node in tree:
                dx = node.position[0] - point[0]
                dy = node.position[1] - point[1]
                if dim == 3:
                    dz = node.position[2] - point[2]
                    d2 = dx * dx + dy * dy + dz * dz
                else:
                    d2 = dx * dx + dy * dy
                if d2 <= r2:
                    result.append(node)
            return result

        def neighbor_radius(num_nodes):
            if num_nodes <= 1:
                return self.max_neighbor_radius
            gamma = self.max_neighbor_radius
            r = gamma * ((math.log(num_nodes) / num_nodes) ** (1.0 / dim))
            return max(self.min_neighbor_radius, min(r, self.max_neighbor_radius))

        def rewire(tree, new_node, radius):
            neighbors = nearby_nodes(tree, new_node.position, radius)

            # Choose best parent from neighbors
            min_parent = new_node.parent
            min_cost = new_node.cost
            for node in neighbors:
                if node is new_node.parent:
                    continue
                if not is_edge_in_obstacle(node.position, new_node.position):
                    cost_cand = node.cost + dist(node.position, new_node.position)
                    if cost_cand < min_cost:
                        min_parent = node
                        min_cost = cost_cand

            if min_parent is not new_node.parent:
                if new_node.parent:
                    new_node.parent.remove_child(new_node)
                new_node.parent = min_parent
                new_node.cost = min_cost
                min_parent.add_child(new_node)

            # Rewire neighbors through new_node if cheaper
            for node in neighbors:
                if node is new_node:
                    continue
                if not is_edge_in_obstacle(new_node.position, node.position):
                    cost_through_new = new_node.cost + dist(new_node.position, node.position)
                    if cost_through_new < node.cost:
                        if node.parent:
                            node.parent.remove_child(node)
                        node.parent = new_node
                        node.cost = cost_through_new
                        new_node.add_child(node)
                        # propagate cost downstream
                        self._propagate_cost(node)

        def propagate_cost_downstream(node):
            # Recursively update children costs if parent's cost changed
            for c in node.children:
                new_cost = node.cost + dist(node.position, c.position)
                if new_cost < c.cost:
                    c.cost = new_cost
                    c.parent = node
                    propagate_cost_downstream(c)

        self._propagate_cost = propagate_cost_downstream  # assign so can call easily

        def attempt_connect(node_a, node_b):
            d = dist(node_a.position, node_b.position)
            if d <= self.step_size:
                if not is_edge_in_obstacle(node_a.position, node_b.position):
                    return True
            return False

        def try_connect_trees(new_node, other_tree):
            nearest_other = nearest_node(other_tree, new_node.position)
            if attempt_connect(new_node, nearest_other):
                return nearest_other
            return None

        def extract_path(node_start, node_goal):
            path_start = []
            cur = node_start
            while cur is not None:
                path_start.append(cur.position)
                cur = cur.parent
            path_start.reverse()
            path_goal = []
            cur = node_goal
            while cur is not None:
                path_goal.append(cur.position)
                cur = cur.parent
            if path_start[-1] == path_goal[0]:
                path_goal = path_goal[1:]
            return path_start + path_goal

        def shortcut_path(path: List[Tuple[float, ...]]):
            if len(path) < 3:
                return path
            new_path = list(path)
            for _ in range(self.shortcut_attempts):
                if len(new_path) < 3:
                    break
                i = random.randint(0, len(new_path) - 3)
                j = random.randint(i + 2, len(new_path) - 1)
                if not is_edge_in_obstacle(new_path[i], new_path[j]):
                    new_path = new_path[:i + 1] + new_path[j:]
            return new_path

        # Main loop: alternate tree growth
        trees = [(tree_start, tree_goal), (tree_goal, tree_start)]
        for iteration in range(self.max_iter):
            elapsed = time.monotonic() - start_time
            if elapsed > self.time_limit:
                break

            for tree_a, tree_b in trees:
                # Determine best cost for informed sampling for this tree
                c_best = best_cost if success else float("inf")

                # Sample either goal or informed sampling inside ellipsoid around best path
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if tree_a is tree_start else start_pos
                else:
                    if success:
                        sample = sample_informed(c_best, start_pos, goal_pos)
                    else:
                        sample = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                        # Avoid collision
                        tries = 0
                        while is_in_obstacle(sample) and tries < 50:
                            sample = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                            tries += 1
                        if is_in_obstacle(sample):
                            # fallback to root
                            sample = tree_a[0].position

                nearest_in_a = nearest_node(tree_a, sample)
                new_pos = steer(nearest_in_a.position, sample, self.step_size)
                if not in_bounds(new_pos):
                    continue
                if is_in_obstacle(new_pos):
                    continue
                if is_edge_in_obstacle(nearest_in_a.position, new_pos):
                    continue

                radius = neighbor_radius(len(tree_a))
                neighbors = nearby_nodes(tree_a, new_pos, radius)

                # Choose best parent among neighbors
                min_cost = nearest_in_a.cost + dist(nearest_in_a.position, new_pos)
                best_parent = nearest_in_a
                for nb in neighbors:
                    c_cand = nb.cost + dist(nb.position, new_pos)
                    if c_cand < min_cost and not is_edge_in_obstacle(nb.position, new_pos):
                        min_cost = c_cand
                        best_parent = nb

                new_node = Node(new_pos)
                new_node.update_parent(best_parent, min_cost)
                tree_a.append(new_node)
                all_nodes.append(new_node)
                edges.append((best_parent, new_node))

                # Rewire neighbors through new_node if it lowers cost
                rewire(tree_a, new_node, radius)

                # Attempt to connect trees
                connect_node = try_connect_trees(new_node, tree_b)
                if connect_node:
                    total_cost = new_node.cost + connect_node.cost + dist(new_node.position, connect_node.position)
                    if total_cost < best_cost:
                        best_cost = total_cost
                        best_path = extract_path(new_node, connect_node)
                        success = True

                # Early exit if path found with timeout not reached
                if success and elapsed < self.time_limit - 0.01:
                    final_path = shortcut_path(best_path)
                    return PlannerResult(success=True, path=final_path, nodes=all_nodes, edges=edges)

        # If timeout or no exact connection, try fallback best path
        if not success:
            # Find node closest to goal in tree_start
            min_dist = float("inf")
            best_node = None
            for node in tree_start:
                dcur = dist(node.position, goal_pos)
                if dcur < min_dist:
                    min_dist = dcur
                    best_node = node
            if best_node is not None:
                path = []
                cur = best_node
                while cur is not None:
                    path.append(cur.position)
                    cur = cur.parent
                path.reverse()
                best_path = path

        final_path = shortcut_path(best_path)

        return PlannerResult(success=success, path=final_path, nodes=all_nodes, edges=edges)

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []

    def add_child(self, child):
        self.children.append(child)

    def remove_child(self, child):
        try:
            self.children.remove(child)
        except ValueError:
            pass

    def update_parent(self, new_parent, new_cost):
        if self.parent is new_parent:
            self.cost = new_cost
            return
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        if new_parent:
            new_parent.add_child(self)
        # After parent update, propagate cost to children
        self._propagate_cost()

    def _propagate_cost(self):
        for c in self.children:
            new_cost = self.cost + math.dist(self.position, c.position)
            if new_cost < c.cost:
                c.cost = new_cost
                c._propagate_cost()
class Planner:
    def __init__(self, max_iter=5000, step_size=5.0, goal_sample_rate=0.1):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map):
        import random
        import time
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = dim == 3

        def in_bounds(p):
            return all(0 <= p[i] <= bounds[i] for i in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p, max_d):
            d = dist(from_p, to_p)
            if d <= max_d:
                return to_p
            ratio = max_d / d
            return tuple(from_p[i] + ratio * (to_p[i] - from_p[i]) for i in range(dim))

        def is_free_node(p):
            if not in_bounds(p):
                return False
            return not self._is_in_obstacle(p, obstacles, is_3d)

        def is_free_edge(p1, p2):
            distance = dist(p1, p2)
            resolution = min(1.0, self.step_size * 0.5)
            steps = max(1, int(distance / resolution))
            for i in range(steps + 1):
                q = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if self._is_in_obstacle(q, obstacles, is_3d):
                    return False
            return True

        nodes = []
        edges = []
        start_time = time.monotonic()
        time_limit = 30.0

        root = Node(start_pos)
        nodes.append(root)

        success = False
        goal_node = None

        # Adaptive radius scaling factor
        def neighbor_radius():
            n = len(nodes)
            if n < 2:
                return self.step_size * 5
            gamma = 30.0  # tuning parameter
            return min(self.step_size * 5, gamma * (math.log(n) / n) ** (1.0 / dim))

        def nearest(p):
            best_node = nodes[0]
            best_d = dist(p, best_node.position)
            for n in nodes[1:]:
                dcur = dist(p, n.position)
                if dcur < best_d:
                    best_d = dcur
                    best_node = n
            return best_node

        def nearby(p, radius):
            return [n for n in nodes if dist(p, n.position) <= radius]

        for _ in range(self.max_iter):
            if time.monotonic() - start_time > time_limit:
                break

            sample = goal_pos if random.random() < self.goal_sample_rate else tuple(random.uniform(0, bounds[i]) for i in range(dim))

            nearest_node = nearest(sample)
            new_pos = steer(nearest_node.position, sample, self.step_size)

            if not is_free_node(new_pos):
                continue
            if not is_free_edge(nearest_node.position, new_pos):
                continue

            radius = neighbor_radius()
            neighbor_nodes = nearby(new_pos, radius)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for n in neighbor_nodes:
                cost_through_n = n.cost + dist(n.position, new_pos)
                if cost_through_n < min_cost and is_free_edge(n.position, new_pos):
                    min_cost = cost_through_n
                    best_parent = n

            new_node = Node(new_pos)
            new_node.update_parent(best_parent, min_cost)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            for n in neighbor_nodes:
                if n is best_parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, n.position)
                if cost_through_new < n.cost and is_free_edge(new_node.position, n.position):
                    if n.parent:
                        try:
                            edges.remove((n.parent, n))
                        except ValueError:
                            pass
                    n.update_parent(new_node, cost_through_new)
                    edges.append((new_node, n))

            if dist(new_pos, goal_pos) <= self.step_size and is_free_node(goal_pos) and is_free_edge(new_pos, goal_pos):
                goal_node = Node(goal_pos)
                goal_node.update_parent(new_node, new_node.cost + dist(new_pos, goal_pos))
                nodes.append(goal_node)
                edges.append((new_node, goal_node))
                success = True
                break

        if success and goal_node:
            current = goal_node
        else:
            if nodes:
                current = min(nodes, key=lambda n: dist(n.position, goal_pos))
            else:
                current = None

        path = []
        while current:
            path.append(current.position)
            current = current.parent
        path.reverse()

        return PlannerResult(success=success, path=path, nodes=nodes, edges=edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter=5000, step_size=5.0, goal_sample_rate=0.05, time_limit_sec=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.time_limit_sec = time_limit_sec
        self.rewire_radius = 15.0

    def plan(self, map):
        import time
        import math
        import random

        start_time = time.monotonic()
        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        def dist(a, b):
            return math.dist(a, b)

        def in_bounds(p):
            return all(0.0 <= p[i] <= bounds[i] for i in range(dim))

        def is_in_obstacle(p):
            for obs in obstacles:
                if is_3d:
                    x,y,z,w,h,d = obs
                    px,py,pz = p
                    if x <= px <= x+w and y <= py <= y+h and z <= pz <= z+d:
                        return True
                else:
                    x,y,w_,h_ = obs
                    px,py = p
                    if x <= px <= x+w_ and y <= py <= y+h_:
                        return True
            return False

        def is_edge_in_obstacle(a,b):
            dist_ab = dist(a,b)
            resolution = min(1.0, self.step_size*0.5)
            steps = max(1, int(dist_ab / resolution))
            for i in range(steps + 1):
                interp = tuple(a[j] + (b[j]-a[j])*(i/steps) for j in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def steer(fr, to):
            d = dist(fr, to)
            if d <= self.step_size:
                return to
            ratio = self.step_size / d
            return tuple(fr[i] + ratio * (to[i] - fr[i]) for i in range(dim))

        def uniform_sample():
            trials = 0
            while trials < 1000:
                s = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not is_in_obstacle(s):
                    return s
                trials += 1
            return start_pos  # fallback if stuck

        def nearest_node(tree_nodes, point):
            best = tree_nodes[0]
            best_d = dist(best.position, point)
            for node in tree_nodes[1:]:
                d_ = dist(node.position, point)
                if d_ < best_d:
                    best = node
                    best_d = d_
            return best

        def near_nodes(tree_nodes, point):
            r = self.rewire_radius
            r_sq = r*r
            result = []
            for n in tree_nodes:
                d_sq = sum((n.position[i]-point[i])**2 for i in range(dim))
                if d_sq <= r_sq:
                    result.append(n)
            return result

        def add_node(tree, pos, nodes_all, edges_list):
            neighbors = near_nodes(tree, pos)
            # filter parents with collision-free edges
            parents = [n for n in neighbors if not is_edge_in_obstacle(n.position, pos)]
            if not parents:
                # fallback to nearest connected node
                nearest = None
                nearest_dist = float('inf')
                for n in tree:
                    d_ = dist(n.position, pos)
                    if d_ < nearest_dist and not is_edge_in_obstacle(n.position, pos):
                        nearest = n
                        nearest_dist = d_
                if nearest is None:
                    return None, []
                parents = [nearest]

            best_parent = min(parents, key=lambda n: n.cost + dist(n.position, pos))
            new_cost = best_parent.cost + dist(best_parent.position, pos)
            new_node = Node(pos, parent=None, cost=new_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            nodes_all.append(new_node)
            edges_list.append((best_parent, new_node))
            return new_node, neighbors

        def rewire(new_node, neighbors, edges_list):
            for nb in neighbors:
                if nb is new_node:
                    continue
                if is_edge_in_obstacle(new_node.position, nb.position):
                    continue
                new_cost = new_node.cost + dist(new_node.position, nb.position)
                if new_cost < nb.cost:
                    if nb.parent:
                        nb.parent.remove_child(nb)
                        try:
                            edges_list.remove((nb.parent, nb))
                        except ValueError:
                            pass
                    new_node.add_child(nb)
                    nb.cost = new_cost

        def can_connect(n1, n2):
            return n1.valid and n2.valid and not is_edge_in_obstacle(n1.position, n2.position)

        def stitch_path(node_start, node_goal, swap):
            path_a = node_start.path_from_root()
            path_b = node_goal.path_from_root()
            if path_a and path_b and path_a[-1] == path_b[-1]:
                full_path = path_a + path_b[-2::-1]
            else:
                full_path = path_a + path_b[::-1]
            return full_path if swap else full_path[::-1]

        def shortcut_path(path):
            if len(path) < 3:
                return path
            smoothed = [path[0]]
            i = 0
            while i < len(path) - 1:
                j = len(path) - 1
                while j > i + 1:
                    if not is_edge_in_obstacle(path[i], path[j]):
                        break
                    j -= 1
                smoothed.append(path[j])
                i = j
            return smoothed

        tree_start = [Node(start_pos, cost=0.0)]
        tree_goal = [Node(goal_pos, cost=0.0)]
        nodes = [tree_start[0], tree_goal[0]]
        edges = []

        best_path = []
        best_cost = float('inf')
        found = False

        for i in range(self.max_iter):
            if time.monotonic() - start_time > self.time_limit_sec:
                break

            side = (i % 2 == 0)
            tree_a = tree_start if side else tree_goal
            tree_b = tree_goal if side else tree_start

            # Sample with goal bias
            if random.random() < self.goal_sample_rate:
                sample = goal_pos if side else start_pos
            else:
                sample = uniform_sample()

            nearest = nearest_node(tree_a, sample)
            new_pos = steer(nearest.position, sample)

            if (not in_bounds(new_pos) or is_in_obstacle(new_pos) or 
                is_edge_in_obstacle(nearest.position, new_pos)):
                continue

            new_node, neighbors = add_node(tree_a, new_pos, nodes, edges)
            if new_node is None:
                continue

            rewire(new_node, neighbors, edges)

            # Try direct connection to other tree
            nearest_other = nearest_node(tree_b, new_node.position)
            dist_other = dist(nearest_other.position, new_node.position)
            connected_node = None
            if dist_other <= self.step_size and can_connect(nearest_other, new_node):
                conn_cost = nearest_other.cost + dist(nearest_other.position, new_node.position)
                final_node = Node(new_node.position, parent=None, cost=conn_cost)
                nearest_other.add_child(final_node)
                tree_b.append(final_node)
                nodes.append(final_node)
                edges.append((nearest_other, final_node))
                connected_node = final_node

            if connected_node is not None:
                path_candidate = stitch_path(new_node, connected_node, side)
                cost_candidate = 0.0
                for k in range(len(path_candidate) - 1):
                    cost_candidate += dist(path_candidate[k], path_candidate[k+1])
                if cost_candidate < best_cost:
                    best_cost = cost_candidate
                    best_path = path_candidate
                    found = True
                # Early stop on first found path for simplicity
                break

        if found and best_path:
            best_path = shortcut_path(best_path)
            return PlannerResult(success=True, path=best_path, nodes=nodes, edges=edges)
        else:
            return PlannerResult(success=False, path=[], nodes=nodes, edges=edges)

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter=12000, step_size=4.0, goal_sample_rate=0.05,
                 max_no_improve=120, time_limit_sec=30.0, gamma_rrt_star=35.0, cell_size=10.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.max_no_improve = max_no_improve
        self.time_limit_sec = time_limit_sec
        self.gamma_rrt_star = gamma_rrt_star
        self.cell_size = cell_size  # for spatial hashing

    def plan(self, map):
        import math
        import random
        import time

        start_time = time.monotonic()

        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        def dist(a, b):
            return math.dist(a, b)

        def steer(fr, to):
            d = dist(fr, to)
            if d <= self.step_size:
                return to
            r = self.step_size / d
            return tuple(fr[i] + (to[i] - fr[i]) * r for i in range(dim))

        def within_bounds(p):
            for i in range(dim):
                if p[i] < 0.0 or p[i] > bounds[i]:
                    return False
            return True

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d_ = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d_:
                        return True
                else:
                    x, y, w_, h_ = obs
                    px, py = pos
                    if x <= px <= x + w_ and y <= py <= y + h_:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2):
            length = dist(p1, p2)
            resolution = min(1.0, self.step_size * 0.4)
            steps = max(1, int(length / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def uniform_sample():
            trials = 0
            while trials < 500:
                s = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not is_in_obstacle(s):
                    return s
                trials += 1
            return start_pos

        def informed_sample(start, goal, c_best, c_min):
            if c_best == float('inf') or c_min < 1e-12:
                return uniform_sample()
            center = tuple((start[i] + goal[i]) * 0.5 for i in range(dim))
            a1 = tuple((goal[i] - start[i]) / c_min for i in range(dim))
            if dim == 2:
                a2 = (-a1[1], a1[0])
                basis = [a1, a2]
                r1 = c_best / 2.0
                r2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [r1, r2]
            else:
                ref = (1.0, 0.0, 0.0) if abs(a1[0]) < 0.9 else (0.0, 1.0, 0.0)
                b2 = self._normalize(self._cross(a1, ref))
                if self._norm(b2) < 1e-12:
                    ref = (0.0, 0.0, 1.0)
                    b2 = self._normalize(self._cross(a1, ref))
                b3 = self._cross(a1, b2)
                basis = [a1, b2, b3]
                r1 = c_best / 2.0
                r_other = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [r1, r_other, r_other]

            def sample_unit_ball(d):
                while True:
                    v = [random.gauss(0, 1) for _ in range(d)]
                    norm_ = math.sqrt(sum(x * x for x in v))
                    if norm_ > 1e-12:
                        v = [x / norm_ for x in v]
                        r = random.random() ** (1.0 / d)
                        return [x * r for x in v]

            u = sample_unit_ball(dim)
            p = [center[i] for i in range(dim)]
            for i in range(dim):
                for j in range(dim):
                    p[i] += basis[j][i] * radii[j] * u[j]
            clipped = tuple(min(max(p[i], 0.0), bounds[i]) for i in range(dim))
            if is_in_obstacle(clipped):
                return uniform_sample()
            return clipped

        def neighbor_radius(n_nodes):
            if n_nodes < 2:
                return max(self.step_size * 2.0, 3.0)
            val = self.gamma_rrt_star * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            max_r = max(bounds) * 0.15
            min_r = max(3.0, self.step_size)
            return max(min_r, min(max_r, val))

        # spatial hashing for neighbors: buckets keyed by tuple indices
        def to_cell(pos):
            return tuple(int(p // self.cell_size) for p in pos)

        spatial_hash_start = dict()
        spatial_hash_goal = dict()

        def add_to_spatial_hash(spatial_hash, node):
            c = to_cell(node.position)
            if c not in spatial_hash:
                spatial_hash[c] = []
            spatial_hash[c].append(node)

        def get_near_nodes(spatial_hash, pos, radius):
            r_c = int(math.ceil(radius / self.cell_size))
            c = to_cell(pos)
            neighbors = []
            for dx in range(-r_c, r_c + 1):
                for dy in range(-r_c, r_c + 1):
                    if dim == 3:
                        for dz in range(-r_c, r_c + 1):
                            bucket = (c[0] + dx, c[1] + dy, c[2] + dz)
                            if bucket in spatial_hash:
                                neighbors.extend(spatial_hash[bucket])
                    else:
                        bucket = (c[0] + dx, c[1] + dy)
                        if bucket in spatial_hash:
                            neighbors.extend(spatial_hash[bucket])
            r_sq = radius * radius
            filtered = [n for n in neighbors if sum((n.position[i] - pos[i])**2 for i in range(dim)) <= r_sq]
            return filtered

        def nearest_node(spatial_hash, pos):
            # Search only close cells around pos
            c = to_cell(pos)
            search_r = 1
            best = None
            best_d = float('inf')
            while search_r < 10 and best is None:
                candidates = []
                for dx in range(-search_r, search_r + 1):
                    for dy in range(-search_r, search_r + 1):
                        if dim == 3:
                            for dz in range(-search_r, search_r + 1):
                                bucket = (c[0] + dx, c[1] + dy, c[2] + dz)
                                candidates.extend(spatial_hash.get(bucket, []))
                        else:
                            bucket = (c[0] + dx, c[1] + dy)
                            candidates.extend(spatial_hash.get(bucket, []))
                for n in candidates:
                    d_ = dist(n.position, pos)
                    if d_ < best_d:
                        best_d = d_
                        best = n
                if best is not None:
                    break
                search_r += 1
            return best

        def add_node_rrt_star(tree, spatial_hash, pos, nodes_all, edges_list):
            r = neighbor_radius(len(tree))
            neighbors = get_near_nodes(spatial_hash, pos, r)
            candidates = [n for n in neighbors if not is_edge_in_obstacle(n.position, pos)]
            if not candidates:
                fallback = nearest_node(spatial_hash, pos)
                if fallback is None or is_edge_in_obstacle(fallback.position, pos):
                    return None, []
                candidates = [fallback]
            best_parent = min(candidates, key=lambda n: n.cost + dist(n.position, pos))
            new_cost = best_parent.cost + dist(best_parent.position, pos)
            new_node = Node(pos, parent=None, cost=new_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            nodes_all.append(new_node)
            edges_list.append((best_parent, new_node))
            add_to_spatial_hash(spatial_hash, new_node)
            return new_node, neighbors

        def propagate_cost(node):
            for child in node.children:
                old_cost = child.cost
                child.cost = node.cost + dist(node.position, child.position)
                if child.cost + 1e-12 < old_cost:
                    propagate_cost(child)

        def rewire(tree, spatial_hash, new_node, neighbors, edges_list):
            for nb in neighbors:
                if nb is new_node:
                    continue
                if is_edge_in_obstacle(new_node.position, nb.position):
                    continue
                new_cost = new_node.cost + dist(new_node.position, nb.position)
                if new_cost + 1e-12 < nb.cost:
                    if nb.parent:
                        nb.parent.remove_child(nb)
                        try:
                            edges_list.remove((nb.parent, nb))
                        except ValueError:
                            pass
                    new_node.add_child(nb)
                    nb.cost = new_cost
                    edges_list.append((new_node, nb))
                    propagate_cost(nb)

        def can_connect(n1, n2):
            return n1.valid and n2.valid and not is_edge_in_obstacle(n1.position, n2.position)

        def stitch_path(n_start, n_goal, start_tree_first):
            path_start = n_start.path_from_root()
            path_goal = n_goal.path_from_root()
            if path_start and path_goal and path_start[-1] == path_goal[-1]:
                combined = path_start + path_goal[-2::-1]
            else:
                combined = path_start + path_goal[::-1]
            return combined if start_tree_first else combined[::-1]

        def shortcut_path(path):
            if len(path) < 3:
                return path
            smoothed = [path[0]]
            i = 0
            while i < len(path) - 1:
                j = len(path) - 1
                while j > i + 1:
                    if not is_edge_in_obstacle(path[i], path[j]):
                        break
                    j -= 1
                smoothed.append(path[j])
                i = j
            return smoothed

        def iterative_shortcut_path(path, iterations=3):
            # Iteratively shortcut multiple times for better smoothing
            pth = path
            for _ in range(iterations):
                pth = shortcut_path(pth)
            return pth

        tree_start = [Node(start_pos, cost=0.0)]
        tree_goal = [Node(goal_pos, cost=0.0)]
        nodes = list(tree_start) + list(tree_goal)
        edges = []

        spatial_hash_start.clear()
        spatial_hash_goal.clear()
        add_to_spatial_hash(spatial_hash_start, tree_start[0])
        add_to_spatial_hash(spatial_hash_goal, tree_goal[0])

        best_path = []
        best_cost = float('inf')
        found_first = False
        no_improve_counter = 0
        c_min = dist(start_pos, goal_pos)

        for it in range(self.max_iter):
            if time.monotonic() - start_time > self.time_limit_sec:
                break

            expand_start_tree = (it % 2 == 0)
            tree_curr = tree_start if expand_start_tree else tree_goal
            tree_other = tree_goal if expand_start_tree else tree_start
            spatial_hash_curr = spatial_hash_start if expand_start_tree else spatial_hash_goal
            spatial_hash_other = spatial_hash_goal if expand_start_tree else spatial_hash_start

            if found_first:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if expand_start_tree else start_pos
                else:
                    sample = informed_sample(start_pos, goal_pos, best_cost, c_min)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if expand_start_tree else start_pos
                else:
                    sample = uniform_sample()

            nearest_n = nearest_node(spatial_hash_curr, sample)
            if nearest_n is None:
                no_improve_counter += 1
                if found_first and no_improve_counter >= self.max_no_improve:
                    break
                continue

            new_pos = steer(nearest_n.position, sample)

            if not within_bounds(new_pos):
                no_improve_counter += 1
                if found_first and no_improve_counter >= self.max_no_improve:
                    break
                continue
            if is_in_obstacle(new_pos) or is_edge_in_obstacle(nearest_n.position, new_pos):
                no_improve_counter += 1
                if found_first and no_improve_counter >= self.max_no_improve:
                    break
                continue

            new_node, neighbors = add_node_rrt_star(tree_curr, spatial_hash_curr, new_pos, nodes, edges)
            if new_node is None:
                no_improve_counter += 1
                if found_first and no_improve_counter >= self.max_no_improve:
                    break
                continue
            rewire(tree_curr, spatial_hash_curr, new_node, neighbors, edges)

            # Incremental greedy connect attempt from tree_other to new_node.position
            connected_node = None
            nearest_other = nearest_node(spatial_hash_other, new_node.position)
            if nearest_other and dist(nearest_other.position, new_node.position) <= self.step_size and can_connect(nearest_other, new_node):
                conn_cost = nearest_other.cost + dist(nearest_other.position, new_node.position)
                final_node = Node(new_node.position, cost=conn_cost)
                nearest_other.add_child(final_node)
                tree_other.append(final_node)
                nodes.append(final_node)
                edges.append((nearest_other, final_node))
                add_to_spatial_hash(spatial_hash_other, final_node)
                connected_node = final_node
            else:
                current = nearest_other
                max_connect_steps = 10
                for _ in range(max_connect_steps):
                    if current is None:
                        break
                    step_pos = steer(current.position, new_node.position)
                    if not within_bounds(step_pos):
                        break
                    if is_in_obstacle(step_pos) or is_edge_in_obstacle(current.position, step_pos):
                        break
                    new_other_node, neighbors_other = add_node_rrt_star(tree_other, spatial_hash_other, step_pos, nodes, edges)
                    if new_other_node is None:
                        break
                    rewire(tree_other, spatial_hash_other, new_other_node, neighbors_other, edges)
                    current = new_other_node
                    if dist(current.position, new_node.position) <= self.step_size and can_connect(current, new_node):
                        final_node = Node(new_node.position, cost=current.cost + dist(current.position, new_node.position))
                        current.add_child(final_node)
                        tree_other.append(final_node)
                        nodes.append(final_node)
                        edges.append((current, final_node))
                        add_to_spatial_hash(spatial_hash_other, final_node)
                        connected_node = final_node
                        break

            if connected_node is not None:
                candidate_path = stitch_path(new_node, connected_node, expand_start_tree)
                candidate_cost = 0.0
                for i in range(len(candidate_path) - 1):
                    candidate_cost += dist(candidate_path[i], candidate_path[i + 1])
                if candidate_cost + 1e-6 < best_cost:
                    best_cost = candidate_cost
                    best_path = candidate_path
                    found_first = True
                    no_improve_counter = 0
                else:
                    no_improve_counter += 1
            else:
                no_improve_counter += 1

            if found_first and no_improve_counter >= self.max_no_improve:
                break

        if best_path:
            best_path = iterative_shortcut_path(best_path, iterations=4)
            return PlannerResult(success=True, path=best_path, nodes=nodes, edges=edges)
        else:
            return PlannerResult(success=False, path=[], nodes=nodes, edges=edges)

    def _cross(self, a, b):
        ax = a[0]
        ay = a[1]
        az = a[2] if len(a) > 2 else 0.0
        bx = b[0]
        by = b[1]
        bz = b[2] if len(b) > 2 else 0.0
        return (ay * bz - az * by, az * bx - ax * bz, ax * by - ay * bx)

    def _norm(self, v):
        return math.sqrt(sum(x * x for x in v))

    def _normalize(self, v):
        n = self._norm(v)
        if n < 1e-12:
            return v
        return tuple(x / n for x in v)

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)

    def remove_child(self, child):
        try:
            self.children.remove(child)
        except ValueError:
            pass
class Planner:
    def __init__(self, max_iter=5000, step_size=4.0, goal_sample_rate=0.1, time_limit=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.time_limit = time_limit

    def plan(self, map):
        import time, random, math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        start_time = time.monotonic()

        def dist(a,b):
            return math.dist(a, b)

        def steer(from_p, to_p, max_step):
            d = dist(from_p, to_p)
            if d <= max_step:
                return to_p
            ratio = max_step / d
            return tuple(from_p[i] + ratio*(to_p[i]-from_p[i]) for i in range(dim))

        def is_in_bounds(p):
            for i in range(dim):
                if p[i]<0 or p[i]>bounds[i]:
                    return False
            return True

        def is_in_obstacle(p):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d_ = obs
                    px, py, pz = p
                    if x <= px <= x+w and y <= py <= y+h and z <= pz <= z+d_:
                        return True
                else:
                    x, y, w_, h_ = obs
                    px, py = p
                    if x <= px <= x+w_ and y <= py <= y+h_:
                        return True
            return False

        def is_edge_in_obstacle(from_p, to_p, resolution=0.5):
            distance = dist(from_p, to_p)
            steps = max(1, int(distance / resolution))
            for i in range(steps+1):
                interp = tuple(from_p[d] + (to_p[d]-from_p[d])*(i/steps) for d in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def nearest(tree, point):
            return min(tree, key=lambda node: dist(node.position, point))

        def near(tree, point, radius):
            r2 = radius*radius
            res = []
            for node in tree:
                dx = node.position[0] - point[0]
                dy = node.position[1] - point[1]
                if is_3d:
                    dz = node.position[2] - point[2]
                    d2 = dx*dx + dy*dy + dz*dz
                else:
                    d2 = dx*dx + dy*dy
                if d2 <= r2:
                    res.append(node)
            return res

        def extract_path(node_start, node_goal):
            path_start = []
            cur = node_start
            while cur:
                path_start.append(cur.position)
                cur = cur.parent
            path_start.reverse()
            path_goal = []
            cur = node_goal
            while cur:
                path_goal.append(cur.position)
                cur = cur.parent
            if path_start[-1] == path_goal[0]:
                path_goal = path_goal[1:]
            return path_start + path_goal

        def neighbor_radius(num_nodes):
            if num_nodes <= 1: 
                return self.step_size*2.5
            gamma = 15.0
            return max(gamma * ((math.log(num_nodes)/num_nodes) ** (1/dim)), self.step_size*0.5)

        def rewire(tree, new_node, radius):
            near_nodes = near(tree, new_node.position, radius)
            min_parent = new_node.parent
            min_cost = new_node.cost
            for node in near_nodes:
                if node is new_node.parent:
                    continue
                if not is_edge_in_obstacle(node.position, new_node.position):
                    c = node.cost + dist(node.position, new_node.position)
                    if c < min_cost:
                        min_parent = node
                        min_cost = c
            if min_parent != new_node.parent:
                if new_node.parent:
                    new_node.parent.remove_child(new_node)
                new_node.parent = min_parent
                new_node.cost = min_cost
                min_parent.add_child(new_node)
            for node in near_nodes:
                if node is new_node:
                    continue
                if not is_edge_in_obstacle(new_node.position, node.position):
                    cost_through = new_node.cost + dist(new_node.position, node.position)
                    if cost_through < node.cost:
                        if node.parent:
                            node.parent.remove_child(node)
                        node.parent = new_node
                        node.cost = cost_through
                        new_node.add_child(node)

        def try_connect(new_node, other_tree):
            nearest_node = nearest(other_tree, new_node.position)
            if dist(new_node.position, nearest_node.position) <= self.step_size:
                if not is_edge_in_obstacle(new_node.position, nearest_node.position):
                    return nearest_node
            return None

        def incremental_shortcut(path, attempts=20):
            if len(path) < 3:
                return path
            new_path = list(path)
            for _ in range(attempts):
                if len(new_path) < 3:
                    break
                i = random.randint(0, len(new_path)-3)
                j = random.randint(i+2, len(new_path)-1)
                if not is_edge_in_obstacle(new_path[i], new_path[j]):
                    new_path = new_path[:i+1]+new_path[j:]
            return new_path

        start_tree = [Node(start_pos)]
        goal_tree = [Node(goal_pos)]
        all_nodes = []
        edges = []

        best_path = []
        best_cost = float('inf')
        success = False

        trees = [(start_tree, goal_tree),(goal_tree, start_tree)]
        for itr in range(self.max_iter):
            if time.monotonic() - start_time > self.time_limit:
                break

            sample = goal_pos if random.random() < self.goal_sample_rate else None
            if sample is None:
                for _ in range(10):
                    q = tuple(random.uniform(0,bounds[i]) for i in range(dim))
                    if not is_in_obstacle(q):
                        sample = q
                        break
                if sample is None:
                    continue  # failed to sample a free point after tries

            for treeA, treeB in trees:
                nearest_node = nearest(treeA, sample)
                new_pos = steer(nearest_node.position, sample, self.step_size)
                if not is_in_bounds(new_pos):
                    continue
                if is_in_obstacle(new_pos) or is_edge_in_obstacle(nearest_node.position, new_pos):
                    continue

                new_node = Node(new_pos, parent=nearest_node, cost=nearest_node.cost + dist(nearest_node.position, new_pos))
                nearest_node.add_child(new_node)
                treeA.append(new_node)
                all_nodes.append(new_node)
                edges.append((nearest_node, new_node))

                radius = neighbor_radius(len(treeA))
                rewire(treeA, new_node, radius)

                connect_node = try_connect(new_node, treeB)
                if connect_node:
                    total_cost = new_node.cost + connect_node.cost + dist(new_node.position, connect_node.position)
                    if total_cost < best_cost:
                        best_cost = total_cost
                        best_path = extract_path(new_node, connect_node)
                        best_path = incremental_shortcut(best_path, attempts=15)
                        success = True
            if success:
                # incremental shortcut again to polish slightly before stopping
                best_path = incremental_shortcut(best_path, attempts=25)
                break

        if not success and all_nodes:
            # fallback: best node nearest goal in start tree
            candidate = min(start_tree + all_nodes, key=lambda n: dist(n.position, goal_pos))
            path_fallback = []
            cur = candidate
            while cur:
                path_fallback.append(cur.position)
                cur = cur.parent
            path_fallback.reverse()
            best_path = incremental_shortcut(path_fallback, attempts=15)
            success = False

        nodes = start_tree + goal_tree + all_nodes
        return PlannerResult(success=success, path=best_path, nodes=nodes, edges=edges)

class Node:
    def __init__(self, position: Tuple[float, ...], parent: Optional["Node"]=None, cost: float=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children: List["Node"] = []
        self.valid = True

    def add_child(self, child: "Node"):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child: "Node"):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: Optional["Node"], new_cost: float):
        if self.parent is not new_parent:
            if self.parent is not None:
                self.parent.remove_child(self)
            self.parent = new_parent
            self.cost = new_cost
            if new_parent is not None:
                new_parent.children.append(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int=5000, step_size: float=5.0, goal_sample_rate: float=0.1,
                 gamma_rrt_star: float=50.0, max_neighbor_radius: float=40.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.gamma_rrt_star = gamma_rrt_star
        self.max_neighbor_radius = max_neighbor_radius

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        start_time = time.monotonic()
        time_limit = 30.0

        nodes: List[Node] = []
        edges: List[Tuple[Node, Node]] = []

        def in_bounds(p):
            for i in range(dim):
                if p[i] < 0 or p[i] > bounds[i]:
                    return False
            return True

        def is_free_node(p):
            if not in_bounds(p):
                return False
            return not self._is_in_obstacle(p, obstacles, is_3d)

        def is_free_edge(p1, p2):
            d = math.dist(p1, p2)
            resolution = min(1.0, self.step_size * 0.5)
            steps = max(1, int(d / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if self._is_in_obstacle(interp, obstacles, is_3d):
                    return False
            return True

        def steer(from_pos, to_pos, max_dist):
            d = math.dist(from_pos, to_pos)
            if d <= max_dist:
                return to_pos
            ratio = max_dist / d
            return tuple(from_pos[i] + ratio * (to_pos[i] - from_pos[i]) for i in range(dim))

        def sample_free():
            while True:
                p = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if is_free_node(p):
                    return p

        def nearest(tree: List[Node], point: Tuple[float, ...]) -> Node:
            best_node = tree[0]
            best_dist = math.dist(best_node.position, point)
            for node in tree[1:]:
                d = math.dist(node.position, point)
                if d < best_dist:
                    best_dist = d
                    best_node = node
            return best_node

        def near_nodes(tree: List[Node], point: Tuple[float, ...], n_nodes: int) -> List[Node]:
            if n_nodes < 2:
                return []
            r = self.gamma_rrt_star * (math.log(n_nodes) / n_nodes) ** (1/dim)
            radius = min(self.max_neighbor_radius, max(self.step_size * 2.0, r))
            result = [node for node in tree if math.dist(node.position, point) <= radius]
            return result

        def choose_parent_and_add(tree, new_pos, obstacles, is_3d, nodes, edges):
            nearest_node_ = nearest(tree, new_pos)
            neighbors = near_nodes(tree, new_pos, len(tree))
            min_cost = nearest_node_.cost + math.dist(nearest_node_.position, new_pos)
            best_parent = nearest_node_
            for nb in neighbors:
                if nb == nearest_node_:
                    continue
                if not self._is_edge_in_obstacle(nb.position, new_pos, obstacles, is_3d):
                    cost_cand = nb.cost + math.dist(nb.position, new_pos)
                    if cost_cand < min_cost:
                        min_cost = cost_cand
                        best_parent = nb
            if not is_free_edge(best_parent.position, new_pos):
                return None, []
            new_node = Node(new_pos, None, min_cost)
            best_parent.children.append(new_node)
            new_node.parent = best_parent
            nodes.append(new_node)
            tree.append(new_node)
            edges.append((best_parent, new_node))
            return new_node, neighbors

        def rewire_from(pivot: Node, neighbors: List[Node], obstacles, is_3d, edges):
            for nb in neighbors:
                if nb is pivot:
                    continue
                if not self._is_edge_in_obstacle(pivot.position, nb.position, obstacles, is_3d):
                    new_cost = pivot.cost + math.dist(pivot.position, nb.position)
                    if new_cost + 1e-15 < nb.cost:
                        if nb.parent is not None:
                            try:
                                edges.remove((nb.parent, nb))
                            except ValueError:
                                pass
                            nb.parent.children.remove(nb)
                        nb.parent = pivot
                        nb.cost = new_cost
                        pivot.children.append(nb)
                        edges.append((pivot, nb))

        start_root = Node(start_pos, None, 0.0)
        goal_root = Node(goal_pos, None, 0.0)
        nodes.extend([start_root, goal_root])
        tree_start = [start_root]
        tree_goal = [goal_root]

        best_cost = float('inf')
        best_path_nodes: List[Node] = []

        for iter_count in range(self.max_iter):
            if time.monotonic() - start_time > time_limit:
                break

            # Goal bias sampling: alternate sampling randomly or goal
            if random.random() < self.goal_sample_rate:
                sample = goal_pos
            else:
                sample = sample_free()

            # Alternate expansion trees each iteration
            if iter_count % 2 == 0:
                tree_a, tree_b = tree_start, tree_goal
            else:
                tree_a, tree_b = tree_goal, tree_start

            new_node, neighbors = choose_parent_and_add(tree_a, steer(nearest(tree_a, sample).position, sample, self.step_size),
                                                       obstacles, is_3d, nodes, edges)
            if new_node is None:
                continue

            rewire_from(new_node, neighbors, obstacles, is_3d, edges)

            # Attempt to connect the other tree to new node
            nearest_in_b = nearest(tree_b, new_node.position)
            dist_to_near_b = math.dist(new_node.position, nearest_in_b.position)
            last_connect_node = None
            curr_pos = new_node.position

            while dist_to_near_b > self.step_size:
                next_pos = steer(curr_pos, nearest_in_b.position, self.step_size)
                if self._is_in_obstacle(next_pos, obstacles, is_3d) or self._is_edge_in_obstacle(curr_pos, next_pos, obstacles, is_3d):
                    break
                chosen_node, nbs = choose_parent_and_add(tree_b, next_pos, obstacles, is_3d, nodes, edges)
                if chosen_node is None:
                    break
                rewire_from(chosen_node, nbs, obstacles, is_3d, edges)
                curr_pos = chosen_node.position
                dist_to_near_b = math.dist(curr_pos, nearest_in_b.position)
                last_connect_node = chosen_node

            # Try to connect directly if within step size
            if last_connect_node is not None and math.dist(last_connect_node.position, nearest_in_b.position) <= self.step_size:
                if not self._is_edge_in_obstacle(last_connect_node.position, nearest_in_b.position, obstacles, is_3d) and \
                    not self._is_in_obstacle(nearest_in_b.position, obstacles, is_3d):

                    final_node = Node(nearest_in_b.position, None,
                                      last_connect_node.cost + math.dist(last_connect_node.position, nearest_in_b.position))
                    last_connect_node.children.append(final_node)
                    final_node.parent = last_connect_node
                    nodes.append(final_node)
                    tree_b.append(final_node)
                    edges.append((last_connect_node, final_node))

                    # Extract combined path
                    path_start = new_node.path_from_root()
                    path_goal = final_node.path_from_root()
                    if path_start[-1] == path_goal[-1]:
                        # Avoid duplicate at connection point
                        path_goal = path_goal[:-1]
                    full_path = path_start + path_goal[::-1]

                    # Update best path if cost better
                    path_cost = final_node.cost + new_node.cost - math.dist(new_node.position, nearest_in_b.position)
                    if path_cost < best_cost:
                        best_cost = path_cost
                        best_path_nodes = full_path
                        success = True

                    # Early stopping on first valid path
                    break

        # If no path connected, try closest nodes
        if not best_path_nodes:
            # Find node closest to goal in start tree
            goal_nearest_node = min(tree_start, key=lambda n: math.dist(n.position, goal_pos))
            # Backtrack
            best_path_nodes = goal_nearest_node.path_from_root()
            success = False

        # Path shortcutting/post-processing
        def shortcut_path(path_points: List[Tuple[float, ...]]) -> List[Tuple[float, ...]]:
            if len(path_points) < 3:
                return path_points
            shortened = [path_points[0]]
            i = 0
            while i < len(path_points) - 1:
                j = len(path_points) - 1
                while j > i + 1:
                    if is_free_edge(path_points[i], path_points[j]):
                        shortened.append(path_points[j])
                        i = j
                        break
                    j -= 1
                else:
                    i += 1
                    if i < len(path_points):
                        shortened.append(path_points[i])
            return shortened

        best_path_nodes = shortcut_path(best_path_nodes)

        return PlannerResult(
            success=success if 'success' in locals() else False,
            path=best_path_nodes,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * i / steps for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
        child.parent = None

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]

    def update_cost(self, new_cost):
        cost_diff = new_cost - self.cost
        self.cost = new_cost
        for c in self.children:
            c.update_cost(c.cost + cost_diff)
class Planner:
    def __init__(self, max_iter=8000, step_size=5.0, goal_sample_rate=0.05,
                 time_limit_sec=30.0, gamma_rrt_star=50.0, post_opt_max=200, post_opt_no_improve=100,
                 improve_tol=1e-8):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.time_limit_sec = time_limit_sec
        self.gamma_rrt_star = gamma_rrt_star
        self.post_opt_max = post_opt_max
        self.post_opt_no_improve = post_opt_no_improve
        self.improve_tol = improve_tol

    def plan(self, map):
        import time
        import math
        import random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def dist(a, b):
            return math.dist(a, b)

        def steer(fr, to):
            d = dist(fr, to)
            if d <= self.step_size:
                return to
            r = self.step_size / d
            return tuple(fr[i] + (to[i] - fr[i]) * r for i in range(dim))

        def within_bounds(p):
            for i in range(dim):
                if p[i] < 0 or p[i] > bounds[i]:
                    return False
            return True

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x,y,z,w,h,d = obs
                    px,py,pz = pos
                    if x <= px <= x+w and y <= py <= y+h and z <= pz <= z+d:
                        return True
                else:
                    x,y,w_,h_ = obs
                    px, py = pos
                    if x <= px <= x+w_ and y <= py <= y+h_:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2, resolution=1.0):
            length = dist(p1,p2)
            steps = max(1, int(length/resolution))
            for i in range(steps+1):
                p = tuple(p1[j] + (p2[j]-p1[j]) * (i/steps) for j in range(dim))
                if is_in_obstacle(p):
                    return True
            return False

        def uniform_sample():
            trials = 0
            while True:
                s = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not is_in_obstacle(s):
                    return s
                trials += 1
                if trials > 100:
                    # fallback to start to avoid infinite loop
                    return start_pos

        def informed_sample(start, goal, c_best, c_min):
            if c_best == float('inf') or c_min <= 1e-12:
                return uniform_sample()
            center = tuple((s+g)*0.5 for s,g in zip(start, goal))
            a1 = tuple((g - s)/c_min for s,g in zip(start, goal))

            if dim == 2:
                a2 = (-a1[1], a1[0])
                basis = (a1, a2)
                r1 = c_best / 2.0
                r2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0.0))/2.0
                radii = (r1, r2)
            else:
                ref = (1.0,0.0,0.0) if abs(a1[0])<0.9 else (0.0,1.0,0.0)
                b2 = self._normalize(self._cross(a1, ref))
                if self._norm(b2)<1e-12:
                    ref = (0.0,0.0,1.0)
                    b2 = self._normalize(self._cross(a1, ref))
                b3 = self._cross(a1,b2)
                basis = (a1,b2,b3)
                r1 = c_best/2.0
                r_other = math.sqrt(max(c_best*c_best - c_min*c_min,0.0))/2.0
                radii = (r1, r_other, r_other)

            def sample_unit_ball(dim_):
                while True:
                    v = [random.gauss(0,1) for _ in range(dim_)]
                    normv = math.sqrt(sum(x*x for x in v))
                    if normv > 1e-12:
                        v = [x/normv for x in v]
                        r = random.random()**(1.0/dim_)
                        return [x*r for x in v]

            u = sample_unit_ball(dim)
            mapped = [center[i] for i in range(dim)]
            for i in range(dim):
                for j in range(dim):
                    mapped[i] += basis[j][i]*radii[j]*u[j]
            clipped = tuple(min(max(mapped[i],0), bounds[i]) for i in range(dim))
            if is_in_obstacle(clipped):
                return uniform_sample()
            return clipped

        def nearest(nodes_list, p):
            return min(nodes_list, key=lambda n: dist(n.position, p))

        def neighbor_radius(n_nodes):
            if n_nodes < 2:
                return max(2.0, self.step_size*2.0)
            val = self.gamma_rrt_star * ((math.log(n_nodes)/n_nodes)**(1.0/dim))
            max_r = max(bounds)*0.25
            min_r = max(2.0, self.step_size)
            return max(min_r, min(max_r, val))

        def near_nodes(nodes_list, p, radius):
            r_sq = radius*radius
            return [n for n in nodes_list if dist(n.position,p) <= radius]

        def add_node_rrt_star(tree, pos, all_nodes, edge_list):
            radius = neighbor_radius(len(tree))
            neighbors = near_nodes(tree, pos, radius)
            # parents candidates with collision-free edges
            parents = [n for n in neighbors if not is_edge_in_obstacle(n.position,pos)]
            if not parents:
                p = nearest(tree,pos)
                if is_edge_in_obstacle(p.position,pos):
                    return None, []
                parents = [p]
            best_parent = min(parents, key=lambda n: n.cost + dist(n.position,pos))
            new_cost = best_parent.cost + dist(best_parent.position,pos)
            new_node = Node(pos, parent=None, cost=new_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            all_nodes.append(new_node)
            edge_list.append((best_parent,new_node))
            return new_node, neighbors

        def rewire(tree, new_node, neighbors, edge_list):
            for nb in neighbors:
                if nb is new_node:
                    continue
                if is_edge_in_obstacle(new_node.position, nb.position):
                    continue
                new_cost = new_node.cost + dist(new_node.position, nb.position)
                if new_cost + 1e-12 < nb.cost:
                    if nb.parent:
                        nb.parent.remove_child(nb)
                        try:
                            edge_list.remove((nb.parent, nb))
                        except ValueError:
                            pass
                    new_node.add_child(nb)
                    nb.update_cost(new_cost)
                    edge_list.append((new_node, nb))

        def try_direct_connection(node_from, node_to, all_nodes, edge_list, tree_to):
            if dist(node_from.position, node_to.position) <= self.step_size and not is_edge_in_obstacle(node_from.position, node_to.position):
                # Create new node at position of node_to as direct connection
                new_cost = node_from.cost + dist(node_from.position, node_to.position)
                new_node = Node(node_to.position, parent=None, cost=new_cost)
                node_from.add_child(new_node)
                tree_to.append(new_node)
                all_nodes.append(new_node)
                edge_list.append((node_from, new_node))
                return new_node
            return None

        def stitch_path(node_start, node_goal, a_is_start):
            path_a = node_start.path_from_root()
            path_b = node_goal.path_from_root()
            if path_a and path_b and path_a[-1] == path_b[-1]:
                merged = path_a + path_b[-2::-1]
            else:
                merged = path_a + path_b[::-1]
            return merged if a_is_start else merged[::-1]

        def shortcut_path(path):
            if len(path) < 3:
                return path
            smoothed = [path[0]]
            i = 0
            while i < len(path) - 1:
                j = len(path) - 1
                while j > i + 1:
                    if not is_edge_in_obstacle(path[i], path[j]):
                        break
                    j -= 1
                smoothed.append(path[j])
                i = j
            return smoothed

        start_time = time.monotonic()

        tree_start = [Node(start_pos,cost=0.0)]
        tree_goal  = [Node(goal_pos,cost=0.0)]
        all_nodes = [tree_start[0], tree_goal[0]]
        edges = []

        best_path = []
        best_cost = float('inf')
        found_path = False
        c_min = dist(start_pos, goal_pos)

        postopt_iters = 0
        postopt_no_improve = 0

        for it in range(self.max_iter):
            elapsed = time.monotonic() - start_time
            if elapsed > self.time_limit_sec:
                break

            curr_tree = tree_start if it % 2 == 0 else tree_goal
            other_tree = tree_goal if it % 2 == 0 else tree_start
            a_is_start_side = (curr_tree is tree_start)

            # Sample with goal bias and informed sampling after first path found
            if found_path:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if a_is_start_side else start_pos
                else:
                    sample = informed_sample(start_pos, goal_pos, best_cost, c_min)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if a_is_start_side else start_pos
                else:
                    sample = uniform_sample()

            nearest_curr = nearest(curr_tree, sample)
            new_pos = steer(nearest_curr.position, sample)

            if (not within_bounds(new_pos)) or is_in_obstacle(new_pos):
                if found_path:
                    postopt_iters += 1
                    postopt_no_improve += 1
                    if postopt_iters >= self.post_opt_max or postopt_no_improve >= self.post_opt_no_improve:
                        break
                continue
            if is_edge_in_obstacle(nearest_curr.position, new_pos):
                if found_path:
                    postopt_iters += 1
                    postopt_no_improve += 1
                    if postopt_iters >= self.post_opt_max or postopt_no_improve >= self.post_opt_no_improve:
                        break
                continue

            new_node, neighbors = add_node_rrt_star(curr_tree, new_pos, all_nodes, edges)
            if new_node is None:
                if found_path:
                    postopt_iters += 1
                    postopt_no_improve += 1
                    if postopt_iters >= self.post_opt_max or postopt_no_improve >= self.post_opt_no_improve:
                        break
                continue

            rewire(curr_tree, new_node, neighbors, edges)

            # Try to directly connect the new node to the nearest node in other tree to shortcut
            nearest_other = nearest(other_tree, new_node.position)
            connect_node = None

            direct_conn = try_direct_connection(nearest_other, new_node, all_nodes, edges, other_tree)
            if direct_conn is not None:
                connect_node = direct_conn
            else:
                # Otherwise try incremental greedy extension from other tree to new_node
                current = nearest_other
                incremental_connected = None
                max_steps = 20
                for _ in range(max_steps):
                    step = steer(current.position, new_node.position)
                    if (not within_bounds(step)) or is_in_obstacle(step):
                        break
                    if is_edge_in_obstacle(current.position, step):
                        break
                    step_node, step_neighbors = add_node_rrt_star(other_tree, step, all_nodes, edges)
                    if step_node is None:
                        break
                    rewire(other_tree, step_node, step_neighbors, edges)
                    current = step_node
                    if dist(current.position, new_node.position) <= self.step_size:
                        if not is_edge_in_obstacle(current.position, new_node.position):
                            # Make final connection node
                            final_cost = current.cost + dist(current.position, new_node.position)
                            final_node = Node(new_node.position, parent=None, cost=final_cost)
                            current.add_child(final_node)
                            other_tree.append(final_node)
                            all_nodes.append(final_node)
                            edges.append((current, final_node))
                            incremental_connected = final_node
                        break
                if incremental_connected is not None:
                    connect_node = incremental_connected

            if connect_node is not None:
                candidate_path = stitch_path(new_node, connect_node, a_is_start_side)
                total_cost = 0.0
                for i in range(len(candidate_path)-1):
                    total_cost += dist(candidate_path[i], candidate_path[i+1])
                if total_cost + self.improve_tol < best_cost:
                    best_cost = total_cost
                    best_path = candidate_path
                    found_path = True
                    postopt_iters = 0
                    postopt_no_improve = 0
                else:
                    if found_path:
                        postopt_no_improve += 1
                if found_path:
                    postopt_iters += 1
                    if postopt_iters >= self.post_opt_max or postopt_no_improve >= self.post_opt_no_improve:
                        break
            else:
                if found_path:
                    postopt_iters += 1
                    postopt_no_improve += 1
                    if postopt_iters >= self.post_opt_max or postopt_no_improve >= self.post_opt_no_improve:
                        break

        if best_path:
            best_path = shortcut_path(best_path)
            success = True
        else:
            success = False

        return PlannerResult(
            success=success,
            path=best_path,
            nodes=all_nodes,
            edges=edges
        )

    def _cross(self,a,b):
        ax,ay,az = a[0], a[1], a[2]
        bx,by,bz = b[0], b[1], b[2]
        return (ay*bz - az*by, az*bx - ax*bz, ax*by - ay*bx)

    def _norm(self,v):
        return math.sqrt(sum(x*x for x in v))

    def _normalize(self,v):
        n = self._norm(v)
        if n < 1e-12:
            return v
        return tuple(x / n for x in v)

