class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0, goal_sample_rate: float = 0.1):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = dim == 3

        start_time = time.monotonic()
        TIME_LIMIT = 30.0

        start_tree = [Node(start_pos)]
        goal_tree = [Node(goal_pos)]
        all_nodes = []  # Nodes other than tree roots
        edges = []

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p, step):
            d = dist(from_p, to_p)
            if d <= step:
                return to_p
            ratio = step / d
            return tuple(from_p[i] + ratio * (to_p[i] - from_p[i]) for i in range(dim))

        def is_in_obstacle(p):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = p
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w, h = obs
                    px, py = p
                    if x <= px <= x + w and y <= py <= y + h:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2, resolution=1.0):
            distance = dist(p1, p2)
            steps = max(1, int(distance / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[d] + (p2[d] - p1[d]) * (i / steps) for d in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def nearest(tree, point):
            min_node = tree[0]
            min_dist = dist(min_node.position, point)
            for node in tree[1:]:
                cd = dist(node.position, point)
                if cd < min_dist:
                    min_node = node
                    min_dist = cd
            return min_node

        def near(tree, point, radius):
            result = []
            r2 = radius * radius
            for node in tree:
                d = 0.0
                # Use squared distance for efficiency
                if is_3d:
                    d = (node.position[0]-point[0])**2 + (node.position[1]-point[1])**2 + (node.position[2]-point[2])**2
                else:
                    d = (node.position[0]-point[0])**2 + (node.position[1]-point[1])**2
                if d <= r2:
                    result.append(node)
            return result

        def extract_path(node_s, node_g):
            path_s = []
            cur = node_s
            while cur is not None:
                path_s.append(cur.position)
                cur = cur.parent
            path_s.reverse()
            path_g = []
            cur = node_g
            while cur is not None:
                path_g.append(cur.position)
                cur = cur.parent
            if path_s[-1] == path_g[0]:
                path_g = path_g[1:]
            return path_s + path_g

        def get_cost(node):
            return node.cost

        def rewire(tree, new_node, radius):
            near_nodes = near(tree, new_node.position, radius)
            # Choose best parent
            min_parent = new_node.parent
            min_cost = new_node.cost
            for node in near_nodes:
                if node == new_node.parent:
                    continue
                edge_free = not is_edge_in_obstacle(node.position, new_node.position)
                if edge_free:
                    cost_via_node = node.cost + dist(node.position, new_node.position)
                    if cost_via_node < min_cost:
                        min_parent = node
                        min_cost = cost_via_node
            if min_parent != new_node.parent:
                if new_node.parent:
                    try:
                        new_node.parent.children.remove(new_node)
                    except Exception:
                        pass
                new_node.parent = min_parent
                new_node.cost = min_cost
                min_parent.add_child(new_node)
            # Try rewiring neighbors to new_node
            for node in near_nodes:
                if node == new_node:
                    continue
                edge_free = not is_edge_in_obstacle(new_node.position, node.position)
                if edge_free:
                    cost_via_new = new_node.cost + dist(new_node.position, node.position)
                    if cost_via_new < node.cost:
                        if node.parent:
                            try:
                                node.parent.children.remove(node)
                            except Exception:
                                pass
                        node.parent = new_node
                        node.cost = cost_via_new
                        new_node.add_child(node)

        def attempt_connect(n1, n2):
            if dist(n1.position, n2.position) <= self.step_size and not is_edge_in_obstacle(n1.position, n2.position):
                return True
            return False

        def try_connect_trees(new_node, other_tree):
            nearest_node = nearest(other_tree, new_node.position)
            if attempt_connect(new_node, nearest_node):
                return nearest_node
            return None

        best_path = []
        best_cost = float('inf')
        success = False

        # Adaptive neighbor radius factor: gamma_RRTstar ~ 2*(1+1/dim)**(1/dim) * (volume_free / zeta_ball)^(1/dim)
        # For simplicity use basic radius proportional to (log(n)/n)^(1/dim), multiplied by a constant (20)
        def neighbor_radius(n):
            if n <= 1:
                return self.step_size * 2.5
            const_gamma = 20.0
            r = const_gamma * ((math.log(n) / n) ** (1.0 / dim))
            return max(r, self.step_size*0.5)

        for iteration in range(self.max_iter):
            if time.monotonic() - start_time > TIME_LIMIT:
                break

            # Sample with goal bias
            if random.random() < self.goal_sample_rate:
                sample = goal_pos
            else:
                sampled = False
                while not sampled:
                    q = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                    if not is_in_obstacle(q):
                        sample = q
                        sampled = True

            for tree_a, tree_b in [(start_tree, goal_tree), (goal_tree, start_tree)]:
                # Extend tree_a toward sample
                nearest_node = nearest(tree_a, sample)
                new_pos = steer(nearest_node.position, sample, self.step_size)
                if is_in_obstacle(new_pos):
                    continue
                if is_edge_in_obstacle(nearest_node.position, new_pos):
                    continue
                new_node = Node(new_pos)
                new_node.parent = nearest_node
                new_node.cost = nearest_node.cost + dist(nearest_node.position, new_pos)
                nearest_node.add_child(new_node)
                tree_a.append(new_node)
                all_nodes.append(new_node)
                edges.append((nearest_node, new_node))

                # Rewire around new_node
                radius = neighbor_radius(len(tree_a))
                rewire(tree_a, new_node, radius)

                # Try connecting to other tree
                connect_node = try_connect_trees(new_node, tree_b)
                if connect_node:
                    total_cost = new_node.cost + connect_node.cost + dist(new_node.position, connect_node.position)
                    if total_cost < best_cost:
                        best_cost = total_cost
                        best_path = extract_path(new_node, connect_node)
                        success = True
                        # Early stop on first solution found
                        return PlannerResult(success=True, path=best_path, nodes=start_tree+goal_tree+all_nodes, edges=edges)

        return PlannerResult(success=success, path=best_path, nodes=start_tree+goal_tree+all_nodes, edges=edges)

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []

    def add_child(self, child):
        self.children.append(child)

    def remove_child(self, child):
        try:
            self.children.remove(child)
        except ValueError:
            pass

    def update_parent(self, new_parent, new_cost):
        if self.parent is not new_parent:
            if self.parent is not None:
                self.parent.remove_child(self)
            self.parent = new_parent
            self.cost = new_cost
            if new_parent is not None:
                new_parent.add_child(self)
class Planner:
    def __init__(self, max_iter=5000, step_size=5.0, goal_sample_rate=0.15,
                 max_neighbor_radius=30.0, min_neighbor_radius=5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.max_neighbor_radius = max_neighbor_radius
        self.min_neighbor_radius = min_neighbor_radius

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        start_time = time.monotonic()
        time_limit = 30.0

        nodes: List[Node] = []
        edges: List[Tuple[Node, Node]] = []

        def dist(a, b):
            return math.dist(a, b)

        def in_bounds(p):
            for i in range(dim):
                if p[i] < 0 or p[i] > bounds[i]:
                    return False
            return True

        def is_free_node(p):
            # Node collision & bounds check
            if not in_bounds(p):
                return False
            return not self._is_in_obstacle(p, obstacles, is_3d)

        def is_free_edge(p1, p2):
            # Edge collision check with adaptive resolution
            d = dist(p1, p2)
            resolution = min(1.0, self.step_size * 0.5)
            steps = max(1, int(d / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if self._is_in_obstacle(interp, obstacles, is_3d):
                    return False
            return True

        def steer(from_pos, to_pos, max_dist):
            d = dist(from_pos, to_pos)
            if d <= max_dist:
                return to_pos
            ratio = max_dist / d
            return tuple(from_pos[i] + ratio * (to_pos[i] - from_pos[i]) for i in range(dim))

        def heuristic_cost(a, b):
            return dist(a, b)

        # Ellipsoidal informed sampling
        def sample_informed(c_best, x_start, x_goal):
            if math.isinf(c_best):
                return tuple(random.uniform(0, bounds[i]) for i in range(dim))

            c_min = heuristic_cost(x_start, x_goal)
            if c_min == 0:
                return x_start

            # Center and unit vector for ellipse
            center = tuple((x_start[i] + x_goal[i]) / 2.0 for i in range(dim))
            a1 = tuple((x_goal[i] - x_start[i]) / c_min for i in range(dim))

            # Build rotation matrix aligning x-axis to a1 for 2D or 3D
            def build_rotation_matrix(a):
                if dim == 2:
                    cos_theta = a[0]
                    sin_theta = a[1]
                    return [[cos_theta, -sin_theta],
                            [sin_theta, cos_theta]]
                elif dim == 3:
                    x_axis = a
                    if abs(x_axis[0]) < 0.9:
                        temp = (1, 0, 0)
                    else:
                        temp = (0, 1, 0)
                    v = tuple(temp[i] - x_axis[i] * sum(x_axis[j] * temp[j] for j in range(3)) for i in range(3))
                    norm_v = math.sqrt(sum(v[i] * v[i] for i in range(3)))
                    if norm_v < 1e-10:
                        return [[1, 0, 0], [0, 1, 0], [0, 0, 1]]
                    v_unit = tuple(v[i] / norm_v for i in range(3))
                    w = (x_axis[1] * v_unit[2] - x_axis[2] * v_unit[1],
                         x_axis[2] * v_unit[0] - x_axis[0] * v_unit[2],
                         x_axis[0] * v_unit[1] - x_axis[1] * v_unit[0])
                    R = [list(x_axis), list(v_unit), list(w)]
                    return [[R[0][0], R[1][0], R[2][0]],
                            [R[0][1], R[1][1], R[2][1]],
                            [R[0][2], R[1][2], R[2][2]]]
                else:
                    return None

            R = build_rotation_matrix(a1)
            if R is None:
                return tuple(random.uniform(0, bounds[i]) for i in range(dim))

            r1 = c_best / 2.0
            if c_best < c_min:
                r2 = 0.0001
            else:
                r2 = math.sqrt(abs(c_best * c_best - c_min * c_min)) / 2.0
                if r2 < 0.001:
                    r2 = 0.001
            radii = [r1] + [r2] * (dim - 1)

            while True:
                rnd = [random.gauss(0, 1) for _ in range(dim)]
                norm_rnd = math.sqrt(sum(x * x for x in rnd))
                if norm_rnd > 1e-10:
                    break
            unit_ball = [x / norm_rnd for x in rnd]
            u = random.random()
            scale = u ** (1.0 / dim)
            sample_ball = [scale * unit_ball[i] * radii[i] for i in range(dim)]

            sample = [0.0] * dim
            for i in range(dim):
                s = 0.0
                for j in range(dim):
                    s += R[i][j] * sample_ball[j]
                sample[i] = s + center[i]

            clipped = tuple(max(0.0, min(sample[i], bounds[i])) for i in range(dim))
            return clipped

        # Nearest node search linear
        def nearest_node(p):
            best_i = 0
            best_d = dist(nodes[0].position, p)
            for i in range(1, len(nodes)):
                dcur = dist(nodes[i].position, p)
                if dcur < best_d:
                    best_d = dcur
                    best_i = i
            return nodes[best_i], best_d

        # Neighbor radius shrinks as tree grows (adaptive)
        def neighbor_radius(n):
            # Following RRT* radius formula (Karaman & Frazzoli) with min/max caps
            gamma_rrt_star = self.max_neighbor_radius
            r = gamma_rrt_star * (math.log(n + 1) / (n + 1)) ** (1.0 / dim)
            return max(self.min_neighbor_radius, min(r, self.max_neighbor_radius))

        def nearby_nodes(p, radius):
            result = []
            for node in nodes:
                if dist(node.position, p) <= radius:
                    result.append(node)
            return result

        root = Node(start_pos)
        nodes.append(root)

        best_goal_node = None
        best_goal_cost = float('inf')
        success = False

        for it in range(self.max_iter):
            if time.monotonic() - start_time > time_limit:
                break

            if random.random() < self.goal_sample_rate:
                sample = goal_pos
            else:
                sample = sample_informed(best_goal_cost, start_pos, goal_pos)

            nearest, _ = nearest_node(sample)
            new_pos = steer(nearest.position, sample, self.step_size)

            if not is_free_node(new_pos):
                continue

            if not is_free_edge(nearest.position, new_pos):
                continue

            n_nodes = len(nodes)
            radius = neighbor_radius(n_nodes)

            neighbors = nearby_nodes(new_pos, radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest

            for nb in neighbors:
                tentative_cost = nb.cost + dist(nb.position, new_pos)
                if tentative_cost < min_cost and is_free_edge(nb.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nb

            new_node = Node(new_pos)
            new_node.update_parent(best_parent, min_cost)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors via new node if cheaper
            for nb in neighbors:
                if nb is best_parent:
                    continue
                alt_cost = new_node.cost + dist(new_node.position, nb.position)
                if alt_cost < nb.cost and is_free_edge(new_node.position, nb.position):
                    if nb.parent is not None:
                        try:
                            edges.remove((nb.parent, nb))
                        except ValueError:
                            pass
                    nb.update_parent(new_node, alt_cost)
                    edges.append((new_node, nb))

            dist_to_goal = dist(new_pos, goal_pos)
            if dist_to_goal <= self.step_size and is_free_node(goal_pos) and is_free_edge(new_pos, goal_pos):
                goal_node = Node(goal_pos)
                goal_cost = new_node.cost + dist_to_goal
                goal_node.update_parent(new_node, goal_cost)
                nodes.append(goal_node)
                edges.append((new_node, goal_node))
                if goal_cost < best_goal_cost:
                    best_goal_cost = goal_cost
                    best_goal_node = goal_node
                    success = True
                    break

        # Extract path from best goal node or closest node to goal
        path: List[Tuple[float, ...]] = []
        if success and best_goal_node is not None:
            current = best_goal_node
            while current is not None:
                path.append(current.position)
                current = current.parent
            path.reverse()
        else:
            # No solution found: closest node to goal
            min_dist = float('inf')
            best_node = None
            for n in nodes:
                dcur = dist(n.position, goal_pos)
                if dcur < min_dist:
                    min_dist = dcur
                    best_node = n
            if best_node is not None:
                current = best_node
                while current is not None:
                    path.append(current.position)
                    current = current.parent
                path.reverse()

        # Post-processing shortcutting to smooth and shorten path
        def shortcut_path(path_points):
            if len(path_points) < 3:
                return path_points
            shortened = [path_points[0]]
            i = 0
            while i < len(path_points) - 1:
                j = len(path_points) - 1
                while j > i + 1:
                    if is_free_edge(path_points[i], path_points[j]):
                        shortened.append(path_points[j])
                        i = j
                        break
                    j -= 1
                else:
                    i += 1
                    if i < len(path_points):
                        shortened.append(path_points[i])
            return shortened

        path = shortcut_path(path)

        return PlannerResult(
            success=success,
            path=path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent: Optional["Node"] = None, cost: float = 0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children: List["Node"] = []
        self.valid = True

    def add_child(self, child: "Node") -> None:
        self.children.append(child)
        child.parent = self

    def remove_child(self, child: "Node") -> None:
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: Optional["Node"], new_cost: float) -> None:
        if self.parent is new_parent:
            return
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        if new_parent is not None:
            new_parent.children.append(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size

        self.goal_sample_rate = 0.15
        self.gamma_rrt_star = 40.0  # for neighbor radius
        self.max_neighbor_radius = 35.0

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=0.5):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        start_time = time.monotonic()
        TIME_LIMIT = 30.0

        nodes: List[Node] = []
        edges: List[Tuple[Node, Node]] = []

        def in_bounds(p):
            for i in range(dim):
                if p[i] < 0 or p[i] > bounds[i]:
                    return False
            return True

        def is_free_node(p):
            if not in_bounds(p):
                return False
            if self._is_in_obstacle(p, obstacles, is_3d):
                return False
            return True

        def is_free_edge(p1, p2):
            # adaptive resolution relative to step size and distance; min resolution 0.3 for efficiency
            dist = math.dist(p1, p2)
            resolution = max(0.3, min(1.0, self.step_size * 0.3))
            steps = max(1, int(dist / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if self._is_in_obstacle(interp, obstacles, is_3d):
                    return False
            return True

        def steer(from_pos, to_pos, max_dist) -> Tuple[float, ...]:
            dist = math.dist(from_pos, to_pos)
            if dist <= max_dist:
                return to_pos
            ratio = max_dist / dist
            return tuple(from_pos[i] + ratio * (to_pos[i] - from_pos[i]) for i in range(dim))

        def uniform_sample():
            while True:
                p = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if is_free_node(p):
                    return p

        def ellipsoidal_sample(c_best: float) -> Tuple[float, ...]:
            # Samples within an ellipsoid defined by start and goal and current best cost c_best (> dist(start,goal))
            c_min = math.dist(start_pos, goal_pos)
            if c_best < float('inf'):
                # Rotation matrix from x-axis to vector from start to goal
                center = tuple((s + g) / 2.0 for s, g in zip(start_pos, goal_pos))
                a1 = tuple((g - s) / c_min for s, g in zip(start_pos, goal_pos))  # unit vector along start->goal

                # Construct orthonormal basis
                def unit_vector(vec):
                    norm = math.sqrt(sum(x * x for x in vec))
                    if norm == 0:
                        return vec
                    return tuple(x / norm for x in vec)

                def random_unit_vector(d):
                    # Generate random vector on unit sphere surface in d dimension
                    while True:
                        vec = [random.gauss(0, 1) for _ in range(d)]
                        norm = math.sqrt(sum(x * x for x in vec))
                        if norm > 1e-5:
                            return [x / norm for x in vec]

                # Basis: first axis a1, generate orthonormal basis for the ellipsoid transform
                basis = [a1]
                # Simple Gram-Schmidt process for 2D or 3D:
                if dim == 2:
                    # a2 perpendicular to a1
                    a2 = (-a1[1], a1[0])
                    basis.append(a2)
                else:
                    # For 3D, find two vectors orthogonal to a1 using cross products or Gram-Schmidt
                    import sys
                    # Reference vector not colinear with a1
                    ref = (1.0, 0.0, 0.0) if abs(a1[0]) < 0.9 else (0.0, 1.0, 0.0)
                    # Cross a1 and ref to get basis[1]
                    b1 = (a1[1]*ref[2] - a1[2]*ref[1],
                          a1[2]*ref[0] - a1[0]*ref[2],
                          a1[0]*ref[1] - a1[1]*ref[0])
                    norm_b1 = math.sqrt(sum(x*x for x in b1))
                    if norm_b1 < 1e-6:
                        # fallback different ref vector
                        ref = (0.0, 0.0, 1.0)
                        b1 = (a1[1]*ref[2] - a1[2]*ref[1],
                              a1[2]*ref[0] - a1[0]*ref[2],
                              a1[0]*ref[1] - a1[1]*ref[0])
                        norm_b1 = math.sqrt(sum(x*x for x in b1))
                    b1 = tuple(x / norm_b1 for x in b1)
                    # Cross a1 and b1 to get basis[2]
                    b2 = (a1[1]*b1[2] - a1[2]*b1[1],
                          a1[2]*b1[0] - a1[0]*b1[2],
                          a1[0]*b1[1] - a1[1]*b1[0])
                    basis.extend([b1, b2])

                # Radii of ellipsoid axes
                r1 = c_best / 2.0
                r2 = math.sqrt(c_best ** 2 - c_min ** 2) / 2.0

                # Sample inside unit ball
                while True:
                    if dim == 2:
                        x = random.gauss(0, 1)
                        y = random.gauss(0, 1)
                        norm = math.sqrt(x ** 2 + y ** 2)
                        if norm > 1e-6:
                            u = x / norm
                            v = y / norm
                            r = random.uniform(0, 1) ** 0.5
                            sample_ball = (r * u, r * v)
                            break
                    elif dim == 3:
                        # Sample inside unit sphere by rejection
                        rx, ry, rz = random.uniform(-1, 1), random.uniform(-1, 1), random.uniform(-1, 1)
                        if rx*rx + ry*ry + rz*rz <= 1:
                            sample_ball = (rx, ry, rz)
                            break
                    else:
                        # For dimensions != 2 or 3, fallback uniform
                        sample_ball = tuple(random.uniform(-1, 1) for _ in range(dim))
                        break

                # Scale sample_ball by ellipsoid axes radii
                if dim == 2:
                    sample_ellip = (r1 * sample_ball[0], r2 * sample_ball[1])
                    # Transform back to map coordinate system
                    sample_coords = [center[i] + sample_ellip[0] * basis[0][i] + sample_ellip[1] * basis[1][i]
                                     for i in range(dim)]
                elif dim == 3:
                    sample_ellip = (r1 * sample_ball[0], r2 * sample_ball[1], r2 * sample_ball[2])
                    sample_coords = [center[i] + sample_ellip[0] * basis[0][i] + sample_ellip[1] * basis[1][i] +
                                     sample_ellip[2] * basis[2][i]
                                     for i in range(dim)]
                else:
                    # Fallback: uniform sample in full space (should rarely occur)
                    sample_coords = tuple(random.uniform(0, bounds[i]) for i in range(dim))

                sample_coords = tuple(max(0.0, min(bounds[i], sample_coords[i])) for i in range(dim))  # clamp in bounds
                if is_free_node(sample_coords):
                    return sample_coords
            else:
                # If no current best cost, fallback uniform
                return uniform_sample()

        def nearest(tree_nodes: List[Node], point: Tuple[float, ...]) -> Node:
            best_node = tree_nodes[0]
            best_dist = math.dist(best_node.position, point)
            for node in tree_nodes[1:]:
                d = math.dist(node.position, point)
                if d < best_dist:
                    best_dist = d
                    best_node = node
            return best_node

        def near_nodes(tree_nodes: List[Node], point: Tuple[float, ...]) -> List[Node]:
            n = len(tree_nodes)
            if n < 2:
                return []
            r = self.gamma_rrt_star * (math.log(n) / n) ** (1 / dim)
            radius = min(self.max_neighbor_radius, max(self.step_size * 1.5, r))
            return [node for node in tree_nodes if math.dist(node.position, point) <= radius]

        def choose_parent_and_add(tree_nodes: List[Node], new_pos: Tuple[float, ...]) -> Optional[Node]:
            nearest_node = nearest(tree_nodes, new_pos)
            neighbors = near_nodes(tree_nodes, new_pos)
            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            # Check collision and cost among neighbors for better parent
            for nb in neighbors:
                if nb is nearest_node:
                    continue
                # Prune parents that cannot connect due to collision
                if self._is_edge_in_obstacle(nb.position, new_pos, obstacles, is_3d):
                    continue
                cost_candidate = nb.cost + math.dist(nb.position, new_pos)
                if cost_candidate < min_cost:
                    min_cost = cost_candidate
                    best_parent = nb
            if self._is_edge_in_obstacle(best_parent.position, new_pos, obstacles, is_3d):
                return None
            if not is_free_node(new_pos):  # Extra safety
                return None
            if not is_free_edge(best_parent.position, new_pos):
                return None
            new_node = Node(new_pos, best_parent, min_cost)
            best_parent.children.append(new_node)
            tree_nodes.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))
            rewire(new_node, neighbors)
            return new_node

        def rewire(pivot: Node, neighbors: List[Node]) -> None:
            for nb in neighbors:
                if nb is pivot:
                    continue
                if self._is_edge_in_obstacle(pivot.position, nb.position, obstacles, is_3d):
                    continue
                new_cost = pivot.cost + math.dist(pivot.position, nb.position)
                if new_cost + 1e-15 < nb.cost:
                    # Remove old edge
                    if nb.parent is not None:
                        old_parent = nb.parent
                        if (old_parent, nb) in edges:
                            edges.remove((old_parent, nb))
                        try:
                            old_parent.children.remove(nb)
                        except ValueError:
                            pass
                    nb.update_parent(pivot, new_cost)
                    edges.append((pivot, nb))
                    # Propagate cost to children for consistency
                    propagate_cost(nb)

        def propagate_cost(node: Node) -> None:
            for child in node.children:
                new_cost = node.cost + math.dist(node.position, child.position)
                if new_cost + 1e-15 < child.cost:
                    child.cost = new_cost
                    propagate_cost(child)

        def merge_trees(node_start: Node, tree_goal: List[Node]) -> Optional[Tuple[List[Tuple[float, ...]], float]]:
            nearest_goal_node = nearest(tree_goal, node_start.position)
            dist_goal = math.dist(node_start.position, nearest_goal_node.position)
            if dist_goal > self.step_size:
                return None
            # Check if edge free and nodes free
            if self._is_edge_in_obstacle(node_start.position, nearest_goal_node.position, obstacles, is_3d):
                return None
            if not is_free_node(nearest_goal_node.position):
                return None
            # Connect cost
            connecting_cost = node_start.cost + dist_goal + nearest_goal_node.cost
            path_start = node_start.path_from_root()
            path_goal = nearest_goal_node.path_from_root()
            if path_start[-1] == path_goal[-1]:
                path_goal = path_goal[:-1]
            full_path = path_start + path_goal[::-1]
            return full_path, connecting_cost

        def shortcut_path(path_points: List[Tuple[float, ...]]) -> List[Tuple[float, ...]]:
            if len(path_points) < 3:
                return path_points
            shortened = [path_points[0]]
            i = 0
            while i < len(path_points) - 1:
                j = len(path_points) - 1
                while j > i + 1:
                    if is_free_edge(path_points[i], path_points[j]):
                        shortened.append(path_points[j])
                        i = j
                        break
                    j -= 1
                else:
                    i += 1
                    if i < len(path_points):
                        shortened.append(path_points[i])
            return shortened

        def smooth_path(path_points: List[Tuple[float, ...]]) -> List[Tuple[float, ...]]:
            # Simple smoothing by smoothing points locally using moving average kernel, respecting collisions
            if len(path_points) < 3:
                return path_points
            smoothed = [path_points[0]]
            window = 3
            for i in range(1, len(path_points) - 1):
                indices = range(max(0, i - window//2), min(len(path_points), i + window//2 + 1))
                avg_point = []
                for dim_i in range(dim):
                    avg_coord = sum(path_points[j][dim_i] for j in indices) / len(indices)
                    avg_point.append(avg_coord)
                avg_point = tuple(avg_point)
                # Only replace with avg_point if edge from prior to avg_point and avg_point to next is collision free
                if is_free_edge(smoothed[-1], avg_point) and is_free_edge(avg_point, path_points[i + 1]):
                    smoothed.append(avg_point)
                else:
                    smoothed.append(path_points[i])
            smoothed.append(path_points[-1])
            return smoothed

        # Initialization of trees
        start_root = Node(start_pos, None, 0.0)
        goal_root = Node(goal_pos, None, 0.0)
        nodes.extend([start_root, goal_root])
        tree_start = [start_root]
        tree_goal = [goal_root]

        best_path: List[Tuple[float, ...]] = []
        best_cost = float('inf')
        success = False

        for iter_count in range(self.max_iter):
            if time.monotonic() - start_time > TIME_LIMIT:
                break

            # Sample point (informed sampling if solution known)
            if random.random() < self.goal_sample_rate and not success:
                sample = goal_pos  # goal bias before solution found
            else:
                if success:
                    sample = ellipsoidal_sample(best_cost)
                else:
                    sample = uniform_sample()

            # Alternate tree expansions per iteration
            if iter_count % 2 == 0:
                tree_a, tree_b = tree_start, tree_goal
            else:
                tree_a, tree_b = tree_goal, tree_start

            nearest_node = nearest(tree_a, sample)
            new_pos = steer(nearest_node.position, sample, self.step_size)
            if not is_free_node(new_pos):
                continue
            # Attempt to add new node with rewiring
            new_node = choose_parent_and_add(tree_a, new_pos)
            if new_node is None:
                continue

            # Attempt to connect other tree to this new node
            merged = merge_trees(new_node, tree_b)
            if merged is not None:
                path_candidate, cost_candidate = merged
                if cost_candidate < best_cost:
                    best_cost = cost_candidate
                    best_path = path_candidate
                    success = True
                    # Early stop after first feasible solution
                    break

        # If no success, attempt closest approach from start tree to goal
        if not success:
            nearest_goal_node = nearest(tree_start, goal_pos)
            if nearest_goal_node and is_free_edge(nearest_goal_node.position, goal_pos):
                candidate_path = nearest_goal_node.path_from_root()
                candidate_path.append(goal_pos)
                best_path = candidate_path

        # Post-processing shortcut and smoothing
        if best_path:
            best_path = shortcut_path(best_path)
            best_path = smooth_path(best_path)

        return PlannerResult(success=success, path=best_path, nodes=nodes, edges=edges)

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float = 0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0, radius: float = 15.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.radius = radius

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        start_tree = [Node(start_pos)]
        goal_tree = [Node(goal_pos)]
        nodes = []
        edges = []
        best_path = []
        success = False

        best_cost = float('inf')

        start_time = time.monotonic()
        TIME_LIMIT = 30.0

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p, step):
            d = dist(from_p, to_p)
            if d <= step:
                return to_p
            ratio = step / d
            return tuple(from_p[i] + ratio * (to_p[i] - from_p[i]) for i in range(len(from_p)))

        def is_colliding(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w, h = obs
                    px, py = pos
                    if x <= px <= x + w and y <= py <= y + h:
                        return True
            return False

        def is_edge_colliding(p1, p2):
            d = dist(p1, p2)
            step_count = max(1, int(d / 1.0))
            for i in range(step_count + 1):
                inter = tuple(p1[j] + (p2[j] - p1[j]) * (i / step_count) for j in range(len(p1)))
                if is_colliding(inter):
                    return True
            return False

        def nearest(tree, point):
            return min(tree, key=lambda n: dist(n.position, point))

        def near(tree, point, radius):
            return [n for n in tree if dist(n.position, point) <= radius]

        def extract_path(node_start, node_goal):
            path_start = []
            current = node_start
            while current:
                path_start.append(current.position)
                current = current.parent
            path_start.reverse()

            path_goal = []
            current = node_goal
            while current:
                path_goal.append(current.position)
                current = current.parent

            # Remove duplicate point at connection
            if path_start and path_goal and path_start[-1] == path_goal[0]:
                path_goal = path_goal[1:]

            return path_start + path_goal

        # Attempt to connect two nodes by straight-line if collision free
        def can_connect(n1, n2):
            return dist(n1.position, n2.position) <= self.step_size and not is_edge_colliding(n1.position, n2.position)

        def try_connect_tree(new_node, other_tree):
            nearest_node = nearest(other_tree, new_node.position)
            if can_connect(new_node, nearest_node):
                return nearest_node
            return None

        def rewire(tree, new_node):
            near_nodes = near(tree, new_node.position, self.radius)
            # Choose best parent from near nodes
            min_cost = new_node.cost
            min_parent = new_node.parent
            for near_node in near_nodes:
                if near_node == new_node.parent:
                    continue
                if not is_edge_colliding(near_node.position, new_node.position):
                    cost_through_near = near_node.cost + dist(near_node.position, new_node.position)
                    if cost_through_near < min_cost:
                        min_cost = cost_through_near
                        min_parent = near_node
            # Re-assign parent if better
            if min_parent != new_node.parent:
                if new_node.parent:
                    try:
                        new_node.parent.children.remove(new_node)
                    except ValueError:
                        pass
                new_node.parent = min_parent
                new_node.cost = min_cost
                min_parent.add_child(new_node)

            # Try rewiring near nodes to new_node
            for near_node in near_nodes:
                if near_node == new_node:
                    continue
                edge_free = not is_edge_colliding(new_node.position, near_node.position)
                if edge_free:
                    potential_cost = new_node.cost + dist(new_node.position, near_node.position)
                    if potential_cost < near_node.cost:
                        if near_node.parent:
                            try:
                                near_node.parent.children.remove(near_node)
                            except ValueError:
                                pass
                        near_node.parent = new_node
                        near_node.cost = potential_cost
                        new_node.add_child(near_node)

        # Update best solution path and cost
        def update_best_path(node_start, node_goal):
            nonlocal best_cost, best_path, success
            candidate_path = extract_path(node_start, node_goal)
            candidate_cost = node_start.cost + node_goal.cost + dist(node_start.position, node_goal.position)
            if candidate_cost < best_cost:
                best_cost = candidate_cost
                best_path = candidate_path
                success = True

        for iteration in range(self.max_iter):
            if time.monotonic() - start_time > TIME_LIMIT:
                break

            rand_sample = None
            # Goal bias 5%
            if random.random() < 0.05:
                rand_sample = goal_pos
            else:
                found_sample = False
                while not found_sample:
                    dim = len(bounds)
                    sample = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                    if not is_colliding(sample):
                        rand_sample = sample
                        found_sample = True

            for tree_a, tree_b in [(start_tree, goal_tree), (goal_tree, start_tree)]:
                nearest_node = nearest(tree_a, rand_sample)
                new_pos = steer(nearest_node.position, rand_sample, self.step_size)

                if is_colliding(new_pos):
                    continue
                if is_edge_colliding(nearest_node.position, new_pos):
                    continue

                new_node = Node(new_pos)
                new_node.parent = nearest_node
                new_node.cost = nearest_node.cost + dist(nearest_node.position, new_pos)
                nearest_node.add_child(new_node)
                tree_a.append(new_node)
                nodes.append(new_node)
                edges.append((nearest_node, new_node))

                # Rewire within fixed radius
                rewire(tree_a, new_node)

                # Try connect trees
                connect_node = try_connect_tree(new_node, tree_b)
                if connect_node:
                    update_best_path(new_node, connect_node)

            if success:
                # Early stop on first solution
                break

        return PlannerResult(success, best_path, start_tree + goal_tree + nodes, edges)
