class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter=5000, step_size=5.0, goal_sample_rate=0.1):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map):
        import time
        import math
        import random

        start_time = time.monotonic()

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            # Euclidean distance
            return math.dist(a, b)

        def sample():
            # Goal biased sampling: with some probability sample the goal
            if random.random() < self.goal_sample_rate:
                return goal_pos
            # Otherwise sample uniformly inside bounds but avoid obstacles via rejection
            for _ in range(100):
                if is_3d:
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                else:
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p
            # Fallback if no free sample found in 100 tries (unlikely)
            return goal_pos

        def steer(from_pos, to_pos):
            vec = tuple(to_pos[d] - from_pos[d] for d in range(dim))
            norm = dist(from_pos, to_pos)
            if norm <= self.step_size:
                new_pos = to_pos
            else:
                scale = self.step_size / norm
                new_pos = tuple(from_pos[d] + vec[d] * scale for d in range(dim))
            if in_bounds(new_pos):
                return new_pos
            # Clip to bounds if numerics cause issues
            clipped = tuple(min(max(new_pos[d], 0), bounds[d]) for d in range(dim))
            return clipped

        def line_cost(a, b):
            return dist(a, b)

        def adaptive_radius(n):
            # Adaptive rewiring radius: γ*(log(n)/n)^(1/d); γ chosen heuristically
            gamma = 50.0  # Tuned constant, can be adjusted
            if n < 2:
                return self.step_size * 5.0
            return min(gamma * (math.log(n)/n) ** (1/dim), self.step_size * 10)

        start_root = Node(start_pos, parent=None, cost=0.0)
        goal_root = Node(goal_pos, parent=None, cost=0.0)

        tree_start = [start_root]
        tree_goal = [goal_root]

        nodes = [start_root, goal_root]
        edges = []

        best_cost = float('inf')
        best_start_node = None
        best_goal_node = None
        success = False

        for i in range(self.max_iter):
            if time.monotonic() - start_time > 30:
                break

            # Alternate between trees: grow_start -> grow_goal -> grow_start etc
            if i % 2 == 0:
                tree_from, tree_to = tree_start, tree_goal
            else:
                tree_from, tree_to = tree_goal, tree_start

            sample_point = sample()
            nearest = min(tree_from, key=lambda n: dist(n.position, sample_point))
            new_pos = steer(nearest.position, sample_point)

            # Validate new_pos node collision and edge collision
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                continue
            if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
                continue

            # Adaptive rewiring radius
            r = adaptive_radius(len(nodes))

            # Find neighbors in tree_from within radius r
            neighbors = [n for n in tree_from if dist(n.position, new_pos) <= r]

            # Choose best parent minimizing cost + distance via collision-free edge
            min_cost = nearest.cost + line_cost(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                cost_through_nbr = nbr.cost + line_cost(nbr.position, new_pos)
                if cost_through_nbr < min_cost:
                    if not self._is_edge_in_obstacle(nbr.position, new_pos, obstacles, is_3d):
                        min_cost = cost_through_nbr
                        best_parent = nbr

            new_node = Node(new_pos)
            new_node.update_parent(best_parent, min_cost)

            tree_from.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors if going through new_node improves cost
            for nbr in neighbors:
                if nbr is best_parent:
                    continue
                cost_through_new = new_node.cost + line_cost(new_node.position, nbr.position)
                if cost_through_new < nbr.cost:
                    if not self._is_edge_in_obstacle(new_node.position, nbr.position, obstacles, is_3d):
                        try:
                            edges.remove((nbr.parent, nbr))
                        except Exception:
                            pass
                        nbr.update_parent(new_node, cost_through_new)
                        edges.append((new_node, nbr))

            # Try connecting the other tree to new_node to check if paths can be connected
            nearest_to = min(tree_to, key=lambda n: dist(n.position, new_node.position))
            while True:
                connect_pos = steer(nearest_to.position, new_node.position)
                # Check collision for edge
                if self._is_in_obstacle(connect_pos, obstacles, is_3d):
                    break
                if self._is_edge_in_obstacle(nearest_to.position, connect_pos, obstacles, is_3d):
                    break
                # Add node to tree_to
                new_to_node = Node(connect_pos)
                new_to_node.update_parent(nearest_to, nearest_to.cost + line_cost(nearest_to.position, connect_pos))
                tree_to.append(new_to_node)
                nodes.append(new_to_node)
                edges.append((nearest_to, new_to_node))

                if dist(connect_pos, new_node.position) <= self.step_size:
                    # Check final edge to new_node
                    if not self._is_edge_in_obstacle(connect_pos, new_node.position, obstacles, is_3d):
                        final_connect = Node(new_node.position)
                        final_connect.update_parent(new_to_node, new_to_node.cost + dist(new_node.position, connect_pos))
                        tree_to.append(final_connect)
                        nodes.append(final_connect)
                        edges.append((new_to_node, final_connect))

                        # Found a path connecting both trees
                        total_cost = final_connect.cost + new_node.cost
                        if total_cost < best_cost:
                            best_cost = total_cost
                            success = True
                            # We return combined path by remembering nodes connecting trees
                            best_start_node = new_node if tree_from is tree_start else final_connect
                            best_goal_node = final_connect if tree_to is tree_goal else new_node
                        break
                    else:
                        break
                nearest_to = new_to_node

            if success:
                break

        extracted_path = []
        if success and best_start_node and best_goal_node:
            path_start = best_start_node.path_from_root()
            path_goal = best_goal_node.path_from_root()
            # path_goal is from goal_root to connection, reverse except overlap connection node
            extracted_path = path_start + path_goal[-2::-1]

            # Perform path shortcutting smoothing
            extracted_path = self._shortcut_path(extracted_path, obstacles, is_3d, bounds)

        return PlannerResult(
            success=success,
            path=extracted_path,
            nodes=nodes,
            edges=edges,
        )

    def _shortcut_path(self, path, obstacles, is_3d, bounds):
        import math
        def can_connect(a, b):
            if a == b:
                return True
            if self._is_edge_in_obstacle(a, b, obstacles, is_3d):
                return False
            return True

        if len(path) < 3:
            return path

        # Iteratively try to shortcut path by connecting farther points directly
        shortened = [path[0]]
        i = 0
        while i < len(path) - 1:
            j = len(path) - 1
            while j > i + 1:
                if can_connect(path[i], path[j]):
                    break
                j -= 1
            shortened.append(path[j])
            i = j
        return shortened

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

