        path: List[Tuple[float, ...]] = []
        if success and best_goal_node is not None:
            cur = best_goal_node
            while cur is not None:
                path.append(cur.position)
                cur = cur.parent
            path.reverse()
        else:
            # fallback: closest node to goal
            min_dist = float('inf')
            best_node = None
            for n in nodes:
                dcur = math.dist(n.position, goal_pos)
                if dcur < min_dist:
                    min_dist = dcur
                    best_node = n
            if best_node is not None:
                cur = best_node
                while cur is not None:
                    path.append(cur.position)
                    cur = cur.parent
                path.reverse()

        # Aggressive shortcutting to smooth path
        def shortcut_path(path_points):
            if len(path_points) < 3:
                return path_points
            shortened = [path_points[0]]
            i = 0
            while i < len(path_points) - 1:
