        return new_path

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def path_from_root(self):
        path, node = [], self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter=10000, step_size=5.0, goal_sample_rate=0.1, gamma_rrt_star=30.0, max_neighbor_radius=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.gamma_rrt_star = gamma_rrt_star
        self.max_neighbor_radius = max_neighbor_radius
        self.time_limit = 30.0
        self.improve_tol = 1e-6
        self.prune_interval = 500  # prune every 500 iterations

    def plan(self, map):
        import time
        start_time = time.monotonic()
        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        nodes = []
        edges = []

        # Initialize trees
        start_root = Node(start_pos, cost=0.0)
        goal_root = Node(goal_pos, cost=0.0)
        tree_start = [start_root]
        tree_goal = [goal_root]
        nodes.extend([start_root, goal_root])

        best_path = []
        best_cost = float('inf')
        success = False
        found_solution = False

        def in_bounds(p):
            return all(0 <= p[i] <= bounds[i] for i in range(dim))

        def is_in_obstacle(p):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d_ = obs
                    px, py, pz = p
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d_:
                        return True
                else:
                    x, y, w_, h_ = obs
                    px, py = p
                    if x <= px <= x + w_ and y <= py <= y + h_:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2, resolution=1.0):
            dist_ = math.dist(p1, p2)
            steps = max(1, int(dist_ / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[d] + (p2[d] - p1[d]) * (i / steps) for d in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def steer(from_pos, to_pos, max_dist):
            dist_ = math.dist(from_pos, to_pos)
            if dist_ <= max_dist:
                return to_pos
            ratio = max_dist / dist_
            return tuple(from_pos[i] + ratio * (to_pos[i] - from_pos[i]) for i in range(dim))

        def uniform_sample():
            while True:
                sample = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not is_in_obstacle(sample):
                    return sample

        def informed_sample(c_best, c_min):
            if not found_solution or c_best == float('inf'):
                return uniform_sample()
            center = tuple((start_pos[i] + goal_pos[i]) * 0.5 for i in range(dim))
            a1 = tuple(goal_pos[i] - start_pos[i] for i in range(dim))
            length_a1 = math.sqrt(sum(x * x for x in a1))
            if length_a1 < 1e-10:
                return uniform_sample()
            e1 = tuple(x / length_a1 for x in a1)
            r1 = c_best * 0.5
            if dim == 2:
                r2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) * 0.5
                axes = (r1, r2)
            elif dim == 3:
                r_side = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) * 0.5
                axes = (r1, r_side, r_side)
            else:
                return uniform_sample()

            attempts = 0
            while attempts < 200:
                x_raw = [random.gauss(0, 1) for _ in range(dim)]
                norm = math.sqrt(sum(x * x for x in x_raw))
                if norm < 1e-10:
                    attempts += 1
                    continue
                x_unit = [x / norm for x in x_raw]
                radius = random.random() ** (1.0 / dim)
                x_scaled = [radius * axes[i] * x_unit[i] for i in range(dim)]

                v = [1.0] + [0.0] * (dim - 1)
                dot = sum(v[i] * e1[i] for i in range(dim))

                if abs(dot - 1.0) < 1e-10:
                    x_rot = x_scaled
                elif abs(dot + 1.0) < 1e-10:
                    x_rot = [-x_scaled[0]] + x_scaled[1:]
                else:
                    if dim == 2:
                        angle = math.acos(dot)
                        cos_a = math.cos(angle)
                        sin_a = math.sin(angle)
                        x0, x1 = x_scaled[0], x_scaled[1]
                        x_rot = (x0 * cos_a - x1 * sin_a, x0 * sin_a + x1 * cos_a)
                    elif dim == 3:
                        k = [v[1] * e1[2] - v[2] * e1[1],
                             v[2] * e1[0] - v[0] * e1[2],
                             v[0] * e1[1] - v[1] * e1[0]]
                        k_norm = math.sqrt(sum(c * c for c in k))
                        if k_norm < 1e-10:
                            x_rot = x_scaled
                        else:
                            k = [c / k_norm for c in k]
                            angle = math.acos(dot)
                            cos_a = math.cos(angle)
                            sin_a = math.sin(angle)
                            v_vec = x_scaled
                            k_dot_v = sum(k[i] * v_vec[i] for i in range(dim))
                            cross = [k[1] * v_vec[2] - k[2] * v_vec[1],
                                     k[2] * v_vec[0] - k[0] * v_vec[2],
                                     k[0] * v_vec[1] - k[1] * v_vec[0]]
                            x_rot = [0] * 3
                            for i in range(3):
                                x_rot[i] = v_vec[i] * cos_a + cross[i] * sin_a + k[i] * k_dot_v * (1 - cos_a)
                    else:
                        x_rot = x_scaled

                sample = tuple(center[i] + x_rot[i] for i in range(dim))
                if all(0 <= sample[i] <= bounds[i] for i in range(dim)):
                    if not is_in_obstacle(sample):
                        return sample
                attempts += 1
            return uniform_sample()

        def nearest(nodes_list, pt):
            return min(nodes_list, key=lambda n: math.dist(n.position, pt))

        def near_nodes(nodes_list, pt, radius):
            return [n for n in nodes_list if math.dist(n.position, pt) <= radius]

        def choose_parent_and_add(tree_nodes, new_pos):
            nearest_node = nearest(tree_nodes, new_pos)
            base_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            gamma = self.gamma_rrt_star
            n = len(tree_nodes)
            radius = min(self.max_neighbor_radius,
                         max(self.step_size * 2.0, gamma * ((math.log(n) / n) ** (1 / dim))))
            nearlist = near_nodes(tree_nodes, new_pos, radius)
            best_parent = nearest_node
            best_cost = base_cost
            for nb in nearlist:
                if nb is nearest_node:
                    continue
                if not is_edge_in_obstacle(nb.position, new_pos):
                    cost_candidate = nb.cost + math.dist(nb.position, new_pos)
                    if cost_candidate + self.improve_tol < best_cost:
                        best_parent = nb
                        best_cost = cost_candidate
            if is_edge_in_obstacle(best_parent.position, new_pos):
                return None
            new_node = Node(new_pos)
            new_node.cost = best_cost
            best_parent.add_child(new_node)
            tree_nodes.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))
            rewire(new_node, nearlist, tree_nodes)
            return new_node

        def rewire(new_node, neighbors, tree_nodes):
            for nb in neighbors:
                if nb is new_node:
                    continue
                if not is_edge_in_obstacle(new_node.position, nb.position):
                    alt_cost = new_node.cost + math.dist(new_node.position, nb.position)
                    if alt_cost + self.improve_tol < nb.cost:
                        if nb.parent:
                            try:
                                edges.remove((nb.parent, nb))
                            except ValueError:
                                pass
                            nb.parent.remove_child(nb)
                        new_node.add_child(nb)
                        nb.cost = alt_cost
                        edges.append((new_node, nb))

        def merge_trees(node, tree_other):
            nearest_node_other = nearest(tree_other, node.position)
            dist_ = math.dist(node.position, nearest_node_other.position)
            if dist_ > self.step_size:
                return None
            if is_edge_in_obstacle(node.position, nearest_node_other.position):
                return None
            path_start = node.path_from_root()
            path_goal = nearest_node_other.path_from_root()
            # Remove duplicated meeting point
            if path_start[-1] == path_goal[-1]:
                path_goal = path_goal[:-1]
            full_path = path_start + path_goal[::-1]
            total_cost = node.cost + dist_ + nearest_node_other.cost
            return full_path, total_cost

        def shortcut_path(path_points):
            if len(path_points) < 3:
                return path_points
            shortened = [path_points[0]]
            i = 0
            while i < len(path_points) - 1:
                j = len(path_points) - 1
                while j > i + 1:
                    if not is_edge_in_obstacle(path_points[i], path_points[j]):
                        shortened.append(path_points[j])
                        i = j
                        break
                    j -= 1
                else:
                    i += 1
                    if i < len(path_points):
                        shortened.append(path_points[i])
            return shortened

        def prune_invalid_nodes(tree_nodes):
            valid_nodes = []
            for node in tree_nodes:
                if not node.valid or node.parent is None and node is not start_root and node is not goal_root:
                    # Disconnect node children recursively
                    def invalidate_subtree(nd):
                        nd.valid = False
                        for c in nd.children[:]:
                            invalidate_subtree(c)
                        nd.children = []
                    invalidate_subtree(node)
                else:
                    valid_nodes.append(node)
            return valid_nodes

        for it in range(self.max_iter):
            curr_time = time.monotonic()
            if curr_time - start_time > self.time_limit:
                break

            # Sampling
            c_best = best_cost if found_solution else float('inf')
            c_min = math.dist(start_pos, goal_pos)
            sample = uniform_sample() if not found_solution else informed_sample(c_best, c_min)

            # Goal bias for faster convergence
            if random.random() < self.goal_sample_rate:
                sample = goal_pos

            # Alternate tree expansion
            tree_from, tree_to = (tree_start, tree_goal) if it % 2 == 0 else (tree_goal, tree_start)

            # Extend tree_from toward sample
            nearest_node = nearest(tree_from, sample)
            new_pos = steer(nearest_node.position, sample, self.step_size)
            if is_in_obstacle(new_pos):
                continue
            if is_edge_in_obstacle(nearest_node.position, new_pos):
                continue

            new_node = choose_parent_and_add(tree_from, new_pos)
            if new_node is None:
                continue

            merge_result = merge_trees(new_node, tree_to)
            if merge_result is not None:
                path_candidate, cost_candidate = merge_result
                if cost_candidate + self.improve_tol < best_cost:
                    best_path = path_candidate
                    best_cost = cost_candidate
                    success = True
                    found_solution = True
                    # Early stop upon first connection
                    break

            # Periodic pruning every prune_interval iterations
            if it > 0 and it % self.prune_interval == 0:
                tree_start = prune_invalid_nodes(tree_start)
                tree_goal = prune_invalid_nodes(tree_goal)
                # Also prune from nodes list:
                nodes = [n for n in nodes if n.valid]

        if not success and tree_start:
            # Try direct connection from closest start tree node to goal if feasible
            nearest_goal_node = nearest(tree_start, goal_pos)
            if nearest_goal_node and not is_edge_in_obstacle(nearest_goal_node.position, goal_pos):
                candidate_path = nearest_goal_node.path_from_root() + [goal_pos]
                candidate_cost = nearest_goal_node.cost + math.dist(nearest_goal_node.position, goal_pos)
                if candidate_cost + self.improve_tol < best_cost:
                    best_path = candidate_path
                    best_cost = candidate_cost
                    success = False

        if best_path:
            best_path = shortcut_path(best_path)

        return PlannerResult(
            success=success,
            path=best_path,
            nodes=nodes,
            edges=edges
        )

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position    # tuple (x,y) or (x,y,z)
        self.parent = parent        # Node or None
        self.cost = cost            # path cost from root
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)

    def path_from_root(self):
        node = self
        path = []
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self,
                 max_iter=5000,
                 step_size=5.0,
                 goal_sample_rate=0.1,
                 max_no_improve=150,
                 improve_tol=1e-6,
                 time_limit_sec=30.0,
                 gamma_rrt_star=50.0,
                 hash_cell_size=10.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.max_no_improve = max_no_improve
        self.improve_tol = improve_tol
        self.time_limit_sec = time_limit_sec
        self.gamma_rrt_star = gamma_rrt_star
        self.hash_cell_size = hash_cell_size  # cell size for spatial hash

    def plan(self, map):
        import time, random, math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def dist(a, b):
            return math.dist(a, b)

        def steer(fr, to):
            d = dist(fr, to)
            if d <= self.step_size:
                return to
            r = self.step_size / d
            return tuple(fr[i] + (to[i] - fr[i]) * r for i in range(dim))

        def within_bounds(p):
            return all(0.0 <= p[i] <= bounds[i] for i in range(dim))

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w_, h_ = obs
                    px, py = pos
                    if x <= px <= x + w_ and y <= py <= y + h_:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2, resolution=1.0):
            length = dist(p1, p2)
            steps = max(1, int(length / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def uniform_sample():
            tries = 0
            while True:
                s = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not is_in_obstacle(s):
                    return s
                tries += 1
                if tries > 1000:
                    return start_pos

        def informed_sample(start, goal, c_best, c_min):
            if c_best == float('inf') or c_min <= 1e-12:
                return uniform_sample()
            center = tuple((s + g) * 0.5 for s, g in zip(start, goal))
            a1 = tuple((g - s) / c_min for s, g in zip(start, goal))

            if dim == 2:
                a2 = (-a1[1], a1[0])
                basis = (a1, a2)
                r1 = c_best / 2.0
                r2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) / 2.0
                radii = (r1, r2)
            else:
                def cross(u, v):
                    return (u[1]*v[2]-u[2]*v[1], u[2]*v[0]-u[0]*v[2], u[0]*v[1]-u[1]*v[0])
                def norm(v):
                    return math.sqrt(sum(x*x for x in v))
                def normalize(v):
                    n = norm(v)
                    if n < 1e-12:
                        return v
                    return tuple(x / n for x in v)

                ref = (1.0, 0.0, 0.0) if abs(a1[0]) < 0.9 else (0.0, 1.0, 0.0)
                b2 = normalize(cross(a1, ref))
                if norm(b2) < 1e-12:
                    ref = (0.0, 0.0, 1.0)
                    b2 = normalize(cross(a1, ref))
                b3 = cross(a1, b2)
                basis = (a1, b2, b3)
                r1 = c_best / 2.0
                r_other = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) / 2.0
                radii = (r1, r_other, r_other)

            def sample_unit_ball(d):
                while True:
                    v = [random.gauss(0, 1) for _ in range(d)]
                    norm_ = math.sqrt(sum(x*x for x in v))
                    if norm_ > 1e-12:
                        v = [x / norm_ for x in v]
                        r = random.random() ** (1.0 / d)
                        return [x * r for x in v]

            u = sample_unit_ball(dim)
            mapped = [center[i] for i in range(dim)]
            for i in range(dim):
                for j in range(dim):
                    mapped[i] += basis[j][i] * radii[j] * u[j]
            mapped = tuple(min(max(mapped[i], 0.0), bounds[i]) for i in range(dim))
            if is_in_obstacle(mapped):
                return uniform_sample()
            return mapped

        def neighbor_radius(n_nodes):
            if n_nodes < 2:
                return max(2.0, self.step_size * 2.0)
            val = self.gamma_rrt_star * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            max_r = max(bounds) * 0.25
            min_r = max(2.0, self.step_size)
            return max(min_r, min(max_r, val))

        # Spatial hashing for neighbor queries
        def hash_coords(pos):
            return tuple(int(p // self.hash_cell_size) for p in pos)

        def cells_around(pos, radius):
            center_cell = hash_coords(pos)
            cell_range = int(math.ceil(radius / self.hash_cell_size))
            ranges = [range(c - cell_range, c + cell_range + 1) for c in center_cell]
            # Cartesian product of ranges
            if dim == 2:
                for x in ranges[0]:
                    for y in ranges[1]:
                        yield (x, y)
            else:
                for x in ranges[0]:
                    for y in ranges[1]:
                        for z in ranges[2]:
                            yield (x, y, z)

        def insert_node_hash(node, hash_map):
            c = hash_coords(node.position)
            if c not in hash_map:
                hash_map[c] = []
            hash_map[c].append(node)

        def get_neighbors_hash(pos, radius, hash_map):
            neighbors = []
            r_sq = radius * radius
            for c in cells_around(pos, radius):
                if c in hash_map:
                    for n in hash_map[c]:
                        if dist(n.position, pos) <= radius:
                            neighbors.append(n)
            return neighbors

        def nearest_node_hash(nodes_list, pos):
            min_node = None
            min_d = float('inf')
            for n in nodes_list:
                d_ = dist(n.position, pos)
                if d_ < min_d:
                    min_d = d_
                    min_node = n
            return min_node

        def add_node_rrt_star(tree, pos, all_nodes, edges_list, hash_map):
            r = neighbor_radius(len(tree))
            neighbors = get_neighbors_hash(pos, r, hash_map)
            candidate_parents = [n for n in neighbors if not is_edge_in_obstacle(n.position, pos)]
            if not candidate_parents:
                # fallback nearest
                fallback = nearest_node_hash(tree, pos)
                if fallback is None or is_edge_in_obstacle(fallback.position, pos):
                    return None, []
                candidate_parents = [fallback]
            best_parent = min(candidate_parents, key=lambda n: n.cost + dist(n.position, pos))
            new_cost = best_parent.cost + dist(best_parent.position, pos)
            new_node = Node(pos, parent=None, cost=new_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            all_nodes.append(new_node)
            edges_list.append((best_parent, new_node))
            insert_node_hash(new_node, hash_map)
            return new_node, neighbors

        def propagate_cost_to_children(node):
            for child in node.children:
                old_cost = child.cost
                child.cost = node.cost + dist(node.position, child.position)
                if child.cost + 1e-12 < old_cost:
                    propagate_cost_to_children(child)

        def rewire(tree, new_node, neighbors, edges_list, hash_map):
            for nb in neighbors:
                if nb is new_node:
                    continue
                if is_edge_in_obstacle(new_node.position, nb.position):
                    continue
                new_cost = new_node.cost + dist(new_node.position, nb.position)
                if new_cost + 1e-12 < nb.cost:
                    if nb.parent is not None:
                        nb.parent.remove_child(nb)
                        try:
                            edges_list.remove((nb.parent, nb))
                        except ValueError:
                            pass
                    new_node.add_child(nb)
                    nb.cost = new_cost
                    edges_list.append((new_node, nb))
                    propagate_cost_to_children(nb)

        def can_connect(n1, n2):
            if not n1.valid or not n2.valid:
                return False
            if is_edge_in_obstacle(n1.position, n2.position):
                return False
            return True

        def stitch_path(n_start, n_goal, forward_start=True):
            path_start = n_start.path_from_root()
            path_goal = n_goal.path_from_root()
            if not path_start or not path_goal:
                return []
            # Remove duplicate connection node if identical
            if path_start[-1] == path_goal[-1]:
                merged = path_start + path_goal[-2::-1]
            else:
                merged = path_start + path_goal[::-1]
            return merged if forward_start else merged[::-1]

        def shortcut_path(path):
            if len(path) < 3:
                return path
            # Iterative shortcutting, 30 passes max
            for _ in range(30):
                shortened = [path[0]]
                i = 0
                changed = False
                while i < len(path) - 1:
                    j = len(path) - 1
                    while j > i + 1:
                        if not is_edge_in_obstacle(path[i], path[j]):
                            break
                        j -= 1
                    if j != i + 1:
                        changed = True
                    shortened.append(path[j])
                    i = j
                path = shortened
                if not changed:
                    break
            return path

        start_time = time.monotonic()

        tree_start = [Node(start_pos, cost=0.0)]
        tree_goal = [Node(goal_pos, cost=0.0)]
        nodes = [tree_start[0], tree_goal[0]]
        edges = []

        # Initialize spatial hash maps for fast neighbor lookup
        hash_map_start = {}
        hash_map_goal = {}
        insert_node_hash(tree_start[0], hash_map_start)
        insert_node_hash(tree_goal[0], hash_map_goal)

        best_path = []
        best_cost = float('inf')
        first_solution_found = False
        iter_since_improve = 0
        c_min = dist(start_pos, goal_pos)

        for it in range(self.max_iter):
            elapsed = time.monotonic() - start_time
            if elapsed > self.time_limit_sec:
                break

            forward_expand = (it % 2 == 0)
            curr_tree = tree_start if forward_expand else tree_goal
            other_tree = tree_goal if forward_expand else tree_start
            curr_hash = hash_map_start if forward_expand else hash_map_goal
            other_hash = hash_map_goal if forward_expand else hash_map_start

            # Sample with goal bias and informed sampling after first solution
            if first_solution_found:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if forward_expand else start_pos
                else:
                    sample = informed_sample(start_pos, goal_pos, best_cost, c_min)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if forward_expand else start_pos
                else:
                    sample = uniform_sample()

            nearest_node = None
            # Nearest in current tree using hash: fallback loop as no direct nearest indexing
            # Optimized by checking nodes in neighbor cells within radius step_size*2
            candidates = get_neighbors_hash(sample, self.step_size * 2.0, curr_hash)
            if candidates:
                nearest_node = min(candidates, key=lambda n: dist(n.position, sample))
            else:
                # fallback to full tree linear search
                nearest_node = min(curr_tree, key=lambda n: dist(n.position, sample))

            new_pos = steer(nearest_node.position, sample)
            if (not within_bounds(new_pos)) or is_in_obstacle(new_pos) or is_edge_in_obstacle(nearest_node.position, new_pos):
                iter_since_improve += 1
                if first_solution_found and iter_since_improve >= self.max_no_improve:
                    break
                continue

            new_node, neighbors = add_node_rrt_star(curr_tree, new_pos, nodes, edges, curr_hash)
            if new_node is None:
                iter_since_improve += 1
                if first_solution_found and iter_since_improve >= self.max_no_improve:
                    break
                continue

            rewire(curr_tree, new_node, neighbors, edges, curr_hash)

            # Attempt to connect trees greedily with incremental steps
            nearest_other = None
            # Nearest node in the other tree within radius
            candidates_oth = get_neighbors_hash(new_node.position, self.step_size * 2.0, other_hash)
            if candidates_oth:
                nearest_other = min(candidates_oth, key=lambda n: dist(n.position, new_node.position))
            else:
                nearest_other = min(other_tree, key=lambda n: dist(n.position, new_node.position))

            connected_other = None
            if dist(nearest_other.position, new_node.position) <= self.step_size and can_connect(nearest_other, new_node):
                # Direct connection possible
                final_node = Node(new_node.position, cost=nearest_other.cost + dist(nearest_other.position, new_node.position))
                nearest_other.add_child(final_node)
                other_tree.append(final_node)
                nodes.append(final_node)
                edges.append((nearest_other, final_node))
                insert_node_hash(final_node, other_hash)
                connected_other = final_node
            else:
                current = nearest_other
                tries = 0
                max_steps = 20
                while tries < max_steps:
                    tries += 1
                    step_pos = steer(current.position, new_node.position)
                    if (not within_bounds(step_pos)) or is_in_obstacle(step_pos) or is_edge_in_obstacle(current.position, step_pos):
                        break
                    new_other_node, neighbors_other = add_node_rrt_star(other_tree, step_pos, nodes, edges, other_hash)
                    if new_other_node is None:
                        break
                    rewire(other_tree, new_other_node, neighbors_other, edges, other_hash)
                    current = new_other_node
                    if dist(current.position, new_node.position) <= self.step_size:
                        if can_connect(current, new_node):
                            final_node = Node(new_node.position, cost=current.cost + dist(current.position, new_node.position))
                            current.add_child(final_node)
                            other_tree.append(final_node)
                            nodes.append(final_node)
                            edges.append((current, final_node))
                            insert_node_hash(final_node, other_hash)
                            connected_other = final_node
                        break

            if connected_other is not None:
                candidate_path = stitch_path(new_node, connected_other, forward_expand)
                candidate_cost = 0.0
                for i in range(len(candidate_path) - 1):
                    candidate_cost += dist(candidate_path[i], candidate_path[i + 1])
                if candidate_cost + self.improve_tol < best_cost:
                    best_cost = candidate_cost
                    best_path = candidate_path
                    first_solution_found = True
                    iter_since_improve = 0
                    # Early stop on first better path (speed optimization)
                    break
                else:
                    iter_since_improve += 1
            else:
                iter_since_improve += 1

            if first_solution_found and iter_since_improve >= self.max_no_improve:
                break

        # Perform iterative shortcutting to smooth and shorten path
        if best_path:
            best_path = shortcut_path(best_path)
            success_state = True
        else:
            success_state = False

        return PlannerResult(
            success=success_state,
            path=best_path,
            nodes=nodes,
            edges=edges
        )

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children: List[Node] = []
        self.valid = True

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = 0.05
        self.time_limit_sec = 30.0
        self.gamma_rrt_star = 40.0
        self.improve_tol = 1e-6

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        dim = len(bounds)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = dim == 3

        def dist(a, b):
            return math.dist(a, b)

        def steer(fr, to):
            d = dist(fr, to)
            if d <= self.step_size:
                return to
            r = self.step_size / d
            return tuple(fr[i] + (to[i] - fr[i]) * r for i in range(dim))

        def within_bounds(p):
            return all(0.0 <= p[i] <= bounds[i] for i in range(dim))

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w_, h_ = obs
                    px, py = pos
                    if x <= px <= x + w_ and y <= py <= y + h_:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2, resolution=1.0):
            length = dist(p1, p2)
            steps = max(1, int(length / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def uniform_sample():
            trials = 0
            while True:
                s = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not is_in_obstacle(s):
                    return s
                trials += 1
                if trials > 500:
                    return start_pos  # fallback

        def informed_sample(start, goal, c_best, c_min):
            if c_best == float('inf') or c_min <= 1e-12:
                return uniform_sample()
            center = tuple((s + g) * 0.5 for s, g in zip(start, goal))
            a1 = tuple((g - s) / c_min for s, g in zip(start, goal))
            if dim == 2:
                a2 = (-a1[1], a1[0])
                basis = [a1, a2]
                r1 = c_best / 2.0
                r2 = math.sqrt(max(c_best**2 - c_min**2, 0.0)) / 2.0
                radii = [r1, r2]
            else:
                ref = (1.0, 0.0, 0.0) if abs(a1[0]) < 0.9 else (0.0, 1.0, 0.0)
                b2 = self._normalize(self._cross(a1, ref))
                if self._norm(b2) < 1e-12:
                    ref = (0.0, 0.0, 1.0)
                    b2 = self._normalize(self._cross(a1, ref))
                b3 = self._cross(a1, b2)
                basis = [a1, b2, b3]
                r1 = c_best / 2.0
                r_other = math.sqrt(max(c_best ** 2 - c_min ** 2, 0.0)) / 2.0
                radii = [r1, r_other, r_other]

            def sample_unit_ball(dim_):
                while True:
                    v = [random.gauss(0, 1) for _ in range(dim_)]
                    norm_ = math.sqrt(sum(x * x for x in v))
                    if norm_ > 1e-12:
                        v = [x / norm_ for x in v]
                        r = random.random() ** (1.0 / dim_)
                        return [x * r for x in v]

            u = sample_unit_ball(dim)
            mapped = [center[i] for i in range(dim)]
            for i in range(dim):
                for j in range(dim):
                    mapped[i] += basis[j][i] * radii[j] * u[j]
            mapped_clamped = tuple(min(max(mapped[i], 0.0), bounds[i]) for i in range(dim))
            if is_in_obstacle(mapped_clamped):
                return uniform_sample()
            return mapped_clamped

        def nearest(nodes_list, p):
            min_node = None
            min_d = float('inf')
            for n in nodes_list:
                d_ = dist(n.position, p)
                if d_ < min_d:
                    min_d = d_
                    min_node = n
            return min_node

        def neighbor_radius(n_nodes):
            if n_nodes < 2:
                return max(2.0, self.step_size * 2.0)
            val = self.gamma_rrt_star * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            max_r = max(bounds) * 0.25
            min_r = max(2.0, self.step_size)
            return max(min_r, min(max_r, val))

        def near_nodes(nodes_list, p, radius):
            radius_sq = radius * radius
            result = []
            for n in nodes_list:
                d_ = dist(n.position, p)
                if d_ <= radius:
                    result.append(n)
            return result

        def add_node_rrt_star(tree, pos, nodes_all, edges_list):
            r = neighbor_radius(len(tree))
            neighbors = near_nodes(tree, pos, r)
            parents = [n for n in neighbors if not is_edge_in_obstacle(n.position, pos)]
            if not parents:
                p = nearest(tree, pos)
                if is_edge_in_obstacle(p.position, pos):
                    return None, []
                parents = [p]
            best_parent = min(parents, key=lambda n: n.cost + dist(n.position, pos))
            new_cost = best_parent.cost + dist(best_parent.position, pos)
            new_node = Node(pos, parent=None, cost=new_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            nodes_all.append(new_node)
            edges_list.append((best_parent, new_node))
            return new_node, neighbors

        def propagate_cost_to_children(node):
            for child in node.children:
                old_cost = child.cost
                child.cost = node.cost + dist(node.position, child.position)
                if child.cost + 1e-12 < old_cost:
                    propagate_cost_to_children(child)

        def rewire(tree, new_node, neighbors, edges_list):
            for nb in neighbors:
                if nb is new_node:
                    continue
                if is_edge_in_obstacle(new_node.position, nb.position):
                    continue
                new_cost = new_node.cost + dist(new_node.position, nb.position)
                if new_cost + 1e-12 < nb.cost:
                    if nb.parent:
                        nb.parent.remove_child(nb)
                        try:
                            edges_list.remove((nb.parent, nb))
                        except ValueError:
                            pass
                    new_node.add_child(nb)
                    nb.cost = new_cost
                    edges_list.append((new_node, nb))
                    propagate_cost_to_children(nb)

        def can_connect(n1, n2):
            return not is_edge_in_obstacle(n1.position, n2.position) and n1.valid and n2.valid

        def stitch_path(n_start, n_goal, a_is_start):
            path_a = n_start.path_from_root()
            path_b = n_goal.path_from_root()
            if path_a and path_b and path_a[-1] == path_b[-1]:
                merged = path_a + path_b[-2::-1]
            else:
                merged = path_a + path_b[::-1]
            return merged if a_is_start else merged[::-1]

        def shortcut_path(path):
            if len(path) < 3:
                return path
            smoothed = [path[0]]
            i = 0
            while i < len(path) - 1:
                j = len(path) - 1
                while j > i + 1:
                    if not is_edge_in_obstacle(path[i], path[j]):
                        break
                    j -= 1
                smoothed.append(path[j])
                i = j
            return smoothed

        start_time = time.monotonic()
        tree_start = [Node(start_pos, cost=0.0)]
        tree_goal = [Node(goal_pos, cost=0.0)]
        nodes = [tree_start[0], tree_goal[0]]
        edges = []

        best_path = []
        best_cost = float('inf')
        found_first = False

        c_min = dist(start_pos, goal_pos)

        for it in range(self.max_iter):
            current_time = time.monotonic()
            if current_time - start_time > self.time_limit_sec:
                break

            expand_start_tree = (it % 2 == 0)
            curr_tree = tree_start if expand_start_tree else tree_goal
            other_tree = tree_goal if expand_start_tree else tree_start
            a_is_start_side = expand_start_tree

            # Sample
            if found_first:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if a_is_start_side else start_pos
                else:
                    sample = informed_sample(start_pos, goal_pos, best_cost, c_min)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if a_is_start_side else start_pos
                else:
                    sample = uniform_sample()

            nearest_node_curr = nearest(curr_tree, sample)
            new_pos = steer(nearest_node_curr.position, sample)

            if not within_bounds(new_pos):
                continue
            if is_in_obstacle(new_pos):
                continue
            if is_edge_in_obstacle(nearest_node_curr.position, new_pos):
                continue

            new_node, neighbors = add_node_rrt_star(curr_tree, new_pos, nodes, edges)
            if new_node is None:
                continue
            rewire(curr_tree, new_node, neighbors, edges)

            # Attempt connection to other tree
            nearest_other = nearest(other_tree, new_node.position)
            if dist(nearest_other.position, new_node.position) <= self.step_size and can_connect(nearest_other, new_node):
                final_node = Node(new_node.position, parent=None,
                                  cost=nearest_other.cost + dist(nearest_other.position, new_node.position))
                nearest_other.add_child(final_node)
                other_tree.append(final_node)
                nodes.append(final_node)
                edges.append((nearest_other, final_node))
                connected_other = final_node
            else:
                connected_other = None

            if connected_other is not None:
                candidate_path = stitch_path(new_node, connected_other, a_is_start_side)
                cost_candidate = 0.0
                for idx in range(len(candidate_path) - 1):
                    cost_candidate += dist(candidate_path[idx], candidate_path[idx + 1])
                if cost_candidate + self.improve_tol < best_cost:
                    best_cost = cost_candidate
                    best_path = candidate_path
                    found_first = True
                    # Early termination on first valid path
                    break

        if best_path:
            best_path = shortcut_path(best_path)
            return PlannerResult(success=True, path=best_path, nodes=nodes, edges=edges)
        else:
            # No path found: return best partial from start tree
            best_partial_node = None
            best_dist = float('inf')
            for node in tree_start:
                d = dist(node.position, goal_pos)
                if d < best_dist and node.valid:
                    best_dist = d
                    best_partial_node = node
            if best_partial_node:
                partial_path = best_partial_node.path_from_root()
                return PlannerResult(success=False, path=partial_path, nodes=nodes, edges=edges)
            return PlannerResult(success=False, path=[], nodes=nodes, edges=edges)

    def _cross(self, a, b):
        ax, ay, az = a
        bx, by, bz = b
        return (ay * bz - az * by, az * bx - ax * bz, ax * by - ay * bx)

    def _norm(self, v):
        return math.sqrt(sum(x * x for x in v))

    def _normalize(self, v):
        n = self._norm(v)
        if n < 1e-12:
            return v
        return tuple(x / n for x in v)

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not new_parent:
            if self.parent is not None:
                self.parent.remove_child(self)
            self.parent = new_parent
            self.cost = new_cost
            if new_parent is not None:
                new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]

    def propagate_cost_to_children(self):
        for c in self.children:
            new_cost = self.cost + math.dist(self.position, c.position)
            if new_cost + 1e-10 < c.cost:
                c.cost = new_cost
                c.propagate_cost_to_children()
class Planner:
    def __init__(self, max_iter=5000, step_size=5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = 0.1
        self.time_limit = 30.0
        self.gamma_rrt_star = 35.0
        self.max_neighbor_radius = 30.0
        self.improve_tol = 1e-10
        self.shortcut_passes = 5
        self.max_shortcut_attempts_per_pass = 200

    def plan(self, map):
        import time
        import math
        import random

        start_time = time.monotonic()
        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        def dist(a, b):
            return math.dist(a, b)

        def in_bounds(p):
            return all(0.0 <= p[i] <= bounds[i] for i in range(dim))

        def is_in_obstacle(p):
            for obs in obstacles:
                if is_3d:
                    x,y,z,w,h,d = obs
                    px,py,pz = p
                    if x <= px <= x+w and y <= py <= y+h and z <= pz <= z+d:
                        return True
                else:
                    x,y,w,h = obs
                    px,py = p
                    if x <= px <= x+w and y <= py <= y+h:
                        return True
            return False

        def is_edge_in_obstacle(a,b, resolution=None):
            dist_ab = dist(a,b)
            res = resolution
            if res is None:
                res = min(1.0, self.step_size * 0.5)
            steps = max(1,int(dist_ab/res))
            for i in range(steps+1):
                interp = tuple(a[j] + (b[j]-a[j])*(i/steps) for j in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def steer(from_p, to_p, max_step):
            d = dist(from_p, to_p)
            if d <= max_step:
                return to_p
            ratio = max_step / d
            return tuple(from_p[i] + ratio * (to_p[i] - from_p[i]) for i in range(dim))

        def uniform_sample():
            attempts = 0
            while attempts < 100:
                sample = tuple(random.uniform(0,bounds[i]) for i in range(dim))
                if not is_in_obstacle(sample):
                    return sample
                attempts += 1
            # Fallback uniform without checking
            return tuple(random.uniform(0,bounds[i]) for i in range(dim))

        def informed_sample(c_best):
            c_min = dist(start_pos, goal_pos)
            if c_best == float('inf') or c_best < c_min + 1e-12:
                return uniform_sample()

            center = tuple((start_pos[i]+goal_pos[i])*0.5 for i in range(dim))
            a1 = tuple(goal_pos[i]-start_pos[i] for i in range(dim))
            length_a1 = math.sqrt(sum(x*x for x in a1))
            if length_a1 < 1e-12:
                return uniform_sample()
            e1 = tuple(x/length_a1 for x in a1)
            r1 = c_best/2.0
            r2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0.0))/2.0

            attempts = 0
            while attempts < 100:
                # Sample unit ball
                x_raw = [random.gauss(0,1) for _ in range(dim)]
                norm_x = math.sqrt(sum(x*x for x in x_raw))
                if norm_x < 1e-12:
                    attempts += 1
                    continue
                x_unit = [xi / norm_x for xi in x_raw]
                radius = random.random() ** (1.0 / dim)

                if dim == 2:
                    coords = [radius*r1*x_unit[0], radius*r2*x_unit[1]]
                    cos_t, sin_t = e1[0], e1[1]
                    rot_x = cos_t*coords[0] - sin_t*coords[1]
                    rot_y = sin_t*coords[0] + cos_t*coords[1]
                    sample = (center[0] + rot_x, center[1] + rot_y)
                elif dim == 3:
                    coords = [radius*r1*x_unit[0], radius*r2*x_unit[1], radius*r2*x_unit[2]]
                    v = (1.0, 0.0, 0.0)
                    dot = sum(v[i]*e1[i] for i in range(3))
                    if abs(dot-1.0) < 1e-9:
                        rot = coords
                    elif abs(dot+1.0) < 1e-9:
                        rot = (-coords[0], coords[1], coords[2])
                    else:
                        k = (v[1]*e1[2]-v[2]*e1[1],
                             v[2]*e1[0]-v[0]*e1[2],
                             v[0]*e1[1]-v[1]*e1[0])
                        k_norm = math.sqrt(sum(x*x for x in k))
                        k = tuple(x/k_norm for x in k)
                        angle = math.acos(dot)
                        cos_a = math.cos(angle)
                        sin_a = math.sin(angle)
                        v_vec = coords
                        k_dot_v = sum(k[i]*v_vec[i] for i in range(3))
                        cross = (k[1]*v_vec[2]-k[2]*v_vec[1],
                                 k[2]*v_vec[0]-k[0]*v_vec[2],
                                 k[0]*v_vec[1]-k[1]*v_vec[0])
                        rot = tuple(v_vec[i]*cos_a + cross[i]*sin_a + k[i]*k_dot_v*(1-cos_a) for i in range(3))
                    sample = tuple(center[i]+rot[i] for i in range(3))
                else:
                    sample = uniform_sample()

                if in_bounds(sample) and not is_in_obstacle(sample):
                    return sample
                attempts += 1
            return uniform_sample()

        def nearest_node(tree_nodes, pt):
            best = tree_nodes[0]
            best_dist = dist(best.position, pt)
            for node in tree_nodes[1:]:
                d = dist(node.position, pt)
                if d < best_dist:
                    best = node
                    best_dist = d
            return best

        def adaptive_neighbor_radius(n_nodes):
            if n_nodes < 2:
                return self.max_neighbor_radius
            return min(
                self.max_neighbor_radius,
                max(self.step_size * 2.0,
                    self.gamma_rrt_star * ((math.log(n_nodes) / n_nodes) ** (1.0/dim)))
            )

        def near_nodes(tree_nodes, pt, radius):
            return [node for node in tree_nodes if dist(node.position, pt) <= radius]

        def choose_parent_and_add(tree_nodes, new_pos, nodes_list, edges_list):
            n_nodes = len(tree_nodes)
            radius = adaptive_neighbor_radius(n_nodes)
            near_n = near_nodes(tree_nodes, new_pos, radius)
            nearest_n = nearest_node(tree_nodes, new_pos)
            min_cost = nearest_n.cost + dist(nearest_n.position, new_pos)
            best_parent = nearest_n
            for nbr in near_n:
                if nbr == nearest_n:
                    continue
                if nbr.cost + dist(nbr.position, new_pos) + self.improve_tol < min_cost:
                    if not is_edge_in_obstacle(nbr.position, new_pos):
                        min_cost = nbr.cost + dist(nbr.position, new_pos)
                        best_parent = nbr
            if not is_edge_in_obstacle(best_parent.position, new_pos):
                new_node = Node(new_pos, best_parent, min_cost)
                best_parent.add_child(new_node)
                nodes_list.append(new_node)
                tree_nodes.append(new_node)
                edges_list.append((best_parent, new_node))
                rewire(new_node, near_n, nodes_list, edges_list)
                return new_node
            return None

        def rewire(pivot, neighbors, nodes_list, edges_list):
            for nbr in neighbors:
                if nbr is pivot:
                    continue
                candidate_cost = pivot.cost + dist(pivot.position, nbr.position)
                if candidate_cost + self.improve_tol < nbr.cost:
                    if not is_edge_in_obstacle(pivot.position, nbr.position):
                        if nbr.parent is not None:
                            try:
                                edges_list.remove((nbr.parent, nbr))
                            except ValueError:
                                pass
                            nbr.parent.remove_child(nbr)
                        nbr.update_parent(pivot, candidate_cost)
                        edges_list.append((pivot, nbr))
                        nbr.propagate_cost_to_children()

        def merge_trees(node_in_a, tree_b):
            nearest_b = nearest_node(tree_b, node_in_a.position)
            d = dist(node_in_a.position, nearest_b.position)
            if d > self.step_size:
                return None
            if is_edge_in_obstacle(node_in_a.position, nearest_b.position):
                return None
            total_cost = node_in_a.cost + d + nearest_b.cost
            path_a = node_in_a.path_from_root()
            path_b = nearest_b.path_from_root()
            if path_a[-1] == path_b[-1]:
                path_b = path_b[:-1]
            return path_a + path_b[::-1], total_cost

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.shortcut_passes):
                attempts = 0
                while attempts < self.max_shortcut_attempts_per_pass:
                    if len(path) < 3:
                        break
                    i = random.randint(0, len(path)-3)
                    j = random.randint(i+2, len(path)-1)
                    p_i = path[i]
                    p_j = path[j]
                    if not is_edge_in_obstacle(p_i, p_j):
                        # shortcut by removing intermediate nodes in [i+1:j]
                        path = path[:i+1] + path[j:]
                        # Restart attempts count after successful shortcut
                        attempts = 0
                    else:
                        attempts += 1
            return path

        nodes = []
        edges = []
        tree_start = []
        tree_goal = []

        root_start = Node(start_pos, None, 0.0)
        root_goal = Node(goal_pos, None, 0.0)
        nodes.extend([root_start, root_goal])
        tree_start.append(root_start)
        tree_goal.append(root_goal)

        best_path = []
        best_cost = float('inf')
        found_solution = False

        for it in range(self.max_iter):
            if time.monotonic() - start_time > self.time_limit:
                break

            if found_solution:
                sample = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if not is_in_obstacle(goal_pos) else uniform_sample()
                else:
                    sample = uniform_sample()

            if is_in_obstacle(sample):
                continue
            if not in_bounds(sample):
                continue

            # Alternate tree expansion
            if it % 2 == 0:
                tree_a = tree_start
                tree_b = tree_goal
            else:
                tree_a = tree_goal
                tree_b = tree_start

            nearest_n = nearest_node(tree_a, sample)
            new_pos = steer(nearest_n.position, sample, self.step_size)
            if not in_bounds(new_pos):
                continue
            if is_in_obstacle(new_pos):
                continue
            if is_edge_in_obstacle(nearest_n.position, new_pos):
                continue

            new_node = choose_parent_and_add(tree_a, new_pos, nodes, edges)
            if new_node is None:
                continue

            merge_res = merge_trees(new_node, tree_b)
            if merge_res is not None:
                path_candidate, cost_candidate = merge_res
                if cost_candidate + self.improve_tol < best_cost:
                    best_cost = cost_candidate
                    best_path = path_candidate
                    found_solution = True
                    # Early stop immediately after improvement
                    break

        # If no solution found, fallback: connect closest start node to goal if possible
        if not found_solution:
            nearest_goal_node = nearest_node(tree_start, goal_pos)
            if nearest_goal_node and not is_in_obstacle(goal_pos) and not is_edge_in_obstacle(nearest_goal_node.position, goal_pos):
                best_path = nearest_goal_node.path_from_root() + [goal_pos]

        if best_path:
            best_path = shortcut_path(best_path)
            success = found_solution
        else:
            success = False

        return PlannerResult(
            success=success,
            path=best_path,
            nodes=nodes,
            edges=edges
        )

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = 0.1
        self.time_limit = 30.0
        self.improve_tol = 1e-5
        self.max_no_improve = 100

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = dim == 3

        def dist(a, b):
            return math.dist(a, b)

        def within_bounds(p):
            return all(0.0 <= p[i] <= bounds[i] for i in range(dim))

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w_, h_ = obs
                    px, py = pos
                    if x <= px <= x + w_ and y <= py <= y + h_:
                        return True
            return False

        def edge_in_obstacle(p1, p2, resolution=1.0):
            length = dist(p1, p2)
            steps = max(1, int(length / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def steer(fi, to):
            d = dist(fi, to)
            if d <= self.step_size:
                return to
            ratio = self.step_size / d
            return tuple(fi[i] + ratio * (to[i] - fi[i]) for i in range(dim))

        def uniform_sample():
            for _ in range(100):
                sample = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not is_in_obstacle(sample):
                    return sample
            return start_pos  # fallback in crowded maps

        def informed_sample(c_best, c_min):
            if c_best == float('inf') or c_min < 1e-12:
                return uniform_sample()
            center = tuple((start_pos[i] + goal_pos[i]) * 0.5 for i in range(dim))
            a1 = tuple((goal_pos[i] - start_pos[i]) / c_min for i in range(dim))

            if dim == 2:
                a2 = (-a1[1], a1[0])
                basis = [a1, a2]
                r1 = c_best / 2.0
                r2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) / 2.0
                radii = [r1, r2]
            else:
                # 3D
                ref = (1,0,0) if abs(a1[0]) < 0.9 else (0,1,0)
                def cross(u,v):
                    return (u[1]*v[2]-u[2]*v[1], u[2]*v[0]-u[0]*v[2], u[0]*v[1]-u[1]*v[0])
                def norm(u):
                    return math.sqrt(sum(x*x for x in u))
                def normalize(u):
                    n=norm(u)
                    return tuple(x/n if n>1e-12 else x for x in u)
                b2 = normalize(cross(a1, ref))
                if norm(b2) < 1e-12:
                    ref = (0,0,1)
                    b2 = normalize(cross(a1, ref))
                b3 = cross(a1, b2)
                basis = [a1, b2, b3]
                r1 = c_best / 2.0
                r_other = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0))/2.0
                radii = [r1, r_other, r_other]

            def sample_unit_ball(d):
                while True:
                    v = [random.gauss(0,1) for _ in range(d)]
                    n = math.sqrt(sum(x*x for x in v))
                    if n > 1e-12:
                        unit = [x/n for x in v]
                        r = random.random() ** (1.0/d)
                        return [x*r for x in unit]

            u = sample_unit_ball(dim)
            mapped = [0.0]*dim
            for i in range(dim):
                for j in range(dim):
                    mapped[i] += basis[j][i] * radii[j] * u[j]
                mapped[i] += center[i]
            mapped = tuple(min(max(mapped[i], 0.0), bounds[i]) for i in range(dim))
            if is_in_obstacle(mapped):
                return uniform_sample()
            return mapped

        def neighbor_radius(n_nodes):
            if n_nodes < 2:
                return max(2.0, self.step_size*2)
            val = 30.0 * ((math.log(n_nodes)/n_nodes) ** (1/dim))
            max_r = max(bounds)*0.25
            return max(self.step_size, min(max_r, val))

        def nearest(tree, p):
            best, best_dist = None, float('inf')
            for n in tree:
                d_ = dist(n.position, p)
                if d_ < best_dist:
                    best, best_dist = n, d_
            return best

        def near_nodes(tree, p, radius):
            r2 = radius*radius
            return [n for n in tree if sum((n.position[i]-p[i])**2 for i in range(dim)) <= r2]

        def add_node_rrt_star(tree, pos, nodes_all, edges):
            r = neighbor_radius(len(tree))
            neighbors = near_nodes(tree, pos, r)
            parents = [n for n in neighbors if not edge_in_obstacle(n.position, pos)]
            if not parents:
                p = nearest(tree, pos)
                if edge_in_obstacle(p.position, pos):
                    return None, []
                parents = [p]
            best_parent = min(parents, key=lambda n: n.cost + dist(n.position, pos))
            new_cost = best_parent.cost + dist(best_parent.position, pos)
            new_node = Node(pos, parent=None, cost=new_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            nodes_all.append(new_node)
            edges.append((best_parent, new_node))
            return new_node, neighbors

        def rewire(tree, new_node, neighbors, edges):
            for nb in neighbors:
                if nb is new_node:
                    continue
                if edge_in_obstacle(new_node.position, nb.position):
                    continue
                new_cost = new_node.cost + dist(new_node.position, nb.position)
                if new_cost + 1e-12 < nb.cost:
                    if nb.parent:
                        nb.parent.remove_child(nb)
                        try:
                            edges.remove((nb.parent, nb))
                        except ValueError:
                            pass
                    new_node.add_child(nb)
                    nb.cost = new_cost
                    edges.append((new_node, nb))

        def can_connect(n1, n2):
            return not edge_in_obstacle(n1.position, n2.position) and n1.valid and n2.valid

        def stitch_path(n_start, n_goal, start_side=True):
            path_start = n_start.path_from_root()
            path_goal = n_goal.path_from_root()
            if path_start and path_goal and path_start[-1] == path_goal[-1]:
                merged = path_start + path_goal[-2::-1]
            else:
                merged = path_start + path_goal[::-1]
            return merged if start_side else merged[::-1]

        def shortcut_path(path):
            if len(path) < 3:
                return path
            smooth = [path[0]]
            i = 0
            while i < len(path)-1:
                j = len(path)-1
                while j > i+1:
                    if not edge_in_obstacle(path[i], path[j]):
                        break
                    j -= 1
                smooth.append(path[j])
                i = j
            return smooth

        start_time = time.monotonic()

        tree_start = [Node(start_pos, cost=0.0)]
        tree_goal = [Node(goal_pos, cost=0.0)]
        nodes = [tree_start[0], tree_goal[0]]
        edges = []

        best_path = []
        best_cost = float('inf')
        found_solution = False
        no_improve_count = 0

        for iter in range(self.max_iter):
            if time.monotonic() - start_time > self.time_limit:
                break

            expand_start = (iter % 2 == 0)
            curr_tree = tree_start if expand_start else tree_goal
            other_tree = tree_goal if expand_start else tree_start
            start_side = expand_start

            c_min = dist(start_pos, goal_pos)
            if found_solution:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if expand_start else start_pos
                else:
                    sample = informed_sample(best_cost, c_min)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if expand_start else start_pos
                else:
                    sample = uniform_sample()

            nearest_node = nearest(curr_tree, sample)
            new_pos = steer(nearest_node.position, sample)

            if not within_bounds(new_pos):
                no_improve_count += 1
                if found_solution and no_improve_count > self.max_no_improve:
                    break
                continue
            if is_in_obstacle(new_pos):
                no_improve_count += 1
                if found_solution and no_improve_count > self.max_no_improve:
                    break
                continue
            if edge_in_obstacle(nearest_node.position, new_pos):
                no_improve_count += 1
                if found_solution and no_improve_count > self.max_no_improve:
                    break
                continue

            new_node, neighbors = add_node_rrt_star(curr_tree, new_pos, nodes, edges)
            if new_node is None:
                no_improve_count += 1
                if found_solution and no_improve_count > self.max_no_improve:
                    break
                continue
            rewire(curr_tree, new_node, neighbors, edges)

            nearest_other = nearest(other_tree, new_node.position)
            connected_other = None
            max_connect_dist = self.step_size * 1.5
            if dist(nearest_other.position, new_node.position) <= max_connect_dist and can_connect(nearest_other, new_node):
                final_node = Node(new_node.position, cost=nearest_other.cost + dist(nearest_other.position, new_node.position))
                nearest_other.add_child(final_node)
                other_tree.append(final_node)
                nodes.append(final_node)
                edges.append((nearest_other, final_node))
                connected_other = final_node

            if connected_other:
                candidate_path = stitch_path(new_node, connected_other, start_side)
                candidate_cost = 0.0
                for i in range(len(candidate_path) - 1):
                    candidate_cost += dist(candidate_path[i], candidate_path[i + 1])
                if candidate_cost + self.improve_tol < best_cost:
                    best_cost = candidate_cost
                    best_path = candidate_path
                    found_solution = True
                    no_improve_count = 0
                else:
                    no_improve_count += 1
                    if no_improve_count > self.max_no_improve:
                        break
            else:
                if found_solution:
                    no_improve_count += 1
                    if no_improve_count > self.max_no_improve:
                        break

        if best_path:
            best_path = shortcut_path(best_path)
            success = True
        else:
            success = False
            # fallback path: start only
            best_path = [start_pos]

        return PlannerResult(
            success=success,
            path=best_path,
            nodes=nodes,
            edges=edges
        )

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
    def add_child(self, child):
        self.children.append(child)
    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
    def update_parent(self, new_parent, new_cost):
        if self.parent is not new_parent:
            if self.parent is not None:
                self.parent.remove_child(self)
            self.parent = new_parent
            self.cost = new_cost
            if new_parent is not None:
                new_parent.add_child(self)
class Planner:
    def __init__(self, max_iter=3000, step_size=5.0, goal_sample_rate=0.1):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map):
        import time
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        TIME_LIMIT = 30.0
        start_time = time.monotonic()

        def in_bounds(p):
            for i in range(len(bounds)):
                if p[i] < 0 or p[i] > bounds[i]:
                    return False
            return True

        def is_free_node(p):
            return in_bounds(p) and not self._is_in_obstacle(p, obstacles, is_3d)

        def is_free_edge(p1, p2):
            dist_val = math.dist(p1, p2)
            resolution = min(1.0, self.step_size * 0.5)
            steps = max(1, int(dist_val / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[d] + (p2[d] - p1[d]) * i / steps for d in range(len(p1)))
                if self._is_in_obstacle(interp, obstacles, is_3d):
                    return False
            return True

        def steer(from_p, to_p):
            dist_val = math.dist(from_p, to_p)
            if dist_val <= self.step_size:
                return to_p
            scale = self.step_size / dist_val
            return tuple(from_p[d] + scale * (to_p[d] - from_p[d]) for d in range(len(from_p)))

        def nearest(nodes, point):
            best = None
            best_dist = float("inf")
            for node in nodes:
                d = math.dist(node.position, point)
                if d < best_dist:
                    best_dist = d
                    best = node
            return best

        nodes = []
        edges = []
        root = Node(start_pos, cost=0.0)
        nodes.append(root)

        success = False
        goal_node = None

        for _ in range(self.max_iter):
            if time.monotonic() - start_time > TIME_LIMIT:
                break

            sample = goal_pos if random.random() < self.goal_sample_rate else tuple(random.uniform(0, bounds[d]) for d in range(len(bounds)))

            nearest_node = nearest(nodes, sample)
            new_pos = steer(nearest_node.position, sample)

            if not is_free_node(new_pos):
                continue
            if not is_free_edge(nearest_node.position, new_pos):
                continue

            new_node = Node(new_pos)
            new_node.update_parent(nearest_node, nearest_node.cost + math.dist(nearest_node.position, new_pos))
            nodes.append(new_node)
            edges.append((nearest_node, new_node))

            dist_to_goal = math.dist(new_pos, goal_pos)
            if dist_to_goal <= self.step_size and is_free_node(goal_pos) and is_free_edge(new_pos, goal_pos):
                goal_node = Node(goal_pos)
                goal_node.update_parent(new_node, new_node.cost + dist_to_goal)
                nodes.append(goal_node)
                edges.append((new_node, goal_node))
                success = True
                break

        path = []
        if success and goal_node is not None:
            curr = goal_node
            while curr is not None:
                path.append(curr.position)
                curr = curr.parent
            path.reverse()
        else:
            closest = None
            min_dist = float("inf")
            for node in nodes:
                d = math.dist(node.position, goal_pos)
                if d < min_dist:
                    min_dist = d
                    closest = node
            curr = closest
            while curr is not None:
                path.append(curr.position)
                curr = curr.parent
            path.reverse()

        return PlannerResult(success=success, path=path, nodes=nodes, edges=edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self,
                 max_iter: int = 5000,
                 step_size: float = 5.0,
                 goal_sample_rate: float = 0.05,
                 max_no_improve: int = 100,
                 improve_tol: float = 1e-7,
                 time_limit_sec: float = 30.0,
                 gamma_rrt_star: float = 40.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.max_no_improve = max_no_improve
        self.improve_tol = improve_tol
        self.time_limit_sec = time_limit_sec
        self.gamma_rrt_star = gamma_rrt_star

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def dist(a, b):
            return math.dist(a, b)

        def steer(fr, to):
            d = dist(fr, to)
            if d <= self.step_size:
                return to
            r = self.step_size / d
            return tuple(fr[i] + (to[i] - fr[i]) * r for i in range(dim))

        def within_bounds(p):
            return all(0.0 <= p[i] <= bounds[i] for i in range(dim))

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w_, h_ = obs
                    px, py = pos
                    if x <= px <= x + w_ and y <= py <= y + h_:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2, resolution=1.0):
            length = dist(p1, p2)
            steps = max(1, int(length / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def uniform_sample():
            trials = 0
            while True:
                s = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not is_in_obstacle(s):
                    return s
                trials += 1
                if trials > 1000:
                    return start_pos  # fallback

        def informed_sample(start, goal, c_best, c_min):
            if c_best == float('inf') or c_min <= 1e-12:
                return uniform_sample()
            center = tuple((s + g) * 0.5 for s, g in zip(start, goal))
            a1 = tuple((g - s) / c_min for s, g in zip(start, goal))
            # Build orthonormal basis matrix
            if dim == 2:
                a2 = (-a1[1], a1[0])
                basis = [a1, a2]
                r1 = c_best / 2.0
                r2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) / 2.0
                radii = [r1, r2]
            else:
                ref = (1.0, 0.0, 0.0) if abs(a1[0]) < 0.9 else (0.0, 1.0, 0.0)
                b2 = self._normalize(self._cross(a1, ref))
                if self._norm(b2) < 1e-12:
                    ref = (0.0, 0.0, 1.0)
                    b2 = self._normalize(self._cross(a1, ref))
                b3 = self._cross(a1, b2)
                basis = [a1, b2, b3]
                r1 = c_best / 2.0
                r_other = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) / 2.0
                radii = [r1, r_other, r_other]

            def sample_unit_ball(dim_):
                while True:
                    v = [random.gauss(0, 1) for _ in range(dim_)]
                    norm_ = math.sqrt(sum(x * x for x in v))
                    if norm_ > 1e-12:
                        v = [x / norm_ for x in v]
                        r = random.random() ** (1.0 / dim_)
                        return [x * r for x in v]

            u = sample_unit_ball(dim)
            mapped = [center[i] for i in range(dim)]
            for i in range(dim):
                for j in range(dim):
                    mapped[i] += basis[j][i] * radii[j] * u[j]
            mapped = tuple(min(max(mapped[i], 0.0), bounds[i]) for i in range(dim))
            if is_in_obstacle(mapped):
                return uniform_sample()
            return mapped

        def nearest(nodes_list, p):
            min_node = None
            min_d = float('inf')
            for n in nodes_list:
                d_ = dist(n.position, p)
                if d_ < min_d:
                    min_d = d_
                    min_node = n
            return min_node

        def neighbor_radius(n_nodes):
            if n_nodes < 2:
                return max(2.0, self.step_size * 2.0)
            val = self.gamma_rrt_star * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            max_r = max(bounds) * 0.25
            min_r = max(2.0, self.step_size)
            return max(min_r, min(max_r, val))

        def near_nodes(nodes_list, p, radius):
            result = []
            for n in nodes_list:
                if dist(n.position, p) <= radius:
                    result.append(n)
            return result

        def add_node_rrt_star(tree, pos, nodes_all, edges_list):
            r = neighbor_radius(len(tree))
            neighbors = near_nodes(tree, pos, r)
            parents = [n for n in neighbors if not is_edge_in_obstacle(n.position, pos)]
            if not parents:
                p = nearest(tree, pos)
                if is_edge_in_obstacle(p.position, pos):
                    return None, []
                parents = [p]
            best_parent = min(parents, key=lambda n: n.cost + dist(n.position, pos))
            new_cost = best_parent.cost + dist(best_parent.position, pos)
            new_node = Node(pos, parent=None, cost=new_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            nodes_all.append(new_node)
            edges_list.append((best_parent, new_node))
            return new_node, neighbors

        def propagate_cost_to_children(node):
            for child in node.children:
                old_cost = child.cost
                child.cost = node.cost + dist(node.position, child.position)
                if child.cost + 1e-12 < old_cost:
                    propagate_cost_to_children(child)

        def rewire(tree, new_node, neighbors, edges_list):
            for nb in neighbors:
                if nb is new_node:
                    continue
                if is_edge_in_obstacle(new_node.position, nb.position):
                    continue
                new_cost = new_node.cost + dist(new_node.position, nb.position)
                if new_cost + 1e-12 < nb.cost:
                    if nb.parent:
                        nb.parent.remove_child(nb)
                        try:
                            edges_list.remove((nb.parent, nb))
                        except ValueError:
                            pass
                    new_node.add_child(nb)
                    nb.cost = new_cost
                    edges_list.append((new_node, nb))
                    propagate_cost_to_children(nb)

        def can_connect(n1, n2):
            return not is_edge_in_obstacle(n1.position, n2.position) and n1.valid and n2.valid

        def stitch_path(n_start, n_goal, a_is_start):
            path_a = n_start.path_from_root()
            path_b = n_goal.path_from_root()
            if path_a and path_b and path_a[-1] == path_b[-1]:
                merged = path_a + path_b[-2::-1]
            else:
                merged = path_a + path_b[::-1]
            if a_is_start:
                return merged
            else:
                return merged[::-1]

        def shortcut_path(path):
            if len(path) < 3:
                return path
            smoothed = [path[0]]
            i = 0
            while i < len(path) - 1:
                j = len(path) - 1
                while j > i + 1:
                    if not is_edge_in_obstacle(path[i], path[j]):
                        break
                    j -= 1
                smoothed.append(path[j])
                i = j
            return smoothed

        start_time = time.monotonic()
        tree_start = [Node(start_pos, cost=0.0)]
        tree_goal = [Node(goal_pos, cost=0.0)]
        nodes = [tree_start[0], tree_goal[0]]
        edges = []

        best_path = []
        best_cost = float('inf')
        found_first = False
        no_improve_count = 0

        c_min = dist(start_pos, goal_pos)

        for it in range(self.max_iter):
            if (time.monotonic() - start_time) > self.time_limit_sec:
                break

            if it % 2 == 0:
                curr_tree = tree_start
                other_tree = tree_goal
                a_is_start_side = True
            else:
                curr_tree = tree_goal
                other_tree = tree_start
                a_is_start_side = False

            if found_first:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if a_is_start_side else start_pos
                else:
                    sample = informed_sample(start_pos, goal_pos, best_cost, c_min)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if a_is_start_side else start_pos
                else:
                    sample = uniform_sample()

            nearest_node_curr = nearest(curr_tree, sample)
            new_pos = steer(nearest_node_curr.position, sample)

            if (not within_bounds(new_pos)
                or is_in_obstacle(new_pos)
                or is_edge_in_obstacle(nearest_node_curr.position, new_pos)):
                if found_first:
                    no_improve_count += 1
                    if no_improve_count >= self.max_no_improve:
                        break
                continue

            new_node, neighbors = add_node_rrt_star(curr_tree, new_pos, nodes, edges)
            if new_node is None:
                if found_first:
                    no_improve_count += 1
                    if no_improve_count >= self.max_no_improve:
                        break
                continue

            rewire(curr_tree, new_node, neighbors, edges)

            nearest_other = nearest(other_tree, new_node.position)
            connected_other = None

            # Greedy direct connection if close enough
            if dist(new_node.position, nearest_other.position) <= self.step_size and can_connect(nearest_other, new_node):
                final_node = Node(new_node.position, parent=None, cost=nearest_other.cost + dist(nearest_other.position, new_node.position))
                nearest_other.add_child(final_node)
                other_tree.append(final_node)
                nodes.append(final_node)
                edges.append((nearest_other, final_node))
                connected_other = final_node
            else:
                # Incremental connection attempts with max greedy steps
                current = nearest_other
                max_greedy_steps = 20
                greed_steps = 0
                while greed_steps < max_greedy_steps:
                    greed_steps += 1
                    step_pos = steer(current.position, new_node.position)
                    if (not within_bounds(step_pos)
                        or is_in_obstacle(step_pos)
                        or is_edge_in_obstacle(current.position, step_pos)):
                        break
                    other_new, other_neighbors = add_node_rrt_star(other_tree, step_pos, nodes, edges)
                    if other_new is None:
                        break
                    rewire(other_tree, other_new, other_neighbors, edges)
                    current = other_new
                    if dist(current.position, new_node.position) <= self.step_size and can_connect(current, new_node):
                        final_node = Node(new_node.position, parent=None, cost=current.cost + dist(current.position, new_node.position))
                        current.add_child(final_node)
                        other_tree.append(final_node)
                        nodes.append(final_node)
                        edges.append((current, final_node))
                        connected_other = final_node
                        break

            if connected_other is not None:
                candidate_path = stitch_path(new_node, connected_other, a_is_start_side)
                candidate_cost = 0.0
                for i in range(len(candidate_path) - 1):
                    candidate_cost += dist(candidate_path[i], candidate_path[i + 1])

                if candidate_cost + self.improve_tol < best_cost:
                    best_cost = candidate_cost
                    best_path = candidate_path
                    found_first = True
                    no_improve_count = 0
                else:
                    if found_first:
                        no_improve_count += 1
                        if no_improve_count >= self.max_no_improve:
                            break
            else:
                if found_first:
                    no_improve_count += 1
                    if no_improve_count >= self.max_no_improve:
                        break

        if best_path:
            best_path = shortcut_path(best_path)
            return PlannerResult(success=True, path=best_path, nodes=nodes, edges=edges)
        else:
            return PlannerResult(success=False, path=[], nodes=nodes, edges=edges)

    def _cross(self, a, b):
        ax, ay, az = a
        bx, by, bz = b
        return (ay * bz - az * by, az * bx - ax * bz, ax * by - ay * bx)

    def _norm(self, v):
        return math.sqrt(sum(x * x for x in v))

    def _normalize(self, v):
        n = self._norm(v)
        if n < 1e-12:
            return v
        return tuple(x / n for x in v)

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position  # Tuple[float, ...]
        self.parent = parent      # Node or None
        self.cost = cost          # Cost from root start/goal
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent != new_parent:
            if self.parent:
                self.parent.remove_child(self)
            self.parent = new_parent
            self.cost = new_cost
            if new_parent:
                new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter=5000, step_size=5.0, goal_sample_rate=0.05, rewire_radius_factor=30.0, time_limit_sec=30.0, shortcut_iters=8):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.rewire_radius_factor = rewire_radius_factor
        self.time_limit_sec = time_limit_sec
        self.shortcut_iters = shortcut_iters

    def plan(self, map):
        import time
        import math
        import random

        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        obstacles = map.obstacles
        start_pos = map.start
        goal_pos = map.goal

        t_start = time.monotonic()
        TIME_LIMIT = self.time_limit_sec

        def in_bounds(p):
            for i in range(dim):
                if not (0.0 <= p[i] <= bounds[i]):
                    return False
            return True

        def is_in_obstacle(p):
            for obs in obstacles:
                if is_3d:
                    x,y,z,w,h,d = obs
                    px,py,pz = p
                    if x <= px <= x+w and y <= py <= y+h and z <= pz <= z+d:
                        return True
                else:
                    x,y,w_,h_ = obs
                    px,py = p
                    if x <= px <= x+w_ and y <= py <= y+h_:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2, resolution=None):
            dist = math.dist(p1, p2)
            res = resolution if resolution is not None else max(min(0.5 * self.step_size, 1.0), 0.2)
            steps = max(1, int(dist / res))
            for i in range(steps+1):
                interp = tuple(p1[j] + (p2[j] - p1[j])*(i/steps) for j in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def steer(fr, to):
            d = math.dist(fr, to)
            if d <= self.step_size:
                return to
            r = self.step_size / d
            return tuple(fr[i] + (to[i] - fr[i]) * r for i in range(dim))

        def uniform_sample():
            trials = 0
            while True:
                p = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not is_in_obstacle(p):
                    return p
                trials += 1
                if trials >= 1000:
                    return start_pos

        def compute_rotation_matrix(unit_x):
            if dim == 2:
                x,y = unit_x
                norm = math.hypot(x,y)
                if norm < 1e-14:
                    return [[1,0],[0,1]]
                x/=norm; y/=norm
                return [[ x,-y],[y, x]]
            elif dim == 3:
                ux,uy,uz = unit_x
                norm = math.sqrt(ux*ux + uy*uy + uz*uz)
                if norm < 1e-14:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                ux/=norm; uy/=norm; uz/=norm
                v = (0,0,1) if abs(uz) < 0.9 else (0,1,0)
                ax = uy*v[2] - uz*v[1]
                ay = uz*v[0] - ux*v[2]
                az = ux*v[1] - uy*v[0]
                axis_len = math.sqrt(ax*ax + ay*ay + az*az)
                if axis_len < 1e-14:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                ax /= axis_len; ay /= axis_len; az /= axis_len
                angle = math.acos(uz)
                c,s,t = math.cos(angle), math.sin(angle), 1 - math.cos(angle)
                C = [[0]*3 for _ in range(3)]
                C[0][0] = t*ax*ax + c
                C[0][1] = t*ax*ay - s*az
                C[0][2] = t*ax*az + s*ay
                C[1][0] = t*ax*ay + s*az
                C[1][1] = t*ay*ay + c
                C[1][2] = t*ay*az - s*ax
                C[2][0] = t*ax*az - s*ay
                C[2][1] = t*ay*az + s*ax
                C[2][2] = t*az*az + c
                return C
            else:
                return [[1 if i==j else 0 for j in range(dim)] for i in range(dim)]

        def sample_unit_ball(d):
            for _ in range(100):
                coord = tuple(random.uniform(-1,1) for _ in range(d))
                norm = math.sqrt(sum(x*x for x in coord))
                if norm <= 1 and norm > 1e-14:
                    return tuple(x/norm for x in coord)
            return None

        def informed_sample(c_best, c_min, start, goal):
            # Ellipsoidal informed sampling
            if c_best == float('inf') or c_min < 1e-14:
                return uniform_sample()
            midpoint = tuple(0.5*(start[i]+goal[i]) for i in range(dim))
            diff = tuple(goal[i]-start[i] for i in range(dim))
            norm_diff = math.dist(start, goal)
            if norm_diff < 1e-14:
                return uniform_sample()
            unit_x = tuple(d / norm_diff for d in diff)
            C = compute_rotation_matrix(unit_x)
            r1 = c_best / 2.0
            r2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0.0))/2.0
            for _ in range(50):
                u = sample_unit_ball(dim)
                if u is None:
                    return uniform_sample()
                local = [0.0]*dim
                local[0] = u[0]*r1
                for i in range(1, dim):
                    local[i] = u[i]*r2
                point = []
                for i in range(dim):
                    s = 0.0
                    for j in range(dim):
                        s += C[i][j]*local[j]
                    point.append(s + midpoint[i])
                p = tuple(min(max(point[i], 0.0), bounds[i]) for i in range(dim))
                if not is_in_obstacle(p):
                    return p
            return uniform_sample()

        def dist(a,b):
            return math.dist(a,b)

        def nearest(nodes_list, p):
            if not nodes_list:
                return None
            best = nodes_list[0]
            best_dist = dist(best.position, p)
            for n in nodes_list[1:]:
                d_ = dist(n.position, p)
                if d_ < best_dist:
                    best = n
                    best_dist = d_
            return best

        def neighbor_radius(n_nodes):
            if n_nodes < 2:
                return max(2.0, self.step_size*2.0)
            val = self.rewire_radius_factor * ((math.log(n_nodes)/n_nodes)**(1.0/dim))
            max_r = max(bounds)*0.25
            min_r = max(2.0, self.step_size)
            return max(min_r, min(max_r, val))

        def near_nodes(nodes_list, p, radius):
            radius_sq = radius*radius
            result = []
            for n in nodes_list:
                if dist(n.position, p) <= radius:
                    result.append(n)
            return result

        def add_node_rrt_star(tree, pos, all_nodes, edges_list):
            r = neighbor_radius(len(tree))
            neighbors = near_nodes(tree, pos, r)
            valid_parents = [n for n in neighbors if not is_edge_in_obstacle(n.position, pos)]
            if not valid_parents:
                near_n = nearest(tree, pos)
                if near_n is None or is_edge_in_obstacle(near_n.position, pos):
                    return None, []
                valid_parents = [near_n]
            best_parent = min(valid_parents, key=lambda n: n.cost + dist(n.position, pos))
            new_cost = best_parent.cost + dist(best_parent.position, pos)
            new_node = Node(pos, parent=None, cost=new_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            all_nodes.append(new_node)
            edges_list.append((best_parent, new_node))
            return new_node, neighbors

        def propagate_cost(node):
            # Recursively propagate improved costs to children
            for c in node.children:
                old_cost = c.cost
                c.cost = node.cost + dist(node.position, c.position)
                if c.cost + 1e-14 < old_cost:
                    propagate_cost(c)

        def rewire(tree, new_node, neighbors, edges_list):
            for nb in neighbors:
                if nb is new_node:
                    continue
                if is_edge_in_obstacle(new_node.position, nb.position):
                    continue
                new_cost = new_node.cost + dist(new_node.position, nb.position)
                if new_cost + 1e-14 < nb.cost:
                    if nb.parent:
                        nb.parent.remove_child(nb)
                        try:
                            edges_list.remove((nb.parent, nb))
                        except Exception:
                            pass
                    new_node.add_child(nb)
                    nb.cost = new_cost
                    edges_list.append((new_node, nb))
                    propagate_cost(nb)

        def can_connect(n1, n2):
            if not n1.valid or not n2.valid:
                return False
            if is_edge_in_obstacle(n1.position, n2.position):
                return False
            return True

        def stitch_path(node_a, node_b, a_is_start):
            path_a = node_a.path_from_root()
            path_b = node_b.path_from_root()
            if path_a and path_b and path_a[-1] == path_b[-1]:
                merged = path_a + path_b[-2::-1]
            else:
                merged = path_a + path_b[::-1]
            return merged if a_is_start else merged[::-1]

        def shortcut_path(path):
            if len(path) < 3:
                return path
            shortened = [path[0]]
            i = 0
            while i < len(path)-1:
                j = len(path)-1
                while j > i+1:
                    if not is_edge_in_obstacle(path[i], path[j]):
                        break
                    j -= 1
                shortened.append(path[j])
                i = j
            return shortened

        # Initialize trees and data containers
        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]
        all_nodes = tree_start + tree_goal[:]
        edges = []
        best_path = []
        best_cost = float("inf")
        found_solution = False
        c_min = dist(start_pos, goal_pos)
        no_improve = 0
        max_no_improve = 100
        post_opt_iters = 300
        post_iter = 0

        for i in range(self.max_iter):
            if time.monotonic() - t_start > TIME_LIMIT:
                break

            # Alternate expanding start/goal trees
            curr_start_tree = (i % 2 == 0)
            curr_tree = tree_start if curr_start_tree else tree_goal
            other_tree = tree_goal if curr_start_tree else tree_start

            # Sampling with goal bias and informed sampling if solution found
            if found_solution:
                if random.random() < self.goal_sample_rate:
                    sample_point = goal_pos if curr_start_tree else start_pos
                else:
                    sample_point = informed_sample(best_cost, c_min, start_pos, goal_pos)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_point = goal_pos if curr_start_tree else start_pos
                else:
                    sample_point = uniform_sample()

            nearest_node = nearest(curr_tree, sample_point)
            if nearest_node is None:
                # No nodes in tree, skip iteration
                continue

            new_pos = steer(nearest_node.position, sample_point)

            if (not in_bounds(new_pos)) or is_in_obstacle(new_pos) or is_edge_in_obstacle(nearest_node.position, new_pos):
                if found_solution:
                    no_improve += 1
                    post_iter += 1
                    if post_iter >= post_opt_iters or no_improve >= max_no_improve:
                        break
                continue

            new_node, neighbors = add_node_rrt_star(curr_tree, new_pos, all_nodes, edges)
            if new_node is None:
                if found_solution:
                    no_improve += 1
                    post_iter += 1
                    if post_iter >= post_opt_iters or no_improve >= max_no_improve:
                        break
                continue

            rewire(curr_tree, new_node, neighbors, edges)

            # Attempt connection from new node to closest node in other tree
            nearest_other = nearest(other_tree, new_node.position)
            connected_other_node = None
            if nearest_other is not None:
                dist_trees = dist(new_node.position, nearest_other.position)
                if dist_trees <= self.step_size and can_connect(new_node, nearest_other):
                    # Connect trees by linking nearest_other child to new_node position
                    connected_other_node = Node(new_node.position, parent=None, cost=nearest_other.cost + dist_trees)
                    nearest_other.add_child(connected_other_node)
                    other_tree.append(connected_other_node)
                    all_nodes.append(connected_other_node)
                    edges.append((nearest_other, connected_other_node))

            if connected_other_node:
                candidate_path = stitch_path(new_node, connected_other_node, curr_start_tree)
                candidate_cost = 0.0
                for idx in range(len(candidate_path)-1):
                    candidate_cost += dist(candidate_path[idx], candidate_path[idx+1])
                if candidate_cost + 1e-9 < best_cost:
                    best_cost = candidate_cost
                    best_path = candidate_path
                    found_solution = True
                    no_improve = 0
                    post_iter = 0
                else:
                    if found_solution:
                        no_improve += 1
                        post_iter += 1
                        if post_iter >= post_opt_iters or no_improve >= max_no_improve:
                            break
            else:
                if found_solution:
                    no_improve += 1
                    post_iter += 1
                    if post_iter >= post_opt_iters or no_improve >= max_no_improve:
                        break

        # If no solution found, attempt direct start-goal edge
        if not found_solution and not is_edge_in_obstacle(start_pos, goal_pos):
            best_path = [start_pos, goal_pos]
            found_solution = True

        # Iterative shortcutting for path smoothing within time constraints
        sc_start = time.monotonic()
        for _ in range(self.shortcut_iters):
            if time.monotonic() - t_start > TIME_LIMIT or time.monotonic() - sc_start > TIME_LIMIT*0.25:
                break
            if best_path:
                best_path = shortcut_path(best_path)

        return PlannerResult(
            success=found_solution,
            path=best_path,
            nodes=all_nodes,
            edges=edges
        )

class Node:
    def __init__(self, position: Tuple[float, ...], parent: Optional["Node"]=None, cost: float=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children: List["Node"] = []
        self.valid = True

    def add_child(self, child: "Node"):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child: "Node"):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: Optional["Node"], new_cost: float):
        if self.parent is not new_parent:
            if self.parent is not None:
                self.parent.remove_child(self)
            self.parent = new_parent
            self.cost = new_cost
            if new_parent is not None:
                new_parent.children.append(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int=5000, step_size: float=5.0, goal_sample_rate: float=0.1,
                 gamma_rrt_star: float=30.0, max_neighbor_radius: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.gamma_rrt_star = gamma_rrt_star
        self.max_neighbor_radius = max_neighbor_radius

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        start_time = time.monotonic()
        TIME_LIMIT = 30.0

        nodes: List[Node] = []
        edges: List[Tuple[Node, Node]] = []

        def in_bounds(p):
            for i in range(dim):
                if p[i] < 0 or p[i] > bounds[i]:
                    return False
            return True

        def is_free_node(p):
            if not in_bounds(p):
                return False
            return not self._is_in_obstacle(p, obstacles, is_3d)

        def is_free_edge(p1, p2):
            dist = math.dist(p1, p2)
            resolution = min(1.0, self.step_size*0.5)
            steps = max(1, int(dist / resolution))
            for i in range(steps+1):
                interp = tuple(p1[j] + (p2[j]-p1[j])*(i/steps) for j in range(dim))
                if self._is_in_obstacle(interp, obstacles, is_3d):
                    return False
            return True

        def steer(from_pos, to_pos, max_dist):
            dist = math.dist(from_pos, to_pos)
            if dist <= max_dist:
                return to_pos
            ratio = max_dist / dist
            return tuple(from_pos[i] + ratio * (to_pos[i] - from_pos[i]) for i in range(dim))

        def sample_free():
            while True:
                if random.random() < self.goal_sample_rate:
                    p = goal_pos
                else:
                    p = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if is_free_node(p):
                    return p

        def nearest(tree_nodes: List[Node], point: Tuple[float, ...]) -> Node:
            best_node = tree_nodes[0]
            best_dist = math.dist(best_node.position, point)
            for node in tree_nodes[1:]:
                d = math.dist(node.position, point)
                if d < best_dist:
                    best_dist = d
                    best_node = node
            return best_node

        def near_nodes(tree_nodes: List[Node], point: Tuple[float, ...]) -> List[Node]:
            n = len(tree_nodes)
            if n < 2:
                return []
            rad = self.gamma_rrt_star * (math.log(n) / n) ** (1/dim)
            radius = min(self.max_neighbor_radius, max(self.step_size*2.0, rad))
            return [node for node in tree_nodes if math.dist(node.position, point) <= radius]

        def choose_parent_and_add(tree_nodes: List[Node], new_pos: Tuple[float, ...]) -> Optional[Node]:
            nearest_node = nearest(tree_nodes, new_pos)
            neighbors = near_nodes(tree_nodes, new_pos)
            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for nb in neighbors:
                if nb == nearest_node:
                    continue
                if not self._is_edge_in_obstacle(nb.position, new_pos, obstacles, is_3d):
                    cost_cand = nb.cost + math.dist(nb.position, new_pos)
                    if cost_cand < min_cost:
                        min_cost = cost_cand
                        best_parent = nb
            if not is_free_edge(best_parent.position, new_pos):
                return None
            new_node = Node(new_pos, None, min_cost)
            best_parent.children.append(new_node)
            new_node.parent = best_parent
            nodes.append(new_node)
            tree_nodes.append(new_node)
            edges.append((best_parent, new_node))
            rewire(new_node, neighbors, edges)
            return new_node

        def rewire(pivot: Node, neighbors: List[Node], edges: List[Tuple[Node,Node]]) -> None:
            for nb in neighbors:
                if nb is pivot:
                    continue
                if not self._is_edge_in_obstacle(pivot.position, nb.position, obstacles, is_3d):
                    new_cost = pivot.cost + math.dist(pivot.position, nb.position)
                    if new_cost + 1e-15 < nb.cost:
                        if nb.parent is not None:
                            try:
                                edges.remove((nb.parent, nb))
                            except ValueError:
                                pass
                            nb.parent.children.remove(nb)
                        nb.parent = pivot
                        nb.cost = new_cost
                        pivot.children.append(nb)
                        edges.append((pivot, nb))

        def merge_trees(node_start: Node, tree_goal: List[Node]) -> Optional[Tuple[List[Tuple[float, ...]], float]]:
            nearest_goal_node = nearest(tree_goal, node_start.position)
            dist_goal = math.dist(node_start.position, nearest_goal_node.position)
            if dist_goal > self.step_size or not is_free_edge(node_start.position, nearest_goal_node.position):
                return None
            # connect goal node parent chain if needed
            # Create a connecting node at goal node's position attached to start tree node
            connecting_cost = node_start.cost + dist_goal + nearest_goal_node.cost
            path_start = node_start.path_from_root()
            path_goal = nearest_goal_node.path_from_root()
            if path_start[-1] == path_goal[-1]:
                path_goal = path_goal[:-1]
            full_path = path_start + path_goal[::-1]
            return full_path, connecting_cost

        def shortcut_path(path_points: List[Tuple[float, ...]]) -> List[Tuple[float, ...]]:
            if len(path_points) < 3:
                return path_points
            shortened = [path_points[0]]
            i = 0
            while i < len(path_points) - 1:
                j = len(path_points) - 1
                while j > i + 1:
                    if is_free_edge(path_points[i], path_points[j]):
                        shortened.append(path_points[j])
                        i = j
                        break
                    j -= 1
                else:
                    i += 1
                    if i < len(path_points):
                        shortened.append(path_points[i])
            return shortened

        start_root = Node(start_pos, None, 0.0)
        goal_root = Node(goal_pos, None, 0.0)
        nodes.extend([start_root, goal_root])
        tree_start = [start_root]
        tree_goal = [goal_root]

        best_path: List[Tuple[float, ...]] = []
        best_cost = float('inf')
        success = False

        for iter_count in range(self.max_iter):
            if time.monotonic() - start_time > TIME_LIMIT:
                break

            sample = sample_free()
            # Alternate tree expansions
            if iter_count % 2 == 0:
                tree_a, tree_b = tree_start, tree_goal
            else:
                tree_a, tree_b = tree_goal, tree_start

            nearest_node = nearest(tree_a, sample)
            new_pos = steer(nearest_node.position, sample, self.step_size)
            if not is_free_node(new_pos):
                continue

            new_node = choose_parent_and_add(tree_a, new_pos)
            if new_node is None:
                continue

            merged = merge_trees(new_node, tree_b)
            if merged is not None:
                path_candidate, cost_candidate = merged
                if cost_candidate < best_cost:
                    best_path = path_candidate
                    best_cost = cost_candidate
                    success = True
                    break  # Early stopping

        if not success:
            # Try connecting start tree node nearest goal to form path if possible
            nearest_goal_node = nearest(tree_start, goal_pos)
            if nearest_goal_node and is_free_edge(nearest_goal_node.position, goal_pos):
                path_candidate = nearest_goal_node.path_from_root() + [goal_pos]
                best_path = path_candidate
                success = False

        if best_path:
            best_path = shortcut_path(best_path)

        return PlannerResult(
            success=success,
            path=best_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * i / steps for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self,
                 max_iter: int = 10000,
                 step_size: float = 5.0,
                 goal_sample_rate: float = 0.05,
                 post_opt_iters: int = 300,
                 max_no_improve: int = 100,
                 improve_tol: float = 1e-6,
                 time_limit_sec: float = 30.0,
                 gamma_rrt_star: float = 40.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.post_opt_iters = post_opt_iters
        self.max_no_improve = max_no_improve
        self.improve_tol = improve_tol
        self.time_limit_sec = time_limit_sec
        self.gamma_rrt_star = gamma_rrt_star

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = dim == 3

        def dist(a, b):
            return math.dist(a, b)

        def steer(fr, to):
            d = dist(fr, to)
            if d <= self.step_size:
                return to
            r = self.step_size / d
            return tuple(fr[i] + (to[i] - fr[i]) * r for i in range(dim))

        def within_bounds(p):
            return all(0.0 <= p[i] <= bounds[i] for i in range(dim))

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w_, h_ = obs
                    px, py = pos
                    if x <= px <= x + w_ and y <= py <= y + h_:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2, resolution=1.0):
            length = dist(p1, p2)
            steps = max(1, int(length / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def uniform_sample():
            trials = 0
            while True:
                s = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not is_in_obstacle(s):
                    return s
                trials += 1
                if trials > 1000:
                    # fallback: return start to avoid infinite loop in crowded maps
                    return start_pos

        def informed_sample(start, goal, c_best, c_min):
            if c_best == float('inf') or c_min <= 1e-12:
                return uniform_sample()
            center = tuple((s + g) * 0.5 for s, g in zip(start, goal))
            a1 = tuple((g - s) / c_min for s, g in zip(start, goal))
            if dim == 2:
                a2 = (-a1[1], a1[0])
                basis = [a1, a2]
                r1 = c_best / 2.0
                r2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) / 2.0
                radii = [r1, r2]
            else:
                # 3D case
                ref = (1.0, 0.0, 0.0) if abs(a1[0]) < 0.9 else (0.0, 1.0, 0.0)
                b2 = self._normalize(self._cross(a1, ref))
                if self._norm(b2) < 1e-12:
                    ref = (0.0, 0.0, 1.0)
                    b2 = self._normalize(self._cross(a1, ref))
                b3 = self._cross(a1, b2)
                basis = [a1, b2, b3]
                r1 = c_best / 2.0
                r_other = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) / 2.0
                radii = [r1, r_other, r_other]

            def sample_unit_ball(dim_):
                while True:
                    v = [random.gauss(0, 1) for _ in range(dim_)]
                    norm_ = math.sqrt(sum(x * x for x in v))
                    if norm_ > 1e-12:
                        v = [x / norm_ for x in v]
                        r = random.random() ** (1.0 / dim_)
                        return [x * r for x in v]

            u = sample_unit_ball(dim)
            mapped = [center[i] for i in range(dim)]
            for i in range(dim):
                for j in range(dim):
                    mapped[i] += basis[j][i] * radii[j] * u[j]
            mapped = tuple(min(max(mapped[i], 0.0), bounds[i]) for i in range(dim))
            if is_in_obstacle(mapped):
                return uniform_sample()
            return mapped

        def nearest(nodes_list, p):
            min_node = None
            min_d = float('inf')
            for n in nodes_list:
                d_ = dist(n.position, p)
                if d_ < min_d:
                    min_d = d_
                    min_node = n
            return min_node

        def neighbor_radius(n_nodes):
            if n_nodes < 2:
                return max(2.0, self.step_size * 2.0)
            val = self.gamma_rrt_star * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            max_r = max(bounds) * 0.25
            min_r = max(2.0, self.step_size)
            return max(min_r, min(max_r, val))

        def near_nodes(nodes_list, p, radius):
            r_sq = radius * radius
            result = []
            for n in nodes_list:
                d_ = dist(n.position, p)
                if d_ <= radius:
                    result.append(n)
            return result

        def add_node_rrt_star(tree, pos, nodes_all, edges_list):
            r = neighbor_radius(len(tree))
            neighbors = near_nodes(tree, pos, r)
            parents = [n for n in neighbors if not is_edge_in_obstacle(n.position, pos)]
            if not parents:
                p = nearest(tree, pos)
                if is_edge_in_obstacle(p.position, pos):
                    return None, []
                parents = [p]
            best_parent = min(parents, key=lambda n: n.cost + dist(n.position, pos))
            new_cost = best_parent.cost + dist(best_parent.position, pos)
            new_node = Node(pos, parent=None, cost=new_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            nodes_all.append(new_node)
            edges_list.append((best_parent, new_node))
            return new_node, neighbors

        def propagate_cost_to_children(node):
            for child in node.children:
                old_cost = child.cost
                child.cost = node.cost + dist(node.position, child.position)
                if child.cost + 1e-12 < old_cost:
                    propagate_cost_to_children(child)

        def rewire(tree, new_node, neighbors, edges_list):
            for nb in neighbors:
                if nb is new_node:
                    continue
                if is_edge_in_obstacle(new_node.position, nb.position):
                    continue
                new_cost = new_node.cost + dist(new_node.position, nb.position)
                if new_cost + 1e-12 < nb.cost:
                    if nb.parent:
                        nb.parent.remove_child(nb)
                        try:
                            edges_list.remove((nb.parent, nb))
                        except ValueError:
                            pass
                    new_node.add_child(nb)
                    nb.cost = new_cost
                    edges_list.append((new_node, nb))
                    propagate_cost_to_children(nb)

        def can_connect(n1, n2):
            return not is_edge_in_obstacle(n1.position, n2.position) and n1.valid and n2.valid

        def stitch_path(n_start, n_goal, a_is_start):
            path_a = n_start.path_from_root()
            path_b = n_goal.path_from_root()
            # Remove duplicated connection point if any
            if path_a and path_b and path_a[-1] == path_b[-1]:
                merged = path_a + path_b[-2::-1]
            else:
                merged = path_a + path_b[::-1]
            return merged if a_is_start else merged[::-1]

        def shortcut_path(path):
            if len(path) < 3:
                return path
            smoothed = [path[0]]
            i = 0
            while i < len(path) - 1:
                j = len(path) - 1
                while j > i + 1:
                    # Check if direct path is free
                    if not is_edge_in_obstacle(path[i], path[j]):
                        break
                    j -= 1
                smoothed.append(path[j])
                i = j
            return smoothed

        start_time = time.monotonic()
        tree_start = [Node(start_pos, cost=0.0)]
        tree_goal = [Node(goal_pos, cost=0.0)]
        nodes = [tree_start[0], tree_goal[0]]
        edges = []

        best_path = []
        best_cost = float('inf')
        found_first = False
        post_no_improve = 0
        post_iter = 0
        c_min = dist(start_pos, goal_pos)

        for it in range(self.max_iter):
            if (time.monotonic() - start_time) > self.time_limit_sec:
                break

            if it % 2 == 0:
                curr_tree = tree_start
                other_tree = tree_goal
                a_is_start_side = True
            else:
                curr_tree = tree_goal
                other_tree = tree_start
                a_is_start_side = False

            sample = None
            if found_first:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if a_is_start_side else start_pos
                else:
                    sample = informed_sample(start_pos, goal_pos, best_cost, c_min)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if a_is_start_side else start_pos
                else:
                    sample = uniform_sample()

            nearest_node_curr = nearest(curr_tree, sample)
            new_pos = steer(nearest_node_curr.position, sample)

            if not within_bounds(new_pos):
                if found_first:
                    post_no_improve += 1
                    post_iter += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break
                continue
            if is_in_obstacle(new_pos):
                if found_first:
                    post_no_improve += 1
                    post_iter += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break
                continue
            if is_edge_in_obstacle(nearest_node_curr.position, new_pos):
                if found_first:
                    post_iter += 1
                    post_no_improve += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break
                continue

            new_node, neighbors = add_node_rrt_star(curr_tree, new_pos, nodes, edges)
            if new_node is None:
                if found_first:
                    post_no_improve += 1
                    post_iter += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break
                continue

            rewire(curr_tree, new_node, neighbors, edges)

            nearest_other = nearest(other_tree, new_node.position)
            connected_other = None
            # Try to connect greedy in one step if possible
            if dist(nearest_other.position, new_node.position) <= self.step_size and can_connect(nearest_other, new_node):
                # Connect directly
                final_node = Node(new_node.position, parent=None, cost=nearest_other.cost + dist(nearest_other.position, new_node.position))
                nearest_other.add_child(final_node)
                other_tree.append(final_node)
                nodes.append(final_node)
                edges.append((nearest_other, final_node))
                connected_other = final_node
            else:
                # incremental connection attempts
                current = nearest_other
                tries = 0
                max_greedy_steps = 20
                while tries < max_greedy_steps:
                    tries += 1
                    step_pos = steer(current.position, new_node.position)
                    if not within_bounds(step_pos) or is_in_obstacle(step_pos):
                        break
                    if is_edge_in_obstacle(current.position, step_pos):
                        break
                    other_new, other_neighbors = add_node_rrt_star(other_tree, step_pos, nodes, edges)
                    if other_new is None:
                        break
                    rewire(other_tree, other_new, other_neighbors, edges)
                    current = other_new
                    if dist(current.position, new_node.position) <= self.step_size:
                        if can_connect(current, new_node):
                            final_node = Node(new_node.position, parent=None, cost=current.cost + dist(current.position, new_node.position))
                            current.add_child(final_node)
                            other_tree.append(final_node)
                            nodes.append(final_node)
                            edges.append((current, final_node))
                            connected_other = final_node
                        break

            if connected_other is not None:
                candidate_path = stitch_path(new_node, connected_other, a_is_start_side)
                cost_candidate = 0.0
                for i in range(len(candidate_path) - 1):
                    cost_candidate += dist(candidate_path[i], candidate_path[i + 1])

                if cost_candidate + self.improve_tol < best_cost:
                    best_cost = cost_candidate
                    best_path = candidate_path
                    found_first = True
                    post_iter = 0
                    post_no_improve = 0
                else:
                    if found_first:
                        post_no_improve += 1
                if found_first:
                    post_iter += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break
            else:
                if found_first:
                    post_iter += 1
                    post_no_improve += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break

        if best_path:
            best_path = shortcut_path(best_path)
            return PlannerResult(
                success=True,
                path=best_path,
                nodes=nodes,
                edges=edges
            )
        else:
            return PlannerResult(
                success=False,
                path=[],
                nodes=nodes,
                edges=edges
            )

    def _cross(self, a, b):
        ax, ay, az = a
        bx, by, bz = b
        return (ay * bz - az * by, az * bx - ax * bz, ax * by - ay * bx)

    def _norm(self, v):
        return math.sqrt(sum(x * x for x in v))

    def _normalize(self, v):
        n = self._norm(v)
        if n < 1e-12:
            return v
        return tuple(x / n for x in v)

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)

    def remove_child(self, child):
        try:
            self.children.remove(child)
        except ValueError:
            pass

    def update_parent(self, new_parent, new_cost):
        if self.parent is not new_parent:
            if self.parent is not None:
                self.parent.remove_child(self)
            self.parent = new_parent
            self.cost = new_cost
            if new_parent is not None:
                new_parent.add_child(self)
class Planner:
    def __init__(self, max_iter: int = 15000, step_size: float = 5.0, goal_sample_rate: float = 0.15, max_neighbor_radius: float = 20.0, min_neighbor_radius: float = 4.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.max_neighbor_radius = max_neighbor_radius
        self.min_neighbor_radius = min_neighbor_radius

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        start_time = time.monotonic()
        time_limit = 30.0

        nodes: List[Node] = []
        edges: List[Tuple[Node, Node]] = []

        # Distance metric
        def dist(a: Tuple[float, ...], b: Tuple[float, ...]) -> float:
            return math.dist(a, b)

        # Clamp point inside bounds
        def clamp_point(p):
            return tuple(min(max(0.0, p[i]), bounds[i]) for i in range(dim))

        def in_bounds(pos):
            for i in range(dim):
                if pos[i] < 0 or pos[i] > bounds[i]:
                    return False
            return True

        def is_free_node(pos):
            if not in_bounds(pos):
                return False
            return not self._is_in_obstacle(pos, obstacles, is_3d)

        def is_free_edge(p1, p2):
            d = dist(p1, p2)
            resolution = min(1.0, self.step_size * 0.4)
            steps = max(1, int(d / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if self._is_in_obstacle(interp, obstacles, is_3d):
                    return False
            return True

        def steer(from_pos, to_pos, max_len):
            d = dist(from_pos, to_pos)
            if d <= max_len:
                return to_pos
            ratio = max_len / d
            return tuple(from_pos[i] + ratio * (to_pos[i] - from_pos[i]) for i in range(dim))

        def heuristic_cost(a, b):
            return dist(a, b)

        # Informed sampling inside ellipsoid region after first solution found
        def sample_informed(c_best, x_start, x_goal):
            if math.isinf(c_best):
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            c_min = heuristic_cost(x_start, x_goal)
            if c_min == 0.0:
                return x_start

            center = tuple((x_start[i] + x_goal[i]) * 0.5 for i in range(dim))
            a1 = tuple((x_goal[i] - x_start[i]) / c_min for i in range(dim))

            # Build rotation matrix using Gram-Schmidt for 2D or 3D
            def build_rotation_matrix(a):
                if dim == 2:
                    # 2D rotate so that x-axis aligns with a
                    cos_theta, sin_theta = a
                    return [[cos_theta, -sin_theta],
                            [sin_theta,  cos_theta]]
                elif dim == 3:
                    x_axis = a
                    # find orthogonal vector
                    if abs(x_axis[0]) < 0.9:
                        temp = (1, 0, 0)
                    else:
                        temp = (0, 1, 0)
                    v = [temp[i] - sum(x_axis[j]*temp[j] for j in range(3))*x_axis[i] for i in range(3)]
                    norm_v = math.sqrt(sum(vi*vi for vi in v))
                    if norm_v < 1e-10:
                        return [[1,0,0],[0,1,0],[0,0,1]]
                    v_unit = [vi/norm_v for vi in v]
                    w = [x_axis[1]*v_unit[2] - x_axis[2]*v_unit[1],
                         x_axis[2]*v_unit[0] - x_axis[0]*v_unit[2],
                         x_axis[0]*v_unit[1] - x_axis[1]*v_unit[0]]
                    R = [list(x_axis), v_unit, w]
                    # transpose
                    return [[R[0][0], R[1][0], R[2][0]],
                            [R[0][1], R[1][1], R[2][1]],
                            [R[0][2], R[1][2], R[2][2]]]
                else:
                    return None

            R = build_rotation_matrix(a1)
            if R is None:
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            r1 = c_best / 2.0
            val = c_best*c_best - c_min*c_min
            r2 = math.sqrt(val) / 2.0 if val > 0 else 0.001
            radii = [r1] + [r2]*(dim-1)

            while True:
                rnd = [random.gauss(0,1) for _ in range(dim)]
                norm_rnd = math.sqrt(sum(x*x for x in rnd))
                if norm_rnd > 1e-10:
                    break
            unit_ball = [x / norm_rnd for x in rnd]
            u = random.random()
            scale = u**(1/dim)
            sample_ball = [scale * unit_ball[i] * radii[i] for i in range(dim)]

            sample = [0.0]*dim
            for i in range(dim):
                s = 0.0
                for j in range(dim):
                    s += R[i][j] * sample_ball[j]
                sample[i] = s + center[i]

            clipped = tuple(min(max(0.0, sample[i]), bounds[i]) for i in range(dim))
            return clipped

        # Adaptive neighbor radius for rewiring based on theory
        def radius_adaptive(n_nodes):
            if n_nodes < 2:
                return self.max_neighbor_radius
            gamma = self.max_neighbor_radius
            val = (math.log(n_nodes) / n_nodes)**(1/dim)
            r = gamma * val
            return max(min(r, self.max_neighbor_radius), self.min_neighbor_radius)

        # --- Basic spatial grid for neighbor search ---
        class SpatialGrid:
            def __init__(self, domain, cell_size):
                self.domain = domain
                self.cell_size = cell_size
                self.dim = len(domain)
                self.cells = {}
            def _cell_coords(self, p):
                return tuple(int(p[d] // self.cell_size) for d in range(self.dim))
            def add_node(self, node):
                cc = self._cell_coords(node.position)
                if cc not in self.cells:
                    self.cells[cc] = []
                self.cells[cc].append(node)
            def neighbor_candidates(self, p, radius):
                ci = self._cell_coords(p)
                r_cells = int(math.ceil(radius / self.cell_size))
                neighbors = []
                # Iterate over neighboring cells in grid
                def expand_coords(ci, r_cells):
                    if self.dim == 2:
                        for dx in range(-r_cells, r_cells+1):
                            for dy in range(-r_cells, r_cells+1):
                                yield (ci[0]+dx, ci[1]+dy)
                    elif self.dim == 3:
                        for dx in range(-r_cells,r_cells+1):
                            for dy in range(-r_cells,r_cells+1):
                                for dz in range(-r_cells,r_cells+1):
                                    yield (ci[0]+dx, ci[1]+dy, ci[2]+dz)
                    else:
                        return [] # Not supported
                for c in expand_coords(ci, r_cells):
                    if c in self.cells:
                        neighbors.extend(self.cells[c])
                return neighbors

        # Initialization
        root = Node(start_pos)
        nodes.append(root)
        edges = []
        grid_cell_size = self.max_neighbor_radius  # Reasonable cell size for approximate neighbors
        spatial_grid = SpatialGrid(bounds, grid_cell_size)
        spatial_grid.add_node(root)

        best_goal_cost = float('inf')
        best_goal_node = None

        # Early stopping parameters
        min_improvement_cost = 1e-4
        no_improve_count = 0
        max_no_improve = 1500

        for it in range(self.max_iter):
            cur_time = time.monotonic()
            if cur_time - start_time > time_limit:
                break

            # Sample point
            if best_goal_node is not None:
                # After first solution, sample informed
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos
                else:
                    sample = sample_informed(best_goal_cost, start_pos, goal_pos)
            else:
                # Before first solution sample uniformly with some goal bias
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos
                else:
                    sample = tuple(random.uniform(0, bounds[d]) for d in range(dim))

            # Nearest node search by linear - fallback for safety if spatial grid empty
            def nearest_node(p):
                best = nodes[0]
                best_d = dist(p, best.position)
                for n in nodes[1:]:
                    d_ = dist(p, n.position)
                    if d_ < best_d:
                        best = n
                        best_d = d_
                return best, best_d

            nearest, _ = nearest_node(sample)

            # Steer new node position
            new_pos = steer(nearest.position, sample, self.step_size)
            if not is_free_node(new_pos):
                continue
            if not is_free_edge(nearest.position, new_pos):
                continue

            # Adaptive radius neighborhood
            radius = radius_adaptive(len(nodes))

            # Retrieve candidate neighbors from spatial grid
            candidates = spatial_grid.neighbor_candidates(new_pos, radius)
            # Filter candidates strictly within radius (squared distances)
            radius_sq = radius * radius
            neighbors = []
            for c in candidates:
                sum_sq = 0.0
                pos_c = c.position
                for i in range(dim):
                    diff = pos_c[i] - new_pos[i]
                    sum_sq += diff*diff
                    if sum_sq > radius_sq:
                        break
                if sum_sq <= radius_sq:
                    neighbors.append(c)

            # Select the best parent (lowest cost) among neighbors + nearest
            best_parent = nearest
            min_cost = nearest.cost + dist(nearest.position, new_pos)
            for nb in neighbors:
                tentative = nb.cost + dist(nb.position, new_pos)
                if tentative < min_cost and is_free_edge(nb.position, new_pos):
                    best_parent = nb
                    min_cost = tentative

            new_node = Node(new_pos)
            new_node.update_parent(best_parent, min_cost)
            nodes.append(new_node)
            edges.append((best_parent, new_node))
            spatial_grid.add_node(new_node)

            # Rewire neighbors with better cost through new_node
            for nb in neighbors:
                if nb is best_parent:
                    continue
                alt_cost = new_node.cost + dist(new_node.position, nb.position)
                if alt_cost + 1e-8 < nb.cost and is_free_edge(new_node.position, nb.position):
                    if nb.parent is not None:
                        try:
                            edges.remove((nb.parent, nb))
                        except ValueError:
                            pass
                    nb.update_parent(new_node, alt_cost)
                    edges.append((new_node, nb))

            # Goal check and connect
            dist_to_goal = dist(new_pos, goal_pos)
            if dist_to_goal <= self.step_size and is_free_node(goal_pos) and is_free_edge(new_pos, goal_pos):
                goal_node = Node(goal_pos)
                goal_cost = new_node.cost + dist_to_goal
                goal_node.update_parent(new_node, goal_cost)
                nodes.append(goal_node)
                edges.append((new_node, goal_node))

                if goal_cost + 1e-8 < best_goal_cost:
                    improved = best_goal_cost - goal_cost
                    best_goal_cost = goal_cost
                    best_goal_node = goal_node
                    no_improve_count = 0
                else:
                    improved = 0.0

                # Early stopping: break if cost improvement small or solution sufficient
                if improved < min_improvement_cost:
                    break

            # Track no improvement streak and possibly early terminate
            if best_goal_node is not None:
                no_improve_count += 1
                if no_improve_count > max_no_improve:
                    break

        # Extract path from best goal node or closest node
        extracted_path: List[Tuple[float, ...]] = []
        success_state = False
        if best_goal_node is not None:
            success_state = True
            current = best_goal_node
        else:
            # Find node closest to goal to extract partial path
            min_d = float('inf')
            current = None
            for n in nodes:
                d_ = dist(n.position, goal_pos)
                if d_ < min_d:
                    min_d = d_
                    current = n
            if current is None:
                current = root

        while current is not None:
            extracted_path.append(current.position)
            current = current.parent
        extracted_path.reverse()

        # Post-process shortcutting to smooth and shorten path
        def shortcut_path(path: List[Tuple[float, ...]]) -> List[Tuple[float, ...]]:
            if len(path) <= 2:
                return path[:]
            improved = True
            new_path = path[:]
            max_iter = 200
            iteration = 0
            while improved and iteration < max_iter and len(new_path) > 2:
                improved = False
                iteration += 1
                i = 0
                while i < len(new_path) - 2:
                    p1 = new_path[i]
                    p3 = new_path[i+2]
                    if is_free_edge(p1, p3):
                        del new_path[i+1]
                        improved = True
                    else:
                        i += 1
            return new_path

        extracted_path = shortcut_path(extracted_path)

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges,
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self,
                 max_iter=10000,
                 step_size=5.0,
                 goal_sample_rate=0.05,
                 max_no_improve=100,
                 post_opt_iters=300,
                 improve_tol=1e-6,
                 time_limit_sec=30.0,
                 gamma_rrt_star=40.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.max_no_improve = max_no_improve
        self.post_opt_iters = post_opt_iters
        self.improve_tol = improve_tol
        self.time_limit_sec = time_limit_sec
        self.gamma_rrt_star = gamma_rrt_star

    def plan(self, map) -> 'PlannerResult':
        import time, math, random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def dist(a, b):
            return math.dist(a, b)

        def steer(fr, to):
            d = dist(fr, to)
            if d <= self.step_size:
                return to
            r = self.step_size / d
            return tuple(fr[i] + (to[i] - fr[i]) * r for i in range(dim))

        def within_bounds(p):
            return all(0.0 <= p[i] <= bounds[i] for i in range(dim))

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w_, h_ = obs
                    px, py = pos
                    if x <= px <= x + w_ and y <= py <= y + h_:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2, resolution=1.0):
            length = dist(p1, p2)
            steps = max(1, int(length / resolution))
            for i in range(steps + 1):
                interp = tuple(p1[j] + (p2[j] - p1[j]) * (i / steps) for j in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def uniform_sample():
            trials = 0
            while True:
                s = tuple(random.uniform(0, bounds[i]) for i in range(dim))
                if not is_in_obstacle(s):
                    return s
                trials += 1
                if trials > 1000:
                    return start_pos

        def informed_sample(start, goal, c_best, c_min):
            if c_best == float('inf') or c_min <= 1e-12:
                return uniform_sample()
            center = tuple((s + g) * 0.5 for s, g in zip(start, goal))
            a1 = tuple((g - s) / c_min for s, g in zip(start, goal))
            if dim == 2:
                a2 = (-a1[1], a1[0])
                basis = [a1, a2]
                r1 = c_best / 2.0
                r2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) / 2.0
                radii = [r1, r2]
            else:
                ref = (1.0, 0.0, 0.0) if abs(a1[0]) < 0.9 else (0.0, 1.0, 0.0)
                b2 = self._normalize(self._cross(a1, ref))
                if self._norm(b2) < 1e-12:
                    ref = (0.0, 0.0, 1.0)
                    b2 = self._normalize(self._cross(a1, ref))
                b3 = self._cross(a1, b2)
                basis = [a1, b2, b3]
                r1 = c_best / 2.0
                r_other = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) / 2.0
                radii = [r1, r_other, r_other]

            def sample_unit_ball(d):
                while True:
                    v = [random.gauss(0, 1) for _ in range(d)]
                    norm_ = math.sqrt(sum(x * x for x in v))
                    if norm_ > 1e-12:
                        v = [x / norm_ for x in v]
                        r = random.random() ** (1.0 / d)
                        return [x * r for x in v]

            u = sample_unit_ball(dim)
            mapped = [center[i] for i in range(dim)]
            for i in range(dim):
                for j in range(dim):
                    mapped[i] += basis[j][i] * radii[j] * u[j]
            mapped = tuple(min(max(mapped[i], 0.0), bounds[i]) for i in range(dim))
            if is_in_obstacle(mapped):
                return uniform_sample()
            return mapped

        def neighbor_radius(n_nodes):
            if n_nodes < 2:
                return max(2.0, self.step_size * 2.0)
            val = self.gamma_rrt_star * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            max_r = max(bounds) * 0.25
            min_r = max(2.0, self.step_size)
            return max(min_r, min(max_r, val))

        def nearest(nodes_list, p):
            min_node = None
            min_d = float('inf')
            for n in nodes_list:
                d_ = dist(n.position, p)
                if d_ < min_d:
                    min_d = d_
                    min_node = n
            return min_node

        def near_nodes(nodes_list, p, radius):
            r_sq = radius * radius
            return [n for n in nodes_list if dist(n.position, p) <= radius]

        def add_node_rrt_star(tree, pos, nodes_all, edges_list):
            r = neighbor_radius(len(tree))
            neighbors = near_nodes(tree, pos, r)
            possible_parents = [n for n in neighbors if not is_edge_in_obstacle(n.position, pos)]
            if not possible_parents:
                p = nearest(tree, pos)
                if not p or is_edge_in_obstacle(p.position, pos):
                    return None, []
                possible_parents = [p]
            best_parent = min(possible_parents, key=lambda n: n.cost + dist(n.position, pos))
            new_cost = best_parent.cost + dist(best_parent.position, pos)
            new_node = Node(pos, parent=None, cost=new_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            nodes_all.append(new_node)
            edges_list.append((best_parent, new_node))
            return new_node, neighbors

        def propagate_cost_to_children(node):
            for child in node.children:
                old_cost = child.cost
                child.cost = node.cost + dist(node.position, child.position)
                if child.cost + 1e-12 < old_cost:
                    propagate_cost_to_children(child)

        def rewire(tree, new_node, neighbors, edges_list):
            for nb in neighbors:
                if nb is new_node:
                    continue
                if is_edge_in_obstacle(new_node.position, nb.position):
                    continue
                new_cost = new_node.cost + dist(new_node.position, nb.position)
                if new_cost + 1e-12 < nb.cost:
                    if nb.parent:
                        nb.parent.remove_child(nb)
                        try:
                            edges_list.remove((nb.parent, nb))
                        except ValueError:
                            pass
                    new_node.add_child(nb)
                    nb.cost = new_cost
                    edges_list.append((new_node, nb))
                    propagate_cost_to_children(nb)

        def can_connect(n1, n2):
            return not is_edge_in_obstacle(n1.position, n2.position) and n1.valid and n2.valid

        def stitch_path(n1, n2, n1_is_start):
            path1 = n1.path_from_root()
            path2 = n2.path_from_root()
            # Remove duplicate connection node if any
            if path1 and path2 and path1[-1] == path2[-1]:
                combined = path1 + path2[-2::-1]
            else:
                combined = path1 + path2[::-1]
            return combined if n1_is_start else combined[::-1]

        def shortcut_path(path):
            if len(path) < 3:
                return path
            smoothed = [path[0]]
            i = 0
            while i < len(path) - 1:
                j = len(path) - 1
                while j > i + 1:
                    if not is_edge_in_obstacle(path[i], path[j]):
                        break
                    j -= 1
                smoothed.append(path[j])
                i = j
            return smoothed

        start_time = time.monotonic()
        tree_start = [Node(start_pos, cost=0.0)]
        tree_goal = [Node(goal_pos, cost=0.0)]
        nodes = tree_start + tree_goal[:]
        edges = []

        best_path = []
        best_cost = float('inf')
        found_first = False
        post_no_improve = 0
        post_iter = 0
        c_min = dist(start_pos, goal_pos)

        for it in range(self.max_iter):
            if time.monotonic() - start_time > self.time_limit_sec:
                break

            if it % 2 == 0:
                curr_tree = tree_start
                other_tree = tree_goal
                curr_is_start = True
            else:
                curr_tree = tree_goal
                other_tree = tree_start
                curr_is_start = False

            # Sampling
            if found_first:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if curr_is_start else start_pos
                else:
                    sample = informed_sample(start_pos, goal_pos, best_cost, c_min)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if curr_is_start else start_pos
                else:
                    sample = uniform_sample()

            nearest_node = nearest(curr_tree, sample)
            new_pos = steer(nearest_node.position, sample)

            if (not within_bounds(new_pos)) or is_in_obstacle(new_pos) or is_edge_in_obstacle(nearest_node.position, new_pos):
                if found_first:
                    post_no_improve += 1
                    post_iter += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break
                continue

            new_node, neighbor_nodes = add_node_rrt_star(curr_tree, new_pos, nodes, edges)
            if new_node is None:
                if found_first:
                    post_no_improve += 1
                    post_iter += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break
                continue

            rewire(curr_tree, new_node, neighbor_nodes, edges)

            # Attempt greedy connection
            nearest_other = nearest(other_tree, new_node.position)
            connected_other = None
            if dist(nearest_other.position, new_node.position) <= self.step_size and can_connect(nearest_other, new_node):
                final_node = Node(new_node.position, cost=nearest_other.cost + dist(nearest_other.position, new_node.position))
                nearest_other.add_child(final_node)
                other_tree.append(final_node)
                nodes.append(final_node)
                edges.append((nearest_other, final_node))
                connected_other = final_node
            else:
                current = nearest_other
                tries = 0
                max_steps = 20
                while tries < max_steps:
                    tries += 1
                    step_pos = steer(current.position, new_node.position)
                    if (not within_bounds(step_pos)) or is_in_obstacle(step_pos) or is_edge_in_obstacle(current.position, step_pos):
                        break
                    new_other_node, neighbors_other = add_node_rrt_star(other_tree, step_pos, nodes, edges)
                    if new_other_node is None:
                        break
                    rewire(other_tree, new_other_node, neighbors_other, edges)
                    current = new_other_node
                    if dist(current.position, new_node.position) <= self.step_size:
                        if can_connect(current, new_node):
                            final_node = Node(new_node.position, cost=current.cost + dist(current.position, new_node.position))
                            current.add_child(final_node)
                            other_tree.append(final_node)
                            nodes.append(final_node)
                            edges.append((current, final_node))
                            connected_other = final_node
                        break

            if connected_other is not None:
                candidate_path = stitch_path(new_node, connected_other, curr_is_start)
                candidate_cost = 0.0
                for i in range(len(candidate_path) - 1):
                    candidate_cost += dist(candidate_path[i], candidate_path[i + 1])

                if candidate_cost + self.improve_tol < best_cost:
                    best_cost = candidate_cost
                    best_path = candidate_path
                    found_first = True
                    post_no_improve = 0
                    post_iter = 0
                else:
                    if found_first:
                        post_no_improve += 1
                    post_iter += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break
            else:
                if found_first:
                    post_no_improve += 1
                    post_iter += 1
                    if post_iter >= self.post_opt_iters or post_no_improve >= self.max_no_improve:
                        break

        if best_path:
            best_path = shortcut_path(best_path)
            return PlannerResult(
                success=True,
                path=best_path,
                nodes=nodes,
                edges=edges
            )
        else:
            return PlannerResult(
                success=False,
                path=[],
                nodes=nodes,
                edges=edges
            )

    def _cross(self, a, b):
        ax, ay, az = a
        bx, by, bz = b
        return (ay * bz - az * by, az * bx - ax * bz, ax * by - ay * bx)

    def _norm(self, v):
        return math.sqrt(sum(x * x for x in v))

    def _normalize(self, v):
        n = self._norm(v)
        if n < 1e-12:
            return v
        return tuple(x / n for x in v)
