class Planner:
    def __init__(self, max_iter=5000, step_size=5.0):
        self.max_iter = max_iter
        self.step_size = step_size

    def plan(self, map):
        bounds = map.size
        start = map.start
        goal = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        start_tree = [Node(start)]
        goal_tree = [Node(goal)]

        nodes = start_tree + goal_tree
        edges = []

        for i in range(self.max_iter):
            tree_a, tree_b = (start_tree, goal_tree) if i % 2 == 0 else (goal_tree, start_tree)

            rand_point = self._sample_free(bounds, obstacles, is_3d)
            new_node_a = self._extend(tree_a, rand_point, obstacles, is_3d, nodes, edges)
            if new_node_a is None:
                continue

            new_node_b = self._connect(tree_b, new_node_a, obstacles, is_3d, nodes, edges)
            if new_node_b is not None:
                path_start = new_node_a.path_from_root()
                path_goal = new_node_b.path_from_root()
                # Avoid duplicating connecting node
                if path_start[-1] == path_goal[-1]:
                    full_path = path_start + path_goal[-2::-1]
                else:
                    full_path = path_start + path_goal[::-1]
                return PlannerResult(
                    success=True,
                    path=full_path,
                    nodes=nodes,
                    edges=edges
                )

        return PlannerResult(
            success=False,
            path=[],
            nodes=nodes,
            edges=edges
        )

    def _sample_free(self, bounds, obstacles, is_3d):
        while True:
            if is_3d:
                p = tuple(random.uniform(0, bounds[d]) for d in range(3))
            else:
                p = tuple(random.uniform(0, bounds[d]) for d in range(2))
            if not self._is_in_obstacle(p, obstacles, is_3d):
                return p

    def _nearest(self, tree, point):
        return min(tree, key=lambda node: self._distance(node.position, point))

    def _steer(self, from_pos, to_pos):
        dist = self._distance(from_pos, to_pos)
        if dist <= self.step_size:
            return to_pos
        ratio = self.step_size / dist
        return tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(len(from_pos)))

    def _extend(self, tree, point, obstacles, is_3d, nodes, edges):
        nearest = self._nearest(tree, point)
        new_pos = self._steer(nearest.position, point)
        if (self._within_bounds(new_pos, edges, nodes) and
            not self._is_in_obstacle(new_pos, obstacles, is_3d) and
            not self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d)):
            new_node = Node(new_pos)
            nearest.add_child(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((nearest, new_node))
            return new_node
        return None

    def _connect(self, tree, target_node, obstacles, is_3d, nodes, edges):
        current = self._nearest(tree, target_node.position)
        while True:
            new_pos = self._steer(current.position, target_node.position)
            if not self._within_bounds(new_pos, edges, nodes):
                return None
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(current.position, new_pos, obstacles, is_3d):
                return None
            new_node = Node(new_pos)
            current.add_child(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((current, new_node))
            if self._distance(new_node.position, target_node.position) <= self.step_size:
                if not self._is_edge_in_obstacle(new_node.position, target_node.position, obstacles, is_3d):
                    final_node = Node(target_node.position)
                    new_node.add_child(final_node)
                    tree.append(final_node)
                    nodes.append(final_node)
                    edges.append((new_node, final_node))
                    return final_node
                else:
                    return None
            current = new_node

    def _within_bounds(self, pos, edges, nodes):
        dims = len(pos)
        if hasattr(edges, '__len__') and nodes:
            # edges and nodes arguments don't provide bounds, re-checking not needed
            # Instead, handle bounds from map.size (caller)
            # Simplify to true since map bounds are enforced in sampling and steering
            return True
        # fallback check (shouldn't occur)
        return all(0.0 <= pos[d] <= 10000.0 for d in range(dims))  # large bound

    def _distance(self, a, b):
        return math.sqrt(sum((a[d]-b[d])**2 for d in range(len(a))))

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, p1, p2, obstacles, is_3d, resolution=1.0):
        dist = self._distance(p1, p2)
        steps = max(1, int(dist / resolution))
        for i in range(steps + 1):
            interp = tuple(p1[d] + (p2[d] - p1[d]) * i / steps for d in range(len(p1)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

