class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        start_tree = [Node(start_position)]
        goal_tree = [Node(goal_position)]
        nodes = []
        edges = []
        success_state = False
        extracted_path = []

        for iter in range(self.max_iter):
            rand = self._sample_free(bounds, obstacles, is_3d)
            for tree_a, tree_b in [(start_tree, goal_tree), (goal_tree, start_tree)]:
                nearest = self._nearest(tree_a, rand)
                new_pos = self._steer(nearest.position, rand, self.step_size)
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    continue
                if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
                    continue
                new_node = Node(new_pos, parent=nearest, cost=nearest.cost + self._distance(nearest.position, new_pos))
                nearest.add_child(new_node)
                tree_a.append(new_node)
                nodes.append(new_node)
                edges.append((nearest, new_node))

                connect_node = self._nearest(tree_b, new_pos)
                if self._connect_trees(new_node, connect_node, obstacles, is_3d, tree_b, nodes, edges):
                    success_state = True
                    extracted_path = self._extract_path(new_node, connect_node)
                    return PlannerResult(True, extracted_path, start_tree + goal_tree, edges)

        return PlannerResult(False, [], start_tree + goal_tree, edges)

    def _sample_free(self, bounds, obstacles, is_3d):
        while True:
            if is_3d:
                point = tuple(random.uniform(0, bounds[i]) for i in range(3))
            else:
                point = tuple(random.uniform(0, bounds[i]) for i in range(2))
            if not self._is_in_obstacle(point, obstacles, is_3d):
                return point

    def _nearest(self, tree, point):
        return min(tree, key=lambda node: self._distance(node.position, point))

    def _steer(self, from_pos, to_pos, step_size):
        dist = self._distance(from_pos, to_pos)
        if dist <= step_size:
            return to_pos
        ratio = step_size / dist
        return tuple(from_pos[i] + (to_pos[i] - from_pos[i]) * ratio for i in range(len(from_pos)))

    def _connect_trees(self, node_a, node_b, obstacles, is_3d, tree, nodes, edges):
        from_pos = node_a.position
        to_pos = node_b.position
        if self._is_edge_in_obstacle(from_pos, to_pos, obstacles, is_3d):
            return False
        curr = node_b
        path = []
        while True:
            new_pos = self._steer(curr.position, from_pos, self.step_size)
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return False
            if self._is_edge_in_obstacle(curr.position, new_pos, obstacles, is_3d):
                return False
            new_node = Node(new_pos, parent=curr, cost=curr.cost + self._distance(curr.position, new_pos))
            curr.add_child(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((curr, new_node))
            if self._distance(new_pos, from_pos) < self.step_size:
                new_node_final = Node(from_pos, parent=new_node, cost=new_node.cost + self._distance(new_pos, from_pos))
                new_node.add_child(new_node_final)
                tree.append(new_node_final)
                nodes.append(new_node_final)
                edges.append((new_node, new_node_final))
                return True
            curr = new_node

    def _extract_path(self, node_a, node_b):
        path_a = []
        while node_a:
            path_a.append(node_a.position)
            node_a = node_a.parent
        path_b = []
        while node_b:
            path_b.append(node_b.position)
            node_b = node_b.parent
        return list(reversed(path_a)) + path_b

    def _distance(self, a, b):
        return math.dist(a, b)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
