class Planner:
    def __init__(self, max_iter=4000, max_step=7.0, min_step=1.0):
        self.max_iter = max_iter
        self.max_step = max_step
        self.min_step = min_step

    def plan(self, map):
        bounds = map.size
        start = map.start
        goal = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        start_tree = [Node(start, None, 0.0)]
        goal_tree = [Node(goal, None, 0.0)]
        nodes = start_tree + goal_tree
        edges = []

        for i in range(self.max_iter):
            tree_a, tree_b = (start_tree, goal_tree) if (i % 2 == 0) else (goal_tree, start_tree)
            rand = self._sample_free(bounds, obstacles, is_3d)
            new_node_a = self._extend(tree_a, rand, obstacles, is_3d, nodes, edges)
            if new_node_a is None:
                continue
            new_node_b = self._connect(tree_b, new_node_a, obstacles, is_3d, nodes, edges)
            if new_node_b:
                path_start = new_node_a.path_from_root()
                path_goal = new_node_b.path_from_root()
                if path_start[-1] == path_goal[-1]:
                    full_path = path_start + path_goal[-2::-1]
                else:
                    full_path = path_start + path_goal[::-1]
                return PlannerResult(True, full_path, nodes, edges)

        return PlannerResult(False, [], nodes, edges)

    def _sample_free(self, bounds, obstacles, is_3d):
        while True:
            if is_3d:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(3))
            else:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(2))
            if not self._is_in_obstacle(pt, obstacles, is_3d):
                return pt

    def _nearest(self, tree, point):
        return min(tree, key=lambda node: self._distance(node.position, point))

    def _steer(self, from_pos, to_pos, step_size):
        dist = self._distance(from_pos, to_pos)
        if dist <= step_size:
            return to_pos
        ratio = step_size / dist
        return tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(len(from_pos)))

    def _extend(self, tree, point, obstacles, is_3d, nodes, edges):
        nearest = self._nearest(tree, point)
        dist = self._distance(nearest.position, point)
        step = max(self.min_step, min(self.max_step, dist))
        new_pos = self._steer(nearest.position, point, step)
        if not self._valid_node_and_edge(nearest.position, new_pos, obstacles, is_3d):
            return None
        cost = nearest.cost + self._distance(nearest.position, new_pos)
        new_node = Node(new_pos, nearest, cost)
        nearest.add_child(new_node)
        tree.append(new_node)
        nodes.append(new_node)
        edges.append((nearest, new_node))
        return new_node

    def _connect(self, tree, target_node, obstacles, is_3d, nodes, edges):
        current = self._nearest(tree, target_node.position)
        while True:
            dist = self._distance(current.position, target_node.position)
            if dist <= self.min_step:
                if self._valid_node_and_edge(current.position, target_node.position, obstacles, is_3d):
                    final_node = Node(target_node.position, current, current.cost + dist)
                    current.add_child(final_node)
                    tree.append(final_node)
                    nodes.append(final_node)
                    edges.append((current, final_node))
                    return final_node
                else:
                    return None
            step = max(self.min_step, min(self.max_step, dist))
            new_pos = self._steer(current.position, target_node.position, step)
            if not self._valid_node_and_edge(current.position, new_pos, obstacles, is_3d):
                return None
            cost = current.cost + self._distance(current.position, new_pos)
            new_node = Node(new_pos, current, cost)
            current.add_child(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((current, new_node))
            current = new_node

    def _valid_node_and_edge(self, from_pos, to_pos, obstacles, is_3d):
        return (not self._is_in_obstacle(to_pos, obstacles, is_3d)) and (not self._is_edge_in_obstacle(from_pos, to_pos, obstacles, is_3d))

    def _distance(self, a, b):
        return sum((a[d] - b[d]) ** 2 for d in range(len(a))) ** 0.5

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, p1, p2, obstacles, is_3d, resolution=0.75):
        dist = self._distance(p1, p2)
        steps = max(1, int(dist / resolution))
        for i in range(steps + 1):
            interp = tuple(p1[d] + (p2[d] - p1[d]) * i / steps for d in range(len(p1)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

