            connected = self._try_connect(tree_b, new_node, obstacles, dim)
            if connected:
                path_start = new_node.path_from_root()
                path_goal = connected.path_from_root()
                if path_start[-1] == path_goal[-1]:
                    path = path_start + path_goal[-2::-1]
                else:
                    path = path_start + path_goal[::-1]
                return PlannerResult(True, path, nodes, edges)

        return PlannerResult(False, [], nodes, edges)

    def _sample_free(self, bounds, obstacles, dim):
        import random
        while True:
            pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
            if not self._is_in_obstacle(pt, obstacles, dim):
                return pt

    def _nearest(self, tree, point):
        return min(tree, key=lambda n: self._distance(n.position, point))

    def _steer(self, from_pos, to_pos):
        dist = self._distance(from_pos, to_pos)
