        nearest = self._nearest(tree, point)
        new_pos = self._steer(nearest.position, point)
        if not self._valid_move(nearest.position, new_pos, obstacles, dim):
            return None
        cost = nearest.cost + self._distance(nearest.position, new_pos)
        new_node = Node(new_pos, nearest, cost)
        nearest.add_child(new_node)
        tree.append(new_node)
        return new_node

    def _try_connect(self, tree, target_node, obstacles, dim):
        nearest = self._nearest(tree, target_node.position)
        if not self._valid_move(nearest.position, target_node.position, obstacles, dim):
            return None
        cost = nearest.cost + self._distance(nearest.position, target_node.position)
        new_node = Node(target_node.position, nearest, cost)
        nearest.add_child(new_node)
        tree.append(new_node)
        return new_node

    def _valid_move(self, from_pos, to_pos, obstacles, dim):
        return (not self._is_in_obstacle(to_pos, obstacles, dim)) and (not self._edge_in_obstacle(from_pos, to_pos, obstacles, dim))

    def _distance(self, a, b):
        return sum((a[d]-b[d])**2 for d in range(len(a)))**0.5

    def _is_in_obstacle(self, pos, obstacles, dim):
        if dim == 3:
            x, y, z = pos
            for ox, oy, oz, w, h, d in obstacles:
                if ox <= x <= ox+w and oy <= y <= oy+h and oz <= z <= oz+d:
                    return True
        else:
            x, y = pos
            for ox, oy, w, h in obstacles:
                if ox <= x <= ox+w and oy <= y <= oy+h:
                    return True
        return False

    def _edge_in_obstacle(self, p1, p2, obstacles, dim, res=1.0):
        dist = self._distance(p1, p2)
        steps = max(1, int(dist / res))
        for i in range(steps+1):
            interp = tuple(p1[d] + (p2[d] - p1[d]) * i / steps for d in range(dim))
            if self._is_in_obstacle(interp, obstacles, dim):
