class Planner:
    def __init__(self, max_iter: int = 3000, step_size: float = 4.0, gamma_rrt_star: float = 50.0, max_radius: float = 40.0, goal_sample_rate: float = 0.05):
        self.max_iter = max_iter
        self.step_size = step_size
        self.gamma_rrt_star = gamma_rrt_star
        self.max_radius = max_radius
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> "PlannerResult":
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        tree_start = [Node(start_pos)]
        tree_goal = [Node(goal_pos)]
        nodes = tree_start + tree_goal
        edges: List[Tuple[Node,Node]] = []

        def sample_free():
            if random.random() < self.goal_sample_rate:
                return goal_pos
            while True:
                s = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(s, obstacles, is_3d):
                    return s

        def nearest(tree: List[Node], point: Tuple[float, ...]) -> Node:
            return min(tree, key=lambda n: math.dist(n.position, point))

        def steer(from_pos, to_pos) -> Tuple[float, ...]:
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dim))

        def neighbor_radius(n_nodes: int) -> float:
            if n_nodes < 2:
                return self.step_size * 2.0
            r = self.gamma_rrt_star * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return min(max(self.step_size*2.0, r), self.max_radius)

        def near_nodes(tree: List[Node], pos: Tuple[float,...]) -> List[Node]:
            r = neighbor_radius(len(tree))
            return [n for n in tree if math.dist(n.position, pos) <= r]

        def can_connect(from_p, to_p) -> bool:
            return (not self._is_in_obstacle(to_p, obstacles, is_3d)
                    and not self._is_edge_in_obstacle(from_p, to_p, obstacles, is_3d))

        def add_node_with_parent(tree: List[Node], new_pos: Tuple[float, ...]) -> Optional[Node]:
            near = near_nodes(tree, new_pos)
            candidates = [n for n in near if can_connect(n.position, new_pos)]
            if not candidates:
                parent = nearest(tree, new_pos)
                if not can_connect(parent.position, new_pos):
                    return None
                best_parent = parent
                best_cost = parent.cost + math.dist(parent.position, new_pos)
            else:
                best_parent, best_cost = min(
                    ((n, n.cost + math.dist(n.position, new_pos)) for n in candidates),
                    key=lambda x: x[1]
                )
            new_node = Node(new_pos, parent=None, cost=best_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))
            return new_node

        def rewire(node: Node, neighbors: List[Node]):
            for nb in neighbors:
                if nb is node:
                    continue
                if can_connect(node.position, nb.position):
                    new_cost = node.cost + math.dist(node.position, nb.position)
                    if new_cost + 1e-12 < nb.cost:
                        if nb.parent is not None:
                            try:
                                edges.remove((nb.parent, nb))
                            except ValueError:
                                pass
                            if nb in nb.parent.children:
                                nb.parent.children.remove(nb)
                        node.add_child(nb)
                        nb.cost = new_cost
                        edges.append((node, nb))

        def extend(tree: List[Node], target_point: Tuple[float, ...], other_tree: List[Node]) -> Optional[Node]:
            nearest_node = nearest(tree, target_point)
            new_pos = steer(nearest_node.position, target_point)
            if not can_connect(nearest_node.position, new_pos):
                return None
            new_node = add_node_with_parent(tree, new_pos)
            if not new_node:
                return None
            near = near_nodes(tree, new_pos)
            rewire(new_node, near)
            # Attempt connect with other_tree greedily
            connect_node = attempt_connect(other_tree, new_node)
            if connect_node:
                return (new_node, connect_node)
            return (new_node, None)

        def attempt_connect(tree: List[Node], target_node: Node) -> Optional[Node]:
            nearest_node = nearest(tree, target_node.position)
            current = nearest_node
            while True:
                new_pos = steer(current.position, target_node.position)
                if not can_connect(current.position, new_pos):
                    return None
                new_node = add_node_with_parent(tree, new_pos)
                if new_node is None:
                    return None
                near = near_nodes(tree, new_pos)
                rewire(new_node, near)
                current = new_node
                if math.dist(current.position, target_node.position) <= self.step_size:
                    if can_connect(current.position, target_node.position):
                        final_node = Node(target_node.position, parent=None, cost=current.cost + math.dist(current.position, target_node.position))
                        current.add_child(final_node)
                        tree.append(final_node)
                        nodes.append(final_node)
                        edges.append((current, final_node))
                        return final_node
                    else:
                        return None

        best_path: List[Tuple[float,...]] = []
        solution_found = False

        for _ in range(self.max_iter):
            sample = sample_free()
            result = extend(tree_start, sample, tree_goal)
            if result is None:
                tree_start, tree_goal = tree_goal, tree_start
                continue
            new_node, connect_node = result
            if connect_node:
                path_start = new_node.path_from_root()
                path_goal = connect_node.path_from_root()
                if path_start[-1] == path_goal[-1]:
                    path_goal = path_goal[:-1]
                best_path = path_start + path_goal[::-1]
                solution_found = True
                break
            tree_start, tree_goal = tree_goal, tree_start

        return PlannerResult(success=solution_found, path=best_path, nodes=nodes, edges=edges)

    def _is_in_obstacle(self, pos: Tuple[float,...], obstacles, is_3d: bool) -> bool:
        for obs in obstacles:
            if is_3d:
                x,y,z,w,h,d = obs
                px,py,pz = pos
                if x <= px <= x+w and y <= py <= y+h and z <= pz <= z+d:
                    return True
            else:
                x,y,w,h = obs
                px,py = pos
                if x <= px <= x+w and y <= py <= y+h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_p: Tuple[float,...], to_p: Tuple[float,...], obstacles, is_3d: bool, resolution: float=1.0) -> bool:
        dist = math.dist(from_p, to_p)
        steps = max(1, int(dist / resolution))
        for i in range(steps+1):
            interm = tuple(from_p[d] + (to_p[d] - from_p[d]) * i/steps for d in range(len(from_p)))
            if self._is_in_obstacle(interm, obstacles, is_3d):
                return True
        return False

