        start_time = time.time()

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                return to_p
            r = self.step_size / d
            return tuple(from_p[i] + (to_p[i] - from_p[i]) * r for i in range(dim))

        def nearest(tree, point):
            return min(tree, key=lambda n: dist(n.position, point))

        def neighbor_radius(n_nodes):
            if n_nodes < 2:
                return self.step_size * 2.0
            r = self.gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            max_r = max(bounds) * 0.30
            min_r = max(10.0, self.step_size * 2.0)
            return max(min_r, min(max_r, r))

        def near_nodes(tree, point, radius):
            return [n for n in tree if dist(n.position, point) <= radius]

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w, h = obs
                    px, py = pos
                    if x <= px <= x + w and y <= py <= y + h:
                        return True
            return False

        def is_edge_in_obstacle(a, b, resolution=1.0):
            length = dist(a, b)
            steps = max(1, int(length / resolution))
            for i in range(steps + 1):
                interp = tuple(a[d] + (b[d] - a[d]) * (i / steps) for d in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def sample_uniform():
            while True:
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p

        def sample_unit_ball():
            while True:
                v = [random.gauss(0, 1) for _ in range(dim)]
                nrm = math.sqrt(sum(x*x for x in v))
                if nrm > 1e-12:
                    v = [x / nrm for x in v]
                    r = random.random() ** (1.0 / dim)
                    return tuple(v[i]*r for i in range(dim))

        # Informed sampling inside the ellipsoid (or uniform if no solution)
        def sample_informed(c_best):
            if not found_solution or c_best == float("inf") or c_min <= 1e-12:
                return sample_uniform()
            r1 = c_best / 2.0
            r_other_sq = max(c_best * c_best - c_min * c_min, 0.0)
            r_other = math.sqrt(r_other_sq) / 2.0
            center = tuple((start_pos[d] + goal_pos[d]) / 2.0 for d in range(dim))
            unit_vec = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))

            # Construct orthonormal basis for the ellipsoid axes
            if dim == 2:
                # Perp vector in 2D
                perp_vec = (-unit_vec[1], unit_vec[0])
                basis = [unit_vec, perp_vec]
                radii = [r1, r_other]
            else:  # 3D
                # Create two perpendicular vectors using cross products
                def cross(u,v):
                    return (u[1]*v[2]-u[2]*v[1], u[2]*v[0]-u[0]*v[2], u[0]*v[1]-u[1]*v[0])
                def norm(v):
                    return math.sqrt(sum(x*x for x in v))
                def normalize(v):
                    n = norm(v)
                    if n < 1e-12:
                        return v
                    return tuple(x/n for x in v)
                ref = (1.0, 0.0, 0.0) if abs(unit_vec[0]) < 0.9 else (0.0, 1.0, 0.0)
                b2 = normalize(cross(unit_vec, ref))
                if norm(b2) < 1e-12:
                    ref = (0.0, 0.0, 1.0)
                    b2 = normalize(cross(unit_vec, ref))
                b3 = cross(unit_vec, b2)
                basis = [unit_vec, b2, b3]
                radii = [r1, r_other, r_other]

            u = sample_unit_ball()
            mapped = []
            for d in range(dim):
                val = center[d]
                for i in range(dim):
                    val += basis[i][d] * radii[i] * u[i]
                # Clamp to bounds
                val = max(0.0, min(bounds[d], val))
                mapped.append(val)
            return tuple(mapped)

        def add_node_with_best_parent(tree, pos):
            r = neighbor_radius(len(tree))
            neighbors = near_nodes(tree, pos, r)
            if not neighbors:
                # Use nearest if no neighbor within radius
                neighbors = [nearest(tree, pos)]
            candidates = [p for p in neighbors if not is_edge_in_obstacle(p.position, pos)]
            if not candidates:
                return None, []
            parent = min(candidates, key=lambda p: p.cost + dist(p.position, pos))
            cost_new = parent.cost + dist(parent.position, pos)
            if is_in_obstacle(pos):
                return None, []
            new_node = Node(pos)
            new_node.parent = parent
            new_node.cost = cost_new
            parent.add_child(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((parent, new_node))
            return new_node, neighbors

        def rewire(node, neighbors):
            for n in neighbors:
                if n is node:
                    continue
                if is_edge_in_obstacle(node.position, n.position):
                    continue
                new_cost = node.cost + dist(node.position, n.position)
                if new_cost + 1e-12 < n.cost:
                    if n.parent:
                        try:
                            edges.remove((n.parent, n))
                        except ValueError:
                            pass
                        if n in n.parent.children:
                            n.parent.children.remove(n)
                    node.add_child(n)
                    n.cost = new_cost
                    edges.append((node, n))

        def connect_trees(new_node, other_tree):
            nearest_to_new = nearest(other_tree, new_node.position)
            d = dist(new_node.position, nearest_to_new.position)
            if d > self.step_size * 2.5:
                return None
            if is_edge_in_obstacle(new_node.position, nearest_to_new.position):
                return None
            return nearest_to_new

        def extract_combined_path(n_start, n_goal, start_side=True):
            path_from_start = n_start.path_from_root()
            path_from_goal = n_goal.path_from_root()
            if path_from_start[-1] == path_from_goal[-1]:
                combined = path_from_start + path_from_goal[-2::-1]
            else:
                combined = path_from_start + path_from_goal[::-1]
            if start_side:
                return combined
            else:
                return combined[::-1]

        def path_cost(path):
            if not path or len(path) < 2:
                return 0.0
            c = 0.0
            for i in range(len(path)-1):
                c += dist(path[i], path[i+1])
            return c

        # Shortcut smoothing: tries to shortcut path pairs if collision free
        def shortcut_path(path, attempts=40):
            if len(path) < 3:
                return path[:]
            import random as rand
            path_smoothed = path[:]
            for _ in range(attempts):
                if len(path_smoothed) < 3:
                    break
                i = rand.randint(0, len(path_smoothed)-3)
                j = rand.randint(i+2, len(path_smoothed)-1)
                if j <= i+1:
                    continue
                if not is_edge_in_obstacle(path_smoothed[i], path_smoothed[j]):
                    # Shortcut possible, remove intermediate nodes
                    path_smoothed = path_smoothed[:i+1] + path_smoothed[j:]
            return path_smoothed

        # Main planning loop
        for it in range(self.max_iter):
            # Time limit check
            if time.time() - start_time > self.time_limit_sec:
                break

            # Alternate growth
            grow_tree = tree_a if it % 2 == 0 else tree_b
            other_tree = tree_b if it % 2 == 0 else tree_a
            start_side = (grow_tree == tree_a)

            sample = sample_informed(best_cost)
            nearest_node = nearest(grow_tree, sample)
            new_pos = steer(nearest_node.position, sample)

            if not within_bounds(new_pos):
                # Count post optimization if solution found
                if found_solution:
                    post_opt_count += 1
                    no_improve_count += 1
                    if post_opt_count >= self.post_opt_iters or no_improve_count >= self.max_no_improve:
                        break
                continue

            if is_in_obstacle(new_pos) or is_edge_in_obstacle(nearest_node.position, new_pos):
                if found_solution:
                    post_opt_count += 1
                    no_improve_count += 1
                    if post_opt_count >= self.post_opt_iters or no_improve_count >= self.max_no_improve:
                        break
                continue

            new_node, neighbors = add_node_with_best_parent(grow_tree, new_pos)
            if new_node is None:
                if found_solution:
                    post_opt_count += 1
                    no_improve_count += 1
                    if post_opt_count >= self.post_opt_iters or no_improve_count >= self.max_no_improve:
                        break
                continue

            rewire(new_node, neighbors)

            # Attempt connection to other tree
            connect_node = connect_trees(new_node, other_tree)
            if connect_node:
                # Join trees
                if connect_node.parent is None or new_node.parent is None:
                    # Should not happen, skip if roots
                    continue
                # Combine paths
                candidate_path = extract_combined_path(new_node, connect_node, start_side)
                candidate_cost = path_cost(candidate_path)
                # If improved path found
                if candidate_cost + 1e-7 < best_cost:
                    best_cost = candidate_cost
                    # Smooth path before recording
                    best_path = shortcut_path(candidate_path, attempts=50)
                    found_solution = True
                    no_improve_count = 0
                    post_opt_count = 0
                else:
                    if found_solution:
                        no_improve_count += 1
                        post_opt_count += 1
                        if post_opt_count >= self.post_opt_iters or no_improve_count >= self.max_no_improve:
                            break
            else:
                if found_solution:
                    no_improve_count += 1
                    post_opt_count += 1
                    if post_opt_count >= self.post_opt_iters or no_improve_count >= self.max_no_improve:
                        break

        return PlannerResult(
            success=found_solution,
            path=best_path if found_solution else [],
            nodes=nodes,
            edges=edges
        )

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True  # reserved for future collision checking updates

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        path.reverse()
        return path
class Planner:
    def __init__(self,
                 max_iter=6000,
                 step_size=4.0,
                 neighbor_radius=None,
                 post_opt_iters=600,
                 max_no_improve=150,
                 improve_tol=1e-5,
                 time_limit_sec=25.0,
                 shortcutting_trials=150):
        self.max_iter = max_iter
        self.step_size = step_size
        self.post_opt_iters = post_opt_iters
        self.max_no_improve = max_no_improve
        self.improve_tol = improve_tol
        self.time_limit_sec = time_limit_sec
        self.shortcutting_trials = shortcutting_trials
        # neighbor radius factor for RRT*
        self.gamma_rrt_star = 50.0
        self.neighbor_radius = neighbor_radius  # if None will compute dynamically

    def plan(self, map):
        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        obstacles = map.obstacles

        start_pos = map.start
        goal_pos = map.goal

        # Initialize data
        start_root = Node(start_pos, cost=0.0)
        goal_root = Node(goal_pos, cost=0.0)
        start_tree = [start_root]
        goal_tree = [goal_root]
        nodes = [start_root, goal_root]
        edges = []

        best_cost = float('inf')
        best_path = []
        c_min = math.dist(start_pos, goal_pos)

        found_solution = False
        post_iter = 0
        no_improve_count = 0

        start_time = time.time()

        def within_bounds(p):
            return all(0.0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                return to_p
            r = self.step_size / d
            return tuple(from_p[i] + (to_p[i] - from_p[i]) * r for i in range(dim))

        def neighbor_radius_fn(n_nodes):
            if self.neighbor_radius is not None:
                return self.neighbor_radius
            if n_nodes < 2:
                return self.step_size * 2.0
            r_theory = self.gamma_rrt_star * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            maxr = max(bounds) * 0.25
            return max(self.step_size * 2.0, min(r_theory, maxr))

        def nearest(tree, pt):
            mn = None
            md = float("inf")
            for n in tree:
                d = dist(n.position, pt)
                if d < md:
                    md = d
                    mn = n
            return mn

        def near_nodes(tree, pos, radius):
            result = []
            r_sq = radius * radius
            for n in tree:
                d_sq = sum((n.position[i] - pos[i]) ** 2 for i in range(dim))
                if d_sq <= r_sq:
                    result.append(n)
            return result

        def edge_collision_free(p1, p2):
            distance = dist(p1, p2)
            steps = max(2, int(distance / 1.0))
            for i in range(steps + 1):
                ip = tuple(p1[d] + (p2[d] - p1[d]) * (i / steps) for d in range(dim))
                if self._is_in_obstacle(ip, obstacles, is_3d):
                    return False
            return True

        def collision_free(p):
            return not self._is_in_obstacle(p, obstacles, is_3d)

        def add_node_rrtstar(tree, pos):
            r = neighbor_radius_fn(len(tree))
            neigh = near_nodes(tree, pos, r)

            candidate_parents = [n for n in neigh if edge_collision_free(n.position, pos)]
            if not candidate_parents:
                # fallback to nearest if no collision-free neighbors found
                nnear = nearest(tree, pos)
                if edge_collision_free(nnear.position, pos):
                    parent = nnear
                else:
                    return None, []
            else:
                parent = min(candidate_parents, key=lambda n: n.cost + dist(n.position, pos))
            new_cost = parent.cost + dist(parent.position, pos)
            new_node = Node(pos, parent=None, cost=new_cost)
            parent.add_child(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((parent, new_node))
            return new_node, neigh

        def rewire(node, neighbors):
            for nb in neighbors:
                if nb is node:
                    continue
                if not edge_collision_free(node.position, nb.position):
                    continue
                new_cost = node.cost + dist(node.position, nb.position)
                if new_cost + 1e-12 < nb.cost:
                    # reconnect neighbor via node
                    if nb.parent:
                        try:
                            edges.remove((nb.parent, nb))
                        except ValueError:
                            pass
                        if nb in nb.parent.children:
                            nb.parent.children.remove(nb)
                    node.add_child(nb)
                    nb.cost = new_cost
                    edges.append((node, nb))

        def stitch_paths(node_start, node_goal):
            path_start = node_start.path_from_root()
            path_goal = node_goal.path_from_root()
            # remove duplicated meeting node if any
            if path_start and path_goal and path_start[-1] == path_goal[-1]:
                merged = path_start + path_goal[-2::-1]
            else:
                merged = path_start + path_goal[::-1]
            return merged

        def path_cost(path):
            c = 0.0
            for i in range(len(path) - 1):
                c += dist(path[i], path[i+1])
            return c

        def informed_sample(start, goal, c_best, c_min):
            if c_best == float("inf") or c_min < 1e-12:
                return tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
            r1 = c_best / 2.0
            r_other_sq = max(c_best * c_best - c_min * c_min, 0.0)
            r_other = math.sqrt(r_other_sq) / 2.0
            center = tuple((start[d] + goal[d]) / 2.0 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))

            def sample_unit_ball(k):
                while True:
                    v = [random.gauss(0, 1) for _ in range(k)]
                    norm_v = math.sqrt(sum(x * x for x in v))
                    if norm_v > 1e-12:
                        v = [x / norm_v for x in v]
                        r = random.random() ** (1.0 / k)
                        return tuple(v[i] * r for i in range(k))

            u = sample_unit_ball(dim)

            if dim == 2:
                a2 = (-a1[1], a1[0])
                basis = (a1, a2)
                radii = (r1, r_other)
            else:
                ref = (1.0, 0.0, 0.0) if abs(a1[0]) < 0.9 else (0.0, 1.0, 0.0)
                b2 = self._normalize(self._cross(a1, ref))
                if self._norm(b2) < 1e-12:
                    ref = (0.0, 0.0, 1.0)
                    b2 = self._normalize(self._cross(a1, ref))
                b3 = self._cross(a1, b2)
                basis = (a1, b2, b3)
                radii = (r1, r_other, r_other)

            sample = []
            for d in range(dim):
                val = center[d]
                for i in range(dim):
                    val += basis[i][d] * radii[i] * u[i]
                # Clamp inside bounds
                val = max(0.0, min(bounds[d], val))
                sample.append(val)
            return tuple(sample)

        def smooth_path(path, max_trials):
            if len(path) < 3:
                return path
            new_path = path[:]
            for _ in range(max_trials):
                if len(new_path) < 3:
                    break
                i = random.randint(0, len(new_path) - 3)
                j = random.randint(i+2, len(new_path) - 1)
                if edge_collision_free(new_path[i], new_path[j]):
                    # remove intermediate points between i and j
                    new_path = new_path[:i+1] + new_path[j:]
            return new_path

        def try_connect_trees(new_node, other_tree, other_tree_nodes):
            nearest_other = nearest(other_tree, new_node.position)
            if dist(nearest_other.position, new_node.position) <= self.step_size:
                if edge_collision_free(new_node.position, nearest_other.position):
                    # Connect by creating new node optionally in other_tree for symmetry
                    return new_node, nearest_other
            # Otherwise try incremental steer connection on other tree side
            curr = nearest_other
            while True:
                step_pos = steer(curr.position, new_node.position)
                if not within_bounds(step_pos) or not collision_free(step_pos):
                    break
                if not edge_collision_free(curr.position, step_pos):
                    break
                new_other_node, _ = add_node_rrtstar(other_tree, step_pos)
                if new_other_node is None:
                    break
                rewire(new_other_node, near_nodes(other_tree, step_pos, neighbor_radius_fn(len(other_tree))))
                curr = new_other_node
                if dist(curr.position, new_node.position) <= self.step_size:
                    if edge_collision_free(curr.position, new_node.position):
                        # Create virtual connection node aligned with new_node
                        return new_node, curr
            return None, None

        iteration = 0
        while iteration < self.max_iter:
            if time.time() - start_time > self.time_limit_sec:
                break
            side_toggle = iteration % 2
            if side_toggle == 0:
                grow_tree, other_tree = start_tree, goal_tree
                grow_is_start = True
            else:
                grow_tree, other_tree = goal_tree, start_tree
                grow_is_start = False

            # Sample either uniformly or informed if solution known
            sample = informed_sample(start_pos, goal_pos, best_cost, c_min) if found_solution else \
                     tuple(random.uniform(0.0, bounds[d]) for d in range(dim))

            if not within_bounds(sample) or not collision_free(sample):
                if found_solution:
                    post_iter += 1
                    no_improve_count += 1
                    if post_iter >= self.post_opt_iters or no_improve_count >= self.max_no_improve:
                        break
                iteration += 1
                continue

            nearest_node = nearest(grow_tree, sample)
            new_pos = steer(nearest_node.position, sample)
            if not within_bounds(new_pos) or not collision_free(new_pos):
                if found_solution:
                    post_iter += 1
                    no_improve_count += 1
                    if post_iter >= self.post_opt_iters or no_improve_count >= self.max_no_improve:
                        break
                iteration += 1
                continue
            if not edge_collision_free(nearest_node.position, new_pos):
                if found_solution:
                    post_iter += 1
                    no_improve_count += 1
                    if post_iter >= self.post_opt_iters or no_improve_count >= self.max_no_improve:
                        break
                iteration += 1
                continue

            new_node, neighbors = add_node_rrtstar(grow_tree, new_pos)
            if new_node is None:
                if found_solution:
                    post_iter += 1
                    no_improve_count += 1
                    if post_iter >= self.post_opt_iters or no_improve_count >= self.max_no_improve:
                        break
                iteration += 1
                continue

            rewire(new_node, neighbors)

            # Attempt to connect trees via new_node
            n1, n2 = try_connect_trees(new_node, other_tree, nodes)
            if n1 and n2:
                candidate_path = stitch_paths(n1, n2)
                candidate_cost = path_cost(candidate_path)

                if candidate_cost + self.improve_tol < best_cost:
                    best_cost = candidate_cost
                    best_path = candidate_path
                    found_solution = True
                    no_improve_count = 0
                    post_iter = 0
                else:
                    if found_solution:
                        no_improve_count += 1
                        post_iter += 1

                if found_solution and (post_iter >= self.post_opt_iters or no_improve_count >= self.max_no_improve):
                    break
            else:
                if found_solution:
                    post_iter += 1
                    no_improve_count += 1
                    if post_iter >= self.post_opt_iters or no_improve_count >= self.max_no_improve:
                        break

            iteration += 1

        if found_solution and best_path:
            smooth = smooth_path(best_path, self.shortcutting_trials)
        else:
            smooth = []

        return PlannerResult(
            success=found_solution,
            path=smooth if smooth else best_path,
            nodes=nodes,
            edges=edges
        )

    # ---------------- Collision Helpers ----------------
    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    # ----------------------------------------------------------------
    # Vector math utilities
    # ----------------------------------------------------------------
    def _cross(self, a, b):
        ax, ay, az = a
        bx, by, bz = b
        return (ay * bz - az * by, az * bx - ax * bz, ax * by - ay * bx)

    def _norm(self, v):
        return math.sqrt(sum(x * x for x in v))

    def _normalize(self, v):
        n = self._norm(v)
        if n < 1e-12:
            return v
        return tuple(x / n for x in v)

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(
        self,
        max_iter=6000,
        step_size=5.0,
        post_opt_iters=500,
        max_no_improve=100,
        improve_tol=1e-5,
        goal_sample_rate=0.08,
        bridge_bias_rate=0.12,
        neighbor_gamma=60.0,
        time_limit_sec=28.0
    ):
        self.max_iter = max_iter
        self.step_size = step_size
        self.post_opt_iters = post_opt_iters
        self.max_no_improve = max_no_improve
        self.improve_tol = improve_tol
        self.goal_sample_rate = goal_sample_rate
        self.bridge_bias_rate = bridge_bias_rate
        self.neighbor_gamma = neighbor_gamma
        self.time_limit_sec = time_limit_sec

    def plan(self, map):
        import time
        import math
        import random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0.0 <= p[d] <= bounds[d] for d in range(dim))

        def clamp(p):
            return tuple(min(max(p[d], 0.0), bounds[d]) for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                return clamp(to_p)
            r = self.step_size / d
            return clamp(tuple(from_p[i] + (to_p[i] - from_p[i]) * r for i in range(dim)))

        def is_in_obstacle(pos):
            px = pos[0]
            for obs in obstacles:
                if is_3d:
                    x,y,z,w,h,d=obs
                    if x <= pos[0] <= x + w and y <= pos[1] <= y + h and z <= pos[2] <= z + d:
                        return True
                else:
                    x,y,w,h=obs
                    if x <= pos[0] <= x + w and y <= pos[1] <= y + h:
                        return True
            return False

        def edge_collision(f_pos, t_pos, resolution=1.0):
            d = dist(f_pos, t_pos)
            steps = max(1, int(d / resolution))
            for i in range(steps + 1):
                interp = tuple(f_pos[j] + (t_pos[j] - f_pos[j]) * (i / steps) for j in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def sample_uniform():
            return tuple(random.uniform(0, bounds[d]) for d in range(dim))

        def sample_free(active_tree, other_tree):
            r = random.random()
            if r < self.goal_sample_rate:
                return goal_pos if active_tree is tree_start else start_pos
            if r < self.goal_sample_rate + self.bridge_bias_rate:
                root_o = other_tree[0].position
                jitter = tuple(random.uniform(-2*self.step_size, 2*self.step_size) for _ in range(dim))
                s = clamp(tuple(root_o[i] + jitter[i] for i in range(dim)))
                return s if not is_in_obstacle(s) else sample_uniform()
            return sample_uniform()

        def nearest(tree, p):
            nearest_node = None
            min_dist = float('inf')
            for n in tree:
                d = dist(n.position, p)
                if d < min_dist:
                    min_dist = d
                    nearest_node = n
            return nearest_node

        def neighbor_radius(n_nodes):
            if n_nodes < 2:
                return max(10.0, self.step_size*2)
            r = self.neighbor_gamma * ((math.log(n_nodes)/n_nodes) ** (1.0 / dim))
            return max(10.0, min(max(bounds), r))

        def near(tree, pos, radius):
            return [nd for nd in tree if dist(nd.position, pos) <= radius]

        def add_node_with_best_parent(tree, pos):
            radius = neighbor_radius(len(tree))
            near_nodes = near(tree, pos, radius)
            candidates = [n for n in near_nodes if not edge_collision(n.position, pos)]
            if not candidates:
                parent = nearest(tree, pos)
                if edge_collision(parent.position, pos):
                    return None, []
            else:
                parent = min(candidates, key=lambda c: c.cost + dist(c.position, pos))
            cost_c = parent.cost + dist(parent.position, pos)
            new_node = Node(pos, parent=None, cost=cost_c)
            parent.add_child(new_node)
            tree.append(new_node)
            return new_node, near_nodes

        def rewire(node, neighbors, tree, edges):
            for nb in neighbors:
                if nb is node:
                    continue
                if edge_collision(node.position, nb.position):
                    continue
                new_cost = node.cost + dist(node.position, nb.position)
                if new_cost + 1e-12 < nb.cost:
                    if nb.parent:
                        try:
                            edges.remove((nb.parent, nb))
                        except ValueError:
                            pass
                        if nb in nb.parent.children:
                            nb.parent.children.remove(nb)
                    node.add_child(nb)
                    nb.cost = new_cost
                    edges.append((node, nb))

        def stitch_path(a_node, b_node, a_start=True):
            path_a = a_node.path_from_root()
            path_b = b_node.path_from_root()
            if path_a[-1] == path_b[-1]:
                full_path = path_a + path_b[-2::-1]
            else:
                full_path = path_a + path_b[::-1]
            if a_start:
                return full_path
            return full_path[::-1]

        def path_cost(path):
            return sum(dist(path[i], path[i+1]) for i in range(len(path)-1))

        start_time = time.time()

        tree_start = [Node(start_pos, cost=0.0)]
        tree_goal = [Node(goal_pos, cost=0.0)]
        nodes = tree_start + tree_goal[:]
        edges = []
        best_path = []
        best_cost = float('inf')
        found_solution = False
        post_opt_counter = 0
        no_improve = 0

        for i in range(self.max_iter):
            if time.time() - start_time > self.time_limit_sec:
                break

            grow_tree, other_tree = (tree_start, tree_goal) if i % 2 == 0 else (tree_goal, tree_start)
            a_is_start_tree = (grow_tree is tree_start)

            sample_pos = sample_free(grow_tree, other_tree)
            nearest_node = nearest(grow_tree, sample_pos)
            new_pos = steer(nearest_node.position, sample_pos)

            if not in_bounds(new_pos) or is_in_obstacle(new_pos):
                if found_solution:
                    post_opt_counter += 1
                    no_improve += 1
                    if post_opt_counter >= self.post_opt_iters or no_improve >= self.max_no_improve:
                        break
                continue
            if edge_collision(nearest_node.position, new_pos):
                if found_solution:
                    post_opt_counter += 1
                    no_improve += 1
                    if post_opt_counter >= self.post_opt_iters or no_improve >= self.max_no_improve:
                        break
                continue

            new_node, neighbors = add_node_with_best_parent(grow_tree, new_pos)
            if new_node is None:
                if found_solution:
                    post_opt_counter += 1
                    no_improve += 1
                    if post_opt_counter >= self.post_opt_iters or no_improve >= self.max_no_improve:
                        break
                continue

            edges.append((new_node.parent, new_node))
            nodes.append(new_node)
            rewire(new_node, neighbors, grow_tree, edges)

            # Try to connect other tree greedily one step toward new_node with rewiring
            other_near = nearest(other_tree, new_node.position)
            curr = other_near
            connected_node = None

            while True:
                step_pos = steer(curr.position, new_node.position)
                if not in_bounds(step_pos) or is_in_obstacle(step_pos):
                    break
                if edge_collision(curr.position, step_pos):
                    break

                w_new_node, w_neighbors = add_node_with_best_parent(other_tree, step_pos)
                if w_new_node is None:
                    break
                edges.append((w_new_node.parent, w_new_node))
                nodes.append(w_new_node)
                rewire(w_new_node, w_neighbors, other_tree, edges)
                curr = w_new_node

                if dist(curr.position, new_node.position) <= self.step_size:
                    if not edge_collision(curr.position, new_node.position) and new_node.valid:
                        bridge_node = Node(new_node.position, parent=None,
                                           cost=curr.cost + dist(curr.position, new_node.position))
                        curr.add_child(bridge_node)
                        other_tree.append(bridge_node)
                        nodes.append(bridge_node)
                        edges.append((curr, bridge_node))
                        connected_node = bridge_node
                    break

            if connected_node is not None:
                candidate_path = stitch_path(new_node, connected_node, a_start=a_is_start_tree)
                candidate_cost = path_cost(candidate_path)
                if candidate_cost + self.improve_tol < best_cost:
                    best_cost = candidate_cost
                    best_path = candidate_path
                    found_solution = True
                    no_improve = 0
                    post_opt_counter = 0
                else:
                    no_improve += 1
                    post_opt_counter += 1

                if post_opt_counter >= self.post_opt_iters or no_improve >= self.max_no_improve:
                    break
            else:
                if found_solution:
                    no_improve += 1
                    post_opt_counter += 1
                    if post_opt_counter >= self.post_opt_iters or no_improve >= self.max_no_improve:
                        break

        return PlannerResult(
            success=found_solution,
            path=best_path if found_solution else [],
            nodes=nodes,
            edges=edges
        )

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def path_from_root(self):
        path = []
        n = self
        while n:
            path.append(n.position)
            n = n.parent
        return path[::-1]


class Planner:
    def __init__(
        self,
        max_iter: int = 5000,
        step_size: float = 5.0,
        neighbor_radius: float = 25.0,
        post_opt_iters: int = 400,    # extra iterations after first solution
        max_no_improve: int = 150,    # stop early if this many consecutive iterations show no improvement
        improve_tol: float = 1e-6,    # minimal cost drop to count as improvement
        time_limit_sec: float = 30.0  # hard wall time limit
    ):
        self.max_iter = max_iter
        self.step_size = step_size
        self.neighbor_radius = neighbor_radius
        self.post_opt_iters = post_opt_iters
        self.max_no_improve = max_no_improve
        self.improve_tol = improve_tol
        self.time_limit_sec = time_limit_sec

    def plan(self, map: Map) -> PlannerResult:
        bounds = map.size                  # Tuple[int, ...]: (W,H) or (W,H,D)
        start_position = map.start         # Tuple[float, ...]
        goal_position = map.goal           # Tuple[float, ...]
        obstacles = map.obstacles          # Rectangular blocks / boxes

        dim = len(bounds)
        is_3d = (dim == 3)

        # Core data
        success_state = False
        extracted_path: List[Tuple[float, ...]] = []
        nodes: List[Node] = []
        edges: List[Tuple[Node, Node]] = []

        # Initialize two trees
        start_root = Node(start_position, cost=0.0)
        goal_root  = Node(goal_position,  cost=0.0)
        tree_a: List[Node] = [start_root]
        tree_b: List[Node] = [goal_root]
        nodes.extend([start_root, goal_root])

        # RRT* neighborhood (practical clamp)
        gamma_rrt_star = 60.0
        max_radius = max(bounds) * 0.30
        min_radius = max(10.0, self.step_size * 2.0)

        # Best solution & informed sampling params
        best_cost = float("inf")
        best_path: List[Tuple[float, ...]] = []
        c_min = math.dist(start_position, goal_position)

        # Post-optimization control
        found_first_solution = False
        post_iter_count = 0
        no_improve_streak = 0

        # Time guard
        start_time = time.time()

        # ---------- Helpers ----------
        def within_bounds(p):
            return all(0.0 <= p[d] <= bounds[d] for d in range(dim))

        def nearest(tree, pt):
            return min(tree, key=lambda n: math.dist(n.position, pt))

        def steer(from_pos, to_pos):
            d = math.dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            r = self.step_size / d
            return tuple(from_pos[i] + (to_pos[i] - from_pos[i]) * r for i in range(dim))

        def neighbor_radius_fn(n_nodes: int) -> float:
            if n_nodes < 2:
                return min_radius
            r_theory = gamma_rrt_star * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(min_radius, min(max_radius, r_theory))

        def near_nodes(tree, pos, radius):
            return [nd for nd in tree if math.dist(nd.position, pos) <= radius]

        def add_with_best_parent(tree, new_pos):
            """Insert new_pos using RRT* best-parent selection; return (new_node, neighbors) or (None, [])."""
            r = neighbor_radius_fn(len(tree))
            neigh = near_nodes(tree, new_pos, r)
            parent_candidates = neigh if neigh else [nearest(tree, new_pos)]
            parent_candidates = [nb for nb in parent_candidates
                                 if not self._is_edge_in_obstacle(nb.position, new_pos, obstacles, is_3d)]
            if not parent_candidates:
                return None, []
            parent = min(parent_candidates, key=lambda nb: nb.cost + math.dist(nb.position, new_pos))
            new_node = Node(new_pos, parent=None, cost=parent.cost + math.dist(parent.position, new_pos))
            parent.add_child(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((parent, new_node))
            return new_node, neigh

        def rewire_from(pivot: Node, neigh: List[Node]):
            for nb in neigh:
                if nb is pivot:
                    continue
                if self._is_edge_in_obstacle(pivot.position, nb.position, obstacles, is_3d):
                    continue
                new_c = pivot.cost + math.dist(pivot.position, nb.position)
                if new_c + 1e-12 < nb.cost:
                    if nb.parent is not None:
                        try:
                            edges.remove((nb.parent, nb))
                        except ValueError:
                            pass
                        if nb in nb.parent.children:
                            nb.parent.children.remove(nb)
                    pivot.add_child(nb)
                    nb.cost = new_c
                    edges.append((pivot, nb))

        def stitch_paths(meet_a: Node, meet_b: Node, a_is_start_side: bool) -> List[Tuple[float, ...]]:
            """Create full path from start to goal using meeting nodes from both trees."""
            path_a = meet_a.path_from_root()
            path_b = meet_b.path_from_root()
            if path_a and path_b and path_a[-1] == path_b[-1]:
                merged = path_a + path_b[-2::-1]
            else:
                merged = path_a + path_b[::-1]
            return merged if a_is_start_side else merged[::-1]

        def path_cost(path):
            return sum(math.dist(path[i], path[i+1]) for i in range(len(path)-1))

        # ---------- Main loop ----------
        for it in range(self.max_iter):
            # Hard time limit
            if time.time() - start_time > self.time_limit_sec:
                return PlannerResult(
                    success=(len(best_path) > 0),
                    path=best_path if best_path else [],
                    nodes=nodes,
                    edges=edges
                )

            # Choose expansion/connection sides
            if it % 2 == 0:
                grow_tree, other_tree = tree_a, tree_b
                a_is_start_side = True
            else:
                grow_tree, other_tree = tree_b, tree_a
                a_is_start_side = False

            # Informed sampling (uniform before first solution; ellipsoid after)
            sample = self._sample_informed(start_position, goal_position, best_cost, c_min, bounds)

            # Extend grow_tree toward sample
            a_near = nearest(grow_tree, sample)
            a_new_pos = steer(a_near.position, sample)

            if (not within_bounds(a_new_pos)) or self._is_in_obstacle(a_new_pos, obstacles, is_3d):
                # count post-optimization iterations if already in post phase
                if found_first_solution:
                    post_iter_count += 1
                    no_improve_streak += 1
                    if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                        break
                continue
            if self._is_edge_in_obstacle(a_near.position, a_new_pos, obstacles, is_3d):
                if found_first_solution:
                    post_iter_count += 1
                    no_improve_streak += 1
                    if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                        break
                continue

            a_new, a_neigh = add_with_best_parent(grow_tree, a_new_pos)
            if a_new is None:
                if found_first_solution:
                    post_iter_count += 1
                    no_improve_streak += 1
                    if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                        break
                continue
            rewire_from(a_new, a_neigh)

            # Greedy connect other_tree toward a_new, with local rewiring each step
            b_near = nearest(other_tree, a_new.position)
            current = b_near
            connected_b = None

            while True:
                step_pos = steer(current.position, a_new.position)
                if (not within_bounds(step_pos)) or self._is_in_obstacle(step_pos, obstacles, is_3d):
                    break
                if self._is_edge_in_obstacle(current.position, step_pos, obstacles, is_3d):
                    break

                b_new, b_neigh = add_with_best_parent(other_tree, step_pos)
                if b_new is None:
                    break
                rewire_from(b_new, b_neigh)
                current = b_new

                # Try a final short bridge
                if math.dist(current.position, a_new.position) <= self.step_size:
                    if not self._is_edge_in_obstacle(current.position, a_new.position, obstacles, is_3d) and a_new.valid:
                        final_b = Node(
                            a_new.position,
                            parent=None,
                            cost=current.cost + math.dist(current.position, a_new.position)
                        )
                        current.add_child(final_b)
                        other_tree.append(final_b)
                        nodes.append(final_b)
                        edges.append((current, final_b))
                        connected_b = final_b
                    break

            # If a connection was made, evaluate and manage post-optimization counters
            if connected_b is not None:
                candidate_path = stitch_paths(a_new, connected_b, a_is_start_side)
                cand_cost = path_cost(candidate_path)

                if cand_cost + self.improve_tol < best_cost:
                    best_cost = cand_cost
                    best_path = candidate_path
                    success_state = True
                    no_improve_streak = 0
                else:
                    no_improve_streak += 1

                if not found_first_solution:
                    found_first_solution = True
                    post_iter_count = 0
                else:
                    post_iter_count += 1

                if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                    break
            else:
                # No successful connection this iteration
                if found_first_solution:
                    post_iter_count += 1
                    no_improve_streak += 1
                    if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                        break

        extracted_path = best_path if success_state else []
        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    # ---------------- Informed Sampling ----------------
    def _sample_informed(self, start, goal, c_best, c_min, bounds):
        """Uniform in bounds if no solution; otherwise sample within the start–goal ellipsoid."""
        dim = len(bounds)
        if c_best == float("inf") or c_min <= 1e-12:
            return tuple(random.uniform(0.0, bounds[d]) for d in range(dim))

        # Ellipsoid radii
        r1 = c_best / 2.0
        r_other_sq = max(c_best * c_best - c_min * c_min, 0.0)
        r_other = math.sqrt(r_other_sq) / 2.0

        # Center and first axis (unit)
        center = tuple((s + g) / 2.0 for s, g in zip(start, goal))
        a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))

        # Orthonormal basis aligned with a1
        if dim == 2:
            a2 = (-a1[1], a1[0])
            basis = (a1, a2)
            radii = (r1, r_other)
        else:  # 3D
            ref = (1.0, 0.0, 0.0) if abs(a1[0]) < 0.9 else (0.0, 1.0, 0.0)
            b2 = self._normalize(self._cross(a1, ref))
            if self._norm(b2) < 1e-12:
                ref = (0.0, 0.0, 1.0)
                b2 = self._normalize(self._cross(a1, ref))
            b3 = self._cross(a1, b2)
            basis = (a1, b2, b3)
            radii = (r1, r_other, r_other)

        u = self._sample_unit_ball(dim)

        mapped = []
        for d in range(dim):
            val = center[d]
            for i in range(dim):
                val += basis[i][d] * radii[i] * u[i]
            mapped.append(val)

        mapped = tuple(min(max(mapped[d], 0.0), bounds[d]) for d in range(dim))
        return mapped

    def _sample_unit_ball(self, dim):
        while True:
            v = [random.gauss(0.0, 1.0) for _ in range(dim)]
            n = math.sqrt(sum(x * x for x in v))
            if n > 1e-12:
                v = [x / n for x in v]
                r = random.random() ** (1.0 / dim)
                return tuple(v[i] * r for i in range(dim))

    def _cross(self, a, b):
        ax, ay, az = a
        bx, by, bz = b
        return (ay * bz - az * by, az * bx - ax * bz, ax * by - ay * bx)

    def _norm(self, v):
        return math.sqrt(sum(x * x for x in v))

    def _normalize(self, v):
        n = self._norm(v)
        if n < 1e-12:
            return v
        return tuple(x / n for x in v)

    # ---------------- Collision Utilities ----------------
    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(
                from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps)
                for d in range(len(from_pos))
            )
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False
