class Planner:
    def __init__(
        self,
        max_iter: int = 5000,
        step_size: float = 5.0,
        gamma_rrt_star: float = 50.0,
        max_radius: float = 60.0,
    ):
        self.max_iter = max_iter
        self.step_size = step_size
        self.gamma_rrt_star = gamma_rrt_star
        self.max_radius = max_radius

    # ======================= Public API =======================

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        success_state = False
        extracted_path: List[Tuple[float, ...]] = []
        nodes: List[Node] = []
        edges: List[Tuple[Node, Node]] = []

        start_root = Node(start_position, parent=None, cost=0.0)
        goal_root  = Node(goal_position,  parent=None, cost=0.0)
        nodes.extend([start_root, goal_root])

        tree_a: List[Node] = [start_root]
        tree_b: List[Node] = [goal_root]

        for _ in range(self.max_iter):
            rand_point = self._sample_free(bounds, obstacles, is_3d)

            new_a = self._extend_and_rewire(tree_a, rand_point, obstacles, is_3d, nodes, edges, bounds)
            if new_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_b = self._connect_and_rewire(tree_b, new_a, obstacles, is_3d, nodes, edges, bounds)
            if new_b:
                success_state = True
                path_a = new_a.path_from_root()
                path_b = new_b.path_from_root()
                if path_a and path_b and path_a[-1] == path_b[-1]:
                    path_b = path_b[:-1]
                extracted_path = path_a + path_b[::-1]
                break

            tree_a, tree_b = tree_b, tree_a

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _sample_free(self, bounds, obstacles, is_3d):
        while True:
            p = tuple(random.uniform(0, bounds[d]) for d in range(len(bounds)))
            if not self._is_in_obstacle(p, obstacles, is_3d):
                return p

    def _nearest(self, tree: List[Node], point):
        return min(tree, key=lambda node: math.dist(node.position, point))

    def _steer(self, from_pos, to_pos):
        dist = math.dist(from_pos, to_pos)
        if dist <= self.step_size:
            return to_pos
        ratio = self.step_size / dist
        return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(len(from_pos)))

    def _near_nodes(self, tree: List[Node], position, bounds) -> List[Node]:
        n = max(1, len(tree))
        d = len(position)
        # 공간 스케일을 간단히 bounds로 추정 (정밀 X, 실용적)
        scale = max(1.0, sum(bounds) / d)
        r_theory = self.gamma_rrt_star * ((math.log(n + 1) / (n + 1)) ** (1.0 / d))
        radius = max(self.step_size * 2.0, min(self.max_radius, r_theory * 0.5 + scale * 0.02))
        return [node for node in tree if math.dist(node.position, position) <= radius]

    def _choose_parent_and_add(self, tree, new_pos, obstacles, is_3d, nodes, edges, bounds):
        nearest = self._nearest(tree, new_pos)
        neighbors = self._near_nodes(tree, new_pos, bounds)

        min_cost = nearest.cost + math.dist(nearest.position, new_pos)
        best_parent = nearest

        for nb in neighbors:
            if not self._is_edge_in_obstacle(nb.position, new_pos, obstacles, is_3d):
                cand = nb.cost + math.dist(nb.position, new_pos)
                if cand < min_cost:
                    min_cost = cand
                    best_parent = nb

        new_node = Node(new_pos, parent=None, cost=min_cost)
        best_parent.add_child(new_node)
        tree.append(new_node)
        nodes.append(new_node)
        edges.append((best_parent, new_node))
        return new_node, neighbors

    def _rewire_from(self, pivot: Node, neighbors: List[Node], obstacles, is_3d, edges):
        for nb in neighbors:
            if not self._is_edge_in_obstacle(pivot.position, nb.position, obstacles, is_3d):
                new_cost = pivot.cost + math.dist(pivot.position, nb.position)
                if new_cost + 1e-12 < nb.cost:  # 작은 수치 버퍼
                    if nb.parent is not None:
                        try:
                            edges.remove((nb.parent, nb))
                        except ValueError:
                            pass
                        if nb in nb.parent.children:
                            nb.parent.children.remove(nb)
                    pivot.add_child(nb)
                    nb.cost = new_cost
                    edges.append((pivot, nb))

    def _extend_and_rewire(self, tree, sample_point, obstacles, is_3d, nodes, edges, bounds):
        nearest = self._nearest(tree, sample_point)
        new_pos = self._steer(nearest.position, sample_point)

        if self._is_in_obstacle(new_pos, obstacles, is_3d):
            return None
        if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
            return None

        new_node, neighbors = self._choose_parent_and_add(tree, new_pos, obstacles, is_3d, nodes, edges, bounds)
        self._rewire_from(new_node, neighbors, obstacles, is_3d, edges)
        return new_node

    def _connect_and_rewire(self, tree, target_node: Node, obstacles, is_3d, nodes, edges, bounds):
        nearest = self._nearest(tree, target_node.position)

        while True:
            new_pos = self._steer(nearest.position, target_node.position)

            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
                return None

            connect_node, neighbors = self._choose_parent_and_add(tree, new_pos, obstacles, is_3d, nodes, edges, bounds)
            self._rewire_from(connect_node, neighbors, obstacles, is_3d, edges)

            if math.dist(connect_node.position, target_node.position) <= self.step_size:
                if (not self._is_edge_in_obstacle(connect_node.position, target_node.position, obstacles, is_3d) and
                    not self._is_in_obstacle(target_node.position, obstacles, is_3d)):
                    final_node = Node(target_node.position, parent=None, cost=connect_node.cost + math.dist(connect_node.position, target_node.position))
                    connect_node.add_child(final_node)
                    tree.append(final_node)
                    nodes.append(final_node)
                    edges.append((connect_node, final_node))
                    return final_node
                else:
                    return None

            nearest = connect_node


    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False


    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

