class Planner:
    def __init__(
        self,
        max_iter: int = 5000,
        step_size: float = 5.0,
        neighbor_radius: float = 25.0,
        post_opt_iters: int = 400,      # extra iterations after first solution
        max_no_improve: int = 150,      # stop early if no improvement for this many iters
        improve_tol: float = 1e-6,      # minimal improvement to count
        time_limit_sec: float = 30.0,   # hard wall time limit
    ):
        self.max_iter = max_iter
        self.step_size = step_size
        self.neighbor_radius = neighbor_radius
        self.post_opt_iters = post_opt_iters
        self.max_no_improve = max_no_improve
        self.improve_tol = improve_tol
        self.time_limit_sec = time_limit_sec

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles

        is_3d = len(bounds) == 3
        dim = len(bounds)

        # Core data
        success_state = False
        extracted_path = []
        nodes = []
        edges = []

        start_root = Node(start_position, cost=0.0)
        goal_root = Node(goal_position, cost=0.0)
        start_tree = [start_root]
        goal_tree = [goal_root]
        nodes.extend([start_root, goal_root])

        best_cost = float('inf')
        best_path = []
        c_min = math.dist(start_position, goal_position)

        found_first_solution = False
        post_iter_count = 0
        no_improve_streak = 0

        start_time = time.time()

        for it in range(self.max_iter):
            # Enforce 30s time limit
            if time.time() - start_time > self.time_limit_sec:
                return PlannerResult(
                    success=success_state,
                    path=best_path if success_state else [],
                    nodes=nodes,
                    edges=edges
                )

            # Alternate trees (RRT-Connect style)
            tree_a, tree_b = (start_tree, goal_tree) if (it % 2 == 0) else (goal_tree, start_tree)

            # Informed sampling: uniform before first solution; ellipsoid after
            x_rand = self._sample_informed(start_position, goal_position, best_cost, c_min, bounds)

            # Extend tree_a one step with RRT* best-parent + local rewire
            a_nearest = self._nearest(tree_a, x_rand)
            a_new_pos = self._steer(a_nearest.position, x_rand)

            if not self._within_bounds(a_new_pos, bounds):
                if found_first_solution:
                    post_iter_count += 1
                    no_improve_streak += 1
                if found_first_solution and (post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve):
                    break
                continue

            if self._is_in_obstacle(a_new_pos, obstacles, is_3d) or                self._is_edge_in_obstacle(a_nearest.position, a_new_pos, obstacles, is_3d):
                if found_first_solution:
                    post_iter_count += 1
                    no_improve_streak += 1
                if found_first_solution and (post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve):
                    break
                continue

            a_new, a_neighbors = self._add_with_best_parent(tree_a, a_new_pos, obstacles, is_3d, nodes, edges)
            self._rewire_from(a_new, a_neighbors, obstacles, is_3d, edges)

            # Greedy connect tree_b toward a_new (RRT-Connect) with local RRT* rewiring
            b_meet = self._connect_with_rewire(tree_b, a_new, obstacles, is_3d, nodes, edges, bounds)

            if b_meet is not None:
                # Build full path depending on which side expanded
                if it % 2 == 0:
                    path_a = a_new.path_from_root()       # start side
                    path_b = b_meet.path_from_root()      # goal side
                else:
                    path_a = b_meet.path_from_root()       # start side
                    path_b = a_new.path_from_root()        # goal side

                # Merge (avoid double-counting the joint if equal)
                if path_a and path_b and path_a[-1] == path_b[-1]:
                    merged = path_a + path_b[-2::-1]
                else:
                    merged = path_a + path_b[::-1]

                cost = self._path_cost(merged)

                if cost + self.improve_tol < best_cost:
                    best_cost = cost
                    best_path = merged
                    success_state = True
                    no_improve_streak = 0
                else:
                    no_improve_streak += 1

                if not found_first_solution:
                    found_first_solution = True
                    post_iter_count = 0
                else:
                    post_iter_count += 1

                if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                    break
            else:
                if found_first_solution:
                    post_iter_count += 1
                    no_improve_streak += 1
                    if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                        break

        extracted_path = best_path if success_state else []
        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    # ---------------------- Helpers ----------------------
    def _within_bounds(self, pos, bounds):
        return all(0.0 <= pos[d] <= bounds[d] for d in range(len(bounds)))

    def _path_cost(self, path):
        return sum(math.dist(path[i], path[i+1]) for i in range(len(path)-1))

    def _nearest(self, tree, point):
        return min(tree, key=lambda n: math.dist(n.position, point))

    def _steer(self, from_pos, to_pos):
        dist = math.dist(from_pos, to_pos)
        if dist <= self.step_size:
            return to_pos
        ratio = self.step_size / dist
        return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(len(from_pos)))

    def _neighbors(self, tree, pos):
        return [n for n in tree if math.dist(n.position, pos) <= self.neighbor_radius]

    def _add_with_best_parent(self, tree, new_pos, obstacles, is_3d, nodes, edges):
        nearest = self._nearest(tree, new_pos)
        candidates = self._neighbors(tree, new_pos)

        min_cost = nearest.cost + math.dist(nearest.position, new_pos)
        best_parent = nearest

        for nb in candidates:
            if self._is_edge_in_obstacle(nb.position, new_pos, obstacles, is_3d):
                continue
            cand_cost = nb.cost + math.dist(nb.position, new_pos)
            if cand_cost < min_cost:
                min_cost = cand_cost
                best_parent = nb

        new_node = Node(new_pos, parent=None, cost=min_cost)
        best_parent.add_child(new_node)
        tree.append(new_node)
        nodes.append(new_node)
        edges.append((best_parent, new_node))
        return new_node, candidates

    def _rewire_from(self, pivot: Node, neighbors, obstacles, is_3d, edges):
        for nb in neighbors:
            if self._is_edge_in_obstacle(pivot.position, nb.position, obstacles, is_3d):
                continue
            new_cost = pivot.cost + math.dist(pivot.position, nb.position)
            if new_cost + 1e-12 < nb.cost:
                if nb.parent is not None:
                    try:
                        edges.remove((nb.parent, nb))
                    except ValueError:
                        pass
                    if nb in nb.parent.children:
                        nb.parent.children.remove(nb)
                pivot.add_child(nb)
                nb.cost = new_cost
                edges.append((pivot, nb))

    def _connect_with_rewire(self, tree, target_node: Node, obstacles, is_3d, nodes, edges, bounds):
        """
        Greedily advance 'tree' toward 'target_node' like RRT-Connect.
        At each step, insert with best parent and locally rewire.
        Return the final node in 'tree' placed at target_node.position (if reached), else None.
        """
        current = self._nearest(tree, target_node.position)

        while True:
            new_pos = self._steer(current.position, target_node.position)

            if not self._within_bounds(new_pos, bounds):
                return None
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(current.position, new_pos, obstacles, is_3d):
                return None

            new_node, neighbors = self._add_with_best_parent(tree, new_pos, obstacles, is_3d, nodes, edges)
            self._rewire_from(new_node, neighbors, obstacles, is_3d, edges)

            if math.dist(new_node.position, target_node.position) <= self.step_size:
                # final short edge
                if not self._is_edge_in_obstacle(new_node.position, target_node.position, obstacles, is_3d) and target_node.valid:
                    final_node = Node(
                        target_node.position,
                        parent=None,
                        cost=new_node.cost + math.dist(new_node.position, target_node.position)
                    )
                    new_node.add_child(final_node)
                    tree.append(final_node)
                    nodes.append(final_node)
                    edges.append((new_node, final_node))
                    return final_node
                return None

            current = new_node

    # ------------------ Informed Sampling ------------------
    def _sample_informed(self, start, goal, c_best, c_min, bounds):
        """Uniform before first solution; prolate hyperspheroid after (supports 2D/3D)."""
        dim = len(bounds)
        if c_best == float('inf') or c_min <= 1e-12:
            # uniform in bounds
            return tuple(random.uniform(0, bounds[d]) for d in range(dim))

        # radii along orthonormal basis aligned with start->goal
        r1 = c_best / 2.0
        r_other_sq = max(c_best * c_best - c_min * c_min, 0.0)
        r_other = math.sqrt(r_other_sq) / 2.0

        # center
        center = tuple((s + g) / 2.0 for s, g in zip(start, goal))
        # first axis (unit)
        a1_vec = tuple((goal[d] - start[d]) / c_min for d in range(dim))

        # orthonormal basis
        if dim == 2:
            a2_vec = (-a1_vec[1], a1_vec[0])
            basis = (a1_vec, a2_vec)
            radii = (r1, r_other)
        else:  # dim == 3
            # pick any non-parallel vector
            ref = (1.0, 0.0, 0.0) if abs(a1_vec[0]) < 0.9 else (0.0, 1.0, 0.0)
            # b2 = normalize(cross(a1, ref)), b3 = cross(a1, b2)
            b2 = self._normalize(self._cross(a1_vec, ref))
            if self._norm(b2) < 1e-12:
                ref = (0.0, 0.0, 1.0)
                b2 = self._normalize(self._cross(a1_vec, ref))
            b3 = self._cross(a1_vec, b2)
            basis = (a1_vec, b2, b3)
            radii = (r1, r_other, r_other)

        # sample unit ball
        u = self._sample_unit_ball(dim)

        # map: x = center + sum_i (basis_i * radii_i * u_i)
        mapped = []
        for d in range(dim):
            val = center[d]
            for i in range(dim):
                val += basis[i][d] * radii[i] * u[i]
            mapped.append(val)

        # clamp to bounds to be safe
        mapped = tuple(min(max(mapped[d], 0.0), bounds[d]) for d in range(dim))
        return mapped

    def _sample_unit_ball(self, dim):
        # Draw direction from normal distribution and radius with correct PDF
        while True:
            vec = [random.gauss(0.0, 1.0) for _ in range(dim)]
            n = math.sqrt(sum(v * v for v in vec))
            if n > 1e-12:
                vec = [v / n for v in vec]
                r = random.random() ** (1.0 / dim)
                return tuple(vec[i] * r for i in range(dim))

    def _cross(self, a, b):
        ax, ay, az = a
        bx, by, bz = b
        return (ay * bz - az * by, az * bx - ax * bz, ax * by - ay * bx)

    def _norm(self, v):
        return math.sqrt(sum(x * x for x in v))

    def _normalize(self, v):
        n = self._norm(v)
        if n < 1e-12:
            return v
        return tuple(x / n for x in v)

    # ------------------ Collision Utilities ------------------
    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def path_from_root(self):
        path = []
        n = self
        while n:
            path.append(n.position)
            n = n.parent
        return path[::-1]


class Planner:
    def __init__(
        self,
        max_iter: int = 5000,
        step_size: float = 5.0,
        neighbor_radius: float = 25.0,
        post_opt_iters: int = 400,    # extra iterations after first solution
        max_no_improve: int = 150,    # stop early if this many consecutive iterations show no improvement
        improve_tol: float = 1e-6,    # minimal cost drop to count as improvement
        time_limit_sec: float = 30.0  # hard wall time limit
    ):
        self.max_iter = max_iter
        self.step_size = step_size
        self.neighbor_radius = neighbor_radius
        self.post_opt_iters = post_opt_iters
        self.max_no_improve = max_no_improve
        self.improve_tol = improve_tol
        self.time_limit_sec = time_limit_sec

    def plan(self, map: Map) -> PlannerResult:
        bounds = map.size                  # Tuple[int, ...]: (W,H) or (W,H,D)
        start_position = map.start         # Tuple[float, ...]
        goal_position = map.goal           # Tuple[float, ...]
        obstacles = map.obstacles          # Rectangular blocks / boxes

        dim = len(bounds)
        is_3d = (dim == 3)

        # Core data
        success_state = False
        extracted_path: List[Tuple[float, ...]] = []
        nodes: List[Node] = []
        edges: List[Tuple[Node, Node]] = []

        # Initialize two trees
        start_root = Node(start_position, cost=0.0)
        goal_root  = Node(goal_position,  cost=0.0)
        tree_a: List[Node] = [start_root]
        tree_b: List[Node] = [goal_root]
        nodes.extend([start_root, goal_root])

        # RRT* neighborhood (practical clamp)
        gamma_rrt_star = 60.0
        max_radius = max(bounds) * 0.30
        min_radius = max(10.0, self.step_size * 2.0)

        # Best solution & informed sampling params
        best_cost = float("inf")
        best_path: List[Tuple[float, ...]] = []
        c_min = math.dist(start_position, goal_position)

        # Post-optimization control
        found_first_solution = False
        post_iter_count = 0
        no_improve_streak = 0

        # Time guard
        start_time = time.time()

        # ---------- Helpers ----------
        def within_bounds(p):
            return all(0.0 <= p[d] <= bounds[d] for d in range(dim))

        def nearest(tree, pt):
            return min(tree, key=lambda n: math.dist(n.position, pt))

        def steer(from_pos, to_pos):
            d = math.dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            r = self.step_size / d
            return tuple(from_pos[i] + (to_pos[i] - from_pos[i]) * r for i in range(dim))

        def neighbor_radius_fn(n_nodes: int) -> float:
            if n_nodes < 2:
                return min_radius
            r_theory = gamma_rrt_star * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(min_radius, min(max_radius, r_theory))

        def near_nodes(tree, pos, radius):
            return [nd for nd in tree if math.dist(nd.position, pos) <= radius]

        def add_with_best_parent(tree, new_pos):
            """Insert new_pos using RRT* best-parent selection; return (new_node, neighbors) or (None, [])."""
            r = neighbor_radius_fn(len(tree))
            neigh = near_nodes(tree, new_pos, r)
            parent_candidates = neigh if neigh else [nearest(tree, new_pos)]
            parent_candidates = [nb for nb in parent_candidates
                                 if not self._is_edge_in_obstacle(nb.position, new_pos, obstacles, is_3d)]
            if not parent_candidates:
                return None, []
            parent = min(parent_candidates, key=lambda nb: nb.cost + math.dist(nb.position, new_pos))
            new_node = Node(new_pos, parent=None, cost=parent.cost + math.dist(parent.position, new_pos))
            parent.add_child(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((parent, new_node))
            return new_node, neigh

        def rewire_from(pivot: Node, neigh: List[Node]):
            for nb in neigh:
                if nb is pivot:
                    continue
                if self._is_edge_in_obstacle(pivot.position, nb.position, obstacles, is_3d):
                    continue
                new_c = pivot.cost + math.dist(pivot.position, nb.position)
                if new_c + 1e-12 < nb.cost:
                    if nb.parent is not None:
                        try:
                            edges.remove((nb.parent, nb))
                        except ValueError:
                            pass
                        if nb in nb.parent.children:
                            nb.parent.children.remove(nb)
                    pivot.add_child(nb)
                    nb.cost = new_c
                    edges.append((pivot, nb))

        def stitch_paths(meet_a: Node, meet_b: Node, a_is_start_side: bool) -> List[Tuple[float, ...]]:
            """Create full path from start to goal using meeting nodes from both trees."""
            path_a = meet_a.path_from_root()
            path_b = meet_b.path_from_root()
            if path_a and path_b and path_a[-1] == path_b[-1]:
                merged = path_a + path_b[-2::-1]
            else:
                merged = path_a + path_b[::-1]
            return merged if a_is_start_side else merged[::-1]

        def path_cost(path):
            return sum(math.dist(path[i], path[i+1]) for i in range(len(path)-1))

        # ---------- Main loop ----------
        for it in range(self.max_iter):
            # Hard time limit
            if time.time() - start_time > self.time_limit_sec:
                return PlannerResult(
                    success=(len(best_path) > 0),
                    path=best_path if best_path else [],
                    nodes=nodes,
                    edges=edges
                )

            # Choose expansion/connection sides
            if it % 2 == 0:
                grow_tree, other_tree = tree_a, tree_b
                a_is_start_side = True
            else:
                grow_tree, other_tree = tree_b, tree_a
                a_is_start_side = False

            # Informed sampling (uniform before first solution; ellipsoid after)
            sample = self._sample_informed(start_position, goal_position, best_cost, c_min, bounds)

            # Extend grow_tree toward sample
            a_near = nearest(grow_tree, sample)
            a_new_pos = steer(a_near.position, sample)

            if (not within_bounds(a_new_pos)) or self._is_in_obstacle(a_new_pos, obstacles, is_3d):
                # count post-optimization iterations if already in post phase
                if found_first_solution:
                    post_iter_count += 1
                    no_improve_streak += 1
                    if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                        break
                continue
            if self._is_edge_in_obstacle(a_near.position, a_new_pos, obstacles, is_3d):
                if found_first_solution:
                    post_iter_count += 1
                    no_improve_streak += 1
                    if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                        break
                continue

            a_new, a_neigh = add_with_best_parent(grow_tree, a_new_pos)
            if a_new is None:
                if found_first_solution:
                    post_iter_count += 1
                    no_improve_streak += 1
                    if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                        break
                continue
            rewire_from(a_new, a_neigh)

            # Greedy connect other_tree toward a_new, with local rewiring each step
            b_near = nearest(other_tree, a_new.position)
            current = b_near
            connected_b = None

            while True:
                step_pos = steer(current.position, a_new.position)
                if (not within_bounds(step_pos)) or self._is_in_obstacle(step_pos, obstacles, is_3d):
                    break
                if self._is_edge_in_obstacle(current.position, step_pos, obstacles, is_3d):
                    break

                b_new, b_neigh = add_with_best_parent(other_tree, step_pos)
                if b_new is None:
                    break
                rewire_from(b_new, b_neigh)
                current = b_new

                # Try a final short bridge
                if math.dist(current.position, a_new.position) <= self.step_size:
                    if not self._is_edge_in_obstacle(current.position, a_new.position, obstacles, is_3d) and a_new.valid:
                        final_b = Node(
                            a_new.position,
                            parent=None,
                            cost=current.cost + math.dist(current.position, a_new.position)
                        )
                        current.add_child(final_b)
                        other_tree.append(final_b)
                        nodes.append(final_b)
                        edges.append((current, final_b))
                        connected_b = final_b
                    break

            # If a connection was made, evaluate and manage post-optimization counters
            if connected_b is not None:
                candidate_path = stitch_paths(a_new, connected_b, a_is_start_side)
                cand_cost = path_cost(candidate_path)

                if cand_cost + self.improve_tol < best_cost:
                    best_cost = cand_cost
                    best_path = candidate_path
                    success_state = True
                    no_improve_streak = 0
                else:
                    no_improve_streak += 1

                if not found_first_solution:
                    found_first_solution = True
                    post_iter_count = 0
                else:
                    post_iter_count += 1

                if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                    break
            else:
                # No successful connection this iteration
                if found_first_solution:
                    post_iter_count += 1
                    no_improve_streak += 1
                    if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                        break

        extracted_path = best_path if success_state else []
        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    # ---------------- Informed Sampling ----------------
    def _sample_informed(self, start, goal, c_best, c_min, bounds):
        """Uniform in bounds if no solution; otherwise sample within the start–goal ellipsoid."""
        dim = len(bounds)
