            v = [random.gauss(0.0, 1.0) for _ in range(dim)]
            n = math.sqrt(sum(x * x for x in v))
            if n > 1e-12:
                v = [x / n for x in v]
                r = random.random() ** (1.0 / dim)
                return tuple(v[i] * r for i in range(dim))

    def _cross(self, a, b):
        ax, ay, az = a
        bx, by, bz = b
        return (ay * bz - az * by, az * bx - ax * bz, ax * by - ay * bx)

    def _norm(self, v):
        return math.sqrt(sum(x * x for x in v))

    def _normalize(self, v):
        n = self._norm(v)
        if n < 1e-12:
            return v
        return tuple(x / n for x in v)

    # ---------------- Collision Utilities ----------------
    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
