        min_radius = max(10.0, self.step_size * 2.0)

        # Best solution tracking + post-optimization control
        best_cost = float("inf")
        best_path: List[Tuple[float, ...]] = []
        found_first_solution = False
        post_iter_count = 0
        no_improve_streak = 0

        # Hard time limit
        start_time = time.time()

        # ----------------- Helper functions -----------------
        def within_bounds(p):
            return all(0.0 <= p[d] <= bounds[d] for d in range(dim))

        def clamp_bounds(p):
            return tuple(min(max(p[d], 0.0), bounds[d]) for d in range(dim))

        def steer(from_pos, to_pos):
            d = math.dist(from_pos, to_pos)
            if d <= self.step_size:
                return clamp_bounds(to_pos)
            r = self.step_size / d
            return clamp_bounds(tuple(from_pos[i] + (to_pos[i] - from_pos[i]) * r for i in range(dim)))

        def nearest(tree, pt):
            return min(tree, key=lambda n: math.dist(n.position, pt))

        def neighbor_radius(n_nodes: int) -> float:
            if n_nodes < 2:
                return min_radius
            r_theory = self.neighbor_gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(min_radius, min(max_radius, r_theory))

        def near_nodes(tree, pos, radius):
            return [nd for nd in tree if math.dist(nd.position, pos) <= radius]

        def uniform_sample():
            return tuple(random.uniform(0.0, bounds[d]) for d in range(dim))

        def sample_free(active_tree, other_tree):
            # Goal bias toward the global goal or start depending on active tree
            if random.random() < self.goal_sample_rate:
                return goal_position if active_tree is tree_start else start_position
            # Bridge bias: sample around the opposite root to encourage meeting
            if random.random() < self.bridge_bias_rate:
                anchor = other_tree[0].position
                jitter = tuple(random.uniform(-2.0*self.step_size, 2.0*self.step_size) for _ in range(dim))
                s = clamp_bounds(tuple(anchor[i] + jitter[i] for i in range(dim)))
                return s if not self._is_in_obstacle(s, obstacles, is_3d) else uniform_sample()
            # Uniform fallback
            return uniform_sample()

        def add_with_best_parent(tree, new_pos):
            """Insert new_pos with RRT* best-parent selection; returns (new_node, neighbors) or (None, [])."""
            r = neighbor_radius(len(tree))
            neigh = near_nodes(tree, new_pos, r)
            if not neigh:
                parent = nearest(tree, new_pos)
                if self._is_edge_in_obstacle(parent.position, new_pos, obstacles, is_3d):
                    return None, []
            else:
                candidates = [nb for nb in neigh if not self._is_edge_in_obstacle(nb.position, new_pos, obstacles, is_3d)]
                if not candidates:
                    return None, []
                parent = min(candidates, key=lambda nb: nb.cost + math.dist(nb.position, new_pos))

            new_node = Node(new_pos, parent=None, cost=parent.cost + math.dist(parent.position, new_pos))
            parent.add_child(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((parent, new_node))
            return new_node, neigh

        def rewire_from(pivot: Node, neigh: List[Node]):
            """Local RRT* rewiring around pivot (edge and cost validated)."""
            for nb in neigh:
                if nb is pivot:
                    continue
                if self._is_edge_in_obstacle(pivot.position, nb.position, obstacles, is_3d):
                    continue
                new_c = pivot.cost + math.dist(pivot.position, nb.position)
                if new_c + 1e-12 < nb.cost:
                    if nb.parent is not None:
                        try:
                            edges.remove((nb.parent, nb))
                        except ValueError:
                            pass
                        if nb in nb.parent.children:
                            nb.parent.children.remove(nb)
                    pivot.add_child(nb)
                    nb.cost = new_c
                    edges.append((pivot, nb))

        def stitch_paths(a_node: Node, b_node: Node, a_is_start_side: bool) -> List[Tuple[float, ...]]:
            """Combine root→a_node and root→b_node into a single start→goal path."""
            pa = a_node.path_from_root()
            pb = b_node.path_from_root()
            if pa and pb and pa[-1] == pb[-1]:
                merged = pa + pb[-2::-1]
            else:
                merged = pa + pb[::-1]
            return merged if a_is_start_side else merged[::-1]

        def path_cost(path: List[Tuple[float, ...]]) -> float:
            return sum(math.dist(path[i], path[i+1]) for i in range(len(path)-1))

        # ----------------- Main loop -----------------
        for it in range(self.max_iter):
            # Enforce 30s time limit (return best so far)
            if time.time() - start_time > self.time_limit_sec:
                return PlannerResult(
                    success=(len(best_path) > 0),
                    path=best_path if best_path else [],
                    nodes=nodes,
                    edges=edges
                )

            # Alternate trees
            if it % 2 == 0:
                grow_tree, other_tree = tree_start, tree_goal
                a_is_start_side = True
            else:
                grow_tree, other_tree = tree_goal, tree_start
                a_is_start_side = False

            # Sampling
            s = sample_free(grow_tree, other_tree)

            # Extend grow_tree toward sample
            a_near = nearest(grow_tree, s)
            a_new_pos = steer(a_near.position, s)

            # Node & edge validity
            if not within_bounds(a_new_pos) or self._is_in_obstacle(a_new_pos, obstacles, is_3d):
                if found_first_solution:
                    post_iter_count += 1
                    no_improve_streak += 1
                    if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                        break
                continue
            if self._is_edge_in_obstacle(a_near.position, a_new_pos, obstacles, is_3d):
                if found_first_solution:
                    post_iter_count += 1
                    no_improve_streak += 1
                    if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                        break
                continue

            a_new, a_neigh = add_with_best_parent(grow_tree, a_new_pos)
            if a_new is None:
                if found_first_solution:
                    post_iter_count += 1
                    no_improve_streak += 1
                    if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                        break
                continue
            rewire_from(a_new, a_neigh)

            # Greedy connect the other tree toward a_new with stepwise rewiring
            b_near = nearest(other_tree, a_new.position)
            current = b_near
            connected_b = None

            while True:
                step_pos = steer(current.position, a_new.position)
                if not within_bounds(step_pos) or self._is_in_obstacle(step_pos, obstacles, is_3d):
                    break
                if self._is_edge_in_obstacle(current.position, step_pos, obstacles, is_3d):
                    break

                b_new, b_neigh = add_with_best_parent(other_tree, step_pos)
                if b_new is None:
                    break
                rewire_from(b_new, b_neigh)
                current = b_new

                # Try to close the final short bridge exactly to a_new
                if math.dist(current.position, a_new.position) <= self.step_size:
                    if not self._is_edge_in_obstacle(current.position, a_new.position, obstacles, is_3d) and a_new.valid:
                        final_b = Node(
                            a_new.position,
                            parent=None,
                            cost=current.cost + math.dist(current.position, a_new.position)
                        )
                        current.add_child(final_b)
                        other_tree.append(final_b)
                        nodes.append(final_b)
                        edges.append((current, final_b))
                        connected_b = final_b
                    break

            # If a connection was made, evaluate candidate and manage post-optimization counters
            if connected_b is not None:
                candidate = stitch_paths(a_new, connected_b, a_is_start_side)
                cand_cost = path_cost(candidate)

                if cand_cost + self.improve_tol < best_cost:
                    best_cost = cand_cost
                    best_path = candidate
                    success_state = True
                    no_improve_streak = 0
                else:
                    no_improve_streak += 1

                if not found_first_solution:
                    found_first_solution = True
                    post_iter_count = 0
                else:
                    post_iter_count += 1

                if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                    break
            else:
                # No successful connection this iteration
                if found_first_solution:
                    post_iter_count += 1
                    no_improve_streak += 1
                    if post_iter_count >= self.post_opt_iters or no_improve_streak >= self.max_no_improve:
                        break

        extracted_path = best_path if success_state else []
        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    # ----------------- Collision utilities -----------------
    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(
                from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps)
                for d in range(len(from_pos))
            )
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False
