class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0, radius: float = 10.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.radius = radius

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        start_tree = [Node(start_position)]
        goal_tree = [Node(goal_position)]
        nodes = []
        edges = []
        success_state = False
        extracted_path = []

        for iter in range(self.max_iter):
            rand = self._sample_free(bounds, obstacles, is_3d)
            for tree_a, tree_b in [(start_tree, goal_tree), (goal_tree, start_tree)]:
                nearest = self._nearest(tree_a, rand)
                new_pos = self._steer(nearest.position, rand, self.step_size)
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    continue
                if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
                    continue

                new_node = Node(new_pos)
                near_nodes = self._near(tree_a, new_pos, self.radius)
                min_cost = nearest.cost + self._distance(nearest.position, new_pos)
                min_parent = nearest

                for near in near_nodes:
                    cost = near.cost + self._distance(near.position, new_pos)
                    if cost < min_cost and not self._is_edge_in_obstacle(near.position, new_pos, obstacles, is_3d):
                        min_cost = cost
                        min_parent = near

                new_node.parent = min_parent
                new_node.cost = min_cost
                min_parent.add_child(new_node)
                tree_a.append(new_node)
                nodes.append(new_node)
                edges.append((min_parent, new_node))

                for near in near_nodes:
                    if near == min_parent:
                        continue
                    new_cost = new_node.cost + self._distance(new_node.position, near.position)
                    if new_cost < near.cost and not self._is_edge_in_obstacle(new_node.position, near.position, obstacles, is_3d):
                        if near.parent:
                            edges.remove((near.parent, near))
                        near.parent = new_node
                        near.cost = new_cost
                        new_node.add_child(near)
                        edges.append((new_node, near))

                connect_node = self._nearest(tree_b, new_node.position)
                if self._distance(new_node.position, connect_node.position) < self.step_size and not self._is_edge_in_obstacle(new_node.position, connect_node.position, obstacles, is_3d):
                    success_state = True
                    extracted_path = self._extract_path(new_node, connect_node)
                    return PlannerResult(True, extracted_path, start_tree + goal_tree, edges)

        return PlannerResult(False, [], start_tree + goal_tree, edges)

    def _sample_free(self, bounds, obstacles, is_3d):
        while True:
            point = tuple(random.uniform(0, bounds[i]) for i in range(3 if is_3d else 2))
            if not self._is_in_obstacle(point, obstacles, is_3d):
                return point

    def _nearest(self, tree, point):
        return min(tree, key=lambda node: self._distance(node.position, point))

    def _near(self, tree, point, radius):
        return [node for node in tree if self._distance(node.position, point) <= radius]

    def _steer(self, from_pos, to_pos, step_size):
        dist = self._distance(from_pos, to_pos)
        if dist <= step_size:
            return to_pos
        ratio = step_size / dist
        return tuple(from_pos[i] + (to_pos[i] - from_pos[i]) * ratio for i in range(len(from_pos)))

    def _extract_path(self, node_a, node_b):
        path_a = []
        while node_a:
            path_a.append(node_a.position)
            node_a = node_a.parent
        path_b = []
        while node_b:
            path_b.append(node_b.position)
            node_b = node_b.parent
        return list(reversed(path_a)) + path_b

    def _distance(self, a, b):
        return math.dist(a, b)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = self._distance(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] for 2D or 3D coords
        self.parent = parent            # Parent node or None
        self.cost = cost                # Cost from root node
        self.children = []
        self.valid = True               # For collision validity

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self) -> list:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 prune_interval: int=150, prune_threshold: int=100,
                 smoothing_iterations: int=50, collision_resolution: float=1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_resolution = collision_resolution
        self.min_radius = 5.0
        self.max_radius = 30.0

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        success_state = False
        extracted_path = []
        nodes = []
        edges = []

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio*(to_p[d] - from_p[d]) for d in range(dim))
            # Clamp within bounds
            clamped = tuple(max(0.0, min(new_p[d], bounds[d])) for d in range(dim))
            return clamped

        def is_in_obstacle(p):
            for obs in obstacles:
                if is_3d:
                    x,y,z,w,h,d = obs
                    px,py,pz = p
                    if x <= px <= x+w and y <= py <= y+h and z <= pz <= z+d:
                        return True
                else:
                    x,y,w,h = obs
                    px,py = p
                    if x <= px <= x+w and y <= py <= y+h:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2):
            d_edge = dist(p1, p2)
            steps = max(1, int(d_edge / self.collision_resolution))
            for i in range(steps +1):
                interp = tuple(p1[d] + (p2[d]-p1[d])* i / steps for d in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        def neighbor_radius(iter, n_nodes):
            n = max(n_nodes, 1)
            r_iter = self.max_radius * (1 - (iter / self.max_iter)**0.9)
            r_log = self.min_radius + (self.max_radius - self.min_radius) * math.sqrt(math.log(n+1)/n)
            return max(self.min_radius, min(r_iter, r_log))

        def uniform_sample():
            for _ in range(20):
                sample = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(sample):
                    return sample
            # fallback random if all fail
            return tuple(random.uniform(0, bounds[d]) for d in range(dim))

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best <= c_min * 1.00001:
                return uniform_sample()

            center = np.array([(start[d] + goal[d])/2 for d in range(dim)])
            direction = np.array([(goal[d] - start[d])/c_min for d in range(dim)])
            I = np.eye(dim)
            u = direction - I[:,0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u /= norm_u
                rot = I - 2 * np.outer(u,u)
            else:
                rot = I

            r1 = c_best / 2.0
            r_other = math.sqrt(max(r1**2 - (c_min/2)**2, 0))
            radii = np.array([r1] + [r_other]*(dim-1))

            for _ in range(100):
                x_ball = np.random.uniform(-1,1,dim)
                if np.linalg.norm(x_ball) <= 1:
                    sample = rot @ (x_ball * radii) + center
                    p = tuple(float(v) for v in sample)
                    if in_bounds(p) and not is_in_obstacle(p):
                        return p
            return uniform_sample()

        def nearest(tree, point):
            return min(tree, key=lambda n: dist(n.position, point))

        def near_nodes(tree, point, radius):
            r_sq = radius*radius
            return [node for node in tree if sum((node.position[d]-point[d])**2 for d in range(dim)) <= r_sq]

        def prune_nodes(all_nodes, tree_start, tree_goal, best_cost):
            to_remove = []
            for node in all_nodes:
                if node is root_start or node is root_goal:
                    continue
                heuristic = dist(node.position, goal_pos)
                if node.cost + heuristic >= best_cost:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except ValueError:
                        pass
                try:
                    all_nodes.remove(node)
                except ValueError:
                    pass
                if node in tree_start:
                    tree_start.remove(node)
                if node in tree_goal:
                    tree_goal.remove(node)
                node.valid = False

        def prune_dead_end_nodes():
            removed = []
            for node in nodes[:]:
                if node is root_start or node is root_goal:
                    continue
                if not node.children and node.valid:
                    if node.parent:
                        try:
                            node.parent.children.remove(node)
                        except ValueError:
                            pass
                    try:
                        nodes.remove(node)
                    except ValueError:
                        pass
                    edges[:] = [(p,c) for (p,c) in edges if p != node and c != node]
                    node.valid = False
                    removed.append(node)
            return removed

        def shortcut_path(path):
            length = len(path)
            if length < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path)-3)
                j = random.randint(i+2, len(path)-1)
                p1, p2 = path[i], path[j]
                if not is_edge_in_obstacle(p1, p2):
                    path = path[:i+1] + path[j:]
            return path

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes.extend([root_start, root_goal])
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        for itr in range(self.max_iter):
            sample_pt = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate growth of smaller tree
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            nearest_a = nearest(tree_a, sample_pt)
            new_pos = steer(nearest_a.position, sample_pt)

            if (not in_bounds(new_pos)) or is_in_obstacle(new_pos) or is_edge_in_obstacle(nearest_a.position, new_pos):
                continue

            radius = neighbor_radius(itr, len(nodes))
            neighbors = near_nodes(tree_a, new_pos, radius)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in neighbors:
                candidate_cost = node.cost + dist(node.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and not is_edge_in_obstacle(node.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewiring neighbors
            for neighbor in neighbors:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and not is_edge_in_obstacle(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            # Connect tree_b towards new_node incrementally
            nearest_b = nearest(tree_b, new_node.position)
            last_b = nearest_b
            max_steps = int(dist(last_b.position, new_node.position) / self.step_size) + 1
            connected = False

            for _ in range(max_steps):
                next_pos = steer(last_b.position, new_node.position)
                if (not in_bounds(next_pos)) or is_in_obstacle(next_pos) or is_edge_in_obstacle(last_b.position, next_pos):
                    break

                cost_b = last_b.cost + dist(last_b.position, next_pos)
                new_node_b = Node(next_pos, parent=last_b, cost=cost_b)
                last_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_b, new_node_b))

                near_b = near_nodes(tree_b, next_pos, radius)
                for neighbor_b in near_b:
                    if neighbor_b is last_b:
                        continue
                    cost_b_via_new = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_b_via_new + 1e-12 < neighbor_b.cost and not is_edge_in_obstacle(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_b_via_new)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and not is_edge_in_obstacle(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_a = connection_node.path_from_root()
                        path_b = new_node.path_from_root()
                    else:
                        path_a = new_node.path_from_root()
                        path_b = connection_node.path_from_root()

                    merged_path = path_a + path_b[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_b = new_node_b

            if connected:
                break

            if itr % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_nodes(nodes, tree_start, tree_goal, c_best)
                prune_dead_end_nodes()

        extracted_path = best_path if success_state else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=5.0,
                 max_neighbor_radius: float=30.0,
                 prune_interval: int=200,
                 prune_threshold: int=300,
                 smoothing_iterations: int=100,
                 collision_check_resolution: float=1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> PlannerResult:
        import math
        import random
        import numpy as np

        bounds = map.size                  # Tuple[int, ...]: (W,H) or (W,H,D)
        start_position = map.start         # Tuple[float, ...] (W,H) or (W,H,D)
        goal_position = map.goal           # Tuple[float, ...] (W,H) or (W,H,D)
        obstacles = map.obstacles          # Rectangular blocks: 2D=(x,y,w,h), 3D=(x,y,z,w,h,d)

        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w, h = obs
                    px, py = pos
                    if x <= px <= x + w and y <= py <= y + h:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2):
            distance = dist(p1, p2)
            steps = max(1, int(distance / self.collision_check_resolution))
            for i in range(steps + 1):
                interp = tuple(p1[d] + (p2[d] - p1[d]) * i / steps for d in range(dim))
                if self._is_in_obstacle(interp, obstacles, is_3d):
                    return True
            return False

        # KDTree helper for fast neighbor search
        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        # Collision cache for edges
        edge_collision_cache = {}

        def can_connect(p1, p2):
            if (p1, p2) in edge_collision_cache:
                return not edge_collision_cache[(p1, p2)]
            if (p2, p1) in edge_collision_cache:
                return not edge_collision_cache[(p2, p1)]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[(p1, p2)] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[(p1, p2)] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001:
                for _ in range(10):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))  # unit vector start->goal

            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            u = a1_np - np.array([1 if d == 0 else 0 for d in range(dim)])
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r_other = math.sqrt(max(r1 * r1 - (c_min / 2) * (c_min / 2), 0))
            radii = np.array([r1] + [r_other] * (dim - 1))

            for _ in range(100):
                x = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x) <= 1:
                    x = x * radii
                    p = rot @ x + np.array(center)
                    p_tuple = tuple(float(coord) for coord in p)
                    if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                        return p_tuple

            for _ in range(10):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p  # fallback last sample

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            to_remove = []
            for node in nodes:
                if node is root_start or node is root_goal:
                    continue
                heuristic = dist(node.position, goal_position)
                if node.cost + heuristic >= best_cost:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)

        root_start = Node(start_position, cost=0.0)
        root_goal = Node(goal_position, cost=0.0)
        nodes = [root_start, root_goal]
        tree_start = [root_start]
        tree_goal = [root_goal]
        edges = []

        c_best = float('inf')
        c_min = dist(start_position, goal_position)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_position, goal_position)

            # Alternate expanding smaller tree
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near_nodes_a = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes_a:
                cost_candidate = node.cost + dist(node.position, new_pos)
                if cost_candidate + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_candidate
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors for cost improvement
            for neighbor in near_nodes_a:
                if neighbor is best_parent:
                    continue
                c_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if c_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, c_via_new)
                    edges.append((new_node, neighbor))

            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_nodes_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_nodes_b:
                    if neighbor_b is last_node_b:
                        continue
                    c_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if c_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, c_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]: 2D or 3D coordinates
        self.parent = parent            # Node or None
        self.cost = cost                # Cost from root
        self.children = []
        self.valid = True               # For collision/pruning status

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except Exception:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path, node = [], self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]

    def is_leaf(self):
        return len(self.children) == 0
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 min_radius: float = 5.0, max_radius: float = 30.0,
                 prune_interval: int = 200, prune_threshold: int = 300,
                 smoothing_trials: int = 50):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_radius = min_radius
        self.max_radius = max_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_trials = smoothing_trials

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = dim == 3

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))
            # Clamp to bounds
            new_p = tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))
            return new_p

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_radius + (self.max_radius - self.min_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d)

        # KDTree substitute: brute force for simplicity and generality
        def nearest_node(tree, point):
            best = None
            bd = float('inf')
            for node in tree:
                d = dist(node.position, point)
                if d < bd:
                    bd = d
                    best = node
            return best

        def near_nodes(tree, pos, radius):
            r2 = radius * radius
            return [node for node in tree if sum((node.position[d]-pos[d])**2 for d in range(dim)) <= r2]

        def ellipse_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < 0.1:
                for _ in range(10):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                # fallback uniform sample
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))
            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            u = a1_np - np.array([1 if d == 0 else 0 for d in range(dim)])
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)  # Householder reflection
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r2_val = math.sqrt(max(r1*r1 - (c_min/2)*(c_min/2), 0))
            radii = np.array([r1] + [r2_val]*(dim-1))

            for _ in range(100):
                x = np.random.uniform(-1,1,dim)
                norm_x = np.linalg.norm(x)
                if norm_x <= 1 and norm_x > 1e-10:
                    x = x / norm_x
                    x = x * np.power(np.random.uniform(0,1), 1/dim)  # Uniform in unit ball
                    p = rot @ (x * radii) + np.array(center)
                    p_tuple = tuple(float(coord) for coord in p)
                    if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                        return p_tuple
            # fallback uniform
            for _ in range(10):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        def shortcut_path(path):
            if len(path) < 3:
                return path[:]
            smooth_path = path[:]
            for _ in range(self.smoothing_trials):
                if len(smooth_path) < 3:
                    break
                i = random.randint(0, len(smooth_path) - 3)
                j = random.randint(i + 2, len(smooth_path) -1)
                p1, p2 = smooth_path[i], smooth_path[j]
                if not is_edge_in_obstacle(p1, p2):
                    smooth_path = smooth_path[:i+1] + smooth_path[j:]
            return smooth_path

        def prune_nodes(best_cost):
            to_remove = []
            h = lambda pos: dist(pos, goal_pos)
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                if node.cost + h(node.position) >= best_cost - 1e-9:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except Exception:
                        pass
                try:
                    nodes.remove(node)
                except Exception:
                    pass
                if node in tree_start:
                    tree_start.remove(node)
                if node in tree_goal:
                    tree_goal.remove(node)
            return len(to_remove)

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []
        success_state = False

        for it in range(self.max_iter):
            q_rand = ellipse_sample(c_best, c_min, start_pos, goal_pos)
            # Alternate tree growth for balance
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            nearest_a = nearest_node(tree_a, q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not in_bounds(new_pos) or is_in_obstacle(new_pos):
                continue
            if is_edge_in_obstacle(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near_nodes_a = near_nodes(tree_a, new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes_a:
                cost_cand = node.cost + dist(node.position, new_pos)
                if cost_cand + 1e-12 < min_cost and not is_edge_in_obstacle(node.position, new_pos):
                    min_cost = cost_cand
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for neighbor in near_nodes_a:
                if neighbor is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, neighbor.position)
                if new_cost + 1e-12 < neighbor.cost and not is_edge_in_obstacle(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

            # Connect other tree toward new node
            nearest_b = nearest_node(tree_b, new_node.position)
            last_node_b = nearest_b
            connected = False
            steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not in_bounds(next_pos_b) or is_in_obstacle(next_pos_b):
                    break
                if is_edge_in_obstacle(last_node_b.position, next_pos_b):
                    break
                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_nodes_b = near_nodes(tree_b, next_pos_b, r)
                for neighbor_b in near_nodes_b:
                    if neighbor_b is last_node_b:
                        continue
                    new_cost_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if new_cost_b + 1e-12 < neighbor_b.cost and not is_edge_in_obstacle(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, new_cost_b)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and not is_edge_in_obstacle(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]
                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                break

            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_nodes(c_best)

        extracted_path = best_path if success_state else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 goal_sample_rate: float=0.15, radius_constant: float=20.0,
                 radius_min: float=5.0, prune_interval: int=150,
                 prune_threshold: int=150, smoothing_iterations: int=50):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.radius_constant = radius_constant
        self.radius_min = radius_min
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size                  # Tuple[int, ...]: (W,H) or (W,H,D)
        start_position = map.start         # Tuple[float, ...]
        goal_position = map.goal           # Tuple[float, ...]
        obstacles = map.obstacles          # Rectangular blocks: 2D=(x,y,w,h), 3D=(x,y,z,w,h,d)

        import random
        import math

        is_3d = len(bounds) == 3

        success_state = False
        extracted_path = []
        nodes = []
        edges = []

        start_root = Node(start_position, cost=0.0)
        goal_root = Node(goal_position, cost=0.0)
        tree_start = [start_root]
        tree_goal = [goal_root]
        nodes.extend([start_root, goal_root])

        def dist(a, b):
            return math.dist(a, b)

        def in_bounds(pos):
            for d, val in enumerate(pos):
                if val < 0 or val > bounds[d]:
                    return False
            return True

        def sample_free():
            # Adaptive bias towards goal and start to promote faster connections
            if random.random() < self.goal_sample_rate:
                return goal_position if random.random() < 0.6 else start_position
            else:
                while True:
                    sample = tuple(random.uniform(0, bounds[d]) for d in range(len(bounds)))
                    if not self._is_in_obstacle(sample, obstacles, is_3d):
                        return sample

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            ratio = self.step_size / d
            return tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(len(from_pos)))

        def nearest(tree, point):
            return min(tree, key=lambda node: dist(node.position, point))

        def near_nodes(tree, pos, radius):
            return [node for node in tree if dist(node.position, pos) <= radius]

        def radius_dynamic(n):
            if n == 0:
                return self.radius_min
            rad = self.radius_constant * math.sqrt(math.log(n + 1) / (n + 1))
            return max(rad, self.radius_min)

        def can_add_node(pos):
            if not in_bounds(pos) or self._is_in_obstacle(pos, obstacles, is_3d):
                return False
            return True

        def can_connect(p1, p2):
            if self._is_edge_in_obstacle(p1, p2, obstacles, is_3d):
                return False
            return True

        def try_extend(tree, sample_pt):
            if not tree:
                return None
            nearest_node = nearest(tree, sample_pt)
            new_pos = steer(nearest_node.position, sample_pt)

            if not can_add_node(new_pos):
                return None
            if not can_connect(nearest_node.position, new_pos):
                return None

            radius = radius_dynamic(len(tree))
            near = near_nodes(tree, new_pos, radius)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for node in near:
                tentative_cost = node.cost + dist(node.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors for improved cost
            for neighbor in near:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))
            return new_node

        def try_connect(tree, node):
            if not tree:
                return None
            nearest_node = nearest(tree, node.position)
            last_node = nearest_node

            while True:
                new_pos = steer(last_node.position, node.position)
                if not can_add_node(new_pos):
                    return None
                if not can_connect(last_node.position, new_pos):
                    return None

                new_cost = last_node.cost + dist(last_node.position, new_pos)
                new_node = Node(new_pos, parent=last_node, cost=new_cost)
                last_node.children.append(new_node)
                tree.append(new_node)
                nodes.append(new_node)
                edges.append((last_node, new_node))

                if dist(new_pos, node.position) <= self.step_size:
                    if can_connect(new_pos, node.position):
                        final_cost = new_node.cost + dist(new_pos, node.position)
                        final_node = Node(node.position, parent=new_node, cost=final_cost)
                        new_node.children.append(final_node)
                        tree.append(final_node)
                        nodes.append(final_node)
                        edges.append((new_node, final_node))
                        return final_node
                    else:
                        return None
                last_node = new_node

        def prune_dead_nodes():
            removed = []
            to_remove = [node for node in nodes if node not in (start_root, goal_root) and not node.children]
            for node in to_remove:
                try:
                    if node.parent:
                        node.parent.children.remove(node)
                    nodes.remove(node)
                    edges[:] = [(p, c) for (p, c) in edges if c != node and p != node]
                    removed.append(node)
                except Exception:
                    continue
            return removed

        def path_length(path):
            length = 0.0
            for i in range(len(path) - 1):
                length += dist(path[i], path[i + 1])
            return length

        def shortcut_smoothing(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i+1] + path[j:]
            return path

        for itr in range(self.max_iter):
            sample_pt = sample_free()

            # Always extend smaller tree to balance exploration/exploitation
            if len(tree_start) > len(tree_goal):
                tree_start, tree_goal = tree_goal, tree_start

            new_node_start = try_extend(tree_start, sample_pt)
            if new_node_start is None:
                continue

            new_node_goal = try_connect(tree_goal, new_node_start)
            if new_node_goal is not None:
                success_state = True
                path_start = new_node_start.path_from_root()
                path_goal = new_node_goal.path_from_root()
                raw_path = path_start + path_goal[::-1][1:]

                smoothed_path = shortcut_smoothing(raw_path)
                if path_length(smoothed_path) + 1e-12 < path_length(raw_path):
                    extracted_path = smoothed_path
                else:
                    extracted_path = raw_path
                break

            if itr > 0 and itr % self.prune_interval == 0 and len(nodes) > self.prune_threshold:
                prune_dead_nodes()

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=5.0,
                 max_neighbor_radius: float=30.0,
                 prune_interval: int=200,
                 prune_threshold: int=300,
                 smoothing_iterations: int=150,
                 collision_check_resolution: float=1.0,
                 goal_sample_rate: float=0.15):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> "PlannerResult":
        import math
        import random
        import numpy as np

        bounds = map.size                  # Tuple[int, ...]
        start_pos = map.start              # Tuple[float, ...]
        goal_pos = map.goal                # Tuple[float, ...]
        obstacles = map.obstacles          # List of obstacle tuples
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp to bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                # Uniform sampling with rejection inside obstacles
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                # fallback
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))
            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            u = a1_np - np.array([1 if d == 0 else 0 for d in range(dim)])
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            val = max(r1 * r1 - (c_min / 2) * (c_min / 2), 0)
            r_others = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x <= 1 and norm_x > 1e-15:
                    # Uniform sampling inside unit ball
                    radius_factor = np.random.uniform(0,1) ** (1/dim)
                    x_scaled = (x / norm_x) * radius_factor
                    x_scaled = x_scaled * radii
                    p = rot @ x_scaled + np.array(center)
                    p_tuple = tuple(float(coord) for coord in p)
                    if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                        return p_tuple

            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            if (p1, p2) in edge_collision_cache:
                return not edge_collision_cache[(p1, p2)]
            if (p2, p1) in edge_collision_cache:
                return not edge_collision_cache[(p2, p1)]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[(p1, p2)] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[(p1, p2)] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            removed = 0
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)
                removed += 1
            return removed

        # Initialization
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Balance tree extension: extend smaller tree first
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node_ in near_nodes:
                candidate_cost = node_.cost + dist(node_.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node_.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node_

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            # Incremental connect opposite tree towards new_node
            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Periodic pruning to reduce search space and improve efficiency
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=5.0,
                 max_neighbor_radius: float=30.0,
                 prune_interval: int=200,
                 prune_threshold: int=300,
                 smoothing_iterations: int=100,
                 collision_check_resolution: float=1.0,
                 goal_sample_rate: float=0.15):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> "PlannerResult":
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio*(to_pos[d]-from_pos[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def informed_sample(c_best, c_min, start, goal):
            import numpy as np
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(10):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not self._is_in_obstacle(p, obstacles, is_3d):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r_others = math.sqrt(max(r1**2 - (c_min / 2)**2, 0))
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(100):
                x = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x) <= 1:
                    sample = rot @ (x * radii) + center
                    sample_t = tuple(float(coord) for coord in sample)
                    if in_bounds(sample_t) and not self._is_in_obstacle(sample_t, obstacles, is_3d):
                        return sample_t

            for _ in range(10):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            reverse_key = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            elif reverse_key in edge_collision_cache:
                return not edge_collision_cache[reverse_key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if self._is_in_obstacle(pos, obstacles, is_3d):
                return False
            return True

        # KD-tree substitute for nearest and radius searches, brute-force for clarity & correctness
        class KDTree:
            def __init__(self, pts_nodes):
                self.points = [pn[0] for pn in pts_nodes]
                self.nodes = [pn[1] for pn in pts_nodes]

            def nearest(self, pt):
                best_node = None
                best_dist = float('inf')
                for p, n in zip(self.points, self.nodes):
                    d = dist(p, pt)
                    if d < best_dist:
                        best_dist = d
                        best_node = n
                return best_node

            def radius_search(self, pt, radius):
                r2 = radius*radius
                res = []
                for p, n in zip(self.points, self.nodes):
                    sqd = 0.0
                    for d_ in range(dim):
                        diff = p[d_] - pt[d_]
                        sqd += diff*diff
                        if sqd > r2:
                            break
                    else:
                        res.append(n)
                return res

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i+1] + path[j:]
            return path

        def prune_nonpromising_nodes(nodes_list, tree_s, tree_g, best_cost):
            # Remove dead-end (no children) and nodes with cost+heuristic >= best_cost
            to_remove = []
            for node in nodes_list:
                if node in (root_start, root_goal):
                    continue
                if not node.children:
                    to_remove.append(node)
                    continue
                h_cost = dist(node.position, goal_pos)
                if node.cost + h_cost >= best_cost:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except ValueError:
                        pass
                try:
                    nodes_list.remove(node)
                except ValueError:
                    pass
                if node in tree_s:
                    try:
                        tree_s.remove(node)
                    except ValueError:
                        pass
                if node in tree_g:
                    try:
                        tree_g.remove(node)
                    except ValueError:
                        pass
            return len(to_remove)

        # Initialization
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for itr in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate tree expansion for balanced growth
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(itr, len(nodes))
            near_a = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_a:
                cost_cand = node.cost + dist(node.position, new_pos)
                if cost_cand + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_cand
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewiring neighbors in tree_a
            for neighbor in near_a:
                if neighbor is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, neighbor.position)
                if new_cost + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

            # Incrementally connect tree_b to new_node
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_b = nearest_b
            connected = False

            max_steps = int(dist(last_b.position, new_node.position) / self.step_size) + 1
            for _ in range(max_steps):
                next_pos_b = steer(last_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_b.position, next_pos_b):
                    break

                cost_b = last_b.cost + dist(last_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_b, cost=cost_b)
                last_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_b:
                        continue
                    cost_via_new = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    # Extract and merge paths according to expansion order
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged)

                    connected = True
                    break

                last_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Periodic pruning for efficiency & robustness
            if itr > 0 and itr % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_nonpromising_nodes(nodes, tree_start, tree_goal, c_best)
                edge_collision_cache.clear()

        if success_state and extracted_path:
            # Final smoothing
            extracted_path = shortcut_path(extracted_path)

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * i / steps for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position             # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent                 # Node or None
        self.cost = cost                    # Path cost from root
        self.children: List[Node] = []      # For rewiring & pruning
        self.valid = True                   # Mark valid/pruned

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except (ValueError, AttributeError):
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0, max_neighbor_radius: float = 30.0,
                 prune_interval: int = 200, prune_threshold: int = 300,
                 smoothing_trials: int = 100, collision_check_resolution: float = 1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_trials = smoothing_trials
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> PlannerResult:
        import math, random, numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p: Tuple[float, ...]) -> bool:
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a: Tuple[float, ...], b: Tuple[float, ...]) -> float:
            return math.dist(a, b)

        def steer(from_p: Tuple[float, ...], to_p: Tuple[float, ...]) -> Tuple[float, ...]:
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))
            # Clamp to bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(n_nodes: int, iteration: int) -> float:
            if n_nodes <= 1:
                return self.max_neighbor_radius
            const_factor = max(n_nodes,1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration/self.max_iter)**0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(const_factor+1)/const_factor)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos: Tuple[float, ...]) -> bool:
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1: Tuple[float, ...], p2: Tuple[float, ...]) -> bool:
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        # KD-tree helper (brute-force for portability)
        class KDTree:
            def __init__(self, points_nodes: List[Tuple[Tuple[float, ...], Node]]):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt: Tuple[float, ...]) -> Node:
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best = self.nodes[i]
                return best

            def radius_search(self, pt: Tuple[float, ...], radius: float) -> List[Node]:
                r2 = radius * radius
                res = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        res.append(self.nodes[i])
                return res

        edge_collision_cache = {}

        def can_connect(p1: Tuple[float, ...], p2: Tuple[float, ...]) -> bool:
            key = (p1, p2) if p1 < p2 else (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos: Tuple[float, ...]) -> bool:
            return in_bounds(pos) and not is_in_obstacle(pos)

        # Informed ellipsoidal sample inside evolving domain biased by current best cost c_best
        def informed_sample(c_best: float, c_min: float,
                            start: Tuple[float, ...], goal: Tuple[float, ...]) -> Tuple[float, ...]:
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < 0.1:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])
            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)  # Householder reflection
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r2 = math.sqrt(max(r1*r1 - (c_min/2)**2, 0))
            radii = np.array([r1] + [r2]*(dim-1))

            for _ in range(150):
                x = np.random.uniform(-1,1,dim)
                norm_x = np.linalg.norm(x)
                if norm_x < 1e-10 or norm_x > 1:
                    continue
                x = x / norm_x
                x = x * np.power(np.random.uniform(0,1), 1/dim)  # Uniform in unit ball
                p = rot @ (x * radii) + center
                p_tuple = tuple(float(coord) for coord in p)
                if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                    return p_tuple

            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        # Adaptive sampling: sample near best known path with Gaussian noise, fallback to informed_sample
        def adaptive_sample(best_path: List[Tuple[float, ...]], c_best: float, c_min: float) -> Tuple[float, ...]:
            if best_path and random.random() < 0.75:
                for _ in range(50):
                    base_point = best_path[random.randint(0, len(best_path) - 1)]
                    noise = np.random.normal(0, self.step_size*0.5, dim)
                    sample_point = tuple(base_point[d] + noise[d] for d in range(dim))
                    if in_bounds(sample_point) and not is_in_obstacle(sample_point):
                        return sample_point
            return informed_sample(c_best, c_min, start_pos, goal_pos)

        # Shortcut smoothing by attempting to replace path segments with direct edges
        def shortcut_path(path: List[Tuple[float, ...]]) -> List[Tuple[float, ...]]:
            if len(path) < 3:
                return path[:]
            smooth = path[:]
            for _ in range(self.smoothing_trials):
                if len(smooth) < 3:
                    break
                i = random.randint(0, len(smooth)-3)
                j = random.randint(i+2, len(smooth)-1)
                p1, p2 = smooth[i], smooth[j]
                if can_connect(p1, p2):
                    smooth = smooth[:i+1] + smooth[j:]
            return smooth

        def prune_nodes(nodes_list: List[Node], best_cost: float, root_s: Node, root_g: Node) -> int:
            removed = 0
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for node in nodes_list:
                if node in (root_s, root_g):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-9:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except (ValueError, AttributeError):
                        pass
                try:
                    nodes_list.remove(node)
                except ValueError:
                    pass
                removed += 1
            return removed

        # Initialize roots and data structures
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        tree_start = [root_start]
        tree_goal = [root_goal]
        nodes = [root_start, root_goal]
        edges = []

        c_min = dist(start_pos, goal_pos)
        c_best = float('inf')
        best_path: List[Tuple[float, ...]] = []
        success_state = False

        for it in range(self.max_iter):
            q_rand = adaptive_sample(best_path, c_best, c_min)

            # Extend smaller tree first
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius_a = neighbor_radius(len(nodes), it)
            near_nodes_a = kdtree_a.radius_search(new_pos, radius_a)

            # Parent selection minimizing cost
            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes_a:
                cost_cand = node.cost + dist(node.position, new_pos)
                if cost_cand + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_cand
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for node in near_nodes_a:
                if node is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, node.position)
                if new_cost + 1e-12 < node.cost and can_connect(new_node.position, node.position):
                    try:
                        edges.remove((node.parent, node))
                    except ValueError:
                        pass
                    node.update_parent(new_node, new_cost)
                    edges.append((new_node, node))

            # Connect tree_b to new_node incrementally
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            max_steps = int(dist(nearest_b.position, new_node.position) / self.step_size) + 1
            last_node_b = nearest_b
            connected = False

            for _step in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break
                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                radius_b = neighbor_radius(len(nodes), it)
                near_nodes_b = kdtree_b.radius_search(next_pos_b, radius_b)
                for nbr_b in near_nodes_b:
                    if nbr_b is last_node_b:
                        continue
                    new_cost_b = new_node_b.cost + dist(new_node_b.position, nbr_b.position)
                    if new_cost_b + 1e-12 < nbr_b.cost and can_connect(new_node_b.position, nbr_b.position):
                        try:
                            edges.remove((nbr_b.parent, nbr_b))
                        except ValueError:
                            pass
                        nbr_b.update_parent(new_node_b, new_cost_b)
                        edges.append((new_node_b, nbr_b))

                # Check connection to new_node position
                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    cost_connect = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connect_node = Node(new_node.position, parent=new_node_b, cost=cost_connect)
                    new_node_b.children.append(connect_node)
                    tree_b.append(connect_node)
                    nodes.append(connect_node)
                    edges.append((new_node_b, connect_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connect_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connect_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]
                    if cost_connect + 1e-12 < c_best:
                        c_best = cost_connect
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                break

            # Periodic pruning when a good path exists and node count is high
            if (it > 0 and it % self.prune_interval == 0 and
                len(nodes) > self.prune_threshold and c_best < float('inf')):
                removed_s = prune_nodes(tree_start, c_best, root_start, root_goal)
                removed_g = prune_nodes(tree_goal, c_best, root_start, root_goal)
                edge_collision_cache.clear()

        extracted_path = best_path if success_state else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D or 3D
        self.parent = parent            # Node or None
        self.cost = cost                # Cost from root
        self.children = []
        self.valid = True               # For collision status

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        node, path = self, []
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=5.0,
                 max_neighbor_radius: float=30.0,
                 prune_interval: int=200,
                 prune_threshold: int=300,
                 smoothing_iterations: int=100,
                 collision_check_resolution: float=1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> 'PlannerResult':
        import random
        import math
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d in range(self.dim):
                        delta = p[d] - pt[d]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            import numpy as np

            if c_best == float('inf') or c_best < c_min * 1.00001:
                for _ in range(10):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))

            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            # Householder reflection matrix to rotate unit vector a1 to x-axis
            v = a1_np - np.array([1] + [0]*(dim - 1))
            norm_v = np.linalg.norm(v)
            if norm_v > 1e-10:
                v /= norm_v
                rot = id_mat - 2 * np.outer(v, v)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r_others = math.sqrt(max(r1 * r1 - (c_min / 2) ** 2, 0))
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(100):
                x_ball = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x_ball) <= 1.0:
                    sample = rot @ (x_ball * radii) + np.array(center)
                    p = tuple(float(coord) for coord in sample)
                    if in_bounds(p) and not is_in_obstacle(p):
                        return p

            for _ in range(10):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p  # fallback

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            key_rev = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if key_rev in edge_collision_cache:
                return not edge_collision_cache[key_rev]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i+1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            removed = 0
            to_remove = []
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                heuristic = dist(node.position, goal_pos)
                if node.cost + heuristic >= best_cost:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near:
                cost_candidate = node.cost + dist(node.position, new_pos)
                if cost_candidate + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_candidate
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewiring neighbors for cost improvement
            for neighbor in near:
                if neighbor is best_parent:
                    continue
                c_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if c_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, c_via_new)
                    edges.append((new_node, neighbor))

            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    c_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if c_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, c_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D or 3D
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision/pruning state

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=5.0,
                 max_neighbor_radius: float=30.0,
                 prune_interval: int=200,
                 prune_threshold: int=300,
                 smoothing_iterations: int=100,
                 collision_check_resolution: float=1.0,
                 goal_sample_rate: float=0.15):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> "PlannerResult":
        import math
        import random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp to map bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        # KD-tree for accelerated neighbor search (brute-force for simplicity)
        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        # Ellipsoidal informed sampling with fallback uniform sampling
        def informed_sample(c_best, c_min, start, goal):
            import numpy as np

            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                # Goal bias & uniform sampling when no good solution
                for _ in range(10):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))  # unit vector start->goal

            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            u = a1_np - np.array([1 if d == 0 else 0 for d in range(dim)])
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r_others = math.sqrt(max(r1 * r1 - (c_min / 2) * (c_min / 2), 0))
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(100):
                x = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x) <= 1:
                    x = x * radii
                    p = rot @ x + np.array(center)
                    p_tuple = tuple(float(coord) for coord in p)
                    if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                        return p_tuple

            # Fallback uniform sampling if ellipsoid samples fail
            for _ in range(10):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            if (p1, p2) in edge_collision_cache:
                return not edge_collision_cache[(p1, p2)]
            if (p2, p1) in edge_collision_cache:
                return not edge_collision_cache[(p2, p1)]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[(p1, p2)] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[(p1, p2)] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            removed = 0
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-9:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)
                removed += 1
            return removed

        # Initialize roots
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate growth direction for balanced expansion
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near:
                cost_candidate = node.cost + dist(node.position, new_pos)
                if cost_candidate + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_candidate
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewiring neighbors to improve path cost
            for neighbor in near:
                if neighbor is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, neighbor.position)
                if new_cost + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

            # Try connecting the other tree towards new_node
            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new)
                        edges.append((new_node_b, neighbor_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True
                    # Retrieve paths according to tree order
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Periodic pruning for efficiency if enough nodes and solution found
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D or 3D
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision/pruning etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=5.0,
                 max_neighbor_radius: float=30.0,
                 prune_interval: int=200,
                 prune_threshold: int=300,
                 smoothing_iterations: int=100,
                 collision_check_resolution: float=1.0,
                 goal_sample_rate: float=0.15):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> "PlannerResult":
        import math
        import random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            import numpy as np

            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(10):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))

            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            u = a1_np - np.array([1 if d == 0 else 0 for d in range(dim)])
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r_others = math.sqrt(max(r1 * r1 - (c_min / 2) * (c_min / 2), 0))
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(100):
                x = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x) <= 1:
                    x = x * radii
                    p = rot @ x + np.array(center)
                    p_tuple = tuple(float(coord) for coord in p)
                    if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                        return p_tuple

            for _ in range(10):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            if (p1, p2) in edge_collision_cache:
                return not edge_collision_cache[(p1, p2)]
            if (p2, p1) in edge_collision_cache:
                return not edge_collision_cache[(p2, p1)]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[(p1, p2)] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[(p1, p2)] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            removed = 0
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-9:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)
                removed += 1
            return removed


        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate tree growth for balance
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near:
                cost_candidate = node.cost + dist(node.position, new_pos)
                if cost_candidate + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_candidate
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Incremental rewiring neighbors
            for neighbor in near:
                if neighbor is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, neighbor.position)
                if new_cost + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

            # Connect other tree towards new_node
            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new)
                        edges.append((new_node_b, neighbor_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

