class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from start root
        self.children = []              # Children for rewiring/pruning
        self.valid = True               # Node validity flag

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except (ValueError, AttributeError):
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=5.0, max_neighbor_radius: float=30.0,
                 prune_interval: int=200, prune_threshold: int=300,
                 smoothing_trials: int=120, collision_check_resolution: float=1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_trials = smoothing_trials
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> "PlannerResult":
        import math, random, numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(n_nodes, iteration):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            const_factor = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(const_factor + 1) / const_factor)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        # Collision checks helpers
        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2) if p1 < p2 else (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            return in_bounds(pos) and not is_in_obstacle(pos)

        # KDTree helper (brute force)
        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                res = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        res.append(self.nodes[i])
                return res

        # Informed sampling inside evolving ellipsoid biased by current best path cost
        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < 0.1:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])
            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)  # Householder reflection
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r2 = math.sqrt(max(r1*r1 - (c_min/2)**2, 0))
            radii = np.array([r1] + [r2] * (dim - 1))

            for _ in range(150):
                x = np.random.uniform(-1,1,dim)
                norm_x = np.linalg.norm(x)
                if norm_x < 1e-10 or norm_x > 1:
                    continue
                x = x / norm_x
                x = x * np.power(np.random.uniform(0,1), 1 / dim)  # uniform in unit ball
                p = rot @ (x * radii) + center
                p_tuple = tuple(float(coord) for coord in p)
                if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                    return p_tuple

            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        # Adaptive sampling: near best known path with Gaussian noise, else informed_sample
        def adaptive_sample(best_path, c_best, c_min):
            if best_path and random.random() < 0.75:
                for _ in range(50):
                    base_point = best_path[random.randint(0, len(best_path) - 1)]
                    noise = np.random.normal(0, self.step_size * 0.5, dim)
                    sample_point = tuple(base_point[d] + noise[d] for d in range(dim))
                    if in_bounds(sample_point) and not is_in_obstacle(sample_point):
                        return sample_point
            return informed_sample(c_best, c_min, start_pos, goal_pos)

        # Shortcut smoothing: repeatedly attempt replacing path segments with direct edges
        def shortcut_path(path):
            if len(path) < 3:
                return path[:]
            smooth = path[:]
            for _ in range(self.smoothing_trials):
                if len(smooth) < 3:
                    break
                i = random.randint(0, len(smooth) - 3)
                j = random.randint(i + 2, len(smooth) - 1)
                p1, p2 = smooth[i], smooth[j]
                if can_connect(p1, p2):
                    smooth = smooth[:i+1] + smooth[j:]
            return smooth

        # Prunes nodes that cannot possibly improve the current best path
        def prune_nodes(nodes_list, best_cost, root_s, root_g):
            removed = 0
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for node in nodes_list:
                if node in (root_s, root_g):
                    continue
                # If cost + heuristic exceeds or equals best_cost, remove
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except (ValueError, AttributeError):
                        pass
                try:
                    nodes_list.remove(node)
                    removed += 1
                except ValueError:
                    pass
            return removed

        # Initialize trees
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        tree_start = [root_start]
        tree_goal = [root_goal]
        nodes = [root_start, root_goal]
        edges = []

        c_min = dist(start_pos, goal_pos)
        c_best = float('inf')
        best_path = []
        success_state = False

        for it in range(self.max_iter):
            q_rand = adaptive_sample(best_path, c_best, c_min)

            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            # Node and edge collision check before insertion
            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius_a = neighbor_radius(len(nodes), it)
            near_nodes_a = kdtree_a.radius_search(new_pos, radius_a)

            # Choose parent minimizing cost + collision check edges lazily
            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes_a:
                cost_cand = node.cost + dist(node.position, new_pos)
                if cost_cand + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_cand
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors in tree_a for better costs if possible
            for node in near_nodes_a:
                if node is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, node.position)
                if new_cost + 1e-12 < node.cost and can_connect(new_node.position, node.position):
                    try:
                        edges.remove((node.parent, node))
                    except ValueError:
                        pass
                    node.update_parent(new_node, new_cost)
                    edges.append((new_node, node))

            # Extend tree_b towards new_node incrementally
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            max_steps = int(dist(nearest_b.position, new_node.position) / self.step_size) + 1
            last_node_b = nearest_b
            connected = False

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break
                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                radius_b = neighbor_radius(len(nodes), it)
                near_nodes_b = kdtree_b.radius_search(next_pos_b, radius_b)
                # Rewire neighbors in tree_b
                for nbr_b in near_nodes_b:
                    if nbr_b is last_node_b:
                        continue
                    new_cost_b = new_node_b.cost + dist(new_node_b.position, nbr_b.position)
                    if new_cost_b + 1e-12 < nbr_b.cost and can_connect(new_node_b.position, nbr_b.position):
                        try:
                            edges.remove((nbr_b.parent, nbr_b))
                        except ValueError:
                            pass
                        nbr_b.update_parent(new_node_b, new_cost_b)
                        edges.append((new_node_b, nbr_b))

                # Check if can connect directly to new_node
                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    cost_connect = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connect_node = Node(new_node.position, parent=new_node_b, cost=cost_connect)
                    new_node_b.children.append(connect_node)
                    tree_b.append(connect_node)
                    nodes.append(connect_node)
                    edges.append((new_node_b, connect_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connect_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connect_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]
                    # Check for improvement of best cost
                    total_cost = cost_connect
                    if total_cost + 1e-12 < c_best:
                        c_best = total_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                break

            # Periodic pruning of nodes unlikely to improve best path
            if (it > 0 and it % self.prune_interval == 0 and
                len(nodes) > self.prune_threshold and c_best < float('inf')):
                prune_nodes(tree_start, c_best, root_start, root_goal)
                prune_nodes(tree_goal, c_best, root_start, root_goal)
                edge_collision_cache.clear()

        extracted_path = best_path if success_state else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent: 'Node' = None, cost: float = 0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = parent            # Node or None
        self.cost = cost                # Cost from root
        self.children: List[Node] = []
        self.valid = True               # For pruning or collision checks

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except (ValueError, AttributeError):
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = 5.0
        self.max_neighbor_radius = 30.0
        self.prune_interval = 150
        self.prune_threshold = 250
        self.smoothing_iterations = 150
        self.collision_check_resolution = 1.0
        self.goal_sample_rate = 0.1

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p: Tuple[float, ...]) -> bool:
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a: Tuple[float, ...], b: Tuple[float, ...]) -> float:
            return math.dist(a, b)

        def steer(from_p: Tuple[float, ...], to_p: Tuple[float, ...]) -> Tuple[float, ...]:
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))
            # Clamp within bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(n_nodes: int, iteration: int) -> float:
            if n_nodes <= 1:
                return self.max_neighbor_radius
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(n_nodes + 1) / n_nodes)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos: Tuple[float, ...]) -> bool:
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w, h = obs
                    px, py = pos
                    if x <= px <= x + w and y <= py <= y + h:
                        return True
            return False

        def is_edge_in_obstacle(from_p: Tuple[float, ...], to_p: Tuple[float, ...]) -> bool:
            distance = dist(from_p, to_p)
            steps = max(1, int(distance / self.collision_check_resolution))
            for i in range(steps + 1):
                interp = tuple(from_p[d] + (to_p[d] - from_p[d]) * (i / steps) for d in range(dim))
                if is_in_obstacle(interp):
                    return True
            return False

        class KDTree:
            def __init__(self, points_nodes: List[Tuple[Tuple[float, ...], Node]]):
                self.points = [pn[0] for pn in points_nodes]
                self.nodes = [pn[1] for pn in points_nodes]
                self.dim = dim

            def nearest(self, pt: Tuple[float, ...]) -> Node:
                best_node = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best_node = self.nodes[i]
                return best_node

            def radius_search(self, pt: Tuple[float, ...], radius: float) -> List[Node]:
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    squared_dist = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        squared_dist += delta * delta
                        if squared_dist > r2:
                            break
                    if squared_dist <= r2:
                        result.append(self.nodes[i])
                return result

        edge_collision_cache = {}

        def can_connect(p1: Tuple[float, ...], p2: Tuple[float, ...]) -> bool:
            key = (p1, p2) if p1 < p2 else (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos: Tuple[float, ...]) -> bool:
            return in_bounds(pos) and (not is_in_obstacle(pos))

        def informed_sample(c_best: float, c_min: float, start: Tuple[float, ...], goal: Tuple[float, ...]) -> Tuple[float, ...]:
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))
            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])
            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u /= norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat
            r1 = c_best / 2.0
            val = max(r1 * r1 - (c_min / 2) ** 2, 0)
            r_others = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_others] * (dim - 1))
            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x < 1e-15 or norm_x > 1:
                    continue
                radius_factor = np.random.uniform(0, 1) ** (1 / dim)
                x_scaled = (x / norm_x) * radius_factor
                sample = rot @ (x_scaled * radii) + center
                p = tuple(float(coord) for coord in sample)
                if in_bounds(p) and not is_in_obstacle(p):
                    return p
            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        def shortcut_path(path: List[Tuple[float, ...]]) -> List[Tuple[float, ...]]:
            if len(path) < 3:
                return path[:]
            p = path[:]
            for _ in range(self.smoothing_iterations):
                if len(p) < 3:
                    break
                i = random.randint(0, len(p) - 3)
                j = random.randint(i + 2, len(p) - 1)
                if can_connect(p[i], p[j]):
                    p = p[:i + 1] + p[j:]
            return p

        def prune_nodes(nodes_list: List[Node], best_cost: float, root_start: Node, root_goal: Node) -> int:
            heuristic = lambda pos: dist(pos, goal_pos)
            to_remove = []
            for node in nodes_list:
                if node in (root_start, root_goal):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except (ValueError, AttributeError):
                        pass
                try:
                    nodes_list.remove(rm_node)
                except ValueError:
                    pass
            return len(to_remove)

        # Initialization
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)

        tree_start = [root_start]
        tree_goal = [root_goal]
        nodes = [root_start, root_goal]
        edges: List[Tuple[Node, Node]] = []

        c_min = dist(start_pos, goal_pos)
        c_best = float('inf')
        best_path: List[Tuple[float, ...]] = []
        success_state = False

        for iteration in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate growth of smaller tree
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius = neighbor_radius(len(nodes), iteration)
            near_nodes = kdtree_a.radius_search(new_pos, radius)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes:
                cand_cost = node.cost + dist(node.position, new_pos)
                if cand_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cand_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.add_child(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors to new_node if better cost
            for node in near_nodes:
                if node is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, node.position)
                if cost_via_new + 1e-12 < node.cost and can_connect(new_node.position, node.position):
                    try:
                        edges.remove((node.parent, node))
                    except ValueError:
                        pass
                    node.update_parent(new_node, cost_via_new)
                    edges.append((new_node, node))

            # Incremental connection attempt from opposite tree
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.add_child(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                radius_b = neighbor_radius(len(nodes), iteration)
                near_nodes_b = kdtree_b.radius_search(next_pos_b, radius_b)

                for nbr_b in near_nodes_b:
                    if nbr_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, nbr_b.position)
                    if cost_via_new_b + 1e-12 < nbr_b.cost and can_connect(new_node_b.position, nbr_b.position):
                        try:
                            edges.remove((nbr_b.parent, nbr_b))
                        except ValueError:
                            pass
                        nbr_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, nbr_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    cost_connect = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connect_node = Node(new_node.position, parent=new_node_b, cost=cost_connect)
                    new_node_b.add_child(connect_node)
                    tree_b.append(connect_node)
                    nodes.append(connect_node)
                    edges.append((new_node_b, connect_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connect_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connect_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    smoothed = shortcut_path(merged_path)
                    if cost_connect + 1e-12 < c_best:
                        c_best = cost_connect
                        best_path = smoothed
                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                best_path = shortcut_path(best_path)
                break

            # Periodic pruning
            if iteration > 0 and iteration % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_nodes(tree_start, c_best, root_start, root_goal)
                prune_nodes(tree_goal, c_best, root_start, root_goal)
                edge_collision_cache.clear()

        if success_state and not best_path:
            best_path = []

        return PlannerResult(
            success=success_state,
            path=best_path,
            nodes=nodes,
            edges=edges
        )

class Node:
    def __init__(self, position: Tuple[float, ...], parent: 'Node' = None, cost: float = 0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child: 'Node'):
        self.children.append(child)
        child.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except (ValueError, AttributeError):
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0, max_neighbor_radius: float = 30.0,
                 prune_interval: int = 200, prune_threshold: int = 300,
                 smoothing_trials: int = 100, collision_check_resolution: float = 1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_trials = smoothing_trials
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> PlannerResult:
        import random
        import math
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))
            # Clamp to bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(n_nodes, iteration):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(n_nodes + 1) / n_nodes)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        # KDTree: brute-force search for simplicity and generality
        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [pn[0] for pn in points_nodes]
                self.nodes = [pn[1] for pn in points_nodes]

            def nearest(self, pt):
                best_node = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best_node = self.nodes[i]
                return best_node

            def radius_search(self, pt, radius):
                radius_sq = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    dist_sq = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        dist_sq += delta * delta
                        if dist_sq > radius_sq:
                            break
                    if dist_sq <= radius_sq:
                        result.append(self.nodes[i])
                return result

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2) if p1 < p2 else (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            return in_bounds(pos) and not is_in_obstacle(pos)

        def informed_sample(c_best, c_min, start, goal):
            # Early random sampling if no good path found
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < 0.1:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])
            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u /= norm_u
                rot = id_mat - 2 * np.outer(u, u)  # Householder reflection
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r2 = math.sqrt(max(r1 * r1 - (c_min / 2) ** 2, 0))
            radii = np.array([r1] + [r2] * (dim - 1))

            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x < 1e-10 or norm_x > 1:
                    continue
                x /= norm_x
                x *= np.power(np.random.uniform(0, 1), 1 / dim)  # uniform inside unit ball
                p = rot @ (x * radii) + center
                p_tuple = tuple(float(coord) for coord in p)
                if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                    return p_tuple
            # fallback random sampling
            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        def adaptive_sample(best_path, c_best, c_min):
            if best_path and random.random() < 0.75:
                for _ in range(50):
                    base_point = best_path[random.randint(0, len(best_path) - 1)]
                    noise = np.random.normal(0, self.step_size * 0.5, dim)
                    sample_point = tuple(base_point[d] + noise[d] for d in range(dim))
                    if in_bounds(sample_point) and not is_in_obstacle(sample_point):
                        return sample_point
            return informed_sample(c_best, c_min, start_pos, goal_pos)

        def shortcut_path(path):
            if len(path) < 3:
                return path[:]
            smooth = path[:]
            for _ in range(self.smoothing_trials):
                if len(smooth) < 3:
                    break
                i = random.randint(0, len(smooth) - 3)
                j = random.randint(i + 2, len(smooth) - 1)
                p1, p2 = smooth[i], smooth[j]
                if can_connect(p1, p2):
                    smooth = smooth[:i + 1] + smooth[j:]
            return smooth

        def prune_nodes(tree_nodes, best_cost, root_s, root_g):
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for node in tree_nodes:
                if node in (root_s, root_g):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except (ValueError, AttributeError):
                        pass
                try:
                    tree_nodes.remove(node)
                except ValueError:
                    pass
            return len(to_remove)

        # Initialize trees
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        tree_start = [root_start]
        tree_goal = [root_goal]
        nodes = [root_start, root_goal]
        edges = []

        c_min = dist(start_pos, goal_pos)
        c_best = float('inf')
        best_path = []
        success_state = False

        for it in range(self.max_iter):
            q_rand = adaptive_sample(best_path, c_best, c_min)

            # Extend smaller tree
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius_a = neighbor_radius(len(nodes), it)
            near_nodes_a = kdtree_a.radius_search(new_pos, radius_a)

            # Select best parent minimizing cost
            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes_a:
                cost_cand = node.cost + dist(node.position, new_pos)
                if cost_cand + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_cand
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for node in near_nodes_a:
                if node is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, node.position)
                if new_cost + 1e-12 < node.cost and can_connect(new_node.position, node.position):
                    try:
                        edges.remove((node.parent, node))
                    except ValueError:
                        pass
                    node.update_parent(new_node, new_cost)
                    edges.append((new_node, node))

            # Extend other tree towards new_node incrementally
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            max_steps = int(dist(nearest_b.position, new_node.position) / self.step_size) + 1
            last_node_b = nearest_b
            connected = False

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break
                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                radius_b = neighbor_radius(len(nodes), it)
                near_nodes_b = kdtree_b.radius_search(next_pos_b, radius_b)
                for nbr_b in near_nodes_b:
                    if nbr_b is last_node_b:
                        continue
                    new_cost_b = new_node_b.cost + dist(new_node_b.position, nbr_b.position)
                    if new_cost_b + 1e-12 < nbr_b.cost and can_connect(new_node_b.position, nbr_b.position):
                        try:
                            edges.remove((nbr_b.parent, nbr_b))
                        except ValueError:
                            pass
                        nbr_b.update_parent(new_node_b, new_cost_b)
                        edges.append((new_node_b, nbr_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    cost_connect = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connect_node = Node(new_node.position, parent=new_node_b, cost=cost_connect)
                    new_node_b.children.append(connect_node)
                    tree_b.append(connect_node)
                    nodes.append(connect_node)
                    edges.append((new_node_b, connect_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connect_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connect_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]
                    if cost_connect + 1e-12 < c_best:
                        c_best = cost_connect
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                break

            # Periodic pruning
            if (it > 0 and it % self.prune_interval == 0 and
                len(nodes) > self.prune_threshold and c_best < float('inf')):
                prune_nodes(tree_start, c_best, root_start, root_goal)
                prune_nodes(tree_goal, c_best, root_start, root_goal)
                edge_collision_cache.clear()

        extracted_path = best_path if success_state else []

        return PlannerResult(success_state, extracted_path, nodes, edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        if is_3d:
            pz = pos[2]
            for obs in obstacles:
                x, y, z, w, h, d = obs
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
        else:
            for obs in obstacles:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child_node: 'Node'):
        if child_node in self.children:
            self.children.remove(child_node)

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self,
                 max_iter: int = 5000,
                 step_size: float = 5.0,
                 goal_sample_rate: float = 0.1,
                 min_neighbor_radius: float = 4.0,
                 max_neighbor_radius: float = 25.0,
                 prune_interval: int = 150,
                 prune_threshold: int = 150,
                 smoothing_iterations: int = 100,
                 collision_check_resolution: float = 1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        import numpy as np

        bounds = map.size                       # 2D or 3D bounds
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)

        success_state = False
        extracted_path = []
        nodes = []
        edges = []

        # Collision caches
        node_collision_cache = {}
        edge_collision_cache = {}

        def in_bounds(p):
            return all(0.0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def _is_in_obstacle_cached(pos):
            if pos in node_collision_cache:
                return node_collision_cache[pos]
            res = self._is_in_obstacle(pos, obstacles, is_3d)
            node_collision_cache[pos] = res
            return res

        def _is_edge_in_obstacle_cached(p1, p2):
            key = (p1, p2)
            key_rev = (p2, p1)
            if key in edge_collision_cache:
                return edge_collision_cache[key]
            if key_rev in edge_collision_cache:
                return edge_collision_cache[key_rev]
            res = self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)
            edge_collision_cache[key] = res
            return res

        def can_add_node(pos):
            return in_bounds(pos) and not _is_in_obstacle_cached(pos)

        def can_connect(p1, p2):
            if _is_in_obstacle_cached(p2):
                return False
            if _is_edge_in_obstacle_cached(p1, p2):
                return False
            return True

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d_] + ratio * (to_p[d_] - from_p[d_]) for d_ in range(dim))
            # Clamp to bounds
            new_p_clamped = tuple(max(0.0, min(bounds[d_], new_p[d_])) for d_ in range(dim))
            return new_p_clamped

        # Adaptive informed ellipsoid sampling around best cost
        def within_ellipsoid_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001:
                # fallback uniform sampling with retries for collision-free samples
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not _is_in_obstacle_cached(p):
                        return p
                # final fallback
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) * 0.5 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            eye = np.eye(dim)
            v = a1 - np.array([1] + [0] * (dim - 1))
            norm_v = np.linalg.norm(v)
            if norm_v > 1e-10:
                v /= norm_v
                rotation = eye - 2 * np.outer(v, v)
            else:
                rotation = eye

            r1 = c_best / 2.0
            r_others = math.sqrt(max(r1 * r1 - (c_min / 2) ** 2, 0))
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(150):
                x_ball = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x_ball) <= 1.0:
                    sample = rotation @ (x_ball * radii) + center
                    p = tuple(float(coord) for coord in sample)
                    if in_bounds(p) and not _is_in_obstacle_cached(p):
                        return p

            # fallback uniform again
            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not _is_in_obstacle_cached(p):
                    return p
            return p

        # KDTree-like linear searches
        class KDTree:
            def __init__(self, points_nodes):
                self.points = [pn[0] for pn in points_nodes]
                self.nodes = [pn[1] for pn in points_nodes]

            def nearest(self, pt):
                best_node = None
                best_dist = float('inf')
                for p, n in zip(self.points, self.nodes):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best_node = n
                return best_node

            def radius_search(self, pt, radius):
                r2 = radius * radius
                results = []
                for p, n in zip(self.points, self.nodes):
                    sqd = 0.0
                    for d_ in range(dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        results.append(n)
                return results

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def shortcut_path(path):
            if len(path) < 3:
                return path
            p = path[:]
            for _ in range(self.smoothing_iterations):
                if len(p) < 3:
                    break
                i = random.randint(0, len(p) - 3)
                j = random.randint(i + 2, len(p) - 1)
                if can_connect(p[i], p[j]):
                    p = p[:i + 1] + p[j:]
            return p

        def prune_nodes(best_cost):
            removed = 0
            to_remove = []
            for node in nodes:
                # Never prune roots
                if node is root_start or node is root_goal:
                    continue
                # Prune dead-end nodes without children
                if not node.children:
                    to_remove.append(node)
                    continue
                # Prune nodes with cost + heuristic no better than best_cost
                heuristic = dist(node.position, goal_pos)
                if node.cost + heuristic - 1e-12 >= best_cost:
                    to_remove.append(node)
            for rmn in to_remove:
                if rmn.parent:
                    rmn.parent.remove_child(rmn)
                try:
                    nodes.remove(rmn)
                except ValueError:
                    pass
                if rmn in tree_start:
                    tree_start.remove(rmn)
                if rmn in tree_goal:
                    tree_goal.remove(rmn)
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        tree_start = [root_start]
        tree_goal = [root_goal]
        nodes.extend([root_start, root_goal])

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        for iteration in range(self.max_iter):
            # Sample point with goal bias or informed ellipsoid sampling
            if random.random() < self.goal_sample_rate:
                q_rand = goal_pos
            else:
                q_rand = within_ellipsoid_sample(c_best, c_min, start_pos, goal_pos)

            # Always expand the smaller tree to balance exploration
            if len(tree_start) > len(tree_goal):
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start
            else:
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal

            # Build KDTree and find nearest node in tree_a
            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos):
                continue
            if not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(iteration, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            # Pick best parent to minimize cost
            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes:
                tentative_cost = node.cost + dist(node.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = node

            new_node = Node(new_pos, parent=None, cost=min_cost)
            best_parent.add_child(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors for cost improvement
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                        except ValueError:
                            pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            # Try to connect other tree to new_node
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b

            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1
            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)

                if not can_add_node(next_pos_b):
                    break
                if not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=None, cost=cost_b)
                last_node_b.add_child(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                r_b = neighbor_radius(iteration, len(nodes))
                near_b = kdtree_b.radius_search(next_pos_b, r_b)

                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        if neighbor_b.parent:
                            try:
                                edges.remove((neighbor_b.parent, neighbor_b))
                            except ValueError:
                                pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=None, cost=final_cost)
                    new_node_b.add_child(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    # Merge paths
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    # Smooth path
                    smoothed = shortcut_path(merged_path)

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = smoothed

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Periodic pruning to reduce tree size and improve efficiency
            if iteration > 0 and iteration % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_nodes(c_best)
                edge_collision_cache.clear()
                node_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        pz = pos[2] if is_3d else None
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = 5.0
        self.max_neighbor_radius = 30.0
        self.prune_interval = 200
        self.prune_threshold = 300
        self.smoothing_iterations = 150
        self.collision_check_resolution = 1.0
        self.goal_sample_rate = 0.15

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))
            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            u = a1_np - np.array([1 if d == 0 else 0 for d in range(dim)])
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            val = max(r1 * r1 - (c_min / 2) * (c_min / 2), 0)
            r_others = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x <= 1 and norm_x > 1e-15:
                    radius_factor = np.random.uniform(0, 1) ** (1 / dim)
                    x_scaled = (x / norm_x) * radius_factor
                    x_scaled = x_scaled * radii
                    p = rot @ x_scaled + np.array(center)
                    p_tuple = tuple(float(coord) for coord in p)
                    if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                        return p_tuple
            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            if (p1, p2) in edge_collision_cache:
                return not edge_collision_cache[(p1, p2)]
            if (p2, p1) in edge_collision_cache:
                return not edge_collision_cache[(p2, p1)]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[(p1, p2)] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[(p1, p2)] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)
            return len(to_remove)

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        tree_start = [root_start]
        tree_goal = [root_goal]
        edges = []

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []
        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes:
                candidate_cost = node.cost + dist(node.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]: 2D or 3D coords
        self.parent = parent            # Node or None
        self.cost = cost                # Cost from root
        self.children = []
        self.valid = True               # Validity flag

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size

        # Parameters for neighbor radius calculation
        self.min_neighbor_radius = 5.0
        self.max_neighbor_radius = 30.0

        # Pruning parameters
        self.prune_interval = 150
        self.prune_threshold = 250

        self.smoothing_iterations = 150
        self.collision_check_resolution = 1.0
        self.goal_sample_rate = 0.15

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        import numpy as np

        bounds = map.size                  # Tuple[int,...]
        start_pos = map.start              # Tuple[float,...]
        goal_pos = map.goal                # Tuple[float,...]
        obstacles = map.obstacles          # List obstacles (boxes)

        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                # Construct a simple kd-tree for nearest neighbor and radius search with caching
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                # Linear search sufficient due to limited node count per iteration and for caching simplicity
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    squared_dist = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        squared_dist += delta * delta
                        if squared_dist > r2:
                            break
                    if squared_dist <= r2:
                        result.append(self.nodes[i])
                return result

        # Informed sampling within adaptive ellipsoid centered at midpoint of start and goal
        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                # Uniform random sampling with retry to avoid obstacles
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                # Fallback naive uniform sample
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))
            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            u = a1_np - np.array([1 if i == 0 else 0 for i in range(dim)])
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u /= norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            val = max(r1 * r1 - (c_min / 2) ** 2, 0)
            r_others = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x <= 1 and norm_x > 1e-15:
                    radius_factor = np.random.uniform(0, 1) ** (1 / dim)
                    x_scaled = (x / norm_x) * radius_factor
                    sample = rot @ (x_scaled * radii) + np.array(center)
                    p = tuple(float(coord) for coord in sample)
                    if in_bounds(p) and not is_in_obstacle(p):
                        return p

            # Fallback uniform sample if ellipsoid sampling fails
            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            key_rev = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if key_rev in edge_collision_cache:
                return not edge_collision_cache[key_rev]

            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            coll = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = coll
            return not coll

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            p = path[:]
            for _ in range(self.smoothing_iterations):
                if len(p) < 3:
                    break
                i = random.randint(0, len(p) - 3)
                j = random.randint(i + 2, len(p) - 1)
                if can_connect(p[i], p[j]):
                    p = p[:i + 1] + p[j:]
            return p

        def prune_useless_nodes(best_cost):
            removed = 0
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                # Heuristic-based pruning: prune if cost + heuristic >= best found cost
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)
                removed += 1
            return removed

        # Initialization
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)

        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        # Precompute heuristic distances to goal for pruning efficiency
        for iteration in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate growth of trees biased to smaller tree for balanced expansion
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(iteration, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            # Choose best parent for new node among neighbors
            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes:
                cost_candidate = node.cost + dist(node.position, new_pos)
                if cost_candidate + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_candidate
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors to new_node if cheaper and collision free
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            # Try to connect the opposite tree incrementally
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                # Rewire neighbors in tree_b
                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    # Merge path from both trees
                    success_state = True
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    # Smooth path with multiple shortcut calls
                    smoothed_path = shortcut_path(merged_path)

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = smoothed_path

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Prune nodes periodically to improve efficiency
            if iteration > 0 and iteration % self.prune_interval == 0 \
               and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        if is_3d:
            pz = pos[2]
            for obs in obstacles:
                x, y, z, w, h, d = obs
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
        else:
            for obs in obstacles:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For pruning, collision flag etc.

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except (ValueError, AttributeError):
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float = 5.0, max_neighbor_radius: float = 30.0,
                 prune_interval: int = 200, prune_threshold: int = 300,
                 smoothing_trials: int = 120,
                 collision_check_resolution: float = 1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_trials = smoothing_trials
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))
            # Clamp within bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(n_nodes, iteration):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(n_nodes + 1) / n_nodes)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [pn[0] for pn in points_nodes]
                self.nodes = [pn[1] for pn in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                res = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        res.append(self.nodes[i])
                return res

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2) if p1 < p2 else (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            return in_bounds(pos) and not is_in_obstacle(pos)

        c_min = dist(start_pos, goal_pos)

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < 0.1:
                for _ in range(30):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u /= norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            val = max(r1 * r1 - (c_min / 2) ** 2, 0)
            r_others = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x < 1e-10 or norm_x > 1:
                    continue
                x = x / norm_x
                x = x * np.random.uniform(0, 1) ** (1 / dim)
                p = rot @ (x * radii) + center
                p_tuple = tuple(float(coord) for coord in p)
                if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                    return p_tuple
            for _ in range(30):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        def adaptive_sample(best_path, c_best, c_min):
            if best_path and random.random() < 0.7:
                for _ in range(50):
                    base_point = best_path[random.randint(0, len(best_path) - 1)]
                    noise = np.random.normal(0, self.step_size * 0.4, dim)
                    sample_point = tuple(base_point[d] + noise[d] for d in range(dim))
                    if in_bounds(sample_point) and not is_in_obstacle(sample_point):
                        return sample_point
            return informed_sample(c_best, c_min, start_pos, goal_pos)

        def shortcut_path(path):
            if len(path) < 3:
                return path[:]
            smooth = path[:]
            for _ in range(self.smoothing_trials):
                if len(smooth) < 3:
                    break
                i = random.randint(0, len(smooth) - 3)
                j = random.randint(i + 2, len(smooth) - 1)
                p1, p2 = smooth[i], smooth[j]
                if can_connect(p1, p2):
                    smooth = smooth[:i + 1] + smooth[j:]
            return smooth

        def prune_nodes(nodes_list, best_cost, root_s, root_g):
            removed = 0
            h = lambda pos: dist(pos, goal_pos)
            to_remove = []
            for node in nodes_list:
                if node in (root_s, root_g):
                    continue
                if node.cost + h(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except (ValueError, AttributeError):
                        pass
                try:
                    nodes_list.remove(node)
                except ValueError:
                    pass
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        tree_start = [root_start]
        tree_goal = [root_goal]
        nodes = [root_start, root_goal]
        edges = []

        c_best = float('inf')
        best_path = []
        success_state = False

        for it in range(self.max_iter):
            q_rand = adaptive_sample(best_path, c_best, c_min)

            # Extend smaller tree first
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)
            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius_a = neighbor_radius(len(nodes), it)
            near_nodes_a = kdtree_a.radius_search(new_pos, radius_a)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes_a:
                cand_cost = node.cost + dist(node.position, new_pos)
                if cand_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cand_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for node in near_nodes_a:
                if node is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, node.position)
                if new_cost + 1e-12 < node.cost and can_connect(new_node.position, node.position):
                    try:
                        edges.remove((node.parent, node))
                    except ValueError:
                        pass
                    node.update_parent(new_node, new_cost)
                    edges.append((new_node, node))

            # Multi-step incremental connect from tree_b towards new_node.position
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            max_steps = int(dist(nearest_b.position, new_node.position) / self.step_size) + 1
            last_node_b = nearest_b
            connected = False

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break
                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                radius_b = neighbor_radius(len(nodes), it)
                near_nodes_b = kdtree_b.radius_search(next_pos_b, radius_b)

                for nbr_b in near_nodes_b:
                    if nbr_b is last_node_b:
                        continue
                    new_cost_b = new_node_b.cost + dist(new_node_b.position, nbr_b.position)
                    if new_cost_b + 1e-12 < nbr_b.cost and can_connect(new_node_b.position, nbr_b.position):
                        try:
                            edges.remove((nbr_b.parent, nbr_b))
                        except ValueError:
                            pass
                        nbr_b.update_parent(new_node_b, new_cost_b)
                        edges.append((new_node_b, nbr_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    cost_connect = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=cost_connect)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]
                    if cost_connect + 1e-12 < c_best:
                        c_best = cost_connect
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                # Aggressive iterative shortcut smoothing after each solution
                best_path = shortcut_path(best_path)
                # (Optionally can continue iterations for anytime improvement)
                break

            # Periodic pruning for efficiency, when solution exists and node count high
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                removed_s = prune_nodes(tree_start, c_best, root_start, root_goal)
                removed_g = prune_nodes(tree_goal, c_best, root_start, root_goal)
                edge_collision_cache.clear()

        return PlannerResult(
            success=success_state,
            path=best_path if success_state else [],
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position          # Tuple[float, ...] → 2D or 3D coordinate
        self.parent = parent              # Parent Node or None
        self.cost = cost                  # Cost from root
        self.children = []                # Children nodes (for rewiring/pruning)
        self.valid = True                 # Validity flag for collision pruning etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except (ValueError, AttributeError):
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=5.0, max_neighbor_radius: float=30.0,
                 prune_interval: int=200, prune_threshold: int=300,
                 smoothing_trials: int=150, collision_check_resolution: float=1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_trials = smoothing_trials
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> 'PlannerResult':
        import math, random, numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d_] + ratio * (to_p[d_] - from_p[d_]) for d_ in range(dim))
            return tuple(max(0, min(new_p[d_], bounds[d_])) for d_ in range(dim))

        def neighbor_radius(n_nodes, iteration):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.92)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(n_nodes + 1) / n_nodes)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p,n in points_nodes]
                self.nodes = [n for p,n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                res = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        res.append(self.nodes[i])
                return res

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2) if p1 < p2 else (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            return in_bounds(pos) and not is_in_obstacle(pos)

        def informed_sample(c_best, c_min, start, goal):
            # Adapted ellipsoidal sampling biased by current best cost with fallback uniform sampling
            if c_best == float('inf') or c_best < c_min * 1.00001:
                for _ in range(25):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                # fallback uniform
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])
            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u /= norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r2 = math.sqrt(max(r1 * r1 - (c_min / 2) ** 2, 0))
            radii = np.array([r1] + [r2] * (dim - 1))

            for _ in range(250):
                sample = np.random.uniform(-1,1,dim)
                norm_s = np.linalg.norm(sample)
                if norm_s < 1e-12 or norm_s > 1:
                    continue
                sample /= norm_s
                sample *= np.power(random.uniform(0,1), 1/dim)
                p = tuple(float(coord) for coord in rot @ (sample * radii) + center)
                if in_bounds(p) and not is_in_obstacle(p):
                    return p

            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p

            return p

        def adaptive_sample(best_path, c_best, c_min):
            # Sample near best path with higher probability to focus exploration
            if best_path and random.random() < 0.8:
                for _ in range(50):
                    base = best_path[random.randint(0, len(best_path) - 1)]
                    noise = np.random.normal(0, self.step_size*0.4, dim)
                    candidate = tuple(base[d] + noise[d] for d in range(dim))
                    if in_bounds(candidate) and not is_in_obstacle(candidate):
                        return candidate
            return informed_sample(c_best, c_min, start_pos, goal_pos)

        def shortcut_path(path):
            if len(path) < 3:
                return path[:]
            smooth_path = path[:]
            for _ in range(self.smoothing_trials):
                if len(smooth_path) < 3:
                    break
                i = random.randint(0, len(smooth_path) - 3)
                j = random.randint(i + 2, len(smooth_path) - 1)
                p1, p2 = smooth_path[i], smooth_path[j]
                if can_connect(p1, p2):
                    smooth_path = smooth_path[:i+1] + smooth_path[j:]
            return smooth_path

        def prune_nodes(t_nodes, best_cost, root_s, root_g):
            removed = 0
            heur = lambda pos: dist(pos, goal_pos)
            to_remove = []
            for node in t_nodes:
                if node in (root_s, root_g):
                    continue
                if node.cost + heur(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except (ValueError, AttributeError):
                        pass
                try:
                    t_nodes.remove(node)
                except ValueError:
                    pass
                removed += 1
            return removed

        # Initialization
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        tree_start = [root_start]
        tree_goal = [root_goal]
        nodes = [root_start, root_goal]
        edges = []

        c_min = dist(start_pos, goal_pos)
        c_best = float('inf')
        best_path = []
        success_state = False

        for iteration in range(self.max_iter):
            q_rand = adaptive_sample(best_path, c_best, c_min)

            # Grow smaller tree for balanced exploration
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius_a = neighbor_radius(len(nodes), iteration)
            near_nodes_a = kdtree_a.radius_search(new_pos, radius_a)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for n in near_nodes_a:
                candidate_cost = n.cost + dist(n.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(n.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = n

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors for improved cost if possible
            for n in near_nodes_a:
                if n is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, n.position)
                if cost_via_new + 1e-12 < n.cost and can_connect(new_node.position, n.position):
                    try:
                        edges.remove((n.parent, n))
                    except ValueError:
                        pass
                    n.update_parent(new_node, cost_via_new)
                    edges.append((new_node, n))

            # Incremental multi-step connection attempt from opposite tree
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            max_steps = int(dist(nearest_b.position, new_node.position) / self.step_size) + 1
            last_node_b = nearest_b
            connected = False

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                radius_b = neighbor_radius(len(nodes), iteration)
                near_nodes_b = kdtree_b.radius_search(next_pos_b, radius_b)

                # Rewire neighbors in tree_b
                for nbr in near_nodes_b:
                    if nbr is last_node_b:
                        continue
                    cost_v_nb = new_node_b.cost + dist(new_node_b.position, nbr.position)
                    if cost_v_nb + 1e-12 < nbr.cost and can_connect(new_node_b.position, nbr.position):
                        try:
                            edges.remove((nbr.parent, nbr))
                        except ValueError:
                            pass
                        nbr.update_parent(new_node_b, cost_v_nb)
                        edges.append((new_node_b, nbr))

                # Check connection possibility to new_node
                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    total_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connect_node = Node(new_node.position, parent=new_node_b, cost=total_cost)
                    new_node_b.children.append(connect_node)
                    tree_b.append(connect_node)
                    nodes.append(connect_node)
                    edges.append((new_node_b, connect_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connect_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connect_node.path_from_root()

                    merged = path_start + path_goal[::-1][1:]
                    if total_cost + 1e-12 < c_best:
                        c_best = total_cost
                        best_path = shortcut_path(merged)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                break

            # Periodic pruning of nodes unlikely to improve solution
            if (iteration > 0 and iteration % self.prune_interval == 0 and
                len(nodes) > self.prune_threshold and c_best < float('inf')):
                prune_nodes(tree_start, c_best, root_start, root_goal)
                prune_nodes(tree_goal, c_best, root_start, root_goal)
                edge_collision_cache.clear()

        extracted_path = best_path if success_state else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        if is_3d:
            py, pz = pos[1], pos[2]
            for obs in obstacles:
                x, y, z, w, h, d = obs
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            return False
        else:
            py = pos[1]
            for obs in obstacles:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
            return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For pruning or collision checks

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except (ValueError, AttributeError):
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0, max_neighbor_radius: float = 30.0,
                 prune_interval: int = 200, prune_threshold: int = 300,
                 smoothing_trials: int = 150, collision_check_resolution: float = 1.0,
                 goal_sample_rate: float = 0.1):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_trials = smoothing_trials
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))
            # Clamp to bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(n_nodes, iteration):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(n_nodes + 1) / n_nodes)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [pn[0] for pn in points_nodes]
                self.nodes = [pn[1] for pn in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                res = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        res.append(self.nodes[i])
                return res

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2) if p1 < p2 else (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            return in_bounds(pos) and not is_in_obstacle(pos)

        def informed_sample(c_best, c_min, start, goal):
            # With goal bias
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))
            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])
            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat
            r1 = c_best / 2.0
            val = max(r1 * r1 - (c_min / 2.0) ** 2, 0)
            r_others = math.sqrt(val)
            radii = np.array([r1] + [r_others] * (dim - 1))
            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x < 1e-10 or norm_x > 1:
                    continue
                x_norm = x / norm_x
                radius_sample = np.power(np.random.uniform(0, 1), 1 / dim)
                sample = rot @ (x_norm * radius_sample * radii) + center
                p_tuple = tuple(float(coord) for coord in sample)
                if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                    return p_tuple
            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        def adaptive_sample(best_path, c_best, c_min):
            # 75% near best path with gaussian noise, else informed ellipsoid sampling
            if best_path and random.random() < 0.75:
                for _ in range(50):
                    base = best_path[random.randint(0, len(best_path) - 1)]
                    noise = np.random.normal(0, self.step_size * 0.5, dim)
                    sample_pt = tuple(base[d] + noise[d] for d in range(dim))
                    if in_bounds(sample_pt) and not is_in_obstacle(sample_pt):
                        return sample_pt
            return informed_sample(c_best, c_min, start_pos, goal_pos)

        def shortcut_path(path):
            if len(path) < 3:
                return path[:]
            smooth_path = path[:]
            for _ in range(self.smoothing_trials):
                if len(smooth_path) < 3:
                    break
                i = random.randint(0, len(smooth_path) - 3)
                j = random.randint(i + 2, len(smooth_path) - 1)
                p1, p2 = smooth_path[i], smooth_path[j]
                if can_connect(p1, p2):
                    smooth_path = smooth_path[:i + 1] + smooth_path[j:]
            return smooth_path

        def prune_nodes(node_list, best_cost, root_s, root_g):
            removed_count = 0
            heuristic = lambda pos: dist(pos, goal_pos)
            to_remove = []
            for node in node_list:
                if node in (root_s, root_g):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-9:
                    to_remove.append(node)
            for node in to_remove:
                # Disconnect from parent
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except (ValueError, AttributeError):
                        pass
                try:
                    node_list.remove(node)
                except ValueError:
                    pass
                removed_count += 1
            return removed_count

        # Initialization
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        tree_start = [root_start]
        tree_goal = [root_goal]
        nodes = [root_start, root_goal]
        edges = []

        c_min = dist(start_pos, goal_pos)
        c_best = float('inf')
        best_path = []
        success_state = False

        for it in range(self.max_iter):
            q_rand = adaptive_sample(best_path, c_best, c_min)

            # Extend smaller tree first
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)
            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius_a = neighbor_radius(len(nodes), it)
            near_nodes_a = kdtree_a.radius_search(new_pos, radius_a)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes_a:
                cand_cost = node.cost + dist(node.position, new_pos)
                if cand_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cand_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for node in near_nodes_a:
                if node is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, node.position)
                if new_cost + 1e-12 < node.cost and can_connect(new_node.position, node.position):
                    try:
                        edges.remove((node.parent, node))
                    except ValueError:
                        pass
                    node.update_parent(new_node, new_cost)
                    edges.append((new_node, node))

            # Incremental multi-step connect from tree_b toward new_node.position
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            max_steps = int(dist(nearest_b.position, new_node.position) / self.step_size) + 1
            last_node_b = nearest_b
            connected = False

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break
                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                radius_b = neighbor_radius(len(nodes), it)
                near_nodes_b = kdtree_b.radius_search(next_pos_b, radius_b)

                for nbr_b in near_nodes_b:
                    if nbr_b is last_node_b:
                        continue
                    new_cost_b = new_node_b.cost + dist(new_node_b.position, nbr_b.position)
                    if new_cost_b + 1e-12 < nbr_b.cost and can_connect(new_node_b.position, nbr_b.position):
                        try:
                            edges.remove((nbr_b.parent, nbr_b))
                        except ValueError:
                            pass
                        nbr_b.update_parent(new_node_b, new_cost_b)
                        edges.append((new_node_b, nbr_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    cost_connect = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connect_node = Node(new_node.position, parent=new_node_b, cost=cost_connect)
                    new_node_b.children.append(connect_node)
                    tree_b.append(connect_node)
                    nodes.append(connect_node)
                    edges.append((new_node_b, connect_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connect_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connect_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]
                    if cost_connect + 1e-12 < c_best:
                        c_best = cost_connect
                        best_path = shortcut_path(merged_path)
                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                # Aggressive iterative smoothing
                best_path = shortcut_path(best_path)
                break

            # Periodic aggressive pruning
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_nodes(tree_start, c_best, root_start, root_goal)
                prune_nodes(tree_goal, c_best, root_start, root_goal)
                edge_collision_cache.clear()

        return PlannerResult(
            success=success_state,
            path=best_path if success_state else [],
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # Validity flag for collision checks

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=5.0, max_neighbor_radius: float=30.0,
                 prune_interval: int=200, prune_threshold: int=300,
                 smoothing_iterations: int=150, collision_check_resolution: float=1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> 'PlannerResult':
        import random
        import math
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d_] + ratio * (to_p[d_] - from_p[d_]) for d_ in range(dim))
            # Clamp to bounds
            return tuple(max(0.0, min(new_p[d_], bounds[d_])) for d_ in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.points = [pn[0] for pn in points_nodes]
                self.nodes = [pn[1] for pn in points_nodes]

            def nearest(self, pt):
                best_node = None
                best_dist = float('inf')
                for p, n in zip(self.points, self.nodes):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best_node = n
                return best_node

            def radius_search(self, pt, radius):
                r2 = radius * radius
                results = []
                for p, n in zip(self.points, self.nodes):
                    sqd = 0.0
                    for d_ in range(dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        results.append(n)
                return results

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001:
                # Uniform sampling with obstacle rejection fallback
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d_]) for d_ in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d_]) for d_ in range(dim))

            center = np.array([(start[d_] + goal[d_]) / 2.0 for d_ in range(dim)])
            a1 = np.array([(goal[d_] - start[d_]) / c_min for d_ in range(dim)])

            id_mat = np.eye(dim)
            v = a1 - np.array([1.] + [0.]*(dim - 1))
            v_norm = np.linalg.norm(v)
            if v_norm > 1e-10:
                v = v / v_norm
                rot = id_mat - 2 * np.outer(v, v)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r_others = math.sqrt(max(r1*r1 - (c_min/2)**2, 0))
            radii = np.array([r1] + [r_others]*(dim - 1))

            for _ in range(200):
                sample_ball = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(sample_ball) <= 1:
                    sample_scaled = sample_ball * radii
                    point = rot @ sample_scaled + center
                    p = tuple(float(c) for c in point)
                    if in_bounds(p) and not is_in_obstacle(p):
                        return p
            # fallback uniform
            for _ in range(15):
                p = tuple(random.uniform(0, bounds[d_]) for d_ in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            key_rev = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if key_rev in edge_collision_cache:
                return not edge_collision_cache[key_rev]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            p = path[:]
            for _ in range(self.smoothing_iterations):
                if len(p) < 3:
                    break
                i = random.randint(0, len(p) - 3)
                j = random.randint(i + 2, len(p) - 1)
                if can_connect(p[i], p[j]):
                    p = p[:i + 1] + p[j:]
            return p

        def prune_useless_nodes(best_cost):
            removed = 0
            to_remove = []
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                heuristic = dist(node.position, goal_pos)
                if node.cost + heuristic - 1e-12 >= best_cost:
                    to_remove.append(node)
            for rmn in to_remove:
                if rmn.parent:
                    try:
                        rmn.parent.children.remove(rmn)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rmn)
                except ValueError:
                    pass
                if rmn in tree_start:
                    tree_start.remove(rmn)
                if rmn in tree_goal:
                    tree_goal.remove(rmn)
                removed += 1
            return removed

        # Initialize roots and trees
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)

        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)

        best_path = []
        success_state = False
        extracted_path = []

        for iteration in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate expanding smaller tree for balance
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos):
                continue
            if not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(iteration, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes:
                candidate_cost = node.cost + dist(node.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors to improve cost
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            # Connect new_node with opposite tree
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False

            dist_ab = dist(last_node_b.position, new_node.position)
            max_steps = int(dist_ab / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]
                    smoothed_path = shortcut_path(merged_path)

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = smoothed_path

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Prune unpromising nodes every prune_interval iterations
            if iteration > 0 and iteration % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []              # For rewiring and pruning
        self.valid = True               # Node validity flag

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except (ValueError, AttributeError):
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=5.0, max_neighbor_radius: float=30.0,
                 prune_interval: int = 200, prune_threshold: int = 300,
                 smoothing_trials: int = 100, collision_check_resolution: float = 1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_trials = smoothing_trials
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> 'PlannerResult':
        import math, random, numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(n_nodes, iteration):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(n_nodes + 1) / n_nodes)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p,n in points_nodes]
                self.nodes = [n for p,n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2) if p1 < p2 else (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            return in_bounds(pos) and not is_in_obstacle(pos)

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < 0.1:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                # fallback
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])
            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u /= norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r2 = math.sqrt(max(r1 * r1 - (c_min / 2) ** 2, 0))
            radii = np.array([r1] + [r2] * (dim - 1))

            for _ in range(150):
                x = np.random.uniform(-1,1,dim)
                norm_x = np.linalg.norm(x)
                if norm_x < 1e-10 or norm_x > 1:
                    continue
                x /= norm_x
                x *= np.power(np.random.uniform(0,1), 1/dim)
                p = rot @ (x * radii) + center
                p_tuple = tuple(float(coord) for coord in p)
                if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                    return p_tuple
            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        def adaptive_sample(best_path, c_best, c_min):
            if best_path and random.random() < 0.75:
                for _ in range(50):
                    base_point = best_path[random.randint(0, len(best_path) - 1)]
                    noise = np.random.normal(0, self.step_size * 0.5, dim)
                    sample_point = tuple(base_point[d] + noise[d] for d in range(dim))
                    if in_bounds(sample_point) and not is_in_obstacle(sample_point):
                        return sample_point
            return informed_sample(c_best, c_min, start_pos, goal_pos)

        def shortcut_path(path):
            if len(path) < 3:
                return path[:]
            smooth = path[:]
            for _ in range(self.smoothing_trials):
                if len(smooth) < 3:
                    break
                i = random.randint(0, len(smooth) - 3)
                j = random.randint(i + 2, len(smooth) - 1)
                p1, p2 = smooth[i], smooth[j]
                if can_connect(p1, p2):
                    smooth = smooth[:i + 1] + smooth[j:]
            return smooth

        def prune_nodes(tree_nodes, best_cost, root_s, root_g):
            removed = 0
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for node in tree_nodes:
                if node in (root_s, root_g):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-9:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except (ValueError, AttributeError):
                        pass
                try:
                    tree_nodes.remove(node)
                except ValueError:
                    pass
                removed += 1
            return removed

        # Initialization
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        tree_start = [root_start]
        tree_goal = [root_goal]
        nodes = [root_start, root_goal]
        edges = []

        c_min = dist(start_pos, goal_pos)
        c_best = float('inf')
        best_path = []
        success_state = False

        for it in range(self.max_iter):
            q_rand = adaptive_sample(best_path, c_best, c_min)

            # Choose smaller tree to extend
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position,n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius_a = neighbor_radius(len(nodes), it)
            near_nodes_a = kdtree_a.radius_search(new_pos, radius_a)

            # Parent selection minimizing cost
            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes_a:
                cost_candidate = node.cost + dist(node.position, new_pos)
                if cost_candidate + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_candidate
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors to improve costs
            for node in near_nodes_a:
                if node is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, node.position)
                if new_cost + 1e-12 < node.cost and can_connect(new_node.position, node.position):
                    try:
                        edges.remove((node.parent, node))
                    except ValueError:
                        pass
                    node.update_parent(new_node, new_cost)
                    edges.append((new_node, node))

            # Attempt incremental multi-step connection from tree_b to new_node
            kdtree_b = KDTree([(n.position,n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            max_steps = int(dist(nearest_b.position, new_node.position) / self.step_size) + 1
            last_node_b = nearest_b
            connected = False

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break
                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                radius_b = neighbor_radius(len(nodes), it)
                near_nodes_b = kdtree_b.radius_search(next_pos_b, radius_b)

                # Rewire neighbors in tree_b
                for nbr_b in near_nodes_b:
                    if nbr_b is last_node_b:
                        continue
                    new_cost_b = new_node_b.cost + dist(new_node_b.position, nbr_b.position)
                    if new_cost_b + 1e-12 < nbr_b.cost and can_connect(new_node_b.position, nbr_b.position):
                        try:
                            edges.remove((nbr_b.parent, nbr_b))
                        except ValueError:
                            pass
                        nbr_b.update_parent(new_node_b, new_cost_b)
                        edges.append((new_node_b, nbr_b))

                # Check if connection to new_node is possible
                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    cost_connect = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connect_node = Node(new_node.position, parent=new_node_b, cost=cost_connect)
                    new_node_b.children.append(connect_node)
                    tree_b.append(connect_node)
                    nodes.append(connect_node)
                    edges.append((new_node_b, connect_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connect_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connect_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]
                    if cost_connect + 1e-12 < c_best:
                        c_best = cost_connect
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                break

            # Periodic pruning to remove nodes unlikely to improve solution
            if (it > 0 and it % self.prune_interval == 0 and
                len(nodes) > self.prune_threshold and c_best < float('inf')):
                removed_s = prune_nodes(tree_start, c_best, root_start, root_goal)
                removed_g = prune_nodes(tree_goal, c_best, root_start, root_goal)
                edge_collision_cache.clear()

        extracted_path = best_path if success_state else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        if is_3d:
            pz = pos[2]
            for obs in obstacles:
                x, y, z, w, h, d = obs
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            return False
        else:
            for obs in obstacles:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
            return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except (ValueError, AttributeError):
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 min_radius: float = 5.0, max_radius: float = 30.0,
                 prune_interval: int = 200, prune_threshold: int = 300,
                 smoothing_iterations: int = 100, collision_resolution: float = 1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_radius = min_radius
        self.max_radius = max_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_resolution = collision_resolution

    def plan(self, map) -> 'PlannerResult':
        import math, random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))
            # Clamp within map bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def is_in_obstacle(p):
            return self._is_in_obstacle(p, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_resolution)

        # Simple KD-tree for neighbor searching (brute-force)
        class KDTree:
            def __init__(self, points_nodes):
                self.points = [pn[0] for pn in points_nodes]
                self.nodes = [pn[1] for pn in points_nodes]

            def nearest(self, pt):
                best_dist = float('inf')
                best_node = None
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best_node = self.nodes[i]
                return best_node

            def radius_search(self, pt, radius):
                r2 = radius * radius
                res = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        res.append(self.nodes[i])
                return res

        def can_connect(p1, p2):
            # Cache key tuple sorted to avoid duplicates
            key = (p1, p2) if p1 < p2 else (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            return in_bounds(pos) and not is_in_obstacle(pos)

        # Informed ellipsoidal sampling inside evolving domain biased by current best cost c_best
        def informed_sample(c_best, c_min, start, goal) -> tuple:
            # If no path yet, sample random free points
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < 0.1:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                # fallback
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])
            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u /= norm_u
                rot = id_mat - 2 * np.outer(u, u)   # Householder reflection matrix
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r2 = math.sqrt(max(r1 * r1 - (c_min / 2) ** 2, 0))
            radii = np.array([r1] + [r2] * (dim - 1))

            # Try sampling points in the ellipsoid
            for _ in range(150):
                x = np.random.uniform(-1,1,dim)
                norm_x = np.linalg.norm(x)
                if norm_x < 1e-10 or norm_x > 1:
                    continue
                x = x / norm_x
                x = x * (np.random.uniform(0,1) ** (1/dim))  # Uniform in unit ball
                sample = rot @ (x * radii) + center
                sample_tuple = tuple(float(s) for s in sample)
                if in_bounds(sample_tuple) and not is_in_obstacle(sample_tuple):
                    return sample_tuple

            # Fallback random samples if unable to find in ellipsoid
            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        # Adaptive sampling: sample near best known path with Gaussian noise, fallback to informed_sample
        def adaptive_sample(best_path, c_best, c_min):
            if best_path and random.random() < 0.75:
                for _ in range(50):
                    base_point = best_path[random.randint(0, len(best_path) - 1)]
                    noise = np.random.normal(0, self.step_size * 0.5, dim)
                    sample = tuple(base_point[d] + noise[d] for d in range(dim))
                    if in_bounds(sample) and not is_in_obstacle(sample):
                        return sample
            return informed_sample(c_best, c_min, start_pos, goal_pos)

        def neighbor_radius(n_nodes, iteration):
            if n_nodes <= 1:
                return self.max_radius
            r_iter = self.max_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_radius + (self.max_radius - self.min_radius) * math.sqrt(math.log(max(n_nodes,1) + 1) / max(n_nodes,1))
            return max(self.min_radius, min(r_iter, r_nodes))

        def shortcut_path(path):
            if len(path) < 3:
                return path[:]
            smooth = path[:]
            for _ in range(self.smoothing_iterations):
                if len(smooth) < 3:
                    break
                i = random.randint(0, len(smooth) - 3)
                j = random.randint(i + 2, len(smooth) - 1)
                p1, p2 = smooth[i], smooth[j]
                if can_connect(p1, p2):
                    smooth = smooth[:i+1] + smooth[j:]
            return smooth

        def prune_nodes(node_list, best_cost, root_s, root_g):
            removed = 0
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for n in node_list:
                if n in (root_s, root_g):
                    continue
                if n.cost + heuristic(n.position) >= best_cost - 1e-9:
                    to_remove.append(n)
            for n in to_remove:
                if n.parent:
                    try:
                        n.parent.children.remove(n)
                    except (ValueError, AttributeError):
                        pass
                try:
                    node_list.remove(n)
                except ValueError:
                    pass
                removed += 1
            return removed

        # Initialize roots and data structures
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        tree_start = [root_start]
        tree_goal = [root_goal]
        nodes = [root_start, root_goal]
        edges = []

        c_min = dist(start_pos, goal_pos)
        c_best = float('inf')
        best_path = []
        success = False

        edge_collision_cache = {}

        for itr in range(self.max_iter):
            # Alternate which tree to extend (extend smaller)
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            q_rand = adaptive_sample(best_path, c_best, c_min)
            kdt_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdt_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius_a = neighbor_radius(len(nodes), itr)
            near_nodes_a = kdt_a.radius_search(new_pos, radius_a)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes_a:
                c_cand = node.cost + dist(node.position, new_pos)
                if c_cand + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = c_cand
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for node in near_nodes_a:
                if node is best_parent:
                    continue
                new_c = new_node.cost + dist(new_node.position, node.position)
                if new_c + 1e-12 < node.cost and can_connect(new_node.position, node.position):
                    try:
                        edges.remove((node.parent, node))
                    except ValueError:
                        pass
                    node.update_parent(new_node, new_c)
                    edges.append((new_node, node))

            # Incrementally connect tree_b to new_node
            kdt_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdt_b.nearest(new_node.position)
            max_steps = int(dist(nearest_b.position, new_node.position) / self.step_size) + 1
            last_node_b = nearest_b
            connected = False

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break
                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                radius_b = neighbor_radius(len(nodes), itr)
                near_nodes_b = kdt_b.radius_search(next_pos_b, radius_b)

                for nbr_b in near_nodes_b:
                    if nbr_b is last_node_b:
                        continue
                    new_cost_b = new_node_b.cost + dist(new_node_b.position, nbr_b.position)
                    if new_cost_b + 1e-12 < nbr_b.cost and can_connect(new_node_b.position, nbr_b.position):
                        try:
                            edges.remove((nbr_b.parent, nbr_b))
                        except ValueError:
                            pass
                        nbr_b.update_parent(new_node_b, new_cost_b)
                        edges.append((new_node_b, nbr_b))

                # Connect check to new_node's position
                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    cost_connect = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connect_node = Node(new_node.position, parent=new_node_b, cost=cost_connect)
                    new_node_b.children.append(connect_node)
                    tree_b.append(connect_node)
                    nodes.append(connect_node)
                    edges.append((new_node_b, connect_node))

                    success = True

                    if tree_a is tree_start:
                        path_start = connect_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connect_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]
                    if cost_connect + 1e-12 < c_best:
                        c_best = cost_connect
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                break

            # Prune nodes periodically for efficiency
            if (itr > 0 and itr % self.prune_interval == 0 and
                len(nodes) > self.prune_threshold and c_best < float('inf')):
                prune_nodes(tree_start, c_best, root_start, root_goal)
                prune_nodes(tree_goal, c_best, root_start, root_goal)
                edge_collision_cache.clear()

        extracted_path = best_path if success else []

        # Final smoothing for additional refinement
        if success and len(extracted_path) > 2:
            for _ in range(self.smoothing_iterations):
                if len(extracted_path) < 3:
                    break
                i = random.randint(0, len(extracted_path) - 3)
                j = random.randint(i + 2, len(extracted_path) - 1)
                p1, p2 = extracted_path[i], extracted_path[j]
                if can_connect(p1, p2):
                    extracted_path = extracted_path[:i+1] + extracted_path[j:]

        return PlannerResult(
            success=success,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = 5.0
        self.max_neighbor_radius = 30.0
        self.prune_interval = 200
        self.prune_threshold = 300
        self.smoothing_iterations = 150
        self.collision_check_resolution = 1.0
        self.goal_sample_rate = 0.15

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp to bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))
            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            u = a1_np - np.array([1 if d == 0 else 0 for d in range(dim)])
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            val = max(r1 * r1 - (c_min / 2) * (c_min / 2), 0)
            r_others = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x <= 1 and norm_x > 1e-15:
                    radius_factor = np.random.uniform(0, 1) ** (1 / dim)
                    x_scaled = (x / norm_x) * radius_factor
                    x_scaled = x_scaled * radii
                    p = rot @ x_scaled + np.array(center)
                    p_tuple = tuple(float(coord) for coord in p)
                    if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                        return p_tuple
            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            if (p1, p2) in edge_collision_cache:
                return not edge_collision_cache[(p1, p2)]
            if (p2, p1) in edge_collision_cache:
                return not edge_collision_cache[(p2, p1)]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[(p1, p2)] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[(p1, p2)] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            removed = 0
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)
                removed += 1
            return removed

        # Initialization
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes:
                candidate_cost = node.cost + dist(node.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors around new node
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            # Incremental connect opposite tree towards new_node
            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = parent            # Node or None
        self.cost = cost                # Cost from root
        self.children = []
        self.valid = True               # For pruning etc.

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except (ValueError, AttributeError):
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float = 5.0, max_neighbor_radius: float = 30.0,
                 prune_interval: int = 200, prune_threshold: int = 300,
                 smoothing_trials: int = 100, collision_check_resolution: float = 1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_trials = smoothing_trials
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))
            # Clamp position within bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(n_nodes, iteration):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(n_nodes + 1) / n_nodes)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        # KD-tree approximation (brute-force)
        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [pn[0] for pn in points_nodes]
                self.nodes = [pn[1] for pn in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                res = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        res.append(self.nodes[i])
                return res

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2) if p1 < p2 else (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            return in_bounds(pos) and not is_in_obstacle(pos)

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < 0.1:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))
            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])
            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat
            r1 = c_best / 2.0
            r2 = math.sqrt(max(r1 * r1 - (c_min / 2) ** 2, 0))
            radii = np.array([r1] + [r2] * (dim - 1))
            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x < 1e-10 or norm_x > 1:
                    continue
                x = x / norm_x
                x = x * np.power(np.random.uniform(0, 1), 1 / dim)
                p = rot @ (x * radii) + center
                p_tuple = tuple(float(coord) for coord in p)
                if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                    return p_tuple
            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        def adaptive_sample(best_path, c_best, c_min):
            if best_path and random.random() < 0.75:
                for _ in range(50):
                    base_point = best_path[random.randint(0, len(best_path) - 1)]
                    noise = np.random.normal(0, self.step_size * 0.5, dim)
                    sample_point = tuple(base_point[d] + noise[d] for d in range(dim))
                    if in_bounds(sample_point) and not is_in_obstacle(sample_point):
                        return sample_point
            return informed_sample(c_best, c_min, start_pos, goal_pos)

        def shortcut_path(path):
            if len(path) < 3:
                return path[:]
            smooth = path[:]
            for _ in range(self.smoothing_trials):
                if len(smooth) < 3:
                    break
                i = random.randint(0, len(smooth) - 3)
                j = random.randint(i + 2, len(smooth) - 1)
                p1, p2 = smooth[i], smooth[j]
                if can_connect(p1, p2):
                    smooth = smooth[:i + 1] + smooth[j:]
            return smooth

        def prune_nodes(nodes_list, best_cost, root_s, root_g):
            removed = 0
            to_remove = []
            h = lambda pos: dist(pos, goal_pos)
            for node in nodes_list:
                if node in (root_s, root_g):
                    continue
                if node.cost + h(node.position) >= best_cost - 1e-9:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except (ValueError, AttributeError):
                        pass
                try:
                    nodes_list.remove(node)
                except ValueError:
                    pass
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        tree_start = [root_start]
        tree_goal = [root_goal]
        nodes = [root_start, root_goal]
        edges = []

        c_min = dist(start_pos, goal_pos)
        c_best = float('inf')
        best_path = []
        success_state = False

        for it in range(self.max_iter):
            q_rand = adaptive_sample(best_path, c_best, c_min)

            # Extend smaller tree first
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)
            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius_a = neighbor_radius(len(nodes), it)
            near_nodes_a = kdtree_a.radius_search(new_pos, radius_a)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes_a:
                cand_cost = node.cost + dist(node.position, new_pos)
                if cand_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cand_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for node in near_nodes_a:
                if node is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, node.position)
                if new_cost + 1e-12 < node.cost and can_connect(new_node.position, node.position):
                    try:
                        edges.remove((node.parent, node))
                    except ValueError:
                        pass
                    node.update_parent(new_node, new_cost)
                    edges.append((new_node, node))

            # Incremental multi-step connect from tree_b towards new_node.position
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            max_steps = int(dist(nearest_b.position, new_node.position) / self.step_size) + 1
            last_node_b = nearest_b
            connected = False

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break
                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                radius_b = neighbor_radius(len(nodes), it)
                near_nodes_b = kdtree_b.radius_search(next_pos_b, radius_b)

                for nbr_b in near_nodes_b:
                    if nbr_b is last_node_b:
                        continue
                    new_cost_b = new_node_b.cost + dist(new_node_b.position, nbr_b.position)
                    if new_cost_b + 1e-12 < nbr_b.cost and can_connect(new_node_b.position, nbr_b.position):
                        try:
                            edges.remove((nbr_b.parent, nbr_b))
                        except ValueError:
                            pass
                        nbr_b.update_parent(new_node_b, new_cost_b)
                        edges.append((new_node_b, nbr_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    cost_connect = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connect_node = Node(new_node.position, parent=new_node_b, cost=cost_connect)
                    new_node_b.children.append(connect_node)
                    tree_b.append(connect_node)
                    nodes.append(connect_node)
                    edges.append((new_node_b, connect_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connect_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connect_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]
                    if cost_connect + 1e-12 < c_best:
                        c_best = cost_connect
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                # Aggressive iterative shortcut smoothing
                best_path = shortcut_path(best_path)
                break

            # Periodic pruning when good path exists and node count is high
            if (it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf')):
                removed_s = prune_nodes(tree_start, c_best, root_start, root_goal)
                removed_g = prune_nodes(tree_goal, c_best, root_start, root_goal)
                edge_collision_cache.clear()

        return PlannerResult(
            success=success_state,
            path=best_path if success_state else [],
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position      # Tuple[float, ...] position in 2D or 3D
        self.parent = parent          # Parent Node or None
        self.cost = cost              # Cost from root to this node
        self.children = []            # Children Nodes
        self.valid = True             # For collision validity if needed

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0, max_neighbor_radius: float = 30.0,
                 prune_interval: int = 200, prune_threshold: int = 300,
                 smoothing_iterations: int = 150, collision_check_resolution: float = 1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> 'PlannerResult':
        import math
        import random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = dim == 3

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        # KDTree with simple linear search (quicker for small data, can upgrade to balanced structure)
        class KDTree:
            def __init__(self, points_nodes):
                self.points = [pn[0] for pn in points_nodes]
                self.nodes = [pn[1] for pn in points_nodes]

            def nearest(self, pt):
                best_node = None
                best_dist = float('inf')
                for p, n in zip(self.points, self.nodes):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best_node = n
                return best_node

            def radius_search(self, pt, radius):
                r2 = radius * radius
                results = []
                for p, n in zip(self.points, self.nodes):
                    sqd = 0.0
                    for d in range(dim):
                        delta = p[d] - pt[d]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        results.append(n)
                return results

        # Informed sampling inside ellipsoid based on current best path cost
        def informed_sample(c_best, c_min, start, goal):
            import numpy as np

            if c_best == float('inf') or c_best < c_min * 1.00001:
                # fallback uniform sampling with retries for obstacle free
                for _ in range(15):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2.0 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])
            id_m = np.eye(dim)

            v = a1 - np.array([1] + [0] * (dim - 1))
            norm_v = np.linalg.norm(v)
            if norm_v > 1e-10:
                v /= norm_v
                rot = id_m - 2 * np.outer(v, v)
            else:
                rot = id_m

            r1 = c_best / 2.0
            r_others = math.sqrt(max(r1 * r1 - (c_min / 2) ** 2, 0))
            radii = np.array([r1] + [r_others] * (dim - 1))

            # Allow more tries but break early for efficiency
            for _ in range(200):
                x_ball = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x_ball) <= 1.0:
                    sample = rot @ (x_ball * radii) + center
                    p = tuple(float(coord) for coord in sample)
                    if in_bounds(p) and not is_in_obstacle(p):
                        return p

            # fallback uniform sampling if ellipsoid fails
            for _ in range(15):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            key_rev = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if key_rev in edge_collision_cache:
                return not edge_collision_cache[key_rev]

            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            p = path[:]
            for _ in range(self.smoothing_iterations):
                if len(p) < 3:
                    break
                i = random.randint(0, len(p) - 3)
                j = random.randint(i + 2, len(p) - 1)
                if can_connect(p[i], p[j]):
                    p = p[:i + 1] + p[j:]
            return p

        def prune_useless_nodes(best_cost):
            removed = 0
            to_remove = []
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                heuristic = dist(node.position, goal_pos)
                if node.cost + heuristic - 1e-12 >= best_cost:
                    to_remove.append(node)
            for rmn in to_remove:
                if rmn.parent:
                    try:
                        rmn.parent.children.remove(rmn)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rmn)
                except ValueError:
                    pass
                if rmn in tree_start:
                    tree_start.remove(rmn)
                if rmn in tree_goal:
                    tree_goal.remove(rmn)
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)

        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)

        best_path: list = []
        success_state = False
        extracted_path = []

        for iteration in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate growth direction depending on size for balance
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos):
                continue
            if not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(iteration, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a

            for node in near_nodes:
                cost_candidate = node.cost + dist(node.position, new_pos)
                if cost_candidate + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_candidate
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewiring neighbors around new_node for cost improvement
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                c_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if c_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, c_via_new)
                    edges.append((new_node, neighbor))

            # Try connecting trees via new_node
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False

            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b):
                    break
                if not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    c_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if c_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, c_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    # Mark success and update best path
                    success_state = True

                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    # Shortcut smoothing to improve path quality
                    smoothed = shortcut_path(merged_path)

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = smoothed

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Periodic pruning of nodes unlikely to yield improvement
            if iteration > 0 and iteration % self.prune_interval == 0 \
               and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from tree root
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0, max_neighbor_radius: float = 25.0,
                 goal_sample_rate: float = 0.1,
                 prune_interval: int = 200, prune_threshold: int = 300,
                 smoothing_iterations: int = 150, collision_check_resolution: float = 1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.goal_sample_rate = goal_sample_rate
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> PlannerResult:
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def within_ellipsoid_sample(c_best, c_min, start, goal):
            # Use informed sampling inside ellipsoid
            if c_best == float('inf') or c_best < c_min * 1.00001:
                # fallback uniform sampling with retries for collision-free sample
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                # fallback: random anyway
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2.0 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            eye = np.eye(dim)
            v = a1 - np.array([1] + [0] * (dim - 1))
            norm_v = np.linalg.norm(v)
            if norm_v > 1e-10:
                v /= norm_v
                rotation = eye - 2 * np.outer(v, v)
            else:
                rotation = eye

            r1 = c_best / 2.0
            r_others = math.sqrt(max(r1 * r1 - (c_min / 2) ** 2, 0))
            radii = np.array([r1] + [r_others] * (dim - 1))

            # Try multiple times for collision-free sample
            for _ in range(200):
                x_ball = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x_ball) <= 1.0:
                    sample = rotation @ (x_ball * radii) + center
                    p = tuple(float(coord) for coord in sample)
                    if in_bounds(p) and not is_in_obstacle(p):
                        return p

            # fallback again to uniform if ellipsoid fails
            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def is_in_obstacle(pos):
            # check if pos inside any obstacle
            px = pos[0]
            py = pos[1]
            pz = pos[2] if is_3d else None
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w, h = obs
                    if x <= px <= x + w and y <= py <= y + h:
                        return True
            return False

        def is_edge_in_obstacle(p1, p2):
            key = (p1, p2)
            key_rev = (p2, p1)
            if key in edge_collision_cache:
                return edge_collision_cache[key]
            if key_rev in edge_collision_cache:
                return edge_collision_cache[key_rev]

            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return True

            distance = dist(p1, p2)
            steps = max(1, int(distance / self.collision_check_resolution))
            for i in range(steps + 1):
                interp = tuple(p1[d] + (p2[d] - p1[d]) * (i / steps) for d in range(dim))
                if is_in_obstacle(interp):
                    edge_collision_cache[key] = True
                    return True
            edge_collision_cache[key] = False
            return False

        def can_connect(p1, p2):
            if is_in_obstacle(p2):
                return False
            if is_edge_in_obstacle(p1, p2):
                return False
            return True

        def can_add_node(pos):
            return in_bounds(pos) and not is_in_obstacle(pos)

        # KDTree with linear search (sufficient for moderate node counts)
        class KDTree:
            def __init__(self, points_nodes):
                self.points = [pn[0] for pn in points_nodes]
                self.nodes = [pn[1] for pn in points_nodes]

            def nearest(self, pt):
                best_node = None
                best_dist = float('inf')
                for p, n in zip(self.points, self.nodes):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best_node = n
                return best_node

            def radius_search(self, pt, radius):
                r2 = radius * radius
                results = []
                for p, n in zip(self.points, self.nodes):
                    sqd = 0.0
                    for d in range(dim):
                        delta = p[d] - pt[d]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        results.append(n)
                return results

        def shortcut_path(path):
            if len(path) < 3:
                return path
            p = path[:]
            for _ in range(self.smoothing_iterations):
                if len(p) < 3:
                    break
                i = random.randint(0, len(p) - 3)
                j = random.randint(i + 2, len(p) - 1)
                if can_connect(p[i], p[j]):
                    p = p[:i + 1] + p[j:]
            return p

        def prune_useless_nodes(best_cost):
            removed = 0
            to_remove = []
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                heuristic = dist(node.position, goal_pos)
                if node.cost + heuristic - 1e-12 >= best_cost:
                    to_remove.append(node)
            for rmn in to_remove:
                if rmn.parent:
                    rmn.parent.remove_child(rmn)
                try:
                    nodes.remove(rmn)
                except ValueError:
                    pass
                if rmn in tree_start:
                    tree_start.remove(rmn)
                if rmn in tree_goal:
                    tree_goal.remove(rmn)
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)

        # Trees: lists of nodes for start and goal sides
        tree_start = [root_start]
        tree_goal = [root_goal]
        # All nodes and edges
        nodes = [root_start, root_goal]
        edges = []

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path: List[Tuple[float, ...]] = []
        success_state = False
        extracted_path = []

        for iteration in range(self.max_iter):
            # Sample point with goal bias or informed ellipsoid
            if random.random() < self.goal_sample_rate:
                q_rand = goal_pos
            else:
                q_rand = within_ellipsoid_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate growth tree selection for balance
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            # Build KDTree for tree_a and find nearest to q_rand
            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos):
                continue
            if not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(iteration, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            # Choose best parent minimizing cost
            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes:
                candidate_cost = node.cost + dist(node.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node

            new_node = Node(new_pos, parent=None, cost=min_cost)
            best_parent.add_child(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors for cost improvements
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                        except ValueError:
                            pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            # Attempt to connect trees via new_node
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False

            # Extend tree_b towards new_node stepwise
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1
            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)

                if not can_add_node(next_pos_b):
                    break
                if not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=None, cost=cost_b)
                last_node_b.add_child(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        if neighbor_b.parent:
                            try:
                                edges.remove((neighbor_b.parent, neighbor_b))
                            except ValueError:
                                pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=None, cost=final_cost)
                    new_node_b.add_child(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    # Depending on tree assignment, assemble path
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    # Shortcut smoothing to improve path quality
                    smoothed = shortcut_path(merged_path)

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = smoothed

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Periodic pruning of nodes unlikely to improve best path
            if iteration > 0 and iteration % self.prune_interval == 0 \
               and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    # Collision helpers unchanged
    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        pz = pos[2] if is_3d else None
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.15, radius_constant: float = 18.0,
                 radius_min: float = 5.0, prune_interval: int = 150,
                 prune_threshold: int = 150, smoothing_iterations: int = 40,
                 smoothing_attempts: int = 100):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.radius_constant = radius_constant
        self.radius_min = radius_min
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.smoothing_attempts = smoothing_attempts

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        from collections import deque

        bounds = map.size                  # Tuple[int, ...]: (W,H) or (W,H,D)
        start_position = map.start         # Tuple[float, ...]
        goal_position = map.goal           # Tuple[float, ...]
        obstacles = map.obstacles          # Rectangular blocks: 2D=(x,y,w,h), 3D=(x,y,z,w,h,d)

        is_3d = len(bounds) == 3

        success_state = False
        extracted_path = []
        nodes = []
        edges = []

        start_root = Node(start_position, cost=0.0)
        goal_root = Node(goal_position, cost=0.0)
        tree_start = [start_root]
        tree_goal = [goal_root]
        nodes.extend([start_root, goal_root])

        def dist(a, b):
            return math.dist(a, b)

        def in_bounds(pos):
            for d, val in enumerate(pos):
                if val < 0 or val > bounds[d]:
                    return False
            return True

        def sample_free():
            # Adaptive bias towards goal and start for faster connections
            r = random.random()
            if r < self.goal_sample_rate:
                return goal_position if random.random() < 0.6 else start_position
            else:
                for _ in range(30):  # Limit sampling attempts to avoid loop
                    sample = tuple(random.uniform(0, bounds[d]) for d in range(len(bounds)))
                    if not self._is_in_obstacle(sample, obstacles, is_3d):
                        return sample
                # fallback to goal if stuck (very rare)
                return goal_position

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            ratio = self.step_size / d
            return tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(len(from_pos)))

        def nearest(tree, point):
            # Use min(dist) for nearest
            best_node = tree[0]
            min_dist = dist(tree[0].position, point)
            for node in tree[1:]:
                cur_dist = dist(node.position, point)
                if cur_dist < min_dist:
                    min_dist = cur_dist
                    best_node = node
            return best_node

        def near_nodes(tree, pos, radius):
            r_sq = radius * radius
            result = []
            for node in tree:
                dx = tuple(node.position[d] - pos[d] for d in range(len(pos)))
                dist_sq = sum(x * x for x in dx)
                if dist_sq <= r_sq:
                    result.append(node)
            return result

        def radius_dynamic(n):
            if n == 0:
                return self.radius_min
            rad = self.radius_constant * math.sqrt(math.log(n + 1) / (n + 1))
            return max(rad, self.radius_min)

        def can_add_node(pos):
            return in_bounds(pos) and not self._is_in_obstacle(pos, obstacles, is_3d)

        def can_connect(p1, p2):
            # Edge collision check with adaptive resolution for speed-efficiency
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=min(self.step_size * 0.5, 1.0))

        def try_extend(tree, sample_pt):
            if not tree:
                return None
            nearest_node = nearest(tree, sample_pt)
            new_pos = steer(nearest_node.position, sample_pt)

            if not can_add_node(new_pos):
                return None
            if not can_connect(nearest_node.position, new_pos):
                return None

            radius = radius_dynamic(len(tree))
            near = near_nodes(tree, new_pos, radius)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node

            for node in near:
                tentative_cost = node.cost + dist(node.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Efficient rewiring using a queue to limit cost propagation
            # Only rewire neighbors if cost reduces and edge is collision-free
            queue = deque()
            for neighbor in near:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))
                    queue.append(neighbor)

            while queue:
                current = queue.popleft()
                # Propagate rewiring cost downstream children if better cost
                for child in current.children[:]:  # Copy to avoid mutation issues
                    cost_via_current = current.cost + dist(current.position, child.position)
                    if cost_via_current + 1e-12 < child.cost and can_connect(current.position, child.position):
                        try:
                            edges.remove((child.parent, child))
                        except ValueError:
                            pass
                        child.update_parent(current, cost_via_current)
                        edges.append((current, child))
                        queue.append(child)

            return new_node

        def try_connect(tree, node):
            if not tree:
                return None
            nearest_node = nearest(tree, node.position)
            last_node = nearest_node

            while True:
                new_pos = steer(last_node.position, node.position)
                if not can_add_node(new_pos):
                    return None
                if not can_connect(last_node.position, new_pos):
                    return None

                new_cost = last_node.cost + dist(last_node.position, new_pos)
                new_node = Node(new_pos, parent=last_node, cost=new_cost)
                last_node.children.append(new_node)
                tree.append(new_node)
                nodes.append(new_node)
                edges.append((last_node, new_node))

                if dist(new_pos, node.position) <= self.step_size:
                    if can_connect(new_pos, node.position):
                        final_cost = new_node.cost + dist(new_pos, node.position)
                        final_node = Node(node.position, parent=new_node, cost=final_cost)
                        new_node.children.append(final_node)
                        tree.append(final_node)
                        nodes.append(final_node)
                        edges.append((new_node, final_node))
                        return final_node
                    else:
                        return None
                last_node = new_node

        def prune_dead_nodes():
            removed = []
            # Only prune if too many nodes present
            if len(nodes) <= self.prune_threshold:
                return removed

            # Collect dead-end leaf nodes excluding roots
            to_remove = [node for node in nodes if node not in (start_root, goal_root)
                         and not node.children and node.valid]

            # Prune up to 25% or 50 nodes at once for efficiency
            max_prune = max(50, len(nodes) // 4)
            to_remove = to_remove[:max_prune]

            for node in to_remove:
                try:
                    if node.parent:
                        node.parent.children.remove(node)
                    nodes.remove(node)
                    edges[:] = [(p, c) for (p, c) in edges if c != node and p != node]
                    node.valid = False
                    removed.append(node)
                except Exception:
                    continue
            return removed

        def path_length(path):
            length = 0.0
            for i in range(len(path) - 1):
                length += dist(path[i], path[i + 1])
            return length

        def shortcut_smoothing(path):
            if len(path) < 3:
                return path

            for _ in range(self.smoothing_attempts):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    # Remove nodes between i+1 and j-1 to shortcut path
                    path = path[:i + 1] + path[j:]
            return path

        # Main planning loop
        for itr in range(self.max_iter):
            sample_pt = sample_free()

            # Always extend smaller tree to balance exploration/exploitation
            if len(tree_start) > len(tree_goal):
                tree_start, tree_goal = tree_goal, tree_start

            new_node_start = try_extend(tree_start, sample_pt)
            if new_node_start is None:
                continue

            new_node_goal = try_connect(tree_goal, new_node_start)
            if new_node_goal is not None:
                success_state = True
                path_start = new_node_start.path_from_root()
                path_goal = new_node_goal.path_from_root()
                raw_path = path_start + path_goal[::-1][1:]

                smoothed_path = shortcut_smoothing(raw_path)
                if path_length(smoothed_path) + 1e-12 < path_length(raw_path):
                    extracted_path = smoothed_path
                else:
                    extracted_path = raw_path
                break

            if itr > 0 and itr % self.prune_interval == 0 and len(nodes) > self.prune_threshold:
                prune_dead_nodes()

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D:(x,y), 3D:(x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For pruning, collision flags etc.

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except (ValueError, AttributeError):
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)
        for child in self.children:
            child._update_cost_recursive()

    def _update_cost_recursive(self):
        if self.parent:
            import math
            self.cost = self.parent.cost + math.dist(self.parent.position, self.position)
        for child in self.children:
            child._update_cost_recursive()

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float = 5.0, max_neighbor_radius: float = 30.0,
                 prune_interval: int = 200, prune_threshold: int = 300,
                 smoothing_trials: int = 120,
                 collision_check_resolution: float = 1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_trials = smoothing_trials
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))
            # Clamp within bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(n_nodes, iteration):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(n_nodes + 1) / n_nodes)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [pn[0] for pn in points_nodes]
                self.nodes = [pn[1] for pn in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                res = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        res.append(self.nodes[i])
                return res

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2) if p1 < p2 else (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            return in_bounds(pos) and not is_in_obstacle(pos)

        c_min = dist(start_pos, goal_pos)

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < 0.1:
                for _ in range(30):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u /= norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            val = max(r1 * r1 - (c_min / 2) ** 2, 0)
            r_others = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x < 1e-10 or norm_x > 1:
                    continue
                x = x / norm_x
                x = x * np.random.uniform(0, 1) ** (1 / dim)
                p = rot @ (x * radii) + center
                p_tuple = tuple(float(coord) for coord in p)
                if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                    return p_tuple
            for _ in range(30):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        def adaptive_sample(best_path, c_best, c_min):
            if best_path and random.random() < 0.7:
                for _ in range(50):
                    base_point = best_path[random.randint(0, len(best_path) - 1)]
                    noise = np.random.normal(0, self.step_size * 0.4, dim)
                    sample_point = tuple(base_point[d] + noise[d] for d in range(dim))
                    if in_bounds(sample_point) and not is_in_obstacle(sample_point):
                        return sample_point
            return informed_sample(c_best, c_min, start_pos, goal_pos)

        def shortcut_path(path):
            if len(path) < 3:
                return path[:]
            smooth = path[:]
            for _ in range(self.smoothing_trials):
                if len(smooth) < 3:
                    break
                i = random.randint(0, len(smooth) - 3)
                j = random.randint(i + 2, len(smooth) - 1)
                p1, p2 = smooth[i], smooth[j]
                if can_connect(p1, p2):
                    smooth = smooth[:i + 1] + smooth[j:]
            return smooth

        def prune_nodes(nodes_list, best_cost, root_s, root_g):
            removed = 0
            h = lambda pos: dist(pos, goal_pos)
            to_remove = []
            for node in nodes_list:
                if node in (root_s, root_g):
                    continue
                if node.cost + h(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except (ValueError, AttributeError):
                        pass
                try:
                    nodes_list.remove(node)
                except ValueError:
                    pass
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        tree_start = [root_start]
        tree_goal = [root_goal]
        nodes = [root_start, root_goal]
        edges = []

        c_best = float('inf')
        best_path = []
        success_state = False

        for it in range(self.max_iter):
            q_rand = adaptive_sample(best_path, c_best, c_min)

            # Extend smaller tree first
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)
            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius_a = neighbor_radius(len(nodes), it)
            near_nodes_a = kdtree_a.radius_search(new_pos, radius_a)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes_a:
                cand_cost = node.cost + dist(node.position, new_pos)
                if cand_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cand_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for node in near_nodes_a:
                if node is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, node.position)
                if new_cost + 1e-12 < node.cost and can_connect(new_node.position, node.position):
                    try:
                        edges.remove((node.parent, node))
                    except ValueError:
                        pass
                    node.update_parent(new_node, new_cost)
                    edges.append((new_node, node))

            # Multi-step incremental connect from tree_b towards new_node.position
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            max_steps = int(dist(nearest_b.position, new_node.position) / self.step_size) + 1
            last_node_b = nearest_b
            connected = False

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break
                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                radius_b = neighbor_radius(len(nodes), it)
                near_nodes_b = kdtree_b.radius_search(next_pos_b, radius_b)

                for nbr_b in near_nodes_b:
                    if nbr_b is last_node_b:
                        continue
                    new_cost_b = new_node_b.cost + dist(new_node_b.position, nbr_b.position)
                    if new_cost_b + 1e-12 < nbr_b.cost and can_connect(new_node_b.position, nbr_b.position):
                        try:
                            edges.remove((nbr_b.parent, nbr_b))
                        except ValueError:
                            pass
                        nbr_b.update_parent(new_node_b, new_cost_b)
                        edges.append((new_node_b, nbr_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    cost_connect = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=cost_connect)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]
                    if cost_connect + 1e-12 < c_best:
                        c_best = cost_connect
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                # Aggressive iterative shortcut smoothing after each solution
                best_path = shortcut_path(best_path)
                break

            # Periodic pruning for efficiency, when solution exists and node count high
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_nodes(tree_start, c_best, root_start, root_goal)
                prune_nodes(tree_goal, c_best, root_start, root_goal)
                edge_collision_cache.clear()

        return PlannerResult(
            success=success_state,
            path=best_path if success_state else [],
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child_node: 'Node'):
        if child_node in self.children:
            self.children.remove(child_node)

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 goal_sample_rate: float=0.12, radius_constant: float=20.0,
                 radius_min: float=4.0, prune_interval: int=100,
                 prune_threshold: int=120, smoothing_iterations: int=50,
                 collision_check_resolution: float=1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.radius_constant = radius_constant
        self.radius_min = radius_min
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size                  # Tuple[int, ...]: (W,H) or (W,H,D)
        start_position = map.start         # Tuple[float, ...]
        goal_position = map.goal           # Tuple[float, ...]
        obstacles = map.obstacles          # List of obstacle rects/cuboids

        import math
        import random

        is_3d = (len(bounds) == 3)
        dim = len(bounds)

        success_state = False
        extracted_path = []
        nodes = []
        edges = []

        start_root = Node(start_position, cost=0.0)
        goal_root = Node(goal_position, cost=0.0)
        tree_start = [start_root]
        tree_goal = [goal_root]
        nodes.extend([start_root, goal_root])

        edge_collision_cache = {}
        node_collision_cache = {}

        def dist(a, b):
            return math.dist(a, b)

        def in_bounds(pos):
            for i in range(dim):
                if pos[i] < 0 or pos[i] > bounds[i]:
                    return False
            return True

        def is_in_obstacle_cached(pos):
            if pos in node_collision_cache:
                return node_collision_cache[pos]
            res = self._is_in_obstacle(pos, obstacles, is_3d)
            node_collision_cache[pos] = res
            return res

        def is_edge_in_obstacle_cached(p1, p2):
            key = (p1, p2)
            key_rev = (p2, p1)
            if key in edge_collision_cache:
                return edge_collision_cache[key]
            if key_rev in edge_collision_cache:
                return edge_collision_cache[key_rev]
            res = self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)
            edge_collision_cache[key] = res
            return res

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle_cached(pos):
                return False
            return True

        def can_connect(p1, p2):
            if is_in_obstacle_cached(p2):
                return False
            if is_edge_in_obstacle_cached(p1, p2):
                return False
            return True

        # Adaptive informed sampling biased near start and goal with fallback uniform sampling
        def sample_free():
            p_bias = 0.7
            if random.random() < self.goal_sample_rate:
                # Bias mixture toward goal and start
                return goal_position if random.random() < p_bias else start_position
            else:
                for _ in range(15):
                    sample = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle_cached(sample):
                        return sample
                return goal_position  # fallback

        def steer(from_pos, to_pos):
            distance_between = dist(from_pos, to_pos)
            if distance_between <= self.step_size:
                return to_pos
            ratio = self.step_size / distance_between
            new_pos = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp to bounds
            clamped = tuple(max(0.0, min(bounds[d], new_pos[d])) for d in range(dim))
            return clamped

        # KDTree substitute: linear search for nearest node
        def nearest(tree, point):
            min_node = None
            min_dist = float('inf')
            for node in tree:
                d = dist(node.position, point)
                if d < min_dist:
                    min_dist = d
                    min_node = node
            return min_node

        def near_nodes(tree, pos, radius):
            radius_sq = radius * radius
            result = []
            for node in tree:
                d_sq = 0.0
                for i in range(dim):
                    diff = node.position[i] - pos[i]
                    d_sq += diff * diff
                    if d_sq > radius_sq:
                        break
                if d_sq <= radius_sq:
                    result.append(node)
            return result

        def radius_dynamic(n):
            if n <= 1:
                return self.radius_constant
            rad = self.radius_constant * math.sqrt(math.log(n + 1) / (n + 1))
            return max(rad, self.radius_min)

        def try_extend(tree, sample_pt):
            if not tree:
                return None
            nearest_node = nearest(tree, sample_pt)
            new_pos = steer(nearest_node.position, sample_pt)
            if not can_add_node(new_pos):
                return None
            if not can_connect(nearest_node.position, new_pos):
                return None

            radius = radius_dynamic(len(tree))
            near = near_nodes(tree, new_pos, radius)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for node in near:
                tentative_cost = node.cost + dist(node.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = node

            new_node = Node(new_pos, parent=None, cost=min_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors for improved cost
            for neighbor in near:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                        except ValueError:
                            pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            return new_node

        def try_connect(tree, node):
            if not tree:
                return None
            nearest_node = nearest(tree, node.position)
            last_node = nearest_node

            while True:
                new_pos = steer(last_node.position, node.position)
                if not can_add_node(new_pos):
                    return None
                if not can_connect(last_node.position, new_pos):
                    return None

                new_cost = last_node.cost + dist(last_node.position, new_pos)
                new_node = Node(new_pos, parent=None, cost=new_cost)
                last_node.add_child(new_node)
                tree.append(new_node)
                nodes.append(new_node)
                edges.append((last_node, new_node))

                if dist(new_pos, node.position) <= self.step_size:
                    # Final edge 
                    if can_connect(new_pos, node.position):
                        final_cost = new_node.cost + dist(new_pos, node.position)
                        final_node = Node(node.position, parent=None, cost=final_cost)
                        new_node.add_child(final_node)
                        tree.append(final_node)
                        nodes.append(final_node)
                        edges.append((new_node, final_node))
                        return final_node
                    else:
                        return None
                last_node = new_node

        def prune_dead_nodes_and_nonimprovable(best_cost=float('inf')):
            removed = []
            for node in nodes[:]:
                if node is start_root or node is goal_root:
                    continue
                # Dead-end nodes (no children)
                if not node.children:
                    try:
                        if node.parent:
                            node.parent.remove_child(node)
                        nodes.remove(node)
                        edges[:] = [(p, c) for (p, c) in edges if p != node and c != node]
                        removed.append(node)
                    except Exception:
                        continue
                else:
                    # Prune nodes whose cost + heuristic cannot improve best_cost
                    heuristic = dist(node.position, goal_position)
                    if node.cost + heuristic - 1e-12 >= best_cost:
                        try:
                            if node.parent:
                                node.parent.remove_child(node)
                            nodes.remove(node)
                            edges[:] = [(p,c) for (p,c) in edges if p != node and c != node]
                            removed.append(node)
                        except Exception:
                            continue
            return removed

        def path_length(path):
            length = 0.0
            for i in range(len(path) -1):
                length += dist(path[i], path[i+1])
            return length

        def shortcut_smoothing(path):
            if len(path) < 3:
                return path
            p = path[:]
            for _ in range(self.smoothing_iterations):
                if len(p) < 3:
                    break
                i = random.randint(0, len(p) - 3)
                j = random.randint(i+2, len(p)-1)
                if can_connect(p[i], p[j]):
                    p = p[:i+1] + p[j:]
            return p

        best_path = []
        best_cost = float('inf')

        for itr in range(self.max_iter):
            sample_pt = sample_free()

            # Always extend smaller tree first
            if len(tree_start) > len(tree_goal):
                tree_start, tree_goal = tree_goal, tree_start
                root_start, root_goal = goal_root, start_root
            else:
                root_start, root_goal = start_root, goal_root

            new_node_start = try_extend(tree_start, sample_pt)
            if new_node_start is None:
                continue

            new_node_goal = try_connect(tree_goal, new_node_start)
            if new_node_goal is not None:
                success_state = True
                path_start = new_node_start.path_from_root()
                path_goal = new_node_goal.path_from_root()
                raw_path = path_start + path_goal[::-1][1:]
                smoothed = shortcut_smoothing(raw_path)
                if path_length(smoothed) + 1e-12 < path_length(raw_path):
                    extracted_path = smoothed
                    new_path_cost = path_length(smoothed)
                else:
                    extracted_path = raw_path
                    new_path_cost = path_length(raw_path)
                if new_path_cost + 1e-12 < best_cost:
                    best_cost = new_path_cost
                    best_path = extracted_path
                break

            # Periodic pruning to reduce overhead and improve efficiency
            if itr > 0 and itr % self.prune_interval == 0 and len(nodes) > self.prune_threshold and best_cost < float('inf'):
                prune_dead_nodes_and_nonimprovable(best_cost)
                edge_collision_cache.clear()
                node_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False
