class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost=0.0):
        self.position = position            # Tuple[float, ...]
        self.parent = parent                # Node or None
        self.cost = cost                    # Total cost from root
        self.children = []
        self.valid = True                   # For obstacle validity if needed

    def add_child(self, child: 'Node'):
        self.children.append(child)
        child.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        if new_parent:
            new_parent.children.append(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.15, max_time: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.max_time = max_time

    def plan(self, map) -> PlannerResult:
        bounds = map.size  # Tuple[int, ...]
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)

        def within_bounds(pos):
            return all(0 <= pos[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        # Adaptive neighbor radius based on number of nodes and dimension (RRT*)
        def adaptive_neighbor_radius(n_nodes):
            gamma_rrt_star = 2 * (1.5 ** (1 / dim))  # approx. factor for dimensional space
            r = min(self.step_size * 5.0, gamma_rrt_star * ((math.log(n_nodes + 1) / (n_nodes + 1)) ** (1 / dim)))
            # Minimum radius floor
            return max(r, self.step_size * 1.5)

        def sample_free(tree_toggle):
            if random.random() < self.goal_sample_rate:
                return goal_pos if tree_toggle else start_pos
            return tuple(random.uniform(0, bounds[d]) for d in range(dim))

        def nearest(tree, point):
            # simple linear search suffices
            return min(tree, key=lambda n: dist(n.position, point))

        def near_nodes(tree, position, radius):
            return [n for n in tree if dist(n.position, position) <= radius]

        def collision_free_node(pos):
            return within_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(from_pos, to_pos):
            return not self._is_edge_in_obstacle(from_pos, to_pos, obstacles, is_3d, resolution=1.0)

        def rewire(tree, new_node, neighbors):
            for neighbor in neighbors:
                cost_through_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_through_new + 1e-7 < neighbor.cost and collision_free_edge(new_node.position, neighbor.position):
                    neighbor.update_parent(new_node, cost_through_new)

        def extend(tree, target_point):
            nearest_node = nearest(tree, target_point)
            new_pos = steer(nearest_node.position, target_point)
            if not collision_free_node(new_pos):
                return None
            if not collision_free_edge(nearest_node.position, new_pos):
                return None

            radius = adaptive_neighbor_radius(len(tree))
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in neighbors:
                tentative_cost = neighbor.cost + dist(neighbor.position, new_pos)
                if tentative_cost + 1e-7 < min_cost and collision_free_edge(neighbor.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            rewire(tree, new_node, neighbors)
            return new_node

        def try_connect(tree, from_node):
            nearest_node = nearest(tree, from_node.position)
            d = dist(nearest_node.position, from_node.position)
            if d <= self.step_size and collision_free_node(from_node.position) and collision_free_edge(nearest_node.position, from_node.position):
                return nearest_node
            return None

        # Path shortcutting for smoothing: attempt random shortcuts on path to reduce waypoints
        def path_shortcut(path: List[Tuple[float, ...]], max_attempts=50):
            if len(path) < 3:
                return path
            for _ in range(max_attempts):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if not self._is_edge_in_obstacle(path[i], path[j], obstacles, is_3d, resolution=1.0):
                    # Remove nodes between i+1 and j-1
                    path = path[:i+1] + path[j:]
            return path

        tree_start = [Node(start_pos)]
        tree_goal = [Node(goal_pos)]
        nodes = list(tree_start) + list(tree_goal)
        edges = []

        grow_start = True
        success = False
        final_path = []
        # Store best cost and best nodes to extract feasible path after timeout
        best_cost = float('inf')
        best_connection = None  # (node_in_start_tree, node_in_goal_tree)

        time_start = time.time()

        for iter_num in range(self.max_iter):
            elapsed = time.time() - time_start
            if elapsed > self.max_time:
                break  # Timeout

            rand_point = sample_free(grow_start)

            current_tree = tree_start if grow_start else tree_goal
            other_tree = tree_goal if grow_start else tree_start

            new_node = extend(current_tree, rand_point)
            if new_node is None:
                grow_start = not grow_start
                continue

            # Track edges added during extend
            # (parent,new_node) must be collision free and checked during extend
            edges.append((new_node.parent, new_node))

            # Attempt to connect new_node to other_tree
            connecting_node = try_connect(other_tree, new_node)
            if connecting_node:
                # Rewire other tree around possible connection point with adaptive radius
                radius_other = adaptive_neighbor_radius(len(other_tree))
                neighbors_other = near_nodes(other_tree, new_node.position, radius_other)

                # Find best parent in other tree for connecting node
                min_cost = connecting_node.cost + dist(connecting_node.position, new_node.position)
                best_parent_other = connecting_node
                for neighbor in neighbors_other:
                    tentative_cost = neighbor.cost + dist(neighbor.position, new_node.position)
                    if tentative_cost + 1e-7 < min_cost and collision_free_edge(neighbor.position, new_node.position):
                        min_cost = tentative_cost
                        best_parent_other = neighbor

                connecting_node_other = Node(new_node.position)
                best_parent_other.add_child(connecting_node_other)
                connecting_node_other.cost = min_cost
                other_tree.append(connecting_node_other)

                edges.append((best_parent_other, connecting_node_other))

                rewire(other_tree, connecting_node_other, neighbors_other)

                # Extract paths from both trees
                if grow_start:
                    path_start = new_node.path_from_root()
                    path_goal = connecting_node_other.path_from_root()
                else:
                    path_start = connecting_node_other.path_from_root()
                    path_goal = new_node.path_from_root()

                # Combine paths, remove duplicated node
                joined_path = path_start + path_goal[::-1][1:]

                # Update best cost and path if improved
                total_cost = new_node.cost + connecting_node_other.cost
                if total_cost < best_cost:
                    best_cost = total_cost
                    best_connection = (path_start, path_goal[::-1])

                # Mark success and break
                final_path = joined_path
                success = True
                break

            nodes.extend([new_node])
            grow_start = not grow_start

        # If not success but have best connection for partial path:
        if not success and best_connection is not None:
            path_start, path_goal_rev = best_connection
            final_path = path_start + path_goal_rev[1:]
            success = True if len(final_path) > 1 else False

        # Post-processing: path smoothing via shortcutting if path found and more than 2 points
        if success and len(final_path) > 2:
            final_path = path_shortcut(final_path, max_attempts=75)

        # Build edge list from nodes (parent-child)
        edges.clear()
        all_nodes = []
        node_set = set()
        def collect_nodes_and_edges(node):
            if node in node_set:
                return
            node_set.add(node)
            all_nodes.append(node)
            for c in node.children:
                edges.append((node, c))
                collect_nodes_and_edges(c)

        # Collect from both trees, avoid duplicates by set
        for node in tree_start:
            collect_nodes_and_edges(node)
        for node in tree_goal:
            collect_nodes_and_edges(node)

        return PlannerResult(
            success=success,
            path=final_path,
            nodes=all_nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position          # Tuple[float, ...]
        self.parent = None                # Node or None
        self.cost = cost                  # float
        self.children = []
        self.valid = True
        if parent is not None:
            self.update_parent(parent, cost)

    def add_child(self, child_node):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0, goal_sample_rate: float = 0.15,
                 neighbor_radius_constant: float = 30.0, time_limit_sec: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_constant = neighbor_radius_constant
        self.time_limit_sec = time_limit_sec

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        start_tree = [Node(start_position, None, 0.0)]
        goal_tree = [Node(goal_position, None, 0.0)]

        edges = []
        connection_found = False
        best_cost = float('inf')
        best_start_node = None
        best_goal_node = None

        start_time = time.time()

        def swap_trees():
            nonlocal start_tree, goal_tree
            start_tree, goal_tree = goal_tree, start_tree

        def dist(a, b):
            return math.dist(a, b)

        def sample_free():
            dims = len(bounds)
            while True:
                if random.random() < self.goal_sample_rate:
                    yield goal_position
                else:
                    point = tuple(random.uniform(0, bounds[d]) for d in range(dims))
                    if not self._is_in_obstacle(point, obstacles, is_3d):
                        yield point

        sample_iterator = sample_free()

        for iter_count in range(self.max_iter):
            elapsed = time.time() - start_time
            if elapsed > self.time_limit_sec:
                break

            sample = next(sample_iterator)

            # Extend start_tree towards sample
            nearest_start = self._nearest(start_tree, sample)
            new_pos_start = self._steer(nearest_start.position, sample)

            if not self._in_bounds(new_pos_start, bounds):
                continue
            if self._is_in_obstacle(new_pos_start, obstacles, is_3d):
                continue
            if self._is_edge_in_obstacle(nearest_start.position, new_pos_start, obstacles, is_3d):
                continue

            # Find neighbors in start_tree for rewiring
            neighbor_radius_start = self._adaptive_radius(len(start_tree))
            neighbors_start = self._near_nodes(start_tree, new_pos_start, neighbor_radius_start)

            # Choose best parent in start_tree
            min_cost_start = nearest_start.cost + dist(nearest_start.position, new_pos_start)
            best_parent_start = nearest_start
            for neighbor in neighbors_start:
                cost = neighbor.cost + dist(neighbor.position, new_pos_start)
                if cost < min_cost_start and not self._is_edge_in_obstacle(neighbor.position, new_pos_start, obstacles, is_3d):
                    min_cost_start = cost
                    best_parent_start = neighbor

            new_start_node = Node(new_pos_start)
            best_parent_start.add_child(new_start_node)
            new_start_node.cost = min_cost_start
            start_tree.append(new_start_node)
            edges.append((best_parent_start, new_start_node))

            # Rewire neighbors in start_tree through new_start_node
            for neighbor in neighbors_start:
                if neighbor is best_parent_start:
                    continue
                new_cost = new_start_node.cost + dist(new_start_node.position, neighbor.position)
                if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_start_node.position, neighbor.position, obstacles, is_3d):
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                            neighbor.parent.remove_child(neighbor)
                        except ValueError:
                            pass
                    neighbor.update_parent(new_start_node, new_cost)
                    edges.append((new_start_node, neighbor))

            # Extend goal_tree towards new_start_node.position to attempt connection
            nearest_goal = self._nearest(goal_tree, new_start_node.position)
            new_pos_goal = self._steer(nearest_goal.position, new_start_node.position)

            if not self._in_bounds(new_pos_goal, bounds):
                pass  # don't connect if out of bounds
            elif self._is_in_obstacle(new_pos_goal, obstacles, is_3d):
                pass  # skip if in obstacle
            elif self._is_edge_in_obstacle(nearest_goal.position, new_pos_goal, obstacles, is_3d):
                pass  # skip if edge collides
            else:
                neighbor_radius_goal = self._adaptive_radius(len(goal_tree))
                neighbors_goal = self._near_nodes(goal_tree, new_pos_goal, neighbor_radius_goal)

                min_cost_goal = nearest_goal.cost + dist(nearest_goal.position, new_pos_goal)
                best_parent_goal = nearest_goal
                for neighbor in neighbors_goal:
                    cost = neighbor.cost + dist(neighbor.position, new_pos_goal)
                    if cost < min_cost_goal and not self._is_edge_in_obstacle(neighbor.position, new_pos_goal, obstacles, is_3d):
                        min_cost_goal = cost
                        best_parent_goal = neighbor

                new_goal_node = Node(new_pos_goal)
                best_parent_goal.add_child(new_goal_node)
                new_goal_node.cost = min_cost_goal
                goal_tree.append(new_goal_node)
                edges.append((best_parent_goal, new_goal_node))

                # Rewire neighbors in goal_tree through new_goal_node
                for neighbor in neighbors_goal:
                    if neighbor is best_parent_goal:
                        continue
                    new_cost = new_goal_node.cost + dist(new_goal_node.position, neighbor.position)
                    if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_goal_node.position, neighbor.position, obstacles, is_3d):
                        if neighbor.parent:
                            try:
                                edges.remove((neighbor.parent, neighbor))
                                neighbor.parent.remove_child(neighbor)
                            except ValueError:
                                pass
                        neighbor.update_parent(new_goal_node, new_cost)
                        edges.append((new_goal_node, neighbor))

                # Check if we can connect new_start_node and new_goal_node
                if dist(new_start_node.position, new_goal_node.position) <= self.step_size:
                    if not self._is_edge_in_obstacle(new_start_node.position, new_goal_node.position, obstacles, is_3d):
                        cost_to_connect = new_start_node.cost + dist(new_start_node.position, new_goal_node.position) + new_goal_node.cost
                        if cost_to_connect < best_cost:
                            best_cost = cost_to_connect
                            best_start_node = new_start_node
                            best_goal_node = new_goal_node
                            connection_found = True

            if connection_found:
                break

            # Swap trees for balanced growth
            swap_trees()

        success_state = connection_found
        extracted_path = []

        if success_state and best_start_node and best_goal_node:
            path_start = best_start_node.path_from_root()
            path_goal = best_goal_node.path_from_root()
            path_goal.reverse()  # reverse to goal root
            # Concatenate paths avoiding duplication of connection point
            full_path = path_start + path_goal[1:]
            extracted_path = self._path_smoothing(full_path, obstacles, is_3d)
        else:
            # If no connection found, return best path among both trees' closest nodes to goal
            # Find closest node in start_tree to goal and in goal_tree to start, pick shorter path
            def best_approx_path(tree, target_pos):
                best_node = min(tree, key=lambda n: dist(n.position, target_pos))
                return best_node.path_from_root(), best_node.cost, best_node

            start_path, start_cost, _ = best_approx_path(start_tree, goal_position)
            goal_path, goal_cost, _ = best_approx_path(goal_tree, start_position)

            # Pick the path with smaller heuristic cost to goal or start respectively
            if start_cost + dist(start_path[-1], goal_position) < goal_cost + dist(goal_path[-1], start_position):
                approx_path = start_path
            else:
                approx_path = goal_path[::-1]

            extracted_path = self._path_smoothing(approx_path, obstacles, is_3d)
            success_state = False

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=start_tree + goal_tree,
            edges=edges
        )

    def _nearest(self, tree, point):
        return min(tree, key=lambda node: math.dist(node.position, point))

    def _near_nodes(self, tree, position, radius):
        return [node for node in tree if math.dist(node.position, position) <= radius]

    def _steer(self, from_pos, to_pos):
        dist = math.dist(from_pos, to_pos)
        if dist <= self.step_size:
            return to_pos
        ratio = self.step_size / dist
        return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(len(from_pos)))

    def _in_bounds(self, pos, bounds):
        return all(0 <= pos[d] <= bounds[d] for d in range(len(pos)))

    def _adaptive_radius(self, n_nodes):
        # Algorithm adapts neighbor radius proportional to (log n / n)^{1/d} with constant scaling
        if n_nodes <= 1:
            return self.neighbor_radius_constant
        dim = len(bounds) if hasattr(self, 'bounds') else 2  # fallback dim=2
        gamma_rrt_star = self.neighbor_radius_constant
        radius = min(gamma_rrt_star * (math.log(n_nodes) / n_nodes) ** (1/dim), self.step_size * 50)
        return radius

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

    def _path_smoothing(self, path, obstacles, is_3d, max_iterations=150):
        if len(path) < 3:
            return path[:]
        length = len(path)
        for _ in range(max_iterations):
            if length <= 2:
                break
            i = random.randint(0, length - 3)
            j = random.randint(i + 2, length - 1)
            if not self._is_edge_in_obstacle(path[i], path[j], obstacles, is_3d):
                path = path[:i + 1] + path[j:]
                length = len(path)
        return path

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 goal_sample_rate: float=0.15,
                 neighbor_radius_min: float=10.0, neighbor_radius_max: float=30.0,
                 max_planning_time: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> 'PlannerResult':
        import time
        import random
        import math

        bounds = map.size                  # Tuple[int, ...]: (W,H) or (W,H,D)
        start_pos = map.start              # Tuple[float, ...]
        goal_pos = map.goal                # Tuple[float, ...]
        obstacles = map.obstacles          # Rectangular blocks: 2D=(x,y,w,h), 3D=(x,y,z,w,h,d)

        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def nearest(tree, point):
            return min(tree, key=lambda n: dist(n.position, point))

        def adaptive_neighbor_radius(n_nodes: int) -> float:
            if n_nodes <= 1:
                return self.neighbor_radius_max
            # RRT* radius scaling for dimension dim
            gamma_rrt = 30.0
            r = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree, position, radius):
            return [node for node in tree if dist(node.position, position) <= radius]

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(from_pos, to_pos):
            return not self._is_edge_in_obstacle(from_pos, to_pos, obstacles, is_3d, resolution=self.step_size / 5)

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x/norm for x in pt)

        c_min = dist(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling_enabled = False

        def clamp_to_bounds(pos):
            return tuple(
                max(0.0, min(bounds[d], pos[d])) for d in range(dim)
            )

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a = math.cos(angle)
                sin_a = math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                # fallback to identity if direction is aligned with x-axis
                norm_k = math.sqrt(kx*kx + ky*ky + kz*kz)
                if norm_k < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= norm_k; ky /= norm_k; kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(1 - cos_theta*cos_theta)
                K = [[0, -kz, ky],[kz, 0, -kx],[-ky, kx, 0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A,B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A,s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A,B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K,K)
                R = mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))
                return R

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_free()
            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0/n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1] + R[0][2]*sample_scaled[2]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1] + R[1][2]*sample_scaled[2]
                    z_rot = R[2][0]*sample_scaled[0] + R[2][1]*sample_scaled[1] + R[2][2]*sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = clamp_to_bounds(sample_pt)
                if collision_free_node(sample_pt):
                    return sample_pt

        def rewire(tree, new_node, neighbors):
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

        def extend(tree, target_point):
            nearest_node = nearest(tree, target_point)
            new_pos = steer(nearest_node.position, target_point)
            new_pos = clamp_to_bounds(new_pos)

            if not collision_free_node(new_pos):
                return None
            if not collision_free_edge(nearest_node.position, new_pos):
                return None

            neighbors_radius = adaptive_neighbor_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, neighbors_radius)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for nbr in neighbors:
                if nbr == nearest_node:
                    continue
                tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                if tentative_cost < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            rewire(tree, new_node, neighbors)

            return new_node

        # Connect the new_node to opposite tree incrementally with rewiring
        def try_connect_trees(new_node, other_tree):
            curr_node = nearest(other_tree, new_node.position)
            while True:
                next_pos = steer(curr_node.position, new_node.position)
                next_pos = clamp_to_bounds(next_pos)
                if not collision_free_node(next_pos) or not collision_free_edge(curr_node.position, next_pos):
                    return None

                neighbors_radius = adaptive_neighbor_radius(len(other_tree) + 1)
                neighbors = near_nodes(other_tree, next_pos, neighbors_radius)

                min_cost = curr_node.cost + dist(curr_node.position, next_pos)
                best_parent = curr_node
                for nbr in neighbors:
                    if nbr == curr_node:
                        continue
                    tentative_cost = nbr.cost + dist(nbr.position, next_pos)
                    if tentative_cost < min_cost and collision_free_edge(nbr.position, next_pos):
                        min_cost = tentative_cost
                        best_parent = nbr

                new_connect_node = Node(next_pos)
                best_parent.add_child(new_connect_node)
                new_connect_node.cost = min_cost
                other_tree.append(new_connect_node)

                rewire(other_tree, new_connect_node, neighbors)

                if dist(new_connect_node.position, new_node.position) <= self.step_size:
                    if collision_free_edge(new_connect_node.position, new_node.position):
                        return new_connect_node
                    else:
                        return None

                curr_node = new_connect_node

        def path_smoothing(path, max_trials=100):
            if len(path) < 3:
                return path[:]
            for _ in range(max_trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start = [Node(start_pos, cost=0.0)]
        tree_goal = [Node(goal_pos, cost=0.0)]

        all_nodes = tree_start + tree_goal
        edges = []

        start_time = time.perf_counter()
        grow_start_tree = True

        while True:
            iter_count = 0
            while iter_count < self.max_iter:
                if time.perf_counter() - start_time > self.max_planning_time:
                    # Time limit reached — extract best path found so far
                    if best_path:
                        extracted_path_final = path_smoothing(best_path)
                        return PlannerResult(True, extracted_path_final, all_nodes, edges)
                    else:
                        return PlannerResult(False, [], all_nodes, edges)

                iter_count += 1
                sample = None
                if informed_sampling_enabled:
                    sample = informed_sample(best_cost)
                else:
                    if random.random() < self.goal_sample_rate:
                        sample = goal_pos if grow_start_tree else start_pos
                    else:
                        sample = sample_free()

                if grow_start_tree:
                    new_node = extend(tree_start, sample)
                    if new_node is None:
                        grow_start_tree = not grow_start_tree
                        continue
                    connect_node = try_connect_trees(new_node, tree_goal)
                    if connect_node:
                        # Build path
                        path_s = new_node.path_from_root()
                        path_g = connect_node.path_from_root()

                        # Avoid duplicate node position overlap in merged path
                        if path_g and path_g[0] == new_node.position:
                            path_g = path_g[1:]
                        combined_path = path_s + path_g[::-1]

                        combined_cost = new_node.cost + connect_node.cost + dist(new_node.position, connect_node.position)

                        if combined_cost < best_cost:
                            best_cost = combined_cost
                            best_path = combined_path
                            informed_sampling_enabled = True

                        # Update edges for all new nodes (maintain consistent edges)
                        # Clear and re-build edges
                        edges.clear()
                        all_nodes = tree_start + tree_goal
                        for node in all_nodes:
                            for c in node.children:
                                edges.append((node, c))

                        extracted_path = path_smoothing(best_path)
                        return PlannerResult(True, extracted_path, all_nodes, edges)

                else:
                    new_node = extend(tree_goal, sample)
                    if new_node is None:
                        grow_start_tree = not grow_start_tree
                        continue
                    connect_node = try_connect_trees(new_node, tree_start)
                    if connect_node:
                        path_g = new_node.path_from_root()
                        path_s = connect_node.path_from_root()
                        if path_s and path_s[0] == new_node.position:
                            path_s = path_s[1:]
                        combined_path = path_s + path_g[::-1]

                        combined_cost = new_node.cost + connect_node.cost + dist(new_node.position, connect_node.position)

                        if combined_cost < best_cost:
                            best_cost = combined_cost
                            best_path = combined_path
                            informed_sampling_enabled = True

                        edges.clear()
                        all_nodes = tree_start + tree_goal
                        for node in all_nodes:
                            for c in node.children:
                                edges.append((node, c))

                        extracted_path = path_smoothing(best_path)
                        return PlannerResult(True, extracted_path, all_nodes, edges)

                grow_start_tree = not grow_start_tree
            break  # exit outer while if max_iter reached

        # No complete connection found, try best path extraction
        if best_path:
            extracted_path = path_smoothing(best_path)
            success_state = True
        else:
            success_state = False
            extracted_path = []

        return PlannerResult(success_state, extracted_path, all_nodes, edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = 0.0
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]; 2D or 3D
        self.parent = parent            # Parent Node or None
        self.cost = cost                # Cost from root to this node
        self.children = []              # List of child nodes
        self.valid = True               # Validity flag for collision check

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0, goal_sample_rate: float=0.1, neighbor_radius: float=15.0, max_time_sec: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius = neighbor_radius
        self.max_time_sec = max_time_sec

    def plan(self, map) -> PlannerResult:
        import time
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        dimension = len(bounds)
        is_3d = (dimension == 3)

        start_time = time.perf_counter()

        nodes = []
        edges = []

        tree_start = [Node(start_position)]
        tree_goal = [Node(goal_position)]
        nodes.extend(tree_start)
        nodes.extend(tree_goal)

        success_state = False
        extracted_path = []
        best_path_cost = float('inf')
        best_path_nodes = (None, None)  # Tuple of nodes in start and goal trees

        def sample_free():
            # Goal biasing
            if random.random() < self.goal_sample_rate:
                return goal_position
            while True:
                p = tuple(random.uniform(0, bounds[d]) for d in range(dimension))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p

        def within_bounds(pos):
            return all(0 <= pos[d] <= bounds[d] for d in range(dimension))

        def nearest(tree, point):
            return min(tree, key=lambda n: math.dist(n.position, point))

        def steer(from_pos, to_pos):
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dimension))

        def near_nodes(tree, position):
            return [node for node in tree if math.dist(node.position, position) <= self.neighbor_radius]

        def try_extend_and_rewire(tree, point):
            nearest_node = nearest(tree, point)
            new_pos = steer(nearest_node.position, point)

            if (
                not within_bounds(new_pos) or
                self._is_in_obstacle(new_pos, obstacles, is_3d) or
                self._is_edge_in_obstacle(nearest_node.position, new_pos, obstacles, is_3d)
            ):
                return None

            neighbors = near_nodes(tree, new_pos)

            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in neighbors:
                cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                    min_cost = cost
                    best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            for neighbor in neighbors:
                cost_thru_new = new_node.cost + math.dist(new_node.position, neighbor.position)
                if cost_thru_new < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                    neighbor.update_parent(new_node, cost_thru_new)
                    edges.append((new_node, neighbor))
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass

            return new_node

        def try_connect_and_rewire(tree, new_node):
            target = new_node.position
            current_node = nearest(tree, target)

            while True:
                dist = math.dist(current_node.position, target)
                if dist <= self.step_size:
                    new_pos = target
                else:
                    new_pos = steer(current_node.position, target)

                if (
                    not within_bounds(new_pos) or
                    self._is_in_obstacle(new_pos, obstacles, is_3d) or
                    self._is_edge_in_obstacle(current_node.position, new_pos, obstacles, is_3d)
                ):
                    return None

                neighbors = near_nodes(tree, new_pos)

                min_cost = current_node.cost + math.dist(current_node.position, new_pos)
                best_parent = current_node
                for neighbor in neighbors:
                    cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                    if cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                        min_cost = cost
                        best_parent = neighbor

                new_node_goal = Node(new_pos)
                best_parent.add_child(new_node_goal)
                new_node_goal.cost = min_cost
                tree.append(new_node_goal)
                nodes.append(new_node_goal)
                edges.append((best_parent, new_node_goal))

                for neighbor in neighbors:
                    cost_thru_new = new_node_goal.cost + math.dist(new_node_goal.position, neighbor.position)
                    if cost_thru_new < neighbor.cost and not self._is_edge_in_obstacle(new_node_goal.position, neighbor.position, obstacles, is_3d):
                        neighbor.update_parent(new_node_goal, cost_thru_new)
                        edges.append((new_node_goal, neighbor))
                        try:
                            edges.remove((neighbor.parent, neighbor))
                        except ValueError:
                            pass

                if new_node_goal.position == target:
                    return new_node_goal
                current_node = new_node_goal

        def merge_paths(node_start, node_goal):
            path_start = node_start.path_from_root()
            path_goal = node_goal.path_from_root()
            return path_start + path_goal[::-1][1:]  # Avoid duplicate middle node

        for iter_num in range(self.max_iter):
            if time.perf_counter() - start_time > self.max_time_sec:
                # Time exceeded, return best path found (if any)
                if success_state:
                    extracted_path = merge_paths(best_path_nodes[0], best_path_nodes[1])
                break

            rnd_point = sample_free()

            new_node_start = try_extend_and_rewire(tree_start, rnd_point)
            if new_node_start is None:
                tree_start, tree_goal = tree_goal, tree_start
                continue

            connect_node_goal = try_connect_and_rewire(tree_goal, new_node_start)
            if connect_node_goal:
                total_cost = new_node_start.cost + connect_node_goal.cost
                if total_cost < best_path_cost:
                    best_path_cost = total_cost
                    best_path_nodes = (new_node_start, connect_node_goal)
                    extracted_path = merge_paths(new_node_start, connect_node_goal)
                    success_state = True

                tree_start, tree_goal = tree_goal, tree_start

                # Optional early break to reduce planning time on success
                if success_state:
                    break
            else:
                tree_start, tree_goal = tree_goal, tree_start

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0, goal_sample_rate: float = 0.15, neighbor_radius: float = 15.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius = neighbor_radius

    def plan(self, map) -> 'PlannerResult':
        import random
        import math

        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        start_tree = [Node(start_position)]
        goal_tree = [Node(goal_position)]
        nodes = start_tree + goal_tree
        edges = []

        success_state = False
        extracted_path = []

        tree_a, tree_b = start_tree, goal_tree

        for _ in range(self.max_iter):
            # Sample point with goal bias towards opposite tree's root
            if random.random() < self.goal_sample_rate:
                sample = goal_position if tree_a is start_tree else start_position
            else:
                sample = self._sample_free(bounds, obstacles, is_3d)

            new_node_a = self._extend_and_rewire(tree_a, sample, obstacles, is_3d, nodes, edges, bounds)
            if new_node_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_node_b = self._incremental_connect_and_rewire(tree_b, new_node_a.position, obstacles, is_3d, nodes, edges, bounds)
            if new_node_b:
                path_a = new_node_a.path_from_root()
                path_b = new_node_b.path_from_root()
                # Remove duplicate connection node to avoid repetition
                if path_b and path_b[0] == path_a[-1]:
                    path_b = path_b[1:]
                extracted_path = path_a + path_b[::-1]
                success_state = True
                break

            tree_a, tree_b = tree_b, tree_a

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _sample_free(self, bounds, obstacles, is_3d):
        import random
        while True:
            point = tuple(max(0, min(bounds[d], random.uniform(0, bounds[d]))) for d in range(len(bounds)))
            if not self._is_in_obstacle(point, obstacles, is_3d):
                return point

    def _nearest(self, tree, point):
        import math
        return min(tree, key=lambda node: math.dist(node.position, point))

    def _steer(self, from_pos, to_pos):
        import math
        dist = math.dist(from_pos, to_pos)
        if dist <= self.step_size:
            return to_pos
        ratio = self.step_size / dist
        return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(len(from_pos)))

    def _near_nodes(self, tree, position, radius):
        import math
        return [node for node in tree if math.dist(node.position, position) <= radius]

    def _extend_and_rewire(self, tree, point, obstacles, is_3d, nodes, edges, bounds):
        import math

        nearest = self._nearest(tree, point)
        new_pos = self._steer(nearest.position, point)

        # Clamp new_pos within bounds
        new_pos = tuple(max(0, min(bounds[d], new_pos[d])) for d in range(len(bounds)))

        if self._is_in_obstacle(new_pos, obstacles, is_3d) or self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
            return None

        new_node = Node(new_pos)
        neighbors = self._near_nodes(tree, new_pos, self.neighbor_radius)

        min_cost = nearest.cost + math.dist(nearest.position, new_pos)
        best_parent = nearest
        for neighbor in neighbors:
            cost = neighbor.cost + math.dist(neighbor.position, new_pos)
            if cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                min_cost = cost
                best_parent = neighbor

        best_parent.add_child(new_node)
        new_node.cost = min_cost
        tree.append(new_node)
        nodes.append(new_node)
        edges.append((best_parent, new_node))

        # Rewire neighbors to new_node if path cost improves
        for neighbor in neighbors:
            if neighbor is best_parent:
                continue
            new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
            if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                if neighbor.parent:
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.parent.remove_child(neighbor)
                neighbor.update_parent(new_node, new_cost)
                edges.append((new_node, neighbor))

        return new_node

    def _incremental_connect_and_rewire(self, tree, target_pos, obstacles, is_3d, nodes, edges, bounds):
        import math

        curr_node = self._nearest(tree, target_pos)

        while True:
            new_pos = self._steer(curr_node.position, target_pos)
            new_pos = tuple(max(0, min(bounds[d], new_pos[d])) for d in range(len(bounds)))

            if self._is_in_obstacle(new_pos, obstacles, is_3d) or self._is_edge_in_obstacle(curr_node.position, new_pos, obstacles, is_3d):
                return None

            new_node = Node(new_pos)
            neighbors = self._near_nodes(tree, new_pos, self.neighbor_radius)

            min_cost = curr_node.cost + math.dist(curr_node.position, new_pos)
            best_parent = curr_node
            for neighbor in neighbors:
                cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                    min_cost = cost
                    best_parent = neighbor

            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors to new_node if better cost path found
            for neighbor in neighbors:
                if neighbor is best_parent:
                    continue
                new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
                if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                        except ValueError:
                            pass
                        neighbor.parent.remove_child(neighbor)
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

            if math.dist(new_node.position, target_pos) <= self.step_size:
                return new_node

            curr_node = new_node

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        new_parent.add_child(self)
        self.parent = new_parent
        self.cost = new_cost

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000,
                 step_size: float = 5.0,
                 goal_sample_rate: float = 0.1,
                 neighbor_factor: float = 2.0,
                 collision_resolution: float = 0.5,
                 max_time: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_factor = neighbor_factor
        self.collision_resolution = collision_resolution
        self.max_time = max_time

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        import time
        try:
            import numpy as np
        except ImportError:
            np = None

        bounds = map.size              # Tuple[int, ...]: (W,H) or (W,H,D)
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        def dist(a, b):
            return math.dist(a, b)

        def within_bounds(pos):
            return all(0 <= pos[d] <= bounds[d] for d in range(dim))

        # Constants for informed sampling
        c_min = dist(start_pos, goal_pos)
        c_best = float('inf')
        center = tuple((s + g)*0.5 for s, g in zip(start_pos, goal_pos))

        def rotation_matrix():
            if np is None:
                return None
            a1 = np.array([(g - s) / c_min if c_min > 1e-9 else 1.0 for s, g in zip(start_pos, goal_pos)])
            if dim == 2:
                angle = math.atan2(a1[1], a1[0])
                c_, s_ = math.cos(angle), math.sin(angle)
                return np.array([[c_, -s_], [s_, c_]])
            elif dim == 3:
                eye = np.eye(3)
                v = np.cross([1, 0, 0], a1)
                s_ = np.linalg.norm(v)
                if s_ < 1e-9:
                    return eye
                c_ = np.dot([1, 0, 0], a1)
                vx = np.array([[0, -v[2], v[1]],
                               [v[2], 0, -v[0]],
                               [-v[1], v[0], 0]])
                R = eye + vx + vx @ vx * ((1 - c_) / (s_ ** 2))
                return R
            else:
                return None

        rotation = rotation_matrix()

        def informed_sample():
            if c_best == float('inf') or np is None:
                return sample_free()
            r1 = c_best * 0.5
            r2 = math.sqrt(max(c_best**2 - c_min**2, 0)) * 0.5
            while True:
                if dim == 2:
                    theta = random.uniform(0, 2 * math.pi)
                    radius = math.sqrt(random.uniform(0, 1))
                    unit_sample = np.array([radius * math.cos(theta), radius * math.sin(theta)])
                    sample_ellipsoid = np.array([r1, r2]) * unit_sample
                    sample_world = rotation @ sample_ellipsoid + np.array(center)
                    p = tuple(np.clip(sample_world, [0]*dim, bounds))
                else:  # 3D
                    while True:
                        unit_sample = np.random.uniform(-1, 1, 3)
                        if np.linalg.norm(unit_sample) <= 1:
                            break
                    sample_ellipsoid = np.array([r1, r2, r2]) * unit_sample
                    sample_world = rotation @ sample_ellipsoid + np.array(center)
                    p = tuple(np.clip(sample_world, [0]*dim, bounds))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p

        def sample_free():
            while True:
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_pos = to_pos
            else:
                ratio = self.step_size / d
                new_pos = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp to bounds
            clamped = tuple(max(0, min(new_pos[d], bounds[d])) for d in range(dim))
            return clamped

        def nearest(tree, point):
            return min(tree, key=lambda n: dist(n.position, point))

        def near_nodes(tree, new_node):
            n = len(tree)
            if n == 0:
                return []
            radius = min(self.step_size * 3,
                         self.neighbor_factor * ((math.log(n + 1) / (n + 1)) ** (1 / dim)))
            return [node for node in tree if node != new_node and dist(node.position, new_node.position) <= radius]

        def extend_and_rewire(tree, point):
            nearest_node = nearest(tree, point)
            new_pos = steer(nearest_node.position, point)
            if not within_bounds(new_pos):
                return None
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(nearest_node.position, new_pos, obstacles, is_3d, self.collision_resolution):
                return None

            temp_node = Node(new_pos)
            neighbors = near_nodes(tree, temp_node)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in neighbors:
                cost = neighbor.cost + dist(neighbor.position, new_pos)
                if cost < min_cost:
                    if not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d, self.collision_resolution):
                        min_cost = cost
                        best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            # Rewire neighbors if better path via new_node
            for neighbor in neighbors:
                if neighbor == best_parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_through_new < neighbor.cost:
                    if not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d, self.collision_resolution):
                        neighbor.update_parent(new_node, cost_through_new)

            return new_node

        def connect_trees(tree_connect, node_to_connect):
            nearest_node = nearest(tree_connect, node_to_connect.position)
            last_node = nearest_node
            goal_pos_local = node_to_connect.position

            while True:
                next_pos = steer(last_node.position, goal_pos_local)
                if next_pos == last_node.position:
                    break
                if self._is_in_obstacle(next_pos, obstacles, is_3d):
                    return None
                if self._is_edge_in_obstacle(last_node.position, next_pos, obstacles, is_3d, self.collision_resolution):
                    return None

                cost_to_next = last_node.cost + dist(last_node.position, next_pos)
                candidate_node = Node(next_pos)
                candidate_node.cost = cost_to_next

                neighbors = near_nodes(tree_connect, candidate_node)

                min_cost = cost_to_next
                min_parent = last_node
                for near in neighbors:
                    if not self._is_edge_in_obstacle(near.position, next_pos, obstacles, is_3d, self.collision_resolution):
                        cost_via_near = near.cost + dist(near.position, next_pos)
                        if cost_via_near < min_cost:
                            min_cost = cost_via_near
                            min_parent = near

                min_parent.add_child(candidate_node)
                candidate_node.parent = min_parent
                candidate_node.cost = min_cost
                tree_connect.append(candidate_node)

                # Rewire neighbors through candidate_node
                for near in neighbors:
                    if near == candidate_node.parent:
                        continue
                    cost_through_candidate = candidate_node.cost + dist(candidate_node.position, near.position)
                    if cost_through_candidate < near.cost:
                        if not self._is_edge_in_obstacle(candidate_node.position, near.position, obstacles, is_3d, self.collision_resolution):
                            near.update_parent(candidate_node, cost_through_candidate)

                if dist(candidate_node.position, goal_pos_local) < self.step_size * 0.5:
                    return candidate_node

                last_node = candidate_node

            return None

        tree_start = [Node(start_pos)]
        tree_goal = [Node(goal_pos)]
        nodes = tree_start[:] + tree_goal[:]
        edges = []
        success_state = False
        extracted_path = []
        best_cost = float('inf')

        tree_a, tree_b = tree_start, tree_goal
        start_time = time.time()

        for iter_num in range(self.max_iter):
            elapsed = time.time() - start_time
            if elapsed > self.max_time:
                break

            # For both trees alternately
            for tree_current, tree_other in [(tree_a, tree_b), (tree_b, tree_a)]:
                if elapsed > self.max_time:
                    break

                # Sampling: goal bias + informed sampling when path found
                if c_best < float('inf') and random.random() > self.goal_sample_rate:
                    sample = informed_sample()
                else:
                    if random.random() < self.goal_sample_rate:
                        sample = goal_pos if tree_current is tree_start else start_pos
                    else:
                        sample = sample_free()

                new_node = extend_and_rewire(tree_current, sample)
                if new_node is None:
                    continue

                nodes.append(new_node)
                if new_node.parent is not None:
                    edges.append((new_node.parent, new_node))

                connected_node = connect_trees(tree_other, new_node)
                if connected_node is not None:
                    # Build combined path
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    combined_path = path_start + path_goal[::-1][1:]

                    # Compute path cost
                    path_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                    if path_cost < best_cost:
                        best_cost = path_cost
                        extracted_path = combined_path
                        success_state = True
                        c_best = best_cost

                    # Add edges for connected_node subtree
                    def add_edges_recursively(node):
                        for c in node.children:
                            if (node, c) not in edges:
                                edges.append((node, c))
                            add_edges_recursively(c)
                    add_edges_recursively(connected_node)

                    # Return on first found path to maximize speed
                    return PlannerResult(
                        success=True,
                        path=extracted_path,
                        nodes=nodes,
                        edges=edges
                    )

            tree_a, tree_b = tree_b, tree_a

        # If no connection found return best partial path by checking nearby nodes between trees
        if not success_state:
            min_cost = float('inf')
            best_connection = None
            for ns in tree_start:
                for ng in tree_goal:
                    if dist(ns.position, ng.position) <= self.step_size:
                        if not self._is_edge_in_obstacle(ns.position, ng.position, obstacles, is_3d, self.collision_resolution):
                            total_cost = ns.cost + dist(ns.position, ng.position) + ng.cost
                            if total_cost < min_cost:
                                min_cost = total_cost
                                best_connection = (ns, ng)
            if best_connection is not None:
                path_start = best_connection[0].path_from_root()
                path_goal = best_connection[1].path_from_root()
                extracted_path = path_start + path_goal[::-1][1:]
                success_state = True
                best_cost = min_cost

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position        # Tuple[float, ...] → 2D or 3D coords
        self.parent = None              # Node or None
        self.cost = cost                # Cost from root to this node
        self.children: List[Node] = []
        self.valid = True               # For collision checking etc.

        if parent is not None:
            self.update_parent(parent, cost)

    def add_child(self, child_node: 'Node'):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node: 'Node'):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        if new_parent is not None:
            if self not in new_parent.children:
                new_parent.children.append(self)
        # Recursively update cost of descendants
        for child in self.children:
            child_cost = self.cost + math.dist(self.position, child.position)
            child.update_parent(self, child_cost)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float = 4.0,
                 goal_sample_rate: float = 0.15,
                 min_neighbor_radius: float = 5.0, max_neighbor_radius: float = 25.0,
                 max_time_sec: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.max_time_sec = max_time_sec

    def plan(self, map) -> PlannerResult:
        bounds = map.size                  # Tuple[int, ...]
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        start_tree: List[Node] = [Node(start_pos)]
        goal_tree: List[Node] = [Node(goal_pos)]
        nodes: List[Node] = start_tree[:] + goal_tree[:]
        edges: List[Tuple[Node, Node]] = []

        def within_bounds(pos):
            return all(0 <= pos[d] <= bounds[d] for d in range(dim))

        def is_free_point(p):
            return within_bounds(p) and not self._is_in_obstacle(p, obstacles, is_3d)

        def is_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d)

        def sample_free(tree_indicator: bool):
            # tree_indicator=True means sampling for start_tree toward goal, else vice versa
            if random.random() < self.goal_sample_rate:
                return goal_pos if tree_indicator else start_pos
            for _ in range(30):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if is_free_point(p):
                    return p
            return goal_pos if tree_indicator else start_pos

        def steer(from_p, to_p):
            dist = math.dist(from_p, to_p)
            if dist <= self.step_size:
                return to_p
            ratio = self.step_size / dist
            return tuple(from_p[d] + (to_p[d] - from_p[d]) * ratio for d in range(dim))

        def adaptive_neighbor_radius(n_nodes):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            gamma = 30.0  # tuning param, can be adjusted
            radius = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.min_neighbor_radius, min(radius, self.max_neighbor_radius))

        def near_nodes(tree, pos, radius):
            radius_sq = radius * radius
            return [n for n in tree if (math.dist(n.position, pos) ** 2) <= radius_sq]

        def add_node(tree, new_pos, best_parent, cost):
            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))
            return new_node

        def rewire(tree, new_node, neighbors):
            for neighbor in neighbors:
                if neighbor is new_node.parent:
                    continue
                dist = math.dist(new_node.position, neighbor.position)
                new_cost = new_node.cost + dist
                if new_cost < neighbor.cost and is_free_edge(new_node.position, neighbor.position):
                    if neighbor.parent is not None:
                        if (neighbor.parent, neighbor) in edges:
                            edges.remove((neighbor.parent, neighbor))
                        neighbor.parent.remove_child(neighbor)
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

        def extend_and_rewire(tree, sample, tree_indicator):
            if len(tree) == 0:
                return None
            nearest = min(tree, key=lambda n: math.dist(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not is_free_point(new_pos) or not is_free_edge(nearest.position, new_pos):
                return None
            radius = adaptive_neighbor_radius(len(tree))
            neighbors = near_nodes(tree, new_pos, radius)
            min_cost = nearest.cost + math.dist(nearest.position, new_pos)
            best_parent = nearest
            for neighbor in neighbors:
                cost_through_neighbor = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost_through_neighbor < min_cost and is_free_edge(neighbor.position, new_pos):
                    min_cost = cost_through_neighbor
                    best_parent = neighbor
            new_node = add_node(tree, new_pos, best_parent, min_cost)
            rewire(tree, new_node, neighbors)
            return new_node

        def incremental_connect_and_rewire(tree, target_pos):
            if len(tree) == 0:
                return None
            nearest = min(tree, key=lambda n: math.dist(n.position, target_pos))
            curr_node = nearest
            while True:
                new_pos = steer(curr_node.position, target_pos)
                if not is_free_point(new_pos) or not is_free_edge(curr_node.position, new_pos):
                    return None
                radius = adaptive_neighbor_radius(len(tree))
                neighbors = near_nodes(tree, new_pos, radius)
                min_cost = curr_node.cost + math.dist(curr_node.position, new_pos)
                best_parent = curr_node
                for neighbor in neighbors:
                    cost_through_neighbor = neighbor.cost + math.dist(neighbor.position, new_pos)
                    if cost_through_neighbor < min_cost and is_free_edge(neighbor.position, new_pos):
                        min_cost = cost_through_neighbor
                        best_parent = neighbor
                new_node = add_node(tree, new_pos, best_parent, min_cost)
                rewire(tree, new_node, neighbors)
                if math.dist(new_node.position, target_pos) <= self.step_size:
                    return new_node
                curr_node = new_node

        def path_smoothing(raw_path: List[Tuple[float, ...]], iterations: int = 100) -> List[Tuple[float, ...]]:
            if len(raw_path) <= 2:
                return raw_path[:]
            path = raw_path[:]
            for _ in range(iterations):
                if len(path) <= 2:
                    break
                idx1 = random.randint(0, len(path) - 2)
                idx2 = random.randint(idx1 + 1, len(path) - 1)
                if idx2 == idx1 + 1:
                    continue
                p1 = path[idx1]
                p2 = path[idx2]
                if is_free_edge(p1, p2):
                    path = path[:idx1 + 1] + path[idx2:]
            return path

        start_time = time.time()
        tree_a, tree_b = start_tree, goal_tree
        success = False
        best_path: List[Tuple[float, ...]] = []
        best_cost = math.inf

        for _ in range(self.max_iter):
            if time.time() - start_time > self.max_time_sec:
                break

            sample = sample_free(tree_indicator=True)
            new_node_a = extend_and_rewire(tree_a, sample, True)
            if new_node_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_node_b = incremental_connect_and_rewire(tree_b, new_node_a.position)
            if new_node_b is not None:
                path_from_start = new_node_a.path_from_root()
                path_from_goal = new_node_b.path_from_root()
                combined_path = path_from_start + path_from_goal[::-1][1:]  # avoid duplicated node

                total_cost = new_node_a.cost + new_node_b.cost
                if total_cost < best_cost:
                    best_cost = total_cost
                    best_path = path_smoothing(combined_path, iterations=100)
                    success = True
                    # Optional: stop early on first solution, comment next line to keep searching
                    break

            tree_a, tree_b = tree_b, tree_a

        return PlannerResult(
            success=success,
            path=best_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent: Optional['Node']=None, cost: float=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = None              # Node or None
        self.cost = cost                # Path cost from root to this node
        self.children = []
        self.valid = True               # For collision checking etc.
        if parent:
            self.update_parent(parent, cost)

    def add_child(self, child_node: 'Node'):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node: 'Node'):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0, goal_sample_rate: float=0.15,
                 neighbor_radius_min: float=10.0, neighbor_radius_max: float=30.0,
                 max_planning_time: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> PlannerResult:
        bounds = map.size                  # Tuple[int, ...]: (W,H) or (W,H,D)
        start_position = map.start         # Tuple[float, ...] (W,H) or (W,H,D)
        goal_position = map.goal           # Tuple[float, ...] (W,H) or (W,H,D)
        obstacles = map.obstacles          # Rectangular blocks: 2D=(x,y,w,h), 3D=(x,y,z,w,h,d)
        is_3d = len(bounds) == 3
        dim = len(bounds)

        start_tree = [Node(start_position, None, 0.0)]
        goal_tree = [Node(goal_position, None, 0.0)]
        nodes = start_tree + goal_tree
        edges = []

        tree_a, tree_b = start_tree, goal_tree  # Trees alternate growth each iteration

        success_state = False
        extracted_path: List[Tuple[float, ...]] = []
        best_connection_nodes = None
        best_cost = math.inf

        start_time = time.perf_counter()

        # Precompute start-goal dist and initialize sampling ellipse params
        c_best = math.dist(start_position, goal_position)
        x_center = tuple((s+g)/2.0 for s, g in zip(start_position, goal_position))
        use_informed_sampling = False

        def in_bounds(pos: Tuple[float, ...]) -> bool:
            return all(0 <= pos[d] <= bounds[d] for d in range(dim))

        def _ellipsoidal_sample(c_max: float) -> Tuple[float, ...]:
            # Implements informed sampling inside an ellipse between start and goal
            # Reference: Informed RRT* sampling (Gammell, 2014)
            if c_max == math.inf:
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            a1 = np.array([(g - s) / c_max for s, g in zip(start_position, goal_position)], dtype=float)
            # Build orthonormal basis matrix C via a rotation matrix:
            # Create identity and replace first dim with a1
            I = np.eye(dim)
            M = np.outer(a1, np.array([1.0] + [0]*(dim-1)))  # rank-1 matrix
            U, _, Vt = np.linalg.svd(M)
            C = np.dot(U, Vt)

            # Sample in unit n-ball 
            while True:
                z = np.random.normal(0, 1, dim)
                norm_z = np.linalg.norm(z)
                if norm_z > 1e-9:  # avoid division by zero
                    break
            z_unit = z / norm_z

            # Sample radius with probability ~ r^{dim-1} to fill ball uniformly
            r = random.random() ** (1/dim)
            r *= z_unit

            # Lengths of the ellipse axes
            r1 = c_max / 2.0
            r2 = math.sqrt(c_max**2 - c_best**2) / 2.0
            if dim == 2:
                L = np.diag([r1, r2])
            elif dim == 3:
                # For 3D, use r2 for axes 2 and 3
                L = np.diag([r1, r2, r2])
            else:
                # fallback: use r2 for other axes
                L = np.diag([r1] + [r2]*(dim-1))

            sample_ball = np.dot(C, np.dot(L, r))
            sample_pt = np.array(x_center) + sample_ball
            sample_pt = np.clip(sample_pt, 0, bounds)
            return tuple(float(x) for x in sample_pt)

        def sample_free():
            for _ in range(100):  # Max 100 attempts
                if use_informed_sampling and best_cost < math.inf:
                    sample = _ellipsoidal_sample(best_cost)
                else:
                    sample = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(sample, obstacles, is_3d) and in_bounds(sample):
                    return sample
            # fallback to uniform sample ignoring obstacle if no free found (very unlikely)
            return tuple(random.uniform(0, bounds[d]) for d in range(dim))

        def adaptive_neighbor_radius(n_nodes: int) -> float:
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 30.0
            r = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def nearest(tree: List[Node], point: Tuple[float, ...]) -> Node:
            return min(tree, key=lambda node: math.dist(node.position, point))

        def steer(from_pos: Tuple[float, ...], to_pos: Tuple[float, ...]) -> Tuple[float, ...]:
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dim))

        def near_nodes(tree: List[Node], position: Tuple[float, ...], radius: float) -> List[Node]:
            return [node for node in tree if math.dist(node.position, position) <= radius]

        def extend_and_rewire(tree: List[Node], sample: Tuple[float, ...]) -> Optional[Node]:
            nearest_node = nearest(tree, sample)
            new_pos = steer(nearest_node.position, sample)
            if not in_bounds(new_pos):
                return None
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(nearest_node.position, new_pos, obstacles, is_3d):
                return None

            new_node = Node(new_pos)
            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for nbr in neighbors:
                c = nbr.cost + math.dist(nbr.position, new_pos)
                if c < min_cost and not self._is_edge_in_obstacle(nbr.position, new_pos, obstacles, is_3d):
                    min_cost = c
                    best_parent = nbr

            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for nbr in neighbors:
                if nbr == best_parent:
                    continue
                new_c = new_node.cost + math.dist(new_node.position, nbr.position)
                if new_c < nbr.cost and not self._is_edge_in_obstacle(new_node.position, nbr.position, obstacles, is_3d):
                    if nbr.parent:
                        try:
                            edges.remove((nbr.parent, nbr))
                            nbr.parent.remove_child(nbr)
                        except ValueError:
                            pass
                    nbr.update_parent(new_node, new_c)
                    edges.append((new_node, nbr))
            return new_node

        def incremental_connect_and_rewire(tree: List[Node], target_pos: Tuple[float, ...]) -> Optional[Node]:
            nearest_node = nearest(tree, target_pos)
            curr_node = nearest_node
            max_steps = int(math.dist(curr_node.position, target_pos) / self.step_size) + 2
            for _ in range(max_steps):
                new_pos = steer(curr_node.position, target_pos)
                if not in_bounds(new_pos):
                    return None
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    return None
                if self._is_edge_in_obstacle(curr_node.position, new_pos, obstacles, is_3d):
                    return None

                new_node = Node(new_pos)
                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = curr_node.cost + math.dist(curr_node.position, new_pos)
                best_parent = curr_node
                for nbr in neighbors:
                    c = nbr.cost + math.dist(nbr.position, new_pos)
                    if c < min_cost and not self._is_edge_in_obstacle(nbr.position, new_pos, obstacles, is_3d):
                        min_cost = c
                        best_parent = nbr

                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)
                nodes.append(new_node)
                edges.append((best_parent, new_node))

                # Rewire neighbors
                for nbr in neighbors:
                    if nbr == best_parent:
                        continue
                    new_c = new_node.cost + math.dist(new_node.position, nbr.position)
                    if new_c < nbr.cost and not self._is_edge_in_obstacle(new_node.position, nbr.position, obstacles, is_3d):
                        if nbr.parent:
                            try:
                                edges.remove((nbr.parent, nbr))
                                nbr.parent.remove_child(nbr)
                            except ValueError:
                                pass
                        nbr.update_parent(new_node, new_c)
                        edges.append((new_node, nbr))

                if math.dist(new_node.position, target_pos) <= self.step_size:
                    # If direct connection between new_node and target_pos is collision-free, return new_node
                    if not self._is_edge_in_obstacle(new_node.position, target_pos, obstacles, is_3d):
                        return new_node
                curr_node = new_node
            return None

        def path_smoothing(path: List[Tuple[float, ...]], max_iterations: int=120) -> List[Tuple[float, ...]]:
            if len(path) < 3:
                return path[:]
            length = len(path)
            for _ in range(max_iterations):
                if length <= 2:
                    break
                i = random.randint(0, length - 3)
                j = random.randint(i + 2, length - 1)
                if not self._is_edge_in_obstacle(path[i], path[j], obstacles, is_3d):
                    path = path[:i + 1] + path[j:]
                    length = len(path)
            return path

        for iteration in range(self.max_iter):
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                # Time exceeded: break and return best path if any
                if best_connection_nodes is not None:
                    node_a, node_b = best_connection_nodes
                    path_a = node_a.path_from_root()
                    path_b = node_b.path_from_root()
                    combined_path = path_a + path_b[::-1]
                    extracted_path = path_smoothing(combined_path)
                    success_state = True
                break

            # Goal-biased sampling: with probability goal_sample_rate, sample towards opposite tree root
            if random.random() < self.goal_sample_rate:
                sample = tree_b[0].position if tree_a is start_tree else tree_a[0].position
            else:
                sample = sample_free()

            new_node_a = extend_and_rewire(tree_a, sample)
            if new_node_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_node_b = incremental_connect_and_rewire(tree_b, new_node_a.position)
            if new_node_b:
                connect_cost = new_node_a.cost + new_node_b.cost + math.dist(new_node_a.position, new_node_b.position)
                if connect_cost < best_cost:
                    best_cost = connect_cost
                    best_connection_nodes = (new_node_a, new_node_b)
                    # Update informed sampling usage and parameters
                    c_best = best_cost
                    x_center = tuple((s+g)/2.0 for s,g in zip(start_position, goal_position))
                    use_informed_sampling = True

                    # Extract and smooth path early for faster return on success
                    path_a = new_node_a.path_from_root()
                    path_b = new_node_b.path_from_root()
                    # Make sure no duplicate point at connection
                    if path_b and path_b[0] == new_node_a.position:
                        path_b = path_b[1:]
                    combined_path = path_a + path_b[::-1]
                    extracted_path = path_smoothing(combined_path)
                    success_state = True
                    break
            tree_a, tree_b = tree_b, tree_a

        if not success_state and best_connection_nodes is not None:
            node_a, node_b = best_connection_nodes
            path_a = node_a.path_from_root()
            path_b = node_b.path_from_root()
            if path_b and path_b[0] == node_a.position:
                path_b = path_b[1:]
            combined_path = path_a + path_b[::-1]
            extracted_path = path_smoothing(combined_path)
            success_state = True

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=0.5):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)
        for child in self.children:
            child.update_parent(self, self.cost + math.dist(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float=3.0, goal_sample_rate: float=0.15,
                 min_neighbor_radius: float=5.0, max_neighbor_radius: float=30.0, max_time_sec: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.max_time_sec = max_time_sec

    def plan(self, map) -> 'PlannerResult':
        import random
        import time
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        is_3d = len(bounds) == 3
        dim = len(bounds)

        def sample_free(tree_indicator):
            # tree_indicator: True for start_tree sampling goal, False for goal_tree sampling start
            if random.random() < self.goal_sample_rate:
                return goal_pos if tree_indicator else start_pos
            for _ in range(30):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(p, obstacles, is_3d) and self._within_bounds(p, bounds):
                    return p
            return goal_pos if tree_indicator else start_pos

        def steer(from_p, to_p):
            dist = math.dist(from_p, to_p)
            if dist <= self.step_size:
                return to_p
            ratio = self.step_size / dist
            return tuple(from_p[d] + (to_p[d] - from_p[d]) * ratio for d in range(dim))

        def near_nodes(tree, pos, radius):
            radius_sq = radius * radius
            return [n for n in tree if (math.dist(n.position, pos) ** 2) <= radius_sq]

        def valid_node(pos):
            return self._within_bounds(pos, bounds) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def valid_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d)

        def adaptive_neighbor_radius(n_nodes):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            # Volume of ball in R^d: For 2D πr^2, 3D (4/3)πr^3; Using heuristic R ~ gamma * (log(n)/n)^(1/d)
            gamma = 25.0  # tuning parameter
            radius = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.min_neighbor_radius, min(radius, self.max_neighbor_radius))

        def add_node(tree, new_pos, best_parent, cost):
            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))
            return new_node

        def rewire(tree, new_node, neighbors):
            for neighbor in neighbors:
                if neighbor is new_node.parent:
                    continue
                dist = math.dist(new_node.position, neighbor.position)
                new_cost = new_node.cost + dist
                if new_cost < neighbor.cost and valid_edge(new_node.position, neighbor.position):
                    if neighbor.parent is not None:
                        if (neighbor.parent, neighbor) in edges:
                            edges.remove((neighbor.parent, neighbor))
                        neighbor.parent.remove_child(neighbor)
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

        def extend_and_rewire(tree, sample, tree_indicator):
            if len(tree) == 0:
                return None
            nearest = min(tree, key=lambda node: math.dist(node.position, sample))
            new_pos = steer(nearest.position, sample)
            if not valid_node(new_pos): 
                return None
            if not valid_edge(nearest.position, new_pos):
                return None
            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes)
            neighbors = near_nodes(tree, new_pos, radius)
            min_cost = nearest.cost + math.dist(nearest.position, new_pos)
            best_parent = nearest
            for neighbor in neighbors:
                cost_through_neighbor = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost_through_neighbor < min_cost and valid_edge(neighbor.position, new_pos):
                    min_cost = cost_through_neighbor
                    best_parent = neighbor
            new_node = add_node(tree, new_pos, best_parent, min_cost)
            rewire(tree, new_node, neighbors)
            return new_node

        def incremental_connect_and_rewire(tree, target_pos):
            if len(tree) == 0:
                return None
            nearest = min(tree, key=lambda node: math.dist(node.position, target_pos))
            curr_node = nearest

            while True:
                new_pos = steer(curr_node.position, target_pos)
                if not valid_node(new_pos) or not valid_edge(curr_node.position, new_pos):
                    return None
                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes)
                neighbors = near_nodes(tree, new_pos, radius)
                min_cost = curr_node.cost + math.dist(curr_node.position, new_pos)
                best_parent = curr_node
                for neighbor in neighbors:
                    cost_through_neighbor = neighbor.cost + math.dist(neighbor.position, new_pos)
                    if cost_through_neighbor < min_cost and valid_edge(neighbor.position, new_pos):
                        min_cost = cost_through_neighbor
                        best_parent = neighbor
                new_node = add_node(tree, new_pos, best_parent, min_cost)
                rewire(tree, new_node, neighbors)
                if math.dist(new_node.position, target_pos) <= self.step_size:
                    return new_node
                curr_node = new_node

        def path_smoothing(raw_path, iterations=50):
            if len(raw_path) <= 2:
                return raw_path[:]
            path = raw_path[:]
            for _ in range(iterations):
                if len(path) <= 2:
                    break
                idx1 = random.randint(0, len(path) - 2)
                idx2 = random.randint(idx1 + 1, len(path) -1)
                if idx2 == idx1 + 1:
                    continue
                p1 = path[idx1]
                p2 = path[idx2]
                # Check collision-free shortcut
                if valid_edge(p1, p2):
                    # Remove intermediate nodes
                    path = path[:idx1 + 1] + path[idx2:]
            return path

        start_time = time.time()

        start_tree = [Node(start_pos)]
        goal_tree = [Node(goal_pos)]
        nodes = start_tree[:] + goal_tree[:]
        edges = []

        tree_a = start_tree
        tree_b = goal_tree
        success = False
        best_path = []
        best_cost = math.inf

        for iter_cnt in range(self.max_iter):
            if time.time() - start_time > self.max_time_sec:
                # Time exceeded: return best found
                break

            sample = sample_free(tree_indicator=True)  # tree_a grows towards goal_pos
            new_node_a = extend_and_rewire(tree_a, sample, True)
            if new_node_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_node_b = incremental_connect_and_rewire(tree_b, new_node_a.position)
            if new_node_b is not None:
                # Found connection
                path_from_start = new_node_a.path_from_root()
                path_from_goal = new_node_b.path_from_root()
                combined_path = path_from_start + path_from_goal[::-1][1:]  # avoid duplicate connect node

                total_cost = new_node_a.cost + new_node_b.cost
                if total_cost < best_cost:
                    best_cost = total_cost
                    best_path = combined_path
                    success = True

                # Attempt smoothing
                smoothed_path = path_smoothing(best_path, iterations=100)
                best_path = smoothed_path

                # Stop on first found improved solution
                break

            tree_a, tree_b = tree_b, tree_a

        return PlannerResult(
            success=success,
            path=best_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _within_bounds(self, pos, bounds):
        return all(0 <= pos[d] <= bounds[d] for d in range(len(bounds)))

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0, goal_sample_rate: float = 0.1, neighbor_radius: float = 15.0, max_time_sec: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius = neighbor_radius
        self.max_time_sec = max_time_sec

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        dimension = len(bounds)
        is_3d = (dimension == 3)

        start_time = time.perf_counter()

        nodes = []
        edges = []

        tree_start = [Node(start_position)]
        tree_goal = [Node(goal_position)]
        nodes.extend(tree_start)
        nodes.extend(tree_goal)

        success_state = False
        extracted_path: List[Tuple[float, ...]] = []
        best_path_cost = float('inf')
        best_path_nodes = (None, None)  # (node_in_start_tree, node_in_goal_tree)

        def sample_free():
            if random.random() < self.goal_sample_rate:
                return goal_position
            while True:
                p = tuple(random.uniform(0, bounds[d]) for d in range(dimension))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p

        def nearest(tree, point):
            return min(tree, key=lambda n: math.dist(n.position, point))

        def steer(from_pos, to_pos):
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dimension))

        def near_nodes(tree, position):
            return [node for node in tree if math.dist(node.position, position) <= self.neighbor_radius]

        def try_extend_and_rewire(tree, point):
            nearest_node = nearest(tree, point)
            new_pos = steer(nearest_node.position, point)

            if (
                not self._within_bounds(new_pos, bounds) or
                self._is_in_obstacle(new_pos, obstacles, is_3d) or
                self._is_edge_in_obstacle(nearest_node.position, new_pos, obstacles, is_3d)
            ):
                return None

            neighbors = near_nodes(tree, new_pos)

            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in neighbors:
                cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                    min_cost = cost
                    best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors if beneficial via the new node
            for neighbor in neighbors:
                cost_through_new = new_node.cost + math.dist(new_node.position, neighbor.position)
                if (
                    cost_through_new < neighbor.cost and
                    not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d)
                ):
                    neighbor.update_parent(new_node, cost_through_new)
                    edges.append((new_node, neighbor))
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass

            return new_node

        def try_connect_and_rewire(tree, new_node):
            # Incrementally extend tree towards new_node.position within step size and rewire
            target_pos = new_node.position
            current_node = nearest(tree, target_pos)

            while True:
                dist = math.dist(current_node.position, target_pos)
                if dist <= self.step_size:
                    new_pos = target_pos
                else:
                    new_pos = steer(current_node.position, target_pos)

                if (
                    not self._within_bounds(new_pos, bounds) or
                    self._is_in_obstacle(new_pos, obstacles, is_3d) or
                    self._is_edge_in_obstacle(current_node.position, new_pos, obstacles, is_3d)
                ):
                    return None

                neighbors = near_nodes(tree, new_pos)
                min_cost = current_node.cost + math.dist(current_node.position, new_pos)
                best_parent = current_node
                for neighbor in neighbors:
                    cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                    if cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                        min_cost = cost
                        best_parent = neighbor

                new_node_goal = Node(new_pos)
                best_parent.add_child(new_node_goal)
                new_node_goal.cost = min_cost
                tree.append(new_node_goal)
                nodes.append(new_node_goal)
                edges.append((best_parent, new_node_goal))

                # Rewire neighbors
                for neighbor in neighbors:
                    cost_through_new = new_node_goal.cost + math.dist(new_node_goal.position, neighbor.position)
                    if (
                        cost_through_new < neighbor.cost and
                        not self._is_edge_in_obstacle(new_node_goal.position, neighbor.position, obstacles, is_3d)
                    ):
                        neighbor.update_parent(new_node_goal, cost_through_new)
                        edges.append((new_node_goal, neighbor))
                        try:
                            edges.remove((neighbor.parent, neighbor))
                        except ValueError:
                            pass

                if new_node_goal.position == target_pos:
                    return new_node_goal
                current_node = new_node_goal

        def path_cost_sum(path_nodes: List[Node]):
            if not path_nodes:
                return float('inf')
            return path_nodes[-1].cost

        def merge_paths(node_start, node_goal):
            path_start = node_start.path_from_root()
            path_goal = node_goal.path_from_root()
            # Avoid duplicate middle node
            return path_start + path_goal[::-1][1:]

        for iter_num in range(self.max_iter):
            if time.perf_counter() - start_time > self.max_time_sec:
                # Time limit exceeded, extract best path so far if any
                if success_state:
                    extracted_path = merge_paths(best_path_nodes[0], best_path_nodes[1])
                break

            rnd_point = sample_free()

            new_node_start = try_extend_and_rewire(tree_start, rnd_point)
            if new_node_start is None:
                # Swap trees to maintain balanced growth
                tree_start, tree_goal = tree_goal, tree_start
                continue

            connect_node_goal = try_connect_and_rewire(tree_goal, new_node_start)
            if connect_node_goal:
                # Connected trees successfully
                total_cost = new_node_start.cost + connect_node_goal.cost
                if total_cost < best_path_cost:
                    best_path_cost = total_cost
                    best_path_nodes = (new_node_start, connect_node_goal)
                    extracted_path = merge_paths(new_node_start, connect_node_goal)
                    success_state = True
                # Swap trees to keep balanced exploration
                tree_start, tree_goal = tree_goal, tree_start
                # Early break for success to reduce planning time
                if success_state:
                    break
            else:
                # Swap trees for next iteration
                tree_start, tree_goal = tree_goal, tree_start

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

    def _within_bounds(self, pos, bounds):
        return all(0 <= pos[d] <= bounds[d] for d in range(len(bounds)))

