class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0, radius: float = 10.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.radius = radius

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        start_tree = [Node(start_position)]
        goal_tree = [Node(goal_position)]
        nodes = []
        edges = []
        success_state = False
        extracted_path = []

        for iter in range(self.max_iter):
            rand = self._sample_free(bounds, obstacles, is_3d)
            for tree_a, tree_b in [(start_tree, goal_tree), (goal_tree, start_tree)]:
                nearest = self._nearest(tree_a, rand)
                new_pos = self._steer(nearest.position, rand, self.step_size)
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    continue
                if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
                    continue

                new_node = Node(new_pos)
                near_nodes = self._near(tree_a, new_pos, self.radius)
                min_cost = nearest.cost + self._distance(nearest.position, new_pos)
                min_parent = nearest

                for near in near_nodes:
                    cost = near.cost + self._distance(near.position, new_pos)
                    if cost < min_cost and not self._is_edge_in_obstacle(near.position, new_pos, obstacles, is_3d):
                        min_cost = cost
                        min_parent = near

                new_node.parent = min_parent
                new_node.cost = min_cost
                min_parent.add_child(new_node)
                tree_a.append(new_node)
                nodes.append(new_node)
                edges.append((min_parent, new_node))

                for near in near_nodes:
                    if near == min_parent:
                        continue
                    new_cost = new_node.cost + self._distance(new_node.position, near.position)
                    if new_cost < near.cost and not self._is_edge_in_obstacle(new_node.position, near.position, obstacles, is_3d):
                        if near.parent:
                            edges.remove((near.parent, near))
                        near.parent = new_node
                        near.cost = new_cost
                        new_node.add_child(near)
                        edges.append((new_node, near))

                connect_node = self._nearest(tree_b, new_node.position)
                if self._distance(new_node.position, connect_node.position) < self.step_size and not self._is_edge_in_obstacle(new_node.position, connect_node.position, obstacles, is_3d):
                    success_state = True
                    extracted_path = self._extract_path(new_node, connect_node)
                    return PlannerResult(True, extracted_path, start_tree + goal_tree, edges)

        return PlannerResult(False, [], start_tree + goal_tree, edges)

    def _sample_free(self, bounds, obstacles, is_3d):
        while True:
            point = tuple(random.uniform(0, bounds[i]) for i in range(3 if is_3d else 2))
            if not self._is_in_obstacle(point, obstacles, is_3d):
                return point

    def _nearest(self, tree, point):
        return min(tree, key=lambda node: self._distance(node.position, point))

    def _near(self, tree, point, radius):
        return [node for node in tree if self._distance(node.position, point) <= radius]

    def _steer(self, from_pos, to_pos, step_size):
        dist = self._distance(from_pos, to_pos)
        if dist <= step_size:
            return to_pos
        ratio = step_size / dist
        return tuple(from_pos[i] + (to_pos[i] - from_pos[i]) * ratio for i in range(len(from_pos)))

    def _extract_path(self, node_a, node_b):
        path_a = []
        while node_a:
            path_a.append(node_a.position)
            node_a = node_a.parent
        path_b = []
        while node_b:
            path_b.append(node_b.position)
            node_b = node_b.parent
        return list(reversed(path_a)) + path_b

    def _distance(self, a, b):
        return math.dist(a, b)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = self._distance(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = None
        self.cost = cost
        self.children = []
        self.valid = True
        if parent:
            self.update_parent(parent, cost)

    def add_child(self, child_node):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float = 4.0,
                 goal_sample_rate: float = 0.15, neighbor_radius_min: float = 10.0,
                 neighbor_radius_max: float = 25.0, time_limit_sec: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.time_limit_sec = time_limit_sec

    def plan(self, map) -> 'PlannerResult':
        import time
        import math
        import random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        start_tree = [Node(start_pos, None, 0.0)]
        goal_tree = [Node(goal_pos, None, 0.0)]
        nodes = start_tree + goal_tree
        edges = []

        best_cost = math.inf
        best_connection = None
        extracted_path = []
        success_state = False

        def in_bounds(pos):
            return all(0 <= pos[d] <= bounds[d] for d in range(dim))

        def sample_free(tree_from, tree_to):
            # Goal bias toward opposite tree root
            if random.random() < self.goal_sample_rate:
                if tree_from is start_tree:
                    return goal_pos
                else:
                    return start_pos
            for _ in range(50):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p
            # fallback
            if tree_from is start_tree:
                return goal_pos
            else:
                return start_pos

        def steer(from_p, to_p):
            dist = math.dist(from_p, to_p)
            if dist <= self.step_size:
                return to_p
            ratio = self.step_size / dist
            return tuple(from_p[d] + (to_p[d] - from_p[d]) * ratio for d in range(dim))

        def nearest(tree, point):
            return min(tree, key=lambda n: math.dist(n.position, point))

        def near_nodes(tree, position, radius):
            radius_sq = radius * radius
            return [node for node in tree if (math.dist(node.position, position) ** 2) <= radius_sq]

        def adaptive_radius(n_nodes):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma = 50.0
            r = gamma * (math.log(n_nodes) / n_nodes) ** (1 / dim)
            return max(self.neighbor_radius_min, min(r, self.neighbor_radius_max))

        def try_extend(tree_from, sample):
            node_near = nearest(tree_from, sample)
            new_pos = steer(node_near.position, sample)
            if not in_bounds(new_pos):
                return None
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(node_near.position, new_pos, obstacles, is_3d):
                return None

            new_node = Node(new_pos)
            n_nodes = len(tree_from)
            radius = adaptive_radius(n_nodes)
            neighbors = near_nodes(tree_from, new_pos, radius)

            min_cost = node_near.cost + math.dist(node_near.position, new_pos)
            best_parent = node_near
            for neighbor in neighbors:
                cost_try = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost_try < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                    min_cost = cost_try
                    best_parent = neighbor

            best_parent.add_child(new_node)
            new_node.cost = min_cost

            tree_from.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for neighbor in neighbors:
                if neighbor == best_parent:
                    continue
                new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
                if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                            neighbor.parent.remove_child(neighbor)
                        except ValueError:
                            pass
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

            return new_node

        def try_connect(tree, node):
            node_near = nearest(tree, node.position)
            dist_to = math.dist(node_near.position, node.position)
            if dist_to <= self.step_size:
                if not self._is_in_obstacle(node.position, obstacles, is_3d) and \
                   not self._is_edge_in_obstacle(node_near.position, node.position, obstacles, is_3d):
                    return node_near
            return None

        def incremental_connect(tree, target_pos):
            curr = nearest(tree, target_pos)
            max_steps = int(math.dist(curr.position, target_pos) / self.step_size) + 2
            last_node = curr
            for _ in range(max_steps):
                new_pos = steer(last_node.position, target_pos)
                if not in_bounds(new_pos):
                    return None
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    return None
                if self._is_edge_in_obstacle(last_node.position, new_pos, obstacles, is_3d):
                    return None

                new_node = Node(new_pos)
                n_nodes = len(tree)
                radius = adaptive_radius(n_nodes)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = last_node.cost + math.dist(last_node.position, new_pos)
                best_parent = last_node
                for neighbor in neighbors:
                    cost_try = neighbor.cost + math.dist(neighbor.position, new_pos)
                    if cost_try < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                        min_cost = cost_try
                        best_parent = neighbor

                best_parent.add_child(new_node)
                new_node.cost = min_cost

                tree.append(new_node)
                nodes.append(new_node)
                edges.append((best_parent, new_node))

                for neighbor in neighbors:
                    if neighbor == best_parent:
                        continue
                    new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
                    if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                        if neighbor.parent:
                            try:
                                edges.remove((neighbor.parent, neighbor))
                                neighbor.parent.remove_child(neighbor)
                            except ValueError:
                                pass
                        neighbor.update_parent(new_node, new_cost)
                        edges.append((new_node, neighbor))

                last_node = new_node
                if math.dist(last_node.position, target_pos) <= self.step_size:
                    return last_node
            return None

        def path_smoothing(path, max_iters=100):
            if len(path) < 3:
                return path[:]
            length = len(path)
            for _ in range(max_iters):
                if length <= 2:
                    break
                i = random.randint(0, length - 3)
                j = random.randint(i + 2, length - 1)
                if not self._is_edge_in_obstacle(path[i], path[j], obstacles, is_3d):
                    path = path[:i + 1] + path[j:]
                    length = len(path)
            return path

        import time
        start_time = time.time()
        tree_a, tree_b = start_tree, goal_tree

        for _ in range(self.max_iter):
            if (time.time() - start_time) > self.time_limit_sec:
                break

            sample = sample_free(tree_a, tree_b)
            new_node_a = try_extend(tree_a, sample)
            if new_node_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_node_b = incremental_connect(tree_b, new_node_a.position)
            if new_node_b:
                total_cost = new_node_a.cost + new_node_b.cost + math.dist(new_node_a.position, new_node_b.position)
                if total_cost < best_cost:
                    best_cost = total_cost
                    best_connection = (new_node_a, new_node_b)
                    path_a = new_node_a.path_from_root()
                    path_b = new_node_b.path_from_root()
                    combined = path_a + path_b[::-1]
                    extracted_path = path_smoothing(combined)
                    success_state = True
                    # Optional early break for speed:
                    break

            tree_a, tree_b = tree_b, tree_a

        if not success_state and best_connection is not None:
            node_a, node_b = best_connection
            path_a = node_a.path_from_root()
            path_b = node_b.path_from_root()
            combined = path_a + path_b[::-1]
            extracted_path = path_smoothing(combined)
            success_state = True

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = parent            # Node or None
        self.cost = cost                # Accumulated path cost from root
        self.children = []
        self.valid = True

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 8000, step_size: float=5.0,
                 goal_sample_rate: float=0.15, neighbor_radius: float=12.0,
                 max_time_sec: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius = neighbor_radius
        self.max_time_sec = max_time_sec

    def plan(self, map) -> 'PlannerResult':
        import time
        import math
        import random
        from collections import defaultdict

        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles

        dimension = len(bounds)
        is_3d = (dimension == 3)

        start_time = time.perf_counter()

        # Spatial hashing grid for efficient neighbor search
        cell_size = self.neighbor_radius
        def grid_key(pos):
            return tuple(int(p // cell_size) for p in pos)

        grid_nodes_start = defaultdict(list)
        grid_nodes_goal = defaultdict(list)

        def insert_node_to_grid(grid, node):
            k = grid_key(node.position)
            grid[k].append(node)

        def nearby_nodes_grid(grid, pos):
            base = grid_key(pos)
            neighbors = []
            # Search neighbors in adjacent cells only
            for dx in (-1,0,1):
                for dy in (-1,0,1):
                    for dz in ([0] if dimension==2 else (-1,0,1)):
                        if dimension ==2 and dz!=0:
                            continue
                        neighbor_cell = tuple(base[d]+(dx if d==0 else dy if d==1 else dz) for d in range(dimension))
                        neighbors.extend(grid.get(neighbor_cell, []))
            return neighbors

        # Core data
        success_state = False
        extracted_path = []
        best_path_cost = float('inf')
        best_path_nodes = (None, None)  # (node_in_start_tree, node_in_goal_tree)

        tree_start = [Node(start_position, cost=0.0)]
        tree_goal = [Node(goal_position, cost=0.0)]

        for n in tree_start: insert_node_to_grid(grid_nodes_start, n)
        for n in tree_goal: insert_node_to_grid(grid_nodes_goal, n)

        def sample_free():
            # Adaptive goal biasing: slightly increase goal bias if already found a better path
            rate = self.goal_sample_rate if not success_state else min(0.4, self.goal_sample_rate + 0.1)
            if random.random() < rate:
                return goal_position
            while True:
                p = tuple(random.uniform(0, bounds[d]) for d in range(dimension))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p

        def nearest(tree, point):
            # Use spatial hashing grid for efficient search
            grid_tree = grid_nodes_start if tree is tree_start else grid_nodes_goal
            candidates = nearby_nodes_grid(grid_tree, point)
            if not candidates:
                return min(tree, key=lambda n: math.dist(n.position, point))
            return min(candidates, key=lambda n: math.dist(n.position, point))

        def steer(from_pos, to_pos):
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dimension))

        def near_nodes(tree, pos):
            grid_tree = grid_nodes_start if tree is tree_start else grid_nodes_goal
            neighbors = []
            candidates = nearby_nodes_grid(grid_tree, pos)
            radius_sq = self.neighbor_radius * self.neighbor_radius
            neighbors = [n for n in candidates if (self._dist_sq(n.position, pos) <= radius_sq)]
            return neighbors

        def try_extend_and_rewire(tree, grid_tree, point):
            nearest_node = nearest(tree, point)
            new_pos = steer(nearest_node.position, point)

            if not self._within_bounds(new_pos, bounds):
                return None
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(nearest_node.position, new_pos, obstacles, is_3d):
                return None

            neighbors = near_nodes(tree, new_pos)
            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in neighbors:
                cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost < min_cost:
                    if not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                        min_cost = cost
                        best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            insert_node_to_grid(grid_tree, new_node)

            # Rewire neighbors if beneficial
            for neighbor in neighbors:
                cost_through_new = new_node.cost + math.dist(new_node.position, neighbor.position)
                if cost_through_new < neighbor.cost:
                    if not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                        neighbor.update_parent(new_node, cost_through_new)

            return new_node

        def try_connect_and_rewire(tree, grid_tree, new_node):
            # Incrementally extend tree toward new_node.position with rewiring
            target_pos = new_node.position
            current_node = nearest(tree, target_pos)

            while True:
                dist = math.dist(current_node.position, target_pos)
                if dist <= self.step_size:
                    new_pos = target_pos
                else:
                    new_pos = steer(current_node.position, target_pos)

                if not self._within_bounds(new_pos, bounds):
                    return None
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    return None
                if self._is_edge_in_obstacle(current_node.position, new_pos, obstacles, is_3d):
                    return None

                neighbors = near_nodes(tree, new_pos)
                min_cost = current_node.cost + math.dist(current_node.position, new_pos)
                best_parent = current_node
                for neighbor in neighbors:
                    cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                    if cost < min_cost:
                        if not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                            min_cost = cost
                            best_parent = neighbor

                new_node_goal = Node(new_pos)
                best_parent.add_child(new_node_goal)
                new_node_goal.cost = min_cost
                tree.append(new_node_goal)
                insert_node_to_grid(grid_tree, new_node_goal)

                # Rewire neighbors
                for neighbor in neighbors:
                    cost_through_new = new_node_goal.cost + math.dist(new_node_goal.position, neighbor.position)
                    if cost_through_new < neighbor.cost:
                        if not self._is_edge_in_obstacle(new_node_goal.position, neighbor.position, obstacles, is_3d):
                            neighbor.update_parent(new_node_goal, cost_through_new)

                if new_node_goal.position == target_pos:
                    return new_node_goal
                current_node = new_node_goal

        def merge_paths(n_start, n_goal):
            path_start = n_start.path_from_root()
            path_goal = n_goal.path_from_root()
            return path_start + path_goal[::-1][1:]  # avoid duplicate node

        for iter_num in range(self.max_iter):
            if time.perf_counter() - start_time > self.max_time_sec:
                if success_state:
                    extracted_path = merge_paths(best_path_nodes[0], best_path_nodes[1])
                break

            rnd_point = sample_free()

            new_node_start = try_extend_and_rewire(tree_start, grid_nodes_start, rnd_point)
            if new_node_start is None:
                tree_start, tree_goal = tree_goal, tree_start
                grid_nodes_start, grid_nodes_goal = grid_nodes_goal, grid_nodes_start
                continue

            connect_node_goal = try_connect_and_rewire(tree_goal, grid_nodes_goal, new_node_start)
            if connect_node_goal:
                total_cost = new_node_start.cost + connect_node_goal.cost
                if total_cost < best_path_cost:
                    best_path_cost = total_cost
                    best_path_nodes = (new_node_start, connect_node_goal)
                    extracted_path = merge_paths(new_node_start, connect_node_goal)
                    success_state = True
                tree_start, tree_goal = tree_goal, tree_start
                grid_nodes_start, grid_nodes_goal = grid_nodes_goal, grid_nodes_start
                if success_state:
                    break
            else:
                tree_start, tree_goal = tree_goal, tree_start
                grid_nodes_start, grid_nodes_goal = grid_nodes_goal, grid_nodes_start

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=tree_start+tree_goal,
            edges=self._extract_edges(tree_start+tree_goal)
        )

    def _dist_sq(self, p1, p2):
        return sum((a-b)**2 for a,b in zip(p1,p2))

    def _extract_edges(self, nodes):
        edges = []
        for node in nodes:
            for c in node.children:
                edges.append((node, c))
        return edges

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

    def _within_bounds(self, pos, bounds):
        return all(0 <= pos[d] <= bounds[d] for d in range(len(bounds)))

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position          # Tuple[float, ...]
        self.parent = parent              # Node or None
        self.cost = cost                  # Path cost from root
        self.children = []
        self.valid = True                 # Collision status

        if parent:
            self.parent.add_child(self)

    def add_child(self, child):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            self.parent.remove_child(self)
        new_parent.add_child(self)
        self.cost = new_cost
        # Propagate cost update to children
        for child in self.children:
            dist_to_child = math.dist(self.position, child.position)
            child.update_parent(self, self.cost + dist_to_child)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 goal_sample_rate: float=0.15, neighbor_radius: float=15.0,
                 max_time_sec: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius = neighbor_radius
        self.max_time_sec = max_time_sec

    def plan(self, map) -> 'PlannerResult':
        import time
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def within_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def nearest(tree, point):
            return min(tree, key=lambda n: dist(n.position, point))

        def near_nodes(tree, position):
            radius = self.neighbor_radius
            return [n for n in tree if dist(n.position, position) <= radius]

        def collision_free_node(pos):
            return within_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(from_pos, to_pos):
            return not self._is_edge_in_obstacle(from_pos, to_pos, obstacles, is_3d, resolution=self.step_size/5)

        c_min = dist(start_pos, goal_pos)
        best_path_cost = math.inf
        best_path_nodes = []

        tree_start = [Node(start_pos)]
        tree_goal = [Node(goal_pos)]
        all_nodes = tree_start + tree_goal
        edges = []

        start_time = time.perf_counter()
        grow_start = True
        informed_sampling_enabled = False

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a = math.cos(angle)
                sin_a = math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                k = (-0.0, -vy, -vz)
                k_norm = math.sqrt(k[0]**2 + k[1]**2 + k[2]**2)
                if k_norm < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                k = tuple(ki / k_norm for ki in k)
                cos_theta = vx
                sin_theta = k_norm
                Kx, Ky, Kz = k
                K = [[0, -Kz, Ky], [Kz, 0, -Kx], [-Ky, Kx, 0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A,B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A,s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A,B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K,K)
                R = mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))
                return R

        def clamp_to_bounds(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(pt, obstacles, is_3d):
                    return pt

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-8:
                return sample_free()
            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best**2 - c_min**2, 0.0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best**2 - c_min**2, 0.0)) / 2.0
                radii = [a1, a_minor, a_minor]

            def unit_ball_sample(n):
                while True:
                    pt = [random.uniform(-1,1) for _ in range(n)]
                    norm_sq = sum(x*x for x in pt)
                    if norm_sq <= 1.0 and norm_sq > 1e-10:
                        norm = math.sqrt(norm_sq)
                        return tuple(x / norm for x in pt)

            while True:
                n = dim
                direction_sample = unit_ball_sample(n)
                radius_sample = random.uniform(0,1) ** (1.0/n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    sample_rot = (x_rot, y_rot)
                else:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1] + R[0][2]*sample_scaled[2]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1] + R[1][2]*sample_scaled[2]
                    z_rot = R[2][0]*sample_scaled[0] + R[2][1]*sample_scaled[1] + R[2][2]*sample_scaled[2]
                    sample_rot = (x_rot, y_rot, z_rot)

                sample_point = tuple(center[d] + sample_rot[d] for d in range(n))
                sample_point = clamp_to_bounds(sample_point)
                if not self._is_in_obstacle(sample_point, obstacles, is_3d):
                    return sample_point

        def rewire(tree, new_node, neighbors):
            for neighbor in neighbors:
                if neighbor is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_through_new < neighbor.cost and collision_free_edge(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except Exception:
                        pass
                    neighbor.update_parent(new_node, cost_through_new)
                    edges.append((new_node, neighbor))

        def extend(tree, target_point):
            nearest_node = nearest(tree, target_point)
            new_pos = steer(nearest_node.position, target_point)
            new_pos = clamp_to_bounds(new_pos)

            if not collision_free_node(new_pos):
                return None
            if not collision_free_edge(nearest_node.position, new_pos):
                return None

            neighbors = near_nodes(tree, new_pos)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in neighbors:
                tentative_cost = neighbor.cost + dist(neighbor.position, new_pos)
                if tentative_cost < min_cost and collision_free_edge(neighbor.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            all_nodes.append(new_node)
            edges.append((best_parent, new_node))

            rewire(tree, new_node, neighbors)
            return new_node

        def try_connect(tree, from_node):
            nearest_node = nearest(tree, from_node.position)
            d_curr = dist(nearest_node.position, from_node.position)
            if d_curr <= self.step_size and collision_free_node(from_node.position) and collision_free_edge(nearest_node.position, from_node.position):
                return nearest_node
            return None

        while True:
            iter_count = 0
            while iter_count < self.max_iter:
                elapsed = time.perf_counter() - start_time
                if elapsed > self.max_time_sec:
                    # Time limit reached - return best path found so far
                    if best_path_nodes:
                        return PlannerResult(success=True, path=best_path_nodes, nodes=all_nodes, edges=edges)
                    return PlannerResult(success=False, path=[], nodes=all_nodes, edges=edges)

                iter_count += 1
                if informed_sampling_enabled:
                    sample = informed_sample(best_path_cost)
                else:
                    if random.random() < self.goal_sample_rate:
                        sample = goal_pos if grow_start else start_pos
                    else:
                        sample = sample_free()

                if grow_start:
                    new_node = extend(tree_start, sample)
                    if new_node is None:
                        grow_start = not grow_start
                        continue

                    connect_node = try_connect(tree_goal, new_node)
                    if connect_node:
                        neighbors = near_nodes(tree_goal, new_node.position)
                        min_cost_local = connect_node.cost + dist(connect_node.position, new_node.position)
                        best_parent_local = connect_node
                        for neighbor in neighbors:
                            tentative_cost = neighbor.cost + dist(neighbor.position, new_node.position)
                            if tentative_cost < min_cost_local and collision_free_edge(neighbor.position, new_node.position):
                                min_cost_local = tentative_cost
                                best_parent_local = neighbor

                        connecting_node = Node(new_node.position)
                        best_parent_local.add_child(connecting_node)
                        connecting_node.cost = min_cost_local
                        tree_goal.append(connecting_node)
                        all_nodes.append(connecting_node)
                        edges.append((best_parent_local, connecting_node))

                        rewire(tree_goal, connecting_node, neighbors)

                        path_start = new_node.path_from_root()
                        path_goal = connecting_node.path_from_root()
                        merged_path = path_start + path_goal[::-1][1:]
                        cost_path = 0.0
                        for i in range(len(merged_path)-1):
                            cost_path += dist(merged_path[i], merged_path[i+1])
                        if cost_path < best_path_cost:
                            best_path_cost = cost_path
                            best_path_nodes = merged_path

                        informed_sampling_enabled = True
                        return PlannerResult(success=True, path=merged_path, nodes=all_nodes, edges=edges)
                else:
                    new_node = extend(tree_goal, sample)
                    if new_node is None:
                        grow_start = not grow_start
                        continue

                    connect_node = try_connect(tree_start, new_node)
                    if connect_node:
                        neighbors = near_nodes(tree_start, new_node.position)
                        min_cost_local = connect_node.cost + dist(connect_node.position, new_node.position)
                        best_parent_local = connect_node
                        for neighbor in neighbors:
                            tentative_cost = neighbor.cost + dist(neighbor.position, new_node.position)
                            if tentative_cost < min_cost_local and collision_free_edge(neighbor.position, new_node.position):
                                min_cost_local = tentative_cost
                                best_parent_local = neighbor

                        connecting_node = Node(new_node.position)
                        best_parent_local.add_child(connecting_node)
                        connecting_node.cost = min_cost_local
                        tree_start.append(connecting_node)
                        all_nodes.append(connecting_node)
                        edges.append((best_parent_local, connecting_node))

                        rewire(tree_start, connecting_node, neighbors)

                        path_start = connecting_node.path_from_root()
                        path_goal = new_node.path_from_root()
                        merged_path = path_start + path_goal[::-1][1:]
                        cost_path = 0.0
                        for i in range(len(merged_path)-1):
                            cost_path += dist(merged_path[i], merged_path[i+1])
                        if cost_path < best_path_cost:
                            best_path_cost = cost_path
                            best_path_nodes = merged_path

                        informed_sampling_enabled = True
                        return PlannerResult(success=True, path=merged_path, nodes=all_nodes, edges=edges)

                grow_start = not grow_start

            break

        # If max iterations reached without success but path found
        if best_path_nodes:
            return PlannerResult(success=True, path=best_path_nodes, nodes=all_nodes, edges=edges)
        return PlannerResult(success=False, path=[], nodes=all_nodes, edges=edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        for child in self.children:
            child.update_parent(self, self.cost + math.dist(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float=3.0, goal_sample_rate: float=0.15,
                 min_neighbor_radius: float=5.0, max_neighbor_radius: float=25.0, max_time_sec: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.max_time_sec = max_time_sec

    def plan(self, map) -> 'PlannerResult':
        import random
        import time
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        is_3d = len(bounds) == 3
        dim = len(bounds)

        def _within_bounds(pos):
            return all(0 <= pos[d] <= bounds[d] for d in range(dim))

        def _is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w, h = obs
                    px, py = pos
                    if x <= px <= x + w and y <= py <= y + h:
                        return True
            return False

        def _is_edge_in_obstacle(from_pos, to_pos, resolution=1.0):
            dist = math.dist(from_pos, to_pos)
            steps = max(1, int(dist / resolution))
            for i in range(steps + 1):
                interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(dim))
                if _is_in_obstacle(interp):
                    return True
            return False

        def sample_free(tree_indicator):
            # tree_indicator: True for start_tree sampling towards goal, False for goal_tree sampling towards start
            if random.random() < self.goal_sample_rate:
                return goal_pos if tree_indicator else start_pos
            for _ in range(30):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if _within_bounds(p) and not _is_in_obstacle(p):
                    return p
            return goal_pos if tree_indicator else start_pos

        def steer(from_p, to_p):
            dist = math.dist(from_p, to_p)
            if dist <= self.step_size:
                return to_p
            ratio = self.step_size / dist
            return tuple(from_p[d] + (to_p[d] - from_p[d]) * ratio for d in range(dim))

        def near_nodes(tree, pos, radius):
            r_sq = radius * radius
            return [n for n in tree if (math.dist(n.position, pos) ** 2) <= r_sq]

        def adaptive_neighbor_radius(n_nodes):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            gamma = 25.0
            radius = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.min_neighbor_radius, min(radius, self.max_neighbor_radius))

        def add_node(tree, pos, parent_node, cost):
            new_node = Node(pos)
            parent_node.add_child(new_node)
            new_node.cost = cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((parent_node, new_node))
            return new_node

        def rewire(tree, new_node, neighbors):
            for neighbor in neighbors:
                if neighbor is new_node.parent:
                    continue
                dist = math.dist(new_node.position, neighbor.position)
                new_cost = new_node.cost + dist
                if new_cost < neighbor.cost and not _is_edge_in_obstacle(new_node.position, neighbor.position):
                    if neighbor.parent is not None:
                        if (neighbor.parent, neighbor) in edges:
                            edges.remove((neighbor.parent, neighbor))
                        neighbor.parent.remove_child(neighbor)
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

        def extend_and_rewire(tree, sample, tree_indicator):
            if len(tree) == 0:
                return None
            nearest = min(tree, key=lambda node: math.dist(node.position, sample))
            new_pos = steer(nearest.position, sample)
            if not _within_bounds(new_pos) or _is_in_obstacle(new_pos):
                return None
            if _is_edge_in_obstacle(nearest.position, new_pos):
                return None
            radius = adaptive_neighbor_radius(len(tree))
            neighbors = near_nodes(tree, new_pos, radius)
            min_cost = nearest.cost + math.dist(nearest.position, new_pos)
            best_parent = nearest
            for neighbor in neighbors:
                cost_through = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost_through < min_cost and not _is_edge_in_obstacle(neighbor.position, new_pos):
                    min_cost = cost_through
                    best_parent = neighbor
            new_node = add_node(tree, new_pos, best_parent, min_cost)
            rewire(tree, new_node, neighbors)
            return new_node

        def incremental_connect(tree, target_pos):
            if len(tree) == 0:
                return None
            nearest = min(tree, key=lambda node: math.dist(node.position, target_pos))
            curr_node = nearest
            while True:
                new_pos = steer(curr_node.position, target_pos)
                if not _within_bounds(new_pos) or _is_in_obstacle(new_pos) or _is_edge_in_obstacle(curr_node.position, new_pos):
                    return None
                radius = adaptive_neighbor_radius(len(tree))
                neighbors = near_nodes(tree, new_pos, radius)
                min_cost = curr_node.cost + math.dist(curr_node.position, new_pos)
                best_parent = curr_node
                for neighbor in neighbors:
                    cost_through = neighbor.cost + math.dist(neighbor.position, new_pos)
                    if cost_through < min_cost and not _is_edge_in_obstacle(neighbor.position, new_pos):
                        min_cost = cost_through
                        best_parent = neighbor
                new_node = add_node(tree, new_pos, best_parent, min_cost)
                rewire(tree, new_node, neighbors)
                if math.dist(new_node.position, target_pos) <= self.step_size:
                    return new_node
                curr_node = new_node

        def path_smoothing(path, iterations=100):
            if len(path) <= 2:
                return path[:]
            p = path[:]
            for _ in range(iterations):
                if len(p) <= 2:
                    break
                i = random.randint(0, len(p) - 2)
                j = random.randint(i + 1, len(p) - 1)
                if j == i + 1:
                    continue
                if not _is_edge_in_obstacle(p[i], p[j]):
                    p = p[:i + 1] + p[j:]
            return p

        start_time = time.time()

        start_tree = [Node(start_pos)]
        goal_tree = [Node(goal_pos)]
        nodes = start_tree[:] + goal_tree[:]
        edges = []

        tree_a = start_tree
        tree_b = goal_tree
        success = False
        best_path = []
        best_cost = math.inf

        for iter_cnt in range(self.max_iter):
            if time.time() - start_time > self.max_time_sec:
                break  # timeout

            sample = sample_free(tree_indicator=True)
            new_node_a = extend_and_rewire(tree_a, sample, True)
            if new_node_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_node_b = incremental_connect(tree_b, new_node_a.position)
            if new_node_b is not None:
                path_from_start = new_node_a.path_from_root()
                path_from_goal = new_node_b.path_from_root()
                combined_path = path_from_start + path_from_goal[::-1][1:]
                total_cost = new_node_a.cost + new_node_b.cost
                if total_cost < best_cost:
                    best_cost = total_cost
                    best_path = path_smoothing(combined_path, iterations=100)
                    success = True
                break

            tree_a, tree_b = tree_b, tree_a

        # If timeout but no path, try extracting best known path
        if not success and best_path:
            best_path = path_smoothing(best_path, iterations=100)

        return PlannerResult(
            success=success,
            path=best_path,
            nodes=nodes,
            edges=edges
        )

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = None
        self.cost = cost
        self.children = []
        self.valid = True
        if parent is not None:
            self.update_parent(parent, cost)

    def add_child(self, child_node):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float = 4.0,
                 goal_sample_rate: float = 0.15, neighbor_radius_min: float = 10.0,
                 neighbor_radius_max: float = 25.0, time_limit_sec: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.time_limit_sec = time_limit_sec

    def plan(self, map) -> 'PlannerResult':
        import time
        import math
        import random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        start_tree = [Node(start_pos, None, 0.0)]
        goal_tree = [Node(goal_pos, None, 0.0)]
        nodes = start_tree + goal_tree
        edges = []

        best_cost = math.inf
        best_connection = None
        extracted_path = []
        success_state = False

        def in_bounds(pos):
            return all(0 <= pos[d] <= bounds[d] for d in range(dim))

        def sample_free(tree_from, tree_to):
            if random.random() < self.goal_sample_rate:
                return tree_to[0].position
            for _ in range(50):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p
            return tree_to[0].position

        def steer(from_p, to_p):
            dist = math.dist(from_p, to_p)
            if dist <= self.step_size:
                return to_p
            ratio = self.step_size / dist
            return tuple(from_p[d] + (to_p[d] - from_p[d]) * ratio for d in range(dim))

        def nearest(tree, point):
            return min(tree, key=lambda n: math.dist(n.position, point))

        def near_nodes(tree, position, radius):
            radius_sq = radius * radius
            return [node for node in tree if (math.dist(node.position, position) ** 2) <= radius_sq]

        def adaptive_radius(n_nodes):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma = 50.0
            r = gamma * (math.log(n_nodes) / n_nodes) ** (1 / dim)
            return max(self.neighbor_radius_min, min(r, self.neighbor_radius_max))

        def try_extend(tree_from, sample):
            node_near = nearest(tree_from, sample)
            new_pos = steer(node_near.position, sample)
            if not in_bounds(new_pos):
                return None
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(node_near.position, new_pos, obstacles, is_3d):
                return None

            new_node = Node(new_pos)
            n_nodes = len(tree_from)
            radius = adaptive_radius(n_nodes)
            neighbors = near_nodes(tree_from, new_pos, radius)

            min_cost = node_near.cost + math.dist(node_near.position, new_pos)
            best_parent = node_near
            for neighbor in neighbors:
                cost_try = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost_try < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                    min_cost = cost_try
                    best_parent = neighbor

            best_parent.add_child(new_node)
            new_node.cost = min_cost

            tree_from.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for neighbor in neighbors:
                if neighbor == best_parent:
                    continue
                new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
                if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                            neighbor.parent.remove_child(neighbor)
                        except ValueError:
                            pass
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

            return new_node

        def incremental_connect(tree, target_pos):
            curr = nearest(tree, target_pos)
            max_steps = int(math.dist(curr.position, target_pos) / self.step_size) + 2
            last_node = curr
            for _ in range(max_steps):
                new_pos = steer(last_node.position, target_pos)
                if not in_bounds(new_pos):
                    return None
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    return None
                if self._is_edge_in_obstacle(last_node.position, new_pos, obstacles, is_3d):
                    return None

                new_node = Node(new_pos)
                n_nodes = len(tree)
                radius = adaptive_radius(n_nodes)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = last_node.cost + math.dist(last_node.position, new_pos)
                best_parent = last_node
                for neighbor in neighbors:
                    cost_try = neighbor.cost + math.dist(neighbor.position, new_pos)
                    if cost_try < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                        min_cost = cost_try
                        best_parent = neighbor

                best_parent.add_child(new_node)
                new_node.cost = min_cost

                tree.append(new_node)
                nodes.append(new_node)
                edges.append((best_parent, new_node))

                for neighbor in neighbors:
                    if neighbor == best_parent:
                        continue
                    new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
                    if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                        if neighbor.parent:
                            try:
                                edges.remove((neighbor.parent, neighbor))
                                neighbor.parent.remove_child(neighbor)
                            except ValueError:
                                pass
                        neighbor.update_parent(new_node, new_cost)
                        edges.append((new_node, neighbor))

                last_node = new_node
                if math.dist(last_node.position, target_pos) <= self.step_size:
                    return last_node
            return None

        def path_smoothing(path, max_iters=100):
            if len(path) < 3:
                return path[:]
            length = len(path)
            for _ in range(max_iters):
                if length <= 2:
                    break
                i = random.randint(0, length - 3)
                j = random.randint(i + 2, length - 1)
                if not self._is_edge_in_obstacle(path[i], path[j], obstacles, is_3d):
                    path = path[:i + 1] + path[j:]
                    length = len(path)
            return path

        start_time = time.time()
        tree_a, tree_b = start_tree, goal_tree

        for _ in range(self.max_iter):
            if (time.time() - start_time) > self.time_limit_sec:
                break

            sample = sample_free(tree_a, tree_b)
            new_node_a = try_extend(tree_a, sample)
            if new_node_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_node_b = incremental_connect(tree_b, new_node_a.position)
            if new_node_b:
                total_cost = new_node_a.cost + new_node_b.cost + math.dist(new_node_a.position, new_node_b.position)
                if total_cost < best_cost:
                    best_cost = total_cost
                    best_connection = (new_node_a, new_node_b)
                    path_a = new_node_a.path_from_root()
                    path_b = new_node_b.path_from_root()
                    combined = path_a + path_b[::-1]
                    extracted_path = path_smoothing(combined)
                    success_state = True
                    break  # Early termination on successful connection

            tree_a, tree_b = tree_b, tree_a

        if not success_state and best_connection is not None:
            node_a, node_b = best_connection
            path_a = node_a.path_from_root()
            path_b = node_b.path_from_root()
            combined = path_a + path_b[::-1]
            extracted_path = path_smoothing(combined)
            success_state = True

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # Collision status

        if parent:
            parent.children.append(self)

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)
        # Update costs recursively for descendants
        for child in self.children:
            dist_child = math.dist(self.position, child.position)
            child.update_parent(self, new_cost + dist_child)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0, goal_sample_rate: float = 0.1, neighbor_radius: float = 15.0, max_time_sec: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius = neighbor_radius
        self.max_time_sec = max_time_sec

    def plan(self, map) -> 'PlannerResult':
        import time
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def within_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dim))

        def nearest(tree, point):
            return min(tree, key=lambda n: math.dist(n.position, point))

        def near_nodes(tree, position):
            radius = self.neighbor_radius
            return [n for n in tree if math.dist(n.position, position) <= radius]

        def collision_free_node(pos):
            return within_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(from_pos, to_pos):
            return not self._is_edge_in_obstacle(from_pos, to_pos, obstacles, is_3d, resolution=self.step_size / 5)

        def sample_free():
            if random.random() < self.goal_sample_rate:
                return goal_pos
            while True:
                sample = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(sample, obstacles, is_3d):
                    return sample

        tree_start = [Node(start_pos)]
        tree_goal = [Node(goal_pos)]
        nodes = tree_start + tree_goal
        edges = []
        best_path_cost = float('inf')
        best_path_nodes = (None, None)
        success = False

        start_time = time.perf_counter()
        grow_start = True

        for _ in range(self.max_iter):
            if time.perf_counter() - start_time > self.max_time_sec:
                break

            sample = sample_free()
            current_tree = tree_start if grow_start else tree_goal
            other_tree = tree_goal if grow_start else tree_start

            nearest_node = nearest(current_tree, sample)
            new_pos = steer(nearest_node.position, sample)

            if not collision_free_node(new_pos) or not collision_free_edge(nearest_node.position, new_pos):
                grow_start = not grow_start
                continue

            neighbors = near_nodes(current_tree, new_pos)
            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node

            for neighbor in neighbors:
                tentative_cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                if tentative_cost < min_cost and collision_free_edge(neighbor.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.children.append(new_node)
            new_node.parent = best_parent
            new_node.cost = min_cost
            current_tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for neighbor in neighbors:
                cost_through_new = new_node.cost + math.dist(new_node.position, neighbor.position)
                if cost_through_new < neighbor.cost and collision_free_edge(new_node.position, neighbor.position):
                    neighbor.update_parent(new_node, cost_through_new)
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    edges.append((new_node, neighbor))

            # Try to connect other tree to new node
            nearest_other = nearest(other_tree, new_node.position)
            dist_to_other = math.dist(nearest_other.position, new_node.position)

            if dist_to_other <= self.step_size and collision_free_node(new_node.position) and collision_free_edge(nearest_other.position, new_node.position):
                neighbors_other = near_nodes(other_tree, new_node.position)
                min_cost_other = nearest_other.cost + dist_to_other
                best_parent_other = nearest_other

                for neighbor in neighbors_other:
                    tentative_cost = neighbor.cost + math.dist(neighbor.position, new_node.position)
                    if tentative_cost < min_cost_other and collision_free_edge(neighbor.position, new_node.position):
                        min_cost_other = tentative_cost
                        best_parent_other = neighbor

                connecting_node = Node(new_node.position)
                best_parent_other.children.append(connecting_node)
                connecting_node.parent = best_parent_other
                connecting_node.cost = min_cost_other
                other_tree.append(connecting_node)
                nodes.append(connecting_node)
                edges.append((best_parent_other, connecting_node))

                # Rewire neighbors in other tree
                for neighbor in neighbors_other:
                    cost_through_conn = connecting_node.cost + math.dist(connecting_node.position, neighbor.position)
                    if cost_through_conn < neighbor.cost and collision_free_edge(connecting_node.position, neighbor.position):
                        neighbor.update_parent(connecting_node, cost_through_conn)
                        try:
                            edges.remove((neighbor.parent, neighbor))
                        except ValueError:
                            pass
                        edges.append((connecting_node, neighbor))

                # Construct full path
                path_start = new_node.path_from_root() if grow_start else connecting_node.path_from_root()
                path_goal = connecting_node.path_from_root() if grow_start else new_node.path_from_root()
                merged_path = path_start + path_goal[::-1][1:]
                cost_path = 0.0
                for i in range(len(merged_path) - 1):
                    cost_path += math.dist(merged_path[i], merged_path[i+1])

                if cost_path < best_path_cost:
                    best_path_cost = cost_path
                    best_path_nodes = (new_node if grow_start else connecting_node, connecting_node if grow_start else new_node)
                    extracted_path = merged_path
                    success = True

                # Early exit on connection
                break

            grow_start = not grow_start

        return type('PlannerResult', (), {})() if not hasattr(self, '_is_in_obstacle') else PlannerResult(
            success=success,
            path=extracted_path if success else [],
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position            # Tuple[float, ...]
        self.parent = None                  # Node or None
        self.cost = cost                   # Cost from root
        self.children = []
        self.valid = True                  # For collision status

        if parent:
            self.update_parent(parent, cost)

    def add_child(self, child):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        # Propagate cost update recursively to children
        for child in self.children:
            dist_to_child = math.dist(self.position, child.position)
            child.update_parent(self, self.cost + dist_to_child)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int=10000, step_size: float=4.0, 
                 goal_sample_rate: float=0.15, min_neighbor_radius: float=5.0, 
                 max_neighbor_radius: float=25.0, max_time_sec: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.max_time_sec = max_time_sec

    def plan(self, map) -> 'PlannerResult':
        import time
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)

        def within_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_p, to_p):
            dist_ = math.dist(from_p, to_p)
            if dist_ <= self.step_size:
                return to_p
            ratio = self.step_size / dist_
            return tuple(from_p[d] + (to_p[d] - from_p[d]) * ratio for d in range(dim))

        def clamp_to_bounds(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def _is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x, y, z, w, h, d = obs
                    px, py, pz = pos
                    if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                        return True
                else:
                    x, y, w, h = obs
                    px, py = pos
                    if x <= px <= x + w and y <= py <= y + h:
                        return True
            return False

        def _is_edge_in_obstacle(from_pos, to_pos, resolution=0.5):
            dist_ = math.dist(from_pos, to_pos)
            steps = max(1, int(dist_ / resolution))
            for i in range(steps + 1):
                interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(dim))
                if _is_in_obstacle(interp):
                    return True
            return False

        def adaptive_neighbor_radius(n_nodes):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            gamma = 25.0
            radius = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.min_neighbor_radius, min(radius, self.max_neighbor_radius))

        # Ellipsoidal informed sampling once a solution is found
        c_min = math.dist(start_pos, goal_pos)
        best_cost = math.inf
        informed_sampling_enabled = False

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a = math.cos(angle)
                sin_a = math.sin(angle)
                return [[cos_a, -sin_a],[sin_a, cos_a]]
            else:
                # Rodrigues' formula for rotation matrix to align x-axis with direction
                vx, vy, vz = direction
                k = (-0.0, -vy, -vz)
                k_norm = math.sqrt(k[0]**2 + k[1]**2 + k[2]**2)
                if k_norm < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                k = tuple(ki / k_norm for ki in k)
                cos_theta = vx
                sin_theta = k_norm
                Kx, Ky, Kz = k
                K = [[ 0, -Kz, Ky],
                     [ Kz,   0, -Kx],
                     [-Ky, Kx,   0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A,B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A,s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A,B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K,K)
                R = mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))
                return R

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-8:
                return sample_free()
            center = tuple( (start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim) )
            direction = tuple( (goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best**2 - c_min**2, 0.0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best**2 - c_min**2, 0.0)) / 2.0
                radii = [a1, a_minor, a_minor]

            def unit_ball_sample(n):
                while True:
                    pt = [random.uniform(-1,1) for _ in range(n)]
                    norm_sq = sum(x*x for x in pt)
                    if norm_sq <= 1.0 and norm_sq > 1e-10:
                        norm = math.sqrt(norm_sq)
                        return tuple(x / norm for x in pt)

            while True:
                n = dim
                direction_sample = unit_ball_sample(n)
                radius_sample = random.uniform(0,1) ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    sample_rot = (x_rot, y_rot)
                else:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1] + R[0][2]*sample_scaled[2]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1] + R[1][2]*sample_scaled[2]
                    z_rot = R[2][0]*sample_scaled[0] + R[2][1]*sample_scaled[1] + R[2][2]*sample_scaled[2]
                    sample_rot = (x_rot, y_rot, z_rot)

                sample_point = tuple(center[d] + sample_rot[d] for d in range(n))
                sample_point = clamp_to_bounds(sample_point)

                if not _is_in_obstacle(sample_point):
                    return sample_point

        def sample_free():
            for _ in range(30):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if within_bounds(p) and not _is_in_obstacle(p):
                    return p
            return goal_pos  # fallback

        def near_nodes(tree, pos, radius):
            radius_sq = radius * radius
            return [n for n in tree if (math.dist(n.position, pos) ** 2) <= radius_sq]

        def rewire(tree, new_node, neighbors):
            for neighbor in neighbors:
                if neighbor is new_node.parent:
                    continue
                dist_ = math.dist(new_node.position, neighbor.position)
                new_cost = new_node.cost + dist_
                if new_cost < neighbor.cost and not _is_edge_in_obstacle(new_node.position, neighbor.position):
                    if neighbor.parent is not None:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                        except Exception:
                            pass
                        neighbor.parent.remove_child(neighbor)
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

        def add_node(tree, pos, parent_node, cost):
            new_node = Node(pos)
            parent_node.add_child(new_node)
            new_node.cost = cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((parent_node, new_node))
            return new_node

        def extend_and_rewire(tree, sample):
            if not tree:
                return None
            nearest_node = min(tree, key=lambda n: math.dist(n.position, sample))
            new_pos = steer(nearest_node.position, sample)
            new_pos = clamp_to_bounds(new_pos)

            if not within_bounds(new_pos) or _is_in_obstacle(new_pos):
                return None
            if _is_edge_in_obstacle(nearest_node.position, new_pos):
                return None

            radius = adaptive_neighbor_radius(len(tree))
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in neighbors:
                cost_through = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost_through < min_cost and not _is_edge_in_obstacle(neighbor.position, new_pos):
                    min_cost = cost_through
                    best_parent = neighbor

            return add_node(tree, new_pos, best_parent, min_cost), neighbors

        def incremental_connect(tree, target_pos):
            if not tree:
                return None
            nearest_node = min(tree, key=lambda n: math.dist(n.position, target_pos))
            curr_node = nearest_node

            while True:
                new_pos = steer(curr_node.position, target_pos)
                new_pos = clamp_to_bounds(new_pos)
                if (not within_bounds(new_pos) or _is_in_obstacle(new_pos) or _is_edge_in_obstacle(curr_node.position, new_pos)):
                    return None

                radius = adaptive_neighbor_radius(len(tree))
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = curr_node.cost + math.dist(curr_node.position, new_pos)
                best_parent = curr_node
                for neighbor in neighbors:
                    cost_through = neighbor.cost + math.dist(neighbor.position, new_pos)
                    if cost_through < min_cost and not _is_edge_in_obstacle(neighbor.position, new_pos):
                        min_cost = cost_through
                        best_parent = neighbor

                new_node = add_node(tree, new_pos, best_parent, min_cost)
                rewire(tree, new_node, neighbors)

                if math.dist(new_node.position, target_pos) <= self.step_size:
                    return new_node
                curr_node = new_node

        def path_smoothing(path, iterations=100):
            if len(path) <= 2:
                return path[:]
            p = path[:]
            for _ in range(iterations):
                if len(p) <= 2:
                    break
                i = random.randint(0, len(p) - 2)
                j = random.randint(i + 1, len(p) - 1)
                if j == i + 1:
                    continue
                if not _is_edge_in_obstacle(p[i], p[j]):
                    p = p[:i + 1] + p[j:]
            return p

        import time
        start_time = time.perf_counter()

        # Initialize trees and data containers
        start_tree = [Node(start_pos)]
        goal_tree = [Node(goal_pos)]
        nodes = start_tree[:] + goal_tree[:]
        edges = []

        grow_start_tree = True
        best_path = []
        best_cost = math.inf
        success = False

        for iteration in range(self.max_iter):
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_time_sec:
                # Time limit reached, return best path found so far
                if best_path:
                    success = True
                break

            # Select sample point
            if informed_sampling_enabled and best_cost < math.inf:
                sample = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if grow_start_tree else start_pos
                else:
                    sample = sample_free()

            if grow_start_tree:
                extended_result = extend_and_rewire(start_tree, sample)
                if extended_result is None or extended_result[0] is None:
                    grow_start_tree = not grow_start_tree
                    continue
                new_node, neighbors = extended_result

                connect_node = incremental_connect(goal_tree, new_node.position)
                if connect_node is not None:
                    path_start = new_node.path_from_root()
                    path_goal = connect_node.path_from_root()
                    combined_path = path_start + path_goal[::-1][1:]
                    combined_cost = new_node.cost + connect_node.cost
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = path_smoothing(combined_path, iterations=100)
                        success = True
                        informed_sampling_enabled = True
                        # Early exit with best path found
                        break
            else:
                extended_result = extend_and_rewire(goal_tree, sample)
                if extended_result is None or extended_result[0] is None:
                    grow_start_tree = not grow_start_tree
                    continue
                new_node, neighbors = extended_result

                connect_node = incremental_connect(start_tree, new_node.position)
                if connect_node is not None:
                    path_goal = new_node.path_from_root()
                    path_start = connect_node.path_from_root()
                    combined_path = path_start + path_goal[::-1][1:]
                    combined_cost = new_node.cost + connect_node.cost
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = path_smoothing(combined_path, iterations=100)
                        success = True
                        informed_sampling_enabled = True
                        # Early exit with best path found
                        break

            grow_start_tree = not grow_start_tree

        # If failed but path found during process return best path
        if not success and best_path:
            success = True
            best_path = path_smoothing(best_path, iterations=100)

        return PlannerResult(
            success=success,
            path=best_path,
            nodes=nodes,
            edges=edges
        )

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float=5.0, goal_sample_rate: float=0.1,
                 neighbor_radius_min: float=10.0, neighbor_radius_max: float=30.0, time_limit_sec: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.time_limit_sec = time_limit_sec

    def plan(self, map) -> 'PlannerResult':
        import random
        import time
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(pos):
            return all(0 <= pos[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def nearest(tree, point):
            return min(tree, key=lambda n: dist(n.position, point))

        def adaptive_radius(n_nodes):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            r = 50.0 * (math.log(n_nodes) / n_nodes) ** (1/dim)
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree, pos, radius):
            return [n for n in tree if dist(n.position, pos) <= radius]

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(pt, obstacles, is_3d):
                    return pt

        def clamp(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        # Rotation and informed sampling based on ellipsoid (start-goal direction, cost)
        c_min = dist(start_pos, goal_pos)
        best_path_cost = math.inf
        informed_sampling_enabled = False

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a = math.cos(angle)
                sin_a = math.sin(angle)
                return [[cos_a, -sin_a],[sin_a, cos_a]]
            else:
                # 3D rotation matrix aligning direction vector to x-axis
                vx, vy, vz = direction
                norm = math.sqrt(vx*vx + vy*vy + vz*vz)
                if norm < 1e-12:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                vx, vy, vz = vx/norm, vy/norm, vz/norm
                # Rodrigues' formula
                k = (0, -vz, vy)  # cross with x-axis vector(1,0,0)
                k_norm = math.sqrt(k[0]**2 + k[1]**2 + k[2]**2)
                if k_norm < 1e-12:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                k = (k[0]/k_norm, k[1]/k_norm, k[2]/k_norm)
                cos_theta = vx
                sin_theta = k_norm
                K = [[0, -k[2], k[1]],
                     [k[2], 0, -k[0]],
                     [-k[1], k[0], 0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]
                def mat_add(A,B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A,s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A,B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]
                K2 = mat_mul(K,K)
                res = mat_add(mat_add(I, mat_scale(K,sin_theta)), mat_scale(K2, 1 - cos_theta))
                return res

        def matrix_vector_mul(M, v):
            return tuple(sum(M[i][j]*v[j] for j in range(dim)) for i in range(dim))

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1,1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in pt)

        def informed_sample():
            if best_path_cost == math.inf or best_path_cost < c_min - 1e-12:
                return sample_free()

            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)

            a1 = best_path_cost / 2.0
            if dim == 2:
                a2 = math.sqrt(max(best_path_cost**2 - c_min**2, 0.0)) / 2.0
                radii = (a1, a2)
            else:
                a_minor = math.sqrt(max(best_path_cost**2 - c_min**2, 0.0)) / 2.0
                radii = (a1, a_minor, a_minor)

            while True:
                direction_sample = unit_ball_sample(dim)
                radius_sample = random.uniform(0,1) ** (1.0/dim)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d]*radii[d] for d in range(dim))
                rotated = matrix_vector_mul(R, sample_scaled)
                sample_pt = tuple(center[d] + rotated[d] for d in range(dim))
                sample_pt = clamp(sample_pt)
                if not self._is_in_obstacle(sample_pt, obstacles, is_3d):
                    return sample_pt

        def rewire(tree, new_node, neighbors):
            for neighbor in neighbors:
                if neighbor is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_through_new < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d, resolution=self.step_size/5):
                    # safely remove old edge and update parent
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                        except ValueError:
                            pass
                    neighbor.update_parent(new_node, cost_through_new)
                    edges.append((new_node, neighbor))

        def extend(tree, target_point):
            nearest_node = nearest(tree, target_point)
            new_pos = steer(nearest_node.position, target_point)
            if not in_bounds(new_pos):
                return None
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(nearest_node.position, new_pos, obstacles, is_3d, resolution=self.step_size/5):
                return None

            radius = adaptive_radius(len(tree))
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in neighbors:
                tentative_cost = neighbor.cost + dist(neighbor.position, new_pos)
                if tentative_cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d, resolution=self.step_size/5):
                    min_cost = tentative_cost
                    best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            rewire(tree, new_node, neighbors)
            return new_node

        def try_connect(tree, node):
            # Incremental connection attempt from node to tree
            nearest_node = nearest(tree, node.position)
            d = dist(nearest_node.position, node.position)
            if d <= self.step_size:
                if self._is_in_obstacle(node.position, obstacles, is_3d):
                    return None
                if self._is_edge_in_obstacle(nearest_node.position, node.position, obstacles, is_3d, resolution=self.step_size/5):
                    return None
                return nearest_node
            return None

        def path_shortcut(path, max_iter=100):
            # Try to shortcut path by removing unnecessary points where direct connect is possible
            if len(path) < 3:
                return path[:]
            import random
            path = path[:]
            length = len(path)
            for _ in range(max_iter):
                if length <= 2:
                    break
                i = random.randint(0, length - 3)
                j = random.randint(i + 2, length - 1)
                if not self._is_edge_in_obstacle(path[i], path[j], obstacles, is_3d, resolution=self.step_size/5):
                    path = path[:i + 1] + path[j:]
                    length = len(path)
            return path

        # Initialize trees
        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]
        nodes = tree_start + tree_goal
        edges = []

        success_state = False
        extracted_path = []

        start_time = time.perf_counter()
        grow_start = True

        for iteration in range(self.max_iter):
            if (time.perf_counter() - start_time) > self.time_limit_sec:
                # Time limit reached, return best path if found
                if success_state and extracted_path:
                    extracted_path = path_shortcut(extracted_path)
                return PlannerResult(success=success_state, path=extracted_path, nodes=nodes, edges=edges)

            # Sampling
            if informed_sampling_enabled:
                sample = informed_sample()
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if grow_start else start_pos
                else:
                    sample = sample_free()

            if grow_start:
                new_node = extend(tree_start, sample)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connect_node = try_connect(tree_goal, new_node)
                if connect_node:
                    radius = adaptive_radius(len(tree_goal))
                    neighbors = near_nodes(tree_goal, new_node.position, radius)

                    min_local_cost = connect_node.cost + dist(connect_node.position, new_node.position)
                    best_parent_local = connect_node
                    for neighbor in neighbors:
                        tentative = neighbor.cost + dist(neighbor.position, new_node.position)
                        if tentative < min_local_cost and not self._is_edge_in_obstacle(neighbor.position, new_node.position, obstacles, is_3d, resolution=self.step_size/5):
                            min_local_cost = tentative
                            best_parent_local = neighbor

                    connecting_node = Node(new_node.position)
                    best_parent_local.add_child(connecting_node)
                    connecting_node.cost = min_local_cost
                    tree_goal.append(connecting_node)
                    nodes.append(connecting_node)
                    edges.append((best_parent_local, connecting_node))
                    rewire(tree_goal, connecting_node, neighbors)

                    path_start = new_node.path_from_root()
                    path_goal = connecting_node.path_from_root()
                    combined_path = path_start + path_goal[::-1][1:]
                    current_cost = sum(dist(combined_path[i], combined_path[i+1]) for i in range(len(combined_path)-1))
                    if current_cost < best_path_cost:
                        best_path_cost = current_cost
                        extracted_path = combined_path
                        success_state = True
                        informed_sampling_enabled = True
                        extracted_path = path_shortcut(extracted_path)
                        # Early exit on first connection with improved path
                        return PlannerResult(success=success_state, path=extracted_path, nodes=nodes, edges=edges)

            else:
                new_node = extend(tree_goal, sample)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connect_node = try_connect(tree_start, new_node)
                if connect_node:
                    radius = adaptive_radius(len(tree_start))
                    neighbors = near_nodes(tree_start, new_node.position, radius)

                    min_local_cost = connect_node.cost + dist(connect_node.position, new_node.position)
                    best_parent_local = connect_node
                    for neighbor in neighbors:
                        tentative = neighbor.cost + dist(neighbor.position, new_node.position)
                        if tentative < min_local_cost and not self._is_edge_in_obstacle(neighbor.position, new_node.position, obstacles, is_3d, resolution=self.step_size/5):
                            min_local_cost = tentative
                            best_parent_local = neighbor

                    connecting_node = Node(new_node.position)
                    best_parent_local.add_child(connecting_node)
                    connecting_node.cost = min_local_cost
                    tree_start.append(connecting_node)
                    nodes.append(connecting_node)
                    edges.append((best_parent_local, connecting_node))
                    rewire(tree_start, connecting_node, neighbors)

                    path_start = connecting_node.path_from_root()
                    path_goal = new_node.path_from_root()
                    combined_path = path_start + path_goal[::-1][1:]
                    current_cost = sum(dist(combined_path[i], combined_path[i+1]) for i in range(len(combined_path)-1))
                    if current_cost < best_path_cost:
                        best_path_cost = current_cost
                        extracted_path = combined_path
                        success_state = True
                        informed_sampling_enabled = True
                        extracted_path = path_shortcut(extracted_path)
                        return PlannerResult(success=success_state, path=extracted_path, nodes=nodes, edges=edges)

            grow_start = not grow_start

        if success_state and extracted_path:
            extracted_path = path_shortcut(extracted_path)
        return PlannerResult(success=success_state, path=extracted_path, nodes=nodes, edges=edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position          # Tuple[float, ...]
        self.parent = None                # Node or None
        self.cost = cost                  # Path cost from root
        self.children: List[Node] = []
        self.valid = True                 # For collision checking etc.
        if parent is not None:
            self.update_parent(parent, cost)

    def add_child(self, child_node):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        # Update costs recursively for descendants
        for child in self.children:
            dist = math.dist(self.position, child.position)
            child.update_parent(self, new_cost + dist)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int=10000, step_size: float=3.0,
                 goal_sample_rate: float=0.15,
                 neighbor_radius_min: float=8.0,
                 neighbor_radius_max: float=20.0,
                 time_limit_sec: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.time_limit_sec = time_limit_sec

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def sample_free(tree_from, tree_to_root):
            # Biased sampling towards opposite tree root
            if random.random() < self.goal_sample_rate:
                return tree_to_root
            for _ in range(50):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p
            return tree_to_root

        def steer(from_p, to_p):
            dist = math.dist(from_p, to_p)
            if dist <= self.step_size:
                return to_p
            ratio = self.step_size / dist
            return tuple(from_p[d] + (to_p[d] - from_p[d]) * ratio for d in range(dim))

        def nearest(tree, point):
            return min(tree, key=lambda node: math.dist(node.position, point))

        def near_nodes(tree, position, radius):
            radius_sq = radius * radius
            return [node for node in tree if (math.dist(node.position, position) ** 2) <= radius_sq]

        def adaptive_radius(n_nodes):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma = 50.0
            r = gamma * (math.log(n_nodes) / n_nodes) ** (1 / dim)
            return max(self.neighbor_radius_min, min(r, self.neighbor_radius_max))

        def try_extend(tree_from, sample):
            node_near = nearest(tree_from, sample)
            new_pos = steer(node_near.position, sample)
            if not in_bounds(new_pos):
                return None
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(node_near.position, new_pos, obstacles, is_3d):
                return None

            new_node = Node(new_pos)
            n_nodes = len(tree_from)
            radius = adaptive_radius(n_nodes)

            neighbors = near_nodes(tree_from, new_pos, radius)
            min_cost = node_near.cost + math.dist(node_near.position, new_pos)
            best_parent = node_near

            for neighbor in neighbors:
                cost_try = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost_try < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                    min_cost = cost_try
                    best_parent = neighbor

            best_parent.add_child(new_node)
            new_node.cost = min_cost

            tree_from.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewiring neighbors
            for neighbor in neighbors:
                if neighbor == best_parent:
                    continue
                new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
                if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                            neighbor.parent.remove_child(neighbor)
                        except ValueError:
                            pass
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

            return new_node

        def incremental_connect(tree, target_pos):
            curr = nearest(tree, target_pos)
            max_steps = int(math.dist(curr.position, target_pos) / self.step_size) + 2
            last_node = curr
            for _ in range(max_steps):
                new_pos = steer(last_node.position, target_pos)
                if not in_bounds(new_pos):
                    return None
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    return None
                if self._is_edge_in_obstacle(last_node.position, new_pos, obstacles, is_3d):
                    return None

                new_node = Node(new_pos)
                n_nodes = len(tree)
                radius = adaptive_radius(n_nodes)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = last_node.cost + math.dist(last_node.position, new_pos)
                best_parent = last_node
                for neighbor in neighbors:
                    cost_try = neighbor.cost + math.dist(neighbor.position, new_pos)
                    if cost_try < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                        min_cost = cost_try
                        best_parent = neighbor

                best_parent.add_child(new_node)
                new_node.cost = min_cost

                tree.append(new_node)
                nodes.append(new_node)
                edges.append((best_parent, new_node))

                for neighbor in neighbors:
                    if neighbor == best_parent:
                        continue
                    new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
                    if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                        if neighbor.parent:
                            try:
                                edges.remove((neighbor.parent, neighbor))
                                neighbor.parent.remove_child(neighbor)
                            except ValueError:
                                pass
                        neighbor.update_parent(new_node, new_cost)
                        edges.append((new_node, neighbor))

                last_node = new_node
                if math.dist(last_node.position, target_pos) <= self.step_size:
                    return last_node
            return None

        def path_smoothing(path: List[Tuple[float, ...]], max_iters=150):
            if len(path) < 3:
                return path[:]
            length = len(path)
            for _ in range(max_iters):
                if length <= 2:
                    break
                i = random.randint(0, length - 3)
                j = random.randint(i + 2, length - 1)
                if not self._is_edge_in_obstacle(path[i], path[j], obstacles, is_3d):
                    path = path[:i + 1] + path[j:]
                    length = len(path)
            return path

        start_time = time.perf_counter()
        start_tree = [Node(start_pos, None, 0.0)]
        goal_tree = [Node(goal_pos, None, 0.0)]
        nodes = start_tree + goal_tree
        edges = []

        best_cost = float('inf')
        best_connection = None
        extracted_path: List[Tuple[float, ...]] = []
        success_state = False

        # Alternate tree growth
        tree_a, tree_b = start_tree, goal_tree

        for _ in range(self.max_iter):
            if time.perf_counter() - start_time > self.time_limit_sec:
                break

            sample = sample_free(tree_a, tree_b[0].position)
            new_node_a = try_extend(tree_a, sample)
            if new_node_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_node_b = incremental_connect(tree_b, new_node_a.position)
            if new_node_b:
                total_cost = new_node_a.cost + new_node_b.cost + math.dist(new_node_a.position, new_node_b.position)
                if total_cost < best_cost:
                    best_cost = total_cost
                    best_connection = (new_node_a, new_node_b)
                    path_a = new_node_a.path_from_root()
                    path_b = new_node_b.path_from_root()
                    combined = path_a + path_b[::-1]
                    extracted_path = path_smoothing(combined)
                    success_state = True
                    break  # Early termination on successful connection

            tree_a, tree_b = tree_b, tree_a

        if not success_state and best_connection is not None:
            node_a, node_b = best_connection
            path_a = node_a.path_from_root()
            path_b = node_b.path_from_root()
            combined = path_a + path_b[::-1]
            extracted_path = path_smoothing(combined)
            success_state = True

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)


class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        start_tree = [Node(start_position)]
        goal_tree = [Node(goal_position)]
        nodes = []
        edges = []
        success_state = False
        extracted_path = []

        for iter in range(self.max_iter):
            rand = self._sample_free(bounds, obstacles, is_3d)
            for tree_a, tree_b in [(start_tree, goal_tree), (goal_tree, start_tree)]:
                nearest = self._nearest(tree_a, rand)
                new_pos = self._steer(nearest.position, rand, self.step_size)
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    continue
                if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
                    continue
                new_node = Node(new_pos, parent=nearest, cost=nearest.cost + self._distance(nearest.position, new_pos))
                nearest.add_child(new_node)
                tree_a.append(new_node)
                nodes.append(new_node)
                edges.append((nearest, new_node))

                connect_node = self._nearest(tree_b, new_pos)
                if self._connect_trees(new_node, connect_node, obstacles, is_3d, tree_b, nodes, edges):
                    success_state = True
                    extracted_path = self._extract_path(new_node, connect_node)
                    return PlannerResult(True, extracted_path, start_tree + goal_tree, edges)

        return PlannerResult(False, [], start_tree + goal_tree, edges)

    def _sample_free(self, bounds, obstacles, is_3d):
        while True:
            if is_3d:
                point = tuple(random.uniform(0, bounds[i]) for i in range(3))
            else:
                point = tuple(random.uniform(0, bounds[i]) for i in range(2))
            if not self._is_in_obstacle(point, obstacles, is_3d):
                return point

    def _nearest(self, tree, point):
        return min(tree, key=lambda node: self._distance(node.position, point))

    def _steer(self, from_pos, to_pos, step_size):
        dist = self._distance(from_pos, to_pos)
        if dist <= step_size:
            return to_pos
        ratio = step_size / dist
        return tuple(from_pos[i] + (to_pos[i] - from_pos[i]) * ratio for i in range(len(from_pos)))

    def _connect_trees(self, node_a, node_b, obstacles, is_3d, tree, nodes, edges):
        from_pos = node_a.position
        to_pos = node_b.position
        if self._is_edge_in_obstacle(from_pos, to_pos, obstacles, is_3d):
            return False
        curr = node_b
        path = []
        while True:
            new_pos = self._steer(curr.position, from_pos, self.step_size)
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return False
            if self._is_edge_in_obstacle(curr.position, new_pos, obstacles, is_3d):
                return False
            new_node = Node(new_pos, parent=curr, cost=curr.cost + self._distance(curr.position, new_pos))
            curr.add_child(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((curr, new_node))
            if self._distance(new_pos, from_pos) < self.step_size:
                new_node_final = Node(from_pos, parent=new_node, cost=new_node.cost + self._distance(new_pos, from_pos))
                new_node.add_child(new_node_final)
                tree.append(new_node_final)
                nodes.append(new_node_final)
                edges.append((new_node, new_node_final))
                return True
            curr = new_node

    def _extract_path(self, node_a, node_b):
        path_a = []
        while node_a:
            path_a.append(node_a.position)
            node_a = node_a.parent
        path_b = []
        while node_b:
            path_b.append(node_b.position)
            node_b = node_b.parent
        return list(reversed(path_a)) + path_b

    def _distance(self, a, b):
        return math.dist(a, b)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = self._distance(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        new_parent.add_child(self)
        self.cost = new_cost
        # Recursively update cost of descendants
        for child in self.children:
            dist = self._distance(child.position)
            child.update_parent(self, self.cost + dist)

    def _distance(self, other_pos):
        import math
        return math.dist(self.position, other_pos)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0, goal_sample_rate: float = 0.15, neighbor_radius: float = 15.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius = neighbor_radius

    def plan(self, map) -> 'PlannerResult':
        import random, math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dimension = len(bounds)
        is_3d = (dimension == 3)

        nodes = []
        edges = []

        tree_start = [Node(start_pos)]
        tree_goal = [Node(goal_pos)]
        nodes.extend(tree_start)
        nodes.extend(tree_goal)

        def sample_free():
            if random.random() < self.goal_sample_rate:
                return goal_pos
            while True:
                p = tuple(random.uniform(0, bounds[d]) for d in range(dimension))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p

        def steer(from_pos, to_pos):
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dimension))

        def near_nodes(tree, position):
            return [node for node in tree if math.dist(node.position, position) <= self.neighbor_radius]

        def is_collision_free_node(pos):
            return not self._is_in_obstacle(pos, obstacles, is_3d)

        def is_collision_free_edge(from_pos, to_pos):
            return not self._is_edge_in_obstacle(from_pos, to_pos, obstacles, is_3d)

        def rewire(tree, new_node, neighbors):
            for neighbor in neighbors:
                if neighbor == new_node.parent:
                    continue
                dist = math.dist(new_node.position, neighbor.position)
                new_cost = new_node.cost + dist
                if new_cost < neighbor.cost and is_collision_free_edge(new_node.position, neighbor.position):
                    prev_parent = neighbor.parent
                    if prev_parent:
                        if (prev_parent, neighbor) in edges:
                            edges.remove((prev_parent, neighbor))
                        prev_parent.remove_child(neighbor)
                    neighbor.parent = new_node
                    neighbor.cost = new_cost
                    new_node.add_child(neighbor)
                    edges.append((new_node, neighbor))

        def try_extend(tree, sample):
            # Find nearest node
            nearest_node = min(tree, key=lambda n: math.dist(n.position, sample))
            new_pos = steer(nearest_node.position, sample)

            if not self._within_bounds(new_pos, bounds):
                return None
            if not is_collision_free_node(new_pos):
                return None
            if not is_collision_free_edge(nearest_node.position, new_pos):
                return None

            neighbors = near_nodes(tree, new_pos)

            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in neighbors:
                cost_through_neighbor = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost_through_neighbor < min_cost and is_collision_free_edge(neighbor.position, new_pos):
                    min_cost = cost_through_neighbor
                    best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            rewire(tree, new_node, neighbors)
            return new_node

        def try_connect(tree, connect_node):
            nearest_node = min(tree, key=lambda n: math.dist(n.position, connect_node.position))
            dist = math.dist(nearest_node.position, connect_node.position)
            if dist > self.step_size:
                return None

            if not is_collision_free_node(connect_node.position):
                return None
            if not is_collision_free_edge(nearest_node.position, connect_node.position):
                return None

            neighbors = near_nodes(tree, connect_node.position)

            min_cost = nearest_node.cost + dist
            best_parent = nearest_node
            for neighbor in neighbors:
                cost_through_neighbor = neighbor.cost + math.dist(neighbor.position, connect_node.position)
                if cost_through_neighbor < min_cost and is_collision_free_edge(neighbor.position, connect_node.position):
                    min_cost = cost_through_neighbor
                    best_parent = neighbor

            new_node = Node(connect_node.position)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            rewire(tree, new_node, neighbors)
            return new_node

        success_state = False
        final_path = []

        # Alternate tree starts with start tree
        tree_a = tree_start
        tree_b = tree_goal

        for _ in range(self.max_iter):
            sample = sample_free()
            new_node_a = try_extend(tree_a, sample)
            if new_node_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_node_b = try_connect(tree_b, new_node_a)
            if new_node_b:
                path_a = new_node_a.path_from_root()
                path_b = new_node_b.path_from_root()
                path_b.reverse()
                final_path = path_a + path_b[1:]  # avoid duplicate connection node
                success_state = True
                break

            tree_a, tree_b = tree_b, tree_a

        return PlannerResult(
            success=success_state,
            path=final_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

    def _within_bounds(self, pos, bounds):
        return all(0 <= pos[d] <= bounds[d] for d in range(len(bounds)))

