class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.2,
                 min_neighbor_radius: float = 10.0,
                 max_neighbor_radius: float = 30.0,
                 max_time_sec: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.max_time_sec = max_time_sec

    def plan(self, map) -> 'PlannerResult':
        import time
        import math
        import random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def adaptive_neighbor_radius(n_nodes):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            gamma = 20.0
            radius = gamma * ((math.log(n_nodes) / n_nodes) ** (1/dim))
            return max(self.min_neighbor_radius, min(self.max_neighbor_radius, radius))

        def sample_free(towards_pos):
            if random.random() < self.goal_sample_rate:
                return towards_pos
            for _ in range(30):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(p, obstacles, is_3d) and in_bounds(p):
                    return p
            return towards_pos  # fallback

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            ratio = self.step_size / d
            return tuple(from_pos[d_] + ratio * (to_pos[d_] - from_pos[d_]) for d_ in range(dim))

        def nearest(tree, point):
            return min(tree, key=lambda node: dist(node.position, point))

        def near_nodes(tree, position, radius):
            radius_sq = radius * radius
            return [node for node in tree if (dist(node.position, position)**2) <= radius_sq]

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=0.5)

        def rewire(tree, new_node, neighbors):
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, nbr.position)
                if new_cost + 1e-8 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, new_cost)

        def extend(tree, target_point, nodes, edges):
            nearest_node = nearest(tree, target_point)
            new_pos = steer(nearest_node.position, target_point)
            # Clamp new_pos to bounds
            new_pos = tuple(max(0, min(bounds[d], new_pos[d])) for d in range(dim))

            if not collision_free_node(new_pos) or not collision_free_edge(nearest_node.position, new_pos):
                return None

            n_radius = adaptive_neighbor_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, n_radius)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for nbr in neighbors:
                if nbr is nearest_node:
                    continue
                c = nbr.cost + dist(nbr.position, new_pos)
                if c + 1e-8 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = c
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            rewire(tree, new_node, neighbors)
            return new_node

        def incremental_connect(new_node, other_tree, nodes, edges):
            curr_node = nearest(other_tree, new_node.position)
            while True:
                new_pos = steer(curr_node.position, new_node.position)
                new_pos = tuple(max(0, min(bounds[d], new_pos[d])) for d in range(dim))

                if not collision_free_node(new_pos) or not collision_free_edge(curr_node.position, new_pos):
                    return None

                n_radius = adaptive_neighbor_radius(len(other_tree) + 1)
                neighbors = near_nodes(other_tree, new_pos, n_radius)

                min_cost = curr_node.cost + dist(curr_node.position, new_pos)
                best_parent = curr_node
                for nbr in neighbors:
                    if nbr is curr_node:
                        continue
                    c = nbr.cost + dist(nbr.position, new_pos)
                    if c + 1e-8 < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = c
                        best_parent = nbr

                connecting_node = Node(new_pos)
                best_parent.add_child(connecting_node)
                connecting_node.cost = min_cost
                other_tree.append(connecting_node)
                nodes.append(connecting_node)
                edges.append((best_parent, connecting_node))

                rewire(other_tree, connecting_node, neighbors)

                if dist(connecting_node.position, new_node.position) <= self.step_size:
                    if collision_free_edge(connecting_node.position, new_node.position):
                        return connecting_node
                curr_node = connecting_node

        def smooth_path(path, max_trials=150):
            if len(path) < 3:
                return path[:]

            def can_connect(p1, p2):
                return collision_free_edge(p1, p2)

            smoothed = path[:]
            for _ in range(max_trials):
                if len(smoothed) < 3:
                    break
                i = random.randint(0, len(smoothed) - 3)
                j = random.randint(i + 2, len(smoothed) - 1)
                if can_connect(smoothed[i], smoothed[j]):
                    smoothed = smoothed[:i + 1] + smoothed[j:]
            return smoothed

        # Initialization
        start_tree = [Node(start_pos, cost=0.0)]
        goal_tree = [Node(goal_pos, cost=0.0)]
        nodes = start_tree[:] + goal_tree[:]
        edges = []

        tree_a, tree_b = start_tree, goal_tree
        best_path = []
        success_state = False
        start_time = time.time()

        for _ in range(self.max_iter):
            if (time.time() - start_time) > self.max_time_sec:
                break

            sample = sample_free(tree_b[0].position)

            new_node = extend(tree_a, sample, nodes, edges)
            if not new_node:
                tree_a, tree_b = tree_b, tree_a
                continue

            connect_node = incremental_connect(new_node, tree_b, nodes, edges)
            if connect_node:
                path_a = new_node.path_from_root()
                path_b = connect_node.path_from_root()
                if path_b and path_b[0] == new_node.position:
                    path_b = path_b[1:]
                full_path = path_a + path_b[::-1]
                best_path = smooth_path(full_path)
                success_state = True
                break

            tree_a, tree_b = tree_b, tree_a

        return PlannerResult(
            success=success_state,
            path=best_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=0.5):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = parent            # Node or None
        self.cost = cost                # Cumulative cost from root
        self.children = []
        self.valid = True               # For collision/status checks

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=3.0, goal_sample_rate: float=0.1, neighbor_radius: float=20.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius = neighbor_radius

    def plan(self, map) -> 'PlannerResult':
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        start_tree = [Node(start_pos)]
        goal_tree = [Node(goal_pos)]
        nodes = start_tree + goal_tree
        edges = []

        success = False
        final_path = []

        tree_a, tree_b = start_tree, goal_tree  # Alternate expansions

        for _ in range(self.max_iter):
            # Goal bias sampling towards opposite tree's root
            if random.random() < self.goal_sample_rate:
                sample = tree_b[0].position
            else:
                sample = self._sample_free(bounds, obstacles, is_3d)

            new_node_a = self._extend_and_rewire(tree_a, sample, obstacles, is_3d, nodes, edges)
            if new_node_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_node_b = self._incremental_connect_and_rewire(tree_b, new_node_a.position, obstacles, is_3d, nodes, edges)
            if new_node_b:
                path_a = new_node_a.path_from_root()
                path_b = new_node_b.path_from_root()
                final_path = path_a + path_b[::-1]
                success = True
                break

            tree_a, tree_b = tree_b, tree_a

        return PlannerResult(
            success=success,
            path=final_path,
            nodes=nodes,
            edges=edges
        )

    def _sample_free(self, bounds, obstacles, is_3d):
        import random
        while True:
            point = tuple(random.uniform(0, bounds[d]) for d in range(len(bounds)))
            if not self._is_in_obstacle(point, obstacles, is_3d):
                return point

    def _nearest(self, tree, point):
        import math
        return min(tree, key=lambda node: math.dist(node.position, point))

    def _steer(self, from_pos, to_pos):
        import math
        dist = math.dist(from_pos, to_pos)
        if dist <= self.step_size:
            return to_pos
        ratio = self.step_size / dist
        return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(len(from_pos)))

    def _near_nodes(self, tree, position):
        import math
        return [node for node in tree if math.dist(node.position, position) <= self.neighbor_radius]

    def _extend_and_rewire(self, tree, point, obstacles, is_3d, nodes, edges):
        import math

        nearest = self._nearest(tree, point)
        new_pos = self._steer(nearest.position, point)

        if self._is_in_obstacle(new_pos, obstacles, is_3d) or self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
            return None

        neighbors = self._near_nodes(tree, new_pos)

        # Find optimal parent among neighbors
        min_cost = nearest.cost + math.dist(nearest.position, new_pos)
        best_parent = nearest
        for neighbor in neighbors:
            if neighbor == nearest:
                continue
            candidate_cost = neighbor.cost + math.dist(neighbor.position, new_pos)
            if candidate_cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                min_cost = candidate_cost
                best_parent = neighbor

        new_node = Node(new_pos)
        best_parent.add_child(new_node)
        new_node.cost = min_cost
        tree.append(new_node)
        nodes.append(new_node)
        edges.append((best_parent, new_node))

        # Rewire neighbors through new_node if cheaper
        for neighbor in neighbors:
            if neighbor == best_parent:
                continue
            new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
            if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                if neighbor.parent:
                    try:
                        edges.remove((neighbor.parent, neighbor))
                        neighbor.parent.remove_child(neighbor)
                    except ValueError:
                        pass
                neighbor.update_parent(new_node, new_cost)
                edges.append((new_node, neighbor))

        return new_node

    def _incremental_connect_and_rewire(self, tree, target_pos, obstacles, is_3d, nodes, edges):
        import math

        nearest = self._nearest(tree, target_pos)
        current = nearest

        while True:
            new_pos = self._steer(current.position, target_pos)

            if self._is_in_obstacle(new_pos, obstacles, is_3d) or self._is_edge_in_obstacle(current.position, new_pos, obstacles, is_3d):
                return None

            neighbors = self._near_nodes(tree, new_pos)

            min_cost = current.cost + math.dist(current.position, new_pos)
            best_parent = current
            for neighbor in neighbors:
                if neighbor == current:
                    continue
                candidate_cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                if candidate_cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                    min_cost = candidate_cost
                    best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors through new_node if cheaper
            for neighbor in neighbors:
                if neighbor == best_parent:
                    continue
                new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
                if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                            neighbor.parent.remove_child(neighbor)
                        except ValueError:
                            pass
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

            if math.dist(new_node.position, target_pos) <= self.step_size:
                return new_node

            current = new_node

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        dist = math.dist(from_pos, to_pos)
        steps = max(1, int(dist / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision validation

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def path_from_root(self):
        path, node = [], self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0, goal_sample_rate: float = 0.15, neighbor_radius: float = 12.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius = neighbor_radius

    def plan(self, map) -> 'PlannerResult':
        import random, math

        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        dimension = len(bounds)
        is_3d = (dimension == 3)

        success_state = False
        extracted_path = []
        nodes = []
        edges = []

        tree_start = [Node(start_position)]
        tree_goal = [Node(goal_position)]
        nodes.extend(tree_start)
        nodes.extend(tree_goal)

        def sample_free():
            if random.random() < self.goal_sample_rate:
                return goal_position
            while True:
                p = tuple(random.uniform(0, bounds[d]) for d in range(dimension))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p

        def nearest(tree, point):
            # Efficient nearest search: linear for simplicity, can be optimized with spatial indexing
            min_node = None
            min_dist = float('inf')
            for node in tree:
                dist = math.dist(node.position, point)
                if dist < min_dist:
                    min_dist = dist
                    min_node = node
            return min_node

        def steer(from_pos, to_pos):
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dimension))

        def near_nodes(tree, position):
            return [node for node in tree if math.dist(node.position, position) <= self.neighbor_radius]

        def try_extend(tree, point):
            nearest_node = nearest(tree, point)
            new_pos = steer(nearest_node.position, point)
            if not self._within_bounds(new_pos, bounds):
                return None
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(nearest_node.position, new_pos, obstacles, is_3d):
                return None

            neighbors = near_nodes(tree, new_pos)
            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node

            for neighbor in neighbors:
                potential_cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                if potential_cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                    min_cost = potential_cost
                    best_parent = neighbor

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors to new_node if cheaper
            for neighbor in neighbors:
                if neighbor == best_parent:
                    continue
                cost_through_new = new_node.cost + math.dist(new_node.position, neighbor.position)
                if cost_through_new < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                    # Remove old edge
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                        except ValueError:
                            pass
                        neighbor.parent.remove_child(neighbor)
                    # Re-assign parent
                    neighbor.parent = new_node
                    neighbor.cost = cost_through_new
                    new_node.add_child(neighbor)
                    edges.append((new_node, neighbor))

            return new_node

        def try_connect(tree, connect_to_node):
            nearest_node = nearest(tree, connect_to_node.position)
            dist = math.dist(nearest_node.position, connect_to_node.position)
            if dist > self.step_size:
                return None
            if self._is_in_obstacle(connect_to_node.position, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(nearest_node.position, connect_to_node.position, obstacles, is_3d):
                return None

            neighbors = near_nodes(tree, connect_to_node.position)
            min_cost = nearest_node.cost + dist
            best_parent = nearest_node

            for neighbor in neighbors:
                potential_cost = neighbor.cost + math.dist(neighbor.position, connect_to_node.position)
                if potential_cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, connect_to_node.position, obstacles, is_3d):
                    min_cost = potential_cost
                    best_parent = neighbor

            new_node = Node(connect_to_node.position, parent=best_parent, cost=min_cost)
            best_parent.add_child(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors to new_node if cheaper
            for neighbor in neighbors:
                if neighbor == best_parent:
                    continue
                cost_through_new = new_node.cost + math.dist(new_node.position, neighbor.position)
                if cost_through_new < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                        except ValueError:
                            pass
                        neighbor.parent.remove_child(neighbor)
                    neighbor.parent = new_node
                    neighbor.cost = cost_through_new
                    new_node.add_child(neighbor)
                    edges.append((new_node, neighbor))

            return new_node

        # Alternate tree expansion each iteration to balance growth
        start_tree = tree_start
        goal_tree = tree_goal

        for i in range(self.max_iter):
            sample = sample_free()
            new_node_start = try_extend(start_tree, sample)
            if new_node_start is None:
                start_tree, goal_tree = goal_tree, start_tree
                continue

            new_node_goal = try_connect(goal_tree, new_node_start)
            if new_node_goal:
                # Paths connecting start_tree root to new_node_start, and goal_tree root to new_node_goal
                path_start = new_node_start.path_from_root()
                path_goal = new_node_goal.path_from_root()
                path_goal.reverse()
                extracted_path = path_start + path_goal[1:]  # Avoid duplicating connecting node
                success_state = True
                break

            start_tree, goal_tree = goal_tree, start_tree

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

    def _within_bounds(self, pos, bounds):
        return all(0 <= pos[d] <= bounds[d] for d in range(len(bounds)))

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost=0.0):
        self.position = position          # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent              # Node or None
        self.cost = cost                  # Path cost from root
        self.children: List[Node] = []    # Children nodes
        self.valid = True                 # For collision checking etc.

    def add_child(self, child_node: "Node"):
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child_node: "Node"):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(
        self, max_iter: int = 5000, step_size: float = 5.0,
        goal_sample_rate: float = 0.1, neighbor_radius: float = 20.0,
        time_limit_sec: float = 30.0
    ):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius = neighbor_radius
        self.time_limit_sec = time_limit_sec

    def plan(self, map) -> PlannerResult:
        bounds = map.size                 # Tuple[int, ...]
        start_position = map.start        # Tuple[float, ...]
        goal_position = map.goal          # Tuple[float, ...]
        obstacles = map.obstacles         # List of obstacles
        dimension = len(bounds)
        is_3d = (dimension == 3)

        success_state = False
        extracted_path: List[Tuple[float, ...]] = []
        nodes: List[Node] = []
        edges: List[Tuple[Node, Node]] = []

        tree_start: List[Node] = [Node(start_position)]
        tree_goal: List[Node] = [Node(goal_position)]
        nodes.extend(tree_start)
        nodes.extend(tree_goal)

        start_time = time.time()

        def sample_free() -> Tuple[float, ...]:
            if (math.isclose(self.goal_sample_rate, 0.0) or
                math.isclose(self.goal_sample_rate, 1.0)):
                biased = False if self.goal_sample_rate == 0.0 else True
            else:
                biased = (math.random() < self.goal_sample_rate)
            if (math.random() if hasattr(math, "random") else True) < self.goal_sample_rate:
                return goal_position
            while True:
                p = tuple(random.uniform(0, bounds[d]) for d in range(dimension))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p

        import random

        def sample_free() -> Tuple[float, ...]:
            if random.random() < self.goal_sample_rate:
                return goal_position
            while True:
                p = tuple(random.uniform(0, bounds[d]) for d in range(dimension))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p

        def nearest(tree: List[Node], point: Tuple[float, ...]) -> Node:
            return min(tree, key=lambda n: math.dist(n.position, point))

        def steer(from_pos: Tuple[float, ...], to_pos: Tuple[float, ...]) -> Tuple[float, ...]:
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dimension))

        def near_nodes(tree: List[Node], position: Tuple[float, ...]) -> List[Node]:
            radius = self.neighbor_radius
            return [node for node in tree if math.dist(node.position, position) <= radius]

        def _can_connect(from_node: Node, to_point: Tuple[float, ...]) -> bool:
            if not self._within_bounds(to_point, bounds):
                return False
            if self._is_in_obstacle(to_point, obstacles, is_3d):
                return False
            if self._is_edge_in_obstacle(from_node.position, to_point, obstacles, is_3d):
                return False
            return True

        def try_extend(tree: List[Node], point: Tuple[float, ...]) -> Node:
            nearest_node = nearest(tree, point)
            new_pos = steer(nearest_node.position, point)
            if not _can_connect(nearest_node, new_pos):
                return None

            neighbors = near_nodes(tree, new_pos)

            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in neighbors:
                cost_to_new = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost_to_new < min_cost and _can_connect(neighbor, new_pos):
                    min_cost = cost_to_new
                    best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors to new_node if better path found
            for neighbor in neighbors:
                if neighbor == best_parent:
                    continue
                cost_via_new = new_node.cost + math.dist(new_node.position, neighbor.position)
                if cost_via_new < neighbor.cost and _can_connect(new_node, neighbor.position):
                    # Rewire neighbor
                    if neighbor.parent:
                        edges.remove((neighbor.parent, neighbor))
                        neighbor.parent.remove_child(neighbor)
                    neighbor.parent = new_node
                    neighbor.cost = cost_via_new
                    new_node.add_child(neighbor)
                    edges.append((new_node, neighbor))

            return new_node

        def try_connect(tree: List[Node], new_node: Node) -> Node:
            nearest_node = nearest(tree, new_node.position)
            dist = math.dist(nearest_node.position, new_node.position)
            if dist > self.step_size:
                return None
            if not _can_connect(nearest_node, new_node.position):
                return None

            neighbors = near_nodes(tree, new_node.position)

            min_cost = nearest_node.cost + dist
            best_parent = nearest_node
            for neighbor in neighbors:
                cost_to_new = neighbor.cost + math.dist(neighbor.position, new_node.position)
                if cost_to_new < min_cost and _can_connect(neighbor, new_node.position):
                    min_cost = cost_to_new
                    best_parent = neighbor

            connect_node = Node(new_node.position)
            best_parent.add_child(connect_node)
            connect_node.cost = min_cost
            tree.append(connect_node)
            nodes.append(connect_node)
            edges.append((best_parent, connect_node))

            # Rewire neighbors of linking tree near new node
            for neighbor in neighbors:
                if neighbor == best_parent:
                    continue
                cost_via_connect = connect_node.cost + math.dist(connect_node.position, neighbor.position)
                if cost_via_connect < neighbor.cost and _can_connect(connect_node, neighbor.position):
                    if neighbor.parent:
                        edges.remove((neighbor.parent, neighbor))
                        neighbor.parent.remove_child(neighbor)
                    neighbor.parent = connect_node
                    neighbor.cost = cost_via_connect
                    connect_node.add_child(neighbor)
                    edges.append((connect_node, neighbor))

            return connect_node

        # Alternate trees:
        active_tree = tree_start
        passive_tree = tree_goal

        best_path_cost = float("inf")
        best_path_nodes: Tuple[Node, Node] = (None, None)  # Nodes where trees connected

        for iter_num in range(self.max_iter):
            if time.time() - start_time > self.time_limit_sec:
                break

            rand_point = sample_free()

            new_node = try_extend(active_tree, rand_point)
            if new_node is None:
                active_tree, passive_tree = passive_tree, active_tree
                continue

            connecting_node = try_connect(passive_tree, new_node)
            if connecting_node:
                # Calculate total cost and update best if better
                total_cost = new_node.cost + connecting_node.cost
                if total_cost < best_path_cost:
                    best_path_cost = total_cost
                    best_path_nodes = (new_node, connecting_node)
                    success_state = True

                    # Extract path immediately to return faster
                    path_start = new_node.path_from_root()
                    path_goal = connecting_node.path_from_root()
                    extracted_path = path_start + path_goal[::-1][1:]

                    break  # Stop here as we found the path

            # Swap trees for next iteration (bidirectional alternate expansion)
            active_tree, passive_tree = passive_tree, active_tree

        # If timed out without connection but some node pairs exist, try to extract best found path
        if not success_state and best_path_nodes[0] is not None and best_path_nodes[1] is not None:
            node_start, node_goal = best_path_nodes
            path_start = node_start.path_from_root()
            path_goal = node_goal.path_from_root()
            extracted_path = path_start + path_goal[::-1][1:]
            success_state = True

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

    def _within_bounds(self, pos, bounds):
        return all(0 <= pos[d] <= bounds[d] for d in range(len(bounds)))

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float = 0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child_node):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        if self not in new_parent.children:
            new_parent.children.append(self)
        # Recursively update costs of descendants for consistency
        for child in self.children:
            child.update_parent(self, self.cost + math.dist(self.position, child.position))

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 4.0, goal_sample_rate: float = 0.15,
                 min_neighbor_radius: float = 5.0, max_neighbor_radius: float = 20.0,
                 time_limit_sec: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.time_limit_sec = time_limit_sec

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles

        is_3d = len(bounds) == 3
        dimension = len(bounds)

        start_tree = [Node(start_position)]
        goal_tree = [Node(goal_position)]
        nodes = start_tree + goal_tree
        edges = []

        success_state = False
        extracted_path: List[Tuple[float, ...]] = []

        start_time = time.time()

        # Utility functions
        def _random():
            import random
            return random.random()

        def sample_free(goal_bias_for_start=True):
            # Goal biasing for alternating trees
            if _random() < self.goal_sample_rate:
                return goal_position if goal_bias_for_start else start_position
            while True:
                sample = tuple(_random() * bounds[d] for d in range(dimension))
                if not self._is_in_obstacle(sample, obstacles, is_3d):
                    return sample

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            distance = dist(from_pos, to_pos)
            if distance <= self.step_size:
                return to_pos
            ratio = self.step_size / distance
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dimension))

        def nearest(tree, point):
            return min(tree, key=lambda node: dist(node.position, point))

        def near_nodes(tree, position, radius):
            return [node for node in tree if dist(node.position, position) <= radius]

        def update_neighbor_radius(n_nodes):
            val = self.min_neighbor_radius * (math.log(n_nodes + 1) / (n_nodes + 1))**(1/dimension)
            return max(self.min_neighbor_radius, min(self.max_neighbor_radius, val))

        def within_bounds(pos):
            return all(0 <= pos[d] <= bounds[d] for d in range(dimension))

        def try_extend_and_rewire(tree, point):
            if time.time() - start_time > self.time_limit_sec:
                return None
            nearest_node = nearest(tree, point)
            new_pos = steer(nearest_node.position, point)
            if not within_bounds(new_pos):
                return None
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(nearest_node.position, new_pos, obstacles, is_3d):
                return None

            radius = update_neighbor_radius(len(tree))
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in neighbors:
                tentative_cost = neighbor.cost + dist(neighbor.position, new_pos)
                if tentative_cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                    min_cost = tentative_cost
                    best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors if cost improvement possible
            for neighbor in neighbors:
                if neighbor is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, neighbor.position)
                if new_cost < neighbor.cost:
                    if not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                        if neighbor.parent:
                            try:
                                edges.remove((neighbor.parent, neighbor))
                                neighbor.parent.remove_child(neighbor)
                            except ValueError:
                                pass
                        neighbor.update_parent(new_node, new_cost)
                        edges.append((new_node, neighbor))
            return new_node

        def try_incremental_connect_and_rewire(tree, target_pos):
            if time.time() - start_time > self.time_limit_sec:
                return None
            curr_node = nearest(tree, target_pos)
            while True:
                new_pos = steer(curr_node.position, target_pos)
                if not within_bounds(new_pos):
                    return None
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    return None
                if self._is_edge_in_obstacle(curr_node.position, new_pos, obstacles, is_3d):
                    return None

                radius = update_neighbor_radius(len(tree))
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = curr_node.cost + dist(curr_node.position, new_pos)
                best_parent = curr_node
                for neighbor in neighbors:
                    tentative_cost = neighbor.cost + dist(neighbor.position, new_pos)
                    if tentative_cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                        min_cost = tentative_cost
                        best_parent = neighbor

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)
                nodes.append(new_node)
                edges.append((best_parent, new_node))

                for neighbor in neighbors:
                    if neighbor is best_parent:
                        continue
                    new_cost = new_node.cost + dist(new_node.position, neighbor.position)
                    if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                        if neighbor.parent:
                            try:
                                edges.remove((neighbor.parent, neighbor))
                                neighbor.parent.remove_child(neighbor)
                            except ValueError:
                                pass
                        neighbor.update_parent(new_node, new_cost)
                        edges.append((new_node, neighbor))

                if dist(new_node.position, target_pos) <= self.step_size:
                    return new_node
                if time.time() - start_time > self.time_limit_sec:
                    return None
                curr_node = new_node

        def path_smoothing(path: List[Tuple[float, ...]], max_attempts=50) -> List[Tuple[float, ...]]:
            if len(path) < 3:
                return path
            path = path[:]
            attempts = 0
            while attempts < max_attempts:
                if len(path) < 3:
                    break
                i = int(_random() * (len(path) - 2))
                j = i + 2 + int(_random() * (len(path) - i - 2))
                if j >= len(path):
                    j = len(path) - 1
                if j <= i + 1:
                    attempts += 1
                    continue
                if not self._is_edge_in_obstacle(path[i], path[j], obstacles, is_3d):
                    del path[i + 1:j]
                    attempts = 0
                else:
                    attempts += 1
            return path

        tree_a, tree_b = start_tree, goal_tree

        for _ in range(self.max_iter):
            if time.time() - start_time > self.time_limit_sec:
                break  # Time limit exceeded

            sample = sample_free(goal_bias_for_start=(tree_a is start_tree))
            new_node_a = try_extend_and_rewire(tree_a, sample)
            if new_node_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_node_b = try_incremental_connect_and_rewire(tree_b, new_node_a.position)
            if new_node_b is not None:
                # Merge and smooth final path
                path_a = new_node_a.path_from_root()
                path_b = new_node_b.path_from_root()
                raw_path = path_a + path_b[::-1][1:]  # avoid duplicate node
                extracted_path = path_smoothing(raw_path)
                success_state = True
                break

            tree_a, tree_b = tree_b, tree_a

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=0.5):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position          # 2D or 3D coordinate
        self.parent = parent              # Parent Node or None
        self.cost = cost                  # Cost from root to this node
        self.children: List[Node] = []    # List of child nodes
        self.valid = True                 # Collision free flag

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        new_parent.add_child(self)
        self.cost = new_cost
        # Recursively update costs of descendants
        for child in self.children:
            dist = math.dist(self.position, child.position)
            child.update_parent(self, self.cost + dist)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int=5000, step_size: float=5.0, goal_sample_rate: float=0.2, max_time_sec: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.max_time_sec = max_time_sec
        self.informed_sampling_enabled = True

    def plan(self, map) -> PlannerResult:
        bounds = map.size                  # Tuple[int, ...]: (W,H) or (W,H,D)
        start_position = map.start         # Tuple[float, ...]
        goal_position = map.goal           # Tuple[float, ...]
        obstacles = map.obstacles          # List of obstacles rectangles: 2D=(x,y,w,h), 3D=(x,y,z,w,h,d)
        is_3d = len(bounds) == 3
        dim = len(bounds)

        # Initialize trees
        tree_start = [Node(start_position)]
        tree_goal = [Node(goal_position)]
        nodes = tree_start[:] + tree_goal[:]
        edges: List[Tuple[Node, Node]] = []

        best_path: List[Tuple[float, ...]] = []
        best_cost = math.inf
        success = False
        start_time = time.time()

        def sample_uniform_free():
            # Attempts at sampling free space uniformly
            for _ in range(30):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p
            # fallback goal position if free sample not found
            return goal_position

        def rotation_to_world(start, goal):
            # Compute rotation matrix from x-axis to (goal - start) direction
            direction = np.array(goal) - np.array(start)
            length = np.linalg.norm(direction)
            if length == 0:
                return np.eye(dim)
            unit = direction / length
            if dim == 2:
                angle = math.atan2(unit[1], unit[0])
                c, s = math.cos(angle), math.sin(angle)
                return np.array([[c, -s], [s, c]])
            elif dim == 3:
                # Align vector to x-axis using Rodrigues' rotation formula
                x_axis = np.array([1.0, 0.0, 0.0])
                v = np.cross(x_axis, unit)
                s = np.linalg.norm(v)
                if s == 0:
                    # vectors are parallel
                    return np.eye(3)
                c = np.dot(x_axis, unit)
                vx = np.array([[0, -v[2], v[1]],
                               [v[2], 0, -v[0]],
                               [-v[1], v[0], 0]])
                R = np.eye(3) + vx + (vx @ vx) * ((1 - c) / (s**2))
                return R
            else:
                # fallback identity for other dims
                return np.eye(dim)

        def informed_sample():
            # Informed sampling within an ellipsoid which shrinks as best path cost improves
            if best_cost == math.inf or best_cost == 0:
                return sample_uniform_free()

            c_min = math.dist(start_position, goal_position)
            c_best = best_cost
            if c_best < c_min:
                return sample_uniform_free()
            center = np.array([(s + g) / 2 for s, g in zip(start_position, goal_position)])
            rot = rotation_to_world(start_position, goal_position)
            # Ellipsoid axes lengths
            if dim == 2:
                a1 = c_best / 2
                a2 = math.sqrt(c_best**2 - c_min**2) / 2
                L = np.diag([a1, a2])
            elif dim == 3:
                a1 = c_best / 2
                val = max(c_best**2 - c_min**2, 0.0)
                a2 = math.sqrt(val) / 2
                a3 = a2
                L = np.diag([a1, a2, a3])
            else:
                # fallback: uniform if dimension unexpected
                return sample_uniform_free()

            for _ in range(30):
                unit_ball = np.random.normal(0, 1, dim)
                unit_ball /= np.linalg.norm(unit_ball)
                r = random.uniform(0, 1) ** (1/dim)
                sample_ball = r * unit_ball
                sample_ellipsoid = rot @ (L @ sample_ball) + center
                sample_pos = tuple(np.clip(sample_ellipsoid, 0, bounds))
                if not self._is_in_obstacle(sample_pos, obstacles, is_3d):
                    return sample_pos
            # fallback
            return sample_uniform_free()

        def sample_free():
            if random.random() < self.goal_sample_rate:
                return goal_position
            if self.informed_sampling_enabled and best_cost < math.inf:
                return informed_sample()
            return sample_uniform_free()

        def steer(from_pos, to_pos):
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dim))

        def near_nodes(tree: List[Node], pos: Tuple[float, ...], n_nodes: int) -> List[Node]:
            # Adaptive neighbor radius (RRT*) formula: gamma * (log(n)/n)^(1/dim)
            gamma = self.step_size * 4.0
            radius = gamma * ((math.log(n_nodes + 1) / (n_nodes + 1)) ** (1/dim))
            radius = min(radius, self.step_size * 50)  # Limit max radius
            r_sq = radius * radius
            return [node for node in tree if (math.dist(node.position, pos)**2) <= r_sq]

        def is_valid_node(pos):
            if not self._within_bounds(pos, bounds):
                return False
            if self._is_in_obstacle(pos, obstacles, is_3d):
                return False
            return True

        def is_valid_edge(from_pos, to_pos):
            if self._is_edge_in_obstacle(from_pos, to_pos, obstacles, is_3d):
                return False
            return True

        def add_node(tree: List[Node], new_node: Node, parent_node: Node):
            parent_node.add_child(new_node)
            new_node.cost = parent_node.cost + math.dist(parent_node.position, new_node.position)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((parent_node, new_node))

        def rewire(tree: List[Node], new_node: Node, neighbors: List[Node]):
            for neighbor in neighbors:
                if neighbor == new_node.parent:
                    continue
                dist = math.dist(new_node.position, neighbor.position)
                new_cost = new_node.cost + dist
                if new_cost < neighbor.cost and is_valid_edge(new_node.position, neighbor.position):
                    neighbor.update_parent(new_node, new_cost)

                    # Update edges list accordingly
                    prev_parent = neighbor.parent
                    # Safe handling edges (avoid double edges)
                    for edge in edges[:]:
                        if edge[1] == neighbor:
                            edges.remove(edge)
                    edges.append((new_node, neighbor))

        def try_extend(tree: List[Node], sample: Tuple[float, ...], other_tree: List[Node]) -> Tuple[Node, bool, Node]:
            # Return (new_node_in_tree, connected_flag, connecting_node_in_other_tree)
            nearest_node = min(tree, key=lambda n: math.dist(n.position, sample))
            new_pos = steer(nearest_node.position, sample)

            if not is_valid_node(new_pos):
                return None, False, None
            if not is_valid_edge(nearest_node.position, new_pos):
                return None, False, None

            neighbors = near_nodes(tree, new_pos, len(nodes) + 1)

            # Choose best parent minimizing cost
            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in neighbors:
                cost_via_neighbor = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost_via_neighbor < min_cost and is_valid_edge(neighbor.position, new_pos):
                    min_cost = cost_via_neighbor
                    best_parent = neighbor

            new_node = Node(new_pos)
            add_node(tree, new_node, best_parent)

            # Rewire neighbors
            rewire(tree, new_node, neighbors)

            # Try connecting from other tree to this new node
            nearest_other = min(other_tree, key=lambda n: math.dist(n.position, new_node.position))
            dist_to_other = math.dist(nearest_other.position, new_node.position)
            if dist_to_other <= self.step_size:
                if is_valid_node(new_node.position) and is_valid_edge(nearest_other.position, new_node.position):
                    neighbors_other = near_nodes(other_tree, new_node.position, len(nodes) +1)

                    # Find best parent in other tree
                    min_cost_other = nearest_other.cost + dist_to_other
                    best_parent_other = nearest_other
                    for neighbor in neighbors_other:
                        cost_try = neighbor.cost + math.dist(neighbor.position, new_node.position)
                        if cost_try < min_cost_other and is_valid_edge(neighbor.position, new_node.position):
                            min_cost_other = cost_try
                            best_parent_other = neighbor

                    connecting_node = Node(new_node.position)
                    add_node(other_tree, connecting_node, best_parent_other)
                    rewire(other_tree, connecting_node, neighbors_other)

                    return new_node, True, connecting_node

            return new_node, False, None

        # Alternate expansion between start and goal trees
        tree_a = tree_start
        tree_b = tree_goal

        for iter_num in range(self.max_iter):
            current_time = time.time()
            if (current_time - start_time) > self.max_time_sec:
                # Time limit reached: return best found path so far
                break

            sample = sample_free()

            new_node_a, connected, connect_node_b = try_extend(tree_a, sample, tree_b)
            if new_node_a is None:
                # swap trees even if failed extension, to keep balanced growth
                tree_a, tree_b = tree_b, tree_a
                continue

            if connected:
                # Construct full path by joining start and goal trees
                path_start = new_node_a.path_from_root()
                path_goal = connect_node_b.path_from_root()
                path_goal.reverse()
                combined_path = path_start + path_goal[1:]

                total_cost = new_node_a.cost + connect_node_b.cost
                if total_cost < best_cost:
                    best_cost = total_cost
                    best_path = combined_path
                    success = True
                    # Shrink informed sampling region
                    self.informed_sampling_enabled = True
                    # Stop immediately on first successful connection
                    break

            # Swap trees for next iteration
            tree_a, tree_b = tree_b, tree_a

        return PlannerResult(
            success=success,
            path=best_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                if len(obs) != 6:
                    continue
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                if len(obs) != 4:
                    continue
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=0.5):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

    def _within_bounds(self, pos, bounds):
        return all(0 <= pos[d] <= bounds[d] for d in range(len(bounds)))

class Node:
    def __init__(self, position: Tuple[float, ...], parent: Optional["Node"]=None, cost: float=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child_node: "Node") -> None:
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child_node: "Node") -> None:
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent: "Node", new_cost: float) -> None:
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(
        self,
        max_iter: int = 7000,
        step_size: float = 4.0,
        goal_sample_rate: float = 0.15,
        neighbor_radius: float = 20.0,
        smoothing_iters: int = 50,
        collision_resolution: float = 0.5,
    ):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius = neighbor_radius
        self.smoothing_iters = smoothing_iters
        self.collision_resolution = collision_resolution

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles

        dimension = len(bounds)
        is_3d = dimension == 3

        # Trees and data structures
        start_tree = [Node(start_position)]
        goal_tree = [Node(goal_position)]
        nodes = start_tree + goal_tree
        edges = []

        success_state = False
        extracted_path: List[Tuple[float, ...]] = []

        # Variables for informed sampling
        c_best = math.inf
        c_min = math.dist(start_position, goal_position)
        x_center = tuple((s + g) / 2.0 for s, g in zip(start_position, goal_position))
        # Rotation matrix from unit vector along x axis to vector from start to goal
        def rotation_to_goal():
            """Calculate rotation matrix to align x-axis with vector from start to goal in dimension D."""
            a1 = np.array(goal_position) - np.array(start_position)
            a1 = a1 / np.linalg.norm(a1)
            Id = np.eye(dimension)
            if dimension == 2:
                # 2D rotation matrix
                angle = math.atan2(a1[1], a1[0])
                c, s = math.cos(angle), math.sin(angle)
                return np.array([[c, -s], [s, c]])
            else:
                # For 3D, use Householder transformation
                e1 = np.zeros(dimension)
                e1[0] = 1.0
                v = a1 - e1
                if np.linalg.norm(v) < 1e-10:
                    return Id
                v = v / np.linalg.norm(v)
                H = Id - 2.0 * np.outer(v, v)
                return H

        rotation_matrix = rotation_to_goal()

        def informed_sample():
            # Sample inside prolate hyperspheroid defined by current best cost c_best
            if c_best == math.inf:
                return uniform_sample()
            r1 = c_best/2.0
            r2 = math.sqrt(c_best**2 - c_min**2) / 2.0

            while True:
                # Sample unit ball in dimension
                if dimension == 2:
                    x = random.uniform(-1, 1)
                    y = random.uniform(-1, 1)
                    if x * x + y * y > 1:
                        continue
                    unit_ball = np.array([x, y])
                else:
                    # Sample 3D unit ball via rejection
                    while True:
                        px = random.uniform(-1,1)
                        py = random.uniform(-1,1)
                        pz = random.uniform(-1,1)
                        if px*px+py*py+pz*pz <= 1:
                            unit_ball = np.array([px, py, pz])
                            break
                L = np.diag([r1] + [r2]*(dimension-1))
                sample_local = L @ unit_ball

                # Rotate and translate sample
                sample_world = rotation_matrix @ sample_local + np.array(x_center)

                point = tuple(np.clip(sample_world, [0]*dimension, bounds))
                if not self._is_in_obstacle(point, obstacles, is_3d):
                    return point

        def uniform_sample():
            while True:
                point = tuple(random.uniform(0, bounds[d]) for d in range(dimension))
                if not self._is_in_obstacle(point, obstacles, is_3d):
                    return point

        def sample():
            # Goal bias applied along with informed sampling when c_best is finite
            if c_best < math.inf:
                p_goal = random.random()
                if p_goal < self.goal_sample_rate:
                    return goal_position
                if random.random() < 0.5:
                    return informed_sample()
                return uniform_sample()
            else:
                if random.random() < self.goal_sample_rate:
                    return goal_position
                return uniform_sample()

        def nearest(tree, point):
            return min(tree, key=lambda n: math.dist(n.position, point))

        def steer(from_pos, to_pos):
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dimension))

        def near_nodes(tree, position):
            return [node for node in tree if math.dist(node.position, position) <= self.neighbor_radius]

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_resolution)

        def try_extend(tree, point):
            nearest_node = nearest(tree, point)
            new_pos = steer(nearest_node.position, point)
            if (
                not self._within_bounds(new_pos, bounds) or
                self._is_in_obstacle(new_pos, obstacles, is_3d) or
                not collision_free_edge(nearest_node.position, new_pos)
            ):
                return None

            neighbors = near_nodes(tree, new_pos)

            # Choose best parent by cost
            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in neighbors:
                cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost < min_cost and collision_free_edge(neighbor.position, new_pos):
                    min_cost = cost
                    best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost

            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors if cheaper via new_node
            for neighbor in neighbors:
                if neighbor == best_parent:
                    continue
                new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
                if new_cost < neighbor.cost and collision_free_edge(new_node.position, neighbor.position):
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                        except ValueError:
                            pass
                        neighbor.parent.remove_child(neighbor)
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

            return new_node

        def incremental_connect(tree, target_pos):
            nearest_node = nearest(tree, target_pos)
            curr_node = nearest_node
            while True:
                new_pos = steer(curr_node.position, target_pos)
                if (
                    not self._within_bounds(new_pos, bounds) or
                    self._is_in_obstacle(new_pos, obstacles, is_3d) or
                    not collision_free_edge(curr_node.position, new_pos)
                ):
                    return None

                neighbors = near_nodes(tree, new_pos)

                min_cost = curr_node.cost + math.dist(curr_node.position, new_pos)
                best_parent = curr_node
                for neighbor in neighbors:
                    cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                    if cost < min_cost and collision_free_edge(neighbor.position, new_pos):
                        min_cost = cost
                        best_parent = neighbor

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost

                tree.append(new_node)
                nodes.append(new_node)
                edges.append((best_parent, new_node))

                for neighbor in neighbors:
                    if neighbor == best_parent:
                        continue
                    new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
                    if new_cost < neighbor.cost and collision_free_edge(new_node.position, neighbor.position):
                        if neighbor.parent:
                            try:
                                edges.remove((neighbor.parent, neighbor))
                            except ValueError:
                                pass
                            neighbor.parent.remove_child(neighbor)
                        neighbor.update_parent(new_node, new_cost)
                        edges.append((new_node, neighbor))

                if math.dist(new_node.position, target_pos) <= self.step_size:
                    return new_node

                curr_node = new_node

        def extract_final_path(node_start: Node, node_goal: Node) -> List[Tuple[float, ...]]:
            path_start = node_start.path_from_root()
            path_goal = node_goal.path_from_root()
            # Avoid duplicate node position at connection
            return path_start + path_goal[::-1][1:]

        def path_smoothing(path: List[Tuple[float, ...]]) -> List[Tuple[float, ...]]:
            if len(path) < 3:
                return path
            new_path = path[:]
            for _ in range(self.smoothing_iters):
                if len(new_path) < 3:
                    break
                i = random.randint(0, len(new_path) - 3)
                j = random.randint(i + 2, len(new_path) - 1)

                p1 = new_path[i]
                p2 = new_path[j]

                if collision_free_edge(p1, p2):
                    # Shortcut by removing intermediate points
                    new_path = new_path[:i + 1] + new_path[j:]
            return new_path

        tree_a, tree_b = start_tree, goal_tree

        for iter_num in range(self.max_iter):
            rand_point = sample()

            new_node_a = try_extend(tree_a, rand_point)
            if not new_node_a:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_node_b = incremental_connect(tree_b, new_node_a.position)
            if new_node_b:
                extracted_path = extract_final_path(new_node_a, new_node_b)
                # Update c_best and informed sampling parameters
                c_best = sum(math.dist(extracted_path[i], extracted_path[i + 1]) for i in range(len(extracted_path) - 1))
                success_state = True
                break

            tree_a, tree_b = tree_b, tree_a

        if success_state and len(extracted_path) > 2:
            extracted_path = path_smoothing(extracted_path)

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

    def _within_bounds(self, pos, bounds):
        return all(0 <= pos[d] <= bounds[d] for d in range(len(bounds)))

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        new_parent.add_child(self)
        self.cost = new_cost
        for child in self.children:
            dist = math.dist(self.position, child.position)
            child.update_parent(self, self.cost + dist)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=3.0, goal_sample_rate: float=0.15, neighbor_radius: float=15.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius = neighbor_radius

    def plan(self, map) -> 'PlannerResult':
        import random
        import math

        bounds = map.size                  # Tuple[int, ...]: (W,H) or (W,H,D)
        start_position = map.start         # Tuple[float, ...] (W,H) or (W,H,D)
        goal_position = map.goal           # Tuple[float, ...] (W,H) or (W,H,D)
        obstacles = map.obstacles          # Rectangular blocks: 2D=(x,y,w,h), 3D=(x,y,z,w,h,d)
        is_3d = len(bounds) == 3
        dimension = len(bounds)

        start_tree = [Node(start_position)]
        goal_tree = [Node(goal_position)]
        nodes = start_tree + goal_tree
        edges = []

        def sample_free(tree_from, tree_to_root):
            if random.random() < self.goal_sample_rate:
                # bias sample towards opposing tree root
                return tree_to_root.position
            while True:
                point = tuple(random.uniform(0, bounds[d]) for d in range(dimension))
                if not self._is_in_obstacle(point, obstacles, is_3d):
                    return point

        def steer(from_pos, to_pos):
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dimension))

        def near_nodes(tree, position):
            return [node for node in tree if math.dist(node.position, position) <= self.neighbor_radius]

        def within_bounds(pos):
            return all(0 <= pos[d] <= bounds[d] for d in range(dimension))

        def extend_and_rewire(tree, sample):
            nearest = min(tree, key=lambda n: math.dist(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not within_bounds(new_pos):
                return None
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
                return None

            neighbors = near_nodes(tree, new_pos)
            min_cost = nearest.cost + math.dist(nearest.position, new_pos)
            best_parent = nearest
            for neighbor in neighbors:
                cost_through = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost_through < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                    min_cost = cost_through
                    best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            for neighbor in neighbors:
                if neighbor == best_parent:
                    continue
                new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
                if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                            neighbor.parent.remove_child(neighbor)
                        except ValueError:
                            pass
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))
            return new_node

        def incremental_connect_and_rewire(tree, target_pos):
            curr_node = min(tree, key=lambda n: math.dist(n.position, target_pos))
            while True:
                new_pos = steer(curr_node.position, target_pos)
                if not within_bounds(new_pos):
                    return None
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    return None
                if self._is_edge_in_obstacle(curr_node.position, new_pos, obstacles, is_3d):
                    return None

                neighbors = near_nodes(tree, new_pos)
                min_cost = curr_node.cost + math.dist(curr_node.position, new_pos)
                best_parent = curr_node
                for neighbor in neighbors:
                    cost_through = neighbor.cost + math.dist(neighbor.position, new_pos)
                    if cost_through < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                        min_cost = cost_through
                        best_parent = neighbor

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)
                nodes.append(new_node)
                edges.append((best_parent, new_node))

                for neighbor in neighbors:
                    if neighbor == best_parent:
                        continue
                    new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
                    if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                        if neighbor.parent:
                            try:
                                edges.remove((neighbor.parent, neighbor))
                                neighbor.parent.remove_child(neighbor)
                            except ValueError:
                                pass
                        neighbor.update_parent(new_node, new_cost)
                        edges.append((new_node, neighbor))

                if math.dist(new_node.position, target_pos) <= self.step_size:
                    return new_node
                curr_node = new_node

        success_state = False
        extracted_path = []

        tree_a, tree_b = start_tree, goal_tree

        for _ in range(self.max_iter):
            sample_point = sample_free(tree_a[0], tree_b[0])
            new_node_a = extend_and_rewire(tree_a, sample_point)
            if new_node_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_node_b = incremental_connect_and_rewire(tree_b, new_node_a.position)
            if new_node_b is not None:
                path_a = new_node_a.path_from_root()
                path_b = new_node_b.path_from_root()
                if path_b and path_b[0] == new_node_a.position:
                    path_b = path_b[1:]
                extracted_path = path_a + path_b[::-1]
                success_state = True
                break

            tree_a, tree_b = tree_b, tree_a

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.15, neighbor_radius: float = 15.0,
                 max_time_sec: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius = neighbor_radius
        self.max_time_sec = max_time_sec

    def plan(self, map) -> 'PlannerResult':
        import time
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def within_bounds(pos):
            return all(0 <= pos[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def nearest(tree, point):
            return min(tree, key=lambda n: dist(n.position, point))

        def near_nodes(tree, position):
            radius = self.neighbor_radius
            return [n for n in tree if dist(n.position, position) <= radius]

        def collision_free_node(pos):
            return within_bounds(pos) and not self._is_in_obstacle(pos, obstacles, is_3d)

        def collision_free_edge(from_pos, to_pos):
            return not self._is_edge_in_obstacle(from_pos, to_pos, obstacles, is_3d, resolution=self.step_size/5)

        # For informed sampling ellipsoid initialization
        c_min = dist(start_pos, goal_pos)
        best_path_cost = math.inf

        # Initial trees (start and goal)
        tree_start = [Node(start_pos)]
        tree_goal = [Node(goal_pos)]
        all_nodes = tree_start + tree_goal
        edges = []

        start_time = time.perf_counter()
        grow_start = True  # Alternate tree grow

        # Enables informed sampling after first solution found
        informed_sampling_enabled = False

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a = math.cos(angle)
                sin_a = math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                # Rodrigues' rotation formula to rotate direction to x-axis
                vx, vy, vz = direction
                k = (-0.0, -vy, -vz)
                k_norm = math.sqrt(k[0]**2 + k[1]**2 + k[2]**2)
                if k_norm < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                k = tuple(ki / k_norm for ki in k)
                cos_theta = vx
                sin_theta = k_norm
                Kx, Ky, Kz = k
                K = [[0, -Kz, Ky], [Kz, 0, -Kx], [-Ky, Kx, 0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A,B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A,s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A,B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K,K)
                R = mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))
                return R

        def clamp_to_bounds(pos):
            return tuple(
                max(0.0, min(bounds[d], pos[d])) for d in range(dim)
            )

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(pt, obstacles, is_3d):
                    return pt

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-8:
                return sample_free()

            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best**2 - c_min**2, 0.0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best**2 - c_min**2, 0.0)) / 2.0
                radii = [a1, a_minor, a_minor]

            def unit_ball_sample(n):
                while True:
                    pt = [random.uniform(-1,1) for _ in range(n)]
                    norm_sq = sum(x*x for x in pt)
                    if norm_sq <= 1.0 and norm_sq > 1e-10:
                        norm = math.sqrt(norm_sq)
                        return tuple(x / norm for x in pt)

            while True:
                n = dim
                direction_sample = unit_ball_sample(n)
                radius_sample = random.uniform(0,1) ** (1.0/n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    sample_rot = (x_rot, y_rot)
                else:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1] + R[0][2]*sample_scaled[2]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1] + R[1][2]*sample_scaled[2]
                    z_rot = R[2][0]*sample_scaled[0] + R[2][1]*sample_scaled[1] + R[2][2]*sample_scaled[2]
                    sample_rot = (x_rot, y_rot, z_rot)

                sample_point = tuple(center[d] + sample_rot[d] for d in range(n))
                sample_point = clamp_to_bounds(sample_point)

                if not self._is_in_obstacle(sample_point, obstacles, is_3d):
                    return sample_point

        def rewire(tree, new_node, neighbors):
            for neighbor in neighbors:
                if neighbor is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_through_new < neighbor.cost and collision_free_edge(new_node.position, neighbor.position):
                    neighbor.update_parent(new_node, cost_through_new)
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except Exception:
                        pass
                    edges.append((new_node, neighbor))

        def extend(tree, target_point):
            nearest_node = nearest(tree, target_point)
            new_pos = steer(nearest_node.position, target_point)
            new_pos = clamp_to_bounds(new_pos)

            if not collision_free_node(new_pos):
                return None
            if not collision_free_edge(nearest_node.position, new_pos):
                return None

            neighbors = near_nodes(tree, new_pos)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node

            for neighbor in neighbors:
                tentative_cost = neighbor.cost + dist(neighbor.position, new_pos)
                if tentative_cost < min_cost and collision_free_edge(neighbor.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            all_nodes.append(new_node)
            edges.append((best_parent, new_node))

            rewire(tree, new_node, neighbors)
            return new_node

        def try_connect(tree, from_node):
            nearest_node = nearest(tree, from_node.position)
            d = dist(nearest_node.position, from_node.position)
            if d <= self.step_size and collision_free_node(from_node.position) and collision_free_edge(nearest_node.position, from_node.position):
                return nearest_node
            return None

        while True:
            iter_count = 0
            while iter_count < self.max_iter:
                if time.perf_counter() - start_time > self.max_time_sec:
                    # Return best path found so far
                    # Try connecting trees to form final path if possible
                    best_path = []
                    success_flag = False
                    min_cost = math.inf
                    for n_s in tree_start:
                        for n_g in tree_goal:
                            if n_s.position == n_g.position:
                                cost_sum = n_s.cost + n_g.cost
                                if cost_sum < min_cost:
                                    min_cost = cost_sum
                                    path_s = n_s.path_from_root()
                                    path_g = n_g.path_from_root()
                                    best_path = path_s + path_g[::-1][1:]
                                    success_flag = True
                    return PlannerResult(success=success_flag, path=best_path, nodes=all_nodes, edges=edges)

                iter_count += 1
                sample = None
                if informed_sampling_enabled:
                    sample = informed_sample(best_path_cost)
                else:
                    if random.random() < self.goal_sample_rate:
                        sample = goal_pos if grow_start else start_pos
                    else:
                        sample = sample_free()

                if grow_start:
                    new_node = extend(tree_start, sample)
                    if new_node is None:
                        grow_start = not grow_start
                        continue

                    connect_node = try_connect(tree_goal, new_node)
                    if connect_node:
                        # Connect new_node to tree_goal, add connecting node
                        neighbors = near_nodes(tree_goal, new_node.position)
                        min_cost_local = connect_node.cost + dist(connect_node.position, new_node.position)
                        best_parent_local = connect_node
                        for neighbor in neighbors:
                            tentative_cost = neighbor.cost + dist(neighbor.position, new_node.position)
                            if tentative_cost < min_cost_local and collision_free_edge(neighbor.position, new_node.position):
                                min_cost_local = tentative_cost
                                best_parent_local = neighbor

                        connecting_node = Node(new_node.position)
                        best_parent_local.add_child(connecting_node)
                        connecting_node.cost = min_cost_local
                        tree_goal.append(connecting_node)
                        all_nodes.append(connecting_node)
                        edges.append((best_parent_local, connecting_node))

                        rewire(tree_goal, connecting_node, neighbors)

                        # Construct combined path
                        path_start = new_node.path_from_root()
                        path_goal = connecting_node.path_from_root()
                        extracted_path = path_start + path_goal[::-1][1:]
                        success_state = True
                        if dist(start_pos, goal_pos) < best_path_cost:
                            best_path_cost = dist(start_pos, goal_pos)

                        informed_sampling_enabled = True
                        return PlannerResult(success=success_state, path=extracted_path, nodes=all_nodes, edges=edges)
                else:
                    new_node = extend(tree_goal, sample)
                    if new_node is None:
                        grow_start = not grow_start
                        continue

                    connect_node = try_connect(tree_start, new_node)
                    if connect_node:
                        neighbors = near_nodes(tree_start, new_node.position)
                        min_cost_local = connect_node.cost + dist(connect_node.position, new_node.position)
                        best_parent_local = connect_node
                        for neighbor in neighbors:
                            tentative_cost = neighbor.cost + dist(neighbor.position, new_node.position)
                            if tentative_cost < min_cost_local and collision_free_edge(neighbor.position, new_node.position):
                                min_cost_local = tentative_cost
                                best_parent_local = neighbor

                        connecting_node = Node(new_node.position)
                        best_parent_local.add_child(connecting_node)
                        connecting_node.cost = min_cost_local
                        tree_start.append(connecting_node)
                        all_nodes.append(connecting_node)
                        edges.append((best_parent_local, connecting_node))

                        rewire(tree_start, connecting_node, neighbors)

                        path_start = connecting_node.path_from_root()
                        path_goal = new_node.path_from_root()
                        extracted_path = path_start + path_goal[::-1][1:]
                        success_state = True
                        if dist(start_pos, goal_pos) < best_path_cost:
                            best_path_cost = dist(start_pos, goal_pos)

                        informed_sampling_enabled = True
                        return PlannerResult(success=success_state, path=extracted_path, nodes=all_nodes, edges=edges)

                grow_start = not grow_start

            break  # End outer while after max_iter

        return PlannerResult(success=False, path=[], nodes=all_nodes, edges=edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent: Optional['Node']=None, cost: float=0.0):
        self.position = position
        self.parent = None
        self.cost = cost
        self.children = []
        self.valid = True
        if parent:
            self.update_parent(parent, cost)

    def add_child(self, child_node: 'Node'):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node: 'Node'):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int=5000, step_size: float=4.0,
                 goal_sample_rate: float=0.1,
                 neighbor_radius_min: float=10.0,
                 neighbor_radius_max: float=30.0,
                 time_limit_sec: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.time_limit_sec = time_limit_sec

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dimension = len(bounds)

        start_tree = [Node(start_position, None, 0.0)]
        goal_tree = [Node(goal_position, None, 0.0)]
        nodes = start_tree + goal_tree
        edges: List[Tuple[Node, Node]] = []

        success_state = False
        extracted_path: List[Tuple[float, ...]] = []

        tree_a, tree_b = start_tree, goal_tree
        start_time = time.time()
        best_connection_nodes: Optional[Tuple[Node, Node]] = None
        best_cost = math.inf

        def _adaptive_neighbor_radius(n_nodes: int) -> float:
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt_star = 50.0
            radius = gamma_rrt_star * (math.log(n_nodes) / n_nodes) ** (1/dimension)
            radius = max(self.neighbor_radius_min, min(radius, self.neighbor_radius_max))
            return radius

        def _sample_free() -> Tuple[float, ...]:
            if random.random() < self.goal_sample_rate:
                return goal_position if tree_a is start_tree else start_position
            while True:
                p = tuple(random.uniform(0, bounds[d]) for d in range(dimension))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p

        def _nearest(tree: List[Node], p: Tuple[float, ...]) -> Node:
            return min(tree, key=lambda node: math.dist(node.position, p))

        def _steer(from_pos: Tuple[float, ...], to_pos: Tuple[float, ...]) -> Tuple[float, ...]:
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dimension))

        def _near_nodes(tree: List[Node], position: Tuple[float, ...], radius: float) -> List[Node]:
            return [n for n in tree if math.dist(n.position, position) <= radius]

        def _extend_and_rewire(tree: List[Node], sample: Tuple[float, ...]) -> Optional[Node]:
            nearest_node = _nearest(tree, sample)
            new_pos = _steer(nearest_node.position, sample)

            if not self._in_bounds(new_pos, bounds):
                return None
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return None
            if self._is_edge_in_obstacle(nearest_node.position, new_pos, obstacles, is_3d):
                return None

            n_nodes = len(tree)
            radius = _adaptive_neighbor_radius(n_nodes)
            neighbors = _near_nodes(tree, new_pos, radius)

            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in neighbors:
                cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                    min_cost = cost
                    best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost

            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            for neighbor in neighbors:
                if neighbor is best_parent:
                    continue
                new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
                if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                            neighbor.parent.remove_child(neighbor)
                        except ValueError:
                            pass
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

            return new_node

        def _incremental_connect_and_rewire(tree: List[Node], target_pos: Tuple[float, ...]) -> Optional[Node]:
            nearest_node = _nearest(tree, target_pos)
            current_node = nearest_node
            dist_total = math.dist(current_node.position, target_pos)
            max_steps = int(dist_total / self.step_size) + 2

            for _ in range(max_steps):
                new_pos = _steer(current_node.position, target_pos)

                if not self._in_bounds(new_pos, bounds):
                    return None
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    return None
                if self._is_edge_in_obstacle(current_node.position, new_pos, obstacles, is_3d):
                    return None

                n_nodes = len(tree)
                radius = _adaptive_neighbor_radius(n_nodes)
                neighbors = _near_nodes(tree, new_pos, radius)

                min_cost = current_node.cost + math.dist(current_node.position, new_pos)
                best_parent = current_node
                for neighbor in neighbors:
                    cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                    if cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                        min_cost = cost
                        best_parent = neighbor

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost

                tree.append(new_node)
                nodes.append(new_node)
                edges.append((best_parent, new_node))

                for neighbor in neighbors:
                    if neighbor is best_parent:
                        continue
                    new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
                    if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                        if neighbor.parent:
                            try:
                                edges.remove((neighbor.parent, neighbor))
                                neighbor.parent.remove_child(neighbor)
                            except ValueError:
                                pass
                        neighbor.update_parent(new_node, new_cost)
                        edges.append((new_node, neighbor))

                if math.dist(new_node.position, target_pos) <= self.step_size:
                    return new_node
                current_node = new_node

            return None

        def _path_smoothing(path: List[Tuple[float, ...]], max_iter=100) -> List[Tuple[float, ...]]:
            if len(path) < 3:
                return path[:]
            length = len(path)
            for _ in range(max_iter):
                if length <= 2:
                    break
                i = random.randint(0, length - 3)
                j = random.randint(i + 2, length - 1)
                if not self._is_edge_in_obstacle(path[i], path[j], obstacles, is_3d):
                    path = path[:i+1] + path[j:]
                    length = len(path)
            return path

        def _build_final_path(node_a: Node, node_b: Node) -> List[Tuple[float, ...]]:
            path_a = node_a.path_from_root()
            path_b = node_b.path_from_root()
            # Remove duplicate node between paths
            return path_a + path_b[::-1][1:]

        iter_count = 0
        while iter_count < self.max_iter:
            elapsed = time.time() - start_time
            if elapsed > self.time_limit_sec:
                # Time limit reached, return best path found if any
                if best_connection_nodes is not None:
                    node_a, node_b = best_connection_nodes
                    path = _build_final_path(node_a, node_b)
                    smoothed = _path_smoothing(path)
                    extracted_path = smoothed
                    success_state = True
                break

            sample = _sample_free()
            new_node_a = _extend_and_rewire(tree_a, sample)
            if new_node_a is None:
                tree_a, tree_b = tree_b, tree_a
                iter_count += 1
                continue

            new_node_b = _incremental_connect_and_rewire(tree_b, new_node_a.position)
            if new_node_b:
                total_cost = new_node_a.cost + new_node_b.cost + math.dist(new_node_a.position, new_node_b.position)
                if total_cost < best_cost:
                    best_cost = total_cost
                    best_connection_nodes = (new_node_a, new_node_b)
                    path = _build_final_path(new_node_a, new_node_b)
                    smoothed = _path_smoothing(path)
                    extracted_path = smoothed
                    success_state = True
                    # break for performance - comment to allow further improvements
                    break

            tree_a, tree_b = tree_b, tree_a
            iter_count += 1

        if not success_state and best_connection_nodes is not None:
            node_a, node_b = best_connection_nodes
            path = _build_final_path(node_a, node_b)
            smoothed = _path_smoothing(path)
            extracted_path = smoothed
            success_state = True

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

    def _in_bounds(self, pos: Tuple[float, ...], bounds: Tuple[int, ...]) -> bool:
        return all(0 <= pos[d] <= bounds[d] for d in range(len(pos)))

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = parent            # Node or None
        self.cost = cost                # Cost from root
        self.children = []
        self.valid = True               # Node validity

    def add_child(self, child_node):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        for child in self.children:
            child.update_parent(self, self.cost + math.dist(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float=4.0,
                 goal_sample_rate: float=0.15, min_neighbor_radius: float=5.0,
                 max_neighbor_radius: float=25.0, max_time_sec: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.max_time_sec = max_time_sec

    def plan(self, map) -> 'PlannerResult':
        import time
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)

        def sample_free(towards_goal: bool):
            # towards_goal: True when sampling for start tree (towards goal), False vice versa
            if random.random() < self.goal_sample_rate:
                return goal_pos if towards_goal else start_pos
            for _ in range(30):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if self._within_bounds(p, bounds) and not self._is_in_obstacle(p, obstacles, is_3d):
                    return p
            return goal_pos if towards_goal else start_pos

        def steer(from_p, to_p):
            dist = math.dist(from_p, to_p)
            if dist <= self.step_size:
                return to_p
            ratio = self.step_size / dist
            return tuple(from_p[d] + (to_p[d] - from_p[d]) * ratio for d in range(dim))

        def near_nodes(tree, pos, radius):
            r_sq = radius * radius
            return [n for n in tree if (math.dist(n.position, pos) ** 2) <= r_sq]

        def valid_node(pos):
            return self._within_bounds(pos, bounds) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def valid_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d)

        def adaptive_radius(node_count):
            if node_count <= 1:
                return self.max_neighbor_radius
            gamma = 20.0  # a tuning parameter for radius scaling
            radius = gamma * ((math.log(node_count) / node_count) ** (1.0 / dim))
            return max(self.min_neighbor_radius, min(radius, self.max_neighbor_radius))

        def add_node(tree, pos, parent_node, cost):
            new_node = Node(pos)
            parent_node.add_child(new_node)
            new_node.cost = cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((parent_node, new_node))
            return new_node

        def rewire(tree, new_node, neighbors):
            for neighbor in neighbors:
                if neighbor is new_node.parent:
                    continue
                dist = math.dist(new_node.position, neighbor.position)
                new_cost = new_node.cost + dist
                if new_cost < neighbor.cost and valid_edge(new_node.position, neighbor.position):
                    if neighbor.parent is not None:
                        if (neighbor.parent, neighbor) in edges:
                            edges.remove((neighbor.parent, neighbor))
                        neighbor.parent.remove_child(neighbor)
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

        def extend_and_rewire(tree, sample, towards_goal):
            if not tree:
                return None
            nearest_node = min(tree, key=lambda n: math.dist(n.position, sample))
            new_pos = steer(nearest_node.position, sample)
            if not valid_node(new_pos) or not valid_edge(nearest_node.position, new_pos):
                return None

            radius = adaptive_radius(len(tree))
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node

            for neighbor in neighbors:
                cost_through = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost_through < min_cost and valid_edge(neighbor.position, new_pos):
                    min_cost = cost_through
                    best_parent = neighbor

            new_node = add_node(tree, new_pos, best_parent, min_cost)
            rewire(tree, new_node, neighbors)
            return new_node

        def incremental_connect(tree, target_pos):
            if not tree:
                return None
            nearest_node = min(tree, key=lambda n: math.dist(n.position, target_pos))
            curr = nearest_node
            while True:
                new_pos = steer(curr.position, target_pos)
                if not valid_node(new_pos) or not valid_edge(curr.position, new_pos):
                    return None

                radius = adaptive_radius(len(tree))
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = curr.cost + math.dist(curr.position, new_pos)
                best_parent = curr
                for neighbor in neighbors:
                    cost_through = neighbor.cost + math.dist(neighbor.position, new_pos)
                    if cost_through < min_cost and valid_edge(neighbor.position, new_pos):
                        min_cost = cost_through
                        best_parent = neighbor

                new_node = add_node(tree, new_pos, best_parent, min_cost)
                rewire(tree, new_node, neighbors)

                if math.dist(new_node.position, target_pos) <= self.step_size:
                    return new_node

                curr = new_node

        def path_smoothing(raw_path, iterations=80):
            if len(raw_path) <= 2:
                return raw_path[:]
            path = raw_path[:]
            for _ in range(iterations):
                if len(path) <= 2:
                    break
                i = random.randint(0, len(path) - 2)
                j = random.randint(i + 1, len(path) -1)
                if j == i + 1:
                    continue
                p1 = path[i]
                p2 = path[j]
                if valid_edge(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path


        import time

        start_time = time.time()

        start_tree = [Node(start_pos)]
        goal_tree = [Node(goal_pos)]
        nodes = start_tree[:] + goal_tree[:]
        edges = []

        tree_a, tree_b = start_tree, goal_tree
        success = False
        best_path = []
        best_cost = math.inf

        for _ in range(self.max_iter):
            if (time.time() - start_time) > self.max_time_sec:
                break  # Stop on timeout

            sample = sample_free(towards_goal=True)  # Always sample toward goal for start_tree
            new_node_a = extend_and_rewire(tree_a, sample, towards_goal=True)

            if new_node_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_node_b = incremental_connect(tree_b, new_node_a.position)

            if new_node_b:
                # Connected trees, build combined path
                path_from_start = new_node_a.path_from_root()
                path_from_goal = new_node_b.path_from_root()
                combined = path_from_start + path_from_goal[::-1][1:]
                total_cost = new_node_a.cost + new_node_b.cost

                if total_cost < best_cost:
                    best_cost = total_cost
                    best_path = path_smoothing(combined, iterations=100)
                    success = True

                break

            tree_a, tree_b = tree_b, tree_a

        return PlannerResult(
            success=success,
            path=best_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

    def _within_bounds(self, pos, bounds):
        return all(0 <= pos[d] <= bounds[d] for d in range(len(bounds)))

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] 2D or 3D coordinate
        self.parent = parent            # Parent Node or None
        self.cost = cost                # Cost from root to this node
        self.children = []              # List of child nodes

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
