        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 goal_sample_rate: float=0.15,
                 min_neighbor_radius: float=8.0,
                 max_neighbor_radius: float=30.0,
                 time_limit_sec: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.time_limit_sec = time_limit_sec

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def sample_free(towards_goal_root):
            if random.random() < self.goal_sample_rate:
                return goal_pos if towards_goal_root else start_pos
            while True:
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            ratio = self.step_size / d
            return tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))

        def nearest(tree, point):
            return min(tree, key=lambda node: dist(node.position, point))

        def near_nodes(tree, position, radius):
            return [node for node in tree if dist(node.position, position) <= radius]

        def adaptive_neighbor_radius(n_nodes):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            gamma = 20.0
            radius = gamma * ((math.log(n_nodes) / n_nodes) ** (1/dim))
            return max(self.min_neighbor_radius, min(self.max_neighbor_radius, radius))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=0.5)

        def rewire(tree, new_node, neighbors):
            for nbr in neighbors:
                if nbr == new_node.parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, nbr.position)
                if new_cost + 1e-6 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, new_cost)

        def extend(tree, target_point, nodes: List[Node], edges: List[Tuple[Node, Node]]):
            nearest_node = nearest(tree, target_point)
            new_pos = steer(nearest_node.position, target_point)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest_node.position, new_pos):
                return None

            n_radius = adaptive_neighbor_radius(len(tree)+1)
            neighbors = near_nodes(tree, new_pos, n_radius)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for nbr in neighbors:
                if nbr == nearest_node:
                    continue
                cost_thru_nbr = nbr.cost + dist(nbr.position, new_pos)
                if cost_thru_nbr + 1e-6 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_thru_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            rewire(tree, new_node, neighbors)
            return new_node

        def try_connect_trees(new_node, other_tree, nodes, edges):
            curr_node = nearest(other_tree, new_node.position)

            while True:
                new_pos = steer(curr_node.position, new_node.position)
                if (not collision_free_node(new_pos) or not collision_free_edge(curr_node.position, new_pos)):
                    return None

                n_radius = adaptive_neighbor_radius(len(other_tree)+1)
                neighbors = near_nodes(other_tree, new_pos, n_radius)

                min_cost = curr_node.cost + dist(curr_node.position, new_pos)
                best_parent = curr_node
                for nbr in neighbors:
                    if nbr == curr_node:
                        continue
                    cost_thru_nbr = nbr.cost + dist(nbr.position, new_pos)
                    if cost_thru_nbr + 1e-6 < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_thru_nbr
                        best_parent = nbr

                connecting_node = Node(new_pos)
                best_parent.add_child(connecting_node)
                connecting_node.cost = min_cost
                other_tree.append(connecting_node)
                nodes.append(connecting_node)
                edges.append((best_parent, connecting_node))

                rewire(other_tree, connecting_node, neighbors)

                if dist(connecting_node.position, new_node.position) <= self.step_size:
                    if collision_free_edge(connecting_node.position, new_node.position):
                        return connecting_node
                curr_node = connecting_node

        def smooth_path(path, max_trials=100):
            if len(path) < 3:
                return path[:]

            def can_connect(p1, p2):
                return collision_free_edge(p1, p2)

            smoothed = path[:]
            for _ in range(max_trials):
                if len(smoothed) < 3:
                    break
                i = random.randint(0, len(smoothed)-3)
                j = random.randint(i+2, len(smoothed)-1)
                if can_connect(smoothed[i], smoothed[j]):
                    smoothed = smoothed[:i+1] + smoothed[j:]
            return smoothed

        # Initialization
        start_tree = [Node(start_pos, cost=0.0)]
        goal_tree = [Node(goal_pos, cost=0.0)]
        nodes = start_tree + goal_tree[:]
        edges = []

        tree_a, tree_b = start_tree, goal_tree
        success_state = False
        extracted_path = []

        start_time = time.time()

        for _ in range(self.max_iter):
            if time.time() - start_time > self.time_limit_sec:
                # Time limit exceeded - return best found path so far or empty
                break

            sample = goal_pos if (random.random() < self.goal_sample_rate) else tuple(random.uniform(0, bounds[d]) for d in range(dim))

            nearest_node = nearest(tree_a, sample)
            new_pos = steer(nearest_node.position, sample)

            if (not collision_free_node(new_pos) or not collision_free_edge(nearest_node.position, new_pos)):
                tree_a, tree_b = tree_b, tree_a
                continue

            n_radius = adaptive_neighbor_radius(len(tree_a) + 1)
            neighbors = near_nodes(tree_a, new_pos, n_radius)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for nbr in neighbors:
                if nbr == nearest_node:
                    continue
                c = nbr.cost + dist(nbr.position, new_pos)
                if c + 1e-6 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = c
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            rewire(tree_a, new_node, neighbors)

            connect_node = try_connect_trees(new_node, tree_b, nodes, edges)

            if connect_node:
                path_a = new_node.path_from_root()
                path_b = connect_node.path_from_root()
                if path_b and path_b[0] == new_node.position:
                    path_b = path_b[1:]
                full_path = path_a + path_b[::-1]
                extracted_path = smooth_path(full_path)
                success_state = True
                break

            tree_a, tree_b = tree_b, tree_a  # Alternate expansion

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent: Optional['Node']=None, cost: float=0.0):
        self.position = position
        self.parent = None
        self.cost = cost
        self.children = []
        self.valid = True
        if parent:
            self.update_parent(parent, cost)

    def add_child(self, child_node: 'Node'):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node: 'Node'):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        # Detach from old parent
        if self.parent is not None:
            self.parent.remove_child(self)
        # Attach to new parent
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0, goal_sample_rate: float=0.1, 
                 neighbor_radius_min: float=10.0, neighbor_radius_max: float=30.0, time_limit_sec: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.time_limit_sec = time_limit_sec

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        start_tree = [Node(start_position, None, 0.0)]
        goal_tree = [Node(goal_position, None, 0.0)]
        nodes = start_tree + goal_tree
        edges = []

        success_state = False
        extracted_path: List[Tuple[float, ...]] = []

        tree_a, tree_b = start_tree, goal_tree  # alternate growing trees
        start_time = time.time()
        last_best_cost = math.inf
        best_connection_nodes = None

        for iter_count in range(self.max_iter):
            if (time.time() - start_time) > self.time_limit_sec:
                # Time exceeded: break and return best path so far if any
                if best_connection_nodes is not None:
                    node_a, node_b = best_connection_nodes
                    path_a = node_a.path_from_root()
                    path_b = node_b.path_from_root()
                    extracted_path = self._path_smoothing(path_a + path_b[::-1], obstacles, is_3d)
                    success_state = True
                break

            # Goal biased sampling
            if (math.isclose(self.goal_sample_rate, 0.0) == False) and (math.isclose(self.goal_sample_rate, 1.0) == False):
                if math.isclose(self.goal_sample_rate, 0.0):
                    sample = self._sample_free(bounds, obstacles, is_3d)
                else:
                    import random
                    if random.random() < self.goal_sample_rate:
                        sample = goal_position if tree_a is start_tree else start_position
                    else:
                        sample = self._sample_free(bounds, obstacles, is_3d)
            else:
                # goal_sample_rate=0 or 1 handled simply
                sample = goal_position if tree_a is start_tree else start_position if self.goal_sample_rate==1 else self._sample_free(bounds, obstacles, is_3d)

            new_node_a = self._extend_and_rewire(tree_a, sample, obstacles, is_3d, nodes, edges, bounds)
            if new_node_a is None:
                tree_a, tree_b = tree_b, tree_a
                continue

            new_node_b = self._incremental_connect_and_rewire(tree_b, new_node_a.position, obstacles, is_3d, nodes, edges, bounds)
            if new_node_b:
                total_cost = new_node_a.cost + new_node_b.cost + math.dist(new_node_a.position, new_node_b.position)
                if total_cost < last_best_cost:
                    last_best_cost = total_cost
                    best_connection_nodes = (new_node_a, new_node_b)
                    # Extract path now to allow early return
                    path_a = new_node_a.path_from_root()
                    path_b = new_node_b.path_from_root()
                    extracted_path = self._path_smoothing(path_a + path_b[::-1], obstacles, is_3d)
                    success_state = True
                    break

            tree_a, tree_b = tree_b, tree_a

        if not success_state and best_connection_nodes is not None:
            node_a, node_b = best_connection_nodes
            path_a = node_a.path_from_root()
            path_b = node_b.path_from_root()
            extracted_path = self._path_smoothing(path_a + path_b[::-1], obstacles, is_3d)
            success_state = True

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _adaptive_neighbor_radius(self, n_nodes: int) -> float:
        # RRT* theory: radius ~ gamma*(log(n)/n)^{1/d}
        if n_nodes <= 1:
            return self.neighbor_radius_max
        d = 2  # default dimension, adjusted in usage
        # Let's keep dimension dynamic based on start node position length
        # but fallback safely to 2D dimensional radius
        return max(self.neighbor_radius_min, min(self.neighbor_radius_max, 50.0 * (math.log(n_nodes) / n_nodes)**(1/d)))

    def _sample_free(self, bounds, obstacles, is_3d) -> Tuple[float, ...]:
        import random
        dims = len(bounds)
        while True:
            point = tuple(random.uniform(0, bounds[d]) for d in range(dims))
            if not self._is_in_obstacle(point, obstacles, is_3d):
                return point

    def _nearest(self, tree: List[Node], point: Tuple[float, ...]) -> Node:
        return min(tree, key=lambda node: math.dist(node.position, point))

    def _steer(self, from_pos: Tuple[float, ...], to_pos: Tuple[float, ...]) -> Tuple[float, ...]:
        dist = math.dist(from_pos, to_pos)
        if dist <= self.step_size:
            return to_pos
        ratio = self.step_size / dist
        return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(len(from_pos)))

    def _near_nodes(self, tree: List[Node], position: Tuple[float, ...], radius: float) -> List[Node]:
        return [node for node in tree if math.dist(node.position, position) <= radius]

    def _extend_and_rewire(self,
                          tree: List[Node],
                          point: Tuple[float, ...],
                          obstacles,
                          is_3d: bool,
                          nodes: List[Node],
                          edges: List[Tuple[Node, Node]],
                          bounds: Tuple[int, ...]) -> Optional[Node]:
        nearest = self._nearest(tree, point)
        new_pos = self._steer(nearest.position, point)

        if not self._in_bounds(new_pos, bounds):
            return None

        if self._is_in_obstacle(new_pos, obstacles, is_3d) or self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
            return None

        new_node = Node(new_pos)
        n_nodes = len(tree)
        radius = self._adaptive_neighbor_radius(n_nodes)

        neighbors = self._near_nodes(tree, new_pos, radius)

        min_cost = nearest.cost + math.dist(nearest.position, new_pos)
        best_parent = nearest
        for neighbor in neighbors:
            cost = neighbor.cost + math.dist(neighbor.position, new_pos)
            if cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                min_cost = cost
                best_parent = neighbor

        best_parent.add_child(new_node)
        new_node.cost = min_cost
        tree.append(new_node)
        nodes.append(new_node)
        edges.append((best_parent, new_node))

        # Rewire neighbors if better cost available
        for neighbor in neighbors:
            if neighbor == best_parent:
                continue
            new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
            if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                if neighbor.parent:
                    try:
                        edges.remove((neighbor.parent, neighbor))
                        neighbor.parent.remove_child(neighbor)
                    except ValueError:
                        pass
                neighbor.update_parent(new_node, new_cost)
                edges.append((new_node, neighbor))

        return new_node

    def _incremental_connect_and_rewire(self,
                                      tree: List[Node],
                                      target_pos: Tuple[float, ...],
                                      obstacles,
                                      is_3d: bool,
                                      nodes: List[Node],
                                      edges: List[Tuple[Node, Node]],
                                      bounds: Tuple[int, ...]) -> Optional[Node]:
        nearest = self._nearest(tree, target_pos)
        curr_node = nearest

        max_steps = int(math.dist(curr_node.position, target_pos) / self.step_size) + 2
        for _ in range(max_steps):
            new_pos = self._steer(curr_node.position, target_pos)
            if not self._in_bounds(new_pos, bounds):
                return None

            if self._is_in_obstacle(new_pos, obstacles, is_3d) or self._is_edge_in_obstacle(curr_node.position, new_pos, obstacles, is_3d):
                return None

            new_node = Node(new_pos)
            n_nodes = len(tree)
            radius = self._adaptive_neighbor_radius(n_nodes)
            neighbors = self._near_nodes(tree, new_pos, radius)

            min_cost = curr_node.cost + math.dist(curr_node.position, new_pos)
            best_parent = curr_node
            for neighbor in neighbors:
                cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                    min_cost = cost
                    best_parent = neighbor

            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for neighbor in neighbors:
                if neighbor == best_parent:
                    continue
                new_cost = new_node.cost + math.dist(new_node.position, neighbor.position)
                if new_cost < neighbor.cost and not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d):
                    if neighbor.parent:
                        try:
                            edges.remove((neighbor.parent, neighbor))
                            neighbor.parent.remove_child(neighbor)
                        except ValueError:
                            pass
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

            if math.dist(new_node.position, target_pos) <= self.step_size:
                return new_node

            curr_node = new_node

        return None

    def _path_smoothing(self,
                        path: List[Tuple[float, ...]],
                        obstacles,
                        is_3d: bool,
                        max_iterations: int = 100) -> List[Tuple[float, ...]]:
        if len(path) < 3:
            return path[:]

        import random
        path = path[:]
        length = len(path)
        for _ in range(max_iterations):
            if length <= 2:
                break
            i = random.randint(0, length - 3)
            j = random.randint(i + 2, length - 1)
            if not self._is_edge_in_obstacle(path[i], path[j], obstacles, is_3d):
                # Shortcut possible: remove intermediate points between i and j
                path = path[:i + 1] + path[j:]
                length = len(path)
        return path

    def _is_in_obstacle(self, pos: Tuple[float, ...], obstacles, is_3d: bool) -> bool:
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos: Tuple[float, ...], to_pos: Tuple[float, ...], obstacles, is_3d: bool, resolution: float=1.0) -> bool:
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

    def _in_bounds(self, pos: Tuple[float, ...], bounds: Tuple[int, ...]) -> bool:
        return all(0 <= pos[d] <= bounds[d] for d in range(len(pos)))
