class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D or 3D coordinates
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checking

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_radius: float=5.0, max_radius: float=30.0,
                 prune_interval: int=200, prune_threshold: int=300,
                 smoothing_iters: int=150,
                 collision_resolution: float=1.0,
                 goal_sample_rate: float=0.15):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_radius = min_radius
        self.max_radius = max_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iters = smoothing_iters
        self.collision_resolution = collision_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> 'PlannerResult':
        import random
        import math
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(frm, to):
            d = dist(frm, to)
            if d <= self.step_size:
                new_p = to
            else:
                ratio = self.step_size / d
                new_p = tuple(frm[d] + ratio * (to[d] - frm[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_radius + (self.max_radius - self.min_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_resolution)

        # Simple KDTree replacement for nearest and radius searches
        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                attempts = 0
                while attempts < 20:
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                    attempts += 1
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))
            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            u = a1_np - np.array([1 if d == 0 else 0 for d in range(dim)])
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            val = max(r1*r1 - (c_min/2)**2, 0)
            r_others = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_others]*(dim -1))

            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if 1e-15 < norm_x <= 1:
                    radius_factor = np.random.uniform(0,1) ** (1/dim)
                    x_scaled = (x / norm_x) * radius_factor
                    x_scaled = x_scaled * radii
                    p = rot @ x_scaled + np.array(center)
                    p_tuple = tuple(float(coord) for coord in p)
                    if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                        return p_tuple

            # fallback uniform sampling
            attempts = 0
            while attempts < 20:
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
                attempts += 1
            return p

        edge_cache = {}

        def can_connect(p1, p2):
            if (p1, p2) in edge_cache:
                return not edge_cache[(p1, p2)]
            if (p2, p1) in edge_cache:
                return not edge_cache[(p2, p1)]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_cache[(p1, p2)] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_cache[(p1, p2)] = collision
            return not collision

        def can_add_node(pos):
            return in_bounds(pos) and not is_in_obstacle(pos)

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iters):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path)-3)
                j = random.randint(i+2, len(path)-1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i+1] + path[j:]
            return path

        def prune_nodes(best_cost):
            heuristic = lambda p: dist(p, goal_pos)
            to_remove = []
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for rm in to_remove:
                if rm.parent:
                    try:
                        rm.parent.children.remove(rm)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm)
                except ValueError:
                    pass
                if rm in tree_start:
                    tree_start.remove(rm)
                if rm in tree_goal:
                    tree_goal.remove(rm)
            return len(to_remove)

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate growth: always extend smaller tree first
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for near in near_nodes:
                cand_cost = near.cost + dist(near.position, new_pos)
                if cand_cost + 1e-12 < min_cost and can_connect(near.position, new_pos):
                    min_cost = cand_cost
                    best_parent = near

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_nodes(c_best)
                edge_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position  # Tuple[float, ...] (2D or 3D)
        self.parent = parent      # Parent Node or None
        self.cost = cost          # Cost from root to this node
        self.children = []        # Child nodes
        self.valid = True         # For marking nodes invalid if needed

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0,
                 max_neighbor_radius: float = 30.0,
                 prune_interval: int = 200,
                 prune_threshold: int = 300,
                 smoothing_iterations: int = 150,
                 collision_check_resolution: float = 1.0,
                 goal_sample_rate: float = 0.15):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> "PlannerResult":
        import math
        import random
        import numpy as np

        bounds = map.size                  # Tuple[int, ...]
        start_pos = map.start              # Tuple[float, ...]
        goal_pos = map.goal                # Tuple[float, ...]
        obstacles = map.obstacles          # List of obstacle tuples
        dim = len(bounds)
        is_3d = dim == 3

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp new point to map bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            # If no solution found or cost near minimal, sample uniformly with goal biasing
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            # Ellipsoidal informed sampling centered on midpoint between start and goal
            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])
            id_mat = np.eye(dim)
            u = a1 - np.array([1 if d == 0 else 0 for d in range(dim)])
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u /= norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            val = max(r1 * r1 - (c_min / 2) ** 2, 0)
            r_others = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if 1e-15 < norm_x <= 1:
                    radius_scale = np.random.uniform(0, 1) ** (1 / dim)
                    x_scaled = (x / norm_x) * radius_scale * radii
                    p = rot @ x_scaled + center
                    p_tuple = tuple(float(c) for c in p)
                    if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                        return p_tuple

            # fallback to uniform sampling with obstacle rejection
            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            key_rev = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if key_rev in edge_collision_cache:
                return not edge_collision_cache[key_rev]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos) or is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            heuristic = lambda pos: dist(pos, goal_pos)
            to_remove = []
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            removed = 0
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate extension from smaller tree for balance
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos):
                continue
            if not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes:
                candidate_cost = node.cost + dist(node.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors around new_node to improve path cost
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            # Try to connect opposite tree towards new_node incrementally
            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)

                if not can_add_node(next_pos_b):
                    break
                if not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]
                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        # Aggressive shortcut smoothing to shorten path
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Periodic pruning of nodes that cannot improve best path
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        if is_3d:
            pz = pos[2]
            for obs in obstacles:
                x, y, z, w, h, d = obs
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
        else:
            for obs in obstacles:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []              # For rewiring & pruning
        self.valid = True               # Mark valid/pruned or for collision

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except (ValueError, AttributeError):
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=5.0, max_neighbor_radius: float=30.0,
                 prune_interval: int=200, prune_threshold: int=300,
                 smoothing_trials: int=100, collision_check_resolution: float=1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_trials = smoothing_trials
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> 'PlannerResult':
        import math, random, numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))
            # Clamp to bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(n_nodes, iteration):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            r_iter = self.max_neighbor_radius * (1.0 - (iteration/self.max_iter)**0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius)*math.sqrt(math.log(max(n_nodes,1)+1)/max(n_nodes,1))
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        # KD-tree brute-force for portability
        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius*radius
                res = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta*delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        res.append(self.nodes[i])
                return res

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2) if p1 < p2 else (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            return in_bounds(pos) and not is_in_obstacle(pos)

        # Informed ellipsoidal sampling biased by the best known cost c_best
        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min*1.00001 or random.random() < 0.1:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d]+goal[d])/2 for d in range(dim)])
            direction = np.array([(goal[d]-start[d])/c_min for d in range(dim)])
            id_mat = np.eye(dim)
            # Householder reflection matrix
            u = direction - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r2 = math.sqrt(max(r1*r1 - (c_min/2)**2, 0))
            radii = np.array([r1] + [r2]*(dim-1))

            for _ in range(150):
                x = np.random.uniform(-1,1,dim)
                norm_x = np.linalg.norm(x)
                if norm_x < 1e-10 or norm_x > 1:
                    continue
                x = x / norm_x
                x = x * np.power(np.random.uniform(0,1), 1/dim)
                p = rot @ (x * radii) + center
                p_tuple = tuple(float(coord) for coord in p)
                if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                    return p_tuple

            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        # Adaptive sample near current best path with Gaussian noise
        def adaptive_sample(best_path, c_best, c_min):
            if best_path and random.random() < 0.75:
                for _ in range(50):
                    base_point = best_path[random.randint(0,len(best_path)-1)]
                    noise = np.random.normal(0, self.step_size*0.5, dim)
                    sample_point = tuple(base_point[d]+noise[d] for d in range(dim))
                    if in_bounds(sample_point) and not is_in_obstacle(sample_point):
                        return sample_point
            return informed_sample(c_best, c_min, start_pos, goal_pos)

        # Shortcut smoothing by direct edge replacement
        def shortcut_path(path):
            if len(path) < 3:
                return path[:]
            smooth = path[:]
            for _ in range(self.smoothing_trials):
                if len(smooth) < 3:
                    break
                i = random.randint(0,len(smooth)-3)
                j = random.randint(i+2,len(smooth)-1)
                p1, p2 = smooth[i], smooth[j]
                if can_connect(p1, p2):
                    smooth = smooth[:i+1] + smooth[j:]
            return smooth

        def prune_nodes(nodes_list, best_cost, root_s, root_g):
            removed = 0
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for node in nodes_list:
                if node in (root_s, root_g):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-9:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except (ValueError, AttributeError):
                        pass
                try:
                    nodes_list.remove(node)
                except ValueError:
                    pass
                removed += 1
            return removed


        # Initialize trees
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        tree_start = [root_start]
        tree_goal = [root_goal]
        nodes = [root_start, root_goal]
        edges = []

        c_min = dist(start_pos, goal_pos)
        c_best = float('inf')
        best_path = []
        success_state = False

        for it in range(self.max_iter):
            q_rand = adaptive_sample(best_path, c_best, c_min)

            # Extend smaller tree
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius_a = neighbor_radius(len(nodes), it)
            near_nodes_a = kdtree_a.radius_search(new_pos, radius_a)

            # Parent selection minimizing cost
            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes_a:
                cost_cand = node.cost + dist(node.position, new_pos)
                if cost_cand + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_cand
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for node in near_nodes_a:
                if node is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, node.position)
                if new_cost + 1e-12 < node.cost and can_connect(new_node.position, node.position):
                    try:
                        edges.remove((node.parent, node))
                    except ValueError:
                        pass
                    node.update_parent(new_node, new_cost)
                    edges.append((new_node, node))

            # Incremental connection from tree_b towards new_node
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            max_steps = int(dist(nearest_b.position, new_node.position) / self.step_size) + 1
            last_node_b = nearest_b
            connected = False

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break
                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                radius_b = neighbor_radius(len(nodes), it)
                near_nodes_b = kdtree_b.radius_search(next_pos_b, radius_b)

                # Rewire tree B
                for nbr_b in near_nodes_b:
                    if nbr_b is last_node_b:
                        continue
                    new_cost_b = new_node_b.cost + dist(new_node_b.position, nbr_b.position)
                    if new_cost_b + 1e-12 < nbr_b.cost and can_connect(new_node_b.position, nbr_b.position):
                        try:
                            edges.remove((nbr_b.parent, nbr_b))
                        except ValueError:
                            pass
                        nbr_b.update_parent(new_node_b, new_cost_b)
                        edges.append((new_node_b, nbr_b))

                # Check if connection established
                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    cost_connect = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connect_node = Node(new_node.position, parent=new_node_b, cost=cost_connect)
                    new_node_b.children.append(connect_node)
                    tree_b.append(connect_node)
                    nodes.append(connect_node)
                    edges.append((new_node_b, connect_node))

                    success_state = True

                    # Extract full path concatenating start-goal or goal-start accordingly
                    if tree_a is tree_start:
                        path_start = connect_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connect_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]
                    if cost_connect + 1e-12 < c_best:
                        c_best = cost_connect
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                # Continue searching for better paths if desired
                # Optional: could continue instead of break to get anytime property
                break

            # Periodic pruning for efficiency
            if (it > 0 and it % self.prune_interval == 0 and
                len(nodes) > self.prune_threshold and c_best < float('inf')):
                prune_nodes(tree_start, c_best, root_start, root_goal)
                prune_nodes(tree_goal, c_best, root_start, root_goal)
                edge_collision_cache.clear()

        extracted_path = best_path if success_state else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D or 3D coordinate
        self.parent = parent            # Node or None
        self.cost = cost                # Cost from root
        self.children: List[Node] = []
        self.valid = True               # Collision validity

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        node, path = self, []
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 4000, step_size: float=5.0,
                 goal_sample_rate: float=0.2, radius_const: float=18.0,
                 radius_min: float=5.0, prune_interval: int=120,
                 prune_threshold: int=120, smoothing_iters: int=40,
                 collision_cache_resolution: float=1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.radius_const = radius_const
        self.radius_min = radius_min
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iters = smoothing_iters
        self.collision_cache_resolution = collision_cache_resolution
        self._edge_collision_cache: Dict[Tuple[Tuple[float, ...], Tuple[float, ...]], bool] = {}

    def plan(self, map) -> PlannerResult:
        bounds = map.size                  # Tuple[int, ...]
        start_pos = map.start              # Tuple[float, ...]
        goal_pos = map.goal                # Tuple[float, ...]
        obstacles = map.obstacles          # List of obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def dist(a,b): return math.dist(a,b)

        # Cached collision checking to save time on repeated queries
        def cache_key(p1, p2):
            return tuple(sorted((p1, p2)))

        def is_in_obstacle(pos):
            for obs in obstacles:
                if is_3d:
                    x,y,z,w,h,d = obs
                    px,py,pz = pos
                    if x <= px <= x+w and y <= py <= y+h and z <= pz <= z+d:
                        return True
                else:
                    x,y,w,h = obs
                    px,py = pos
                    if x <= px <= x+w and y <= py <= y+h:
                        return True
            return False

        # Edge collision check with caching
        def is_edge_in_obstacle(p1, p2):
            key = cache_key(p1, p2)
            if key in self._edge_collision_cache:
                return self._edge_collision_cache[key]
            distance = dist(p1, p2)
            steps = max(1, int(distance / self.collision_cache_resolution))
            for i in range(steps + 1):
                interp = tuple(p1[d] + (p2[d]-p1[d])*(i/steps) for d in range(dim))
                if is_in_obstacle(interp):
                    self._edge_collision_cache[key] = True
                    return True
            self._edge_collision_cache[key] = False
            return False

        def in_bounds(pos):
            for d in range(dim):
                if pos[d] < 0 or pos[d] > bounds[d]:
                    return False
            return True

        # Adaptive radius based on node count
        def radius_dynamic(n):
            if n == 0:
                return self.radius_min
            val = self.radius_const * math.sqrt(math.log(n+1)/(n+1))
            return max(val, self.radius_min)

        # Heuristic: Euclidean distance as cost
        def cost_line(p1, p2):
            return dist(p1, p2)

        # Steer from from_pos towards to_pos at step_size
        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            ratio = self.step_size / d
            return tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))

        # Nearest node in tree to the point
        def nearest(tree, point):
            return min(tree, key=lambda n: dist(n.position, point))

        # Nodes within radius to pos in tree
        def near_nodes(tree, pos, radius):
            return [n for n in tree if dist(n.position, pos) <= radius]

        # Check if a node position is valid (in bounds and collision free)
        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        # Check if the edge between two positions is collision free
        def can_connect(p1, p2):
            if is_edge_in_obstacle(p1, p2):
                return False
            return True

        # Informed sampling inside ellipse defined by current best cost
        def sample_informed(best_cost):
            # If no best_cost, sample uniformly
            if best_cost is None or best_cost == float('inf'):
                while True:
                    sample = tuple(random.uniform(0,bounds[d]) for d in range(dim))
                    if not is_in_obstacle(sample):
                        return sample
            # Ellipsoid parameters
            c_min = dist(start_pos, goal_pos)
            if best_cost < c_min:
                best_cost = c_min * 1.1  # numerical safety margin

            # Compute center and rotation matrix (only along vector start -> goal)
            center = tuple((start_pos[d] + goal_pos[d])/2 for d in range(dim))
            a1 = tuple((goal_pos[d]-start_pos[d])/c_min for d in range(dim))  # unit vector along main axis

            # For 2D or 3D, create orthonormal basis
            basis = [a1]
            # Use Gram-Schmidt for extending to basis vectors in 2D/3D
            def gram_schmidt(vecs):
                ortho = []
                for v in vecs:
                    w = list(v)
                    for b in ortho:
                        proj = sum(v[i]*b[i] for i in range(dim))
                        w = [w[i] - proj*b[i] for i in range(dim)]
                    norm = math.sqrt(sum(w[i]*w[i] for i in range(dim)))
                    if norm > 1e-10:
                        w = [w[i]/norm for i in range(dim)]
                        ortho.append(tuple(w))
                return ortho

            random_dirs = []
            for _ in range(dim-1):
                random_vec = tuple(random.uniform(-1,1) for _ in range(dim))
                random_dirs.append(random_vec)
            ortho_basis = gram_schmidt([a1]+random_dirs)[1:]  # exclude a1
            basis.extend(ortho_basis)

            # Ellipse radii: a = best_cost/2 along main axis, b,c,... smaller along others
            radii = [best_cost/2] + [math.sqrt(best_cost**2 - c_min**2)/2]*(dim-1)

            # Sample random point inside unit ball (dim-dim)
            while True:
                # Sample in unit ball
                if dim == 2:
                    r = random.uniform(0,1)**0.5
                    theta = random.uniform(0, 2*math.pi)
                    x_ball = (r*math.cos(theta), r*math.sin(theta))
                elif dim == 3:
                    phi = random.uniform(0, 2*math.pi)
                    costheta = random.uniform(-1,1)
                    u = random.uniform(0,1)
                    sintheta = math.sqrt(1 - costheta**2)
                    r = u ** (1/3)
                    x_ball = (
                        r * sintheta * math.cos(phi),
                        r * sintheta * math.sin(phi),
                        r * costheta
                    )
                else:
                    # For higher dims, fallback uniform sampling without bias
                    sample = tuple(random.uniform(0,bounds[d]) for d in range(dim))
                    if not is_in_obstacle(sample):
                        return sample
                # Map unit ball to ellipsoid
                coord = [radii[i]*x_ball[i] for i in range(dim)]
                # Rotate and translate point back to space
                sample = [center[i] for i in range(dim)]
                for i in range(dim):
                    for j in range(dim):
                        sample[i] += basis[j][i] * coord[j]
                sample_t = tuple(sample)
                if in_bounds(sample_t) and not is_in_obstacle(sample_t):
                    return sample_t

        # Extend tree towards sample point with RRT* style rewiring
        def try_extend(tree, sample_pt, all_nodes):
            if not tree:
                return None
            nearest_node = nearest(tree, sample_pt)
            new_pos = steer(nearest_node.position, sample_pt)
            if not can_add_node(new_pos):
                return None
            if not can_connect(nearest_node.position, new_pos):
                return None

            # Determine dynamic radius
            radius = radius_dynamic(len(tree))
            near = near_nodes(tree, new_pos, radius)

            min_cost = nearest_node.cost + cost_line(nearest_node.position, new_pos)
            best_parent = nearest_node
            for node in near:
                tentative_cost = node.cost + cost_line(node.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree.append(new_node)
            all_nodes.append(new_node)

            # Rewire neighbors
            for neighbor in near:
                if neighbor is best_parent:
                    continue
                cost_through_new = new_node.cost + cost_line(new_node.position, neighbor.position)
                if cost_through_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    neighbor.update_parent(new_node, cost_through_new)
            return new_node

        # Incrementally try to connect two trees from node_start to tree_goal
        def try_connect(tree_goal, node_start):
            if not tree_goal or node_start is None:
                return None
            last_node = nearest(tree_goal, node_start.position)
            dist_to_goal = dist(last_node.position, node_start.position)
            # Walk from last_node toward node_start in steps
            direction = tuple(node_start.position[d] - last_node.position[d] for d in range(dim))
            length = dist_to_goal
            steps = max(1, int(length / self.step_size))
            curr_pos = last_node.position
            prev_node = last_node
            for i in range(1, steps+1):
                interp_pos = tuple(last_node.position[d] + direction[d]*i/steps for d in range(dim))
                if not can_add_node(interp_pos) or not can_connect(prev_node.position, interp_pos):
                    return None
                cost = prev_node.cost + cost_line(prev_node.position, interp_pos)
                new_node = Node(interp_pos, parent=prev_node, cost=cost)
                prev_node.children.append(new_node)
                tree_goal.append(new_node)
                prev_node = new_node
            # Finally add node_start if last connection free
            if can_connect(prev_node.position, node_start.position):
                final_cost = prev_node.cost + cost_line(prev_node.position, node_start.position)
                final_node = Node(node_start.position, parent=prev_node, cost=final_cost)
                prev_node.children.append(final_node)
                tree_goal.append(final_node)
                return final_node
            return None

        # Prune dead-end leaf nodes periodically to reduce tree size
        def prune_dead_nodes():
            removed = []
            to_remove = [node for node in all_nodes if node not in (start_root, goal_root) and not node.children]
            for node in to_remove:
                try:
                    if node.parent:
                        node.parent.children.remove(node)
                    all_nodes.remove(node)
                except Exception:
                    continue
            return removed

        # Shortcut smoothing to reduce path length and sharp turns
        def shortcut_smoothing(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iters):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i+1] + path[j:]
            return path

        def path_length(path):
            return sum(dist(path[i], path[i+1]) for i in range(len(path)-1))

        # Initialize trees and data
        success_state = False
        extracted_path: List[Tuple[float, ...]] = []
        all_nodes: List[Node] = []
        edges: List[Tuple[Node, Node]] = []

        start_root = Node(start_pos, cost=0.0)
        goal_root = Node(goal_pos, cost=0.0)
        tree_start = [start_root]
        tree_goal = [goal_root]
        all_nodes.extend([start_root, goal_root])

        best_cost = float('inf')
        best_path = None

        # Iteratively grow trees with balanced alternation
        for itr in range(self.max_iter):
            # Alternate trees: always extend smaller tree to balance search
            if len(tree_start) > len(tree_goal):
                tree_start, tree_goal = tree_goal, tree_start

            # Sample according to informed sampling if we have a solution; else uniform
            sample_pt = sample_informed(best_cost if best_cost != float('inf') else None)

            new_node_start = try_extend(tree_start, sample_pt, all_nodes)
            if new_node_start is None:
                # No new node added this iteration
                continue

            new_node_goal = try_connect(tree_goal, new_node_start)
            if new_node_goal is not None:
                # Path connected, extract paths
                success_state = True
                path_start = new_node_start.path_from_root()
                path_goal = new_node_goal.path_from_root()
                raw_path = path_start + path_goal[::-1][1:]
                raw_len = path_length(raw_path)

                # If better than previous best, update
                if raw_len + 1e-12 < best_cost:
                    smoothed = shortcut_smoothing(raw_path)
                    smooth_len = path_length(smoothed)
                    # Accept smoothed if better
                    if smooth_len + 1e-12 < raw_len:
                        best_path = smoothed
                        best_cost = smooth_len
                    else:
                        best_path = raw_path
                        best_cost = raw_len

                extracted_path = best_path
                # Continue searching for better
                # Optional: can break early here to save time when first found
                break

            # Periodic pruning to maintain performance
            if itr > 0 and itr % self.prune_interval == 0 and len(all_nodes) > self.prune_threshold:
                prune_dead_nodes()

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=all_nodes,
            edges=[(n.parent, n) for n in all_nodes if n.parent is not None]
        )

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        node, path = self, []
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0,
                 max_neighbor_radius: float = 30.0,
                 prune_interval: int = 200,
                 prune_threshold: int = 300,
                 smoothing_iterations: int = 100,
                 collision_check_resolution: float = 1.0,
                 goal_sample_rate: float = 0.05):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> 'PlannerResult':
        import random
        import math
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            return tuple(max(0.0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_i in range(self.dim):
                        delta = p[d_i] - pt[d_i]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001:
                if random.random() < self.goal_sample_rate:
                    return goal if random.random() < 0.7 else start
                for _ in range(10):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2.0 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            e1 = np.zeros(dim)
            e1[0] = 1.0
            v = a1 - e1
            norm_v = np.linalg.norm(v)
            if norm_v > 1e-10:
                v /= norm_v
                rot = np.eye(dim) - 2.0 * np.outer(v, v)
            else:
                rot = np.eye(dim)

            r1 = c_best / 2.0
            r_others = math.sqrt(max(r1 * r1 - (c_min / 2) ** 2, 0.0))
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(100):
                x_ball = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x_ball) <= 1.0:
                    sample = rot @ (x_ball * radii) + center
                    p = tuple(np.clip(sample, 0.0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p

            for _ in range(10):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            key_rev = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if key_rev in edge_collision_cache:
                return not edge_collision_cache[key_rev]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i+1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            to_remove = []
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                heuristic = dist(node.position, goal_pos)
                if node.cost + heuristic >= best_cost:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate growth: extend smaller tree first
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near:
                cost_candidate = node.cost + dist(node.position, new_pos)
                if cost_candidate + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_candidate
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for neighbor in near:
                if neighbor == best_parent:
                    continue
                c_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if c_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, c_via_new)
                    edges.append((new_node, neighbor))

            # Attempt connection between trees
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False

            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1
            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b == last_node_b:
                        continue
                    c_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if c_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, c_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()
                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break
                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            if (it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf')):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        node, path = self, []
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self,
                 max_iter: int = 5000,
                 step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0,
                 max_neighbor_radius: float = 25.0,
                 prune_interval: int = 200,
                 prune_threshold: int = 300,
                 smoothing_iterations: int = 120,
                 collision_check_resolution: float = 1.0,
                 goal_sample_rate: float = 0.05):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0.0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp within bounds
            return tuple(max(0.0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter)**0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_i in range(self.dim):
                        delta = p[d_i] - pt[d_i]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            # If no solution yet found: uniform random + goal bias
            if c_best == float('inf') or c_best < c_min * 1.00001:
                if random.random() < self.goal_sample_rate:
                    return goal if random.random() < 0.7 else start
                for _ in range(10):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2.0 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            e1 = np.zeros(dim)
            e1[0] = 1.0
            v = a1 - e1
            norm_v = np.linalg.norm(v)
            if norm_v > 1e-10:
                v /= norm_v
                rot = np.eye(dim) - 2.0 * np.outer(v, v)
            else:
                rot = np.eye(dim)

            r1 = c_best / 2.0
            r_others = math.sqrt(max(r1 * r1 - (c_min / 2) ** 2, 0.0))
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(100):
                x_ball = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x_ball) <= 1.0:
                    sample = rot @ (x_ball * radii) + center
                    p = tuple(np.clip(sample, 0.0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p

            for _ in range(10):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            key_rev = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if key_rev in edge_collision_cache:
                return not edge_collision_cache[key_rev]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            to_remove = []
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                heuristic = dist(node.position, goal_pos)
                if node.cost + heuristic >= best_cost:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path: list = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate growth: extend the smaller tree first
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes:
                cost_candidate = node.cost + dist(node.position, new_pos)
                if cost_candidate + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_candidate
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for neighbor in near_nodes:
                if neighbor == best_parent:
                    continue
                c_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if c_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, c_via_new)
                    edges.append((new_node, neighbor))

            # Connect trees attempt
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False

            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1
            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b == last_node_b:
                        continue
                    c_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if c_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, c_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()
                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break
                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            if (it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf')):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float = 0.0):
        self.position = position          # Tuple[float, ...]
        self.parent = parent              # Node or None
        self.cost = cost                  # Path cost from root
        self.children: List[Node] = []
        self.valid = True                 # For collision checking

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self,
                 max_iter: int = 5000,
                 step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0,
                 max_neighbor_radius: float = 30.0,
                 prune_interval: int = 200,
                 prune_threshold: int = 300,
                 smoothing_iterations: int = 150,
                 collision_check_resolution: float = 1.0,
                 goal_sample_rate: float = 0.15):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp within bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        # Simple brute-force KDTree substitute for demonstration
        class KDTree:
            def __init__(self, points_nodes: List[Tuple[Tuple[float, ...], Node]]):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt: Tuple[float, ...]) -> Node:
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best = self.nodes[i]
                return best

            def radius_search(self, pt: Tuple[float, ...], radius: float) -> List[Node]:
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        edge_collision_cache = {}

        def can_connect(p1, p2):
            if (p1, p2) in edge_collision_cache:
                return not edge_collision_cache[(p1, p2)]
            if (p2, p1) in edge_collision_cache:
                return not edge_collision_cache[(p2, p1)]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[(p1, p2)] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[(p1, p2)] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def informed_sample(c_best, c_min, start, goal):
            # If no solution yet or no improvement, uniform sampling with goal bias
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                # fallback
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))
            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            u = a1_np - np.array([1 if d == 0 else 0 for d in range(dim)])
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            val = max(r1 * r1 - (c_min / 2) * (c_min / 2), 0)
            r_others = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x <= 1 and norm_x > 1e-15:
                    radius_factor = np.random.uniform(0, 1) ** (1 / dim)
                    x_scaled = (x / norm_x) * radius_factor
                    x_scaled = x_scaled * radii
                    p = rot @ x_scaled + np.array(center)
                    p_tuple = tuple(float(coord) for coord in p)
                    if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                        return p_tuple

            # fallback uniform sampling
            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        def shortcut_path(path: List[Tuple[float, ...]]) -> List[Tuple[float, ...]]:
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path

        def prune_useless_nodes(best_cost: float) -> int:
            heuristic = lambda pos: dist(pos, goal_pos)
            removed = 0
            to_remove = []
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges: List[Tuple[Node, Node]] = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path: List[Tuple[float, ...]] = []

        success_state = False
        extracted_path: List[Tuple[float, ...]] = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate expansion: always grow smaller tree to balance growth
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            # Choose best parent minimizing cost to new node
            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes:
                candidate_cost = node.cost + dist(node.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors if better path via new_node is found
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            # Extend other tree toward new_node incrementally to attempt connection
            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Periodic pruning to reduce graph size
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos: Tuple[float, ...], obstacles: List[Tuple], is_3d: bool) -> bool:
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos: Tuple[float, ...], to_pos: Tuple[float, ...], obstacles: List[Tuple], is_3d: bool, resolution: float = 1.0) -> bool:
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checks

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=5.0,
                 max_neighbor_radius: float=30.0,
                 prune_interval: int=200,
                 prune_threshold: int=300,
                 smoothing_iterations: int=150,
                 collision_check_resolution: float=1.0,
                 goal_sample_rate: float=0.15):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> 'PlannerResult':
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp within bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        # Brute-force KDTree substitute for simplicity
        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            # If no solution yet or c_best < c_min, uniform random sampling with goal bias
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = tuple((start[d] + goal[d]) / 2 for d in range(dim))
            a1 = tuple((goal[d] - start[d]) / c_min for d in range(dim))
            a1_np = np.array(a1)
            id_mat = np.eye(dim)
            u = a1_np - np.array([1 if d == 0 else 0 for d in range(dim)])
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u = u / norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            val = max(r1*r1 - (c_min/2)*(c_min/2), 0)
            r_others = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_others]*(dim -1))

            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x <= 1 and norm_x > 1e-15:
                    radius_factor = np.random.uniform(0,1) ** (1/dim)
                    x_scaled = (x / norm_x) * radius_factor
                    x_scaled = x_scaled * radii
                    p = rot @ x_scaled + np.array(center)
                    p_tuple = tuple(float(coord) for coord in p)
                    if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                        return p_tuple

            # Fallback uniform sampling
            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            # Check cache
            if (p1, p2) in edge_collision_cache:
                return not edge_collision_cache[(p1, p2)]
            if (p2, p1) in edge_collision_cache:
                return not edge_collision_cache[(p2, p1)]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[(p1, p2)] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[(p1, p2)] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path)-3)
                j = random.randint(i+2, len(path)-1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i+1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            heuristic = lambda pos: dist(pos, goal_pos)
            removed = 0
            to_remove = []
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path: List[Tuple[float, ...]] = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Always extend smaller tree first for balanced growth
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes:
                candidate_cost = node.cost + dist(node.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors to improve costs
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            # Incrementally extend tree_b towards new_node for connection
            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Periodic pruning to reduce unpromising nodes
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D or 3D coordinate
        self.parent = parent            # Node or None
        self.cost = cost                # Cost from root
        self.children = []
        self.valid = True               # Validity flag for collision pruning

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=3.0,
                 max_neighbor_radius: float=25.0,
                 prune_interval: int=150,
                 prune_threshold: int=300,
                 smoothing_iterations: int=150,
                 collision_check_resolution: float=0.5,
                 goal_sample_rate: float=0.1):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> "PlannerResult":
        import math
        import random
        import numpy as np
        from typing import List, Tuple

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = dim == 3

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def adaptive_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        edge_collision_cache = {}

        def can_connect(p1, p2):
            if (p1, p2) in edge_collision_cache:
                return not edge_collision_cache[(p1, p2)]
            if (p2, p1) in edge_collision_cache:
                return not edge_collision_cache[(p2, p1)]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[(p1, p2)] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[(p1, p2)] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos) or is_in_obstacle(pos):
                return False
            return True

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(30):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2.0 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            id_mat = np.eye(dim)
            v = a1 - np.array([1.0] + [0.0] * (dim - 1))
            norm_v = np.linalg.norm(v)
            if norm_v > 1e-15:
                v /= norm_v
                rot = id_mat - 2 * np.outer(v, v)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            val = max(r1 * r1 - (c_min / 2.0) * (c_min / 2.0), 0)
            r_other = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_other] * (dim - 1))

            for _ in range(100):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x <= 1 and norm_x > 1e-15:
                    u = np.random.uniform(0, 1)
                    sample = rot @ (radii * (x / norm_x) * (u ** (1 / dim))) + center
                    sample_t = tuple(float(c) for c in sample)
                    if in_bounds(sample_t) and not is_in_obstacle(sample_t):
                        return sample_t

            for _ in range(30):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path

        def prune_nodes(best_cost):
            removed = 0
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)

            for rm in to_remove:
                if rm.parent:
                    try:
                        rm.parent.children.remove(rm)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm)
                except ValueError:
                    pass
                if rm in tree_start:
                    tree_start.remove(rm)
                if rm in tree_goal:
                    tree_goal.remove(rm)
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)

        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path: List[Tuple[float, ...]] = []

        # Alternate tree expansion: start with start tree
        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = adaptive_radius(it, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a

            for node in near_nodes:
                candidate_cost = node.cost + dist(node.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewiring neighbors of new_node
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            # Try connecting tree_b incrementally towards new_node
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_b = nearest_b
            connected = False
            max_steps = int(dist(last_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_b.position, next_pos_b):
                    break

                cost_b = last_b.cost + dist(last_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_b, cost=cost_b)
                last_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            # Periodic pruning to remove nodes unlikely to improve the solution
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self,
                 max_iter: int = 5000,
                 step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0,
                 max_neighbor_radius: float = 30.0,
                 prune_interval: int = 150,
                 prune_threshold: int = 250,
                 smoothing_iterations: int = 120,
                 collision_check_resolution: float = 1.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> 'PlannerResult':
        import random
        import math
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp to bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d in range(self.dim):
                        delta = p[d] - pt[d]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        # Adaptive informed sampling within ellipsoid around current best path
        def informed_sample(c_best, c_min, start, goal):
            import numpy as np
            # If no solution yet or trivial best solution, sample uniformly
            if c_best == float('inf') or c_best < c_min * 1.00001:
                for _ in range(15):  # increased attempts for better sample quality
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2.0 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            id_mat = np.eye(dim)
            v = a1 - np.array([1] + [0] * (dim - 1))
            norm_v = np.linalg.norm(v)
            if norm_v > 1e-10:
                v /= norm_v
                rot = id_mat - 2 * np.outer(v, v)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r_others = math.sqrt(max(r1 * r1 - (c_min / 2) ** 2, 0))
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(120):  # increased attempts for ellipsoid sampling
                x_ball = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x_ball) <= 1.0:
                    sample = rot @ (x_ball * radii) + center
                    p = tuple(float(coord) for coord in sample)
                    if in_bounds(p) and not is_in_obstacle(p):
                        return p

            for _ in range(15):  # fallback uniform sampling attempts
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p  # fallback sample

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            key_rev = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if key_rev in edge_collision_cache:
                return not edge_collision_cache[key_rev]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path

        def prune_nodes(best_cost):
            removed_count = 0
            to_remove = []
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                heuristic = dist(node.position, goal_pos)
                if node.cost + heuristic >= best_cost - 1e-12:
                    to_remove.append(node)
            for node_rm in to_remove:
                if node_rm.parent:
                    try:
                        node_rm.parent.children.remove(node_rm)
                    except ValueError:
                        pass
                try:
                    nodes.remove(node_rm)
                except ValueError:
                    pass
                if node_rm in tree_start:
                    tree_start.remove(node_rm)
                if node_rm in tree_goal:
                    tree_goal.remove(node_rm)
                removed_count += 1
            return removed_count

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate tree expansions, always grow smaller tree for better balance
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near:
                cost_candidate = node.cost + dist(node.position, new_pos)
                if cost_candidate + 1e-13 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_candidate
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewiring neighbors within radius for improved cost - tight epsilon tolerance
            for neighbor in near:
                if neighbor is best_parent:
                    continue
                c_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if c_via_new + 1e-13 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, c_via_new)
                    edges.append((new_node, neighbor))

            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False
            max_steps = max(1, int(dist(last_node_b.position, new_node.position) / self.step_size))

            # Multi-step incremental connection from tree_b to new_node (tree_a)
            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_node_b:
                        continue
                    c_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if c_via_new_b + 1e-13 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, c_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-13 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                # Continue to improve: still run additional smoothing for better path shortening
                extracted_path = shortcut_path(extracted_path)
                break

            # Aggressive pruning of nodes that cannot improve best solution
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_nodes(c_best)
                edge_collision_cache.clear()

        # Final fallback shortcut smoothing if success but no smoothing done yet
        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        if extracted_path:
            extracted_path = shortcut_path(extracted_path)

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child_node: 'Node'):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 min_neighbor_radius: float = 4.0,
                 max_neighbor_radius: float = 25.0,
                 prune_interval: int = 150,
                 prune_threshold: int = 250,
                 smoothing_iterations: int = 150,
                 collision_check_resolution: float = 0.5,
                 goal_sample_rate: float = 0.15):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> "PlannerResult":
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp within bounds
            return tuple(max(0.0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def informed_sample(c_best, c_min, start, goal):
            import numpy as np
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(15):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not self._is_in_obstacle(p, obstacles, is_3d):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u /= norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r_others = math.sqrt(max(r1 ** 2 - (c_min / 2) ** 2, 0))
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(120):
                x = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x) <= 1:
                    sample = rot @ (x * radii) + center
                    sample_t = tuple(float(coord) for coord in sample)
                    if in_bounds(sample_t) and not self._is_in_obstacle(sample_t, obstacles, is_3d):
                        return sample_t

            for _ in range(15):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            rev_key = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if rev_key in edge_collision_cache:
                return not edge_collision_cache[rev_key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if self._is_in_obstacle(pos, obstacles, is_3d):
                return False
            return True

        class KDTree:
            def __init__(self, pts_nodes):
                self.points = [pn[0] for pn in pts_nodes]
                self.nodes = [pn[1] for pn in pts_nodes]

            def nearest(self, pt):
                best_node = None
                best_dist = float('inf')
                for p, n in zip(self.points, self.nodes):
                    d = dist(p, pt)
                    if d < best_dist:
                        best_dist = d
                        best_node = n
                return best_node

            def radius_search(self, pt, radius):
                r2 = radius * radius
                res = []
                for p, n in zip(self.points, self.nodes):
                    sqd = 0.0
                    for d_ in range(dim):
                        diff = p[d_] - pt[d_]
                        sqd += diff * diff
                        if sqd > r2:
                            break
                    else:
                        res.append(n)
                return res

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i + 1] + path[j:]
            return path

        def prune_nonpromising_nodes(nodes_list, tree_s, tree_g, best_cost):
            to_remove = []
            for node in nodes_list:
                if node is root_start or node is root_goal:
                    continue
                if not node.children:
                    to_remove.append(node)
                    continue
                h_cost = dist(node.position, goal_pos)
                if node.cost + h_cost >= best_cost:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except ValueError:
                        pass
                try:
                    nodes_list.remove(node)
                except ValueError:
                    pass
                if node in tree_s:
                    try:
                        tree_s.remove(node)
                    except ValueError:
                        pass
                if node in tree_g:
                    try:
                        tree_g.remove(node)
                    except ValueError:
                        pass
            return len(to_remove)

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for itr in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate expansion to keep trees balanced
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(itr, len(nodes))
            near_a = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_a:
                candidate_cost = node.cost + dist(node.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewiring neighbors
            for neighbor in near_a:
                if neighbor is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, neighbor.position)
                if new_cost + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, new_cost)
                    edges.append((new_node, neighbor))

            # Incremental connection attempt from tree_b to new_node
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_b = nearest_b
            connected = False

            max_steps = max(1, int(dist(last_b.position, new_node.position) / self.step_size) + 1)
            for _ in range(max_steps):
                next_pos_b = steer(last_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_b.position, next_pos_b):
                    break

                cost_b = last_b.cost + dist(last_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_b, cost=cost_b)
                last_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_b:
                        continue
                    cost_via_new = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            if itr > 0 and itr % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_nonpromising_nodes(nodes, tree_start, tree_goal, c_best)
                edge_collision_cache.clear()

        if success_state and extracted_path:
            extracted_path = shortcut_path(extracted_path)

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=0.5):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D or 3D coordinate
        self.parent = parent            # Node or None
        self.cost = cost                # Cost from root
        self.children = []
        self.valid = True               # Validity flag for collision pruning

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 min_neighbor_radius: float=3.0,
                 max_neighbor_radius: float=25.0,
                 prune_interval: int=150,
                 prune_threshold: int=250,
                 smoothing_iterations: int=200,
                 collision_check_resolution: float=0.5,
                 goal_sample_rate: float=0.1):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> "PlannerResult":
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = dim == 3

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp to bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def adaptive_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                for _ in range(30):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2.0 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            # Compute a Householder transformation to align ellipsoid axes
            id_mat = np.eye(dim)
            v = a1 - np.array([1.0] + [0.0]*(dim-1))
            norm_v = np.linalg.norm(v)
            if norm_v > 1e-15:
                v /= norm_v
                rot = id_mat - 2 * np.outer(v, v)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            val = max(r1*r1 - (c_min/2.0)*(c_min/2.0), 0)
            r_other = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_other]*(dim-1))

            for _ in range(100):
                x = np.random.uniform(-1,1,dim)
                norm_x = np.linalg.norm(x)
                if norm_x <= 1 and norm_x > 1e-15:
                    radius_factor = np.random.uniform(0,1)
                    # Uniform sampling inside unit ball
                    u = radius_factor**(1/dim)
                    sample = rot @ (radii * (x / norm_x) * u) + center
                    sample_t = tuple(float(c) for c in sample)
                    if in_bounds(sample_t) and not is_in_obstacle(sample_t):
                        return sample_t

            for _ in range(30):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            if (p1, p2) in edge_collision_cache:
                return not edge_collision_cache[(p1, p2)]
            if (p2, p1) in edge_collision_cache:
                return not edge_collision_cache[(p2, p1)]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[(p1, p2)] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[(p1, p2)] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i+1] + path[j:]
            return path

        def prune_nodes(best_cost):
            removed = 0
            to_remove = []
            heuristic = lambda pos: dist(pos, goal_pos)
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-12:
                    to_remove.append(node)

            for rm in to_remove:
                if rm.parent:
                    try:
                        rm.parent.children.remove(rm)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm)
                except ValueError:
                    pass
                if rm in tree_start:
                    tree_start.remove(rm)
                if rm in tree_goal:
                    tree_goal.remove(rm)
                removed += 1
            return removed

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)

        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path: List[Tuple[float, ...]] = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate expansion on smaller tree for balance
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = adaptive_radius(it, len(nodes))
            near_nodes = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a

            for node in near_nodes:
                candidate_cost = node.cost + dist(node.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire near neighbors of new_node
            for neighbor in near_nodes:
                if neighbor is best_parent:
                    continue
                cost_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if cost_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, cost_via_new)
                    edges.append((new_node, neighbor))

            # Try connecting tree_b incrementally towards new_node
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_b = nearest_b
            connected = False
            max_steps = int(dist(last_b.position, new_node.position) / self.step_size) + 1

            for _ in range(max_steps):
                next_pos_b = steer(last_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_b.position, next_pos_b):
                    break

                cost_b = last_b.cost + dist(last_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_b, cost=cost_b)
                last_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b is last_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if cost_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        # Aggressive shortcut smoothing on improved path
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_b = new_node_b

            if connected:
                extracted_path = best_path
                # We can opt to continue search for better solutions or break early here
                # For efficiency, break here:
                break

            # Periodic pruning to discard nodes unlikely to improve solution
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                prune_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

