class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        start_tree = [Node(start_position)]
        goal_tree = [Node(goal_position)]
        nodes = []
        edges = []
        success_state = False
        extracted_path = []

        for iter in range(self.max_iter):
            rand = self._sample_free(bounds, obstacles, is_3d)
            for tree_a, tree_b in [(start_tree, goal_tree), (goal_tree, start_tree)]:
                nearest = self._nearest(tree_a, rand)
                new_pos = self._steer(nearest.position, rand, self.step_size)
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    continue
                if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
                    continue
                new_node = Node(new_pos, parent=nearest, cost=nearest.cost + self._distance(nearest.position, new_pos))
                nearest.add_child(new_node)
                tree_a.append(new_node)
                nodes.append(new_node)
                edges.append((nearest, new_node))

                connect_node = self._nearest(tree_b, new_pos)
                if self._connect_trees(new_node, connect_node, obstacles, is_3d, tree_b, nodes, edges):
                    success_state = True
                    extracted_path = self._extract_path(new_node, connect_node)
                    return PlannerResult(True, extracted_path, start_tree + goal_tree, edges)

        return PlannerResult(False, [], start_tree + goal_tree, edges)

    def _sample_free(self, bounds, obstacles, is_3d):
        while True:
            if is_3d:
                point = tuple(random.uniform(0, bounds[i]) for i in range(3))
            else:
                point = tuple(random.uniform(0, bounds[i]) for i in range(2))
            if not self._is_in_obstacle(point, obstacles, is_3d):
                return point

    def _nearest(self, tree, point):
        return min(tree, key=lambda node: self._distance(node.position, point))

    def _steer(self, from_pos, to_pos, step_size):
        dist = self._distance(from_pos, to_pos)
        if dist <= step_size:
            return to_pos
        ratio = step_size / dist
        return tuple(from_pos[i] + (to_pos[i] - from_pos[i]) * ratio for i in range(len(from_pos)))

    def _connect_trees(self, node_a, node_b, obstacles, is_3d, tree, nodes, edges):
        from_pos = node_a.position
        to_pos = node_b.position
        if self._is_edge_in_obstacle(from_pos, to_pos, obstacles, is_3d):
            return False
        curr = node_b
        path = []
        while True:
            new_pos = self._steer(curr.position, from_pos, self.step_size)
            if self._is_in_obstacle(new_pos, obstacles, is_3d):
                return False
            if self._is_edge_in_obstacle(curr.position, new_pos, obstacles, is_3d):
                return False
            new_node = Node(new_pos, parent=curr, cost=curr.cost + self._distance(curr.position, new_pos))
            curr.add_child(new_node)
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((curr, new_node))
            if self._distance(new_pos, from_pos) < self.step_size:
                new_node_final = Node(from_pos, parent=new_node, cost=new_node.cost + self._distance(new_pos, from_pos))
                new_node.add_child(new_node_final)
                tree.append(new_node_final)
                nodes.append(new_node_final)
                edges.append((new_node, new_node_final))
                return True
            curr = new_node

    def _extract_path(self, node_a, node_b):
        path_a = []
        while node_a:
            path_a.append(node_a.position)
            node_a = node_a.parent
        path_b = []
        while node_b:
            path_b.append(node_b.position)
            node_b = node_b.parent
        return list(reversed(path_a)) + path_b

    def _distance(self, a, b):
        return math.dist(a, b)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = self._distance(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position              # Tuple of coordinates (2D or 3D)
        self.parent = parent                  # Parent Node or None
        self.cost = cost                      # Cost from root to this node
        self.children: List[Node] = []       # Children nodes
        self.valid = True                    # Valid node flag for pruning

    def add_child(self, child_node: "Node"):
        self.children.append(child_node)
        child_node.parent = self

    def remove_child(self, child_node: "Node"):
        if child_node in self.children:
            self.children.remove(child_node)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]

    def update_parent(self, new_parent: "Node", new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
class Planner:
    def __init__(self, max_iter: int=5000, step_size: float=5.0,
                 goal_sample_rate: float=0.1,
                 rewire_radius_max: float=20.0,
                 rewire_radius_min: float=5.0,
                 prune_interval: int=200,
                 smoothing_iterations: int=150,
                 collision_check_resolution: float=0.5):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.rewire_radius_max = rewire_radius_max
        self.rewire_radius_min = rewire_radius_min
        self.prune_interval = prune_interval
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution

    def plan(self, map) -> PlannerResult:
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = dim == 3

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a: Tuple[float, ...], b: Tuple[float, ...]) -> float:
            return math.dist(a, b)

        def steer(from_pos: Tuple[float, ...], to_pos: Tuple[float, ...], step: float) -> Tuple[float, ...]:
            d = dist(from_pos, to_pos)
            if d <= step:
                n_p = to_pos
            else:
                ratio = step / d
                n_p = tuple(from_pos[d_] + ratio * (to_pos[d_] - from_pos[d_]) for d_ in range(dim))
            return tuple(max(0, min(n_p[d_], bounds[d_])) for d_ in range(dim))

        # Multi-resolution collision checking with caching
        edge_collision_cache = {}

        def can_connect(p1, p2) -> bool:
            key = (p1, p2) if p1 < p2 else (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos) -> bool:
            if not in_bounds(pos):
                return False
            if self._is_in_obstacle(pos, obstacles, is_3d):
                return False
            return True

        # Ellipsoid informed sampling centered on best path cost, start, and goal, shrinking ellipsoid radius with improvements
        def ellipsoidal_sample(c_best, c_min, start, goal):
            if c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate:
                # Uniform random sample in bounds avoiding obstacles
                for _ in range(30):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if can_add_node(p):
                        return p
                # Fallback uniform
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2.0 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            # Householder transform for ellipsoid axes alignment
            e1 = np.array([1.0] + [0.0]*(dim-1))
            v = a1 - e1
            norm_v = np.linalg.norm(v)
            if norm_v > 1e-15:
                v /= norm_v
                H = np.eye(dim) - 2*np.outer(v, v)
            else:
                H = np.eye(dim)

            r1 = c_best / 2.0
            val = max(r1*r1 - (c_min/2.0)**2, 0)
            r_other = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_other]*(dim-1))

            for _ in range(100):
                x_ball = np.random.uniform(-1,1,dim)
                norm_x = np.linalg.norm(x_ball)
                if norm_x > 1e-15 and norm_x <= 1.0:
                    scale = random.random() ** (1/dim)
                    sample = H @ (radii * (x_ball / norm_x) * scale) + center
                    sample_t = tuple(float(c) for c in sample)
                    if in_bounds(sample_t) and can_add_node(sample_t):
                        return sample_t

            # Fallback uniform
            for _ in range(30):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if can_add_node(p):
                    return p
            return p

        # KDTree-like class but simplified for proximity queries caching
        class SimpleProximityGraph:
            def __init__(self, nodes_list):
                self.nodes = nodes_list
                self.positions = [nd.position for nd in nodes_list]

            def nearest(self, pt):
                best_node = None
                best_dist = float('inf')
                for n, p in zip(self.nodes, self.positions):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best_node = n
                return best_node

            def radius_search(self, pt, radius):
                radius_sq = radius * radius
                res = []
                for n, p in zip(self.nodes, self.positions):
                    d_sq = sum((p[d_] - pt[d_])**2 for d_ in range(dim))
                    if d_sq <= radius_sq:
                        res.append(n)
                return res

        # Adaptive rewiring radius shrinking from max to min over iterations and node count
        def adaptive_rewire_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.rewire_radius_max * (1 - (iteration / self.max_iter) ** 1.2)
            r_nodes = self.rewire_radius_min + (self.rewire_radius_max - self.rewire_radius_min) * math.sqrt(math.log(c + 1) / c)
            return max(self.rewire_radius_min, min(r_iter, r_nodes))

        # Shortcut smoothing with collision checks to remove unnecessary waypoints
        def path_shortcut(path: List[Tuple[float, ...]], max_iter: int) -> List[Tuple[float, ...]]:
            if len(path) < 3:
                return path
            for _ in range(max_iter):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i+1] + path[j:]
            return path

        # Prune nodes whose cost plus heuristic exceeds the best cost found minus a small epsilon
        def prune_nodes(best_cost):
            removed = 0
            heuristic = lambda pos: dist(pos, goal_pos)
            to_remove = []
            for nd in nodes:
                if nd in (root_start, root_goal):
                    continue
                if not nd.valid:
                    continue
                if nd.cost + heuristic(nd.position) >= best_cost - 1e-12:
                    to_remove.append(nd)
            for rm_node in to_remove:
                rm_node.valid = False
                if rm_node.parent:
                    rm_node.parent.remove_child(rm_node)
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                removed += 1
            return removed

        # Initialize trees
        root_start = Node(start_pos, None, 0.0)
        root_goal = Node(goal_pos, None, 0.0)
        nodes = [root_start, root_goal]
        edges: List[Tuple[Node, Node]] = []

        tree_start = [root_start]
        tree_goal = [root_goal]

        c_min = dist(start_pos, goal_pos)
        c_best = float('inf')
        best_path: List[Tuple[float, ...]] = []
        success_state = False

        for it in range(self.max_iter):
            # Alternate expanding smaller tree for balanced growth
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            # Sample informed point inside ellipsoid or goal bias probability
            q_rand = ellipsoidal_sample(c_best, c_min, start_pos, goal_pos)

            # Build proximity graph for tree_a
            prox_graph_a = SimpleProximityGraph(tree_a)
            nearest_a = prox_graph_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand, self.step_size)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius = adaptive_rewire_radius(it, len(nodes))
            near_nodes = prox_graph_a.radius_search(new_pos, radius)

            # Choose best parent minimizing cost + feasibility check
            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for near_nd in near_nodes:
                candidate_cost = near_nd.cost + dist(near_nd.position, new_pos)
                if candidate_cost + 1e-12 < min_cost and can_connect(near_nd.position, new_pos):
                    min_cost = candidate_cost
                    best_parent = near_nd

            new_node = Node(new_pos, best_parent, min_cost)
            best_parent.add_child(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors around new_node for better costs
            for near_nd in near_nodes:
                if near_nd is best_parent:
                    continue
                c_via_new = new_node.cost + dist(new_node.position, near_nd.position)
                if c_via_new + 1e-12 < near_nd.cost and can_connect(new_node.position, near_nd.position):
                    if near_nd.parent:
                        near_nd.parent.remove_child(near_nd)
                        try:
                            edges.remove((near_nd.parent, near_nd))
                        except ValueError:
                            pass
                    near_nd.update_parent(new_node, c_via_new)
                    edges.append((new_node, near_nd))

            # Try to connect tree_b towards new_node incrementally
            prox_graph_b = SimpleProximityGraph(tree_b)
            nearest_b = prox_graph_b.nearest(new_node.position)
            last_b = nearest_b
            connected = False

            max_steps = int(dist(last_b.position, new_node.position) / self.step_size) + 1
            for _ in range(max_steps):
                next_pos_b = steer(last_b.position, new_node.position, self.step_size)
                if not can_add_node(next_pos_b) or not can_connect(last_b.position, next_pos_b):
                    break

                cost_b = last_b.cost + dist(last_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, last_b, cost_b)
                last_b.add_child(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_b, new_node_b))

                near_b = prox_graph_b.radius_search(next_pos_b, radius)
                for near_nb in near_b:
                    if near_nb is last_b:
                        continue
                    cost_via_new_b = new_node_b.cost + dist(new_node_b.position, near_nb.position)
                    if cost_via_new_b + 1e-12 < near_nb.cost and can_connect(new_node_b.position, near_nb.position):
                        if near_nb.parent:
                            near_nb.parent.remove_child(near_nb)
                            try:
                                edges.remove((near_nb.parent, near_nb))
                            except ValueError:
                                pass
                        near_nb.update_parent(new_node_b, cost_via_new_b)
                        edges.append((new_node_b, near_nb))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist_to_new
                    connection_node = Node(new_node.position, new_node_b, final_cost)
                    new_node_b.add_child(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True

                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        # Aggressive shortcut smoothing on improved path
                        best_path = path_shortcut(merged_path, self.smoothing_iterations)

                        # Rewiring neighborhoods on shortcut path nodes to improve smoothness further
                        shortcut_nodes = []
                        pos_to_node = {nd.position: nd for nd in nodes if nd.valid}
                        for pt in best_path:
                            if pt in pos_to_node:
                                shortcut_nodes.append(pos_to_node[pt])
                        for idx, snode in enumerate(shortcut_nodes):
                            neighbors = prox_graph_a.radius_search(snode.position, radius)
                            for nbr in neighbors:
                                if nbr is snode or not nbr.valid:
                                    continue
                                trial_cost = snode.cost + dist(snode.position, nbr.position)
                                if trial_cost + 1e-12 < nbr.cost and can_connect(snode.position, nbr.position):
                                    if nbr.parent:
                                        nbr.parent.remove_child(nbr)
                                        try:
                                            edges.remove((nbr.parent, nbr))
                                        except ValueError:
                                            pass
                                    nbr.update_parent(snode, trial_cost)
                                    edges.append((snode, nbr))

                    connected = True
                    break

                last_b = new_node_b

            if connected:
                extracted_path = best_path
                # continue to improve or break early (for efficiency break)
                break

            # Periodic pruning of nodes outside the improved ellipsoid to reduce overhead
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > 100 and c_best < float('inf'):
                removed_count = prune_nodes(c_best)
                # Clear collision cache after pruning to avoid stale cache
                edge_collision_cache.clear()

        if not success_state:
            extracted_path = []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0, radius: float = 10.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.radius = radius

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        start_tree = [Node(start_position)]
        goal_tree = [Node(goal_position)]
        nodes = start_tree + goal_tree
        edges = []
        success_state = False
        extracted_path = []

        def sample_free():
            while True:
                point = tuple(
                    min(max(random.uniform(0, bounds[i]), 0), bounds[i]) for i in range(len(bounds))
                )
                if not self._is_in_obstacle(point, obstacles, is_3d):
                    return point

        def distance(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos, step):
            dist = distance(from_pos, to_pos)
            if dist <= step:
                return to_pos
            ratio = step / dist
            return tuple(from_pos[i] + (to_pos[i] - from_pos[i]) * ratio for i in range(len(from_pos)))

        def nearest(tree, point):
            return min(tree, key=lambda node: distance(node.position, point))

        def near(tree, point, radius):
            return [node for node in tree if distance(node.position, point) <= radius]

        def extract_path(node_a, node_b):
            path_a = []
            while node_a:
                path_a.append(node_a.position)
                node_a = node_a.parent
            path_b = []
            while node_b:
                path_b.append(node_b.position)
                node_b = node_b.parent
            return list(reversed(path_a)) + path_b

        for _ in range(self.max_iter):
            rand_point = sample_free()
            for tree_a, tree_b in [(start_tree, goal_tree), (goal_tree, start_tree)]:
                nearest_node = nearest(tree_a, rand_point)
                new_pos = steer(nearest_node.position, rand_point, self.step_size)

                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    continue
                if self._is_edge_in_obstacle(nearest_node.position, new_pos, obstacles, is_3d):
                    continue

                near_nodes = near(tree_a, new_pos, self.radius)
                min_cost = nearest_node.cost + distance(nearest_node.position, new_pos)
                min_parent = nearest_node

                for near_node in near_nodes:
                    candidate_cost = near_node.cost + distance(near_node.position, new_pos)
                    if candidate_cost < min_cost and not self._is_edge_in_obstacle(near_node.position, new_pos, obstacles, is_3d):
                        min_cost = candidate_cost
                        min_parent = near_node

                new_node = Node(new_pos, parent=min_parent, cost=min_cost)
                min_parent.add_child(new_node)
                tree_a.append(new_node)
                nodes.append(new_node)
                edges.append((min_parent, new_node))

                for near_node in near_nodes:
                    if near_node is min_parent:
                        continue
                    new_cost = new_node.cost + distance(new_node.position, near_node.position)
                    if new_cost < near_node.cost and not self._is_edge_in_obstacle(new_node.position, near_node.position, obstacles, is_3d):
                        if near_node.parent:
                            edges.remove((near_node.parent, near_node))
                        near_node.parent = new_node
                        near_node.cost = new_cost
                        new_node.add_child(near_node)
                        edges.append((new_node, near_node))

                connect_node = nearest(tree_b, new_node.position)
                if distance(new_node.position, connect_node.position) <= self.step_size:
                    if not self._is_edge_in_obstacle(new_node.position, connect_node.position, obstacles, is_3d):
                        success_state = True
                        extracted_path = extract_path(new_node, connect_node)
                        return PlannerResult(success=success_state, path=extracted_path, nodes=nodes, edges=edges)

        return PlannerResult(success=success_state, path=extracted_path, nodes=nodes, edges=edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=0.5):
        dist = math.dist(from_pos, to_pos)
        steps = max(1, int(dist / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position            # Tuple[float, ...]: 2D or 3D coordinate
        self.parent = parent                # Parent Node or None
        self.cost = cost                    # Cost from root
        self.children: List[Node] = []     # Children nodes (for rewiring/pruning)
        self.valid = True                   # Node validity flag (for pruning, collision, etc.)

    def add_child(self, child_node: "Node"):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent: "Node", new_cost: float):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except (ValueError, AttributeError):
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0, max_neighbor_radius: float = 30.0,
                 prune_interval: int = 200, prune_threshold: int = 300,
                 smoothing_trials: int = 150, collision_check_resolution: float = 1.0,
                 goal_sample_rate: float = 0.15):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_trials = smoothing_trials
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> PlannerResult:
        import math
        import random
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        is_3d = len(bounds) == 3
        dim = len(bounds)

        def in_bounds(p: Tuple[float, ...]) -> bool:
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a: Tuple[float, ...], b: Tuple[float, ...]) -> float:
            return math.dist(a, b)

        def steer(from_p: Tuple[float, ...], to_p: Tuple[float, ...]) -> Tuple[float, ...]:
            d = dist(from_p, to_p)
            if d <= self.step_size:
                new_p = to_p
            else:
                ratio = self.step_size / d
                new_p = tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))
            # Clamp to bounds
            return tuple(max(0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(n_nodes: int, iteration: int) -> float:
            if n_nodes <= 1:
                return self.max_neighbor_radius
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.95)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos: Tuple[float, ...]) -> bool:
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1: Tuple[float, ...], p2: Tuple[float, ...]) -> bool:
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes: List[Tuple[Tuple[float, ...], Node]]):
                self.dim = dim
                self.points = [pn[0] for pn in points_nodes]
                self.nodes = [pn[1] for pn in points_nodes]

            def nearest(self, pt: Tuple[float, ...]) -> Node:
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d_ = dist(pt, p)
                    if d_ < best_dist:
                        best_dist = d_
                        best = self.nodes[i]
                return best

            def radius_search(self, pt: Tuple[float, ...], radius: float) -> List[Node]:
                radius_sq = radius * radius
                res: List[Node] = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_ in range(self.dim):
                        delta = p[d_] - pt[d_]
                        sqd += delta*delta
                        if sqd > radius_sq:
                            break
                    if sqd <= radius_sq:
                        res.append(self.nodes[i])
                return res

        edge_collision_cache = {}

        def can_connect(p1: Tuple[float, ...], p2: Tuple[float, ...]) -> bool:
            key = (p1, p2) if p1 < p2 else (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos: Tuple[float, ...]) -> bool:
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def informed_sample(c_best: float, c_min: float,
                            start: Tuple[float, ...], goal: Tuple[float, ...]) -> Tuple[float, ...]:
            # Sample inside ellipsoid of current best path or uniform with goal bias
            if (c_best == float('inf') or c_best < c_min * 1.00001 or random.random() < self.goal_sample_rate):
                # Uniform sampling with rejection
                for _ in range(20):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d])/2 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])
            id_mat = np.eye(dim)
            u = a1 - id_mat[:, 0]
            norm_u = np.linalg.norm(u)
            if norm_u > 1e-10:
                u /= norm_u
                rot = id_mat - 2 * np.outer(u, u)
            else:
                rot = id_mat

            r1 = c_best / 2.0
            r2 = math.sqrt(max(r1*r1 - (c_min/2)*(c_min/2), 0))
            radii = np.array([r1] + [r2]*(dim-1))

            for _ in range(150):
                x = np.random.uniform(-1, 1, dim)
                norm_x = np.linalg.norm(x)
                if norm_x < 1e-15 or norm_x > 1:
                    continue
                x_unit = x / norm_x
                rad_sample = np.random.uniform(0, 1) ** (1/dim)
                sample = x_unit * rad_sample * radii
                p = rot @ sample + center
                p_tuple = tuple(float(coord) for coord in p)
                if in_bounds(p_tuple) and not is_in_obstacle(p_tuple):
                    return p_tuple

            for _ in range(20):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p
            # fallback
            return p

        def adaptive_sample(best_path: List[Tuple[float, ...]], c_best: float, c_min: float) -> Tuple[float, ...]:
            # Sample near best path with gaussian noise (75%) else informed_sample
            if best_path and random.random() < 0.75:
                for _ in range(50):
                    base = best_path[random.randint(0, len(best_path)-1)]
                    noise = np.random.normal(0, self.step_size*0.5, dim)
                    candidate = tuple(base[d] + noise[d] for d in range(dim))
                    if in_bounds(candidate) and not is_in_obstacle(candidate):
                        return candidate
            return informed_sample(c_best, c_min, start_pos, goal_pos)

        def shortcut_path(path: List[Tuple[float, ...]]) -> List[Tuple[float, ...]]:
            if len(path) < 3:
                return path[:]
            smooth_path = path[:]
            for _ in range(self.smoothing_trials):
                if len(smooth_path) < 3:
                    break
                i = random.randint(0, len(smooth_path)-3)
                j = random.randint(i+2, len(smooth_path)-1)
                p1, p2 = smooth_path[i], smooth_path[j]
                if can_connect(p1, p2):
                    smooth_path = smooth_path[:i+1] + smooth_path[j:]
            return smooth_path

        def prune_nodes(node_list: List[Node], best_cost: float, root_s: Node, root_g: Node) -> int:
            removed = 0
            heuristic = lambda pos: dist(pos, goal_pos)
            to_remove = []
            for node in node_list:
                if node in (root_s, root_g):
                    continue
                if node.cost + heuristic(node.position) >= best_cost - 1e-9:
                    to_remove.append(node)
            for node in to_remove:
                if node.parent:
                    try:
                        node.parent.children.remove(node)
                    except (ValueError, AttributeError):
                        pass
                try:
                    node_list.remove(node)
                except ValueError:
                    pass
                removed += 1
            return removed

        # Initialization
        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        tree_start = [root_start]
        tree_goal = [root_goal]
        nodes = [root_start, root_goal]
        edges: List[Tuple[Node, Node]] = []

        c_min = dist(start_pos, goal_pos)
        c_best = float('inf')
        best_path: List[Tuple[float, ...]] = []
        success_state = False
        extracted_path: List[Tuple[float, ...]] = []

        for it in range(self.max_iter):
            q_rand = adaptive_sample(best_path, c_best, c_min)

            # Alternate growth by extending smaller tree first
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(node.position, node) for node in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            radius_a = neighbor_radius(len(nodes), it)
            near_nodes_a = kdtree_a.radius_search(new_pos, radius_a)

            # Choose best parent minimizing cost with collision-free edges
            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near_nodes_a:
                cost_cand = node.cost + dist(node.position, new_pos)
                if cost_cand + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_cand
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewiring neighbors
            for node in near_nodes_a:
                if node is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, node.position)
                if new_cost + 1e-12 < node.cost and can_connect(new_node.position, node.position):
                    try:
                        edges.remove((node.parent, node))
                    except ValueError:
                        pass
                    node.update_parent(new_node, new_cost)
                    edges.append((new_node, node))

            # Incremental multi-step connection to other tree
            kdtree_b = KDTree([(node.position, node) for node in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            max_steps = int(dist(nearest_b.position, new_node.position) / self.step_size) + 1
            last_node_b = nearest_b
            connected = False

            for step in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break
                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                radius_b = neighbor_radius(len(nodes), it)
                near_nodes_b = kdtree_b.radius_search(next_pos_b, radius_b)

                # Rewire neighbors in tree_b
                for nbr in near_nodes_b:
                    if nbr is last_node_b:
                        continue
                    new_cost_b = new_node_b.cost + dist(new_node_b.position, nbr.position)
                    if new_cost_b + 1e-12 < nbr.cost and can_connect(new_node_b.position, nbr.position):
                        try:
                            edges.remove((nbr.parent, nbr))
                        except ValueError:
                            pass
                        nbr.update_parent(new_node_b, new_cost_b)
                        edges.append((new_node_b, nbr))

                dist_to_new = dist(new_node_b.position, new_node.position)
                if dist_to_new <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    cost_connect = new_node_b.cost + dist_to_new
                    connect_node = Node(new_node.position, parent=new_node_b, cost=cost_connect)
                    new_node_b.children.append(connect_node)
                    tree_b.append(connect_node)
                    nodes.append(connect_node)
                    edges.append((new_node_b, connect_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connect_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connect_node.path_from_root()

                    merged_path = path_start + path_goal[::-1][1:]

                    if cost_connect + 1e-12 < c_best:
                        c_best = cost_connect
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break

                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                # Aggressive shortcut smoothing on improvement
                best_path = shortcut_path(best_path)
                extracted_path = best_path
                if c_best <= c_min * 1.00001:
                    # Close to optimal; can break early
                    break

            # Periodic pruning to remove nodes that cannot improve solution
            if it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf'):
                removed_start = prune_nodes(tree_start, c_best, root_start, root_goal)
                removed_goal = prune_nodes(tree_goal, c_best, root_start, root_goal)
                # Remove corresponding edges linked to pruned nodes
                edges = [(p, c) for (p, c) in edges if p in nodes and c in nodes]
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        if is_3d:
            pz = pos[2]
            for obs in obstacles:
                x, y, z, w, h, d = obs
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
        else:
            for obs in obstacles:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] → 2D: (x,y), 3D: (x,y,z)
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child):
        self.children.append(child)
        child.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        node, path = self, []
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self,
                 max_iter: int = 5000,
                 step_size: float = 5.0,
                 min_neighbor_radius: float = 5.0,
                 max_neighbor_radius: float = 30.0,
                 prune_interval: int = 150,
                 prune_threshold: int = 250,
                 smoothing_iterations: int = 120,
                 collision_check_resolution: float = 0.5,
                 goal_sample_rate: float = 0.05):
        self.max_iter = max_iter
        self.step_size = step_size
        self.min_neighbor_radius = min_neighbor_radius
        self.max_neighbor_radius = max_neighbor_radius
        self.prune_interval = prune_interval
        self.prune_threshold = prune_threshold
        self.smoothing_iterations = smoothing_iterations
        self.collision_check_resolution = collision_check_resolution
        self.goal_sample_rate = goal_sample_rate

    def plan(self, map) -> 'PlannerResult':
        import random
        import math
        import numpy as np

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0 <= p[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                new_p = to_pos
            else:
                ratio = self.step_size / d
                new_p = tuple(from_pos[d] + ratio * (to_pos[d] - from_pos[d]) for d in range(dim))
            # clamp within bounds
            return tuple(max(0.0, min(new_p[d], bounds[d])) for d in range(dim))

        def neighbor_radius(iteration, n_nodes):
            c = max(n_nodes, 1)
            r_iter = self.max_neighbor_radius * (1.0 - (iteration / self.max_iter) ** 0.9)
            r_nodes = self.min_neighbor_radius + (self.max_neighbor_radius - self.min_neighbor_radius) * math.sqrt(math.log(c + 1) / c)
            return max(self.min_neighbor_radius, min(r_iter, r_nodes))

        def is_in_obstacle(pos):
            return self._is_in_obstacle(pos, obstacles, is_3d)

        def is_edge_in_obstacle(p1, p2):
            return self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, self.collision_check_resolution)

        class KDTree:
            def __init__(self, points_nodes):
                self.dim = dim
                self.points = [p for p, n in points_nodes]
                self.nodes = [n for p, n in points_nodes]

            def nearest(self, pt):
                best = None
                best_dist = float('inf')
                for i, p in enumerate(self.points):
                    d = dist(pt, p)
                    if d < best_dist:
                        best_dist = d
                        best = self.nodes[i]
                return best

            def radius_search(self, pt, radius):
                r2 = radius * radius
                result = []
                for i, p in enumerate(self.points):
                    sqd = 0.0
                    for d_i in range(self.dim):
                        delta = p[d_i] - pt[d_i]
                        sqd += delta * delta
                        if sqd > r2:
                            break
                    if sqd <= r2:
                        result.append(self.nodes[i])
                return result

        def informed_sample(c_best, c_min, start, goal):
            # If no solution yet or c_best very close to c_min, sample uniformly + goal bias
            if c_best == float('inf') or c_best < c_min * 1.00001:
                if random.random() < self.goal_sample_rate:
                    return goal if random.random() < 0.7 else start
                for _ in range(15):
                    p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p
                # fallback
                return tuple(random.uniform(0, bounds[d]) for d in range(dim))

            center = np.array([(start[d] + goal[d]) / 2.0 for d in range(dim)])
            a1 = np.array([(goal[d] - start[d]) / c_min for d in range(dim)])

            e1 = np.zeros(dim)
            e1[0] = 1.0
            v = a1 - e1
            norm_v = np.linalg.norm(v)
            if norm_v > 1e-12:
                v /= norm_v
                rot = np.eye(dim) - 2.0 * np.outer(v, v)
            else:
                rot = np.eye(dim)

            r1 = c_best / 2.0
            val = r1 * r1 - (c_min / 2) ** 2
            r_others = math.sqrt(val) if val > 0 else 0.0
            radii = np.array([r1] + [r_others] * (dim - 1))

            for _ in range(120):
                x_ball = np.random.uniform(-1, 1, dim)
                if np.linalg.norm(x_ball) <= 1.0:
                    sample = rot @ (x_ball * radii) + center
                    p = tuple(np.clip(sample, 0.0, bounds[d]) for d in range(dim))
                    if not is_in_obstacle(p):
                        return p

            # fallback uniform sampling outside informed set, check obstacle
            for _ in range(15):
                p = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if not is_in_obstacle(p):
                    return p

            # last fallback
            return p

        edge_collision_cache = {}

        def can_connect(p1, p2):
            key = (p1, p2)
            key_rev = (p2, p1)
            if key in edge_collision_cache:
                return not edge_collision_cache[key]
            if key_rev in edge_collision_cache:
                return not edge_collision_cache[key_rev]
            if not in_bounds(p1) or not in_bounds(p2):
                edge_collision_cache[key] = True
                return False
            collision = is_edge_in_obstacle(p1, p2)
            edge_collision_cache[key] = collision
            return not collision

        def can_add_node(pos):
            if not in_bounds(pos):
                return False
            if is_in_obstacle(pos):
                return False
            return True

        def shortcut_path(path):
            if len(path) < 3:
                return path
            for _ in range(self.smoothing_iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                p1, p2 = path[i], path[j]
                if can_connect(p1, p2):
                    path = path[:i+1] + path[j:]
            return path

        def prune_useless_nodes(best_cost):
            to_remove = []
            for node in nodes:
                if node in (root_start, root_goal):
                    continue
                heuristic = dist(node.position, goal_pos)
                if node.cost + heuristic >= best_cost:
                    to_remove.append(node)
            for rm_node in to_remove:
                if rm_node.parent:
                    try:
                        rm_node.parent.children.remove(rm_node)
                    except ValueError:
                        pass
                try:
                    nodes.remove(rm_node)
                except ValueError:
                    pass
                if rm_node in tree_start:
                    tree_start.remove(rm_node)
                if rm_node in tree_goal:
                    tree_goal.remove(rm_node)

        root_start = Node(start_pos, cost=0.0)
        root_goal = Node(goal_pos, cost=0.0)
        nodes = [root_start, root_goal]
        edges = []
        tree_start = [root_start]
        tree_goal = [root_goal]

        c_best = float('inf')
        c_min = dist(start_pos, goal_pos)
        best_path = []

        success_state = False
        extracted_path = []

        for it in range(self.max_iter):
            q_rand = informed_sample(c_best, c_min, start_pos, goal_pos)

            # Alternate growth: extend smaller tree first
            if len(tree_start) <= len(tree_goal):
                tree_a, tree_b = tree_start, tree_goal
                root_a, root_b = root_start, root_goal
            else:
                tree_a, tree_b = tree_goal, tree_start
                root_a, root_b = root_goal, root_start

            kdtree_a = KDTree([(n.position, n) for n in tree_a])
            nearest_a = kdtree_a.nearest(q_rand)
            new_pos = steer(nearest_a.position, q_rand)

            if not can_add_node(new_pos) or not can_connect(nearest_a.position, new_pos):
                continue

            r = neighbor_radius(it, len(nodes))
            near = kdtree_a.radius_search(new_pos, r)

            min_cost = nearest_a.cost + dist(nearest_a.position, new_pos)
            best_parent = nearest_a
            for node in near:
                cost_candidate = node.cost + dist(node.position, new_pos)
                if cost_candidate + 1e-12 < min_cost and can_connect(node.position, new_pos):
                    min_cost = cost_candidate
                    best_parent = node

            new_node = Node(new_pos, parent=best_parent, cost=min_cost)
            best_parent.children.append(new_node)
            tree_a.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors
            for neighbor in near:
                if neighbor == best_parent:
                    continue
                c_via_new = new_node.cost + dist(new_node.position, neighbor.position)
                if c_via_new + 1e-12 < neighbor.cost and can_connect(new_node.position, neighbor.position):
                    try:
                        edges.remove((neighbor.parent, neighbor))
                    except ValueError:
                        pass
                    neighbor.update_parent(new_node, c_via_new)
                    edges.append((new_node, neighbor))

            # Attempt connection between trees incrementally
            kdtree_b = KDTree([(n.position, n) for n in tree_b])
            nearest_b = kdtree_b.nearest(new_node.position)
            last_node_b = nearest_b
            connected = False

            max_steps = int(dist(last_node_b.position, new_node.position) / self.step_size) + 1
            for _ in range(max_steps):
                next_pos_b = steer(last_node_b.position, new_node.position)
                if not can_add_node(next_pos_b) or not can_connect(last_node_b.position, next_pos_b):
                    break

                cost_b = last_node_b.cost + dist(last_node_b.position, next_pos_b)
                new_node_b = Node(next_pos_b, parent=last_node_b, cost=cost_b)
                last_node_b.children.append(new_node_b)
                tree_b.append(new_node_b)
                nodes.append(new_node_b)
                edges.append((last_node_b, new_node_b))

                near_b = kdtree_b.radius_search(next_pos_b, r)
                for neighbor_b in near_b:
                    if neighbor_b == last_node_b:
                        continue
                    c_via_new_b = new_node_b.cost + dist(new_node_b.position, neighbor_b.position)
                    if c_via_new_b + 1e-12 < neighbor_b.cost and can_connect(new_node_b.position, neighbor_b.position):
                        try:
                            edges.remove((neighbor_b.parent, neighbor_b))
                        except ValueError:
                            pass
                        neighbor_b.update_parent(new_node_b, c_via_new_b)
                        edges.append((new_node_b, neighbor_b))

                if dist(new_node_b.position, new_node.position) <= self.step_size and can_connect(new_node_b.position, new_node.position):
                    final_cost = new_node_b.cost + dist(new_node_b.position, new_node.position)
                    connection_node = Node(new_node.position, parent=new_node_b, cost=final_cost)
                    new_node_b.children.append(connection_node)
                    tree_b.append(connection_node)
                    nodes.append(connection_node)
                    edges.append((new_node_b, connection_node))

                    success_state = True
                    if tree_a is tree_start:
                        path_start = connection_node.path_from_root()
                        path_goal = new_node.path_from_root()
                    else:
                        path_start = new_node.path_from_root()
                        path_goal = connection_node.path_from_root()
                    merged_path = path_start + path_goal[::-1][1:]

                    if final_cost + 1e-12 < c_best:
                        c_best = final_cost
                        best_path = shortcut_path(merged_path)

                    connected = True
                    break
                last_node_b = new_node_b

            if connected:
                extracted_path = best_path
                break

            if (it > 0 and it % self.prune_interval == 0 and len(nodes) > self.prune_threshold and c_best < float('inf')):
                prune_useless_nodes(c_best)
                edge_collision_cache.clear()

        if success_state and not extracted_path:
            extracted_path = best_path if best_path else []

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        px = pos[0]
        py = pos[1]
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                pz = pos[2]
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False
