class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position          # Tuple[float, ...]
        self.parent = None                # Node or None
        self.cost = cost                  # Cost from root
        self.children = []
        self.valid = True                 # Validity flag

        if parent is not None:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        dist_func = math.dist
        for child in self.children:
            child.update_parent(self, self.cost + dist_func(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self,
                 max_iter: int = 5000,
                 step_size: float = 5.0,
                 goal_sample_rate: float = 0.2,
                 neighbor_radius_min: float = 8.0,
                 neighbor_radius_max: float = 25.0,
                 max_planning_time: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> 'PlannerResult':
        import time
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist_func(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            new_pt = tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp to bounds
            return tuple(min(max(new_pt[d], 0.0), bounds[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes: int):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 30.0
            r = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist_func(node.position, pos) ** 2) <= r_sq]

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        # Rotation matrix for ellipsoidal sampling
        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0,1 - cos_theta*cos_theta))
                K = [[0, -kz, ky],[kz,0,-kx],[-ky,kx,0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A, B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A, s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A, B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1-cos_theta))

        self._rotation_matrix = rotation_matrix

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x/norm for x in pt)

        self._unit_ball_sample = unit_ball_sample

        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_free()
            center = tuple(0.5*(start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = self._rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = self._unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1] + R[0][2]*sample_scaled[2]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1] + R[1][2]*sample_scaled[2]
                    z_rot = R[2][0]*sample_scaled[0] + R[2][1]*sample_scaled[1] + R[2][2]*sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: list, sample: tuple) -> Node or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: list, target_pos: tuple) -> Node or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                    if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through_nbr
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                if dist_func(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_smoothing(path: list, trials: int = 100) -> list:
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        best_path = []
        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - start_time
            if elapsed >= self.max_planning_time:
                break

            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start else start_pos
                else:
                    sample_pt = sample_free()

            if grow_start:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_goal, new_node.position)
                if connected_node is not None:
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        smoothed = path_smoothing(best_path)
                        success_state = True
                        all_nodes = tree_start + tree_goal
                        all_edges = []
                        for node in all_nodes:
                            for c in node.children:
                                all_edges.append((node, c))
                        return PlannerResult(True, smoothed, all_nodes, all_edges)
            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_start, new_node.position)
                if connected_node is not None:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        smoothed = path_smoothing(best_path)
                        success_state = True
                        all_nodes = tree_start + tree_goal
                        all_edges = []
                        for node in all_nodes:
                            for c in node.children:
                                all_edges.append((node, c))
                        return PlannerResult(True, smoothed, all_nodes, all_edges)

            grow_start = not grow_start

        success_state = len(best_path) > 0
        smoothed = path_smoothing(best_path) if success_state else []

        all_nodes = tree_start + tree_goal
        all_edges = []
        for node in all_nodes:
            for c in node.children:
                all_edges.append((node, c))

        return PlannerResult(success_state, smoothed, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = None              # Node or None
        self.cost = 0.0
        self.children = []
        self.valid = True

        if parent is not None:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node') -> None:
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node') -> None:
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float) -> None:
        if self.parent is new_parent and abs(self.cost - new_cost) < 1e-10:
            return
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        if new_parent and self not in new_parent.children:
            new_parent.children.append(self)
        for child in self.children:
            child.update_parent(self, self.cost + math.dist(self.position, child.position))

    def path_from_root(self) -> list:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float = 5.0, goal_sample_rate: float = 0.2, 
                 neighbor_radius_min: float = 8.0, neighbor_radius_max: float = 30.0, max_planning_time: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist_func(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes: int):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma = 30.0
            r = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist_func(node.position, pos) ** 2) <= r_sq]

        def uniform_sample():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                c, s = math.cos(angle), math.sin(angle)
                return [[c, -s], [s, c]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx**2 + ky**2 + kz**2)
                if norm_k < 1e-10:
                    return [[1, 0, 0], [0, 1, 0], [0, 0, 1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0.0, 1 - cos_theta * cos_theta))
                K = [[0, -kz, ky], [kz, 0, -kx], [-ky, kx, 0]]
                I = [[1, 0, 0], [0, 1, 0], [0, 0, 1]]

                def mat_add(A, B):
                    return [[A[i][j] + B[i][j] for j in range(3)] for i in range(3)]

                def mat_scale(A, s):
                    return [[A[i][j] * s for j in range(3)] for i in range(3)]

                def mat_mul(A, B):
                    return [[sum(A[i][k] * B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in pt)

        def informed_sample(c_best):
            c_min = dist_func(start_pos, goal_pos)
            if c_best == math.inf or c_best < c_min - 1e-10:
                return uniform_sample()
            center = tuple(0.5 * (start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)
            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1] + [a_minor] * (dim - 1)

            n = dim
            while True:
                dir_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))
                if dim == 2:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = sum(R[0][i] * sample_scaled[i] for i in range(n))
                    y_rot = sum(R[1][i] * sample_scaled[i] for i in range(n))
                    z_rot = sum(R[2][i] * sample_scaled[i] for i in range(n))
                    rotated = (x_rot, y_rot, z_rot)
                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: list, sample: tuple):
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is best_parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: list, target_pos: tuple):
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                    if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through_nbr
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                if dist_func(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                if time.perf_counter() - start_time > self.max_planning_time:
                    return None

                current = new_node

        def path_smoothing(path: list, trials: int = 100):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path


        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        best_path = []
        best_cost = math.inf
        informed_sampling = False
        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                break

            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start else start_pos
                else:
                    sample_pt = uniform_sample()

            if grow_start:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connected_node = incremental_connect(tree_goal, new_node.position)
                if connected_node:
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        smoothed = path_smoothing(best_path)
                        all_nodes = tree_start + tree_goal
                        all_edges = []
                        for node in all_nodes:
                            for child in node.children:
                                all_edges.append((node, child))
                        return PlannerResult(True, smoothed, all_nodes, all_edges)
            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connected_node = incremental_connect(tree_start, new_node.position)
                if connected_node:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        smoothed = path_smoothing(best_path)
                        all_nodes = tree_start + tree_goal
                        all_edges = []
                        for node in all_nodes:
                            for child in node.children:
                                all_edges.append((node, child))
                        return PlannerResult(True, smoothed, all_nodes, all_edges)

            grow_start = not grow_start

        success_state = (len(best_path) > 0)
        if success_state:
            smoothed = path_smoothing(best_path)
        else:
            smoothed = []

        all_nodes = tree_start + tree_goal
        all_edges = []
        for node in all_nodes:
            for child in node.children:
                all_edges.append((node, child))

        return PlannerResult(success_state, smoothed, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * factor for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position            # Tuple[float, ...], 2D or 3D
        self.parent = None                  # Parent Node or None
        self.cost = cost                    # Cost from root to this node
        self.children = []                  # List of child nodes
        self.valid = True                   # For obstacle validity

        if parent is not None:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        dist = math.dist
        for child in self.children:
            child.update_parent(self, self.cost + dist(self.position, child.position))

    def path_from_root(self) -> list:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.2, neighbor_radius_min: float = 10.0,
                 neighbor_radius_max: float = 30.0, max_planning_time: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> 'PlannerResult':
        import random
        import math
        import time

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist = math.dist
        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes: int):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 30.0
            r = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist(node.position, pos) ** 2) <= r_sq]

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1, 0, 0], [0, 1, 0], [0, 0, 1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0, 1 - cos_theta * cos_theta))
                K = [[0, -kz, ky], [kz, 0, -kx], [-ky, kx, 0]]
                I = [[1, 0, 0], [0, 1, 0], [0, 0, 1]]

                def mat_add(A, B):
                    return [[A[i][j] + B[i][j] for j in range(3)] for i in range(3)]

                def mat_scale(A, s):
                    return [[A[i][j] * s for j in range(3)] for i in range(3)]

                def mat_mul(A, B):
                    return [[sum(A[i][k] * B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))

        self._rotation_matrix = rotation_matrix

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    normed = tuple(x / math.sqrt(norm_sq) for x in pt)
                    return normed

        self._unit_ball_sample = unit_ball_sample

        c_min = dist(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_free()
            center = tuple(0.5 * (start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = self._rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = self._unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1] + R[0][2] * sample_scaled[2]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1] + R[1][2] * sample_scaled[2]
                    z_rot = R[2][0] * sample_scaled[0] + R[2][1] * sample_scaled[1] + R[2][2] * sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: list, sample: tuple) -> Node or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through_nbr = nbr.cost + dist(nbr.position, new_pos)
                if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: list, target_pos: tuple) -> Node or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through_nbr = nbr.cost + dist(nbr.position, new_pos)
                    if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through_nbr
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                if dist(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_smoothing(path: list, trials: int = 100) -> list:
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        all_nodes = tree_start + tree_goal
        all_edges = []

        grow_start = True
        iteration = 0

        best_cost = math.inf
        best_path = []
        informed_sampling = False

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                break

            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start else start_pos
                else:
                    sample_pt = sample_free()

            if grow_start:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_goal, new_node.position)
                if connected_node is not None:
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        all_nodes = tree_start + tree_goal
                        all_edges.clear()
                        for node in all_nodes:
                            for c in node.children:
                                all_edges.append((node, c))

                        smoothed = path_smoothing(best_path)
                        return PlannerResult(True, smoothed, all_nodes, all_edges)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_start, new_node.position)
                if connected_node is not None:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        all_nodes = tree_start + tree_goal
                        all_edges.clear()
                        for node in all_nodes:
                            for c in node.children:
                                all_edges.append((node, c))

                        smoothed = path_smoothing(best_path)
                        return PlannerResult(True, smoothed, all_nodes, all_edges)

            grow_start = not grow_start

        success_state = (len(best_path) > 0)
        smoothed = path_smoothing(best_path) if success_state else []

        all_nodes = tree_start + tree_goal
        all_edges.clear()
        for node in all_nodes:
            for c in node.children:
                all_edges.append((node, c))

        return PlannerResult(success_state, smoothed, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position              # Node coordinate tuple, 2D or 3D
        self.parent = None                    # Parent Node or None
        self.cost = cost                      # Cost from root
        self.children: List[Node] = []       # Children nodes list
        self.valid = True                     # Collision validity flag

        if parent is not None:
            self.update_parent(parent, parent.cost + self._dist(parent.position, position))

    def add_child(self, child_node):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        # Detach from current parent if any
        if self.parent is not None:
            self.parent.remove_child(self)
        # Attach to new parent
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        # Recursively update descendants' costs and parents
        for child in self.children:
            child.update_parent(self, self.cost + self._dist(self.position, child.position))

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]

    @staticmethod
    def _dist(p1: Tuple[float, ...], p2: Tuple[float, ...]) -> float:
        import math
        return math.dist(p1, p2)
class Planner:
    def __init__(self, 
                 max_iter: int = 15000, 
                 step_size: float = 5.0,
                 goal_sample_rate: float = 0.15,
                 neighbor_radius_min: float = 5.0,
                 neighbor_radius_max: float = 30.0,
                 smoothing_trials: int = 150,
                 max_planning_time: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.smoothing_trials = smoothing_trials
        self.max_planning_time = max_planning_time

    def plan(self, map) -> PlannerResult:
        import time
        import math
        import random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist = math.dist

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def clamp(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            scale = self.step_size / d
            return tuple(from_pos[d] + scale * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            resolution = self.step_size / 5.0
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution)

        def adaptive_neighbor_radius(n_nodes, iter_num):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 30.0
            base_radius = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            iteration_factor = min(1.0, iter_num / self.max_iter)
            radius = base_radius * (1.0 - 0.6 * iteration_factor)  # Reduce radius over time
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, radius))

        def near_nodes(tree_nodes, pos, radius):
            rad_sq = radius * radius
            return [node for node in tree_nodes if (dist(node.position, pos) ** 2) <= rad_sq]

        def sample_uniform():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        def unit_ball_sample(n):
            while True:
                point = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in point)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in point)

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a = math.cos(angle)
                sin_a = math.sin(angle)
                return [[cos_a, -sin_a],
                        [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1, 0, 0],
                            [0, 1, 0],
                            [0, 0, 1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k

                cos_theta = vx
                sin_theta = math.sqrt(max(0, 1 - cos_theta * cos_theta))
                K = [[0, -kz, ky],
                     [kz, 0, -kx],
                     [-ky, kx, 0]]
                I = [[1, 0, 0],
                     [0, 1, 0],
                     [0, 0, 1]]

                def mat_add(A, B):
                    return [[A[i][j] + B[i][j] for j in range(3)] for i in range(3)]

                def mat_scale(A, s):
                    return [[A[i][j] * s for j in range(3)] for i in range(3)]

                def mat_mul(A, B):
                    return [[sum(A[i][k] * B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_uniform()
            center = tuple(0.5 * (start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1] + R[0][2] * sample_scaled[2]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1] + R[1][2] * sample_scaled[2]
                    z_rot = R[2][0] * sample_scaled[0] + R[2][1] * sample_scaled[1] + R[2][2] * sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                clamped = clamp(sample_pt)
                if collision_free_node(clamped):
                    return clamped

        def extend_and_rewire(tree: List[Node], sample: Tuple[float, ...], iter_num: int) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist(n.position, sample))
            new_pos = steer(nearest.position, sample)
            new_pos = clamp(new_pos)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1, iter_num)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through_nbr = nbr.cost + dist(nbr.position, new_pos)
                if cost_through_nbr + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            # Rewire neighbors toward new_node if cheaper
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_through_new + 1e-12 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: List[Node], target_pos: Tuple[float, ...], iter_num: int) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                new_pos = clamp(new_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1, iter_num)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through_nbr = nbr.cost + dist(nbr.position, new_pos)
                    if cost_through_nbr + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through_nbr
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist(new_node.position, nbr.position)
                    if cost_through_new + 1e-12 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                dist_to_target = dist(new_node.position, target_pos)
                if dist_to_target <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_shortcut(path: List[Tuple[float, ...]], trials: int = self.smoothing_trials):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start: List[Node] = [Node(start_pos)]
        tree_goal: List[Node] = [Node(goal_pos)]
        all_nodes = tree_start + tree_goal

        best_cost = math.inf
        best_path: List[Tuple[float, ...]] = []
        informed_sampling = False
        c_min = dist(start_pos, goal_pos)

        grow_start = True
        iteration = 0
        t_start = time.perf_counter()

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - t_start
            if elapsed > self.max_planning_time:
                break

            if informed_sampling:
                sample = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if grow_start else start_pos
                else:
                    sample = sample_uniform()

            active_tree = tree_start if grow_start else tree_goal
            other_tree = tree_goal if grow_start else tree_start

            new_node = extend_and_rewire(active_tree, sample, iteration)
            if new_node is None:
                grow_start = not grow_start
                continue
            all_nodes.append(new_node)

            connected_node = incremental_connect(other_tree, new_node.position, iteration)
            if connected_node is not None:
                path_from_start = new_node.path_from_root()
                path_from_goal = connected_node.path_from_root()

                # Avoid duplicated middle node
                if path_from_goal and path_from_goal[0] == new_node.position:
                    path_from_goal = path_from_goal[1:]
                combined_path = path_from_start + path_from_goal[::-1]

                total_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                if total_cost + 1e-12 < best_cost:
                    best_cost = total_cost
                    best_path = combined_path
                    informed_sampling = True

                    smoothed = path_shortcut(best_path)
                    return PlannerResult(True, smoothed, all_nodes, [(n, c) for n in all_nodes for c in n.children])

            grow_start = not grow_start

        success = bool(best_path)
        smoothed = path_shortcut(best_path) if success else []

        edges = [(n, c) for n in all_nodes for c in n.children]

        return PlannerResult(success, smoothed, all_nodes, edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * factor for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position                  # Tuple[float, ...]
        self.parent = None                        # Node or None
        self.cost = cost                          # Cost from root
        self.children = []                        # List[Node]
        self.valid = True                         # For collision/validity flag

        if parent is not None:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        dist_func = math.dist
        for child in self.children:
            child.update_parent(self, self.cost + dist_func(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.2,
                 neighbor_radius_min: float = 10.0,
                 neighbor_radius_max: float = 30.0,
                 max_planning_time: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist_func(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes: int):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 30.0
            r = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist_func(node.position, pos) ** 2) <= r_sq]

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        # Rotation matrix for ellipsoidal informed sampling
        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1, 0, 0], [0, 1, 0], [0, 0, 1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0, 1 - cos_theta * cos_theta))
                K = [[0, -kz, ky],
                     [kz, 0, -kx],
                     [-ky, kx, 0]]
                I = [[1, 0, 0],
                     [0, 1, 0],
                     [0, 0, 1]]

                def mat_add(A, B):
                    return [[A[i][j] + B[i][j] for j in range(3)] for i in range(3)]

                def mat_scale(A, s):
                    return [[A[i][j] * s for j in range(3)] for i in range(3)]

                def mat_mul(A, B):
                    return [[sum(A[i][k] * B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))

        self._rotation_matrix = rotation_matrix

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in pt)

        self._unit_ball_sample = unit_ball_sample

        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_free()
            center = tuple(0.5 * (start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = self._rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = self._unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1] + R[0][2] * sample_scaled[2]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1] + R[1][2] * sample_scaled[2]
                    z_rot = R[2][0] * sample_scaled[0] + R[2][1] * sample_scaled[1] + R[2][2] * sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                # Clamp to bounds
                sample_pt = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: list, sample: tuple) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: list, target_pos: tuple) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                    if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through_nbr
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                if dist_func(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                # Time check to limit execution during incremental connect
                if time.perf_counter() - start_time > self.max_planning_time:
                    return None

                current = new_node

        def path_smoothing(path: list, trials: int = 100):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        # Initialize trees
        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        all_nodes = tree_start + tree_goal
        all_edges = []

        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                break

            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start else start_pos
                else:
                    sample_pt = sample_free()

            if grow_start:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_goal, new_node.position)
                if connected_node:
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    all_nodes = tree_start + tree_goal
                    all_edges.clear()
                    for node in all_nodes:
                        for c in node.children:
                            all_edges.append((node, c))

                    smoothed = path_smoothing(best_path)
                    return PlannerResult(True, smoothed, all_nodes, all_edges)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_start, new_node.position)
                if connected_node:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    all_nodes = tree_start + tree_goal
                    all_edges.clear()
                    for node in all_nodes:
                        for c in node.children:
                            all_edges.append((node, c))

                    smoothed = path_smoothing(best_path)
                    return PlannerResult(True, smoothed, all_nodes, all_edges)

            grow_start = not grow_start

        # On time limit or iteration end, return best found path or failure
        success_state = (len(best_path) > 0)
        if success_state:
            smoothed = path_smoothing(best_path)
        else:
            smoothed = []

        all_nodes = tree_start + tree_goal
        all_edges.clear()
        for node in all_nodes:
            for c in node.children:
                all_edges.append((node, c))

        return PlannerResult(success_state, smoothed, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * factor for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = None
        self.cost = 0.0
        self.children = []
        self.valid = True
        if parent:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        # Recursively update costs of descendants
        for child in self.children:
            child.update_parent(self, self.cost + math.dist(self.position, child.position))

    def path_from_root(self) -> list:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.2,
                 neighbor_radius_min: float = 10.0,
                 neighbor_radius_max: float = 30.0,
                 max_planning_time: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and not self._is_in_obstacle(pos, obstacles, is_3d)

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes: int) -> float:
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 27.0  # Slightly tuned value for tighter neighbor radius
            r = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree: list, pos: tuple, radius: float) -> list:
            radius_sq = radius * radius
            return [node for node in tree if dist(node.position, pos) ** 2 <= radius_sq]

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        c_min = dist(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1, 0, 0], [0, 1, 0], [0, 0, 1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0, 1 - cos_theta * cos_theta))
                K = [[0, -kz, ky],
                     [kz, 0, -kx],
                     [-ky, kx, 0]]
                I = [[1, 0, 0],
                     [0, 1, 0],
                     [0, 0, 1]]

                def mat_add(A, B):
                    return [[A[i][j] + B[i][j] for j in range(3)] for i in range(3)]

                def mat_scale(A, s):
                    return [[A[i][j] * s for j in range(3)] for i in range(3)]

                def mat_mul(A, B):
                    return [[sum(A[i][k] * B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))

        self._rotation_matrix = rotation_matrix

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in pt)

        self._unit_ball_sample = unit_ball_sample

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_free()
            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = self._rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = self._unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = (R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1] + R[0][2] * sample_scaled[2])
                    y_rot = (R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1] + R[1][2] * sample_scaled[2])
                    z_rot = (R[2][0] * sample_scaled[0] + R[2][1] * sample_scaled[1] + R[2][2] * sample_scaled[2])
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: list, sample: tuple) -> 'Node':
            nearest = min(tree, key=lambda n: dist(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr == nearest:
                    continue
                cost_thru_nbr = nbr.cost + dist(nbr.position, new_pos)
                if cost_thru_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_thru_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_thru_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_thru_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_thru_new)

            return new_node

        def incremental_connect(tree: list, target_pos: tuple) -> 'Node':
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr == current:
                        continue
                    cost_thru_nbr = nbr.cost + dist(nbr.position, new_pos)
                    if cost_thru_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_thru_nbr
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_thru_new = new_node.cost + dist(new_node.position, nbr.position)
                    if cost_thru_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_thru_new)

                if dist(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_smoothing(path: list, trials: int = 100) -> list:
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        # Initialize trees
        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        grow_tree_start = True
        iter_cnt = 0
        success = False

        all_nodes = tree_start + tree_goal
        all_edges = []

        while iter_cnt < self.max_iter:
            if time.perf_counter() - start_time > self.max_planning_time:
                break
            iter_cnt += 1

            sample_pt = informed_sample(best_cost) if informed_sampling else (
                goal_pos if random.random() < self.goal_sample_rate else sample_free())

            if grow_tree_start:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if new_node is None:
                    grow_tree_start = not grow_tree_start
                    continue
                connected_node = incremental_connect(tree_goal, new_node.position)
                if connected_node:
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]
                    combined_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True
                        success = True
                        # Prepare edges and nodes for return
                        all_nodes = tree_start + tree_goal
                        all_edges.clear()
                        for node in all_nodes:
                            for c in node.children:
                                all_edges.append((node, c))
                        smoothed_path = path_smoothing(best_path)
                        return PlannerResult(True, smoothed_path, all_nodes, all_edges)
            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if new_node is None:
                    grow_tree_start = not grow_tree_start
                    continue
                connected_node = incremental_connect(tree_start, new_node.position)
                if connected_node:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]
                    combined_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True
                        success = True
                        all_nodes = tree_start + tree_goal
                        all_edges.clear()
                        for node in all_nodes:
                            for c in node.children:
                                all_edges.append((node, c))
                        smoothed_path = path_smoothing(best_path)
                        return PlannerResult(True, smoothed_path, all_nodes, all_edges)

            grow_tree_start = not grow_tree_start

        # On timeout or max iterations reached
        if best_path:
            smoothed_path = path_smoothing(best_path)
            success = True
        else:
            smoothed_path = []
            success = False

        all_nodes = tree_start + tree_goal
        all_edges.clear()
        for node in all_nodes:
            for c in node.children:
                all_edges.append((node, c))

        return PlannerResult(success, smoothed_path, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position          # Tuple[float, ...]
        self.parent = None                # Node or None
        self.cost = cost                  # Cost from root to this node
        self.children = []                # List[Node]
        self.valid = True                 # Validity flag for collision checking

        if parent is not None:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node') -> None:
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node') -> None:
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node' or None, new_cost: float) -> None:
        # Prevent redundant updates
        if self.parent is new_parent and abs(self.cost - new_cost) < 1e-12:
            return
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        if new_parent is not None and self not in new_parent.children:
            new_parent.children.append(self)
        dist_func = math.dist
        # Recursive update for children costs
        for child in self.children:
            child.update_parent(self, self.cost + dist_func(self.position, child.position))

    def path_from_root(self) -> list:
        node = self
        path = []
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self,
                 max_iter: int = 8000,
                 step_size: float = 5.0,
                 goal_sample_rate: float = 0.15,
                 neighbor_radius_min: float = 8.0,
                 neighbor_radius_max: float = 25.0,
                 max_planning_time: float = 30.0,
                 smoothing_trials: int = 150):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time
        self.smoothing_trials = smoothing_trials

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist_func(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes: int) -> float:
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 30.0
            r = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist_func(node.position, pos) ** 2) <= r_sq]

        def uniform_sample():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                c, s = math.cos(angle), math.sin(angle)
                return [[c, -s], [s, c]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1, 0, 0], [0, 1, 0], [0, 0, 1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0.0, 1 - cos_theta * cos_theta))
                K = [[0, -kz, ky], [kz, 0, -kx], [-ky, kx, 0]]
                I = [[1, 0, 0], [0, 1, 0], [0, 0, 1]]

                def mat_add(A, B):
                    return [[A[i][j] + B[i][j] for j in range(3)] for i in range(3)]

                def mat_scale(A, s):
                    return [[A[i][j] * s for j in range(3)] for i in range(3)]

                def mat_mul(A, B):
                    return [[sum(A[i][k] * B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in pt)

        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path: list = []
        informed_sampling = False

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return uniform_sample()
            center = tuple(0.5 * (start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1] + [a_minor] * (dim - 1)

            n = dim
            while True:
                dir_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = sum(R[0][i] * sample_scaled[i] for i in range(n))
                    y_rot = sum(R[1][i] * sample_scaled[i] for i in range(n))
                    z_rot = sum(R[2][i] * sample_scaled[i] for i in range(n))
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: list, sample: tuple) -> Node or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through = nbr.cost + dist_func(nbr.position, new_pos)
                if cost_through < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: list, target_pos: tuple) -> Node or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through = nbr.cost + dist_func(nbr.position, new_pos)
                    if cost_through < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                if dist_func(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_smoothing(path: list, trials: int = None) -> list:
            if trials is None:
                trials = self.smoothing_trials
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        best_path = []
        best_cost = math.inf
        informed_sampling = False
        grow_start_tree = True

        for iteration in range(self.max_iter):
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                # Time-out: return best solution so far
                success_state = (len(best_path) > 0)
                smoothed_path = path_smoothing(best_path) if success_state else []
                all_nodes = tree_start + tree_goal
                all_edges = []
                for node in all_nodes:
                    for c in node.children:
                        all_edges.append((node, c))
                return PlannerResult(success_state, smoothed_path, all_nodes, all_edges)

            # Sampling
            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start_tree else start_pos
                else:
                    sample_pt = uniform_sample()

            if grow_start_tree:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if new_node is None:
                    grow_start_tree = not grow_start_tree
                    continue
                connected_node = incremental_connect(tree_goal, new_node.position)
                if connected_node:
                    # Combine paths from start and goal trees
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    # Avoid duplicated connecting node in joint path
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        smoothed_path = path_smoothing(best_path)
                        all_nodes = tree_start + tree_goal
                        all_edges = []
                        for node in all_nodes:
                            for c in node.children:
                                all_edges.append((node, c))
                        return PlannerResult(True, smoothed_path, all_nodes, all_edges)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if new_node is None:
                    grow_start_tree = not grow_start_tree
                    continue
                connected_node = incremental_connect(tree_start, new_node.position)
                if connected_node:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        smoothed_path = path_smoothing(best_path)
                        all_nodes = tree_start + tree_goal
                        all_edges = []
                        for node in all_nodes:
                            for c in node.children:
                                all_edges.append((node, c))
                        return PlannerResult(True, smoothed_path, all_nodes, all_edges)

            grow_start_tree = not grow_start_tree

        # Iterations exhausted: return best found solution
        final_success = (len(best_path) > 0)
        final_path = path_smoothing(best_path) if final_success else []
        all_nodes = tree_start + tree_goal
        all_edges = []
        for node in all_nodes:
            for c in node.children:
                all_edges.append((node, c))

        return PlannerResult(final_success, final_path, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * factor for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent: 'Node' = None, cost: float = 0.0):
        self.position = position            # Tuple[float, ...] e.g. (x,y) or (x,y,z)
        self.parent = None                  # type: Node or None
        self.cost = cost                    # Path cost from root to this node
        self.children: List['Node'] = []   # Child nodes list
        self.valid = True                   # Validity flag for collision

        if parent:
            self.update_parent(parent, parent.cost + self._dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        # Recursively update cost of all descendants
        for child in self.children:
            child.update_parent(self, self.cost + self._dist(self.position, child.position))

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]

    @staticmethod
    def _dist(p1: Tuple[float, ...], p2: Tuple[float, ...]) -> float:
        return math.dist(p1, p2)
class Planner:
    def __init__(self, max_iter: int = 7000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.15,
                 neighbor_radius_min: float = 10.0,
                 neighbor_radius_max: float = 30.0,
                 max_planning_time: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            dist = dist_func(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            resolution = self.step_size / 5.0
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution)

        def adaptive_neighbor_radius(n_nodes, iter_num):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 30.0
            base_radius = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            iteration_factor = min(1.0, iter_num / self.max_iter)
            radius = base_radius * (1.0 - 0.6 * iteration_factor)
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, radius))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist_func(node.position, pos) ** 2) <= r_sq]

        def sample_uniform():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1,1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x/norm for x in pt)

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a],
                        [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx*kx + ky*ky + kz*kz)
                if norm_k < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0, 1 - cos_theta*cos_theta))
                K = [[0, -kz, ky],[kz, 0, -kx],[-ky, kx, 0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A, B):
                    return [[A[i][j] + B[i][j] for j in range(3)] for i in range(3)]

                def mat_scale(A, s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]

                def mat_mul(A, B):
                    rows = len(A)
                    cols = len(B[0])
                    return [[sum(A[i][k]*B[k][j] for k in range(len(B))) for j in range(cols)] for i in range(rows)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))

        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path: List[Tuple[float, ...]] = []
        informed_sampling = False

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_uniform()
            center = tuple(0.5 * (start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1] + R[0][2]*sample_scaled[2]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1] + R[1][2]*sample_scaled[2]
                    z_rot = R[2][0]*sample_scaled[0] + R[2][1]*sample_scaled[1] + R[2][2]*sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                clamped = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(clamped):
                    return clamped

        def extend_and_rewire(tree: List[Node], sample: Tuple[float, ...], iter_num: int) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1, iter_num)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: List[Node], target_pos: Tuple[float, ...], iter_num: int) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1, iter_num)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                    if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through_nbr
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                dist_to_target = dist_func(new_node.position, target_pos)
                if dist_to_target <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_smoothing(path: List[Tuple[float, ...]], trials: int = 150) -> List[Tuple[float, ...]]:
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        best_path = []
        best_cost = math.inf
        informed_sampling = False
        grow_start = True

        iteration = 0
        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                break

            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start else start_pos
                else:
                    sample_pt = sample_uniform()

            if grow_start:
                new_node = extend_and_rewire(tree_start, sample_pt, iteration)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connect_node = incremental_connect(tree_goal, new_node.position, iteration)
                if connect_node:
                    path_start = new_node.path_from_root()
                    path_goal = connect_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connect_node.cost + dist_func(new_node.position, connect_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    smoothed_path = path_smoothing(best_path)
                    nodes_all = tree_start + tree_goal
                    edges_all = []
                    for node in nodes_all:
                        for c in node.children:
                            edges_all.append((node, c))
                    return PlannerResult(True, smoothed_path, nodes_all, edges_all)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt, iteration)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connect_node = incremental_connect(tree_start, new_node.position, iteration)
                if connect_node:
                    path_goal = new_node.path_from_root()
                    path_start = connect_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connect_node.cost + dist_func(new_node.position, connect_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    smoothed_path = path_smoothing(best_path)
                    nodes_all = tree_start + tree_goal
                    edges_all = []
                    for node in nodes_all:
                        for c in node.children:
                            edges_all.append((node, c))
                    return PlannerResult(True, smoothed_path, nodes_all, edges_all)

            grow_start = not grow_start

        success = (len(best_path) > 0)
        smoothed = path_smoothing(best_path) if success else []

        nodes_all = tree_start + tree_goal
        edges_all = []
        for node in nodes_all:
            for c in node.children:
                edges_all.append((node, c))

        return PlannerResult(success, smoothed, nodes_all, edges_all)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position            # Tuple[float, ...] (2D or 3D)
        self.parent = None                  # Node or None
        self.cost = 0.0                    # Cost from root to this node
        self.children = []                 # List[Node]
        self.valid = True                  # Valid for collision checks
        if parent is not None:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node') -> None:
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node') -> None:
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node' or None, new_cost: float) -> None:
        if self.parent is new_parent and abs(self.cost - new_cost) < 1e-12:
            # No change needed
            return
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        if new_parent and self not in new_parent.children:
            new_parent.children.append(self)

        dist_func = math.dist
        # Recursively propagate cost updates to children
        for child in self.children:
            child.update_parent(self, self.cost + dist_func(self.position, child.position))

    def path_from_root(self) -> list:
        node = self
        path = []
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 15000, step_size: float=5.0,
                 goal_sample_rate: float=0.15,
                 neighbor_radius_min: float=8.0,
                 neighbor_radius_max: float=25.0,
                 max_planning_time: float=30.0,
                 smoothing_trials: int=150):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time
        self.smoothing_trials = smoothing_trials

    def plan(self, map) -> 'PlannerResult':
        import time
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def clamp_to_bounds(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def steer(src, tgt):
            d = dist_func(src, tgt)
            if d <= self.step_size:
                return tgt
            factor = self.step_size / d
            return tuple(src[d] + factor * (tgt[d] - src[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            resolution = self.step_size / 5.0 if self.step_size > 0 else 0.5
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution)

        def adaptive_neighbor_radius(num_nodes: int) -> float:
            if num_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 33.0
            radius = gamma_rrt * ((math.log(num_nodes) / num_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, radius))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist_func(node.position, pos) ** 2) <= r_sq]

        def uniform_sample():
            tries = 0
            while tries < 50:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt
                tries += 1
            # Fallback to goal if failed
            return goal_pos

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                c, s = math.cos(angle), math.sin(angle)
                return [[c, -s],[s, c]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx*kx + ky*ky + kz*kz)
                if norm_k < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = max(0.0, math.sqrt(1 - cos_theta*cos_theta))
                K = [[0, -kz, ky],[kz, 0, -kx],[-ky, kx, 0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A, B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]

                def mat_scale(A, s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]

                def mat_mul(A, B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x/norm for x in pt)

        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        use_informed_sampling = False
        rotation_mat = None

        def informed_sample(c_best):
            nonlocal rotation_mat
            if c_best == math.inf or c_best < c_min - 1e-12:
                return uniform_sample()

            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            if rotation_mat is None:
                rotation_mat = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1] + [a_minor]*(dim-1)

            n = dim
            while True:
                direction_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = rotation_mat[0][0] * sample_scaled[0] + rotation_mat[0][1] * sample_scaled[1]
                    y_rot = rotation_mat[1][0] * sample_scaled[0] + rotation_mat[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = sum(rotation_mat[0][i] * sample_scaled[i] for i in range(n))
                    y_rot = sum(rotation_mat[1][i] * sample_scaled[i] for i in range(n))
                    z_rot = sum(rotation_mat[2][i] * sample_scaled[i] for i in range(n))
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = clamp_to_bounds(sample_pt)
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: list, sample_pt: tuple) -> Node or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, sample_pt))
            new_pos = steer(nearest.position, sample_pt)
            new_pos = clamp_to_bounds(new_pos)

            if not collision_free_node(new_pos):
                return None
            if not collision_free_edge(nearest.position, new_pos):
                return None

            radius = adaptive_neighbor_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                tentative_cost = nbr.cost + dist_func(nbr.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            new_node.update_parent(best_parent, min_cost)
            tree.append(new_node)

            # Rewire neighbors to possibly improve path cost
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_thru_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_thru_new + 1e-12 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_thru_new)

            return new_node

        def incremental_connect(tree: list, target_node: Node) -> Node or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, target_node.position))
            current_node = nearest

            while True:
                new_pos = steer(current_node.position, target_node.position)
                new_pos = clamp_to_bounds(new_pos)

                if not collision_free_node(new_pos) or not collision_free_edge(current_node.position, new_pos):
                    return None

                radius = adaptive_neighbor_radius(len(tree) + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current_node.cost + dist_func(current_node.position, new_pos)
                best_parent = current_node
                for nbr in neighbors:
                    if nbr is current_node:
                        continue
                    candidate_cost = nbr.cost + dist_func(nbr.position, new_pos)
                    if candidate_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = candidate_cost
                        best_parent = nbr

                new_node = Node(new_pos)
                new_node.update_parent(best_parent, min_cost)
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_thru_new = new_node.cost + dist_func(new_node.position, nbr.position)
                    if cost_thru_new + 1e-12 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_thru_new)

                if dist_func(new_node.position, target_node.position) <= self.step_size:
                    if collision_free_edge(new_node.position, target_node.position):
                        return new_node
                    else:
                        return None

                current_node = new_node

        def shortcut_smoothing(path: list, trials: int) -> list:
            if len(path) < 3:
                return path[:]
            p = path[:]
            for _ in range(trials):
                if len(p) < 3:
                    break
                i = random.randint(0, len(p) - 3)
                j = random.randint(i + 2, len(p) - 1)
                if collision_free_edge(p[i], p[j]):
                    p = p[:i+1] + p[j:]
            return p

        tree_start = [Node(start_pos)]
        tree_goal = [Node(goal_pos)]

        best_path_nodes = []
        best_path_cost = math.inf
        use_informed_sampling = False
        grow_start_tree = True
        all_nodes = tree_start + tree_goal

        edges = []

        for iter_idx in range(self.max_iter):
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                # Time limit reached, return best so far
                success = len(best_path_nodes) > 0
                final_path = shortcut_smoothing(best_path_nodes, self.smoothing_trials) if success else []
                # update edges from current nodes
                all_nodes = tree_start + tree_goal
                edges.clear()
                for node in all_nodes:
                    for c in node.children:
                        edges.append((node, c))
                return PlannerResult(success, final_path, all_nodes, edges)

            # Sample point
            if use_informed_sampling:
                sample_pt = informed_sample(best_path_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start_tree else start_pos
                else:
                    sample_pt = uniform_sample()

            active_tree = tree_start if grow_start_tree else tree_goal
            other_tree = tree_goal if grow_start_tree else tree_start

            new_node = extend_and_rewire(active_tree, sample_pt)
            if new_node is None:
                grow_start_tree = not grow_start_tree
                continue
            all_nodes.append(new_node)

            connect_node = incremental_connect(other_tree, new_node)
            if connect_node is not None:
                # Extract path from roots to connection
                if grow_start_tree:
                    path_start = new_node.path_from_root()
                    path_goal = connect_node.path_from_root()
                else:
                    path_goal = new_node.path_from_root()
                    path_start = connect_node.path_from_root()

                if path_goal and path_goal[0] == new_node.position:
                    path_goal = path_goal[1:]

                combined_path = path_start + path_goal[::-1]

                # Calculate total cost accurately via node costs and edge
                total_cost = 0.0
                for i in range(len(combined_path)-1):
                    total_cost += dist_func(combined_path[i], combined_path[i+1])

                if total_cost + 1e-12 < best_path_cost:
                    best_path_cost = total_cost
                    best_path_nodes = combined_path
                    use_informed_sampling = True
                    rotation_mat = None  # reset rotation matrix for sampling

                    # Recompute edges for current trees
                    edges.clear()
                    all_nodes = tree_start + tree_goal
                    for n in all_nodes:
                        for c in n.children:
                            edges.append((n, c))

                    smoothed_path = shortcut_smoothing(best_path_nodes, self.smoothing_trials)
                    return PlannerResult(True, smoothed_path, all_nodes, edges)

            grow_start_tree = not grow_start_tree

        # max_iter reached without timeout or early success
        success = len(best_path_nodes) > 0
        final_path = shortcut_smoothing(best_path_nodes, self.smoothing_trials) if success else []
        all_nodes = tree_start + tree_goal
        edges.clear()
        for node in all_nodes:
            for c in node.children:
                edges.append((node, c))
        return PlannerResult(success, final_path, all_nodes, edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position            # Tuple[float, ...]
        self.parent = None                  # Node or None
        self.cost = 0.0                    # Cost from root to this node
        self.children = []                 # List of children nodes
        self.valid = True                  # Validity flag for collision checking
        if parent is not None:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node') -> None:
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node') -> None:
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node' or None, new_cost: float) -> None:
        if self.parent is new_parent and abs(self.cost - new_cost) < 1e-12:
            return
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        if new_parent and self not in new_parent.children:
            new_parent.children.append(self)

        dist_func = math.dist
        for child in self.children:
            child.update_parent(self, self.cost + dist_func(self.position, child.position))

    def path_from_root(self) -> list:
        node = self
        path = []
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
