            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_thru = nbr.cost + dist_func(nbr.position, new_pos)
                if cost_thru < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_thru
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_thru_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_thru_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_thru_new)

