        def incremental_connect(tree: list, target_pos: tuple) -> Node or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, target_pos))
            current = nearest

            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_thru = nbr.cost + dist_func(nbr.position, new_pos)
                    if cost_thru < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_thru
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_thru_new = new_node.cost + dist_func(new_node.position, nbr.position)
                    if cost_thru_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_thru_new)

                if dist_func(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    return None

                current = new_node

        def path_smoothing(path: list, trials: int) -> list:
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i+1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        best_path = []
        best_cost = math.inf
        informed_sampling = False
        grow_start_tree = True

        for iteration in range(self.max_iter):
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                success = (len(best_path) > 0)
                final_path = path_smoothing(best_path, self.smoothing_trials) if success else []
                all_nodes = tree_start + tree_goal
                all_edges = [(node, c) for node in all_nodes for c in node.children]
                return PlannerResult(success, final_path, all_nodes, all_edges)

            if informed_sampling:
                sample_point = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_point = goal_pos if grow_start_tree else start_pos
                else:
                    sample_point = uniform_sample()

            if grow_start_tree:
                new_node = extend_and_rewire(tree_start, sample_point)
                if new_node is None:
                    grow_start_tree = not grow_start_tree
                    continue

                connect_node = incremental_connect(tree_goal, new_node.position)
                if connect_node is not None:
                    path_start = new_node.path_from_root()
                    path_goal = connect_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connect_node.cost + dist_func(new_node.position, connect_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        smoothed_path = path_smoothing(best_path, self.smoothing_trials)
                        all_nodes = tree_start + tree_goal
                        all_edges = [(node, c) for node in all_nodes for c in node.children]
                        return PlannerResult(True, smoothed_path, all_nodes, all_edges)
            else:
                new_node = extend_and_rewire(tree_goal, sample_point)
                if new_node is None:
                    grow_start_tree = not grow_start_tree
                    continue

                connect_node = incremental_connect(tree_start, new_node.position)
                if connect_node is not None:
                    path_goal = new_node.path_from_root()
                    path_start = connect_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connect_node.cost + dist_func(new_node.position, connect_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        smoothed_path = path_smoothing(best_path, self.smoothing_trials)
                        all_nodes = tree_start + tree_goal
                        all_edges = [(node, c) for node in all_nodes for c in node.children]
                        return PlannerResult(True, smoothed_path, all_nodes, all_edges)

            grow_start_tree = not grow_start_tree

        # End after max_iter without timeout or success
        final_success = (len(best_path) > 0)
        final_path = path_smoothing(best_path, self.smoothing_trials) if final_success else []
        all_nodes = tree_start + tree_goal
        all_edges = [(node, c) for node in all_nodes for c in node.children]

        return PlannerResult(final_success, final_path, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root to this node
        self.children = []
        self.valid = True               # For collision checking etc.

        if parent:
            parent.add_child(self)

    def add_child(self, child):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        self._update_cost_recursive()

    def _update_cost_recursive(self):
        for child in self.children:
            child.cost = self.cost + math.dist(self.position, child.position)
            child._update_cost_recursive()

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 100000, step_size: float=5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.max_planning_time = 30.0  # seconds

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def clamp(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(f_pos, t_pos, resolution=None):
            if resolution is None:
                resolution = self.step_size / 5.0
            return not self._is_edge_in_obstacle(f_pos, t_pos, obstacles, is_3d, resolution=resolution)

        # Uniform random sample within bounds avoiding obstacles
        def sample_free():
            while True:
                sample = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(sample):
                    return sample

        # Unit n-ball sampling for informed sampling
        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in pt)

        c_min = dist(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling_enabled = False

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_free()
            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))

            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a = math.cos(angle)
                sin_a = math.sin(angle)
                R = ((cos_a, -sin_a), (sin_a, cos_a))
            else:
                # Householder approach to build orthonormal basis (direction aligned to x-axis)
                ux, uy, uz = direction
                if abs(ux) < 1e-10 and abs(uy) < 1e-10:
                    vx_, vy_, vz_ = 1, 0, 0
                else:
                    vx_, vy_, vz_ = -uy, ux, 0
                norm_v_ = math.sqrt(vx_ * vx_ + vy_ * vy_ + vz_ * vz_)
                vx_ /= norm_v_
                vy_ /= norm_v_
                vz_ /= norm_v_
                wx = uy * vz_ - uz * vy_
                wy = uz * vx_ - ux * vz_
                wz = ux * vy_ - uy * vx_
                R = [
                    [ux, vx_, wx],
                    [uy, vy_, wy],
                    [uz, vz_, wz]
                ]

            a1 = c_best / 2.0
            if dim == 2:
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1] + R[0][2]*sample_scaled[2]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1] + R[1][2]*sample_scaled[2]
                    z_rot = R[2][0]*sample_scaled[0] + R[2][1]*sample_scaled[1] + R[2][2]*sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = clamp(sample_pt)
                if collision_free_node(sample_pt):
                    return sample_pt

        # Adaptive radius for neighbor search
        def adaptive_radius(num_nodes: int) -> float:
            if num_nodes <= 1:
                return 50.0
            gamma_rrt = 30.0
            r = gamma_rrt * ((math.log(num_nodes) / num_nodes) ** (1.0 / dim))
            return max(5.0, min(30.0, r))

        def near_nodes(tree, pos, radius):
            return [node for node in tree if dist(node.position, pos) <= radius]

        def rewire(tree, new_node, neighbors):
            for nbr in neighbors:
                if nbr == new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

        def extend(tree, target):
            nearest = min(tree, key=lambda n: dist(n.position, target))
            new_pos = steer(nearest.position, target)
            new_pos = clamp(new_pos)

            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            neighbors_radius = adaptive_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, neighbors_radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr == nearest:
                    continue
                tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                if tentative_cost < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            rewire(tree, new_node, neighbors)

            return new_node

        def incremental_connect_and_rewire(tree, target_pos):
            curr = min(tree, key=lambda n: dist(n.position, target_pos))

            while True:
                new_pos = steer(curr.position, target_pos)
                new_pos = clamp(new_pos)

                if not collision_free_node(new_pos) or not collision_free_edge(curr.position, new_pos):
                    return None

                neighbors_radius = adaptive_radius(len(tree) + 1)
                neighbors = near_nodes(tree, new_pos, neighbors_radius)

                min_cost = curr.cost + dist(curr.position, new_pos)
                best_parent = curr
                for nbr in neighbors:
                    if nbr == curr:
                        continue
                    tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                    if tentative_cost < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = tentative_cost
                        best_parent = nbr

                connect_node = Node(new_pos)
                best_parent.add_child(connect_node)
                connect_node.cost = min_cost
                tree.append(connect_node)

                rewire(tree, connect_node, neighbors)

                if dist(connect_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(connect_node.position, target_pos):
                        return connect_node
                    else:
                        return None

                curr = connect_node

        def path_smooth(path: list, max_trials=150):
            if len(path) < 3:
                return path[:]
            for _ in range(max_trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start = [Node(start_pos, cost=0.0)]
        tree_goal = [Node(goal_pos, cost=0.0)]
        all_nodes = tree_start + tree_goal
        edges = []

        best_path = []
        best_cost = math.inf
        success_state = False

        grow_start = True
        iter_count = 0

        while iter_count < self.max_iter:
            current_time = time.perf_counter()
            if current_time - start_time > self.max_planning_time:
                if best_path:
                    smooth_path = path_smooth(best_path)
                    return PlannerResult(True, smooth_path, all_nodes, edges)
                else:
                    return PlannerResult(False, [], all_nodes, edges)

            iter_count += 1

            if informed_sampling_enabled:
                sample = informed_sample(best_cost)
            else:
                if random.random() < 0.15:
                    sample = goal_pos if grow_start else start_pos
                else:
                    sample = sample_free()

            if grow_start:
                new_node = extend(tree_start, sample)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connected_node = incremental_connect_and_rewire(tree_goal, new_node.position)
                if connected_node is not None:
                    path_s = new_node.path_from_root()
                    path_g = connected_node.path_from_root()
                    if path_g and path_g[0] == new_node.position:
                        path_g = path_g[1:]
                    combined = path_s + path_g[::-1]

                    total_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                    if total_cost < best_cost:
                        best_cost = total_cost
                        best_path = combined
                        informed_sampling_enabled = True

                    edges.clear()
                    all_nodes = tree_start + tree_goal
                    for node in all_nodes:
                        for c in node.children:
                            edges.append((node, c))

                    smooth_path = path_smooth(best_path)
                    success_state = True
                    return PlannerResult(success_state, smooth_path, all_nodes, edges)

            else:
                new_node = extend(tree_goal, sample)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connected_node = incremental_connect_and_rewire(tree_start, new_node.position)
                if connected_node is not None:
                    path_g = new_node.path_from_root()
                    path_s = connected_node.path_from_root()
                    if path_s and path_s[0] == new_node.position:
                        path_s = path_s[1:]
                    combined = path_s + path_g[::-1]

                    total_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                    if total_cost < best_cost:
                        best_cost = total_cost
                        best_path = combined
                        informed_sampling_enabled = True

                    edges.clear()
                    all_nodes = tree_start + tree_goal
                    for node in all_nodes:
                        for c in node.children:
                            edges.append((node, c))

                    smooth_path = path_smooth(best_path)
                    success_state = True
                    return PlannerResult(success_state, smooth_path, all_nodes, edges)

            grow_start = not grow_start

        if best_path:
            smooth_path = path_smooth(best_path)
            return PlannerResult(True, smooth_path, all_nodes, edges)

        return PlannerResult(False, [], all_nodes, edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...] e.g. 2D or 3D
        self.parent = None              # Parent Node or None
        self.cost = cost                # Cost from root to this node
        self.children = []              # Child nodes list
        self.valid = True               # Node validity (collision-free)

        if parent is not None:
            self.update_parent(parent, parent.cost + self._dist(parent.position, position))

    def add_child(self, child):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        for child in self.children:
            child.update_parent(self, self.cost + self._dist(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]

    @staticmethod
    def _dist(p1, p2):
        import math
        return math.dist(p1, p2)
class Planner:
    def __init__(self, max_iter: int = 7000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.15,
                 neighbor_radius_min: float = 10.0,
                 neighbor_radius_max: float = 30.0,
                 max_planning_time: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> 'PlannerResult':
        import time
        import math
        import random

        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            resolution = self.step_size / 5.0
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution)

        def adaptive_radius(n_nodes, iter_num=0):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma = 30.0
            base = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            iteration_factor = min(1.0, iter_num / self.max_iter)
            # Reduce radius as iterations progress to balance rewiring cost and connectivity
            radius = base * (1.0 - 0.6 * iteration_factor)
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, radius))

        def near_nodes(nodes_list, pos, radius):
            r_sq = radius * radius
            return [n for n in nodes_list if (math.dist(n.position, pos) ** 2) <= r_sq]

        def sample_uniform():
            while True:
                sample = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(sample):
                    return sample

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in pt)

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a],
                        [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx*kx + ky*ky + kz*kz)
                if norm_k < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= norm_k; ky /= norm_k; kz /= norm_k

                cos_theta = vx
                sin_theta = math.sqrt(max(0,1 - cos_theta*cos_theta))
                K = [[0, -kz, ky],[kz, 0, -kx],[-ky, kx, 0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A, B):
                    return [[A[i][j] + B[i][j] for j in range(3)] for i in range(3)]

                def mat_scale(A, s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]

                def mat_mul(A, B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))

        c_min = math.dist(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False
        rotation_mat = None

        def informed_sample(c_best):
            nonlocal rotation_mat
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_uniform()

            center = tuple(0.5 * (start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))

            if rotation_mat is None:
                rotation_mat = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            while True:
                dir_sample = unit_ball_sample(dim)
                radius_sample = random.random() ** (1.0 / dim)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(dim))

                if dim == 2:
                    x_rot = rotation_mat[0][0]*sample_scaled[0] + rotation_mat[0][1]*sample_scaled[1]
                    y_rot = rotation_mat[1][0]*sample_scaled[0] + rotation_mat[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = rotation_mat[0][0]*sample_scaled[0] + rotation_mat[0][1]*sample_scaled[1] + rotation_mat[0][2]*sample_scaled[2]
                    y_rot = rotation_mat[1][0]*sample_scaled[0] + rotation_mat[1][1]*sample_scaled[1] + rotation_mat[1][2]*sample_scaled[2]
                    z_rot = rotation_mat[2][0]*sample_scaled[0] + rotation_mat[2][1]*sample_scaled[1] + rotation_mat[2][2]*sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(dim))
                clamped = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(dim))
                if collision_free_node(clamped):
                    return clamped

        def extend_and_rewire(tree, sample, iter_num):
            if not tree:
                return None
            nearest = min(tree, key=lambda n: math.dist(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_radius(n_nodes+1, iter_num)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + math.dist(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through = nbr.cost + math.dist(nbr.position, new_pos)
                if cost_through < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + math.dist(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree, target_pos, iter_num):
            if not tree:
                return None
            nearest = min(tree, key=lambda n: math.dist(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_radius(n_nodes+1, iter_num)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + math.dist(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through = nbr.cost + math.dist(nbr.position, new_pos)
                    if cost_through < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + math.dist(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                if math.dist(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_smoothing(path, trials=150):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i+2, len(path) -1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i+1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        best_cost = math.inf
        best_path = []
        informed_sampling = False

        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                break

            sample_pt = (informed_sample(best_cost) if informed_sampling
                         else goal_pos if random.random() < self.goal_sample_rate and grow_start
                         else start_pos if random.random() < self.goal_sample_rate and not grow_start
                         else sample_uniform())

            if grow_start:
                new_node = extend_and_rewire(tree_start, sample_pt, iteration)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_goal, new_node.position, iteration)
                if connected_node:
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + math.dist(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    smoothed = path_smoothing(best_path)
                    return self._build_result(True, smoothed, tree_start + tree_goal)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt, iteration)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_start, new_node.position, iteration)
                if connected_node:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + math.dist(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    smoothed = path_smoothing(best_path)
                    return self._build_result(True, smoothed, tree_start + tree_goal)

            grow_start = not grow_start

        success = (len(best_path) > 0)
        smoothed = path_smoothing(best_path) if success else []
        return self._build_result(success, smoothed, tree_start + tree_goal)

    def _build_result(self, success, path, nodes):
        edges = []
        for node in nodes:
            for child in node.children:
                edges.append((node, child))
        return PlannerResult(
            success=success,
            path=path,
            nodes=nodes,
            edges=edges,
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position                  # Tuple[float, ...]
        self.parent = None                        # Parent Node or None
        self.cost = 0.0                          # Cost from root to this node
        self.children = []                      # List of children nodes
        self.valid = True                       # Validity flag for collision checking
        if parent:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node') -> None:
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node') -> None:
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node' or None, new_cost: float) -> None:
        if self.parent is new_parent and abs(self.cost - new_cost) < 1e-12:
            return
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        if new_parent and self not in new_parent.children:
            new_parent.children.append(self)
        # Recursively update children's costs
        dist_func = math.dist
        for child in self.children:
            child.update_parent(self, self.cost + dist_func(self.position, child.position))

    def path_from_root(self) -> list:
        node = self
        path = []
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.2,
                 neighbor_radius_min: float = 8.0,
                 neighbor_radius_max: float = 25.0,
                 max_planning_time: float = 30.0,
                 smoothing_trials: int = 120):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time
        self.smoothing_trials = smoothing_trials

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(src, tgt):
            d = dist_func(src, tgt)
            if d <= self.step_size:
                return tgt
            factor = self.step_size / d
            return tuple(src[d] + factor * (tgt[d] - src[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes: int) -> float:
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 35.0
            r = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist_func(node.position, pos) ** 2) <= r_sq]

        def uniform_sample():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                c, s = math.cos(angle), math.sin(angle)
                return [[c, -s], [s, c]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx*kx + ky*ky + kz*kz)
                if norm_k < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0.0, 1 - cos_theta*cos_theta))
                K = [[0, -kz, ky],[kz, 0, -kx],[-ky, kx, 0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A, B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A, s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A, B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1,1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x/norm for x in pt)

        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path: list = []
        informed_sampling = False

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return uniform_sample()
            center = tuple(0.5*(start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1] + [a_minor]*(dim - 1)

            n = dim
            while True:
                dir_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = sum(R[0][i]*sample_scaled[i] for i in range(n))
                    y_rot = sum(R[1][i]*sample_scaled[i] for i in range(n))
                    z_rot = sum(R[2][i]*sample_scaled[i] for i in range(n))
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: list, sample: tuple) -> 'Node' or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through = nbr.cost + dist_func(nbr.position, new_pos)
                if cost_through < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: list, target_pos: tuple) -> 'Node' or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through = nbr.cost + dist_func(nbr.position, new_pos)
                    if cost_through < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                if dist_func(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None
                current = new_node

        def path_smoothing(path: list, trials: int = None) -> list:
            if trials is None:
                trials = self.smoothing_trials
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        best_path = []
        best_cost = math.inf
        informed_sampling = False
        grow_start_tree = True

        for iteration in range(self.max_iter):
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                # Time limit reached: return best found so far
                success_state = (len(best_path) > 0)
                smoothed_path = path_smoothing(best_path) if success_state else []
                all_nodes = tree_start + tree_goal
                all_edges = []
                for node in all_nodes:
                    for c in node.children:
                        all_edges.append((node, c))
                return PlannerResult(success_state, smoothed_path, all_nodes, all_edges)

            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start_tree else start_pos
                else:
                    sample_pt = uniform_sample()

            if grow_start_tree:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if new_node is None:
                    grow_start_tree = not grow_start_tree
                    continue
                connected_node = incremental_connect(tree_goal, new_node.position)
                if connected_node:
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]
                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)

                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        smoothed_path = path_smoothing(best_path)
                        all_nodes = tree_start + tree_goal
                        all_edges = []
                        for node in all_nodes:
                            for c in node.children:
                                all_edges.append((node, c))
                        return PlannerResult(True, smoothed_path, all_nodes, all_edges)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if new_node is None:
                    grow_start_tree = not grow_start_tree
                    continue
                connected_node = incremental_connect(tree_start, new_node.position)
                if connected_node:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]
                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)

                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        smoothed_path = path_smoothing(best_path)
                        all_nodes = tree_start + tree_goal
                        all_edges = []
                        for node in all_nodes:
                            for c in node.children:
                                all_edges.append((node, c))
                        return PlannerResult(True, smoothed_path, all_nodes, all_edges)

            grow_start_tree = not grow_start_tree

        # End of iterations reached - return best found solution
        final_success = (len(best_path) > 0)
        final_path = path_smoothing(best_path) if final_success else []
        all_nodes = tree_start + tree_goal
        all_edges = []
        for node in all_nodes:
            for c in node.children:
                all_edges.append((node, c))

        return PlannerResult(final_success, final_path, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * factor for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost: float=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = parent            # Node or None
        self.cost = cost                # Path cost from root to this node
        self.children = []
        self.valid = True               # For collision checking etc.

    def add_child(self, child):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        # Recursively update children costs
        for c in self.children:
            c._update_cost_recursive()

    def _update_cost_recursive(self):
        for c in self.children:
            c.cost = self.cost + math.dist(self.position, c.position)
            c._update_cost_recursive()

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 100000, step_size: float=5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.max_planning_time = 30.0  # seconds

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def clamp(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(f_pos, t_pos, resolution=None):
            if resolution is None:
                resolution = self.step_size / 5.0
            return not self._is_edge_in_obstacle(f_pos, t_pos, obstacles, is_3d, resolution=resolution)

        # Uniform random sample within bounds avoiding obstacles
        def sample_free():
            while True:
                sample = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(sample):
                    return sample

        # Unit n-ball sampling for informed sampling
        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1,1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in pt)

        c_min = dist(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling_enabled = False

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_free()
            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))

            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a = math.cos(angle)
                sin_a = math.sin(angle)
                R = ((cos_a, -sin_a), (sin_a, cos_a))
            else:
                # Householder approach to build orthonormal basis (direction aligned to x-axis)
                ux, uy, uz = direction
                # Create arbitrary orthonormal vectors perpendicular to ux, uy, uz
                if abs(ux) < 1e-10 and abs(uy) < 1e-10:
                    vx_, vy_, vz_ = 1,0,0
                else:
                    vx_, vy_, vz_ = -uy, ux, 0
                norm_v_ = math.sqrt(vx_*vx_ + vy_*vy_ + vz_*vz_)
                vx_ /= norm_v_
                vy_ /= norm_v_
                vz_ /= norm_v_
                wx = uy * vz_ - uz * vy_
                wy = uz * vx_ - ux * vz_
                wz = ux * vy_ - uy * vx_

                R = [
                    [ux, vx_, wx],
                    [uy, vy_, wy],
                    [uz, vz_, wz]
                ]

            a1 = c_best / 2.0
            if dim == 2:
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1] + R[0][2]*sample_scaled[2]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1] + R[1][2]*sample_scaled[2]
                    z_rot = R[2][0]*sample_scaled[0] + R[2][1]*sample_scaled[1] + R[2][2]*sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = clamp(sample_pt)
                if collision_free_node(sample_pt):
                    return sample_pt

        def adaptive_radius(num_nodes: int) -> float:
            if num_nodes <= 1:
                return 50.0
            gamma_rrt = 30.0
            r = gamma_rrt * ((math.log(num_nodes) / num_nodes) ** (1.0 / dim))
            return max(5.0, min(30.0, r))

        def near_nodes(tree, pos, radius):
            return [node for node in tree if dist(node.position, pos) <= radius]

        def rewire(tree, new_node, neighbors):
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

        def extend(tree, target):
            nearest = min(tree, key=lambda n: dist(n.position, target))
            new_pos = steer(nearest.position, target)
            new_pos = clamp(new_pos)

            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            neighbors_radius = adaptive_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, neighbors_radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr == nearest:
                    continue
                tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                if tentative_cost < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            rewire(tree, new_node, neighbors)

            return new_node

        def incremental_connect_and_rewire(tree, target_pos):
            curr = min(tree, key=lambda n: dist(n.position, target_pos))

            while True:
                new_pos = steer(curr.position, target_pos)
                new_pos = clamp(new_pos)

                if not collision_free_node(new_pos) or not collision_free_edge(curr.position, new_pos):
                    return None

                neighbors_radius = adaptive_radius(len(tree) + 1)
                neighbors = near_nodes(tree, new_pos, neighbors_radius)

                min_cost = curr.cost + dist(curr.position, new_pos)
                best_parent = curr
                for nbr in neighbors:
                    if nbr == curr:
                        continue
                    tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                    if tentative_cost < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = tentative_cost
                        best_parent = nbr

                connect_node = Node(new_pos)
                best_parent.add_child(connect_node)
                connect_node.cost = min_cost
                tree.append(connect_node)

                rewire(tree, connect_node, neighbors)

                if dist(connect_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(connect_node.position, target_pos):
                        return connect_node
                    else:
                        return None

                curr = connect_node

        def path_smooth(path: List[Tuple[float, ...]], max_trials=150):
            if len(path) < 3:
                return path[:]
            for _ in range(max_trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i+1] + path[j:]
            return path

        tree_start = [Node(start_pos, cost=0.0)]
        tree_goal = [Node(goal_pos, cost=0.0)]
        all_nodes = tree_start + tree_goal
        edges = []

        best_path = []
        best_cost = math.inf
        success_state = False

        grow_start = True
        iter_count = 0

        while iter_count < self.max_iter:
            if time.perf_counter() - start_time > self.max_planning_time:
                # Time limit exceeded: return best path if any
                if best_path:
                    smooth_path = path_smooth(best_path)
                    return PlannerResult(True, smooth_path, all_nodes, edges)
                else:
                    return PlannerResult(False, [], all_nodes, edges)

            iter_count += 1

            if informed_sampling_enabled:
                sample = informed_sample(best_cost)
            else:
                if random.random() < 0.15:
                    sample = goal_pos if grow_start else start_pos
                else:
                    sample = sample_free()

            if grow_start:
                new_node = extend(tree_start, sample)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connected_node = incremental_connect_and_rewire(tree_goal, new_node.position)
                if connected_node is not None:
                    path_s = new_node.path_from_root()
                    path_g = connected_node.path_from_root()
                    if path_g and path_g[0] == new_node.position:
                        path_g = path_g[1:]
                    combined = path_s + path_g[::-1]

                    total_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                    if total_cost < best_cost:
                        best_cost = total_cost
                        best_path = combined
                        informed_sampling_enabled = True

                    # Refresh edges list
                    edges.clear()
                    all_nodes = tree_start + tree_goal
                    for node in all_nodes:
                        for c in node.children:
                            edges.append((node, c))

                    smooth_path = path_smooth(best_path)
                    success_state = True
                    return PlannerResult(success_state, smooth_path, all_nodes, edges)

            else:
                new_node = extend(tree_goal, sample)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connected_node = incremental_connect_and_rewire(tree_start, new_node.position)
                if connected_node is not None:
                    path_g = new_node.path_from_root()
                    path_s = connected_node.path_from_root()
                    if path_s and path_s[0] == new_node.position:
                        path_s = path_s[1:]
                    combined = path_s + path_g[::-1]

                    total_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                    if total_cost < best_cost:
                        best_cost = total_cost
                        best_path = combined
                        informed_sampling_enabled = True

                    edges.clear()
                    all_nodes = tree_start + tree_goal
                    for node in all_nodes:
                        for c in node.children:
                            edges.append((node, c))

                    smooth_path = path_smooth(best_path)
                    success_state = True
                    return PlannerResult(success_state, smooth_path, all_nodes, edges)

            grow_start = not grow_start

        # Max iterations reached - return best path if any
        if best_path:
            smooth_path = path_smooth(best_path)
            return PlannerResult(True, smooth_path, all_nodes, edges)

        return PlannerResult(False, [], all_nodes, edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position            # Tuple[float, ...], 2D or 3D
        self.parent = None                  # Node or None
        self.cost = 0.0                    # Cost from root to this node
        self.children = []                  # List[Node]
        self.valid = True                   # For collision checking or pruning
        if parent:
            self.update_parent(parent, parent.cost + self._distance(parent.position, position))

    def _distance(self, a, b):
        return math.dist(a, b)

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        for ch in self.children:
            ch.update_parent(self, self.cost + self._distance(self.position, ch.position))

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int=5000, step_size: float=5.0,
                 goal_sample_rate: float=0.2,
                 neighbor_radius_min: float=10.0,
                 neighbor_radius_max: float=30.0,
                 max_planning_time: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes: int) -> float:
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 30.0
            r = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree, pos, radius):
            radius_sq = radius * radius
            return [node for node in tree if (dist(node.position, pos) ** 2) <= radius_sq]

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        c_min = dist(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        # Rotation matrix for informed sampling
        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx*kx + ky*ky + kz*kz)
                if norm_k < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= norm_k; ky /= norm_k; kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0.0,1-cos_theta*cos_theta))
                K = [[0, -kz, ky],[kz,0,-kx],[-ky,kx,0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A,B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A,s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A,B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K,K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1-cos_theta))

        self._rotation_matrix = rotation_matrix

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x/norm for x in pt)

        self._unit_ball_sample = unit_ball_sample

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_free()
            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = self._rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0.0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0.0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n_ = dim
            while True:
                direction_sample = self._unit_ball_sample(n_)
                radius_sample = random.random() ** (1.0 / n_)
                sample_unit = tuple(radius_sample * ds for ds in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n_))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1] + R[0][2]*sample_scaled[2]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1] + R[1][2]*sample_scaled[2]
                    z_rot = R[2][0]*sample_scaled[0] + R[2][1]*sample_scaled[1] + R[2][2]*sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n_))
                sample_pt = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n_))
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree, sample):
            if not tree:
                return None
            nearest_node = min(tree, key=lambda n: dist(n.position, sample))
            new_pos = steer(nearest_node.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest_node.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for nbr in neighbors:
                if nbr == nearest_node:
                    continue
                cost_thru_nbr = nbr.cost + dist(nbr.position, new_pos)
                if cost_thru_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_thru_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            # Rewire neighbors
            for nbr in neighbors:
                if nbr == new_node.parent:
                    continue
                cost_thru_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_thru_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_thru_new)

            return new_node

        def incremental_connect(tree, target_pos):
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr == current:
                        continue
                    cost_thru_nbr = nbr.cost + dist(nbr.position, new_pos)
                    if cost_thru_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_thru_nbr
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                # Rewire neighbors
                for nbr in neighbors:
                    if nbr == new_node.parent:
                        continue
                    cost_thru_new = new_node.cost + dist(new_node.position, nbr.position)
                    if cost_thru_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_thru_new)

                if dist(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None
                current = new_node

        def path_smoothing(path, trials: int=100):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        # Initialize trees
        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        all_nodes = tree_start + tree_goal
        all_edges = []

        grow_tree_start = True
        iter_cnt = 0

        while iter_cnt < self.max_iter:
            if time.perf_counter() - start_time > self.max_planning_time:
                break
            iter_cnt += 1

            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_tree_start else start_pos
                else:
                    sample_pt = sample_free()

            if grow_tree_start:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if not new_node:
                    grow_tree_start = not grow_tree_start
                    continue
                connected_node = incremental_connect(tree_goal, new_node.position)
                if connected_node:
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    # Update edges
                    all_edges.clear()
                    all_nodes = tree_start + tree_goal
                    for node in all_nodes:
                        for c in node.children:
                            all_edges.append((node, c))

                    smoothed = path_smoothing(best_path)
                    return PlannerResult(True, smoothed, all_nodes, all_edges)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if not new_node:
                    grow_tree_start = not grow_tree_start
                    continue
                connected_node = incremental_connect(tree_start, new_node.position)
                if connected_node:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    all_edges.clear()
                    all_nodes = tree_start + tree_goal
                    for node in all_nodes:
                        for c in node.children:
                            all_edges.append((node, c))

                    smoothed = path_smoothing(best_path)
                    return PlannerResult(True, smoothed, all_nodes, all_edges)

            grow_tree_start = not grow_tree_start

        success_state = (len(best_path) > 0)
        if success_state:
            smoothed = path_smoothing(best_path)
        else:
            smoothed = []

        all_nodes = tree_start + tree_goal
        all_edges.clear()
        for node in all_nodes:
            for c in node.children:
                all_edges.append((node, c))

        return PlannerResult(success_state, smoothed, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent: Optional['Node'] = None, cost: float = 0.0):
        self.position = position
        self.parent = None
        self.cost = 0.0
        self.children: List['Node'] = []
        self.valid = True
        if parent:
            self.update_parent(parent, parent.cost + self._distance(parent.position, position))

    def _distance(self, a: Tuple[float, ...], b: Tuple[float, ...]) -> float:
        return math.dist(a, b)

    def add_child(self, child: 'Node') -> None:
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node') -> None:
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: Optional['Node'], new_cost: float) -> None:
        if self.parent is new_parent and abs(self.cost - new_cost) < 1e-10:
            return
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        if new_parent and self not in new_parent.children:
            new_parent.children.append(self)
        # Recursively update cost for children
        for child in self.children:
            child.update_parent(self, self.cost + self._distance(self.position, child.position))

    def path_from_root(self) -> List[Tuple[float, ...]]:
        node = self
        path = []
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float = 5.0, max_time_sec: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.max_time_sec = max_time_sec
        self.min_neighbor_radius = 8.0
        self.max_neighbor_radius = 20.0

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(pos: Tuple[float, ...]) -> bool:
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def dist(a: Tuple[float, ...], b: Tuple[float, ...]) -> float:
            return math.dist(a, b)

        def steer(src: Tuple[float, ...], tgt: Tuple[float, ...]) -> Tuple[float, ...]:
            d = dist(src, tgt)
            if d <= self.step_size:
                return tgt
            factor = self.step_size / d
            return tuple(src[d] + factor * (tgt[d] - src[d]) for d in range(dim))

        def collision_free_node(p: Tuple[float, ...]) -> bool:
            return in_bounds(p) and (not self._is_in_obstacle(p, obstacles, is_3d))

        def collision_free_edge(p1: Tuple[float, ...], p2: Tuple[float, ...]) -> bool:
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def neighbor_radius(n_nodes: int) -> float:
            if n_nodes <= 1:
                return self.max_neighbor_radius
            r = self.max_neighbor_radius * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.min_neighbor_radius, min(self.max_neighbor_radius, r))

        def near_nodes(tree: List[Node], pos: Tuple[float, ...], radius: float) -> List[Node]:
            radius_sq = radius * radius
            return [node for node in tree if dist(node.position, pos)**2 <= radius_sq]

        def uniform_sample() -> Tuple[float, ...]:
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        def rotation_matrix(direction: Tuple[float, ...]) -> List[List[float]]:
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                c, s = math.cos(angle), math.sin(angle)
                return [[c, -s], [s, c]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx**2 + ky**2 + kz**2)
                if norm_k < 1e-10:
                    return [[1, 0, 0], [0, 1, 0], [0, 0, 1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0.0, 1 - cos_theta * cos_theta))
                K = [[0, -kz, ky], [kz, 0, -kx], [-ky, kx, 0]]
                I = [[1, 0, 0], [0, 1, 0], [0, 0, 1]]

                def mat_add(A, B):
                    return [[A[i][j] + B[i][j] for j in range(3)] for i in range(3)]

                def mat_scale(A, s):
                    return [[A[i][j] * s for j in range(3)] for i in range(3)]

                def mat_mul(A, B):
                    return [[sum(A[i][k] * B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))

        def unit_ball_sample(n: int) -> Tuple[float, ...]:
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in pt)

        def informed_sample(c_best: float, c_min: float) -> Tuple[float, ...]:
            if c_best == math.inf or c_best < c_min - 1e-10:
                return uniform_sample()
            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)
            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1] + [a_minor] * (dim - 1)
            n = dim
            while True:
                dir_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))
                if dim == 2:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = sum(R[0][i] * sample_scaled[i] for i in range(n))
                    y_rot = sum(R[1][i] * sample_scaled[i] for i in range(n))
                    z_rot = sum(R[2][i] * sample_scaled[i] for i in range(n))
                    rotated = (x_rot, y_rot, z_rot)
                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: List[Node], sample: Tuple[float, ...]) -> Optional[Node]:
            nearest_node = min(tree, key=lambda n: dist(n.position, sample))
            new_pos = steer(nearest_node.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest_node.position, new_pos):
                return None
            r = neighbor_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, r)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for nbr in neighbors:
                if nbr is nearest_node:
                    continue
                cost_thru = nbr.cost + dist(nbr.position, new_pos)
                if cost_thru < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_thru
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is best_parent:
                    continue
                cost_thru_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_thru_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_thru_new)

            return new_node

        def incremental_connect(tree: List[Node], target_node: Node) -> Optional[Node]:
            curr = min(tree, key=lambda n: dist(n.position, target_node.position))
            while True:
                new_pos = steer(curr.position, target_node.position)
                if not collision_free_node(new_pos) or not collision_free_edge(curr.position, new_pos):
                    return None
                r = neighbor_radius(len(tree) + 1)
                neighbors = near_nodes(tree, new_pos, r)

                min_cost = curr.cost + dist(curr.position, new_pos)
                best_parent = curr
                for nbr in neighbors:
                    if nbr is curr:
                        continue
                    cost_thru = nbr.cost + dist(nbr.position, new_pos)
                    if cost_thru < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_thru
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_thru_new = new_node.cost + dist(new_node.position, nbr.position)
                    if cost_thru_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_thru_new)

                if dist(new_node.position, target_node.position) <= self.step_size:
                    if collision_free_edge(new_node.position, target_node.position):
                        return new_node
                    else:
                        return None
                curr = new_node

        def path_smoothing(path: List[Tuple[float, ...]], iterations: int = 100) -> List[Tuple[float, ...]]:
            if len(path) < 3:
                return path[:]
            for _ in range(iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        start_tree: List[Node] = [Node(start_pos, cost=0.0)]
        goal_tree: List[Node] = [Node(goal_pos, cost=0.0)]

        best_path: List[Tuple[float, ...]] = []
        best_cost = math.inf
        informed_sampling_active = False
        c_min = dist(start_pos, goal_pos)
        grow_start_tree = True

        start_time = time.perf_counter()

        for _ in range(self.max_iter):
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_time_sec:
                break

            sample = informed_sample(best_cost, c_min) if informed_sampling_active else (
                goal_pos if random.random() < 0.2 else uniform_sample()
            )

            if grow_start_tree:
                new_node = extend_and_rewire(start_tree, sample)
                if new_node is None:
                    grow_start_tree = not grow_start_tree
                    continue
                connect_node = incremental_connect(goal_tree, new_node)
                if connect_node:
                    path_s = new_node.path_from_root()
                    path_g = connect_node.path_from_root()
                    if path_g and path_g[0] == new_node.position:
                        path_g = path_g[1:]
                    combined_path = path_s + path_g[::-1]
                    combined_cost = new_node.cost + connect_node.cost + dist(new_node.position, connect_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling_active = True
                        # Early termination on improved feasible path
                        smoothed = path_smoothing(best_path)
                        # Collect nodes and edges for return
                        nodes = start_tree + goal_tree
                        edges = []
                        for node in nodes:
                            for c in node.children:
                                edges.append((node, c))
                        return PlannerResult(True, smoothed, nodes, edges)
            else:
                new_node = extend_and_rewire(goal_tree, sample)
                if new_node is None:
                    grow_start_tree = not grow_start_tree
                    continue
                connect_node = incremental_connect(start_tree, new_node)
                if connect_node:
                    path_g = new_node.path_from_root()
                    path_s = connect_node.path_from_root()
                    if path_s and path_s[0] == new_node.position:
                        path_s = path_s[1:]
                    combined_path = path_s + path_g[::-1]
                    combined_cost = new_node.cost + connect_node.cost + dist(new_node.position, connect_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling_active = True
                        smoothed = path_smoothing(best_path)
                        nodes = start_tree + goal_tree
                        edges = []
                        for node in nodes:
                            for c in node.children:
                                edges.append((node, c))
                        return PlannerResult(True, smoothed, nodes, edges)

            grow_start_tree = not grow_start_tree

        if best_path:
            success_state = True
            smoothed = path_smoothing(best_path)
        else:
            success_state = False
            smoothed = []

        nodes = start_tree + goal_tree
        edges = []
        for node in nodes:
            for c in node.children:
                edges.append((node, c))

        return PlannerResult(success_state, smoothed, nodes, edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d) -> bool:
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0) -> bool:
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position          # Tuple[float, ...]
        self.parent = parent              # Node or None
        self.cost = cost                  # Cost from root
        self.children = []
        self.valid = True

        if parent:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        dist_func = math.dist
        for child in self.children:
            child.update_parent(self, self.cost + dist_func(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 goal_sample_rate: float=0.2,
                 neighbor_radius_min: float=10.0,
                 neighbor_radius_max: float=30.0,
                 max_planning_time: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist_func(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_radius(n_nodes: int):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma = 30.0
            r = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist_func(node.position, pos) ** 2) <= r_sq]

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x/norm for x in pt)

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0,1 - cos_theta*cos_theta))
                K = [[0, -kz, ky],[kz,0,-kx],[-ky,kx,0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A, B):
                    return [[A[i][j] + B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A, s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A, B):
                    return [[sum(A[i][k] * B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))

        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path: list = []
        informed_sampling = False
        rotation_mat = None

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_free()
            center = tuple(0.5*(start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            nonlocal rotation_mat
            if rotation_mat is None:
                rotation_mat = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                dir_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = rotation_mat[0][0]*sample_scaled[0] + rotation_mat[0][1]*sample_scaled[1]
                    y_rot = rotation_mat[1][0]*sample_scaled[0] + rotation_mat[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = rotation_mat[0][0]*sample_scaled[0] + rotation_mat[0][1]*sample_scaled[1] + rotation_mat[0][2]*sample_scaled[2]
                    y_rot = rotation_mat[1][0]*sample_scaled[0] + rotation_mat[1][1]*sample_scaled[1] + rotation_mat[1][2]*sample_scaled[2]
                    z_rot = rotation_mat[2][0]*sample_scaled[0] + rotation_mat[2][1]*sample_scaled[1] + rotation_mat[2][2]*sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: list, sample: tuple) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_radius(n_nodes + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through = nbr.cost + dist_func(nbr.position, new_pos)
                if cost_through < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: list, target_pos: tuple) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through = nbr.cost + dist_func(nbr.position, new_pos)
                    if cost_through < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                if dist_func(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_smoothing(path: list, trials: int = 100):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        # Initialize bidirectional trees
        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                break

            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start else start_pos
                else:
                    sample_pt = sample_free()

            if grow_start:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected = incremental_connect(tree_goal, new_node.position)
                if connected:
                    path_start = new_node.path_from_root()
                    path_goal = connected.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected.cost + dist_func(new_node.position, connected.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined
                        informed_sampling = True

                    smoothed_path = path_smoothing(best_path)
                    return self._build_result(True, smoothed_path, tree_start, tree_goal)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected = incremental_connect(tree_start, new_node.position)
                if connected:
                    path_goal = new_node.path_from_root()
                    path_start = connected.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected.cost + dist_func(new_node.position, connected.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined
                        informed_sampling = True

                    smoothed_path = path_smoothing(best_path)
                    return self._build_result(True, smoothed_path, tree_start, tree_goal)

            grow_start = not grow_start

        success = (len(best_path) > 0)
        smoothed = path_smoothing(best_path) if success else []
        return self._build_result(success, smoothed, tree_start, tree_goal)

    def _build_result(self, success: bool, path: list, tree_start: list, tree_goal: list):
        nodes = tree_start + tree_goal
        edges = []
        for node in nodes:
            for child in node.children:
                edges.append((node, child))
        return PlannerResult(success, path, nodes, edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * factor for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = None              # Node or None
        self.cost = cost                # Cost from root
        self.children = []
        self.valid = True               # Validity flag

        if parent is not None:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        dist_func = math.dist
        for child in self.children:
            child.update_parent(self, self.cost + dist_func(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0, goal_sample_rate: float=0.2,
                 neighbor_radius_min: float=10.0, neighbor_radius_max: float=30.0,
                 max_planning_time: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> 'PlannerResult':
        import time
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist_func(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes: int):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 30.0
            r = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist_func(node.position, pos) ** 2) <= r_sq]

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0,1 - cos_theta*cos_theta))
                K = [[0, -kz, ky],[kz,0,-kx],[-ky,kx,0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A, B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A, s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A, B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1-cos_theta))

        self._rotation_matrix = rotation_matrix

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x/norm for x in pt)

        self._unit_ball_sample = unit_ball_sample

        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_free()
            center = tuple(0.5*(start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = self._rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = self._unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1] + R[0][2]*sample_scaled[2]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1] + R[1][2]*sample_scaled[2]
                    z_rot = R[2][0]*sample_scaled[0] + R[2][1]*sample_scaled[1] + R[2][2]*sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: list, sample: tuple) -> Node or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: list, target_pos: tuple) -> Node or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                    if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through_nbr
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                if dist_func(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_smoothing(path: list, trials: int = 100) -> list:
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        all_nodes = tree_start + tree_goal
        all_edges = []

        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1

            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                break

            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start else start_pos
                else:
                    sample_pt = sample_free()

            if grow_start:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_goal, new_node.position)
                if connected_node is not None:
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        all_nodes = tree_start + tree_goal
                        all_edges.clear()
                        for node in all_nodes:
                            for c in node.children:
                                all_edges.append((node, c))

                        smoothed = path_smoothing(best_path)
                        return PlannerResult(True, smoothed, all_nodes, all_edges)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_start, new_node.position)
                if connected_node is not None:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        all_nodes = tree_start + tree_goal
                        all_edges.clear()
                        for node in all_nodes:
                            for c in node.children:
                                all_edges.append((node, c))

                        smoothed = path_smoothing(best_path)
                        return PlannerResult(True, smoothed, all_nodes, all_edges)

            grow_start = not grow_start

        success_state = (len(best_path) > 0)
        smoothed = path_smoothing(best_path) if success_state else []

        all_nodes = tree_start + tree_goal
        all_edges.clear()
        for node in all_nodes:
            for c in node.children:
                all_edges.append((node, c))

        return PlannerResult(success_state, smoothed, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = None              # Node or None
        self.cost = 0.0                # Cost-to-come
        self.children = []
        self.valid = True              # Collision validity
        if parent:
            self.update_parent(parent, parent.cost + self._dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        # Propagate cost update recursively to children
        for c in self.children:
            c.update_parent(self, self.cost + self._dist(self.position, c.position))

    def path_from_root(self):
        node = self
        path = []
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]

    @staticmethod
    def _dist(a, b):
        return math.dist(a, b)
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.2,
                 neighbor_radius_min: float = 8.0,
                 neighbor_radius_max: float = 30.0,
                 max_time_sec: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_time_sec = max_time_sec

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)
        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=max(self.step_size / 5, 0.5))

        def adaptive_neighbor_radius(n_nodes):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma = 30.0
            r = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree, pos, radius):
            radius_sq = radius * radius
            return [node for node in tree if (dist(node.position, pos) ** 2) <= radius_sq]

        def sample_free():
            attempt = 0
            while True:
                attempt += 1
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt
                if attempt > 1000:  # fallback to goal or start
                    return random.choice([start_pos, goal_pos])

        c_min = dist(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= norm_k; ky /= norm_k; kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0, 1 - cos_theta * cos_theta))
                K = [[0, -kz, ky], [kz, 0, -kx], [-ky, kx, 0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A,B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A,s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A,B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K,K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in pt)

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-12:
                return sample_free()
            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                aminor = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1, aminor, aminor]

            n = dim
            for _ in range(100):
                direction_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1] + R[0][2] * sample_scaled[2]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1] + R[1][2] * sample_scaled[2]
                    z_rot = R[2][0] * sample_scaled[0] + R[2][1] * sample_scaled[1] + R[2][2] * sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(sample_pt):
                    return sample_pt
            # fallback:
            return sample_free()

        def extend_and_rewire(tree, sample):
            nearest = min(tree, key=lambda n: dist(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr == nearest:
                    continue
                new_cost = nbr.cost + dist(nbr.position, new_pos)
                if new_cost < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = new_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_thru_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_thru_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_thru_new)

            return new_node

        def incremental_connect(tree, target_pos):
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr == current:
                        continue
                    new_cost = nbr.cost + dist(nbr.position, new_pos)
                    if new_cost < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = new_cost
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_thru_new = new_node.cost + dist(new_node.position, nbr.position)
                    if cost_thru_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_thru_new)

                if dist(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None
                current = new_node

        def path_smoothing(path, trials=150):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        # Initialize trees
        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        all_nodes = tree_start + tree_goal
        all_edges = []

        grow_start_tree = True
        iter_count = 0

        while iter_count < self.max_iter:
            curr_time = time.perf_counter()
            if curr_time - start_time > self.max_time_sec:
                break

            iter_count += 1

            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                # Goal bias toward opposite root in alternate tree
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start_tree else start_pos
                else:
                    sample_pt = sample_free()

            if grow_start_tree:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if new_node is None:
                    grow_start_tree = not grow_start_tree
                    continue
                connected_node = incremental_connect(tree_goal, new_node.position)
                if connected_node:
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    # Build edges from both trees
                    all_nodes = tree_start + tree_goal
                    all_edges.clear()
                    for n in all_nodes:
                        for c in n.children:
                            all_edges.append((n, c))

                    smoothed_path = path_smoothing(best_path)
                    return PlannerResult(True, smoothed_path, all_nodes, all_edges)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if new_node is None:
                    grow_start_tree = not grow_start_tree
                    continue
                connected_node = incremental_connect(tree_start, new_node.position)
                if connected_node:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    all_nodes = tree_start + tree_goal
                    all_edges.clear()
                    for n in all_nodes:
                        for c in n.children:
                            all_edges.append((n, c))

                    smoothed_path = path_smoothing(best_path)
                    return PlannerResult(True, smoothed_path, all_nodes, all_edges)

            grow_start_tree = not grow_start_tree

        # Timeout or max iterations reached
        success_state = best_path != []
        if success_state:
            best_path = path_smoothing(best_path)
        all_nodes = tree_start + tree_goal
        all_edges.clear()
        for n in all_nodes:
            for c in n.children:
                all_edges.append((n, c))

        return PlannerResult(success_state, best_path, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child):
        self.children.append(child)


class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0, radius: float = 10.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.radius = radius

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        is_3d = len(bounds) == 3

        start_tree = [Node(start_position)]
        goal_tree = [Node(goal_position)]
        nodes = []
        edges = []
        success_state = False
        extracted_path = []

        for iter in range(self.max_iter):
            rand = self._sample_free(bounds, obstacles, is_3d)
            for tree_a, tree_b in [(start_tree, goal_tree), (goal_tree, start_tree)]:
                nearest = self._nearest(tree_a, rand)
                new_pos = self._steer(nearest.position, rand, self.step_size)
                if self._is_in_obstacle(new_pos, obstacles, is_3d):
                    continue
                if self._is_edge_in_obstacle(nearest.position, new_pos, obstacles, is_3d):
                    continue

                new_node = Node(new_pos)
                near_nodes = self._near(tree_a, new_pos, self.radius)
                min_cost = nearest.cost + self._distance(nearest.position, new_pos)
                min_parent = nearest

                for near in near_nodes:
                    cost = near.cost + self._distance(near.position, new_pos)
                    if cost < min_cost and not self._is_edge_in_obstacle(near.position, new_pos, obstacles, is_3d):
                        min_cost = cost
                        min_parent = near

                new_node.parent = min_parent
                new_node.cost = min_cost
                min_parent.add_child(new_node)
                tree_a.append(new_node)
                nodes.append(new_node)
                edges.append((min_parent, new_node))

                for near in near_nodes:
                    if near == min_parent:
                        continue
                    new_cost = new_node.cost + self._distance(new_node.position, near.position)
                    if new_cost < near.cost and not self._is_edge_in_obstacle(new_node.position, near.position, obstacles, is_3d):
                        if near.parent:
                            edges.remove((near.parent, near))
                        near.parent = new_node
                        near.cost = new_cost
                        new_node.add_child(near)
                        edges.append((new_node, near))

                connect_node = self._nearest(tree_b, new_node.position)
                if self._distance(new_node.position, connect_node.position) < self.step_size and not self._is_edge_in_obstacle(new_node.position, connect_node.position, obstacles, is_3d):
                    success_state = True
                    extracted_path = self._extract_path(new_node, connect_node)
                    return PlannerResult(True, extracted_path, start_tree + goal_tree, edges)

        return PlannerResult(False, [], start_tree + goal_tree, edges)

    def _sample_free(self, bounds, obstacles, is_3d):
        while True:
            point = tuple(random.uniform(0, bounds[i]) for i in range(3 if is_3d else 2))
            if not self._is_in_obstacle(point, obstacles, is_3d):
                return point

    def _nearest(self, tree, point):
        return min(tree, key=lambda node: self._distance(node.position, point))

    def _near(self, tree, point, radius):
        return [node for node in tree if self._distance(node.position, point) <= radius]

    def _steer(self, from_pos, to_pos, step_size):
        dist = self._distance(from_pos, to_pos)
        if dist <= step_size:
            return to_pos
        ratio = step_size / dist
        return tuple(from_pos[i] + (to_pos[i] - from_pos[i]) * ratio for i in range(len(from_pos)))

    def _extract_path(self, node_a, node_b):
        path_a = []
        while node_a:
            path_a.append(node_a.position)
            node_a = node_a.parent
        path_b = []
        while node_b:
            path_b.append(node_b.position)
            node_b = node_b.parent
        return list(reversed(path_a)) + path_b

    def _distance(self, a, b):
        return math.dist(a, b)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = self._distance(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position          # Tuple[float, ...]
        self.parent = None                # Node or None
        self.cost = cost                  # Cost from root
        self.children = []                # List[Node]
        self.valid = True                 # Validity flag

        if parent is not None:
            self.update_parent(parent, parent.cost + self._dist(parent.position, position))

    def add_child(self, child_node):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        # Detach from current parent
        if self.parent is not None:
            self.parent.remove_child(self)
        # Attach to new parent
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        # Propagate cost updates to descendants recursively
        for child in self.children:
            child.update_parent(self, self.cost + self._dist(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]

    @staticmethod
    def _dist(p1, p2):
        import math
        return math.dist(p1, p2)
class Planner:
    def __init__(self, max_iter: int = 7000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.15,
                 neighbor_radius_min: float = 10.0,
                 neighbor_radius_max: float = 30.0,
                 max_planning_time: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> 'PlannerResult':
        import time
        import math
        import random

        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        start_time = time.perf_counter()

        # Helper functions
        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            # Use fine resolution for better collision detection
            resolution = self.step_size / 5.0
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution)

        def adaptive_neighbor_radius(n_nodes, iter_num):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 30.0
            base_radius = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            # Interpolate based on iteration progress (favor smaller radius at later stages)
            iteration_factor = min(1.0, iter_num / self.max_iter)
            radius = base_radius * (1.0 - 0.6 * iteration_factor)  # reduce radius as iterations grow
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, radius))

        def near_nodes(tree_nodes, pos, radius):
            rad_sq = radius * radius
            return [node for node in tree_nodes if (math.dist(node.position, pos) ** 2) <= rad_sq]

        def sample_uniform():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        def unit_ball_sample(n):
            while True:
                point = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in point)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in point)

        def rotation_matrix(direction):
            # Returns rotation matrix aligning unit x-axis to direction vector in dim=2 or dim=3
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a = math.cos(angle)
                sin_a = math.sin(angle)
                return [[cos_a, -sin_a],
                        [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1, 0, 0],
                            [0, 1, 0],
                            [0, 0, 1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k

                cos_theta = vx
                sin_theta = math.sqrt(max(0, 1 - cos_theta * cos_theta))
                K = [[0, -kz, ky],
                     [kz, 0, -kx],
                     [-ky, kx, 0]]
                I = [[1, 0, 0],
                     [0, 1, 0],
                     [0, 0, 1]]

                def mat_add(A, B):
                    return [[A[i][j] + B[i][j] for j in range(3)] for i in range(3)]

                def mat_scale(A, s):
                    return [[A[i][j] * s for j in range(3)] for i in range(3)]

                def mat_mul(A, B):
                    return [[sum(A[i][k] * B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))

        # Informed sampling returns a point inside an ellipsoid defined by start, goal, and current best cost
        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_uniform()
            center = tuple(0.5 * (start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best ** 2 - c_min ** 2, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best ** 2 - c_min ** 2, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1] + R[0][2] * sample_scaled[2]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1] + R[1][2] * sample_scaled[2]
                    z_rot = R[2][0] * sample_scaled[0] + R[2][1] * sample_scaled[1] + R[2][2] * sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                clamped = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(clamped):
                    return clamped

        def extend_and_rewire(tree: list, sample: tuple, iter_num: int) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: math.dist(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1, iter_num)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + math.dist(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through_nbr = nbr.cost + math.dist(nbr.position, new_pos)
                if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            # Rewire neighbors toward new_node if cheaper
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + math.dist(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: list, target_pos: tuple, iter_num: int) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: math.dist(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1, iter_num)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + math.dist(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through_nbr = nbr.cost + math.dist(nbr.position, new_pos)
                    if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through_nbr
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + math.dist(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                dist_to_target = math.dist(new_node.position, target_pos)
                if dist_to_target <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_smoothing(path: list, trials: int = 150):
            # Random shortcut smoothing with collision checks
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        # Initialization
        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        best_cost = math.inf
        best_path = []
        informed_sampling = False

        c_min = math.dist(start_pos, goal_pos)

        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                break

            # Sample point: goal bias + informed sampling if solution found
            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start else start_pos
                else:
                    sample_pt = sample_uniform()

            if grow_start:
                new_node = extend_and_rewire(tree_start, sample_pt, iteration)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connect_node = incremental_connect(tree_goal, new_node.position, iteration)
                if connect_node:
                    # Construct combined path
                    path_start = new_node.path_from_root()
                    path_goal = connect_node.path_from_root()
                    # avoid duplicated connecting node position in concatenation
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connect_node.cost + math.dist(new_node.position, connect_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    smoothed = path_smoothing(best_path)
                    return self._build_result(True, smoothed, tree_start + tree_goal)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt, iteration)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connect_node = incremental_connect(tree_start, new_node.position, iteration)
                if connect_node:
                    path_goal = new_node.path_from_root()
                    path_start = connect_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connect_node.cost + math.dist(new_node.position, connect_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    smoothed = path_smoothing(best_path)
                    return self._build_result(True, smoothed, tree_start + tree_goal)

            grow_start = not grow_start

        # Timeout or iteration completion with best path found
        success = (len(best_path) > 0)
        if success:
            smoothed = path_smoothing(best_path)
        else:
            smoothed = []

        return self._build_result(success, smoothed, tree_start + tree_goal)

    def _build_result(self, success, path, nodes):
        edges = []
        for node in nodes:
            for c in node.children:
                edges.append((node, c))
        return PlannerResult(
            success=success,
            path=path,
            nodes=nodes,
            edges=edges,
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * factor for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position      # Tuple[float, ...]
        self.parent = None            # type: Node or None
        self.cost = cost              # Cost from root node
        self.children = []            # List of child nodes
        self.valid = True             # For collision and validity checks

        if parent:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        # Propagate cost updates recursively to descendants
        dist_func = math.dist
        for child in self.children:
            child.update_parent(self, self.cost + dist_func(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.2,
                 neighbor_radius_min: float = 10.0,
                 neighbor_radius_max: float = 30.0,
                 max_planning_time: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist_func(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes: int):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 30.0
            r = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist_func(node.position, pos) ** 2) <= r_sq]

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0,1 - cos_theta*cos_theta))
                K = [[0, -kz, ky],[kz,0,-kx],[-ky,kx,0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A, B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A, s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A, B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1-cos_theta))

        self._rotation_matrix = rotation_matrix

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x/norm for x in pt)

        self._unit_ball_sample = unit_ball_sample

        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_free()
            center = tuple(0.5*(start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = self._rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = self._unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1] + R[0][2]*sample_scaled[2]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1] + R[1][2]*sample_scaled[2]
                    z_rot = R[2][0]*sample_scaled[0] + R[2][1]*sample_scaled[1] + R[2][2]*sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                # Clamp to bounds
                sample_pt = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: list, sample: tuple) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: list, target_pos: tuple) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                    if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through_nbr
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                if dist_func(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_smoothing(path: list, trials: int = 100):
            # Remove unnecessary intermediate points using collision checks
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        # Initialize trees
        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        all_nodes = tree_start + tree_goal
        all_edges = []

        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                break

            # Sample point with goal bias or informed sampling if solution found
            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start else start_pos
                else:
                    sample_pt = sample_free()

            if grow_start:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_goal, new_node.position)
                if connected_node:
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    all_nodes = tree_start + tree_goal
                    all_edges.clear()
                    for node in all_nodes:
                        for c in node.children:
                            all_edges.append((node, c))

                    smoothed = path_smoothing(best_path)
                    return PlannerResult(True, smoothed, all_nodes, all_edges)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_start, new_node.position)
                if connected_node:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    all_nodes = tree_start + tree_goal
                    all_edges.clear()
                    for node in all_nodes:
                        for c in node.children:
                            all_edges.append((node, c))

                    smoothed = path_smoothing(best_path)
                    return PlannerResult(True, smoothed, all_nodes, all_edges)

            grow_start = not grow_start

        # Return best found path on timeout or iteration end
        success_state = (len(best_path) > 0)
        if success_state:
            smoothed = path_smoothing(best_path)
        else:
            smoothed = []

        all_nodes = tree_start + tree_goal
        all_edges.clear()
        for node in all_nodes:
            for c in node.children:
                all_edges.append((node, c))

        return PlannerResult(success_state, smoothed, all_nodes, all_edges)

    # Collision helpers same as provided
    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * factor for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = parent
        self.cost = cost
        self.children = []
        self.valid = True

    def add_child(self, child_node):
        self.children.append(child_node)
        child_node.parent = self

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            try:
                self.parent.children.remove(self)
            except ValueError:
                pass
        self.parent = new_parent
        self.cost = new_cost
        new_parent.children.append(self)

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0, goal_sample_rate: float=0.1,
                 neighbor_radius: float=15.0, max_time_sec: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius = neighbor_radius
        self.max_time_sec = max_time_sec

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles
        dimension = len(bounds)
        is_3d = (dimension == 3)

        import time, random, math
        start_time = time.perf_counter()

        nodes = []
        edges = []

        tree_start = [Node(start_position)]
        tree_goal = [Node(goal_position)]
        nodes.extend(tree_start)
        nodes.extend(tree_goal)

        success_state = False
        extracted_path = []
        best_path_cost = float('inf')
        best_path_nodes = (None, None)  # Tuple[Node,Node]

        def sample_free():
            if random.random() < self.goal_sample_rate:
                return goal_position
            while True:
                p = tuple(random.uniform(0, bounds[d]) for d in range(dimension))
                if not self._is_in_obstacle(p, obstacles, is_3d):
                    return p

        def nearest(tree, point):
            return min(tree, key=lambda n: math.dist(n.position, point))

        def steer(from_pos, to_pos):
            dist = math.dist(from_pos, to_pos)
            if dist <= self.step_size:
                return to_pos
            ratio = self.step_size / dist
            return tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * ratio for d in range(dimension))

        def near_nodes(tree, position):
            r = self.neighbor_radius
            return [node for node in tree if math.dist(node.position, position) <= r]

        def try_extend_and_rewire(tree, point):
            nearest_node = nearest(tree, point)
            new_pos = steer(nearest_node.position, point)

            if (
                not self._within_bounds(new_pos, bounds) or
                self._is_in_obstacle(new_pos, obstacles, is_3d) or
                self._is_edge_in_obstacle(nearest_node.position, new_pos, obstacles, is_3d)
            ):
                return None

            neighbors = near_nodes(tree, new_pos)

            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for neighbor in neighbors:
                cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                if cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                    min_cost = cost
                    best_parent = neighbor

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)
            nodes.append(new_node)
            edges.append((best_parent, new_node))

            # Rewire neighbors via the new node if beneficial
            for neighbor in neighbors:
                cost_through_new = new_node.cost + math.dist(new_node.position, neighbor.position)
                if (
                    cost_through_new < neighbor.cost and
                    not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d)
                ):
                    old_parent = neighbor.parent
                    neighbor.update_parent(new_node, cost_through_new)
                    edges.append((new_node, neighbor))
                    try:
                        edges.remove((old_parent, neighbor))
                    except ValueError:
                        pass

            return new_node

        def try_incremental_connect(tree, target_node):
            target_pos = target_node.position
            current_node = nearest(tree, target_pos)

            while True:
                dist = math.dist(current_node.position, target_pos)
                if dist <= self.step_size:
                    new_pos = target_pos
                else:
                    new_pos = steer(current_node.position, target_pos)

                if (
                    not self._within_bounds(new_pos, bounds) or
                    self._is_in_obstacle(new_pos, obstacles, is_3d) or
                    self._is_edge_in_obstacle(current_node.position, new_pos, obstacles, is_3d)
                ):
                    return None

                neighbors = near_nodes(tree, new_pos)
                min_cost = current_node.cost + math.dist(current_node.position, new_pos)
                best_parent = current_node
                for neighbor in neighbors:
                    cost = neighbor.cost + math.dist(neighbor.position, new_pos)
                    if cost < min_cost and not self._is_edge_in_obstacle(neighbor.position, new_pos, obstacles, is_3d):
                        min_cost = cost
                        best_parent = neighbor

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)
                nodes.append(new_node)
                edges.append((best_parent, new_node))

                # Rewire neighbors for potential cost improvement
                for neighbor in neighbors:
                    cost_through_new = new_node.cost + math.dist(new_node.position, neighbor.position)
                    if (
                        cost_through_new < neighbor.cost and
                        not self._is_edge_in_obstacle(new_node.position, neighbor.position, obstacles, is_3d)
                    ):
                        old_parent = neighbor.parent
                        neighbor.update_parent(new_node, cost_through_new)
                        edges.append((new_node, neighbor))
                        try:
                            edges.remove((old_parent, neighbor))
                        except ValueError:
                            pass

                if new_node.position == target_pos:
                    return new_node

                current_node = new_node

        def merge_paths(node_start, node_goal):
            path_start = node_start.path_from_root()
            path_goal = node_goal.path_from_root()
            return path_start + path_goal[::-1][1:]

        for i in range(self.max_iter):
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_time_sec:
                # Return best path found so far if any
                if success_state:
                    extracted_path = merge_paths(best_path_nodes[0], best_path_nodes[1])
                break

            rnd_point = sample_free()

            new_node_start = try_extend_and_rewire(tree_start, rnd_point)
            if new_node_start is None:
                tree_start, tree_goal = tree_goal, tree_start
                continue

            connect_node_goal = try_incremental_connect(tree_goal, new_node_start)
            if connect_node_goal:
                total_cost = new_node_start.cost + connect_node_goal.cost
                if total_cost < best_path_cost:
                    best_path_cost = total_cost
                    best_path_nodes = (new_node_start, connect_node_goal)
                    extracted_path = merge_paths(new_node_start, connect_node_goal)
                    success_state = True
                tree_start, tree_goal = tree_goal, tree_start
                # Early break as success found
                if success_state:
                    break
            else:
                tree_start, tree_goal = tree_goal, tree_start

        return PlannerResult(
            success=success_state,
            path=extracted_path,
            nodes=nodes,
            edges=edges
        )

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

    def _within_bounds(self, pos, bounds):
        return all(0 <= pos[d] <= bounds[d] for d in range(len(bounds)))

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = None              # Node or None
        self.cost = cost                # Cost from root node
        self.children = []
        self.valid = True               # Validity flag

        if parent:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        dist_func = math.dist
        for child in self.children:
            child.update_parent(self, self.cost + dist_func(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.2,
                 neighbor_radius_min: float = 10.0,
                 neighbor_radius_max: float = 30.0,
                 max_planning_time: float = 30.0,
                 smoothing_trials: int = 120):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time
        self.smoothing_trials = smoothing_trials

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist_func(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            # Use finer resolution for edge collision checking
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes: int):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 30.0
            r = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist_func(node.position, pos) ** 2) <= r_sq]

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        def _rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0,1 - cos_theta*cos_theta))
                K = [[0, -kz, ky],[kz,0,-kx],[-ky,kx,0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A, B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A, s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A, B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1-cos_theta))

        self._rotation_matrix = _rotation_matrix

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x/norm for x in pt)

        self._unit_ball_sample = unit_ball_sample

        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_free()
            center = tuple(0.5*(start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = self._rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = self._unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1] + R[0][2]*sample_scaled[2]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1] + R[1][2]*sample_scaled[2]
                    z_rot = R[2][0]*sample_scaled[0] + R[2][1]*sample_scaled[1] + R[2][2]*sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                # Clamp to bounds
                sample_pt = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: list, sample: tuple) -> Node or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: list, target_pos: tuple) -> Node or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                    if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through_nbr
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                if dist_func(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_smoothing(path: list, trials: int = self.smoothing_trials):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        best_path = []
        best_cost = math.inf
        informed_sampling = False
        all_nodes = tree_start + tree_goal
        all_edges = []
        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                break

            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start else start_pos
                else:
                    sample_pt = sample_free()

            if grow_start:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_goal, new_node.position)
                if connected_node:
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    all_nodes = tree_start + tree_goal
                    all_edges.clear()
                    for node in all_nodes:
                        for c in node.children:
                            all_edges.append((node, c))

                    smoothed = path_smoothing(best_path)
                    return PlannerResult(True, smoothed, all_nodes, all_edges)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_start, new_node.position)
                if connected_node:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    all_nodes = tree_start + tree_goal
                    all_edges.clear()
                    for node in all_nodes:
                        for c in node.children:
                            all_edges.append((node, c))

                    smoothed = path_smoothing(best_path)
                    return PlannerResult(True, smoothed, all_nodes, all_edges)

            grow_start = not grow_start

        success_state = (len(best_path) > 0)
        if success_state:
            smoothed = path_smoothing(best_path)
        else:
            smoothed = []

        all_nodes = tree_start + tree_goal
        all_edges.clear()
        for node in all_nodes:
            for c in node.children:
                all_edges.append((node, c))

        return PlannerResult(success_state, smoothed, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * factor for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position            # Tuple[float, ...]
        self.parent = None                  # type: Node or None
        self.children = []                  # Child nodes
        self.cost = cost                   # Cost from root to this node
        self.valid = True                  # Validity flag

        if parent:
            self.update_parent(parent, parent.cost + self._dist(parent.position, position))

    def add_child(self, child):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        # Recursively update descendants' costs
        for child in self.children:
            child.update_parent(self, self.cost + self._dist(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]

    @staticmethod
    def _dist(p1, p2):
        return math.dist(p1, p2)
class Planner:
    def __init__(self, max_iter=10000, step_size=5.0, goal_sample_rate=0.15,
                 neighbor_radius_min=5.0, neighbor_radius_max=30.0, max_planning_time=30.0,
                 smoothing_trials=150):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time
        self.smoothing_trials = smoothing_trials

    def plan(self, map) -> 'PlannerResult':
        import time
        import random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist = math.dist

        t_start = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def clamp(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            scale = self.step_size / d
            return tuple(from_pos[d] + scale * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and not self._is_in_obstacle(pos, obstacles, is_3d)

        def collision_free_edge(p1, p2):
            resolution = self.step_size / 5.0
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution)

        def adaptive_neighbor_radius(n_nodes):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma = self.step_size * 30.0
            radius = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, radius))

        def near_nodes(tree, pos, radius):
            r_sq = radius * radius
            return [node for node in tree if (dist(node.position, pos) ** 2) <= r_sq]

        def unit_ball_sample(n):
            while True:
                sample = [random.uniform(-1,1) for _ in range(n)]
                norm_sq = sum(x*x for x in sample)
                if 1e-12 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in sample)

        rotation_mat_cache = None
        c_min = dist(start_pos, goal_pos)

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a],[sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                norm_v = math.sqrt(vx*vx + vy*vy + vz*vz)
                if norm_v < 1e-12:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                ux, uy, uz = vx/norm_v, vy/norm_v, vz/norm_v
                if abs(ux) < 1e-6 and abs(uy) < 1e-6:
                    vx_, vy_, vz_ = 0.0, 1.0, 0.0
                else:
                    vx_, vy_, vz_ = -uy, ux, 0.0
                norm_ = math.sqrt(vx_*vx_ + vy_*vy_ + vz_*vz_)
                vx_ /= norm_; vy_ /= norm_; vz_ /= norm_
                wx = uy*vz_ - uz*vy_
                wy = uz*vx_ - ux*vz_
                wz = ux*vy_ - uy*vx_
                return [[ux, vx_, wx],[uy, vy_, wy],[uz, vz_, wz]]

        def informed_sample(c_best):
            nonlocal rotation_mat_cache
            if c_best == math.inf or c_best < c_min - 1e-12:
                while True:
                    candidate = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                    if collision_free_node(candidate):
                        return candidate

            center = tuple(0.5 * (start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            if rotation_mat_cache is None:
                rotation_mat_cache = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                dir_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = rotation_mat_cache[0][0]*sample_scaled[0] + rotation_mat_cache[0][1]*sample_scaled[1]
                    y_rot = rotation_mat_cache[1][0]*sample_scaled[0] + rotation_mat_cache[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = (rotation_mat_cache[0][0]*sample_scaled[0] +
                             rotation_mat_cache[0][1]*sample_scaled[1] +
                             rotation_mat_cache[0][2]*sample_scaled[2])
                    y_rot = (rotation_mat_cache[1][0]*sample_scaled[0] +
                             rotation_mat_cache[1][1]*sample_scaled[1] +
                             rotation_mat_cache[1][2]*sample_scaled[2])
                    z_rot = (rotation_mat_cache[2][0]*sample_scaled[0] +
                             rotation_mat_cache[2][1]*sample_scaled[1] +
                             rotation_mat_cache[2][2]*sample_scaled[2])
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = clamp(sample_pt)
                if collision_free_node(sample_pt):
                    return sample_pt

        def rewire(tree, new_node, neighbors):
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_through_new + 1e-12 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

        def extend(tree, sample_pt):
            nearest = min(tree, key=lambda n: dist(n.position, sample_pt))
            new_pos = steer(nearest.position, sample_pt)
            new_pos = clamp(new_pos)

            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            radius = adaptive_neighbor_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            rewire(tree, new_node, neighbors)
            return new_node

        def connect_trees(new_node, other_tree):
            if not other_tree:
                return None
            current = min(other_tree, key=lambda n: dist(n.position, new_node.position))
            while True:
                new_pos = steer(current.position, new_node.position)
                new_pos = clamp(new_pos)

                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                radius = adaptive_neighbor_radius(len(other_tree) + 1)
                neighbors = near_nodes(other_tree, new_pos, radius)

                min_cost = current.cost + dist(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                    if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = tentative_cost
                        best_parent = nbr

                conn_node = Node(new_pos)
                best_parent.add_child(conn_node)
                conn_node.cost = min_cost
                other_tree.append(conn_node)

                rewire(other_tree, conn_node, neighbors)

                if dist(conn_node.position, new_node.position) <= self.step_size:
                    if collision_free_edge(conn_node.position, new_node.position):
                        return conn_node
                    return None

                current = conn_node

        def path_shortcut(path, trials=self.smoothing_trials):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path)-3)
                j = random.randint(i+2, len(path)-1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i+1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        all_nodes = tree_start + tree_goal
        all_edges = []

        grow_start = True
        best_path = []
        best_cost = math.inf
        informed_sampling = False

        iteration = 0
        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - t_start
            if elapsed > self.max_planning_time:
                # Timeout - return best found path
                success_state = len(best_path) > 0
                smoothed = path_shortcut(best_path) if success_state else []
                all_nodes = tree_start + tree_goal
                all_edges.clear()
                for n in all_nodes:
                    for c in n.children:
                        all_edges.append((n,c))
                return PlannerResult(success_state, smoothed, all_nodes, all_edges)

            # Sampling point selection
            if informed_sampling:
                sample = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if grow_start else start_pos
                else:
                    while True:
                        p = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                        if collision_free_node(p):
                            sample = p
                            break

            active_tree = tree_start if grow_start else tree_goal
            other_tree = tree_goal if grow_start else tree_start

            new_node = extend(active_tree, sample)
            if new_node is None:
                grow_start = not grow_start
                continue
            all_nodes.append(new_node)

            connected_node = connect_trees(new_node, other_tree)
            if connected_node:
                path_start = new_node.path_from_root()
                path_goal = connected_node.path_from_root()
                # avoid duplicated node at connection point
                if path_goal and path_goal[0] == new_node.position:
                    path_goal = path_goal[1:]
                full_path = path_start + path_goal[::-1]

                total_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                if total_cost + 1e-12 < best_cost:
                    best_cost = total_cost
                    best_path = full_path
                    informed_sampling = True

                    smoothed_path = path_shortcut(best_path)
                    all_edges.clear()
                    all_nodes = tree_start + tree_goal
                    for n in all_nodes:
                        for c in n.children:
                            all_edges.append((n,c))

                    return PlannerResult(True, smoothed_path, all_nodes, all_edges)

            grow_start = not grow_start

        success_state = len(best_path) > 0
        smoothed_path = path_shortcut(best_path) if success_state else []

        all_nodes = tree_start + tree_goal
        all_edges.clear()
        for n in all_nodes:
            for c in n.children:
                all_edges.append((n,c))

        return PlannerResult(success_state, smoothed_path, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x,y,z,w,h,d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x,y,w,h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i/steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent: 'Node' = None, cost: float = 0.0):
        self.position = position                  # Tuple[float, ...]
        self.parent = None                        # type: Node or None
        self.cost = cost                          # cumulative cost from root
        self.children = []                        # List[Node]
        self.valid = True                         # validity (collision free)

        if parent is not None:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child_node: 'Node'):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node: 'Node'):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        dist_func = math.dist
        for child in self.children:
            child.update_parent(self, self.cost + dist_func(self.position, child.position))

    def path_from_root(self) -> List[Tuple[float, ...]]:
        node = self
        path = []
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
