            d = dist_func(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            scale = self.step_size / d
            return tuple(from_pos[d] + scale * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and not self._is_in_obstacle(pos, obstacles, is_3d)

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes: int) -> float:
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma = self.step_size * 30.0
            r = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree: List[Node], pos: Tuple[float, ...], radius: float) -> List[Node]:
            r_sq = radius * radius
            return [n for n in tree if (dist_func(n.position, pos) ** 2) <= r_sq]

        def unit_ball_sample(n: int):
            while True:
                sample = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in sample)
                if 1e-12 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in sample)

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                norm_v = math.sqrt(vx*vx + vy*vy + vz*vz)
                if norm_v < 1e-12:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                ux, uy, uz = vx/norm_v, vy/norm_v, vz/norm_v
                if abs(ux) < 1e-6 and abs(uy) < 1e-6:
                    vx_ = 0.0; vy_ = 1.0; vz_ = 0.0
                else:
                    vx_ = -uy; vy_ = ux; vz_ = 0.0
                norm_ = math.sqrt(vx_*vx_ + vy_*vy_ + vz_*vz_)
                vx_ /= norm_; vy_ /= norm_; vz_ /= norm_
                wx = uy*vz_ - uz*vy_
                wy = uz*vx_ - ux*vz_
                wz = ux*vy_ - uy*vx_
                return [[ux, vx_, wx], [uy, vy_, wy], [uz, vz_, wz]]

        rotation_mat_cache = None
        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path: List[Tuple[float, ...]] = []
        informed_sampling = False

        def informed_sample(c_best):
            nonlocal rotation_mat_cache

            if c_best == math.inf or c_best < c_min - 1e-12:
                while True:
                    sample_pt = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                    if collision_free_node(sample_pt):
                        return sample_pt

            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            if rotation_mat_cache is None:
                rotation_mat_cache = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                dir_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = rotation_mat_cache[0][0] * sample_scaled[0] + rotation_mat_cache[0][1] * sample_scaled[1]
                    y_rot = rotation_mat_cache[1][0] * sample_scaled[0] + rotation_mat_cache[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = (rotation_mat_cache[0][0] * sample_scaled[0] +
                             rotation_mat_cache[0][1] * sample_scaled[1] +
                             rotation_mat_cache[0][2] * sample_scaled[2])
                    y_rot = (rotation_mat_cache[1][0] * sample_scaled[0] +
                             rotation_mat_cache[1][1] * sample_scaled[1] +
                             rotation_mat_cache[1][2] * sample_scaled[2])
                    z_rot = (rotation_mat_cache[2][0] * sample_scaled[0] +
                             rotation_mat_cache[2][1] * sample_scaled[1] +
                             rotation_mat_cache[2][2] * sample_scaled[2])
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = clamp(sample_pt)
                if collision_free_node(sample_pt):
                    return sample_pt

        def rewire(tree: List[Node], new_node: Node, neighbors: List[Node]):
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new + 1e-12 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

        def extend(tree: List[Node], sample_pt: Tuple[float, ...]) -> Node:
            nearest = min(tree, key=lambda n: dist_func(n.position, sample_pt))
            new_pos = steer(nearest.position, sample_pt)
            new_pos = clamp(new_pos)

            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            radius = adaptive_neighbor_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                tentative_cost = nbr.cost + dist_func(nbr.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            rewire(tree, new_node, neighbors)
            return new_node

        def incremental_connect(new_node: Node, other_tree: List[Node]) -> Node:
            if not other_tree:
                return None
            current = min(other_tree, key=lambda n: dist_func(n.position, new_node.position))
            while True:
                new_pos = steer(current.position, new_node.position)
                new_pos = clamp(new_pos)

                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                radius = adaptive_neighbor_radius(len(other_tree) + 1)
                neighbors = near_nodes(other_tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    tentative_cost = nbr.cost + dist_func(nbr.position, new_pos)
                    if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = tentative_cost
                        best_parent = nbr

                conn_node = Node(new_pos)
                best_parent.add_child(conn_node)
                conn_node.cost = min_cost
                other_tree.append(conn_node)

                rewire(other_tree, conn_node, neighbors)

                if dist_func(conn_node.position, new_node.position) <= self.step_size:
                    if collision_free_edge(conn_node.position, new_node.position):
                        return conn_node
                    return None

                current = conn_node

        def path_shortcut(path: List[Tuple[float, ...]], trials: int = None) -> List[Tuple[float, ...]]:
            if trials is None:
                trials = self.smoothing_trials
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start: List[Node] = [Node(start_pos, None, 0.0)]
        tree_goal: List[Node] = [Node(goal_pos, None, 0.0)]

        all_nodes: List[Node] = tree_start + tree_goal
        all_edges: List[Tuple[Node, Node]] = []

        grow_start = True
        iteration = 0
        success_state = False
        extracted_path: List[Tuple[float, ...]] = []

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - t_start
            if elapsed > self.max_planning_time:
                if best_path:
                    smoothed = path_shortcut(best_path)
                    success_state = True
                else:
                    smoothed = []
                    success_state = False

                all_edges.clear()
                all_nodes = tree_start + tree_goal
                for n in all_nodes:
                    for c in n.children:
                        all_edges.append((n, c))

                return PlannerResult(success_state, smoothed, all_nodes, all_edges)

            # Sample point with goal bias or informed sampling after first solution
            if informed_sampling:
                sample = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if grow_start else start_pos
                else:
                    while True:
                        candidate = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                        if collision_free_node(candidate):
                            sample = candidate
                            break

            active_tree = tree_start if grow_start else tree_goal
            other_tree = tree_goal if grow_start else tree_start

            new_node = extend(active_tree, sample)
            if new_node is None:
                grow_start = not grow_start
                continue

            all_nodes.append(new_node)

            connected_node = incremental_connect(new_node, other_tree)
            if connected_node is not None:
                path_start = new_node.path_from_root()
                path_goal = connected_node.path_from_root()
                if path_goal and path_goal[0] == new_node.position:
                    path_goal = path_goal[1:]
                combined_path = path_start + path_goal[::-1]

                total_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                if total_cost + 1e-12 < best_cost:
                    best_cost = total_cost
                    best_path = combined_path
                    informed_sampling = True

                    smoothed_path = path_shortcut(best_path)

                    all_edges.clear()
                    all_nodes = tree_start + tree_goal
                    for n in all_nodes:
                        for c in n.children:
                            all_edges.append((n, c))

                    # Return immediately on finding better path
                    return PlannerResult(True, smoothed_path, all_nodes, all_edges)

            grow_start = not grow_start

        # Time or iteration limit reached without timeout
        if best_path:
            smoothed = path_shortcut(best_path)
            success_state = True
        else:
            smoothed = []
            success_state = False

        all_nodes = tree_start + tree_goal
        all_edges.clear()
        for n in all_nodes:
            for c in n.children:
                all_edges.append((n, c))

        return PlannerResult(success_state, smoothed, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * factor for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position            # Tuple[float, ...]
        self.parent = None                  # Node or None
        self.children = []
        self.cost = cost                    # Cost from root
        self.valid = True                   # Validity flag (collision-free)

        if parent is not None:
            self.set_parent(parent, parent.cost + self._distance(parent.position, position))

    def _distance(self, a, b):
        from math import dist
        return dist(a, b)

    def set_parent(self, new_parent, new_cost):
        if self.parent is new_parent and abs(self.cost - new_cost) < 1e-12:
            return
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        if new_parent is not None and self not in new_parent.children:
            new_parent.children.append(self)
        # Recursively update children costs
        for child in self.children:
            child.set_parent(self, self.cost + self._distance(self.position, child.position))

    def add_child(self, child_node):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float = 5.0, max_time_sec: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.max_time_sec = max_time_sec

        self.min_neighbor_radius = 8.0
        self.max_neighbor_radius = 20.0
        self.goal_sample_rate = 0.2
        self.smooth_trials = 150

    def plan(self, map) -> 'PlannerResult':
        import time
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(p):
            return all(0.0 <= p[i] <= bounds[i] for i in range(dim))

        def clamp(p):
            return tuple(max(0.0, min(bounds[i], p[i])) for i in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_p, to_p):
            d = dist(from_p, to_p)
            if d <= self.step_size:
                return to_p
            scale = self.step_size / d
            return tuple(from_p[i] + scale * (to_p[i] - from_p[i]) for i in range(dim))

        def collision_free_node(p):
            return in_bounds(p) and not self._is_in_obstacle(p, obstacles, is_3d)

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def neighbor_radius(n_nodes):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            r = self.max_neighbor_radius * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.min_neighbor_radius, min(self.max_neighbor_radius, r))

        def near_nodes(tree, pos, radius):
            r_sq = radius * radius
            return [node for node in tree if dist(node.position, pos)**2 <= r_sq]

        def uniform_sample():
            for _ in range(100):
                sample = tuple(random.uniform(0.0, bounds[i]) for i in range(dim))
                if collision_free_node(sample):
                    return sample
            return start_pos  # fallback

        def unit_ball_sample(n):
            while True:
                v = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in v)
                if 1e-12 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in v)

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                c, s = math.cos(angle), math.sin(angle)
                return [[c, -s],[s, c]]
            else:
                # Rodrigues' rotation formula: rotate x-axis (1,0,0) => direction vector
                vx, vy, vz = direction
                norm_v = math.sqrt(vx*vx + vy*vy + vz*vz)
                if norm_v < 1e-12:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                ux, uy, uz = vx/norm_v, vy/norm_v, vz/norm_v
                # cross product k = x-axis cross u
                kx, ky, kz = 0, -uz, uy
                k_norm = math.sqrt(kx*kx + ky*ky + kz*kz)
                if k_norm < 1e-12:
                    # Direction aligned with x-axis
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= k_norm
                ky /= k_norm
                kz /= k_norm
                cos_th = ux
                sin_th = math.sqrt(max(0,1 - cos_th*cos_th))
                K = [[0, -kz, ky],
                     [kz, 0, -kx],
                     [-ky, kx, 0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A,B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A,s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A,B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K,K)
                R = mat_add( mat_add(I, mat_scale(K, sin_th)), mat_scale(K2, 1 - cos_th) )
                return R

        def informed_sample(c_best, c_min):
            # If no current best or smaller than minimum distance, uniform sample
            if c_best == math.inf or c_best < c_min - 1e-12:
                return uniform_sample()

            center = tuple((start_pos[i] + goal_pos[i]) * 0.5 for i in range(dim))
            dx = tuple((goal_pos[i] - start_pos[i]) / c_min for i in range(dim))
            R = rotation_matrix(dx)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(0.0, c_best**2 - c_min**2)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(0.0, c_best**2 - c_min**2)) / 2.0
                radii = [a1] + [a_minor]*(dim-1)

            for _ in range(100):
                dir_sample = unit_ball_sample(dim)
                radius_samp = random.random() ** (1.0 / dim)
                ellipsoid_sample = tuple(radius_samp * radii[i] * dir_sample[i] for i in range(dim))

                if dim == 2:
                    x_rot = R[0][0]*ellipsoid_sample[0] + R[0][1]*ellipsoid_sample[1]
                    y_rot = R[1][0]*ellipsoid_sample[0] + R[1][1]*ellipsoid_sample[1]
                    sample_point = (center[0] + x_rot, center[1] + y_rot)
                else:
                    x_rot = sum(R[0][i]*ellipsoid_sample[i] for i in range(dim))
                    y_rot = sum(R[1][i]*ellipsoid_sample[i] for i in range(dim))
                    z_rot = sum(R[2][i]*ellipsoid_sample[i] for i in range(dim))
                    sample_point = (center[0] + x_rot, center[1] + y_rot, center[2] + z_rot)

                clipped = clamp(sample_point)
                if collision_free_node(clipped):
                    return clipped

            return uniform_sample()

        def extend_and_rewire(tree, sample):
            nearest = min(tree, key=lambda n: dist(n.position, sample))
            new_pos = steer(nearest.position, sample)
            new_pos = clamp(new_pos)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            radius = neighbor_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest

            for nbr in neighbors:
                if nbr is nearest:
                    continue
                tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            # Rewire neighbors if beneficial
            for nbr in neighbors:
                if nbr is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, nbr.position)
                if new_cost + 1e-12 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.set_parent(new_node, new_cost)

            return new_node

        def incremental_connect(tree, target_node):
            curr = min(tree, key=lambda n: dist(n.position, target_node.position))
            while True:
                new_pos = steer(curr.position, target_node.position)
                new_pos = clamp(new_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(curr.position, new_pos):
                    return None

                radius = neighbor_radius(len(tree) + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = curr.cost + dist(curr.position, new_pos)
                best_parent = curr
                for nbr in neighbors:
                    if nbr is curr:
                        continue
                    tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                    if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = tentative_cost
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                # Rewire neighbors if beneficial
                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_via_new = new_node.cost + dist(new_node.position, nbr.position)
                    if cost_via_new + 1e-12 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.set_parent(new_node, cost_via_new)

                if dist(new_node.position, target_node.position) <= self.step_size:
                    if collision_free_edge(new_node.position, target_node.position):
                        return new_node
                    return None
                curr = new_node

        def path_smoothing(path, trials=None):
            if trials is None:
                trials = self.smooth_trials
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i+1] + path[j:]
            return path

        c_min = dist(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []

        start_tree = [Node(start_pos, cost=0.0)]
        goal_tree = [Node(goal_pos, cost=0.0)]

        grew_start = True
        informed_sampling = False

        start_time = time.perf_counter()

        for iteration in range(self.max_iter):
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_time_sec:
                break

            if informed_sampling:
                sample = informed_sample(best_cost, c_min)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if grew_start else start_pos
                else:
                    sample = uniform_sample()

            active_tree = start_tree if grew_start else goal_tree
            other_tree = goal_tree if grew_start else start_tree

            new_node = extend_and_rewire(active_tree, sample)
            if new_node is None:
                grew_start = not grew_start
                continue

            connect_node = incremental_connect(other_tree, new_node)
            if connect_node is not None:
                path_start = new_node.path_from_root()
                path_goal = connect_node.path_from_root()
                if path_goal and path_goal[0] == new_node.position:
                    path_goal = path_goal[1:]
                combined_path = path_start + path_goal[::-1]

                combined_cost = new_node.cost + connect_node.cost + dist(new_node.position, connect_node.position)
                if combined_cost + 1e-12 < best_cost:
                    best_cost = combined_cost
                    best_path = combined_path
                    informed_sampling = True

                    smoothed = path_smoothing(best_path, trials=200)

                    # Early return on improved solution
                    all_nodes = start_tree + goal_tree
                    all_edges = []
                    for node in all_nodes:
                        for c in node.children:
                            all_edges.append((node, c))
                    return PlannerResult(True, smoothed, all_nodes, all_edges)

            grew_start = not grew_start

        # Timeout or max iterations reached: return best found path
        success = bool(best_path)
        final_path = path_smoothing(best_path, trials=200) if success else []

        all_nodes = start_tree + goal_tree
        all_edges = []
        for node in all_nodes:
            for c in node.children:
                all_edges.append((node, c))

        return PlannerResult(success, final_path, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position          # Tuple[float, ...]
        self.parent = None                # Node or None
        self.cost = cost                  # Cumulative cost from root
        self.children = []
        self.valid = True

        if parent is not None:
            self.update_parent(parent, parent.cost + self._distance(parent.position, position))

    def _distance(self, a, b):
        from math import dist
        return dist(a, b)

    def add_child(self, child):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is new_parent and abs(self.cost - new_cost) < 1e-12:
            return
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        if new_parent and self not in new_parent.children:
            new_parent.children.append(self)
        # Recursively update descendants' costs
        for child in self.children:
            child.update_parent(self, self.cost + self._distance(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float = 5.0, max_time_sec: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.max_time_sec = max_time_sec
        self.min_neighbor_radius = 8.0
        self.max_neighbor_radius = 20.0
        self.goal_sample_prob = 0.2

    def plan(self, map) -> 'PlannerResult':
        import time
        import math
        import random

        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            scale = self.step_size / d
            return tuple(from_pos[d] + scale * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(p):
            return in_bounds(p) and not self._is_in_obstacle(p, obstacles, is_3d)

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def neighbor_radius(n_nodes):
            if n_nodes <= 1:
                return self.max_neighbor_radius
            r = self.max_neighbor_radius * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.min_neighbor_radius, min(self.max_neighbor_radius, r))

        def near_nodes(tree, pos, radius):
            radius_sq = radius * radius
            return [node for node in tree if dist(node.position, pos) ** 2 <= radius_sq]

        def uniform_sample():
            while True:
                sample = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                if collision_free_node(sample):
                    return sample

        def rotation_matrix(direction):
            # Generates rotation matrix aligning unit vector along x-axis to 'direction'
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                c, s = math.cos(angle), math.sin(angle)
                return [[c, -s], [s, c]]
            else:
                vx, vy, vz = direction
                # Cross product unit x-axis (1,0,0) and direction
                kx, ky, kz = 0.0, -vz, vy
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1, 0, 0], [0, 1, 0], [0, 0, 1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_th = vx
                sin_th = math.sqrt(max(0.0, 1 - cos_th * cos_th))

                K = [[0, -kz, ky],
                     [kz, 0, -kx],
                     [-ky, kx, 0]]
                I = [[1, 0, 0], [0, 1, 0], [0, 0, 1]]

                def mat_add(A, B):
                    return [[A[i][j] + B[i][j] for j in range(3)] for i in range(3)]

                def mat_scale(A, s):
                    return [[A[i][j] * s for j in range(3)] for i in range(3)]

                def mat_mul(A, B):
                    return [[sum(A[i][k] * B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_th)), mat_scale(K2, 1 - cos_th))

        def unit_ball_sample(n):
            while True:
                vec = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in vec)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in vec)

        def informed_sample(c_best, c_min):
            # If no best cost or smaller than theoretic min, revert to uniform sample
            if c_best == math.inf or c_best < c_min - 1e-10:
                return uniform_sample()

            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            dx = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(dx)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(0.0, c_best*c_best - c_min*c_min)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(0.0, c_best*c_best - c_min*c_min)) / 2.0
                radii = [a1] + [a_minor]*(dim-1)

            n = dim
            while True:
                dir_sample = unit_ball_sample(n)
                radius_samp = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_samp * d for d in dir_sample)
                sample_ellipsoid = tuple(sample_unit[i] * radii[i] for i in range(n))
                if dim == 2:
                    x_rot = R[0][0] * sample_ellipsoid[0] + R[0][1] * sample_ellipsoid[1]
                    y_rot = R[1][0] * sample_ellipsoid[0] + R[1][1] * sample_ellipsoid[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = sum(R[0][i] * sample_ellipsoid[i] for i in range(n))
                    y_rot = sum(R[1][i] * sample_ellipsoid[i] for i in range(n))
                    z_rot = sum(R[2][i] * sample_ellipsoid[i] for i in range(n))
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                clipped = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(clipped):
                    return clipped

        def extend_and_rewire(tree, sample):
            nearest = min(tree, key=lambda n: dist(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            radius = neighbor_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest

            # Better parent search
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_via_nbr = nbr.cost + dist(nbr.position, new_pos)
                if cost_via_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_via_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            # Rewiring neighbors if beneficial
            for nbr in neighbors:
                if nbr is best_parent:
                    continue
                new_cost = new_node.cost + dist(new_node.position, nbr.position)
                if new_cost < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, new_cost)

            return new_node

        def incremental_connect(tree, target_node):
            curr_node = min(tree, key=lambda n: dist(n.position, target_node.position))
            while True:
                new_pos = steer(curr_node.position, target_node.position)
                if not collision_free_node(new_pos) or not collision_free_edge(curr_node.position, new_pos):
                    return None

                radius = neighbor_radius(len(tree) + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = curr_node.cost + dist(curr_node.position, new_pos)
                best_parent = curr_node
                for nbr in neighbors:
                    if nbr is curr_node:
                        continue
                    cost_thru = nbr.cost + dist(nbr.position, new_pos)
                    if cost_thru < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_thru
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_via_new = new_node.cost + dist(new_node.position, nbr.position)
                    if cost_via_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_via_new)

                if dist(new_node.position, target_node.position) <= self.step_size:
                    if collision_free_edge(new_node.position, target_node.position):
                        return new_node
                    return None
                curr_node = new_node

        def path_smoothing(path, iterations=100):
            if len(path) < 3:
                return path[:]
            for _ in range(iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) -1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i+1] + path[j:]
            return path

        start_tree = [Node(start_pos, cost=0.0)]
        goal_tree = [Node(goal_pos, cost=0.0)]

        best_path = []
        best_cost = math.inf
        informed_sampling = False
        c_min = dist(start_pos, goal_pos)
        grow_start_tree = True

        start_time = time.perf_counter()

        for iter_count in range(self.max_iter):
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_time_sec:
                break

            sample = informed_sample(best_cost, c_min) if informed_sampling else (
                goal_pos if random.random() < self.goal_sample_prob else uniform_sample()
            )

            if grow_start_tree:
                new_node = extend_and_rewire(start_tree, sample)
                if new_node is None:
                    grow_start_tree = not grow_start_tree
                    continue
                connect_node = incremental_connect(goal_tree, new_node)
                if connect_node:
                    path_start = new_node.path_from_root()
                    path_goal = connect_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined = path_start + path_goal[::-1]
                    combined_cost = new_node.cost + connect_node.cost + dist(new_node.position, connect_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined
                        informed_sampling = True
                        smoothed_path = path_smoothing(best_path, iterations=200)
                        # Early return with best solution
                        nodes_all = start_tree + goal_tree
                        edges_all = []
                        for node in nodes_all:
                            for child in node.children:
                                edges_all.append((node, child))
                        return PlannerResult(True, smoothed_path, nodes_all, edges_all)
            else:
                new_node = extend_and_rewire(goal_tree, sample)
                if new_node is None:
                    grow_start_tree = not grow_start_tree
                    continue
                connect_node = incremental_connect(start_tree, new_node)
                if connect_node:
                    path_goal = new_node.path_from_root()
                    path_start = connect_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined = path_start + path_goal[::-1]
                    combined_cost = new_node.cost + connect_node.cost + dist(new_node.position, connect_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined
                        informed_sampling = True
                        smoothed_path = path_smoothing(best_path, iterations=200)
                        # Early return with best solution
                        nodes_all = start_tree + goal_tree
                        edges_all = []
                        for node in nodes_all:
                            for child in node.children:
                                edges_all.append((node, child))
                        return PlannerResult(True, smoothed_path, nodes_all, edges_all)

            grow_start_tree = not grow_start_tree

        # Timeout or max iterations reached: return best path found
        if best_path:
            success_state = True
            extracted_path = path_smoothing(best_path, iterations=200)
        else:
            success_state = False
            extracted_path = []

        nodes_all = start_tree + goal_tree
        edges_all = []
        for node in nodes_all:
            for child in node.children:
                edges_all.append((node, child))

        return PlannerResult(success_state, extracted_path, nodes_all, edges_all)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent=None, cost=0.0):
        self.position = position
        self.parent = None
        self.cost = cost
        self.children: List[Node] = []
        self.valid = True

        if parent:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        dist_func = math.dist
        for c in self.children:
            c.update_parent(self, self.cost + dist_func(self.position, c.position))

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 goal_sample_rate: float=0.15,
                 neighbor_radius_min: float=8.0,
                 neighbor_radius_max: float=25.0,
                 max_planning_time: float=30.0,
                 smoothing_trials: int=100):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time
        self.smoothing_trials = smoothing_trials

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist_func(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def clamp_to_bounds(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            resolution = self.step_size / 5.0
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution)

        def adaptive_neighbor_radius(n_nodes: int):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 35.0
            r = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist_func(node.position, pos) ** 2) <= r_sq]

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0,1 - cos_theta*cos_theta))
                K = [[0, -kz, ky],[kz,0,-kx],[-ky,kx,0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A, B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A, s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A, B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1-cos_theta))

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-14 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x/norm for x in pt)

        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_free()
            center = tuple(0.5*(start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1] + R[0][2]*sample_scaled[2]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1] + R[1][2]*sample_scaled[2]
                    z_rot = R[2][0]*sample_scaled[0] + R[2][1]*sample_scaled[1] + R[2][2]*sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = clamp_to_bounds(sample_pt)
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: List[Node], sample: Tuple[float, ...]) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, sample))
            new_pos = steer(nearest.position, sample)
            new_pos = clamp_to_bounds(new_pos)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)

            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: List[Node], target_pos: Tuple[float, ...]) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                new_pos = clamp_to_bounds(new_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                    if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through_nbr
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                if dist_func(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_smoothing(path: List[Tuple[float, ...]], trials: int):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        all_nodes = tree_start + tree_goal
        all_edges: List[Tuple[Node, Node]] = []

        grow_start = True
        iteration = 0

        # Best solution tracking
        best_cost = math.inf
        best_path: List[Tuple[float, ...]] = []
        informed_sampling = False

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                break

            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start else start_pos
                else:
                    sample_pt = sample_free()

            if grow_start:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connected_node = incremental_connect(tree_goal, new_node.position)
                if connected_node:
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    all_nodes = tree_start + tree_goal
                    all_edges.clear()
                    for node in all_nodes:
                        for c in node.children:
                            all_edges.append((node, c))

                    smoothed = path_smoothing(best_path, self.smoothing_trials)
                    return PlannerResult(True, smoothed, all_nodes, all_edges)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connected_node = incremental_connect(tree_start, new_node.position)
                if connected_node:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    all_nodes = tree_start + tree_goal
                    all_edges.clear()
                    for node in all_nodes:
                        for c in node.children:
                            all_edges.append((node, c))

                    smoothed = path_smoothing(best_path, self.smoothing_trials)
                    return PlannerResult(True, smoothed, all_nodes, all_edges)

            grow_start = not grow_start

        success_state = (len(best_path) > 0)
        smoothed = path_smoothing(best_path, self.smoothing_trials) if success_state else []

        all_nodes = tree_start + tree_goal
        all_edges.clear()
        for node in all_nodes:
            for c in node.children:
                all_edges.append((node, c))

        return PlannerResult(success_state, smoothed, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        if distance < 1e-10:
            return self._is_in_obstacle(from_pos, obstacles, is_3d)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * factor for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position                # Tuple[float, ...]
        self.parent = None                      # Parent Node or None
        self.cost = cost                        # Cumulative cost from root
        self.children = []                      # Children Nodes list
        self.valid = True                       # Collision validity flag

        if parent is not None:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        dist = math.dist
        for child in self.children:
            child.update_parent(self, self.cost + dist(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float=5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = 0.15
        self.neighbor_radius_min = 5.0
        self.neighbor_radius_max = 30.0
        self.max_planning_time = 30.0
        self.smoothing_trials = 150

    def plan(self, map) -> 'PlannerResult':
        import random
        import time

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist = math.dist

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def clamp(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            scale = self.step_size / d
            return tuple(from_pos[d] + scale*(to_pos[d]-from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and not self._is_in_obstacle(pos, obstacles, is_3d)

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma = self.step_size * 30.0
            r = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree, pos, radius):
            r_sq = radius*radius
            return [node for node in tree if (dist(node.position, pos)**2) <= r_sq]

        def unit_ball_sample(n):
            while True:
                sample = [random.uniform(-1,1) for _ in range(n)]
                norm_sq = sum(x*x for x in sample)
                if 1e-12 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in sample)

        rotation_mat_cache = None
        c_min = dist(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a],[sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                norm_v = math.sqrt(vx*vx + vy*vy + vz*vz)
                if norm_v < 1e-12:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                ux, uy, uz = vx/norm_v, vy/norm_v, vz/norm_v
                if abs(ux) < 1e-6 and abs(uy) < 1e-6:
                    vx_ = 0.0; vy_ = 1.0; vz_ = 0.0
                else:
                    vx_ = -uy; vy_ = ux; vz_ = 0.0
                norm_ = math.sqrt(vx_*vx_ + vy_*vy_ + vz_*vz_)
                vx_ /= norm_; vy_ /= norm_; vz_ /= norm_
                wx = uy*vz_ - uz*vy_
                wy = uz*vx_ - ux*vz_
                wz = ux*vy_ - uy*vx_
                return [[ux, vx_, wx],[uy, vy_, wy],[uz, vz_, wz]]

        def informed_sample(c_best):
            nonlocal rotation_mat_cache
            if c_best == math.inf or c_best < c_min - 1e-12:
                while True:
                    pt = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                    if collision_free_node(pt):
                        return pt

            center = tuple(0.5*(start_pos[d]+goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d]-start_pos[d])/c_min for d in range(dim))
            if rotation_mat_cache is None:
                rotation_mat_cache = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min,0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min,0)) / 2.0
                radii = [a1, a_minor, a_minor]

            while True:
                dir_sample = unit_ball_sample(dim)
                radius_sample = random.random() ** (1.0 / dim)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(dim))

                if dim == 2:
                    x_rot = (rotation_mat_cache[0][0]*sample_scaled[0] +
                             rotation_mat_cache[0][1]*sample_scaled[1])
                    y_rot = (rotation_mat_cache[1][0]*sample_scaled[0] +
                             rotation_mat_cache[1][1]*sample_scaled[1])
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = (rotation_mat_cache[0][0]*sample_scaled[0] + 
                             rotation_mat_cache[0][1]*sample_scaled[1] + 
                             rotation_mat_cache[0][2]*sample_scaled[2])
                    y_rot = (rotation_mat_cache[1][0]*sample_scaled[0] + 
                             rotation_mat_cache[1][1]*sample_scaled[1] + 
                             rotation_mat_cache[1][2]*sample_scaled[2])
                    z_rot = (rotation_mat_cache[2][0]*sample_scaled[0] + 
                             rotation_mat_cache[2][1]*sample_scaled[1] + 
                             rotation_mat_cache[2][2]*sample_scaled[2])
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(dim))
                sample_pt = clamp(sample_pt)
                if collision_free_node(sample_pt):
                    return sample_pt

        def rewire(tree, new_node, neighbors):
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_through_new + 1e-12 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

        def extend(tree, sample_pt):
            nearest = min(tree, key=lambda n: dist(n.position, sample_pt))
            new_pos = steer(nearest.position, sample_pt)
            new_pos = clamp(new_pos)

            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            radius = adaptive_neighbor_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            rewire(tree, new_node, neighbors)
            return new_node

        def incremental_connect(new_node, other_tree):
            if not other_tree:
                return None
            current = min(other_tree, key=lambda n: dist(n.position, new_node.position))
            while True:
                new_pos = steer(current.position, new_node.position)
                new_pos = clamp(new_pos)

                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                radius = adaptive_neighbor_radius(len(other_tree) + 1)
                neighbors = near_nodes(other_tree, new_pos, radius)

                min_cost = current.cost + dist(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                    if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = tentative_cost
                        best_parent = nbr

                conn_node = Node(new_pos)
                best_parent.add_child(conn_node)
                conn_node.cost = min_cost
                other_tree.append(conn_node)

                rewire(other_tree, conn_node, neighbors)

                if dist(conn_node.position, new_node.position) <= self.step_size:
                    if collision_free_edge(conn_node.position, new_node.position):
                        return conn_node
                    return None

                current = conn_node

        def path_shortcut(path, trials=None):
            if trials is None:
                trials = self.smoothing_trials
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path)-3)
                j = random.randint(i+2, len(path)-1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i+1] + path[j:]
            return path

        t_start = time.perf_counter()

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        all_nodes = tree_start + tree_goal
        edges = []

        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - t_start
            if elapsed > self.max_planning_time:
                success_state = best_path != []
                final_path = path_shortcut(best_path) if success_state else []
                all_nodes = tree_start + tree_goal
                edges.clear()
                for n in all_nodes:
                    for c in n.children:
                        edges.append((n, c))
                return PlannerResult(success_state, final_path, all_nodes, edges)

            if informed_sampling:
                sample = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if grow_start else start_pos
                else:
                    for _ in range(20):
                        candidate = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                        if collision_free_node(candidate):
                            sample = candidate
                            break
                    else:
                        grow_start = not grow_start
                        continue

            active_tree = tree_start if grow_start else tree_goal
            other_tree = tree_goal if grow_start else tree_start

            new_node = extend(active_tree, sample)
            if new_node is None:
                grow_start = not grow_start
                continue
            all_nodes.append(new_node)

            connected_node = incremental_connect(new_node, other_tree)
            if connected_node is not None:
                path_start = new_node.path_from_root()
                path_goal = connected_node.path_from_root()
                if path_goal and path_goal[0] == new_node.position:
                    path_goal = path_goal[1:]
                combined_path = path_start + path_goal[::-1]
                total_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)

                if total_cost + 1e-12 < best_cost:
                    best_cost = total_cost
                    best_path = combined_path
                    informed_sampling = True

                # Continue search for better solutions, but update best so far
                # Apply smoothing before returning to improve smoothness
                smoothed = path_shortcut(best_path)

                all_nodes = tree_start + tree_goal
                edges.clear()
                for n in all_nodes:
                    for c in n.children:
                        edges.append((n, c))

                # Return early on first improved path (anytime behavior)
                return PlannerResult(True, smoothed, all_nodes, edges)

            grow_start = not grow_start

        # End on iteration exhaustion
        success_state = best_path != []
        final_path = path_shortcut(best_path) if success_state else []
        all_nodes = tree_start + tree_goal
        edges.clear()
        for n in all_nodes:
            for c in n.children:
                edges.append((n, c))

        return PlannerResult(success_state, final_path, all_nodes, edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps+1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position
        self.parent = None
        self.cost = cost
        self.children = []
        self.valid = True
        if parent:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is new_parent and abs(self.cost - new_cost) < 1e-12:
            return
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        if new_parent and self not in new_parent.children:
            new_parent.children.append(self)
        dist_func = math.dist
        for child in self.children:
            child.update_parent(self, self.cost + dist_func(self.position, child.position))

    def path_from_root(self):
        node = self
        path = []
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.2,
                 neighbor_radius_min: float = 5.0,
                 neighbor_radius_max: float = 30.0,
                 max_planning_time: float = 30.0,
                 smoothing_trials: int = 120):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time
        self.smoothing_trials = smoothing_trials

    def plan(self, map) -> 'PlannerResult':
        import time
        import random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        t_start = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def clamp(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def steer(src, tgt):
            d = dist_func(src, tgt)
            if d <= self.step_size:
                return tgt
            factor = self.step_size / d
            return tuple(src[d] + factor * (tgt[d] - src[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and not self._is_in_obstacle(pos, obstacles, is_3d)

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma = self.step_size * 30.0
            r = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree, pos, radius):
            r_sq = radius * radius
            return [n for n in tree if (dist_func(n.position, pos) ** 2) <= r_sq]

        def unit_ball_sample(n):
            while True:
                sample = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in sample)
                if 1e-12 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in sample)

        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False
        rotation_mat_cache = None

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                c, s = math.cos(angle), math.sin(angle)
                return [[c, -s], [s, c]]
            else:
                vx, vy, vz = direction
                norm_v = math.sqrt(vx*vx + vy*vy + vz*vz)
                if norm_v < 1e-12:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                ux, uy, uz = vx/norm_v, vy/norm_v, vz/norm_v
                if abs(ux) < 1e-6 and abs(uy) < 1e-6:
                    vx_ = 0.0; vy_ = 1.0; vz_ = 0.0
                else:
                    vx_ = -uy; vy_ = ux; vz_ = 0.0
                norm_ = math.sqrt(vx_*vx_ + vy_*vy_ + vz_*vz_)
                vx_ /= norm_
                vy_ /= norm_
                vz_ /= norm_
                wx = uy*vz_ - uz*vy_
                wy = uz*vx_ - ux*vz_
                wz = ux*vy_ - uy*vx_
                return [[ux, vx_, wx], [uy, vy_, wy], [uz, vz_, wz]]

        def informed_sample(c_best):
            nonlocal rotation_mat_cache
            if c_best == math.inf or c_best < c_min - 1e-12:
                while True:
                    sample_pt = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                    if collision_free_node(sample_pt):
                        return sample_pt

            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            if rotation_mat_cache is None:
                rotation_mat_cache = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            while True:
                dir_sample = unit_ball_sample(dim)
                radius_sample = random.random() ** (1.0 / dim)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(dim))

                if dim == 2:
                    x_rot = (rotation_mat_cache[0][0] * sample_scaled[0] +
                             rotation_mat_cache[0][1] * sample_scaled[1])
                    y_rot = (rotation_mat_cache[1][0] * sample_scaled[0] +
                             rotation_mat_cache[1][1] * sample_scaled[1])
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = (rotation_mat_cache[0][0] * sample_scaled[0] +
                             rotation_mat_cache[0][1] * sample_scaled[1] +
                             rotation_mat_cache[0][2] * sample_scaled[2])
                    y_rot = (rotation_mat_cache[1][0] * sample_scaled[0] +
                             rotation_mat_cache[1][1] * sample_scaled[1] +
                             rotation_mat_cache[1][2] * sample_scaled[2])
                    z_rot = (rotation_mat_cache[2][0] * sample_scaled[0] +
                             rotation_mat_cache[2][1] * sample_scaled[1] +
                             rotation_mat_cache[2][2] * sample_scaled[2])
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(dim))
                sample_pt = clamp(sample_pt)

                if collision_free_node(sample_pt):
                    return sample_pt

        def rewire(tree, new_node, neighbors):
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new + 1e-12 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

        def extend(tree, sample):
            nearest = min(tree, key=lambda n: dist_func(n.position, sample))
            new_pos = steer(nearest.position, sample)
            new_pos = clamp(new_pos)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            radius = adaptive_neighbor_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                tentative_cost = nbr.cost + dist_func(nbr.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            rewire(tree, new_node, neighbors)
            return new_node

        def incremental_connect(new_node, other_tree):
            if not other_tree:
                return None
            current = min(other_tree, key=lambda n: dist_func(n.position, new_node.position))
            while True:
                new_pos = steer(current.position, new_node.position)
                new_pos = clamp(new_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                radius = adaptive_neighbor_radius(len(other_tree) + 1)
                neighbors = near_nodes(other_tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    tentative_cost = nbr.cost + dist_func(nbr.position, new_pos)
                    if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = tentative_cost
                        best_parent = nbr

                conn_node = Node(new_pos)
                best_parent.add_child(conn_node)
                conn_node.cost = min_cost
                other_tree.append(conn_node)

                rewire(other_tree, conn_node, neighbors)

                if dist_func(conn_node.position, new_node.position) <= self.step_size:
                    if collision_free_edge(conn_node.position, new_node.position):
                        return conn_node
                    return None

                current = conn_node

        def path_shortcut(path, trials=self.smoothing_trials):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        best_path_found = []
        best_cost_found = math.inf
        informed_sampling_active = False
        grow_start_tree = True

        iteration = 0
        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - t_start
            if elapsed > self.max_planning_time:
                if best_path_found:
                    best_path_found = path_shortcut(best_path_found)
                    success = True
                else:
                    success = False
                    best_path_found = []
                all_nodes = tree_start + tree_goal
                all_edges = []
                for n in all_nodes:
                    for c in n.children:
                        all_edges.append((n, c))
                return PlannerResult(success, best_path_found, all_nodes, all_edges)

            sample_pt = informed_sample(best_cost_found) if informed_sampling_active else \
                (goal_pos if random.random() < self.goal_sample_rate and grow_start_tree else start_pos if random.random() < self.goal_sample_rate else
                 tuple(random.uniform(0, bounds[d]) for d in range(dim)))

            active_tree = tree_start if grow_start_tree else tree_goal
            other_tree = tree_goal if grow_start_tree else tree_start

            new_node = extend(active_tree, sample_pt)
            if new_node is None:
                grow_start_tree = not grow_start_tree
                continue

            connected_node = incremental_connect(new_node, other_tree)
            if connected_node:
                path_from_start = new_node.path_from_root()
                path_from_goal = connected_node.path_from_root()
                # Avoid duplicate connection node
                if path_from_goal and path_from_goal[0] == new_node.position:
                    path_from_goal = path_from_goal[1:]
                combined_path = path_from_start + path_from_goal[::-1]

                total_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                if total_cost + 1e-12 < best_cost_found:
                    best_cost_found = total_cost
                    best_path_found = combined_path
                    informed_sampling_active = True

                    smoothed_path = path_shortcut(best_path_found)
                    all_nodes = tree_start + tree_goal
                    all_edges = []
                    for n in all_nodes:
                        for c in n.children:
                            all_edges.append((n, c))
                    return PlannerResult(True, smoothed_path, all_nodes, all_edges)

            grow_start_tree = not grow_start_tree

        # Max iterations reached without timeout
        if best_path_found:
            best_path_found = path_shortcut(best_path_found)
            success = True
        else:
            success = False
            best_path_found = []
        all_nodes = tree_start + tree_goal
        all_edges = []
        for n in all_nodes:
            for c in n.children:
                all_edges.append((n, c))

        return PlannerResult(success, best_path_found, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position            # Tuple[float, ...] (2D or 3D)
        self.parent = None                  # Node or None
        self.cost = cost                    # Cost from root to this node
        self.children = []                  # List of child nodes
        self.valid = True                   # Validity flag for collision

        if parent is not None:
            self.update_parent(parent, parent.cost + self._distance(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        for child in self.children:
            child.update_parent(self, self.cost + self._distance(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]

    @staticmethod
    def _distance(p1, p2):
        return math.dist(p1, p2)
class Planner:
    def __init__(self, max_iter: int = 7000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.15,
                 neighbor_radius_min: float = 10.0,
                 neighbor_radius_max: float = 30.0,
                 max_planning_time: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> 'PlannerResult':
        import time
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles
        dim = len(bounds)
        is_3d = (dim == 3)
        dist = math.dist

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            resolution = self.step_size / 5.0
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution)

        def adaptive_neighbor_radius(n_nodes: int, iter_num: int):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 30.0
            base_radius = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            iter_factor = min(1.0, iter_num / self.max_iter)
            radius = base_radius * (1.0 - 0.6 * iter_factor)
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, radius))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist(node.position, pos) ** 2) <= r_sq]

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in pt)

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                c, s = math.cos(angle), math.sin(angle)
                return [[c, -s], [s, c]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1, 0, 0],
                            [0, 1, 0],
                            [0, 0, 1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0.0, 1 - cos_theta * cos_theta))
                K = [[0, -kz, ky], [kz, 0, -kx], [-ky, kx, 0]]
                I = [[1, 0, 0], [0, 1, 0], [0, 0, 1]]

                def mat_add(A, B):
                    return [[A[i][j] + B[i][j] for j in range(3)] for i in range(3)]

                def mat_scale(A, s):
                    return [[A[i][j] * s for j in range(3)] for i in range(3)]

                def mat_mul(A, B):
                    cols = len(B[0])
                    return [[sum(A[i][k] * B[k][j] for k in range(len(B))) for j in range(cols)] for i in range(len(A))]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1 - cos_theta))

        c_min = dist(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def sample_uniform():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_uniform()
            center = tuple(0.5 * (start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best * c_best - c_min * c_min, 0.0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                dir_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0] * sample_scaled[0] + R[0][1] * sample_scaled[1] + R[0][2] * sample_scaled[2]
                    y_rot = R[1][0] * sample_scaled[0] + R[1][1] * sample_scaled[1] + R[1][2] * sample_scaled[2]
                    z_rot = R[2][0] * sample_scaled[0] + R[2][1] * sample_scaled[1] + R[2][2] * sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                clamped = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(clamped):
                    return clamped

        def extend_and_rewire(tree: list, sample: tuple, iter_num: int):
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1, iter_num)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through = nbr.cost + dist(nbr.position, new_pos)
                if cost_through < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: list, target_pos: tuple, iter_num: int):
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1, iter_num)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through = nbr.cost + dist(nbr.position, new_pos)
                    if cost_through < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                dist_to_target = dist(new_node.position, target_pos)
                if dist_to_target <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_smoothing(path: list, trials: int = 150):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        best_path = []
        best_cost = math.inf
        informed_sampling = False
        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                break

            sample_pt = informed_sample(best_cost) if informed_sampling else (
                goal_pos if (random.random() < self.goal_sample_rate and grow_start) else
                (start_pos if (random.random() < self.goal_sample_rate and not grow_start) else sample_uniform())
            )

            if grow_start:
                new_node = extend_and_rewire(tree_start, sample_pt, iteration)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connect_node = incremental_connect(tree_goal, new_node.position, iteration)
                if connect_node:
                    p_start = new_node.path_from_root()
                    p_goal = connect_node.path_from_root()
                    if p_goal and p_goal[0] == new_node.position:
                        p_goal = p_goal[1:]
                    combined = p_start + p_goal[::-1]
                    cost = new_node.cost + connect_node.cost + dist(new_node.position, connect_node.position)
                    if cost < best_cost:
                        best_cost = cost
                        best_path = combined
                        informed_sampling = True

                    smoothed = path_smoothing(best_path)
                    nodes_all = tree_start + tree_goal
                    edges_all = []
                    for node in nodes_all:
                        for c in node.children:
                            edges_all.append((node, c))
                    return PlannerResult(True, smoothed, nodes_all, edges_all)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt, iteration)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connect_node = incremental_connect(tree_start, new_node.position, iteration)
                if connect_node:
                    p_goal = new_node.path_from_root()
                    p_start = connect_node.path_from_root()
                    if p_start and p_start[0] == new_node.position:
                        p_start = p_start[1:]
                    combined = p_start + p_goal[::-1]
                    cost = new_node.cost + connect_node.cost + dist(new_node.position, connect_node.position)
                    if cost < best_cost:
                        best_cost = cost
                        best_path = combined
                        informed_sampling = True

                    smoothed = path_smoothing(best_path)
                    nodes_all = tree_start + tree_goal
                    edges_all = []
                    for node in nodes_all:
                        for c in node.children:
                            edges_all.append((node, c))
                    return PlannerResult(True, smoothed, nodes_all, edges_all)

            grow_start = not grow_start

        success = (len(best_path) > 0)
        smoothed = path_smoothing(best_path) if success else []
        nodes_all = tree_start + tree_goal
        edges_all = []
        for node in nodes_all:
            for c in node.children:
                edges_all.append((node, c))

        return PlannerResult(success, smoothed, nodes_all, edges_all)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent: 'Node' = None, cost: float = 0.0):
        self.position = position
        self.parent = None
        self.cost = cost
        self.children = []
        self.valid = True
        if parent is not None:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        if new_parent is not None:
            new_parent.add_child(self)
        for child in self.children:
            dist_child = math.dist(self.position, child.position)
            child.update_parent(self, self.cost + dist_child)

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 100000, step_size: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.max_time_sec = 30.0
        self.goal_sample_rate = 0.15
        self.min_neighbor_radius_factor = 1.5
        self.max_neighbor_radius_factor = 15.0
        self.smooth_trials = 120

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_position = map.start
        goal_position = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def clamp(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            scale = self.step_size / d
            return tuple(from_pos[d] + scale * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_radius(n_nodes: int) -> float:
            if n_nodes <= 1:
                return self.step_size * self.max_neighbor_radius_factor
            gamma = self.step_size * (self.max_neighbor_radius_factor)
            r = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            r = max(self.step_size * self.min_neighbor_radius_factor, min(self.step_size * self.max_neighbor_radius_factor, r))
            return r

        def unit_ball_sample(n: int) -> Tuple[float, ...]:
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in pt)
                if 1e-12 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in pt)

        c_min = dist(start_position, goal_position)
        best_cost = math.inf
        best_path: List[Tuple[float, ...]] = []
        rotation_mat = None

        def rotation_matrix(direction: Tuple[float, ...]):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                norm_v = math.sqrt(vx * vx + vy * vy + vz * vz)
                if norm_v < 1e-12:
                    return [[1, 0, 0], [0, 1, 0], [0, 0, 1]]
                ux, uy, uz = vx / norm_v, vy / norm_v, vz / norm_v
                # Orthonormal basis
                if abs(ux) < 1e-6 and abs(uy) < 1e-6:
                    vx_ = 0
                    vy_ = 1
                    vz_ = 0
                else:
                    vx_ = -uy
                    vy_ = ux
                    vz_ = 0
                norm_ = math.sqrt(vx_ * vx_ + vy_ * vy_ + vz_ * vz_)
                vx_ /= norm_
                vy_ /= norm_
                vz_ /= norm_
                wx = uy * vz_ - uz * vy_
                wy = uz * vx_ - ux * vz_
                wz = ux * vy_ - uy * vx_
                return [[ux, vx_, wx], [uy, vy_, wy], [uz, vz_, wz]]

        def informed_sample(c_best: float) -> Tuple[float, ...]:
            nonlocal rotation_mat

            if c_best == math.inf or c_best < c_min - 1e-12:
                while True:
                    sample_pt = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                    if collision_free_node(sample_pt):
                        return sample_pt

            center = tuple((start_position[d] + goal_position[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_position[d] - start_position[d]) / c_min for d in range(dim))
            if rotation_mat is None:
                rotation_mat = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best * c_best - c_min * c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                dir_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = rotation_mat[0][0] * sample_scaled[0] + rotation_mat[0][1] * sample_scaled[1]
                    y_rot = rotation_mat[1][0] * sample_scaled[0] + rotation_mat[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = (rotation_mat[0][0] * sample_scaled[0] + rotation_mat[0][1] * sample_scaled[1] + rotation_mat[0][2] * sample_scaled[2])
                    y_rot = (rotation_mat[1][0] * sample_scaled[0] + rotation_mat[1][1] * sample_scaled[1] + rotation_mat[1][2] * sample_scaled[2])
                    z_rot = (rotation_mat[2][0] * sample_scaled[0] + rotation_mat[2][1] * sample_scaled[1] + rotation_mat[2][2] * sample_scaled[2])
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = clamp(sample_pt)
                if collision_free_node(sample_pt):
                    return sample_pt

        def near_nodes(tree: List[Node], position: Tuple[float, ...], radius: float) -> List[Node]:
            r_sq = radius * radius
            return [n for n in tree if (math.dist(n.position, position) ** 2) <= r_sq]

        def rewire(tree: List[Node], new_node: Node, neighbors: List[Node]):
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_through_new + 1e-12 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

        def extend(tree: List[Node], sample_pt: Tuple[float, ...]) -> Node or None:
            nearest_node = min(tree, key=lambda n: dist(n.position, sample_pt))
            new_pos = steer(nearest_node.position, sample_pt)
            new_pos = clamp(new_pos)

            if not collision_free_node(new_pos) or not collision_free_edge(nearest_node.position, new_pos):
                return None

            n_nodes = len(tree) + 1
            radius = adaptive_radius(n_nodes)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest_node.cost + dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for nbr in neighbors:
                if nbr == nearest_node:
                    continue
                tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            rewire(tree, new_node, neighbors)
            return new_node

        def incremental_connect(new_node: Node, other_tree: List[Node]) -> Node or None:
            if not other_tree:
                return None
            curr = min(other_tree, key=lambda n: dist(n.position, new_node.position))
            while True:
                new_pos = steer(curr.position, new_node.position)
                new_pos = clamp(new_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(curr.position, new_pos):
                    return None

                radius = adaptive_radius(len(other_tree) + 1)
                neighbors = near_nodes(other_tree, new_pos, radius)

                min_cost = curr.cost + dist(curr.position, new_pos)
                best_parent = curr
                for nbr in neighbors:
                    if nbr == curr:
                        continue
                    tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                    if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = tentative_cost
                        best_parent = nbr

                conn_node = Node(new_pos)
                best_parent.add_child(conn_node)
                conn_node.cost = min_cost
                other_tree.append(conn_node)

                rewire(other_tree, conn_node, neighbors)

                if dist(conn_node.position, new_node.position) <= self.step_size:
                    if collision_free_edge(conn_node.position, new_node.position):
                        return conn_node
                    return None
                curr = conn_node

        def path_shortcut(path: List[Tuple[float, ...]], trials: int = None) -> List[Tuple[float, ...]]:
            if trials is None:
                trials = self.smooth_trials
            if len(path) < 3:
                return path[:]
            path_work = path[:]
            for _ in range(trials):
                if len(path_work) < 3:
                    break
                i = random.randint(0, len(path_work) - 3)
                j = random.randint(i + 2, len(path_work) - 1)
                if collision_free_edge(path_work[i], path_work[j]):
                    path_work = path_work[:i + 1] + path_work[j:]
            return path_work

        tree_start: List[Node] = [Node(start_position, None, 0.0)]
        tree_goal: List[Node] = [Node(goal_position, None, 0.0)]
        all_nodes = tree_start + tree_goal
        edges: List[Tuple[Node, Node]] = []

        grow_start = True
        informed_sampling = False

        start_time = time.perf_counter()
        iter_count = 0

        while iter_count < self.max_iter:
            iter_count += 1

            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_time_sec:
                # Timeout: return best found path
                if best_path:
                    smooth_path = path_shortcut(best_path, self.smooth_trials)
                    edges.clear()
                    all_nodes = tree_start + tree_goal
                    for n in all_nodes:
                        for c in n.children:
                            edges.append((n, c))
                    return PlannerResult(True, smooth_path, all_nodes, edges)
                else:
                    edges.clear()
                    all_nodes = tree_start + tree_goal
                    for n in all_nodes:
                        for c in n.children:
                            edges.append((n, c))
                    return PlannerResult(False, [], all_nodes, edges)

            sample_pt = None
            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_position if grow_start else start_position
                else:
                    while True:
                        cand = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                        if collision_free_node(cand):
                            sample_pt = cand
                            break

            active_tree = tree_start if grow_start else tree_goal
            opposite_tree = tree_goal if grow_start else tree_start

            new_node = extend(active_tree, sample_pt)
            if new_node is None:
                grow_start = not grow_start
                continue

            all_nodes.append(new_node)

            connected_node = incremental_connect(new_node, opposite_tree)
            if connected_node is not None:
                path_1 = new_node.path_from_root()
                path_2 = connected_node.path_from_root()

                # To avoid duplicated connection point:
                if path_2 and path_2[0] == new_node.position:
                    path_2 = path_2[1:]
                combined_path = path_1 + path_2[::-1]

                total_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)

                if total_cost + 1e-12 < best_cost:
                    best_cost = total_cost
                    best_path = combined_path
                    informed_sampling = True

                    edges.clear()
                    all_nodes = tree_start + tree_goal
                    for n in all_nodes:
                        for c in n.children:
                            edges.append((n, c))

                    smooth_path = path_shortcut(best_path, self.smooth_trials)
                    return PlannerResult(True, smooth_path, all_nodes, edges)

            grow_start = not grow_start

        # Max iterations reached without timeout
        if best_path:
            smooth_path = path_shortcut(best_path, self.smooth_trials)
            success_state = True
        else:
            smooth_path = []
            success_state = False

        all_nodes = tree_start + tree_goal
        edges.clear()
        for n in all_nodes:
            for c in n.children:
                edges.append((n, c))

        return PlannerResult(success_state, smooth_path, all_nodes, edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position            # Tuple[float, ...] (2D or 3D)
        self.parent = None                  # type: Node or None
        self.cost = cost                    # Cost from root node
        self.children = []                  # Children nodes
        self.valid = True                   # Validity flag for collision

        if parent is not None:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child_node):
        if child_node not in self.children:
            self.children.append(child_node)
            child_node.parent = self

    def remove_child(self, child_node):
        if child_node in self.children:
            self.children.remove(child_node)
            child_node.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        dist_func = math.dist
        for child in self.children:
            child.update_parent(self, self.cost + dist_func(self.position, child.position))

    def path_from_root(self):
        node = self
        path = []
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.15,
                 neighbor_radius_min: float = 5.0,
                 neighbor_radius_max: float = 30.0,
                 max_planning_time: float = 30.0,
                 smoothing_trials: int = 150):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time
        self.smoothing_trials = smoothing_trials

    def plan(self, map) -> 'PlannerResult':
        import time
        import random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        t_start = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def clamp(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist_func(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            scale = self.step_size / d
            return tuple(from_pos[d] + scale * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and not self._is_in_obstacle(pos, obstacles, is_3d)

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma = self.step_size * 30.0
            r = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree, pos, radius):
            r_sq = radius * radius
            return [n for n in tree if (dist_func(n.position, pos) ** 2) <= r_sq]

        def unit_ball_sample(n):
            while True:
                sample = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in sample)
                if 1e-12 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in sample)

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                norm_v = math.sqrt(vx*vx + vy*vy + vz*vz)
                if norm_v < 1e-12:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                ux, uy, uz = vx/norm_v, vy/norm_v, vz/norm_v
                if abs(ux) < 1e-6 and abs(uy) < 1e-6:
                    vx_ = 0.0; vy_ = 1.0; vz_ = 0.0
                else:
                    vx_ = -uy; vy_ = ux; vz_ = 0.0
                norm_ = math.sqrt(vx_*vx_ + vy_*vy_ + vz_*vz_)
                vx_ /= norm_; vy_ /= norm_; vz_ /= norm_
                wx = uy*vz_ - uz*vy_
                wy = uz*vx_ - ux*vz_
                wz = ux*vy_ - uy*vx_
                return [[ux, vx_, wx], [uy, vy_, wy], [uz, vz_, wz]]

        rotation_mat_cache = None

        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def informed_sample(c_best):
            nonlocal rotation_mat_cache

            if c_best == math.inf or c_best < c_min - 1e-12:
                while True:
                    sample_pt = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                    if collision_free_node(sample_pt):
                        return sample_pt

            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            if rotation_mat_cache is None:
                rotation_mat_cache = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                dir_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = (rotation_mat_cache[0][0] * sample_scaled[0] +
                             rotation_mat_cache[0][1] * sample_scaled[1])
                    y_rot = (rotation_mat_cache[1][0] * sample_scaled[0] +
                             rotation_mat_cache[1][1] * sample_scaled[1])
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = (rotation_mat_cache[0][0] * sample_scaled[0] +
                             rotation_mat_cache[0][1] * sample_scaled[1] +
                             rotation_mat_cache[0][2] * sample_scaled[2])
                    y_rot = (rotation_mat_cache[1][0] * sample_scaled[0] +
                             rotation_mat_cache[1][1] * sample_scaled[1] +
                             rotation_mat_cache[1][2] * sample_scaled[2])
                    z_rot = (rotation_mat_cache[2][0] * sample_scaled[0] +
                             rotation_mat_cache[2][1] * sample_scaled[1] +
                             rotation_mat_cache[2][2] * sample_scaled[2])
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = clamp(sample_pt)

                if collision_free_node(sample_pt):
                    return sample_pt

        def rewire(tree, new_node, neighbors):
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new + 1e-12 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

        def extend(tree, sample_pt):
            nearest = min(tree, key=lambda n: dist_func(n.position, sample_pt))
            new_pos = steer(nearest.position, sample_pt)
            new_pos = clamp(new_pos)

            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            radius = adaptive_neighbor_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                tentative_cost = nbr.cost + dist_func(nbr.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            rewire(tree, new_node, neighbors)
            return new_node

        def incremental_connect(new_node, other_tree):
            if not other_tree:
                return None
            current = min(other_tree, key=lambda n: dist_func(n.position, new_node.position))
            while True:
                new_pos = steer(current.position, new_node.position)
                new_pos = clamp(new_pos)

                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                radius = adaptive_neighbor_radius(len(other_tree) + 1)
                neighbors = near_nodes(other_tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    tentative_cost = nbr.cost + dist_func(nbr.position, new_pos)
                    if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = tentative_cost
                        best_parent = nbr

                conn_node = Node(new_pos)
                best_parent.add_child(conn_node)
                conn_node.cost = min_cost
                other_tree.append(conn_node)

                rewire(other_tree, conn_node, neighbors)

                if dist_func(conn_node.position, new_node.position) <= self.step_size:
                    if collision_free_edge(conn_node.position, new_node.position):
                        return conn_node
                    return None

                current = conn_node

        def path_shortcut(path, trials=self.smoothing_trials):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        all_nodes = tree_start + tree_goal
        all_edges = []

        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - t_start
            if elapsed > self.max_planning_time:
                # Time limit reached - return best found path so far
                if best_path:
                    smoothed = path_shortcut(best_path)
                    success_state = True
                else:
                    smoothed = []
                    success_state = False

                all_nodes = tree_start + tree_goal
                all_edges.clear()
                for n in all_nodes:
                    for c in n.children:
                        all_edges.append((n, c))
                return PlannerResult(success_state, smoothed, all_nodes, all_edges)

            # Sample point with goal bias or informed sampling after first solution
            if informed_sampling:
                sample = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if grow_start else start_pos
                else:
                    while True:
                        candidate = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                        if collision_free_node(candidate):
                            sample = candidate
                            break

            active_tree = tree_start if grow_start else tree_goal
            other_tree = tree_goal if grow_start else tree_start

            new_node = extend(active_tree, sample)
            if new_node is None:
                grow_start = not grow_start
                continue

            all_nodes.append(new_node)

            connected_node = incremental_connect(new_node, other_tree)
            if connected_node is not None:
                path_start = new_node.path_from_root()
                path_goal = connected_node.path_from_root()
                if path_goal and path_goal[0] == new_node.position:
                    path_goal = path_goal[1:]
                combined_path = path_start + path_goal[::-1]

                total_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                if total_cost + 1e-12 < best_cost:
                    best_cost = total_cost
                    best_path = combined_path
                    informed_sampling = True

                    smoothed_path = path_shortcut(best_path)

                    all_edges.clear()
                    all_nodes = tree_start + tree_goal
                    for n in all_nodes:
                        for c in n.children:
                            all_edges.append((n, c))

                    return PlannerResult(True, smoothed_path, all_nodes, all_edges)

            grow_start = not grow_start

        # Maximum iterations reached without time out
        if best_path:
            smoothed = path_shortcut(best_path)
            success_state = True
        else:
            smoothed = []
            success_state = False

        all_nodes = tree_start + tree_goal
        all_edges.clear()
        for n in all_nodes:
            for c in n.children:
                all_edges.append((n, c))

        return PlannerResult(success_state, smoothed, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * factor for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position            # Tuple[float, ...], 2D or 3D
        self.parent = None                  # Parent Node or None
        self.cost = cost                    # Cost from root to this node
        self.children = []                  # List of child Nodes
        self.valid = True                   # Collision validity

        if parent is not None:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        dist = math.dist
        # Recursively update children's costs
        for child in self.children:
            child.update_parent(self, self.cost + dist(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float = 5.0,
                 goal_sample_rate: float = 0.15,
                 neighbor_radius_min: float = 5.0,
                 neighbor_radius_max: float = 30.0,
                 max_planning_time: float = 30.0,
                 smoothing_trials: int = 150):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time
        self.smoothing_trials = smoothing_trials

    def plan(self, map) -> 'PlannerResult':
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist = math.dist

        t_start = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def clamp(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            scale = self.step_size / d
            return tuple(from_pos[d] + scale * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and not self._is_in_obstacle(pos, obstacles, is_3d)

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes: int) -> float:
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma = self.step_size * 30.0
            r = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree: list, pos: tuple, radius: float) -> list:
            r_sq = radius * radius
            return [n for n in tree if (dist(n.position, pos) ** 2) <= r_sq]

        def unit_ball_sample(n: int):
            while True:
                sample = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in sample)
                if 1e-12 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in sample)

        rotation_mat_cache = None

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                norm_v = math.sqrt(vx*vx + vy*vy + vz*vz)
                if norm_v < 1e-12:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                ux, uy, uz = vx/norm_v, vy/norm_v, vz/norm_v
                if abs(ux) < 1e-6 and abs(uy) < 1e-6:
                    vx_ = 0.0; vy_ = 1.0; vz_ = 0.0
                else:
                    vx_ = -uy; vy_ = ux; vz_ = 0.0
                norm_ = math.sqrt(vx_*vx_ + vy_*vy_ + vz_*vz_)
                vx_ /= norm_; vy_ /= norm_; vz_ /= norm_
                wx = uy*vz_ - uz*vy_
                wy = uz*vx_ - ux*vz_
                wz = ux*vy_ - uy*vx_
                return [[ux, vx_, wx], [uy, vy_, wy], [uz, vz_, wz]]

        c_min = dist(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def informed_sample(c_best):
            nonlocal rotation_mat_cache

            if c_best == math.inf or c_best < c_min - 1e-12:
                # uniform sampling until a valid node is found inside bounds and free
                while True:
                    pt = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                    if collision_free_node(pt):
                        return pt

            center = tuple(0.5 * (start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            if rotation_mat_cache is None:
                rotation_mat_cache = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                dir_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = rotation_mat_cache[0][0] * sample_scaled[0] + rotation_mat_cache[0][1] * sample_scaled[1]
                    y_rot = rotation_mat_cache[1][0] * sample_scaled[0] + rotation_mat_cache[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = (rotation_mat_cache[0][0] * sample_scaled[0] +
                             rotation_mat_cache[0][1] * sample_scaled[1] +
                             rotation_mat_cache[0][2] * sample_scaled[2])
                    y_rot = (rotation_mat_cache[1][0] * sample_scaled[0] +
                             rotation_mat_cache[1][1] * sample_scaled[1] +
                             rotation_mat_cache[1][2] * sample_scaled[2])
                    z_rot = (rotation_mat_cache[2][0] * sample_scaled[0] +
                             rotation_mat_cache[2][1] * sample_scaled[1] +
                             rotation_mat_cache[2][2] * sample_scaled[2])
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = clamp(sample_pt)
                if collision_free_node(sample_pt):
                    return sample_pt

        def rewire(tree: list, new_node: Node, neighbors: list):
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_through_new + 1e-12 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

        def extend(tree: list, sample_pt: tuple) -> Node or None:
            nearest = min(tree, key=lambda n: dist(n.position, sample_pt))
            new_pos = steer(nearest.position, sample_pt)
            new_pos = clamp(new_pos)

            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            radius = adaptive_neighbor_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            rewire(tree, new_node, neighbors)
            return new_node

        def incremental_connect(new_node: Node, other_tree: list) -> Node or None:
            if not other_tree:
                return None
            current = min(other_tree, key=lambda n: dist(n.position, new_node.position))
            while True:
                new_pos = steer(current.position, new_node.position)
                new_pos = clamp(new_pos)

                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                radius = adaptive_neighbor_radius(len(other_tree) + 1)
                neighbors = near_nodes(other_tree, new_pos, radius)

                min_cost = current.cost + dist(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                    if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = tentative_cost
                        best_parent = nbr

                conn_node = Node(new_pos)
                best_parent.add_child(conn_node)
                conn_node.cost = min_cost
                other_tree.append(conn_node)

                rewire(other_tree, conn_node, neighbors)

                if dist(conn_node.position, new_node.position) <= self.step_size:
                    if collision_free_edge(conn_node.position, new_node.position):
                        return conn_node
                    return None

                current = conn_node

        def path_shortcut(path: list, trials: int = None) -> list:
            if trials is None:
                trials = self.smoothing_trials
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        # Initialize trees
        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        all_nodes = tree_start + tree_goal
        all_edges = []

        grow_start = True
        iteration = 0
        success_state = False

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - t_start
            if elapsed > self.max_planning_time:
                # Time limit exceeded - return best path found so far
                if best_path:
                    smoothed = path_shortcut(best_path)
                    success_state = True
                else:
                    smoothed = []
                    success_state = False

                all_edges.clear()
                all_nodes = tree_start + tree_goal
                for n in all_nodes:
                    for c in n.children:
                        all_edges.append((n,c))

                return PlannerResult(success_state, smoothed, all_nodes, all_edges)

            # Sample point
            if informed_sampling:
                sample = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if grow_start else start_pos
                else:
                    while True:
                        candidate = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                        if collision_free_node(candidate):
                            sample = candidate
                            break

            active_tree = tree_start if grow_start else tree_goal
            other_tree = tree_goal if grow_start else tree_start

            new_node = extend(active_tree, sample)
            if new_node is None:
                grow_start = not grow_start
                continue

            all_nodes.append(new_node)

            connected_node = incremental_connect(new_node, other_tree)
            if connected_node is not None:
                path_start = new_node.path_from_root()
                path_goal = connected_node.path_from_root()
                if path_goal and path_goal[0] == new_node.position:
                    path_goal = path_goal[1:]
                combined_path = path_start + path_goal[::-1]

                total_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                if total_cost + 1e-12 < best_cost:
                    best_cost = total_cost
                    best_path = combined_path
                    informed_sampling = True

                    smoothed_path = path_shortcut(best_path)

                    all_edges.clear()
                    all_nodes = tree_start + tree_goal
                    for n in all_nodes:
                        for c in n.children:
                            all_edges.append((n, c))

                    # Early return on finding better solution
                    return PlannerResult(True, smoothed_path, all_nodes, all_edges)

            grow_start = not grow_start

        # Max iteration reached
        if best_path:
            smoothed = path_shortcut(best_path)
            success_state = True
        else:
            smoothed = []
            success_state = False

        all_nodes = tree_start + tree_goal
        all_edges.clear()
        for n in all_nodes:
            for c in n.children:
                all_edges.append((n, c))

        return PlannerResult(success_state, smoothed, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * factor for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position          # Tuple[float, ...]
        self.parent = None                # Node or None
        self.cost = cost                  # Path cost from root
        self.children = []                # List[Node]
        self.valid = True                 # Collision validity flag

        if parent:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        # Recursively update children's costs
        dist_func = math.dist
        for c in self.children:
            c.update_parent(self, self.cost + dist_func(self.position, c.position))

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 goal_sample_rate: float=0.2,
                 neighbor_radius_min: float=10.0,
                 neighbor_radius_max: float=30.0,
                 max_planning_time: float=30.0,
                 smoothing_trials: int=100):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time
        self.smoothing_trials = smoothing_trials

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist_func(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            return tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))

        def clamp_to_bounds(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            resolution = self.step_size / 5.0
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution)

        def adaptive_neighbor_radius(n_nodes: int):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 30.0
            r = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist_func(node.position, pos) ** 2) <= r_sq]

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        # Rotation matrix for ellipsoidal sampling
        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0,1 - cos_theta*cos_theta))
                K = [[0, -kz, ky],[kz,0,-kx],[-ky,kx,0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A, B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A, s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A, B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1-cos_theta))

        self._rotation_matrix = rotation_matrix

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-14 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x/norm for x in pt)

        self._unit_ball_sample = unit_ball_sample

        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def informed_sample(c_best):
            # Ellipsoidal informed sampling focused between start and goal
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_free()
            center = tuple(0.5*(start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = self._rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = self._unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1] + R[0][2]*sample_scaled[2]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1] + R[1][2]*sample_scaled[2]
                    z_rot = R[2][0]*sample_scaled[0] + R[2][1]*sample_scaled[1] + R[2][2]*sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = clamp_to_bounds(sample_pt)
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: List[Node], sample: Tuple[float, ...]) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, sample))
            new_pos = steer(nearest.position, sample)
            new_pos = clamp_to_bounds(new_pos)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)

            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: List[Node], target_pos: Tuple[float, ...]) -> Node:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                new_pos = clamp_to_bounds(new_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                    if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through_nbr
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                if dist_func(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_smoothing(path: List[Tuple[float, ...]], trials: int):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        all_nodes = tree_start + tree_goal
        all_edges: List[Tuple[Node, Node]] = []

        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_planning_time:
                break

            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start else start_pos
                else:
                    sample_pt = sample_free()

            if grow_start:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connected_node = incremental_connect(tree_goal, new_node.position)
                if connected_node:
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    all_nodes = tree_start + tree_goal
                    all_edges.clear()
                    for node in all_nodes:
                        for c in node.children:
                            all_edges.append((node, c))

                    smoothed = path_smoothing(best_path, self.smoothing_trials)
                    return PlannerResult(True, smoothed, all_nodes, all_edges)

            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue

                connected_node = incremental_connect(tree_start, new_node.position)
                if connected_node:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                    all_nodes = tree_start + tree_goal
                    all_edges.clear()
                    for node in all_nodes:
                        for c in node.children:
                            all_edges.append((node, c))

                    smoothed = path_smoothing(best_path, self.smoothing_trials)
                    return PlannerResult(True, smoothed, all_nodes, all_edges)

            grow_start = not grow_start

        success_state = (len(best_path) > 0)
        smoothed = path_smoothing(best_path, self.smoothing_trials) if success_state else []

        all_nodes = tree_start + tree_goal
        all_edges.clear()
        for node in all_nodes:
            for c in node.children:
                all_edges.append((node, c))

        return PlannerResult(success_state, smoothed, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * factor for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position        # Tuple[float, ...]
        self.parent = None              # Node or None
        self.cost = cost                # Cost from root
        self.children = []
        self.valid = True               # Validity flag (collision check)

        if parent is not None:
            self.set_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def set_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        dist_fun = math.dist
        for child in self.children:
            child.set_parent(self, self.cost + dist_fun(self.position, child.position))

    def path_from_root(self):
        node = self
        path = []
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float=5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = 0.15
        self.neighbor_radius_min = 5.0
        self.neighbor_radius_max = 30.0
        self.max_planning_time = 30.0
        self.smooth_trials = 150

    def plan(self, map) -> 'PlannerResult':
        import time
        import random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist = math.dist

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def clamp(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def steer(fr, to):
            d = dist(fr, to)
            if d <= self.step_size:
                return to
            scale = self.step_size / d
            return tuple(fr[d] + scale * (to[d] - fr[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and not self._is_in_obstacle(pos, obstacles, is_3d)

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma = self.step_size * 30.0
            r = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree, pos, radius):
            r_sq = radius * radius
            return [node for node in tree if (dist(node.position, pos) ** 2) <= r_sq]

        def unit_ball_sample(n):
            while True:
                sample = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in sample)
                if 1e-12 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in sample)

        rotation_mat_cache = None
        c_min = dist(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                norm_v = math.sqrt(vx*vx + vy*vy + vz*vz)
                if norm_v < 1e-12:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                ux, uy, uz = vx/norm_v, vy/norm_v, vz/norm_v
                if abs(ux) < 1e-6 and abs(uy) < 1e-6:
                    vx_ = 0.0; vy_ = 1.0; vz_ = 0.0
                else:
                    vx_ = -uy; vy_ = ux; vz_ = 0.0
                norm_ = math.sqrt(vx_*vx_ + vy_*vy_ + vz_*vz_)
                vx_ /= norm_; vy_ /= norm_; vz_ /= norm_
                wx = uy*vz_ - uz*vy_
                wy = uz*vx_ - ux*vz_
                wz = ux*vy_ - uy*vx_
                return [[ux, vx_, wx], [uy, vy_, wy], [uz, vz_, wz]]

        def informed_sample(c_best):
            nonlocal rotation_mat_cache
            if c_best == math.inf or c_best < c_min - 1e-12:
                while True:
                    pt = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                    if collision_free_node(pt):
                        return pt

            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            if rotation_mat_cache is None:
                rotation_mat_cache = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                dir_sample = unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = rotation_mat_cache[0][0] * sample_scaled[0] + rotation_mat_cache[0][1] * sample_scaled[1]
                    y_rot = rotation_mat_cache[1][0] * sample_scaled[0] + rotation_mat_cache[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = (rotation_mat_cache[0][0]*sample_scaled[0] +
                             rotation_mat_cache[0][1]*sample_scaled[1] +
                             rotation_mat_cache[0][2]*sample_scaled[2])
                    y_rot = (rotation_mat_cache[1][0]*sample_scaled[0] +
                             rotation_mat_cache[1][1]*sample_scaled[1] +
                             rotation_mat_cache[1][2]*sample_scaled[2])
                    z_rot = (rotation_mat_cache[2][0]*sample_scaled[0] +
                             rotation_mat_cache[2][1]*sample_scaled[1] +
                             rotation_mat_cache[2][2]*sample_scaled[2])
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = clamp(sample_pt)

                if collision_free_node(sample_pt):
                    return sample_pt

        def rewire(tree, new_node, neighbors):
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_through_new + 1e-12 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.set_parent(new_node, cost_through_new)

        def extend(tree, sample_pt):
            nearest = min(tree, key=lambda n: dist(n.position, sample_pt))
            new_pos = steer(nearest.position, sample_pt)
            new_pos = clamp(new_pos)

            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            radius = adaptive_neighbor_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            rewire(tree, new_node, neighbors)
            return new_node

        def incremental_connect(new_node, other_tree):
            if not other_tree:
                return None
            current = min(other_tree, key=lambda n: dist(n.position, new_node.position))
            while True:
                new_pos = steer(current.position, new_node.position)
                new_pos = clamp(new_pos)

                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                radius = adaptive_neighbor_radius(len(other_tree) + 1)
                neighbors = near_nodes(other_tree, new_pos, radius)

                min_cost = current.cost + dist(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                    if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = tentative_cost
                        best_parent = nbr

                conn_node = Node(new_pos)
                best_parent.add_child(conn_node)
                conn_node.cost = min_cost
                other_tree.append(conn_node)

                rewire(other_tree, conn_node, neighbors)

                if dist(conn_node.position, new_node.position) <= self.step_size:
                    if collision_free_edge(conn_node.position, new_node.position):
                        return conn_node
                    return None

                current = conn_node

        def path_shortcut(path, trials=self.smooth_trials):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        t_start = time.perf_counter()

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        all_nodes = tree_start + tree_goal
        all_edges = []

        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - t_start
            if elapsed > self.max_planning_time:
                # Time limit reached - return best found path so far
                if best_path:
                    smoothed = path_shortcut(best_path)
                    success_state = True
                else:
                    smoothed = []
                    success_state = False

                all_nodes = tree_start + tree_goal
                all_edges.clear()
                for n in all_nodes:
                    for c in n.children:
                        all_edges.append((n, c))
                return PlannerResult(success_state, smoothed, all_nodes, all_edges)

            # Sampling
            if informed_sampling:
                sample = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if grow_start else start_pos
                else:
                    # Limit sampling free node
                    for _ in range(20):
                        candidate = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                        if collision_free_node(candidate):
                            sample = candidate
                            break
                    else:
                        grow_start = not grow_start
                        continue

            active_tree = tree_start if grow_start else tree_goal
            other_tree = tree_goal if grow_start else tree_start

            new_node = extend(active_tree, sample)
            if new_node is None:
                grow_start = not grow_start
                continue
            all_nodes.append(new_node)

            connected_node = incremental_connect(new_node, other_tree)
            if connected_node is not None:
                path_start = new_node.path_from_root()
                path_goal = connected_node.path_from_root()
                if path_goal and path_goal[0] == new_node.position:
                    path_goal = path_goal[1:]
                combined_path = path_start + path_goal[::-1]

                total_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                if total_cost + 1e-12 < best_cost:
                    best_cost = total_cost
                    best_path = combined_path
                    informed_sampling = True

                    smoothed_path = path_shortcut(best_path)

                    all_edges.clear()
                    all_nodes = tree_start + tree_goal
                    for n in all_nodes:
                        for c in n.children:
                            all_edges.append((n, c))

                    return PlannerResult(True, smoothed_path, all_nodes, all_edges)

            grow_start = not grow_start

        # Max iterations reached without time limit breach
        success_state = best_path != []
        final_path = path_shortcut(best_path) if success_state else []

        all_edges.clear()
        all_nodes = tree_start + tree_goal
        for n in all_nodes:
            for c in n.children:
                all_edges.append((n, c))

        return PlannerResult(success_state, final_path, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            factor = i / steps
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * factor for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position           # Tuple[float, ...]
        self.parent = None                 # Node or None
        self.cost = cost                   # Accumulated cost from root
        self.children = []
        self.valid = True

        if parent:
            self.update_parent(parent, parent.cost + self.distance(parent.position, position))

    def distance(self, a, b):
        from math import dist
        return dist(a, b)

    def add_child(self, child):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent, new_cost):
        if self.parent is new_parent and abs(self.cost - new_cost) < 1e-12:
            return
        if self.parent:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        if new_parent and self not in new_parent.children:
            new_parent.children.append(self)
        # Recursively update children cost
        for c in self.children:
            c.update_parent(self, self.cost + self.distance(self.position, c.position))

    def path_from_root(self):
        path = []
        node = self
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float=5.0, max_time_sec: float=30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.max_time_sec = max_time_sec
        self.min_neighbor_radius = 8.0
        self.max_neighbor_radius = 25.0
        self.goal_sample_rate = 0.2  # 20% goal bias

    def plan(self, map) -> 'PlannerResult':
        import time
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = dim == 3
        success_state = False
        best_path = []
        best_cost = math.inf

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def dist(a, b):
            return math.dist(a, b)

        def steer(src, tgt):
            d = dist(src, tgt)
            if d <= self.step_size:
                return tgt
            factor = self.step_size / d
            return tuple(src[d] + factor * (tgt[d] - src[d]) for d in range(dim))

        def collision_free_node(p):
            return in_bounds(p) and (not self._is_in_obstacle(p, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def neighbor_radius(n_nodes) -> float:
            if n_nodes <= 1:
                return self.max_neighbor_radius
            r = self.max_neighbor_radius * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.min_neighbor_radius, min(self.max_neighbor_radius, r))

        def near_nodes(tree, pos, radius):
            rad_sq = radius * radius
            return [n for n in tree if (dist(n.position, pos) ** 2) <= rad_sq]

        def uniform_sample():
            for _ in range(100):
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt
            # Fallback to start if no free sample found after retries (rare)
            return start_pos

        # Extend tree by adding a node towards sample with rewiring
        def extend_and_rewire(tree, sample):
            nearest = min(tree, key=lambda n: dist(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            radius = neighbor_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest

            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_thru = nbr.cost + dist(nbr.position, new_pos)
                if cost_thru < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_thru
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            # Rewire neighbors with potential cost improvement
            for nbr in neighbors:
                if nbr is best_parent:
                    continue
                cost_thru_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_thru_new + 1e-10 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_thru_new)

            return new_node

        # Incremental connect attempts from tree nodes towards target_node
        def incremental_connect(tree, target_node):
            curr = min(tree, key=lambda n: dist(n.position, target_node.position))
            while True:
                new_pos = steer(curr.position, target_node.position)
                if not collision_free_node(new_pos) or not collision_free_edge(curr.position, new_pos):
                    return None

                radius = neighbor_radius(len(tree) + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = curr.cost + dist(curr.position, new_pos)
                best_parent = curr

                for nbr in neighbors:
                    if nbr is curr:
                        continue
                    cost_thru = nbr.cost + dist(nbr.position, new_pos)
                    if cost_thru < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_thru
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_thru_new = new_node.cost + dist(new_node.position, nbr.position)
                    if cost_thru_new + 1e-10 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_thru_new)

                if dist(new_node.position, target_node.position) <= self.step_size:
                    if collision_free_edge(new_node.position, target_node.position):
                        return new_node
                    else:
                        return None
                curr = new_node

        def path_smoothing(path, iterations=120):
            if len(path) < 3:
                return path[:]
            for _ in range(iterations):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        start_tree = [Node(start_pos, cost=0.0)]
        goal_tree = [Node(goal_pos, cost=0.0)]

        grow_from_start = True
        start_time = time.perf_counter()

        for iter_count in range(self.max_iter):
            elapsed = time.perf_counter() - start_time
            if elapsed > self.max_time_sec:
                break

            sample = goal_pos if random.random() < self.goal_sample_rate else uniform_sample()

            if grow_from_start:
                new_node = extend_and_rewire(start_tree, sample)
                if new_node is None:
                    grow_from_start = not grow_from_start
                    continue
                connect_node = incremental_connect(goal_tree, new_node)
                if connect_node:
                    path_s = new_node.path_from_root()
                    path_g = connect_node.path_from_root()
                    # Avoid duplicate node in connection
                    if path_g and path_g[0] == new_node.position:
                        path_g = path_g[1:]
                    combined_path = path_s + path_g[::-1]

                    combined_cost = new_node.cost + connect_node.cost + dist(new_node.position, connect_node.position)
                    if combined_cost + 1e-10 < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        success_state = True
                        # Early return on improved path
                        smoothed = path_smoothing(best_path)
                        nodes = start_tree + goal_tree
                        edges = []
                        for node in nodes:
                            for c in node.children:
                                edges.append((node, c))
                        return PlannerResult(True, smoothed, nodes, edges)
            else:
                new_node = extend_and_rewire(goal_tree, sample)
                if new_node is None:
                    grow_from_start = not grow_from_start
                    continue
                connect_node = incremental_connect(start_tree, new_node)
                if connect_node:
                    path_g = new_node.path_from_root()
                    path_s = connect_node.path_from_root()
                    if path_s and path_s[0] == new_node.position:
                        path_s = path_s[1:]
                    combined_path = path_s + path_g[::-1]

                    combined_cost = new_node.cost + connect_node.cost + dist(new_node.position, connect_node.position)
                    if combined_cost + 1e-10 < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        success_state = True
                        smoothed = path_smoothing(best_path)
                        nodes = start_tree + goal_tree
                        edges = []
                        for node in nodes:
                            for c in node.children:
                                edges.append((node, c))
                        return PlannerResult(True, smoothed, nodes, edges)

            grow_from_start = not grow_from_start

        # Return best path found after timeout or max iterations
        if best_path:
            smoothed = path_smoothing(best_path)
        else:
            smoothed = []

        nodes = start_tree + goal_tree
        edges = []
        for node in nodes:
            for c in node.children:
                edges.append((node, c))

        return PlannerResult(success_state, smoothed, nodes, edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        import math
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position: Tuple[float, ...], parent: Optional['Node']=None, cost: float=0.0):
        self.position = position
        self.parent = None
        self.cost = 0.0
        self.children: List['Node'] = []
        self.valid = True
        if parent:
            self.update_parent(parent, parent.cost + self._distance(parent))

    def _distance(self, other: 'Node') -> float:
        return math.dist(self.position, other.position)

    def add_child(self, child: 'Node') -> None:
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node') -> None:
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float) -> None:
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        # Propagate cost update recursively to children
        for c in self.children:
            c.update_parent(self, new_cost + c._distance(self))

    def path_from_root(self) -> List[Tuple[float, ...]]:
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 5000, step_size: float=5.0,
                 max_planning_time: float=30.0, goal_sample_rate: float=0.15,
                 neighbor_radius_min: float=5.0, neighbor_radius_max: float=30.0,
                 smoothing_trials: int=150):
        self.max_iter = max_iter
        self.step_size = step_size
        self.max_planning_time = max_planning_time
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.smoothing_trials = smoothing_trials

    def plan(self, map) -> PlannerResult:
        bounds = map.size
        dim = len(bounds)
        is_3d = (dim == 3)
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        def in_bounds(pos: Tuple[float, ...]) -> bool:
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def clamp_to_bounds(pos: Tuple[float, ...]) -> Tuple[float, ...]:
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def steer(from_p: Tuple[float, ...], to_p: Tuple[float, ...]) -> Tuple[float, ...]:
            dist_ = math.dist(from_p, to_p)
            if dist_ <= self.step_size:
                return to_p
            ratio = self.step_size / dist_
            return tuple(from_p[d] + ratio * (to_p[d] - from_p[d]) for d in range(dim))

        def collision_free_node(pos: Tuple[float, ...]) -> bool:
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(from_p: Tuple[float, ...], to_p: Tuple[float, ...]) -> bool:
            resolution = self.step_size / 5.0 if self.step_size > 0 else 0.5
            return not self._is_edge_in_obstacle(from_p, to_p, obstacles, is_3d, resolution)

        def adaptive_neighbor_radius(n_nodes: int) -> float:
            if n_nodes <= 1:
                return self.neighbor_radius_max
            val = (2.0 * (1 + 1/dim)) ** (1/dim) * ((math.log(n_nodes) / n_nodes) ** (1/dim))
            radius = val * (max(bounds) if max(bounds) > 0 else 10.0)  # scale according to map size
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, radius))

        def near_nodes(tree: List[Node], pos: Tuple[float, ...], radius: float) -> List[Node]:
            r2 = radius * radius
            return [node for node in tree if (math.dist(node.position, pos) ** 2) <= r2]

        def unit_ball_sample(n: int) -> Tuple[float, ...]:
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-12 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in pt)

        c_min = math.dist(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling_enabled = False

        def rotation_matrix(direction: Tuple[float, ...]) -> List[List[float]]:
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                c, s = math.cos(angle), math.sin(angle)
                return [[c, -s], [s, c]]
            else:
                vx, vy, vz = direction
                norm_v = math.sqrt(vx*vx + vy*vy + vz*vz)
                if norm_v < 1e-12:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                vx /= norm_v
                vy /= norm_v
                vz /= norm_v
                arbitrary = (1.0, 0.0, 0.0) if abs(vx) < 0.9 else (0.0, 1.0, 0.0)
                wx = vy*arbitrary[2] - vz*arbitrary[1]
                wy = vz*arbitrary[0] - vx*arbitrary[2]
                wz = vx*arbitrary[1] - vy*arbitrary[0]
                norm_w = math.sqrt(wx*wx + wy*wy + wz*wz)
                if norm_w < 1e-12:
                    wx, wy, wz = 0.0, 0.0, 1.0
                else:
                    wx /= norm_w
                    wy /= norm_w
                    wz /= norm_w
                ux, uy, uz = vx, vy, vz
                vx_, vy_, vz_ = wx, wy, wz
                wx_, wy_, wz_ = uy*wz - uz*vy_, uz*vx_ - ux*wz, ux*vy_ - uy*vx_
                return [
                    [ux, vx_, wx_],
                    [uy, vy_, wy_],
                    [uz, vz_, wz_]
                ]

        def informed_sample(c_best: float) -> Tuple[float, ...]:
            if c_best == math.inf or c_best < c_min - 1e-12:
                return sample_free()

            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0.0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0.0)) / 2.0
                radii = [a1] + [a_minor]*(dim-1)

            n = dim
            while True:
                dir_sample = unit_ball_sample(n)
                r = random.random() ** (1.0 / n)
                sample_unit = tuple(r * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = sum(R[0][i]*sample_scaled[i] for i in range(n))
                    y_rot = sum(R[1][i]*sample_scaled[i] for i in range(n))
                    z_rot = sum(R[2][i]*sample_scaled[i] for i in range(n))
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = clamp_to_bounds(sample_pt)
                if collision_free_node(sample_pt):
                    return sample_pt

        def sample_free() -> Tuple[float, ...]:
            for _ in range(30):
                pt = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt
            return goal_pos  # fallback

        def rewire(tree: List[Node], new_node: Node, neighbors: List[Node]) -> None:
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_thru_new = new_node.cost + math.dist(new_node.position, nbr.position)
                if cost_thru_new + 1e-9 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_thru_new)

        def extend_and_rewire(tree: List[Node], sample_pt: Tuple[float, ...]) -> Optional[Node]:
            if not tree:
                return None
            nearest_node = min(tree, key=lambda n: math.dist(n.position, sample_pt))
            new_pos = steer(nearest_node.position, sample_pt)
            new_pos = clamp_to_bounds(new_pos)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest_node.position, new_pos):
                return None

            n_radius = adaptive_neighbor_radius(len(tree)+1)
            neighbors = near_nodes(tree, new_pos, n_radius)

            min_cost = nearest_node.cost + math.dist(nearest_node.position, new_pos)
            best_parent = nearest_node
            for nbr in neighbors:
                if nbr == nearest_node:
                    continue
                tentative_cost = nbr.cost + math.dist(nbr.position, new_pos)
                if tentative_cost + 1e-9 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            new_node.update_parent(best_parent, min_cost)
            tree.append(new_node)
            rewire(tree, new_node, neighbors)
            return new_node

        def incremental_connect_and_rewire(new_node: Node, other_tree: List[Node]) -> Optional[Node]:
            if not other_tree:
                return None
            current_node = min(other_tree, key=lambda n: math.dist(n.position, new_node.position))
            while True:
                step_pos = steer(current_node.position, new_node.position)
                step_pos = clamp_to_bounds(step_pos)
                if not collision_free_node(step_pos) or not collision_free_edge(current_node.position, step_pos):
                    return None

                radius = adaptive_neighbor_radius(len(other_tree)+1)
                neighbors = near_nodes(other_tree, step_pos, radius)

                min_cost = current_node.cost + math.dist(current_node.position, step_pos)
                best_parent = current_node
                for nbr in neighbors:
                    if nbr == current_node:
                        continue
                    tentative_cost = nbr.cost + math.dist(nbr.position, step_pos)
                    if tentative_cost + 1e-9 < min_cost and collision_free_edge(nbr.position, step_pos):
                        min_cost = tentative_cost
                        best_parent = nbr

                connect_node = Node(step_pos)
                connect_node.update_parent(best_parent, min_cost)
                other_tree.append(connect_node)
                rewire(other_tree, connect_node, neighbors)

                dist_to_new = math.dist(connect_node.position, new_node.position)
                if dist_to_new <= self.step_size:
                    if collision_free_edge(connect_node.position, new_node.position):
                        return connect_node
                    else:
                        return None
                current_node = connect_node

        def path_smoothing(path: List[Tuple[float, ...]], max_trials: int) -> List[Tuple[float, ...]]:
            if len(path) < 3:
                return path[:]
            p = path[:]
            for _ in range(max_trials):
                if len(p) < 3:
                    break
                i = random.randint(0, len(p) - 3)
                j = random.randint(i + 2, len(p) - 1)
                if collision_free_edge(p[i], p[j]):
                    p = p[:i+1] + p[j:]
            return p

        # Initialize trees
        tree_start: List[Node] = [Node(start_pos)]
        tree_goal: List[Node] = [Node(goal_pos)]
        best_path_nodes: List[Tuple[float, ...]] = []
        best_path_cost = math.inf

        grow_start = True
        start_time = time.perf_counter()
        edges: List[Tuple[Node, Node]] = []

        for iter_count in range(self.max_iter):
            elapsed = time.perf_counter() - start_time
            if elapsed >= self.max_planning_time:
                # Time limit exceeded, return best path found so far if any
                if best_path_nodes:
                    smoothed = path_smoothing(best_path_nodes, self.smoothing_trials)
                    all_nodes = tree_start + tree_goal
                    edges.clear()
                    for node in all_nodes:
                        for c in node.children:
                            edges.append((node, c))
                    return PlannerResult(True, smoothed, all_nodes, edges)
                else:
                    all_nodes = tree_start + tree_goal
                    edges.clear()
                    for node in all_nodes:
                        for c in node.children:
                            edges.append((node, c))
                    return PlannerResult(False, [], all_nodes, edges)

            # Sample point
            if informed_sampling_enabled:
                sample_pt = informed_sample(best_path_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start else start_pos
                else:
                    sample_pt = sample_free()

            active_tree = tree_start if grow_start else tree_goal
            other_tree = tree_goal if grow_start else tree_start

            new_node = extend_and_rewire(active_tree, sample_pt)
            if new_node is None:
                grow_start = not grow_start
                continue

            connect_node = incremental_connect_and_rewire(new_node, other_tree)
            if connect_node is not None:
                if grow_start:
                    path_a = new_node.path_from_root()
                    path_b = connect_node.path_from_root()
                else:
                    path_b = new_node.path_from_root()
                    path_a = connect_node.path_from_root()

                # Avoid duplicated node when combining
                if path_b and path_b[0] == new_node.position:
                    path_b = path_b[1:]
                combined_path = path_a + path_b[::-1]

                # Compute combined cost
                total_cost = sum(math.dist(combined_path[i], combined_path[i+1]) for i in range(len(combined_path)-1))
                if total_cost + 1e-9 < best_path_cost:
                    best_path_cost = total_cost
                    best_path_nodes = combined_path
                    informed_sampling_enabled = True  # enable informed sampling

                # Early return upon first found path to reduce planning time
                smoothed_path = path_smoothing(best_path_nodes, self.smoothing_trials)
                all_nodes = tree_start + tree_goal
                edges.clear()
                for node in all_nodes:
                    for c in node.children:
                        edges.append((node, c))
                return PlannerResult(True, smoothed_path, all_nodes, edges)

            grow_start = not grow_start

        # Max iterations reached
        if best_path_nodes:
            smoothed_path = path_smoothing(best_path_nodes, self.smoothing_trials)
            all_nodes = tree_start + tree_goal
            edges.clear()
            for node in all_nodes:
                for c in node.children:
                    edges.append((node, c))
            return PlannerResult(True, smoothed_path, all_nodes, edges)

        all_nodes = tree_start + tree_goal
        edges.clear()
        for node in all_nodes:
            for c in node.children:
                edges.append((node, c))
        return PlannerResult(False, [], all_nodes, edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False

class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position            # Tuple[float, ...]
        self.parent = None                  # Node or None
        self.cost = cost                    # Cost from root node
        self.children = []                  # List[Node]
        self.valid = True                   # Collision validity flag

        if parent is not None:
            self.set_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def set_parent(self, new_parent, new_cost):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        dist_fun = math.dist
        # Recursively update children's cost/parent relationships
        for child in self.children:
            child.set_parent(self, self.cost + dist_fun(self.position, child.position))

    def path_from_root(self):
        node = self
        path = []
        while node:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self, max_iter: int = 10000, step_size: float = 5.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = 0.15                 # Goal bias during sampling
        self.neighbor_radius_min = 5.0                # Minimum neighbor radius
        self.neighbor_radius_max = 30.0               # Maximum neighbor radius
        self.max_planning_time = 30.0                 # Seconds
        self.smooth_trials = 150                       # Path smoothing shortcut trials

    def plan(self, map) -> 'PlannerResult':
        import time
        import random

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist = math.dist

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def clamp(pos):
            return tuple(max(0.0, min(bounds[d], pos[d])) for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            scale = self.step_size / d
            return tuple(from_pos[d] + scale * (to_pos[d] - from_pos[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and not self._is_in_obstacle(pos, obstacles, is_3d)

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma = self.step_size * 30.0
            r = gamma * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree, pos, radius):
            r_sq = radius * radius
            return [node for node in tree if (dist(node.position, pos) ** 2) <= r_sq]

        def unit_ball_sample(n):
            while True:
                sample = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x * x for x in sample)
                if 1e-12 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x / norm for x in sample)

        rotation_mat_cache = None
        c_min = dist(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                norm_v = math.sqrt(vx*vx + vy*vy + vz*vz)
                if norm_v < 1e-12:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                ux, uy, uz = vx/norm_v, vy/norm_v, vz/norm_v
                # Construct orthonormal basis
                # Choose arbitrary vector not colinear to ux,uy,uz
                if abs(ux) < 1e-6 and abs(uy) < 1e-6:
                    vx_ = 0.0; vy_ = 1.0; vz_ = 0.0
                else:
                    vx_ = -uy; vy_ = ux; vz_ = 0.0
                norm_ = math.sqrt(vx_*vx_ + vy_*vy_ + vz_*vz_)
                vx_ /= norm_; vy_ /= norm_; vz_ /= norm_
                wx = uy*vz_ - uz*vy_
                wy = uz*vx_ - ux*vz_
                wz = ux*vy_ - uy*vx_
                return [[ux, vx_, wx], [uy, vy_, wy], [uz, vz_, wz]]

        def informed_sample(c_best):
            nonlocal rotation_mat_cache
            if c_best == math.inf or c_best < c_min - 1e-12:
                # Uniform sampling until first solution found
                while True:
                    pt = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                    if collision_free_node(pt):
                        return pt

            center = tuple((start_pos[d] + goal_pos[d]) * 0.5 for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            if rotation_mat_cache is None:
                rotation_mat_cache = rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            while True:
                dir_sample = unit_ball_sample(dim)
                radius_sample = random.random() ** (1.0 / dim)
                sample_unit = tuple(radius_sample * d for d in dir_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(dim))

                if dim == 2:
                    x_rot = rotation_mat_cache[0][0] * sample_scaled[0] + rotation_mat_cache[0][1] * sample_scaled[1]
                    y_rot = rotation_mat_cache[1][0] * sample_scaled[0] + rotation_mat_cache[1][1] * sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = (rotation_mat_cache[0][0]*sample_scaled[0] +
                             rotation_mat_cache[0][1]*sample_scaled[1] +
                             rotation_mat_cache[0][2]*sample_scaled[2])
                    y_rot = (rotation_mat_cache[1][0]*sample_scaled[0] +
                             rotation_mat_cache[1][1]*sample_scaled[1] +
                             rotation_mat_cache[1][2]*sample_scaled[2])
                    z_rot = (rotation_mat_cache[2][0]*sample_scaled[0] +
                             rotation_mat_cache[2][1]*sample_scaled[1] +
                             rotation_mat_cache[2][2]*sample_scaled[2])
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(dim))
                sample_pt = clamp(sample_pt)

                if collision_free_node(sample_pt):
                    return sample_pt

        def rewire(tree, new_node, neighbors):
            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist(new_node.position, nbr.position)
                if cost_through_new + 1e-12 < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.set_parent(new_node, cost_through_new)

        def extend(tree, sample_pt):
            nearest = min(tree, key=lambda n: dist(n.position, sample_pt))
            new_pos = steer(nearest.position, sample_pt)
            new_pos = clamp(new_pos)

            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            radius = adaptive_neighbor_radius(len(tree) + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = tentative_cost
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            rewire(tree, new_node, neighbors)
            return new_node

        def incremental_connect(new_node, other_tree):
            if not other_tree:
                return None
            current = min(other_tree, key=lambda n: dist(n.position, new_node.position))
            while True:
                new_pos = steer(current.position, new_node.position)
                new_pos = clamp(new_pos)

                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                radius = adaptive_neighbor_radius(len(other_tree) + 1)
                neighbors = near_nodes(other_tree, new_pos, radius)

                min_cost = current.cost + dist(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    tentative_cost = nbr.cost + dist(nbr.position, new_pos)
                    if tentative_cost + 1e-12 < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = tentative_cost
                        best_parent = nbr

                conn_node = Node(new_pos)
                best_parent.add_child(conn_node)
                conn_node.cost = min_cost
                other_tree.append(conn_node)

                rewire(other_tree, conn_node, neighbors)

                if dist(conn_node.position, new_node.position) <= self.step_size:
                    if collision_free_edge(conn_node.position, new_node.position):
                        return conn_node
                    return None

                current = conn_node

        def path_shortcut(path, trials=self.smooth_trials):
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path)-3)
                j = random.randint(i+2, len(path)-1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i+1] + path[j:]
            return path

        import time
        t_start = time.perf_counter()
        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]
        all_nodes = tree_start + tree_goal
        all_edges = []

        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - t_start
            if elapsed > self.max_planning_time:
                # Time limit reached - return best path found so far
                if best_path:
                    smoothed = path_shortcut(best_path)
                    success_state = True
                else:
                    smoothed = []
                    success_state = False

                all_nodes = tree_start + tree_goal
                all_edges.clear()
                for n in all_nodes:
                    for c in n.children:
                        all_edges.append((n, c))

                return PlannerResult(success_state, smoothed, all_nodes, all_edges)

            # Sample point
            if informed_sampling:
                sample = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample = goal_pos if grow_start else start_pos
                else:
                    for _ in range(20):
                        candidate = tuple(random.uniform(0.0, bounds[d]) for d in range(dim))
                        if collision_free_node(candidate):
                            sample = candidate
                            break
                    else:
                        grow_start = not grow_start
                        continue

            active_tree = tree_start if grow_start else tree_goal
            other_tree = tree_goal if grow_start else tree_start

            new_node = extend(active_tree, sample)
            if new_node is None:
                grow_start = not grow_start
                continue
            all_nodes.append(new_node)

            connected_node = incremental_connect(new_node, other_tree)
            if connected_node is not None:
                path_start = new_node.path_from_root()
                path_goal = connected_node.path_from_root()
                if path_goal and path_goal[0] == new_node.position:
                    path_goal = path_goal[1:]
                combined_path = path_start + path_goal[::-1]

                total_cost = new_node.cost + connected_node.cost + dist(new_node.position, connected_node.position)
                if total_cost + 1e-12 < best_cost:
                    best_cost = total_cost
                    best_path = combined_path
                    informed_sampling = True

                    smoothed_path = path_shortcut(best_path)

                    all_edges.clear()
                    all_nodes = tree_start + tree_goal
                    for n in all_nodes:
                        for c in n.children:
                            all_edges.append((n, c))

                    return PlannerResult(True, smoothed_path, all_nodes, all_edges)

            grow_start = not grow_start

        success_state = best_path != []
        final_path = path_shortcut(best_path) if success_state else []

        all_edges.clear()
        all_nodes = tree_start + tree_goal
        for n in all_nodes:
            for c in n.children:
                all_edges.append((n, c))

        return PlannerResult(success_state, final_path, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False
