class Node:
    def __init__(self, position, parent=None, cost=0.0):
        self.position = position          # Tuple[float, ...]
        self.parent = None                # Node or None
        self.cost = cost                  # Cost from root
        self.children = []
        self.valid = True                 # Validity flag

        if parent is not None:
            self.update_parent(parent, parent.cost + math.dist(parent.position, position))

    def add_child(self, child: 'Node'):
        if child not in self.children:
            self.children.append(child)
            child.parent = self

    def remove_child(self, child: 'Node'):
        if child in self.children:
            self.children.remove(child)
            child.parent = None

    def update_parent(self, new_parent: 'Node', new_cost: float):
        if self.parent is not None:
            self.parent.remove_child(self)
        self.parent = new_parent
        self.cost = new_cost
        new_parent.add_child(self)
        dist_func = math.dist
        for child in self.children:
            child.update_parent(self, self.cost + dist_func(self.position, child.position))

    def path_from_root(self):
        path = []
        node = self
        while node is not None:
            path.append(node.position)
            node = node.parent
        return path[::-1]
class Planner:
    def __init__(self,
                 max_iter: int = 5000,
                 step_size: float = 5.0,
                 goal_sample_rate: float = 0.2,
                 neighbor_radius_min: float = 8.0,
                 neighbor_radius_max: float = 25.0,
                 max_planning_time: float = 30.0):
        self.max_iter = max_iter
        self.step_size = step_size
        self.goal_sample_rate = goal_sample_rate
        self.neighbor_radius_min = neighbor_radius_min
        self.neighbor_radius_max = neighbor_radius_max
        self.max_planning_time = max_planning_time

    def plan(self, map) -> 'PlannerResult':
        import time
        import random
        import math

        bounds = map.size
        start_pos = map.start
        goal_pos = map.goal
        obstacles = map.obstacles

        dim = len(bounds)
        is_3d = (dim == 3)
        dist_func = math.dist

        start_time = time.perf_counter()

        def in_bounds(pos):
            return all(0.0 <= pos[d] <= bounds[d] for d in range(dim))

        def steer(from_pos, to_pos):
            d = dist_func(from_pos, to_pos)
            if d <= self.step_size:
                return to_pos
            factor = self.step_size / d
            new_pt = tuple(from_pos[d] + factor * (to_pos[d] - from_pos[d]) for d in range(dim))
            # Clamp to bounds
            return tuple(min(max(new_pt[d], 0.0), bounds[d]) for d in range(dim))

        def collision_free_node(pos):
            return in_bounds(pos) and (not self._is_in_obstacle(pos, obstacles, is_3d))

        def collision_free_edge(p1, p2):
            return not self._is_edge_in_obstacle(p1, p2, obstacles, is_3d, resolution=self.step_size / 5)

        def adaptive_neighbor_radius(n_nodes: int):
            if n_nodes <= 1:
                return self.neighbor_radius_max
            gamma_rrt = 30.0
            r = gamma_rrt * ((math.log(n_nodes) / n_nodes) ** (1.0 / dim))
            return max(self.neighbor_radius_min, min(self.neighbor_radius_max, r))

        def near_nodes(tree_nodes, pos, radius):
            r_sq = radius * radius
            return [node for node in tree_nodes if (dist_func(node.position, pos) ** 2) <= r_sq]

        def sample_free():
            while True:
                pt = tuple(random.uniform(0, bounds[d]) for d in range(dim))
                if collision_free_node(pt):
                    return pt

        # Rotation matrix for ellipsoidal sampling
        def rotation_matrix(direction):
            if dim == 2:
                dx, dy = direction
                angle = math.atan2(dy, dx)
                cos_a, sin_a = math.cos(angle), math.sin(angle)
                return [[cos_a, -sin_a], [sin_a, cos_a]]
            else:
                vx, vy, vz = direction
                kx, ky, kz = -vy, vx, 0.0
                norm_k = math.sqrt(kx * kx + ky * ky + kz * kz)
                if norm_k < 1e-10:
                    return [[1,0,0],[0,1,0],[0,0,1]]
                kx /= norm_k
                ky /= norm_k
                kz /= norm_k
                cos_theta = vx
                sin_theta = math.sqrt(max(0,1 - cos_theta*cos_theta))
                K = [[0, -kz, ky],[kz,0,-kx],[-ky,kx,0]]
                I = [[1,0,0],[0,1,0],[0,0,1]]

                def mat_add(A, B):
                    return [[A[i][j]+B[i][j] for j in range(3)] for i in range(3)]
                def mat_scale(A, s):
                    return [[A[i][j]*s for j in range(3)] for i in range(3)]
                def mat_mul(A, B):
                    return [[sum(A[i][k]*B[k][j] for k in range(3)) for j in range(3)] for i in range(3)]

                K2 = mat_mul(K, K)
                return mat_add(mat_add(I, mat_scale(K, sin_theta)), mat_scale(K2, 1-cos_theta))

        self._rotation_matrix = rotation_matrix

        def unit_ball_sample(n):
            while True:
                pt = [random.uniform(-1, 1) for _ in range(n)]
                norm_sq = sum(x*x for x in pt)
                if 1e-10 < norm_sq <= 1.0:
                    norm = math.sqrt(norm_sq)
                    return tuple(x/norm for x in pt)

        self._unit_ball_sample = unit_ball_sample

        c_min = dist_func(start_pos, goal_pos)
        best_cost = math.inf
        best_path = []
        informed_sampling = False

        def informed_sample(c_best):
            if c_best == math.inf or c_best < c_min - 1e-10:
                return sample_free()
            center = tuple(0.5*(start_pos[d] + goal_pos[d]) for d in range(dim))
            direction = tuple((goal_pos[d] - start_pos[d]) / c_min for d in range(dim))
            R = self._rotation_matrix(direction)

            if dim == 2:
                a1 = c_best / 2.0
                a2 = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a2]
            else:
                a1 = c_best / 2.0
                a_minor = math.sqrt(max(c_best*c_best - c_min*c_min, 0)) / 2.0
                radii = [a1, a_minor, a_minor]

            n = dim
            while True:
                direction_sample = self._unit_ball_sample(n)
                radius_sample = random.random() ** (1.0 / n)
                sample_unit = tuple(radius_sample * d for d in direction_sample)
                sample_scaled = tuple(sample_unit[d] * radii[d] for d in range(n))

                if dim == 2:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1]
                    rotated = (x_rot, y_rot)
                else:
                    x_rot = R[0][0]*sample_scaled[0] + R[0][1]*sample_scaled[1] + R[0][2]*sample_scaled[2]
                    y_rot = R[1][0]*sample_scaled[0] + R[1][1]*sample_scaled[1] + R[1][2]*sample_scaled[2]
                    z_rot = R[2][0]*sample_scaled[0] + R[2][1]*sample_scaled[1] + R[2][2]*sample_scaled[2]
                    rotated = (x_rot, y_rot, z_rot)

                sample_pt = tuple(center[d] + rotated[d] for d in range(n))
                sample_pt = tuple(max(0.0, min(bounds[d], sample_pt[d])) for d in range(n))
                if collision_free_node(sample_pt):
                    return sample_pt

        def extend_and_rewire(tree: list, sample: tuple) -> Node or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, sample))
            new_pos = steer(nearest.position, sample)
            if not collision_free_node(new_pos) or not collision_free_edge(nearest.position, new_pos):
                return None

            n_nodes = len(tree)
            radius = adaptive_neighbor_radius(n_nodes + 1)
            neighbors = near_nodes(tree, new_pos, radius)

            min_cost = nearest.cost + dist_func(nearest.position, new_pos)
            best_parent = nearest
            for nbr in neighbors:
                if nbr is nearest:
                    continue
                cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                    min_cost = cost_through_nbr
                    best_parent = nbr

            new_node = Node(new_pos)
            best_parent.add_child(new_node)
            new_node.cost = min_cost
            tree.append(new_node)

            for nbr in neighbors:
                if nbr is new_node.parent:
                    continue
                cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                    nbr.update_parent(new_node, cost_through_new)

            return new_node

        def incremental_connect(tree: list, target_pos: tuple) -> Node or None:
            if not tree:
                return None
            nearest = min(tree, key=lambda n: dist_func(n.position, target_pos))
            current = nearest
            while True:
                new_pos = steer(current.position, target_pos)
                if not collision_free_node(new_pos) or not collision_free_edge(current.position, new_pos):
                    return None

                n_nodes = len(tree)
                radius = adaptive_neighbor_radius(n_nodes + 1)
                neighbors = near_nodes(tree, new_pos, radius)

                min_cost = current.cost + dist_func(current.position, new_pos)
                best_parent = current
                for nbr in neighbors:
                    if nbr is current:
                        continue
                    cost_through_nbr = nbr.cost + dist_func(nbr.position, new_pos)
                    if cost_through_nbr < min_cost and collision_free_edge(nbr.position, new_pos):
                        min_cost = cost_through_nbr
                        best_parent = nbr

                new_node = Node(new_pos)
                best_parent.add_child(new_node)
                new_node.cost = min_cost
                tree.append(new_node)

                for nbr in neighbors:
                    if nbr is new_node.parent:
                        continue
                    cost_through_new = new_node.cost + dist_func(new_node.position, nbr.position)
                    if cost_through_new < nbr.cost and collision_free_edge(new_node.position, nbr.position):
                        nbr.update_parent(new_node, cost_through_new)

                if dist_func(new_node.position, target_pos) <= self.step_size:
                    if collision_free_edge(new_node.position, target_pos):
                        return new_node
                    else:
                        return None

                current = new_node

        def path_smoothing(path: list, trials: int = 100) -> list:
            if len(path) < 3:
                return path[:]
            for _ in range(trials):
                if len(path) < 3:
                    break
                i = random.randint(0, len(path) - 3)
                j = random.randint(i + 2, len(path) - 1)
                if collision_free_edge(path[i], path[j]):
                    path = path[:i + 1] + path[j:]
            return path

        tree_start = [Node(start_pos, None, 0.0)]
        tree_goal = [Node(goal_pos, None, 0.0)]

        best_path = []
        grow_start = True
        iteration = 0

        while iteration < self.max_iter:
            iteration += 1
            elapsed = time.perf_counter() - start_time
            if elapsed >= self.max_planning_time:
                break

            if informed_sampling:
                sample_pt = informed_sample(best_cost)
            else:
                if random.random() < self.goal_sample_rate:
                    sample_pt = goal_pos if grow_start else start_pos
                else:
                    sample_pt = sample_free()

            if grow_start:
                new_node = extend_and_rewire(tree_start, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_goal, new_node.position)
                if connected_node is not None:
                    path_start = new_node.path_from_root()
                    path_goal = connected_node.path_from_root()
                    if path_goal and path_goal[0] == new_node.position:
                        path_goal = path_goal[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        smoothed = path_smoothing(best_path)
                        success_state = True
                        all_nodes = tree_start + tree_goal
                        all_edges = []
                        for node in all_nodes:
                            for c in node.children:
                                all_edges.append((node, c))
                        return PlannerResult(True, smoothed, all_nodes, all_edges)
            else:
                new_node = extend_and_rewire(tree_goal, sample_pt)
                if new_node is None:
                    grow_start = not grow_start
                    continue
                connected_node = incremental_connect(tree_start, new_node.position)
                if connected_node is not None:
                    path_goal = new_node.path_from_root()
                    path_start = connected_node.path_from_root()
                    if path_start and path_start[0] == new_node.position:
                        path_start = path_start[1:]
                    combined_path = path_start + path_goal[::-1]

                    combined_cost = new_node.cost + connected_node.cost + dist_func(new_node.position, connected_node.position)
                    if combined_cost < best_cost:
                        best_cost = combined_cost
                        best_path = combined_path
                        informed_sampling = True

                        smoothed = path_smoothing(best_path)
                        success_state = True
                        all_nodes = tree_start + tree_goal
                        all_edges = []
                        for node in all_nodes:
                            for c in node.children:
                                all_edges.append((node, c))
                        return PlannerResult(True, smoothed, all_nodes, all_edges)

            grow_start = not grow_start

        success_state = len(best_path) > 0
        smoothed = path_smoothing(best_path) if success_state else []

        all_nodes = tree_start + tree_goal
        all_edges = []
        for node in all_nodes:
            for c in node.children:
                all_edges.append((node, c))

        return PlannerResult(success_state, smoothed, all_nodes, all_edges)

    def _is_in_obstacle(self, pos, obstacles, is_3d):
        for obs in obstacles:
            if is_3d:
                x, y, z, w, h, d = obs
                px, py, pz = pos
                if x <= px <= x + w and y <= py <= y + h and z <= pz <= z + d:
                    return True
            else:
                x, y, w, h = obs
                px, py = pos
                if x <= px <= x + w and y <= py <= y + h:
                    return True
        return False

    def _is_edge_in_obstacle(self, from_pos, to_pos, obstacles, is_3d, resolution=1.0):
        distance = math.dist(from_pos, to_pos)
        steps = max(1, int(distance / resolution))
        for i in range(steps + 1):
            interp = tuple(from_pos[d] + (to_pos[d] - from_pos[d]) * (i / steps) for d in range(len(from_pos)))
            if self._is_in_obstacle(interp, obstacles, is_3d):
                return True
        return False
